INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (1, 'کوفته تبریزی با مرغ', 'در اولین مرحله از دستور پخت کوفته مرغ خوشمزه از استخوان نداشتن گوشت مرغ مطمئن شوید. مهم نیست گوشت ران باشد یا سینه مرغ. آن را چرخ کنید. پیاز، سیر و فلفل دلمه‌ای را رنده کنید. جعفری را ساطوری ریز خرد ک', 'در اولین مرحله از دستور پخت کوفته مرغ خوشمزه از استخوان نداشتن گوشت مرغ مطمئن شوید. مهم نیست گوشت ران باشد یا سینه مرغ. آن را چرخ کنید. پیاز، سیر و فلفل دلمه‌ای را رنده کنید. جعفری را ساطوری ریز خرد کرده و به مرغ اضافه کنید. تخم مرغ را اضافه کنید. زردچوبه، نمک، فلفل سیاه و فلفل قرمز، پاپریکا و پودر زنجبیل و دارچین را به مخلوط اضافه کنید. همه مواد را با هم ورز دهید در حدی که ماده به دست آمده به دست نچسبد. به اندازه یک گردو از مواد بردارید و به شکل دایره‌ای گلوله کنید. داخل گلوله‌ها را یک حفره با دست ایجاد کنید و داخل آن را مغز گردو و کشمش بریزید. اگر با طعم شیرین کشمش در غذا کنار نمی‌‌آیید، می‌توانید به جای آن از زرشک استفاده کنید. در یک تابه یا در سرخ‌کن روغن کافی بریزید و اجازه دهید به اندازه کافی داغ شود. کوفته‌ها را در تابه بیندازید و سرخ کنید تا حدی که طلایی شود. یک پیاز دیگر را نگینی خرد و در یک قابلمه با سایز مناسب، با روغن سرخ کنید. به اندازه یک قاشق غذاخوری به آن رب گوجه فرنگی اضافه کنید و با پیاز و روغن تفت دهید تا طعم خامی آن گرفته شود. به دلخواهتان از ادویه‌‌هایی که دوست دارید، استفاده کنید و کوفته‌های مرغ را در آن بریزید و کمی تفت بدهید. به اندازه ۲ لیوان دسته دار فرانسوی به آن آب جوش اضافه کنید. به اندازه یک قاشق غذاخوری آبلیمو و در ۱۰ دقیقه پایانی به مواد اضافه کنید. وقتی غلظت سس به حد کافی رسید و دیگر آبکی نبود، حرارت را قطع کرده و کوفته‌های مرغ را در ظرف مناسب سرو کنید. نکته: کوفته مرغ را با نان و سبزی خوردن تازه و ماست سرو کنید. می‌توانید در طرز تهیه این کوفته قلقلی و لذیذ از مقداری زنجبیل تازه رنده شده برای طعم و عطر بیشتر استفاده کنید. می‌توانید پیاز و فلفل دلمه را به جای رنده کرن، با مرغ در چرخ گوشت بیندازید و چرخ کنید. در صورت تمایل از یک قاشق چای‌خوری آرد برای انسجام بیشتر مواد این کوفته خوشمزه استفاده کنید. گاهی اوقات در طرز تهیه کوفته مرغ مانند کوفته تبریزی از برنج هم استفاده می‌شود. بهتر است قبل از گلوله کردن کوفته‌ها به مدل حداقل نیم‌ساعت مواد را در یخچال بگذارید. دستتان باز است که کشمش، زرشک و گردو را کلا از دستور پخت کوفته مرغ تبریزی مجلسی حذف کنید که در این صورت کوفته مرغ ساده خواهید داشت. می‌توانید کوفته‌ها را در آرد سوخاری بغلتانید و کوفته مرغ سوخاری مجلسی درست کنید. از اضافه کردن آب سرد به کوفته‌های مرغ خودداری کنید. این کار باعث می‌شود کوفته‌هایتان شل شده و از هم باز شود. اگر می‌خواهید کوفته مرغتان طعم ملس بگیرد از چند برگ زردآلو هم در سس کوفته مرغ استفاده کنید. همچنین می‌توانید به جای استفاده از زرشک درون کوفته‌ها، زرشک را سرخ کنید و در هنگام سرو روی کوفته‌ها بریزید. طرز تهیه کوفته مرغ ارومیه‌ای هم مشابه همین دستور پخت است و گاهی در سس آن از قارچ‌های ورقه‌ای سرخ شده استفاده می‌شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/01/a108335a1ba13ee4715b9c913ac70718-e1610293111586.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (2, 'پیاز داغ مجلسی', 'برای درست کردن پیاز داغ مجلسی، ابتدا پیازها را بعد از شستن و پوست گرفتن، به صورت خلال‌های یکدست خرد کنید.', 'برای درست کردن پیاز داغ مجلسی، ابتدا پیازها را بعد از شستن و پوست گرفتن، به صورت خلال‌های یکدست خرد کنید. خلال‌ها را حدود ۱۰ دقیقه توی آب جوش قرار دهید تا هم بوی تندش گرفته شود و هم کمی نرم شود، سپس آبکش کرده و روی یک دستمال تمیز پهن کنید تا کاملاً خشک شود. در تابه، به اندازه‌ای روغن بریزید که پیازها داخلش شناور باشند و وقتی روغن به اندازه کافی داغ شد، پیازها را کم‌کم اضافه کنید تا دمای روغن پایین نیاید. هر چند دقیقه یک‌بار به‌آرامی زیر و رو کنید تا رنگشان یکنواخت دربیاید. وقتی پیازها نیمه‌طلایی شدند، یک قاشق چای‌خوری زردچوبه اضافه کنید و یکی دو دقیقه تفت دهید تا عطر و رنگش آزاد شود. در پایان، پیاز داغ طلایی و ترد را با کفگیر از روغن خارج کرده و روی دستمال بگذارید تا روغن اضافی گرفته شود، سپس یا همان لحظه استفاده کنید یا بعد از خنک شدن داخل ظرف دربسته در یخچال یا فریزر نگهداری کنید. برای درست کردن پیاز داغ مجلسی چیپسی، پیازها را بشویید و پوست بگیرید. پیاز را از وسط دو نیم کنید و هر نیمه را به خلال‌هایی با ضخامت حدود نیم سانتی‌متر ببُرید. برش‌ها را در جهت خطوط روی پیاز بزنید و ضخامت خلال‌ها را یکسان بگیرید. یک قابلمه با سایز مناسب را پر از آب کنید و روی حرارت زیاد قرار دهید. بعد از اینکه آب به جوش آمد، خلال‌های پیاز را آروم تو آب بریزید و با قاشق کمی زیر و رو کنید. تقریباً ۱۰ دقیقه زمان کافیه؛ بعد از ۱۰ دقیقه، سریع پیازها را داخل آبکش بریزید و برای جلوگیری از ادامه پخت، می‌توانید چند ثانیه آب سرد روشان بگیرید. پیازها را حتماً روی یه دستمال تمیز آشپزخانه پخش کنید تا آب اضافه‌شان کامل خشک شود. یک تابه را روی حرارت متوسط بگذارید و روغن را اضافه کنید. روغن باید به حدی باشد که خلال‌ها کاملاً توش غوطه‌ور شوند. وقتی روغن خوب داغ شد، پیازها را کم‌کم داخل روغن بریزید. اگه حجم پیاز زیاده، حتماً تو چند سری سرخ کنید. مدام هم نزنید، فقط هر یکی دو دقیقه ملایم زیر و رو کنید. وقتی پیاز کمی رنگ گرفت و نیمه‌طلایی شد، زردچوبه را روی پیازها بپاشید و با ملایمت هم بزنید. بیش از یکی دو دقیقه بعد از اضافه کردن زردچوبه، پیاز را تو روغن نگه ندارید. وقتی پیاز به رنگ دلخواه رسید، با کفگیر از روغن دربیارید و روی دستمال آشپزخونه بچینید تا روغنش برود. بعد یا همون موقع استفاده کنید یا بگذارید خنک بشه و در ظرف دربسته تو یخچال یا فریزر نگهداری کنید. برای درست کردن پیاز داغ عسلی، پیازها را بعد از شستن و پوست گرفتن، خلالی و یکدست خرد کنید. توی تابه، روغن زیتون و کره بریزید و روی حرارت ملایم بگذارید تا آروم داغ بشه. وقتی روغن و کره گرم شد، خلال‌های پیاز را اضافه کنید و کمی زیر و رو کنید. بعد از حدود ۱۰ دقیقه پخت آروم، کمی نمک به پیازها بزنید. اگه می‌خواهید پیازها شیرین‌تر بشن، کمی شکر هم همین موقع اضافه کنید. برای تهیه پیاز داغ کاراملی، حرارت را پایین نگه دارید و ۳۰ تا ۴۵ دقیقه صبر کنید تا پیازها کم‌کم کاراملی بشن. یکی دو بار تو این مدت زیر و رو کنید و وقتی رنگشان شروع به تغییر بیشتری کرد، دفعات هم زدن را بیشتر کنید. وقتی پیازها طلایی-قهوه‌ای و شیرین شدند، از روی حرارت بردارید. می‌توانید همون موقع تو غذا یا سس استفاده کنید یا بعد از خنک شدن، تو ظرف دربسته بگذارید و تو یخچال تا یه هفته یا فریزر تا چند ماه نگه دارید. برای پیاز داغ رنده‌شده، پیاز را بعد از پوست گرفتن، رنده یا تو غذاساز له کنید. سپس با مقدار کم روغن و حرارت متوسط، تفت دهید تا آبش تبخیر شود و شروع به سرخ شدن کند. برای پیاز داغ پفکی، بعد از خلالی کردن پیازها، آنها را با مقداری نمک و کمی آرد سفید یا آرد برنج قاطی کنید. روغن را حسابی داغ کنید و پیازها را کم‌کم بریزید تا پف کنند. برای پیاز داغ بدون روغن، پیازها را خلالی یا ریز خرد کنید و توی تابه نچسب بریزید. کمی آب اضافه کنید تا پیازها تو بخار خودش نرم بشن. وقتی آب کم شد، حرارت را ملایم کنید و اجازه دهید خودش شروع به قهوه‌ای شدن کند. برای اینکه رنگش بهتر بشه، می‌توانید قبل از خشک شدن آب کمی زردچوبه بزنید. برای پیاز داغ در تابه رژیمی، پیاز را خلالی کنید و داخل تابه رژیمی بریزید. درب را بگذارید و حرارت را متوسط کنید تا پیازها با بخار خودش نرم بشن. بعد از باز کردن درب، کمی حرارت را بیشتر کنید تا آب اضافی برود و رنگش قهوه‌ای شود. برای پیاز داغ در مایکروفر، پیاز خلالی را توی ظرف مخصوص مایکروفر پخش کنید و کمی نمک یا زردچوبه هم بزنید. روی ظرف را با در مخصوص مایکروفر یا روکش مقاوم بپوشانید. هر ۲-۳ دقیقه یک بار درآورید و هم بزنید. برای پیاز داغ فرپز شده، فر را روی دمای حدود ۱۶۰ تا ۱۸۰ درجه روشن کنید. پیازهای خلال شده را روی کاغذ روغنی توی سینی پهن کنید و خیلی کم آب‌پاشی کنید یا با اسپری کمی روغن بپاشید. سینی را وسط فر بگذارید و هر چند دقیقه یک بار زیر و رو کنید تا طلایی شود. برای پیاز داغ در هواپز، پیاز خلالی را توی سبد هواپز بریزید و کمی نمک بزنید. دما را روی ۱۵۰ تا ۱۶۰ درجه تنظیم کنید و هر چند دقیقه یک بار سبد را تکان دهید. پیاز داغ آش بهتره کمی تیره‌تر و معطرتر باشه. یعنی پیازها را خلالی کنید، با روغن و کمی زردچوبه روی حرارت متوسط سرخ کنید تا طلایی-قهوه‌ای بشه. آخر کار هم کمی نعناع خشک تفت‌داده اضافه کنید. برای نگهداری، پیاز داغ را بگذارید کامل خنک بشه، بعد تو ظرف شیشه‌ای یا پلاستیکی دربسته بریزید. تو یخچال تا یه هفته می‌مونه، تو فریزر هم تا چند ماه قابل نگهداریه. بهتره پیازها را از قبل یکدست خرد کنید و تو چند مرحله سرخ کنید تا دمای روغن نیفته. استفاده از قابلمه یا تابه بزرگ و پهن کمک می‌کنه پیازها روی هم نریزن و یکنواخت سرخ بشن. وقتی پیازها طلایی روشن شدن، فوری از روغن دربیارین و روی دستمال جاذب بذارین چون تو گرمای خودشون کمی دیگه هم رنگ می‌گیرن. اگه صبر کنین تو روغن کاملاً قهوه‌ای بشن، بعد از سرد شدن تلخ میشن.', 28, 57, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/05/1-1-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (3, 'کوفته سبزی شیرازی', 'برای پختن کوفته سبزی شیرازی، ابتدا باید سس آن را درست کنید. در سس کوفته شیرازی، برخلاف دیگر کوفته‌ها، از مواد خود کوفته هم استفاده می‌شود و به همین خاطر هم به آش شباهت بیشتری دارد. بعد از پختن سس، کوف', 'برای پختن کوفته سبزی شیرازی، ابتدا باید سس آن را درست کنید. در سس کوفته شیرازی، برخلاف دیگر کوفته‌ها، از مواد خود کوفته هم استفاده می‌شود و به همین خاطر هم به آش شباهت بیشتری دارد. بعد از پختن سس، کوفته‌ها را باید درست کنید و مواد میانی را در آن‌ها بگذارید و فرم دهید. کوفته‌ها را در نهایت در سس قرار دهید و اجازه دهید کوفته‌ها در سس بپزند. حدود ۲۵ دقیقه تا ۱ ساعت باید زمان دهید تا کوفته‌ها کاملا نرم و آماده سرو شوند. در اولین مرحله از طرز تهیه کوفته سبزی شیرازی، عدس، لپه، گندم و برنج را به صورت جداگانه در ظرف از شب قبل یا به مدت ۲۴ ساعت خیس کنید. هنگام شروع به آشپزی، آن‌ها را آبکش کنید. سبزی‌ها را پاک کنید و بشویید. سپس اجازه دهید آب خود را از دست بدهند و کاملا خشک شوند. سپس سبزی‌ها را به صورت ساطوری خرد و کاملا ریز کنید. حتما اجازه دهید آب همه مواد برود وگرنه کوفته‌تان از هم می‌پاشد و وا می‌رود. در یک قابلمه به اندازه ۲ لیوان آب، عصاره قلم (اگر عصاره قلم در دسترس ندارید، می‌توانید از عصاره مرغ استفاده کنید یا کلا تنها از آب جوش استفاده کرده و عصاره را حذف کنید.)، نصف برنجی که داشتید و به اندازه یک مشت سبزی تازه خرد و ساطوری شده بریزید. حرارت را بالا ببرید و اجازه دهید مواد جوش بیایند. سپس حرارت را کم کنید و صبر کنید برنج نرم شود و لعاب بیندازد. اگر از رب گوجه فرنگی هم در سس کوفته می‌خواهید استفاده کنید، در همین مرحله باید به اندازه یک یا دو قاشق رب به مایه سس کوفته اضافه کنید. این سس باید در نهایت به غلظت برسد و شبیه آش شود. برای تهیه مایه کوفته شیرازی، پیاز را رنده کرده و آب آن را کاملا با دست بگیرید. گوشت، نصف برنج، لپه، عدس و گندم (بلغور گندم) را که خیسانده بودید و کمی نرم شده است، در مخلوط‌کن بریزید و مواد را با هم ترکیب کنید تا یک مخلوط یکدست داشته باشید. می‌توانید این مواد را در چرخ گوشت هم بریزید و دوبار چرخ کنید. سبزی‌های خرد و ساطوری شده را به همراه سبزی‌های خشک به مواد کوفته اضافه کنید. در این مرحله از طرز تهیه کوفته سبزی شیرازی، به ترکیب بالا، مقداری نمک، فلفل و زردچوبه (یا ادویه‌های دلخواه دیگر) بیفزایید. مواد را از مخلوط کن بیرون بیاورید. مایه کوفته را به مدت ۱۰ دقیقه به خوبی ورز دهید تا کاملا منسجم شوند و خودشان را بگیرند. مایه را باید چند بار داخل ظرف بکوبید تا چسبندگی بیشتری پیدا کند. در یک ظرف کمی روغن بریزید و روی حرارت قرار دهید. بعد از داغ شدن روغن، مقداری گردوی خرد شده و کشمش (یا زرشک) را برای چند ثانیه در روغن تفت دهید تا خامی آنها گرفته شود. می‌توانید به آن‌ها کمی زردچوبه هم اضافه کنید. مراقب باشید زیاد مواد را تفت ندهید تا نسوزد. شعله گاز را خاموش کنید و صبر کنید تا مواد از دما بیفتند. مقداری از کوفته به اندازه یک نارنگی، را بردارید و بین دست‌های‌تان بچرخانید تا شکل گلوله شود. مایه کوفته را وسط دست‌تان باز کنید و به اندازه دلخواه یا یک قاشق از مواد میانی را داخل کوفته بریزید. دوباره کوفته را با دست فرم دهید. همه کوفته سبزی‌ها را به همین شکل فرم دهید. سس کوفته که جوشید و به غلظت کافی رسید، کوفته‌ها را یکی یکی به آن اضافه کنید. کوفته‌ها را با فاصله از هم در سس در حال جوشیدن بگذارید. حرارت را روی حالت خیلی ملایم تنظیم کنید و درب قابلمه را بگذارید. بهتر است دور درب قابلمه را دم‌کنی بپیچید تا کوفته‌ها وا نروند. به مدت ۴۵ دقیقه تا ۱ ساعت (بسته به اندازه کوفته‌ها زمان پخت هم متغیر خواهد بود) صبر کنید تا کوفته‌ سبز‌ی‌هایتان بپزد. سپس آن‌ها را در ظرف مناسب سرو کنید. در هنگام سرو، مقداری از سس را روی کوفته‌ها بریزید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/04/%DA%A9%D9%88%D9%81%D8%AA%D9%87-%D8%B3%D8%A8%D8%B2%DB%8C.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (4, 'دنده کباب کرمانشاهی', 'دنده کباب یکی از لذیذترین انواع کباب ایرانی و جزو معروف ترین غذاهای کرمانشاهی است. طرز تهیه دنده کباب کرمانشاهی با گوشت گوسفندی و گوساله مرسوم است. برخی این غذای ایرانی خوشمزه با گوشت را در فر و برخی', 'دنده کباب یکی از لذیذترین انواع کباب ایرانی و جزو معروف ترین غذاهای کرمانشاهی است. طرز تهیه دنده کباب کرمانشاهی با گوشت گوسفندی و گوساله مرسوم است. برخی این غذای ایرانی خوشمزه با گوشت را در فر و برخی روی گاز یا در تنور درست می‌کنند. اما دنده کباب اصیل کرمانشاهی و دنده کباب شاندیز روی منقل ذغالی یا باربیکیو تهیه می‌شود و یک غذای ایرانی مجلسی برای شام شب یلدا و غذای مجلسی مناسب مهمانی هم هست. در ادامه ۳ روش طرز تهیه دنده کباب کرمانشاهی خوشمزه خانگی به سبک رستورانی در فر و روی زغال منقل و طرز تهیه سس مخصوص دنده کباب را به شما آموزش می‌دهیم. بسیاری از افراد دنده کباب را با شیشلیک اشتباه می‌گیرند. اما باید بدانید که روش پخت و آماده کردن دنده کباب طاق بستان با کباب شیشلیک متفاوت است و گاهی اوقات برای تهیه آن از گوشت چرخ کرده نیز در کنار دنده استفاده می‌شود. در این روش تهیه دنده کباب کرمانشاهی، باید تکه‌های گوشت مزه‌دار شده و استراحت داده شوند. سپس اقدام به کباب کردن آنها کنید. در ادامه مراحل درست کردن کباب دنده کرمانشاهی با گوشت گوسفند در فر را بخوانید. در اولین مرحله از طرز تهیه دنده کباب، گوشت دنده‌ گوسفندی را به خوبی بشویید و تکه تکه کنید. دنده‌ها را در یک کاسه بزرگ بریزید و چند قالب کوچک یخ و مقداری آب سرد روی آن بریزید و روی ظرف را بپوشانید. به مدت ۱ ساعت کاسه را در دمای محیط کنار بگذارید. سپس دنده‌ها را بیرون بیاورید و زیر آب بگیرید و در نهایت دنده‌ها را با دستمال حوله‌ای خشک کنید. در این مرحله، پوست سیر و پیاز را بگیرید. پیاز و سیر را رنده کنید. در یک کاسه، ماست، زعفران دم کرده، برگ بو، زردچوبه، نمک، پیاز و سیر رنده شده را با هم مخلوط کنید. تکه‌های دنده را به این مواد اضافه کنید. گوشت دنده را با مواد مرینیت ماساژ دهید تا مواد به همه‌جای گوشت‌ها برسد. سپس روی ظرف را بپوشانید و به مدت ۱۲ تا ۱۴ ساعت در یخچال بگذارید. در این فاصله سس دنده کباب را آماده کنید. برای این کار، کمی روغن مایع یا کره را در تابه بریزید و روی حرارت قرار دهید تا داغ شود. سپس رب گوجه فرنگی در تابه بریزید و آن را تفت دهید تا رنگ باز کند. نمک، فلفل سیاه، زعفران دم‌کرده و روغن کرمانشاهی را در تابه بریزید و کمی تفت دهید تا سس غلیظ شود. وقتی سس آماده شد، آن را از روی حرارت بردارید و کنار بگذارید. کف سینی فر را یک فویل آلومینیومی بکشید و دنده‌های مزه‌دار شده را در سینی فر بگذارید. می‌توانید زیر دنده‌ها، برش‌های گوجه‌ فرنگی حلقه حلقه شده بگذارید و روی دنده‌ها را حلقه‌های پیاز قرار دهید. روی دنده‌ها را با فویل بپوشانید. فر را روشن و روی دمای ۱۷۰ درجه سانتی‌گراد تنظیم کنید. دنده‌ها را به مدت ۹۰ دقیقه در فر بگذارید تا کاملا نرم و مغز پخت شود. بعد از یک ساعت، سینی فر را بیرون بیاورید و فویل را باز کنید تا روی دنده‌ها طلایی و برشته شود. وقتی دنده کباب آماده شد، سینی را از فر بیرون بیاورید و دنده‌های کباب شده را سرو کنید. در این روش تهیه کباب دنده، علاوه بر دنده گوسفندی، از گوشت چرخ کرده هم استفاده می‌شود که روی دنده‌ها را می‌پوشاند. این روش تهیه کباب دنده، طعم آن را بسیار لذیذ و خوشمزه‌ می‌کند و می‌توانید هم در فر، هم روی زغال و هم در تابه آن را تهیه کنید. در ادامه جزییات تهیه دنده کباب کرمانشاهی با گوشت چرخ کرده را بخوانید. در اولین مرحله از طرز تهیه دنده کباب کرمانشاهی باید گوشت گوسفند را به صورت درشت چرخ کنید. سپس پیاز را رنده کنید و به همراه نمک و فلفل سیاه به گوشت اضافه کنید. یک دستکش به دست کنید و گوشت و پیاز را خوب با هم مخلوط کنید. این کار موجب می‌شود تا گوشت انسجام بیشتری داشته باشد و راحت‌تر روی سیخ قرار بگیرد. در دومین مرحله از تهیه دنده کباب رستورانی خوشمزه باید نحوه سیخ زدن دنده کباب را به صورت درست و اصولی انجام دهید. برای این کار لازم است که ابتدا دنده‌ها را با استفاده از سیخ پهن به سیخ بکشید. سپس گوشت چرخ کرده را در کنار دنده‌ها قرار دهید و با استفاده از یک چاقو یا کاردک گوشت چرخ کرده را در کنار دنده‌ها پهن کنید. از مهم‌ترین اصول نحوه سیخ زدن دنده کباب این است که ۳ عدد سیخ پهن را هم زمان برای سیخ کردن دنده کباب‌ها استفاده کنید. این کار موجب می‌شود که گوشت چرخ کرده از کنار دنده‌ها نریزد و جدا نشود. در سومین مرحله از تهیه دنده کباب خوشمزه کرمانشاهی باید منقل زغال را آماده کنید. سپس کباب‌ها را به آرامی روی منقل قرار دهید. ابتدا دنده کباب خوشمزه کرمانشاهی را روی حرارت قرار دهید تا گوشت چرخ کرده به گوشت دنده بچسبد. سپس مدام کباب را بچرخانید تا همه بخش‌های دنده کباب رستورانی خوشمزه به خوبی و به صورت یکدست پخته شود. در آخرین مرحله از درست کردن دنده کباب اصیل کرمانشاهی باید سس مخصوص آن را درست کنید. برای این کار مقداری کره داخل ماهیتابه بریزید. گاز را با حرارت ملایم روشن کنید و اجازه دهید کره آب شود. سپس پوست گوجه فرنگی‌ها را جدا کنید و آن‌ها را خرد کنید و به ماهیتابه اضافه کنید. گوجه فرنگی را خوب سرخ کنید تا به روغن بیفتد. سپس رب گوجه فرنگی را به همراه کمی زعفران، آب لیمو ترش و نمک و فلفل سیاه به آن اضافه کنید و خوب ترکیب کنید. سس را روی حرارت ملایم قرار دهید تا داغ بماند و جا بیفتد. در آخرین مرحله از طرز تهیه دنده کباب کرمانشاهی با دنده و گوشت چرخ کرده، بعد از آماده شدن دنده کباب، دو طرف کباب را به سس آغشته کنید و این کباب اصیل و خوشمزه را همراه با نان یا برنج چلو میل کنید. در این روش، دنده کباب با سس مخصوص روی زغال کباب می‌شود. این روش هم یک روش محبوب برای تهیه دنده کباب است که می‌توانید با انجام مراحل زیر، آن را درست کنید. در اولین مرحله از طرز تهیه دنده کباب کرمانشاهی، گوشت دنده گوسفندی را به‌ صورت پهن و دراز برش بزنید. سپس ۳ یا ۴ سیخ کباب پهن را داخل گوشت فرو کنید تا گوشت حالت خود را حفظ کند. این کار همچنین باعث می‌شود سیخ‌ها بتوانند وزن گوشت را تحمل کنند. با پشت یک چاقوی بلند یا بیفتک‌کوب روی گوشت ضربه بزنید تا گوشت کمی نرم شود. بعد سیخ‌های گوشتی را که کوبیده‌اید، به مدت ۴ تا ۵ ساعت در یخچال قرار دهید. در این فاصله سس دنده کباب را آماده کنید. برای این کار، کمی روغن مایع یا کره را در تابه بریزید و روی حرارت قرار دهید تا داغ شود. سپس رب گوجه فرنگی در تابه بریزید و آن را تفت دهید تا رنگ باز کند. نمک، فلفل سیاه، زعفران دم‌کرده و روغن کرمانشاهی را در تابه بریزید و کمی تفت دهید تا سس غلیظ شود. وقتی سس آماده شد، آن را از روی حرارت بردارید و کنار بگذارید. در این مرحله، بعد از استراحت کردن گوشت‌ها، آنها را از یخچال بیرون بیاورید و باربیکیو یا منقل و زغال را آماده کنید. پس از آماده و سرخ شدن زغال‌ها، سیخ‌های کباب را روی منقل قرار دهید با استفاده از یک قلم‌موی آشپزی، سس را به‌صورت مرتب به دو طرف دنده‌ها بمالید. این نکته را حتما در نظر داشته باشید که حرارت در حین کباب کردن، باید بسیار ملایم باشد تا دنده‌ها کاملا مغزپخت شوند و آبدار بمانند. دنده‌ها که پختند و طلایی شدند، آن‌ها را از سیخ بکشید. در آخر کمی روی کباب‌ها را با روغن کرمانشاهی چرب کنید. دنده کباب را با نان با برنج و سبزی خوردن سرو کنید.', 40, 80, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/01/0fe82844-3110-4301-98f0-226de8644af0.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (5, 'کباب کوبیده', 'در شروع کار ابتدا گوشت چرخ‌ کرده را در یک ظرف بزرگ و گود بریزید و کمی نمک به آن اضافه کنید. گوشت چرخ کرده مخلوط گوسفند و گوساله شامل سر‌دست یا ران گوساله و قلوه‌گاه گوسفند.', 'در شروع کار ابتدا گوشت چرخ‌ کرده را در یک ظرف بزرگ و گود بریزید و کمی نمک به آن اضافه کنید. گوشت چرخ کرده مخلوط گوسفند و گوساله شامل سر‌دست یا ران گوساله و قلوه‌گاه گوسفند. گوشت و نمک را خوب ورز بدهید. در همین مرحله برای مزه‌دار کردن گوشت کوبیده می‌توانید فلفل سیاه و زعفران را هم اضافه کنید تا بوی زهم گوشت از بین برود. مایه کباب کوبیده را در یخچال بگذارید کمی به گوشت استراحت بدهید تا به اصطلاح بیات شود. در این مرحله پیازها را رنده کنید. یک نکته مهم این است که بعد از رنده کرده پیازها، آن‌ها را با دست فشار دهید و آب پیاز را بگیرید، می‌توانید برای گرفتن آب پیاز، پیازهای رنده شده را در پارچه تنظیف بریزید و با فشار دادن، آب پیاز را بگیرید. گرفتن آب پیاز سبب می‌شود تا کباب کوبیده خوب به سیخ بچسبد، روی سیخ وا نرود و توی آتش نریزد. پیازها را به گوشت اضافه کنید و حسابی ورز بدهید. هر چه گوشت و پیاز را بیشتر ورز بدهید کباب کوبیده شما خوشمزه‌تر می‌شود. بعد از این‌که ورز دادن گوشت تمام شد بهتر است به مدت ۲ تا سه ساعت روی گوشت یک کیسه پلاستیکی بکشید (برای این‌که رنگ گوشت تغییر نکند و سیاه نشود) و به مدت ۲ تا سه ساعت آن را در یخچال بگذارید. بعد از گذشت ۲ تا سه ساعت گوشت را از یخچال بیرون بیاورید. این مقدار گوشت کباب کوبیده برای ۶ نفر است. پس گوشت را به ۱۲ قسمت مساوی (هر قسمت به اندازه یک پرتقال متوسط) تقسیم کنید. در این مرحله هر قسمت از گوشت‌ها را دوباره کمی در دست ورز بدهید و به آن شکل یک بیضی بدهید. دست‌تان را با کمی آب مرطوب کنید و آن مقدار از گوشت را که برای هر سیخ کباب تقسیم‌بندی کرده‌اید بردارید و روی سیخ پهن کنید. طریقه سیخ زدن کباب کوبیده به این صورت است که بعد از این‌که گوشت را روی سیخ پهن کردید با دو انگشت اشاره و شصت (انگشت شصت روی گوشت و انگشت اشاره زیر گوشت) به آرامی روی گوشت فشارهای کوچک بیاورید تا پستی و بلندی‌های گوشت صاف شود و جای انگشتان‌تان روی گوشت به صورت یکی در میان فرو رفته باقی بماند. سر و ته گوشت را روی سیخ کمی بیشتر فشار دهید، تا موقع کشیدن کباب از روی سیخ به راحتی از آن جدا شود. گوجه فرنگی‌ها را هم سیخ بگیرید. سیخ‌های کباب کوبیده را ابتدا روی حرارت خیلی تند آتش کباب‌پز قرار دهید و وقتی که گوشت کباب خودش را گرفت و کمی جمع شد، شعله را ملایم کنید. اگر هم از منقل برای درست کردن کباب کوبیده استفاده می‌کنید، صبر کنید تا آتش منقل کاملا بگیرد و زغال‌ها سرخ شوند، سپس سیخ‌ها را به آرامی روی منقل بچینید. سیخ‌های گوجه را هم در انتهای منقل بچینید. بعد از آنکه کباب‌ها خودشان را گرفتند، آنها را به صورت مداوم بچرخانید تا به صورت یکنواخت بپزند. در نهایت کباب را با نان، گوجه کبابی، سماق و برنج چلو آبکش سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/07/%D8%B7%D8%B1%D8%B2-%D8%AA%D9%87%DB%8C%D9%87-%DA%A9%D8%A8%D8%A7%D8%A8-%DA%A9%D9%88%D8%A8%DB%8C%D8%AF%D9%87-%D8%AF%D8%B1-%D8%AE%D8%A7%D9%86%D9%87-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (6, 'ریزوتو ایتالیایی', 'برای تهیه ریزوتو از گوشت و یا از عصاره گوشت یا انواع سبزیجات استفاده کنید. برای تهیه ریزوتو با عصاره، ابتدا عصاره یا همان استاک گوشت، مرغ یا سبزیجات را گرم کنید. پیاز و سیر را پوست بگیرید و ریز خرد کن', 'برای تهیه ریزوتو از گوشت و یا از عصاره گوشت یا انواع سبزیجات استفاده کنید. برای تهیه ریزوتو با عصاره، ابتدا عصاره یا همان استاک گوشت، مرغ یا سبزیجات را گرم کنید. پیاز و سیر را پوست بگیرید و ریز خرد کنید. کرفس را ریز خرد کنید و پارمزان را رنده ریز، رنده کنید. در یک تابه جداگانه، روغن و ۱ قطعه کوچک کره را روی حرارت ملایم گرم کنید. پیاز، سیر و کرفس را به آن اضافه کنید و به مدت حدود ۱۵ دقیقه به آرامی تفت دهید؛ یا تا زمانی که نرم شود، اما رنگ آن تغییر نکند. برنج را اضافه کنید و حرارت را زیاد کنید و برنج را هم بزنید تا کمی سرخ شود. بعد از ۱ دقیقه برنج کمی شفاف به نظر می رسد. یک ملاقه از عصاره را با کمی نمک دریا به ترکیب اضافه کنید. حرارت را ملایم کنید تا بجوشد و برنج خیلی زود نپزد. اضافه کردن ملاقه به ملاقه عصاره را ادامه ندهید تا نشاسته برنج خارج شود. اجازه دهید هر ملاقه قبل از اضافه کردن ملاقه بعدی، به خوبی جذب شود. این کار حدود ۱۵ دقیقه طول خواهد کشید. برنج را بچشید؛ آیا پخته نشده است؟ اضافه کردن آب مایع را ادامه دهید تا برنج نرم شود اما با کمی لقمه. فراموش نکنید که چاشنی را به دقت بررسی کنید. اگر قبل از پختن برنج، عصاره شما تمام شد، مقداری آب جوش اضافه کنید. تابه را از روی حرارت برداشته و مقداری کره و پنیر پارمزان را اضافه کنید؛ سپس خوب هم بزنید. درب ظرف را بگذارید و اجازه دهید ۲ دقیقه بماند. این مهمترین قسمت پخت ریزوتو ایتالیایی است زیرا این حالت به ریزوتو اجازه می دهد که به خوبی خامه ای و عطرآگین شود. حالا هرچه زودتر آن را بخورید، قبل از اینکه سرد شود و بافت زیبای خود را از دست بدهد. نوش جان. فر را با ۱۸۰ درجه سانتیگراد گرم کنید. کدو حلوایی را در مکعب های ۱.۵ سانتی متری خرد کنید. تکه های کدو حلوایی را روی سینی فر قرار دهید، روی آن کمی آب بپاشید، سپس به مدت ۳۰ دقیقه در فر قرار دهید. در حالی که کدو حلوایی در حال رست شدن است، می توانید ریزوتو درست کنید. سیر را له کنید و پیازچه ها را خرد کنید. ۱ قاشق غذاخوری روغن را با کره در تابه و با حرارت متوسط گرم کنید. پیازچه و سیر را اضافه کنید. وقتی پیازچه ها نرم شدند اما قهوه ای نشدند، برنج و زیره را اضافه کنید. خوب هم بزنید و حدود ۱ دقیقه در مخلوط کره قرار گیرد. حالا نصف فنجان از عصاره را اضافه کنید و هر از چند گاهی هم بزنید تا همه آن جذب برنج و تبخیر شود. این کار را تکرار کنید تا باقیمانده عصاره تمام شود و به خوبی به خورد برنج برود. این کار حدود ۲۰ دقیقه طول خواهد کشید. چک کنید که برنج پخته شده است. اگر هنوز پخته نشده است، مقدار آب جوش اضافه کنید و اجازه دهید فرآیند پخت ادامه پیدا کند تا برنج ها به خوبی نرم شوند و برای خوردن مناسب باشند. حالا پنیر رنده شده، گشنیز خرد شده و کدو حلوایی رشد شده را به آن اضافه کنید و به آرامی هم بزنید. غذای ایتالیایی ریزوتو با کدو حلوایی آماده سرو شدن است. نوش جان.', 25, 50, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/11/risotto-7-e1605377978462.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (7, 'آبگوشت بوقلمون مجلسی', 'در اولین مرحله، نخود و لوبیا را برای چند ساعت در آب بخیسانید و در این مدت چند بار آب آن‌ها را عوض کنید. سپس گوشت بوقلمون را به چند تکه تقسیم کنید. پیاز را به صورت نگینی درشت خرد کنید.', 'در اولین مرحله، نخود و لوبیا را برای چند ساعت در آب بخیسانید و در این مدت چند بار آب آن‌ها را عوض کنید. سپس گوشت بوقلمون را به چند تکه تقسیم کنید. پیاز را به صورت نگینی درشت خرد کنید. حبه‌های سیر را پوست بگیرید. سیب زمینی‌ها را بشویید، پوست بگیرید و هر کدام را از وسط برش بزنید و به ۲ قسمت تقسیم کنید. گوجه فرنگی‌ها را بشویید، پوست بگیرید و هر کدام را به ۲ نیم تقسیم کنید. هویج و ساقه‌های کرفس را نیز بشویید و به قطعات نسبتا درشت تقسیم کنید. برگ‌های جعفری تازه را از ساقه جدا کرده، بشویید و ساطوری کنید. لیمو عمانی‌ها را هم برای ۱۵ تا ۲۰ دقیقه در مقداری آب ولرم بخیسانید. مقدار کمی روغن را داخل قابلمه مناسبی بریزید و روی حرارت متوسط بگذارید تا داغ شود. سپس، پیاز‌های نگینی را به قابلمه اضافه کنید و کمی تفت دهید تا سبک شوند. در ادامه، گوشت بوقلمون را هم در قابلمه بریزید و تفت دهید تا رنگ آن تغییر کند. بعد از اینکه بوقلمون را کمی تفت دادید، مقداری فلفل سیاه و زردچوبه را نیز به آن اضافه کرده و صبر کنید تا عطر زردچوبه آزاد شود. سپس، رب گوجه فرنگی را هم در قابلمه بریزید و همراه با پیاز، گوشت بوقلمون و فلفل و زردچوبه تفت دهید تا رنگ باز کند. حالا، حبوبات خیس خورده را در سبد بریزید و پس از آبکشی به قابلمه حاوی گوشت بوقلمون اضافه کنید. مقداری آب جوش نیز در قابلمه بریزید. میزان آب باید به اندازه‌ای باشد که سطح مواد داخل قابلمه را بپوشاند. پس از جوشیدن محتویات داخل قابلمه، دارچین و برگ بو را اضافه کرده، درب قابلمه را ببندید و حرارت را ملایم کنید. حدود ۳ الی ۴ ساعت منتظر بمانید تا گوشت بوقلمون همراه با حبوبات، ادویه‌ها و رب گوجه فرنگی بپزد و طعم‌دار شود. پس از گذشت این مدت، لیمو عمانی‌های خیس خورده و درسته را که حالا کاملا نرم شده‌اند با چنگال سوراخ کنید. در صورت تمایل، می‌توانید لیمو عمانی‌ها را با چاقو نصف کرده و هسته‌های آن را خارج کنید تا طعم آبگوشت شما تلخ نشود. سپس، قطعات لیمو عمانی بدون هسته را در قابلمه بریزید. پس از گذشت نیم ساعت، هویج، گوجه، کرفس و سیر را نیز به قابلمه اضافه کنید. درب قابلمه را دوباره ببندید و نیم ساعت دیگر صبر کنید تا همه این مواد در کنار یکدیگر بپزند و طعم‌دار شوند. در ۴۵ دقیقه پایانی پخت آبگوشت بوقلمون، وقت آن است که مقداری نمک، جعفری ساطوری شده و قطعات سیب زمینی را نیز به قابلمه اضافه کنید. اگر احساس می‌کنید آب قابلمه خیلی کم شده، مقدار مناسبی آب جوش نیز به این ترکیب اضافه کنید. درب قابلمه را ببندید و حدود ۴۵ دقیقه دیگر منتظر بمانید تا سیب زمینی و جعفری هم روی حرارت ملایم با باقی مواد بپزند و آبگوشت بوقلمون شما جا افتاده شود. پس از گذشت این مدت، درب قابلمه را باز کرده و از پخت کامل مواد مطمئن شوید. سپس، حرارت را خاموش کنید و آبگوشت بوقلمون را در ظرف سرو بریزید. در صورت تمایل، می‌توانید آب غذا را به تنهایی در ظرف بریزید و گوشت و سایر مخلفات را به صورت جداگانه بکوبید، درست مثل آبگوشت دیزی سنتی. یادتان باشد، برگ بوها را از غذا خارج کرده و دور بریزید. آبگوشت بوقلمون را می‌توانید همراه با نان، برنج کته یا چلو آبکش، انواع ترشی خانگی و سبزیجات تازه سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/11/abgoosht-iranian-custom-1024x768.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (8, 'آبگوشت دیزی خانگی سنتی', 'برای اینکه نفخ حبوبات گرفته شود، به مدت ۲۴ ساعت نخود و لوبیای سفید را در آب بخیسانید.', 'برای اینکه نفخ حبوبات گرفته شود، به مدت ۲۴ ساعت نخود و لوبیای سفید را در آب بخیسانید. پوست گوجه فرنگی‌ها را جدا کنید. آن را به همراه گوشت، حبوبات، ۱ عدد پیاز کامل و ۳ عدد سیر داخل یک قابلمه بزرگ بریزید. تقریبا ۶ برابر حجم مواد به آن آب اضافه کنید و روی شعله گاز قرار دهید. بعد از حدود یک ساعت و نیم که از پخت مواد گذشت، فلفل سبز را به دیزی اضافه کنید. شعله را تا حد امکان کم کنید تا غذا به آرامی جا بیفتد. بعد از اضافه کردن فلفل سبز، یک ساعت صبر کنید. سپس سیب زمینی‌ها را به آبگوشت اضافه کنید. برای این مرحله پوست سیب زمینی را جدا کنید و آن را به صورت درسته داخل دیزی بیندازید. حالا پیازی را که به صورت درسته داخل آبگوشت ریخته بودید را خارج کنید و دور بیندازید. چربی گوشت را از آن جدا کنید و به همراه یک پیاز خام با گوشت‌کوب بکوبید. برای این کار می‌توانید از میکسر یا گوشت کوب برقی نیز استفاده کنید. بعد از اینکه پیاز و چربی به خوبی با هم ترکیب شدند، آن را به دیزی اضافه کنید. در این مرحله گوشت و حبوبات به خوبی پخته شده‌اند و زمان اضافه کردن نمک و فلفل به غذا است. اگر احساس کردید رنگ غذا کم است می‌توانید یک قاشق رب گوجه فرنگی که در روغن تفت داده‌اید را به دیزی اضافه کنید. هر چقدر دیزی شما آرام‌تر و با شعله ملایم‌تری پخته شود، خوش رنگ‌تر و جا افتاده‌تر خواهد شد. معمولا بیشتر افراد در زمان سرو کردن دیزی، گوشت و مخلفات آن را از آب جدا می‌کنند و به صورت کوبیده می‌خورند. برای این کار می‌توانید آب را در ظرف جداگانه سرو کنید. بعد از جدا کردن استخوان گوشت، مواد دیزی را به وسیله یک گوشت کوب چوبی به خوبی له کنید و در ظرف جداگانه‌ای سرو کنید. ترشی، پیاز و سبزی خوردن را در کنار دیزی فراموش نکنید و از طعم لذیذ و خوشمزه این غذا در کنار خانواده لذت ببرید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/07/%D8%B7%D8%B1%D8%B2-%D8%AA%D9%87%DB%8C%D9%87-%D8%A2%D8%A8%DA%AF%D9%88%D8%B4%D8%AA-%D8%AF%DB%8C%D8%B2%DB%8C-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (9, 'آش رشته', 'در اولین مرحله، حبوبات (عدس، لوبیا سفید، لوبیا چیتی و نخود) را به صورت جداگانه پاک کرده و بشویید. آن‌ها را حداقل ۲ ساعت بخیسانید. بهتر است نخود و لوبیاها را از شب قبل خیس کنید و هر چند ساعت یک بار آب', 'در اولین مرحله، حبوبات (عدس، لوبیا سفید، لوبیا چیتی و نخود) را به صورت جداگانه پاک کرده و بشویید. آن‌ها را حداقل ۲ ساعت بخیسانید. بهتر است نخود و لوبیاها را از شب قبل خیس کنید و هر چند ساعت یک بار آب آن‌ها را عوض کنید تا نفخشان گرفته شود. نخود را آبکش کرده، بشویید و در یک قابلمه با ۵ لیوان آب روی حرارت بگذارید تا بپزد. در قابلمه‌ای دیگر، لوبیاها را با آب بپزید. حبوبات باید نرم شوند اما له نشوند. در این فاصله، سبزی آش را تمیز کرده، بشویید و درشت‌تر از سبزی قورمه سبزی خرد کنید. پیازها را پوست بگیرید و خلالی باریک یا نگینی ریز خرد کنید. در تابه‌ای مقداری روغن داغ کرده و پیازها را تفت دهید تا سبک و طلایی شوند. پیاز داغ را کنار بگذارید. پوست سیرها را جدا کرده و خرد کنید. سیرها را در تابه با روغن داغ برای ۱ دقیقه تفت دهید تا عطر آن‌ها خارج شود. مراقب باشید نسوزند. سیرها را از تابه خارج کنید. سپس نعناع خشک را در همان روغن تفت دهید تا رنگ آن عوض شود و حرارت را قطع کنید. در قابلمه مناسب، عدس آبکش شده را بریزید و تا ۱/۳ حجم آن آب اضافه کنید. حرارت را بالا ببرید تا آب به جوش آید. بعد از جوش آمدن آب، لوبیاها و نخود پخته را اضافه کنید و هم بزنید تا دوباره آب به جوش آید. بعد از جوشیدن آب، سبزی آش خرد شده را به قابلمه اضافه کنید. اگر از سبزی خشک استفاده می‌کنید، آن را با مقداری آب خیس کرده، آبکش کنید و با کمی کره یا روغن تفت دهید و به آش اضافه کنید. نصف قاشق چای‌خوری نمک اضافه کنید تا رنگ سبزی‌ها تغییر نکند. نصف پیاز داغ، نعناع داغ و سیر داغ، زردچوبه و فلفل سیاه را به قابلمه آش رشته اضافه کنید و هم بزنید. درب قابلمه را نبندید تا رنگ آش تیره نشود. به مدت ۴۰ دقیقه روی حرارت ملایم بپزند. بعد از ۴۰ دقیقه، پخته شدن سبزی‌ها را چک کنید. رشته‌های آش را از وسط نصف کرده و به داخل قابلمه بریزید. به آرامی آش را هم بزنید تا رشته‌ها خرد نشوند. به مدت ۲۰ دقیقه صبر کنید تا رشته‌ها نرم شوند. به مقدار دلخواه کشک را که با کمی آب رقیق کرده‌اید، به آش اضافه کنید. طعم آش را بچشید و به مقدار دلخواه نمک، فلفل سیاه یا چاشنی‌های دیگر اضافه کنید. وقتی آش به اندازه کافی لعاب انداخت، حرارت را قطع کنید و آش رشته را در ظرف مناسب سرو بریزید. روی آش رشته را با پیاز داغ، سیر داغ و نعناع داغ باقی‌مانده، مقداری کشک زعفرانی و ساده تزیین کرده و گرم سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/01/Ash-reshteh-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (10, 'آلبالو پلو با گوشت چرخ کرده قلقلی', 'ابتدا به مدت ۱ الی ۲ ساعت برنج را با آب و نمک بخیسانید. آلبالوهای شسته شده را هسته‌گیری کنید.', 'ابتدا به مدت ۱ الی ۲ ساعت برنج را با آب و نمک بخیسانید. آلبالوهای شسته شده را هسته‌گیری کنید. سپس آلبالوهای هسته‌گرفته را در یک قابلمه ریخته و شکر را لابه‌لای آن‌ها بریزید (اگر آلبالو پلو را ملس می‌پسندید برای این مقدار آلبالو از ۲۰۰ گرم شکر استفاده کنید). قابلمه آلبالو و شکر را روی شعله ملایم گاز بگذارید و بعد از به جوش آمدن این ترکیب اجازه دهید آلبالوها به مدت ۳۰ دقیقه با شکر بجوشند. بعد از این مدت آلبالوها را آبکش کنید و آب آلبالو را در یک شیشه تمیز بریزید. از این آب می‌توانید به عنوان شربت آلبالو استفاده کنید. گوشت چرخ کرده را با یک عدد پیاز متوسط رنده شده که آب آن را گرفته‌اید، نمک و فلفل سیاه خوب ورز دهید و به صورت گوشت قلقلی‌های کوچک به اندازه یک گردو درآورید. گوشت قلقلی‌ها را در روغن داغ سرخ کنید. در یک قابلمه آب را جوش بیاورید و برنج را در آن بریزید. برای اینکه برنج شفته نشود موقع آبکش کردن سطح روی برنج نرم و مغز آن باید سفت باشد. در این مرحله برنج را آبکش کنید. در قابلمه‌ای که می‌خواهید آلبالو پلو را دم کنید مقداری روغن داغ کنید و کف آن را با نان یا حلقه‌های سیب‌ زمینی بپوشانید. می‌توانید روی نان یا سیب‌ زمینی مقداری کنجد بپاشید. حالا به صورت لایه لایه برنج و آلبالو و گوشت قلقلی در قابلمه بریزید، لایه آخر باید برنج باشد. می‌توانید در صورت تمایل گوشت را لای برنج دم نکنید و بعد از پایان پخت آلبالو پلو، گوشت‌های قلقلی را روی برنج سرو کنید. وقتی که تمام مواد را در قابلمه لایه لایه ریختید پلو را به مدت ۴۰ الی ۶۰ دقیقه روی شعله ملایم گاز دم بگذارید. بعد از دم کشیدن پلو کمی از برنج روی آن را بردارید، با زعفران ترکیب کنید و روی آلبالو پلو برنج زعفرانی بریزید. در صورت تمایل آلبالو پلو با گوشت چرخ کرده را با خلال پسته و بادام تزیین کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/08/albaloo3-1.webp', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (11, 'آلو تیکی یا آلو تیکا', 'پوست سیب زمینی‌ها را بشویید و آنها را در قابلمه قرار دهید. روی سیب زمینی‌ها را مقداری آب، در حدی که آنها را بپوشاند، بریزید و قابلمه را روی حرارت بگذارید. اجازه دهید سیب زمینی‌ها برای نیم ساعت آبپز شو', 'پوست سیب زمینی‌ها را بشویید و آنها را در قابلمه قرار دهید. روی سیب زمینی‌ها را مقداری آب، در حدی که آنها را بپوشاند، بریزید و قابلمه را روی حرارت بگذارید. اجازه دهید سیب زمینی‌ها برای نیم ساعت آبپز شوند. می‌توانید سیب زمینی‌ها را بخارپز هم بکنید. در فاصله ای که سیب زمینی‌ها در حال پختن هستند، گشنیز، جعفری، پیازچه و فلفل سبز تند را بشویید و در آبکش بگذارید تا آب اضافی آنها گرفته شود. سپس همه این سبزی‌ها را روی تخته آشپزخانه به صورت ریز و ساطوری خرد کنید و کنار بگذارید. اگر سبزی تازه ندارید، می‌توانید از سبزی خشک استفاده کنید. بعد از آبپز شدن سیب زمینی‌ها، آنها را زیر آب سرد بگیرید تا سرد شوند و پوست آنها راحت‌تر جدا شود. سیب زمینی‌ها را پوست بگیرید و در یک کاسه بریزید. با گوشتکوب یا چنگال، سیب زمینی‌ها را له کنید. بعد سبزی‌های ساطوری شده را به آنها اضافه کنید. آرد برنج، فلفل سیاه، نمک، پودر سیر یا ادویه‌های دلخواه را هم به مایه آلو تیکا بیفزایید. در این مرحله، شروع به ورز دادن مواد کنید تا همه ترکیبات آن، با هم مخلوط و یکدست شوند. مایه آلو تیکا باید در آخر به اندازه‌ای میکس شود که به دست نچسبد. سپس روی ظرف را بپوشانید و مایه کتلت هندی را در یخچال به مدت ۳۰ دقیقه استراحت دهید تا مزه‌ها به خورد هم بروند. در یک تابه مقداری روغن سرخ کردنی بریزید و روی حرارت بگذارید تا داغ شود. سپس به اندازه یک نارنگی یا کمتر، از مایه آلو تیکی بردارید و مثل طرز تهیه شامی کباب بین دست‌هایتان به شکل گلوله یا بیضی کرده و آن را صاف کنید. سپس در روغن بگذارید. همه آلو تیکاها را به همین روش، سرخ کنید تا هر دو طرف آلو تیکی‌ طلایی شود. هر کتلت هندی که درست می‌کنید، آن را در یک بشقاب که کف آن دستمال حوله‌ای انداخته‌اید، بگذارید تا روغن اضافی آن گرفته شود. می‌توانید کتلت هندی آلوتیکی را با نان، سبزی خوردن و انواع سس، به خصوص سس‌های تند مثل سس ماست، سس فلفل تند یا سس کچاپ سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/02/d7046acb-c709-45b1-b499-9e985442b14d-e1613480612623.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (12, 'آلو گوبی', 'سیب زمینی‌ها را بشویید و همراه با ۲ لیوان آب در قابلمه بریزید. بسته به نوع سیب زمینی، قابلمه را به مدت ۲۰ تا ۴۰ دقیقه روی حرارت متوسط بگذارید تا سیب زمینی‌ها کاملا آب‌پز شوند.', 'سیب زمینی‌ها را بشویید و همراه با ۲ لیوان آب در قابلمه بریزید. بسته به نوع سیب زمینی، قابلمه را به مدت ۲۰ تا ۴۰ دقیقه روی حرارت متوسط بگذارید تا سیب زمینی‌ها کاملا آب‌پز شوند. گل کلم را بشویید و در ابعاد نسبتا مساوی و به صورت گل‌های کوچک خرد کنید. مقداری آب و اندکی نمک را در یک قابلمه بریزید و روی حرارت قرار دهید تا بجوشد. گل کلم را به مدت ۱۵ دقیقه در آب بجوشانید. پیاز را به صورت نگینی خرد کنید و همراه با مقداری روغن تفت دهید تا سبک و طلایی شود. در ادامه، سیر و زنجبیل را هم پوست بگیرید و به صورت ریز رنده کرده و به تابه حاوی پیاز داغ اضافه کنید. زردچوبه، فلفل سیاه، فلفل قرمز، مقدار کمی نمک و ادویه گرام ماسالا را نیز داخل تابه بریزید. این ترکیب را یکی دو دقیقه تفت دهید تا عطر و رنگ ادویه‌ها آزاد شود. حالا، سیب زمینی و گل کلم آب‌پز شده را به قطعات دلخواه خرد کنید و به این تابه اضافه کنید. مواد داخل تابه را هم بزنید تا با یکدیگر ترکیب شوند و به خوبی تفت ببینند. در نهایت، رب گوجه فرنگی را هم در تابه بریزید و سرخ کنید تا رنگش باز شود و طعم خامی خود را از دست بدهد. گوجه‌ها را پوست بگیرید و رنده کرده یا با کمک گوشتکوب برقی یا دستگاه غذاساز، پوره کنید. سپس، پوره گوجه فرنگی را همراه با آب لیموی تازه به باقی مواد اضافه کنید. این ترکیب را هم بزنید و طعم آن را بچشید تا از مناسب بودن میزان نمک و تندی غذا مطمئن شوید. حالا، درب تابه را بگذارید و حرارت را کاملا ملایم کنید تا خوراک آلو گوبی شما جا افتاده شود و روغن بیندازد. پس از جا افتادن خوراک آلو گوبی، تابه را از روی حرارت بردارید. تا وقتی غذا کاملا داغ است مقداری برگ جعفری و گشنیز تازه و خرد شده را نیز روی آن بریزید. این خوراک هندی خوشمزه و تند و تیز را می‌توانید همراه با نان سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/08/%D8%A2%D9%84%D9%88-%DA%AF%D8%A8%DB%8C-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (13, 'آلو متر هندی', 'سیب زمینی‌ها را بشویید، پوست بگیرید و در ابعاد نسبتا درشت برش بزنید.', 'سیب زمینی‌ها را بشویید، پوست بگیرید و در ابعاد نسبتا درشت برش بزنید. گوجه فرنگی‌های شسته شده را پوست بگیرید و پوره کنید. نخود فرنگی تازه را از غلاف خارج کرده و بشویید. اگر از نخود فرنگی منجمد استفاده می‌کنید صبر کنید تا یخ آن باز شود. اگر از کنسرو استفاده می‌کنید، ۲۰ دقیقه قوطی کنسرو را بجوشانید. گشنیز را پاک کرده، بشویید و ساطوری کنید. مقداری روغن در تابه بریزید و روی حرارت متوسط بگذارید تا داغ شود. پیاز خرد شده را اضافه کنید و تفت دهید. سیر و زنجبیل را پوست بگیرید و ریز رنده کنید. پوره سیر و زنجبیل را به تابه اضافه کرده و همراه با پیاز برای یک دقیقه تفت دهید. زردچوبه، ادویه کاری، زیره، گرام ماسالا، فلفل سیاه و فلفل قرمز را در تابه بریزید و برای مدت کوتاهی تفت دهید تا عطرشان آزاد شود. پوره گوجه فرنگی را با مقداری نمک به محتویات تابه اضافه کنید. هم بزنید و یکی دو دقیقه منتظر بمانید تا پوره گوجه فرنگی سرخ شود. یک و نیم لیوان آب جوش به تابه اضافه کنید. مقدار آب باید به قدری باشد که روی سیب زمینی‌ها را بپوشاند. نخود فرنگی را نیز به باقی مواد اضافه کنید. محتویات تابه را هم بزنید تا همه مواد یکنواخت مخلوط شوند. درب تابه را ببندید و حرارت را ملایم کنید. حدود نیم ساعت منتظر بمانید تا مایه خوراک آلو متر به آرامی قل بزند و کاملا جا افتاده شود. پس از ۳۰ دقیقه، تابه را از روی حرارت بردارید و خوراک آلو متر هندی را در ظرف سرو بریزید. گشنیز ساطوری شده را روی آن بریزید. این غذا را می‌توانید به صورت خورش با برنج کته یا به صورت خوراک با نان سرو کنید.', 13, 27, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/08/%D8%A2%D9%84%D9%88-%D9%85%D8%AA%D8%B1-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (14, 'خورش اسپناساک (اسفناج) مازندرانی', 'قبل از هر چیز، نخود را به مدت ۲ ساعت بخیسانید و چند بار هم آب نخودها را عوض کنید. اسفناج را به خوبی بشویید. حتما اسفناج‌ها را حداقل برای مدت ۲۰ دقیقه در آب نگه دارید تا گل و لای آنها پاک شود. بعد از ا', 'قبل از هر چیز، نخود را به مدت ۲ ساعت بخیسانید و چند بار هم آب نخودها را عوض کنید. اسفناج را به خوبی بشویید. حتما اسفناج‌ها را حداقل برای مدت ۲۰ دقیقه در آب نگه دارید تا گل و لای آنها پاک شود. بعد از این که اسفناج‌ها را آبکش کردید و آب خود را از دست داد، آنها را خرد کنید. پیازها را خلالی باریک یا نگینی ریز خرد کنید. سیر را هم رنده یا له کنید. گوشت را هم در قطعات مکعبی ۲ سانتی‌متری خرد کنید. یک قابلمه مناسب را روی حرارت ملایم قرار دهید و در آن روغن بریزید. وقتی به دمای مناسب رسید، پیازها را اضافه کنید و تفت دهید. پیازها که سبک و طلایی شدند، سیرها را اضافه کنید. سیر را حتما بعد از سرخ شدن پیاز اضافه کنید چون سیر خیلی زود می‌سوزد. گوشت‌ها را که از قبل شسته و به شکل خورشتی خرد کرده‌اید به پیاز و سیر در حال تفت خوردن اضافه کنید. تکه‌های گوشت را تفت دهید. بعد از اینکه گوشت کمی سرخ شد و تغییر رنگ داد، به آن زردچوبه و فلفل سیاه بزنید و مجددا تفت دهید. بعد از اینکه ادویه‌ها تفت خوردند و طعم خامی‌شان گرفته شد، سبزی‌های معطر را اضافه کنید. جعفری، گشنیز، تره و نعنا ساطوری و خرد شده را اضافه کنید و اجازه دهید کمی با گوشت سرخ شوند. سپس اسفناج را اضافه کنید. آن را هم کمی تفت دهید و حرارت را پایین بیاورید تا کمی سبزیجات و گوشت با آب خودشان بپزند. در این مرحله از طرز تهیه خورش اسپناساک، می‌توانید نخود را به ترکیب گوشت و سبزیجات اضافه کنید. مقداری آب جوش هم اضافه کنید و اجازه دهید گوشت و دیگر مواد به خوبی بپزند. این مرحله به حدود ۱ تا ۱/۵ ساعت زمان برای پخت گوشت و نخود نیاز دارید. بعد از یک تا یک ساعت و نیم زمانی که گوشت و نخود به خوبی پختند، هویج را بشویید و نگینی خرد کنید. هویج نگینی خرد شده را به مواد خورشت اضافه کنید. در این مرحله و زمانی که گوشت و نخود تا حدودی پختند نمک خورشت را هم اضافه کنید. تخم مرغ‌ها را در یک ظرف بشکنید و به خوبی به هم بزنید تا از حالت لختگی جدا شوند. در همین مرحله، تخم مرغ‌ها را به خورش اضافه کنید و به هم بزنید تا در کل خورش اسپناساک، مخلوط شوند. رب انار یا رب نارنج را هم می‌توانید به آن اضافه کنید تا غذا، طعم ترش یا ملس بگیرد. بعد از اینکه همه مواد خورشت اسفناج مازندرانی پختند و آب آن کاملا غلیظ شد، خورش را از روی حرارت بردارید. خورش اسپناساک را در ظرف مناسب به همراه نان یا برنج کته و آبکشی ساده یا زعفرانی سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/03/img1693191987-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (15, 'استامبولی پلو قالبی', 'برنج را از قبل به مدت حداقل ۱ ساعت در آب ولرم و نمک بخیسانید. استفاده از رب گوجه فرنگی در طرز تهیه استانبولی پلو کاملا اختیاری است ولی اگر قصد دارید از آن استفاده کنید، حتما کمی در روغن تفت بدهید تا ط', 'برنج را از قبل به مدت حداقل ۱ ساعت در آب ولرم و نمک بخیسانید. استفاده از رب گوجه فرنگی در طرز تهیه استانبولی پلو کاملا اختیاری است ولی اگر قصد دارید از آن استفاده کنید، حتما کمی در روغن تفت بدهید تا طعم خامی آن گرفته شود. برای کسانی که می خواهند رنگ استانبولی پلوشان قرمزتر شود، رب گوجه فرنگی بسیار مناسب است ولی اگر طعم آن را دوست ندارید، آن را حذف کنید. گوجه فرنگی ها را بشویید و به صورت مکعبی ریز خرد یا رنده و پوره کنید. پوست سیب زمینی ها را بگیرید و بشویید. بعد از این که آب آن ها خشک شد، به صورت نگینی خرد کنید. یک قابلمه را روی حرارت بگذارید و مقداری روغن در آن بریزید. سیب زمینی ها را سرخ کنید. لازم نیست کاملا طلایی شوند، همین که خامی آن ها گرفته شود، کافی است. سیب زمینی ها را از قابلمه بیرون بیاورید. رب گوجه فرنگی را در قابلمه بریزید. اجازه دهید رب گوجه در روغن تفت بخورد و رنگ پس بدهد. حرارت را روی ملایم تنظیم کنید تا رب گوجه نسوزد. بعد از این که رب گوجه سرخ و خامی آن گرفته شد، گوجه فرنگی را به آن اضافه کنید. اجازه دهید گوجه فرنگی هم طعم خامی خود را از دست بدهد. در همین مرحله، نمک، فلفل قرمز و زردچوبه را اضافه کنید. سیب زمینی ها را به گوجه فرنگی های سرخ شده اضافه کنید. برنج را از آبی که در آن خیسانید، بکشید و به قابلمه اضافه کنید. بسته به میزان آب گوجه فرنگی، به مواد داخل قابلمه آب اضافه کنید. آبی که اضافه می کنید باید حتما جوش باشد و حداقل به اندازه ۲ تا ۳ سانتی متر آب روی سطح برنج پوشانده شود. حرارت را بالا ببرید تا آب جوش بیاید و به خورد برنج برود و آن را نرم کند. در این فاصله، کف و دیواره قالبی را که برای استانبولی پلو در نظر گرفته اید با کمی روغن یا ترجیحا کره، چرب کنید. بعد از این که آب استانبولی پلو کشیده و برنج نرم شد، مخلوط را در قالب بریزید و روی آن را صاف و یکدست کنید. روی قالب را با فویل نازک بپوشانید. قالب را در فر بگذارید و روی حرارت ۱۸۰درجه سانتی گراد تنظیم کنید. ۳۰ تا ۴۵ دقیقه صبر کنید تا استانبولی پلو بپزد. سپس قالب را از فر خارج کنید و در ظرف مناسب برگردانید و سرو کنید. کنار یا روی استانبولی پلو را با سبزیجات خوردنی تازه تزیین کنید. استانبولی پلو با انواع سالاد و ماست سرو می شود.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/04/estamboloi99.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (16, 'استیک بوقلمون با سس قارچ', 'در اولین مرحله، یک عدد پیاز را به صورت خلالی درشت خرد کنید. پیاز دیگر را نگینی ریز خرد کرده و برای سس قارچ کنار بگذارید. حبه‌های سیر را پوست بگیرید و ریز رنده کنید.', 'در اولین مرحله، یک عدد پیاز را به صورت خلالی درشت خرد کنید. پیاز دیگر را نگینی ریز خرد کرده و برای سس قارچ کنار بگذارید. حبه‌های سیر را پوست بگیرید و ریز رنده کنید. ۴ قاشق غذاخوری آبلیموی تازه آماده کنید. نیمی از آن را برای مرینیت کردن گوشت بوقلمون و نصف دیگر را برای سس قارچ کنار بگذارید. قارچ‌ها را بشویید و اسلایسی برش بزنید. سینه بوقلمون را بشویید و به ۴ فیله با ضخامت ۲ تا ۳ سانتی‌متر برش بزنید. برای مرینیت کردن، فیله‌های بوقلمون را همراه با مقداری روغن زیتون، ۲ قاشق غذاخوری آبلیموی تازه، پیازهای خلالی شده و سیر رنده شده در یک کاسه بریزید و به مدت یکی دو دقیقه ماساژ دهید. مقداری نمک، فلفل سیاه، پودر سیر، پودر پیاز، آویشن، رزماری خشک شده و پودر پاپریکا را به گوشت بوقلمون اضافه کنید و دوباره به مدت یکی دو دقیقه ماساژ دهید تا کاملا طعم‌دار شوند. درب کاسه را ببندید و گوشت بوقلمون مرینیت شده را حداقل برای یک ساعت در یخچال بگذارید تا مزه‌دار شود و بوی زهم آن گرفته شود. هر چقدر زمان استراحت بیشتر باشد، استیک تردتر و خوشمزه‌تر خواهد شد. پس از گذشت این مدت، یک تابه چدنی یا تفلون شیاردار را روی حرارت زیاد بگذارید تا داغ شود. مقداری روغن زیتون در تابه بریزید. فیله‌های بوقلمون را از یخچال خارج کرده و در تابه بگذارید تا هر طرف آن به مدت ۵ تا ۶ دقیقه روی حرارت زیاد سرخ شود. پس از سرخ شدن هر دو طرف فیله‌ها، قطعات استیک را از تابه بیرون بیاورید و در ظرف مناسبی قرار دهید. روی ظرف را با فویل بپوشانید تا استیک‌ها به مدت ۱۰ دقیقه استراحت کنند. فیله‌های سرخ شده را روی یکدیگر قرار ندهید. برای تهیه سس قارچ، تابه مناسبی را روی حرارت متوسط بگذارید و مقداری کره و کمی روغن زیتون به آن اضافه کنید. پیازهای نگینی را در تابه بریزید و تفت دهید تا شیشه‌ای و سبک شوند. پس از سبک شدن پیازها، قطعات قارچ اسلایسی و آبلیموی تازه را نیز در تابه بریزید و تفت دهید. در این مرحله، به دلخواه می‌توانید نصف قاشق غذاخوری آرد سفید هم در تابه بریزید و با کره و روغن زیتون تفت دهید تا سس غلیظ‌تر شود. حالا، شیر، ماست و خامه را با یکدیگر مخلوط کنید و در تابه حاوی قارچ و پیاز بریزید. این ترکیب را به خوبی هم بزنید و پودر سیر، نمک و فلفل سیاه را هم اضافه کنید. سس را دوباره هم بزنید تا کاملا یکدست شود. به محض مشاهده اولین حباب‌ها، حرارت را خاموش کنید و هم زدن سس قارچ را برای یک دقیقه دیگر ادامه دهید. استیک بوقلمون استراحت کرده و آماده سرو است. قطعات استیک را در ظرف سرو بگذارید و با پوره سیب زمینی، انواع سبزیجات تازه، آبپز یا سرخ شده دورچین کنید. می‌توانید سس قارچ را روی استیک بوقلمون بریزید یا در ظرف جداگانه‌ای همراه با استیک سرو کنید. میزان پخت استیک بوقلمون کاملا سلیقه‌ای است. برای استیک رِیر (rare) هر طرف گوشت را ۳ الی ۴ دقیقه، برای مدیوم ریر (medium rare) ۴ تا ۵ دقیقه، برای مدیوم (medium) ۵ تا ۶ دقیقه و برای مدیوم وِل (medium well) ۶ تا ۷ دقیقه سرخ کنید. برای وِل دان (well done) مدت زمان طولانی‌تری نیاز است تا کاملا خشک شود. زمان پخت بسته به ضخامت فیله‌ها و میزان استراحت آنها ممکن است کمی بیشتر یا کمتر باشد. برای پخت در فر، فر را از قبل با دمای ۲۰۰ درجه سانتی‌گراد گرم کرده و سینی فر را چرب کنید. فیله‌های مزه‌دار شده را روی سینی بچینید و به مدت ۱۵ تا ۲۰ دقیقه در فر قرار دهید. در طول این مدت، یک‌بار قطعات گوشت بوقلمون را پشت و رو کنید تا اطراف آن به یک اندازه بپزد.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/frying-turkey-steaks.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (17, 'اسفناج پلو', 'ابتدا برنج را پاک کنید و با مقداری آب ولرم و نمک به مدت ۱ تا ۳ ساعت بخیسانید. اسفناج‌ها را هم پاک کنید و در یک تشت یا کاسه آب، به مدت ۱۵ دقیقه بخیسانید و از روی آب جمع کنید. اسفناج‌ها را در آبکش بریزی', 'ابتدا برنج را پاک کنید و با مقداری آب ولرم و نمک به مدت ۱ تا ۳ ساعت بخیسانید. اسفناج‌ها را هم پاک کنید و در یک تشت یا کاسه آب، به مدت ۱۵ دقیقه بخیسانید و از روی آب جمع کنید. اسفناج‌ها را در آبکش بریزید و اجازه دهید آب اضافی خود را از دست بدهند. برای درست کردن مایه گوشت چرخ کرده، یک عدد پیاز را پوست بگیرید و به صورت نگینی ریز یا خلالی باریک خرد کنید. در یک تابه، مقداری روغن یا کره بریزید و روی حرارت بگذارید تا داغ شود. سپس پیازها را در روغن یا کره تفت دهید تا سبک و طلایی شوند. سپس گوشت چرخ کرده را اضافه کنید و هم بزنید تا رنگ آن تغییر کند و به سفیدی بزند. به گوشت چرخ کرده بعد از تفت خوردن با پیاز، کمی زردچوبه، فلفل سیاه، پودر دارچین، کمی زعفران دم کرده و نمک اضافه کنید و هم بزنید. درب تابه را بگذارید و شعله را کم کنید تا گوشت کمی بپزد. سپس اسفناج‌ها را روی تخته آشپزخانه خرد کنید. حالا اسفناج‌ها را به گوشت اضافه کنید و تفت دهید. دوباره درب تابه را بگذارید تا گوشت با اسفناج بپزد. در این فاصله، یک قابلمه مناسب را تا نیمه آب کنید و روی حرارت بگذارید تا آب بجوشد. آبی که برنج را در آن خیسانده‌اید، دور بریزید. سپس برنج را در قابلمه آب در حال جوش بریزید. کمی روغن (در حد ۲ قاشق غذاخوری) و نمک در قابلمه بریزید و ۷ تا ۱۰ دقیقه برنج را بخیسانید تا وقتی که روی دانه برنج نرم و داخل آن کمی سفت باشد. سپس برنج را در آبکش بریزید و شوید خشک را روی برنج بریزید و با هم مخلوط کنید. قابلمه را با آب سرد خالی بشویید و روی حرارت بگذارید. کف قابلمه را کره یا روغن بریزید و ته دیگ دلخواهتان را قرار دهید. سپس یک لایه از شوید پلو و یک لایه از مایه اسفناج و گوشت چرخ کرده را بریزید. دوباره یک لایه شوید پلو و یک لایه از مایه اسفناج پلو اضافه کنید. لایه‌ها را به همین صورت تکرار کنید تا موادتان تمام شود و لایه آخر، شوید پلو باشد. دور درب قابلمه را دم‌کنی بپیچید و روی آن بگذارید. بعد از ۱ یا ۲ دقیقه که بخار در قابلمه پیچید، شعله را کم کنید یا شعله پخش کن بگذارید. اسفناج پلو با گوشت چرخ کرده به ۴۵ دقیقه زمان برای پخت نیاز دارد. بعد از دم کشیدن اسفناج پلو شعله گاز را خاموش کنید. کمی روی اسفناج پلو، زعفران دم کرده و کره یا روغن داغ بریزید. مواد اسفناج پلو را هم بزنید تا مواد به طور یکدست با هم مخلوط شوند. سپس اسفناج پلو را با ماست، انواع سالاد، انواع ترشی و … سرو کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/10/esfenaj-polo-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (18, 'اسکندر کباب', 'ابتدا گوشت را به صورت تکه‌های نازک به اندازه کف دست خرد کنید و ببرید.', 'ابتدا گوشت را به صورت تکه‌های نازک به اندازه کف دست خرد کنید و ببرید. سس مارینه را آماده کنید. برای درست کردن سس مارینه اسکندر کباب کافی است تمام مواد آن (شیر، پیاز، گوجه فرنگی، روغن مایع، سرکه، سیر، رب گوجه فرنگی، پودر زیره و آویشن، نمک، فلفل سیاه و قرمز) را با هم مخلوط کنید. سپس تکه‌های گوشت را به مدت چند ساعت در سس مارینه بخوابانید تا گوشت مزه‌دار شود. بعد از مزه‌دار شدن تکه‌های گوشت را از سس مارینه بیرون بیاورید و در یک تابه مقداری روغن بریزید. تکه‌های گوشت را در ماهیتابه بچینید. روی گوشت نمک و فلفل بریزید و با حرارت متوسط رو به بالا گوشت را سرخ کنید. در فاصله‌ای که گوشت سرخ می‌شود سس گوجه اسکندر کباب را هم آماده کنید. در یک قابلمه یا ماهیتابه جداگانه گوجه فرنگی‌های رنده شده، رب و کره را بریزید و با هم تفت دهید. نمک و فلفل سیاه را هم اضافه کنید. سس گوجه که غلیظ شد شعله گاز را خاموش کنید. بعد از اینکه دو طرف تکه‌های گوشت سرخ شدند باید غذا را سرو کنید. برای سرو این غذا هم از نان پیتا می‌توانید استفاده کنید و هم نان بربری. در ظرف مورد نظر برای سرو نان را کف ظرف بگذارید و نیمی از سس گوجه فرنگی را روی نان بریزید. سپس تکه‌های گوشت را روی نان بگذارید و تکه‌های گوشت را روی نان بچینید. باقی مانده سس گوجه فرنگی را روی تکه‌های گوشت بریزید و سپس ماست چکیده را اضافه کنید. روی ماست چکیده هم جعفری خرد شده بریزید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/01/%D8%A7%D8%B3%DA%A9%D9%86%D8%AF%D8%B1%DA%A9%D8%A8%D8%A7%D8%A8-%DB%B3.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (19, 'اشکنه', 'برای طرز تهیه اشکنه شیرازی، ابتدا پیاز را ریز خرد کنید و تفت دهید. بعد از این‌که پیازها سبک و طلایی شدند فلفل، نمک و زردچوبه را به پیازها اضافه کنید.', 'برای طرز تهیه اشکنه شیرازی، ابتدا پیاز را ریز خرد کنید و تفت دهید. بعد از این‌که پیازها سبک و طلایی شدند فلفل، نمک و زردچوبه را به پیازها اضافه کنید. سیب زمینی‌ها را نگینی خرد کنید و کمی در یک ظرف جداگانه تفت دهید. وقتی سیب زمینی‌ها مقداری سرخ شد آن‌ها را به پیازهای سرخ شده اضافه کنید. حالا آرد را هم اضافه کنید و همراه با سایر مواد تفت دهید. در مرحله آخر سبزی اصلی اشکنه، شنبلیله را اضافه کنید و خیلی کم تفت دهید تا هم تلخ نشود و هم عطر و بویش را حفظ کند. در این مرحله سه لیوان آب را اضافه کنید و اجازه دهید سیب زمینی‌ها خوب بپزند. وقتی از پخته شدن سیب زمینی‌ها مطمئن شدید تخم مرغ‌ها را به اشکنه شیرازی اضافه کنید و درب ظرف را بگذارید تا تخم مرغ‌ها خوب پخته شوند. تخم مرغ‌ها را هم نزنید تا به همان شکل درسته ببندند. زمانی که آب غذا کم شد و غلیظ شد اشکنه شیرازی جا افتاده و آماده خوردن است. می‌توانید روی اشکنه را با نعنا داغ تزیین کنید. اشکنه را با ترید نان نوش جان کنید. برخی هم اشکنه شیرازی را به این ترتیب درست می‌کنند که ماست یا دوغ و تخم مرغ را با هم مخلوط می‌کنند و سپس به مواد اشکنه اضافه می‌کنند. برای درست کردن مایه ماست و تخم مرغ باید هر کدام را جداگانه خوب هم بزنید و وقتی یکدست و صاف شدند با هم مخلوط کنید و سپس به غذا اشکنه اضافه کنید. در هر صورت اگر ماست یا دوغ را به تخم مرغ اضافه کردید حتما باید تا انتهای پخت اشکنه آن را هم بزنید تا بریده بریده نشود. برای درست کردن اشکنه سنتی تهرانی، پیاز را در یک قابلمه خلالی خرد کنید و در روغن یا کره سرخ کنید تا سبک و طلایی شوند. سپس پیازها را یک طرف قابلمه جمع کنید و در باقی مانده روغن رب گوجه فرنگی را تفت دهید تا رب باز شود و طعم خامی آن گرفته شود. حالا رب و پیاز را با هم مخلوط کنید و کمی دیگر با هم تفت دهید. در مرحله بعد نمک، فلفل و زردچوبه را به مقدار دلخواه اضافه کنید و تفت دهید تا طعم خامی ادویه‌ها گرفته شود. شنبلیله خشک شده یا تازه را اضافه کنید. اگر شنبلیله تازه باشد کمی بیشتری برای تفت زمان لازم است. اگر شنبلیله خشک باشد خیلی کم تفت دهید تا تلخ نشود. سپس به تدریج آرد را به سایر مواد اضافه کنید. خیلی سریع مواد را مرتب هم بزنید تا آرد گوله نشود. حالا سیب زمینی‌ها را نگینی خرد کنید و به مواد اضافه کنید. برای درست کردن اشکنه تهرانی نیازی نیست سیب زمینی‌ها را تفت دهید، کمی که مواد را با هم مخلوط کردید ۳ لیوان آب را اضافه کنید و زمانی که آب به جوش آمد شعله گاز را ملایم کنید تا اشکنه به آرامی بپزد و جا بیفتد. برای درست کردن اشکنه تهرانی وقتی اشکنه جا افتاد تخم مرغ‌ها را در اشکنه بشکنید و هم بزنید تا تخم مرغ‌ها خودشان را بگیرند و بپزند. چند دقیقه درب قابلمه را بگذارید تا تخم مرغ‌ها خودشان را بگیرند. همین‌که آب اشکنه کم شد و به غلظت دلخواه شما رسید شعله گاز را خاموش کنید (غلظت اشکنه باید مثل غلظت سوپ ورمیشل باشد). اشکنه تهرانی را ترید و با سیر ترشی و سبزی خوردن میل کنید. طرز تهیه اشکنه عدس به روش بیرجندی‌ها هم بسیار مشهور است و به این ترتیب است که مقداری عدس را با نمک و روغن و آب می‌پزند و بعد از پخت چرخ می‌کنند. سپس عدس چرخ کرده را به سایر مواد اشکنه اضافه می‌کنند. چند جوش که خورد تخم مرغ‌ها را می‌شکنند.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/05/1-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (20, 'املت ربی قهوه خانه ای', 'برای تهیه املت ربی قهوه خانه ای، برای ۴ عدد تخم مرغ ۴ قاشق غذاخوری روغن مایع یا حیوانی (یا بیشتر) در ماهیتابه بریزید و صبر کنید تا داغ شود.', 'برای تهیه املت ربی قهوه خانه ای، برای ۴ عدد تخم مرغ ۴ قاشق غذاخوری روغن مایع یا حیوانی (یا بیشتر) در ماهیتابه بریزید و صبر کنید تا داغ شود. در ریختن روغن دست و دل باز باشید تا املت خوش رنگ و لعاب شود. سپس ۲ قاشق غذاخوری سرپر رب گوجه فرنگی را در کمی آب جوش حل کنید و این ترکیب را در روغن سرخ کنید. مهم‌ترین فوت و فن درست کردن این املت حل کردن رب در آب جوش و سرخ کردن رب در روغن است که موجب خوشرنگ شدن و خوشمزه شدن املت شما می‌شود. بعد از این که رب کاملا سرخ شد و رنگ باز کرد، تخم مرغ‌ها را یکی یکی اضافه کنید. مقداری هم نمک و فلفل سیاه روی تخم مرغ‌ها بپاشید. صبر کنید کمی سفیده تخم مرغ سفت شود. سپس املت ربی را به سرعت هم بزنید تا املت یکدست و خوشرنگ شود. ۵ دقیقه بعد از هم زدن املت شعله گاز را خاموش کنید. املت ربی را با پیاز و نان بربری یا نان سنگک سرو کنید. اگر املت قهوه خانه ای با پیاز می خواهید، ابتدا پیاز نگینی خرد شده را در روغن تفت دهید و بعد از این که پیاز سرخ شد، در صورت دلخواه کمی زردچوبه اضافه کنید و کمی دیگر تفت دهید، سپس رب گوجه فرنگی یا پوره گوجه فرنگی را اضافه کنید. طعم خامی رب گوجه که گرفته شد (اگر از پوره گوجه فرنگی استفاده می‌کنید آب گوجه فرنگی باید کشیده شود) کمی دیگر روغن در ماهیتابه بریزید، بعد تخم مرغ‌ها را در املت بشکنید و نمک و فلفل املت را اندازه کنید. کمی که سفیده‌ها خودشان را گرفتند، املت را مقداری هم بزنید تا تخم مرغ در املت سفت شود و بپزد. بهترین ماهیتابه برای درست کردن املت ربی به روش قهوه خونه ماهیتابه روی یا روحی است. بهتر است برای این املت از ماهیتابه چدن یا تفلون استفاده نکنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2018/11/%D8%AB%DB%8C%D8%B5.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (21, 'املت هندی پاراتا تخم مرغ', 'ابتدا نان هندی پاراتا را درست کنید. برای درست کردن نان هندی پاراتا یا پراته هندی آرد را در یک کاسه بریزید و به تدریج و کم کم به آن آب گرم اضافه کنید تا خمیر شود.', 'ابتدا نان هندی پاراتا را درست کنید. برای درست کردن نان هندی پاراتا یا پراته هندی آرد را در یک کاسه بریزید و به تدریج و کم کم به آن آب گرم اضافه کنید تا خمیر شود. خمیر را خوب ورز دهید تا حدی که خمیر لطیفی درست شود که به دست نچسبد. سپس خمیر را با چند قطره روغن چرب کنید. چانه‌ی خمیر را در کاسه بگذارید و روی آن را به مدت ۲۰ دقیقه با سلفون بپوشانید تا برسد و ور بیاید. بعد از ۲۰ دقیقه خمیر را به دو چانه‌ی مساوی تقسیم کنید. هر چانه خمیر را مقداری پهن کنید و کمی آرد روی آن بپاشید. سپس چانه‌های خمیر را با وردنه پهن کنید. چانه را دو لا کنید و چند قطره روغن روی آن بپاشید و تمام سطح خمیر را به روغن آغشته کنید. چانه خمیر را یک تای دیگر بزنید تا شبیه مثلث شود و کمی دیگر آن را با وردنه پهن کنید. (می‌توانید خمیر نان پاراتا را به همان صورت گرد با وردنه نازک باز کنید، با روغن چرب کنید و در تابه سرخ کنید. دقت کنید قطر خمیر چند میلی‌متر باشد). یک تابه نچسب را داغ کنید و خمیر مثلثی را در تابه سرخ کنید. خمیر در این مرحله پف می‌کند و سپس پفش می‌خوابد. هر دو طرف خمیر را سرخ کنید. نان هندی پراتا آماده است، آن را در یک بشقاب بگذارید. صبر کنید تابه خنک شود. در این فاصله تخم مرغ را در یک ظرف بشکنید، تمام مواد که شامل پیاز خرد شده به همراه ادویه و سبزیجات است را، به تخم مرغ اضافه کنید و هم بزنید تا یک مایه یکدست درست شود. کف تابه را فقط کمی چرب کنید. مایه تخم مرغی پاراتا را در تابه بریزید و صبر کنید تا یک طرف آن سرخ شود. سپس نان هندی پراته را روی قسمتی از مایه املت که در بالای تابه است بگذارید و به آرامی فشار دهید. حالا با یک کفگیر پاراتا را زیر و رو کنید تا نان پاراتا در کف تابه قرار بگیرد. یک روش دیگر این است که املت هندی را درست کنید و سپس آن را روی نان پاراتا بگذارید و سرو کنید. املت هندی پاراتا تخم‌مرغ شما آماده است.این املت جذاب و خوشمزه را با سالاد سبزیجات، انواع ترشی یا سبزی خوردن میل کنید. هندی‌ها پاراتا تخم مرغ را با چای ماسالا یا سس چانتی سرو می‌کنند.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/10/Indian-Paratha-Egg-Omelette-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (22, 'انچیلادای بوقلمون', 'در اولین مرحله، سینه بوقلمون را بشویید و به صورت نواری و ترجیحا باریک خرد کنید. برای خوشمزه‌تر شدن، نوارهای گوشت بوقلمون را ابتدا با ادویه‌های گوناگون مرینیت کنید و برای چند ساعت در یخچال بگذارید.', 'در اولین مرحله، سینه بوقلمون را بشویید و به صورت نواری و ترجیحا باریک خرد کنید. برای خوشمزه‌تر شدن، نوارهای گوشت بوقلمون را ابتدا با ادویه‌های گوناگون مرینیت کنید و برای چند ساعت در یخچال بگذارید. پیاز را بشویید و پس از پوست گرفتن به صورت نگینی خرد کنید. حبه‌های سیر را هم پوست بگیرید و به صورت ریز رنده کنید. برگ‌های گشنیز تازه را ساطوری کنید. فلفل هالوپینو را نیز بشویید و به شکل دلخواه خرد کنید. فر را روشن کرده و روی دمای ۱۷۵ تا ۱۸۰ درجه سانتی‌گراد تنظیم کنید تا گرم شود. حالا، تابه مناسبی را روی حرارت متوسط بگذارید و مقداری کره در آن بریزید. سپس، نوارهای مرینیت شده گوشت بوقلمون را به تابه اضافه کنید و تفت دهید تا طلایی شوند. یک قاشق روغن زیتون را در تابه مناسبی بریزید و روی حرارت متوسط بگذارید تا گرم شود. حالا، نصف پیاز نگینی را در تابه بریزید و تفت دهید تا طلایی شود. نیمی از سیری که از قبل رنده کردید را نیز به تابه اضافه کنید تا همراه پیاز به مدت یک دقیقه تفت ببیند و عطر آن آزاد شود. مقداری نمک و گشنیز ساطوری شده را همراه با برش‌های بوقلمون پخته و سرخ شده به تابه حاوی پیاز و سیر اضافه کنید. این مواد را به خوبی هم بزنید تا به مدت ۳ دقیقه تفت ببینند و با یکدیگر ترکیب شوند. سپس، حرارت را خاموش کنید و تابه را کنار بگذارید. برای تهیه سس چیلی، مقداری روغن زیتون را در قابلمه مناسبی بریزید و روی حرارت متوسط بگذارید تا داغ شود. سپس، رب گوجه فرنگی را به قابلمه اضافه کنید و تفت دهید تا رنگ باز کند. باقی سیر رنده شده و باقی پیاز نگینی را نیز به رب گوجه اضافه کنید و به مدت یک دقیقه تفت دهید. حالا، آب مرغ یا بوقلمون، شکر، مقداری نمک، سرکه و فلفل هالوپینوی خرد شده را همراه با زیره سبز، پودر فلفل قرمز، پونه کوهی، پودر میخک، دارچین و پودر سیر در قابلمه بریزید. حرارت را ملایم کنید و اجازه دهید این ترکیب برای ۴ الی ۵ دقیقه به آرامی بجوشد. مایه سس را هم بزنید تا همه مواد به خوبی با یکدیگر ترکیب شوند. در این مرحله، باید آرد ذرت را با مقداری آب سرد ترکیب کنید و به خوبی هم بزنید تا کاملا یکدست شود. این ترکیب را به قابلمه حاوی سس در حال جوش اضافه کرده و چند دقیقه دیگر صبر کنید تا بجوشد. پس از آن‌که مایه سس جوشید و به غلظت دلخواه رسید، حرارت را خاموش کنید. در این مرحله باید هر یک از نان‌های ترتیلا را در بشقاب بگذارید و روی هر یک از آن‌ها یکی دو قاشق سس چیلی بریزید. با پشت قاشق، سس چیلی را در سطح نان پخش کنید تا کاملا به سس آغشته شود. سپس، گوشت بوقلمون سرخ شده و مایه انچیلادا را به صورت نواری در وسط نان ترتیلا بریزید. مقداری پنیر چدار یا پنیر موزارلا رنده شده و خامه ترش را نیز به این ترکیب اضافه کنید. حالا، نان ترتیلای حاوی این ترکیب را به آرامی رول کنید و کنار بگذارید. حالا، ظرف مناسبی (ترجیحا پیرکس گود) را انتخاب کرده و کف و دیواره‌های آن را چرب کنید. رول‌های آنچیلادای بوقلمون را بدون فاصله در ظرف بچینید و باقی‌مانده سس چیلی را روی رول‌ها بریزید. ظرف را به مدت ۱۵ تا ۲۰ دقیقه در طبق میانی فر بگذارید. پس از گذشت این مدت، انچیلادای بوقلمون را از فر خارج کنید و در ظرف سرو قرار دهید. این غذا را می‌توانید با خلال پیاز قرمز، پیازچه خرد شده و گشنیز و جعفری تازه تزئین کرده و همراه با انواع سالاد و سبزیجات تازه سرو کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/%D8%A2%D9%86%DA%86%DB%8C%D9%84%D8%A7%D8%AF%D8%A7%DB%8C-%D8%A8%D9%88%D9%82%D9%84%D9%85%D9%88%D9%86-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (23, 'ایچلی کوفته ترکیه ای با سس مخصوص', 'در اولین مرحله، پیازها را پوست بگیرید و خرد کنید یا رنده کنید. یک ماهیتابه روی گاز قرار دهید و مقداری کره به آن اضافه کنید. سپس گوشت چرخ کرده و پیازهای رنده شده را به آن اضافه کنید. مواد را هم بزنید ت', 'در اولین مرحله، پیازها را پوست بگیرید و خرد کنید یا رنده کنید. یک ماهیتابه روی گاز قرار دهید و مقداری کره به آن اضافه کنید. سپس گوشت چرخ کرده و پیازهای رنده شده را به آن اضافه کنید. مواد را هم بزنید تا آب گوشت و پیاز بخار شود. زمانی که پیازها کمی شفاف شدند و رنگ گوشت تغییر کرد، همه ادویه جات و چاشنی ها را به گوشت اضافه کنید. اجازه دهید گوشت و پیاز به خوبی تفت بخورد و سرخ شود. سپس شعله گاز را خاموش کنید. گردوها را با دست خرد کنید. سپس آن ها را به مواد گوشتی که تازه از روی گاز برداشته اید و هنوز داغ است، اضافه کنید. همه مواد را خوب ترکیب کنید. سپس داخل یک ظرف بریزید و کنار بگذارید تا خنک شود. بلغورها را داخل یک کاسه بزرگ بریزید. سپس آرد سمولینا را به آن اضافه کنید. ۵۰۰ میلی لیتر آب در حال جوش به ترکیب بلغور و آرد سمولینا اضافه کنید. مواد را به خوبی هم بزنید. سپس یک سلفون یا درب روی کاسه قرار دهید. اجازه دهید مواد برای ۱۰ دقیقه به همین حالت بمانند تا بلغور به خوبی نرم شود و خیس بخورد. بعد از این که ۱۰ دقیقه تمام شد، آرد سفید، تخم مرغ، رب فلفل قرمز و بقیه طعم دهنده را به ترکیب بلغور و سمولینا اضافه کنید. مواد را برای ۱۰ دقیقه به خوبی ورز دهید. اگر احساس کردید که خمیر شما خیلی سفت شده است، می توانید مقداری کمی آب به آن اضافه کنید. ورز دادن را تا زمانی ادامه دهید که خمیر دیگر به دستان شما نمی چسبد. مزه خمیر را کمی بچشید. در صورت نیاز مقداری نمک، ادویه یا لیمو ترش و رب به آن اضافه کنید تا طعم دلخواهتان به دست بیاید. برای حالت دادن خمیر بلغور، یک کاسه آب کنار خود بگذارید. دستان خود را کمی خیس کنید و مقداری از خمیر بلغور را بردارید. خمیر بلغور را به اندازه یک گردوی بزرگ گرد کنید. اگر خمیر ترک می خورد بهتر است جای ترک را با آب پر کنید. سپس گلوله بلغور را در دست بگیرید. با انگشت شست وسط گلوله را سوراخ کنید سپس خمیر دور سوراخ را به آرامی باز کنید تا سوراخ بزرگ تر شود. در این زمان ضخامت دیواره های خمیر باید تقریباً ۲ میلی متر باشد. بعد از اینکه خمیر کوفته آماده شد، مقداری از مواد گوشتی که از قبل آماده کرده بودید را داخل خمیر قرار دهید. سپس دهانه خمیر را ببندید و آن را به صورت توپ فوتبال آمریکایی که حالتی کشیده دارد، درآورید. هر زمانی که روی خمیر ترکی ایجاد شد، آن را با کمی آب بپوشانید. بعد از اینکه همه کوفته ها را به همین روش آماده کردید، باید آن ها را در روغن سرخ کنید. برای این کار باید مقداری روغن در ماهیتابه ای گود بریزید. مقدار روغن باید به اندازه ای باشد که کوفته ها به طور کامل در آن غوطه ور شوند. سپس کوفته ها را دانه به دانه داخل روغن بیندازید. کمی صبر کنید تا سطح کوفته هایتان طلایی رنگ شود. سپس آن ها را از روغن خارج کنید و روی دستمال قرار دهید. اگر زمانی که کوفته ها را داخل روغن می ریزید، روغن داغ باشد، روغن زیادی به غذا جذب نخواهد شد. برای آماده کردن سس مخصوص، مقداری کره در ماهیتابه بریزید تا داغ شود. سپس رب گوجه فرنگی و رب فلفل را به آن اضافه کنید. رب ها را با هم ترکیب کنید. گوجه فرنگی را رنده کنید و به رب اضافه کنید. سیر های رنده شده و ادویه ها را هم به سس اضافه نمایید و خوب ترکیب کنید. بعد از اینکه کوفته آماده شد می توانید آن را با این سس خوشمزه سرو کنید. در صورتی که کوفته هایتان را به صورت آب پز درست می کنید، بهتر است کوفته ها را در سس غوطه ور کنید تا رنگ و طعم بهتری به خود بگیرند. در صورتی که دوست دارید کوفته شما به صورت آب پز تهیه شود، می توانید مقداری آب در قابلمه بریزید و اجازه دهید تا به جوش بیاید. سپس کمی نمک و آب لیمو ترش تازه به آن اضافه کنید. همان طور که آب در حال جوشیدن است، کوفته ها را یکی یکی و به آرامی به آب اضافه کنید. به طور معمول پخت هر کوفته در آب حدود ۱۰ دقیقه طول می کشد. زمانی که کوفته ها روی آب شناور شدند، یعنی کاملاً پخته اند و می توانید آن ها را از آب خارج کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/08/icli-kofte-tarifi-resimli-anlatim.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (24, 'باتر چیکن (مرغ کره ای) هندی', 'فیله‌های مرغ را در ابعاد جوجه کبابی برش بزنید. فیله‌های خرد شده را بشویید و آب اضافی قطعات مرغ را کاملا بگیرید تا خوب خشک شوند.', 'فیله‌های مرغ را در ابعاد جوجه کبابی برش بزنید. فیله‌های خرد شده را بشویید و آب اضافی قطعات مرغ را کاملا بگیرید تا خوب خشک شوند. مرغ خرد شده و بدون استخوان را داخل کاسه مناسبی بریزید و آبلیمو، سس فلفل، فلفل قرمز، نمک و فلفل سیاه را به آن اضافه کنید. این ترکیب را هم بزنید و به مدت ۳۰ دقیقه در یخچال بگذارید تا مرغ کاملا تند و تیز و مزه‌دار شود. سطح کاسه را با سلفون بپوشانید. به اندازه یک سانتی‌متر از ریشه زنجبیل تازه را برش بزنید و پوست بگیرید. زنجبیل تازه و بدون پوست را قطعه قطعه کرده و با کمک گوشتکوب برقی یا دستگاه غذاساز کاملا پوره کنید یا با رنده ریز رنده کنید. حبه‌های سیر را بشویید و پوست بگیرید. سیر بدون پوست را هم به وسیله غذاساز یا گوشتکوب برقی کاملا پوره کنید یا ریز رنده کنید. بخشی از پوره زنجبیل تازه، مقداری پوره سیر، شنبلیله آسیاب شده، مقداری روغن زیتون یا روغن مایع، پودر زیره، پاپریکا، زردچوبه و ادویه گرام ماسالا را هم به کاسه حاوی قطعات مرغ مرینیت شده اضافه کنید. این مواد را به خوبی هم بزنید تا کاملا با یکدیگر ترکیب شوند. درب کاسه را دوباره با سلفون بپوشانید و به مدت ۱۲ ساعت در یخچال قرار دهید تا قطعات مرغ استراحت کنند و کاملا طعم‌دار و ترد شوند. پس از گذشت مدت زمان لازم برای استراحت، قطعات مرغ مرینیت شده را از یخچال خارج کنید. کف تابه مناسبی را با مقداری کره کاملا چرب کنید و روی حرارت متوسط قرار دهید تا داغ شود. سپس، قطعات مرغ را به تابه اضافه کنید و به مدت ۲۰ دقیقه در کره تفت دهید. پیازها را بشویید و پوست بگیرید. سپس، پیازها را به صورت نگینی خرد کنید و در تابه تمیز دیگری همراه با باقی کره سرخ کنید تا کاملا سبک و طلایی شوند. پس از سبک شدن پیازهای نگینی، کمی زردچوبه، فلفل سیاه، پودر دارچین، میخک و پودر هل (پوست هل را جدا کرده و آسیاب کنید) را هم به تابه اضافه کنید و خوب هم بزنید. یکی دو دقیقه منتظر بمانید تا عطر ادویه‌ها آزاد شود. حالا، باقی‌مانده پوره سیر و زنجبیل تازه را هم به تابه حاوی پیازهای نگینی اضافه کنید. این ترکیب را هم حدود ۲ تا ۳ دقیقه هم بزنید تا سیر و زنجبیل تفت داده شده و عطرشان آزاد شود. گوجه فرنگی‌ها را بشویید. سپس با کمک گوشتکوب برقی یا دستگاه غذاساز، گوجه‌ها را پوره کنید. حالا، بادام هندی را هم به پوره گوجه فرنگی اضافه کرده و دستگاه را دوباره روشن کنید. به این ترتیب، بادام هندی هم کاملا خرد شده و با پوره گوجه ترکیب می‌شود. پوره گوجه فرنگی و بادام هندی را همراه با شکر و مقداری نمک به پیاز و دیگر ادویه‌ها اضافه کنید. این ترکیب را چند دقیقه روی حرارت ملایم قرار دهید و به خوبی هم بزنید تا کاملا غلیظ شود. این کار را باید تا زمانی ادامه بدهید که رنگ گوجه فرنگی باز شود و به خوبی تفت ببیند و روغن بیندازد. پس از غلیظ شدن مواد داخل تابه، به اندازه یک لیوان دسته دار فرانسوی آب به این ترکیب اضافه کنید و خوب هم بزنید. درب تابه را بگذارید و حدود ۵ دقیقه منتظر بمانید تا مخلوط گوجه فرنگی، پیاز و ادویه‌ها با حرارت ملایم بپزند و جا بیفتند. پس از آماده شدن مایه خورشت باتر چیکن، قطعات مرغ تفت داده شده و مزه‌دار شده را اضافه کنید. حالا، درب تابه را ببندید و اجازه بدهید این مواد در کنار یکدیگر برای ۷ دقیقه دیگر هم بپزند. به این ترتیب، قطعات مرغ طعم مواد داخل تابه را به خودشان می‌گیرند و در عین حال، نرم‌تر می‌شوند. حالا خامه را به خورشت باتر چیکن اضافه کنید و هم بزنید. توجه داشته باشید، خامه نباید بجوشد و فقط باید گرم شود. بنابراین، باید خیلی زود حرارت را خاموش کنید و تابه را کنار بگذارید. پس از آماده شدن خورش باتر چیکن می‌توانید آن را با مقداری گشنیز ساطوری شده و چند عدد گوجه گیلاسی تزیین کرده و همراه با برنج چلو یا برنج کته یا نان سرو کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/07/%D9%85%D8%B1%D8%BA-%D9%87%D9%86%D8%AF%DB%8C-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (25, 'باقالی پلو با گردن', 'در اولین مرحله، پیاز ها را پوست بگیرید و نگینی ریز یا خلالی خیلی باریک خرد کنید. سیر ها را پوست بگیرید و ریز، خرد یا له کنید. در یک قابلمه مقداری روغن بریزید و بعد از داغ شدن پیاز ها را در آن سرخ کنید', 'در اولین مرحله، پیاز ها را پوست بگیرید و نگینی ریز یا خلالی خیلی باریک خرد کنید. سیر ها را پوست بگیرید و ریز، خرد یا له کنید. در یک قابلمه مقداری روغن بریزید و بعد از داغ شدن پیاز ها را در آن سرخ کنید. اجازه دهید کاملا سبک و نرم شوند. بعد از این که پیاز ها سرخ شدند، سیر ها را اضافه کنید. همین که عطر آن ها بلند شد، کافی است. گردن را به پیاز و سیر داغ اضافه کنید. هر دو طرف آن را سرخ کنید تا رنگش عوض شود. زردچوبه، فلفل، پودر زیره و کاری را به همراه برگ بو اضافه کنید. اجازه دهید خامی ادویه ها گرفته شود. به قابلمه ۱ و ۱/۲ لیوان آب جوش اضافه کنید. بعد از این که آب شروع به قل زدن کرد، حرارت را روی حالت خیلی ملایم تنظیم کنید. چوب دارچین و زعفران را اضافه کنید و در ظرف را بگذارید. ۳ تا ۴ ساعت صبر کنید تا گردن کاملا بپزد. بعد از اینکه گردن گوسفندی پخت و کاملا نرم شد، نمک را به آن اضافه کنید. چوب های دارچین و برگ بو را خارج کنید. آبلیمو را اضافه کنید. ۵ دقیقه صبر کنید و عصاره را بچشید و اگر نمک مناسب بود، شعله را خاموش کنید. گردن گوسفندی را در ظرف مناسب سرو کنید. عصاره را جداگانه در یگ ظرف بریزید و کنار باقالی پلو با گردن قرار دهید. از حداقل یک ساعت قبل از درست کردن باقالی پلو، برنج را با کمی نمک در آب ولرم خیس کنید. یک قابلمه را روی حرارت قرار دهید و بعد از این که آب جوش آمد، باقالی ها را اضافه کنید. ۵ دقیقه صبر کنید. برنج را از آب بکشید و به قابلمه اضافه کنید. نمک را اضافه کنید. کمی برنج را هم بزنید تا دانه های آن ها، به هم نچسبند. ولی مراقب باشید دانه های برنج نشکنند و باقالی ها خرد نشود. بعد از این که برنج کمی نرم شد ولی هنوز مغز آن سفت بود، برنج و باقالی را آبکش کنید. روی برنج و باقالی را آب بگیرید تا شوری آن خارج شود. برای ته دیگ سیب زمینی، نان، کنجد یا هر چه را که دلخواهتان است، با مقداری روغن کف قابلمه بگذارید. روی ته دیگ را یک لایه از باقالی و برنج آبکش شده بریزید. روی آن را شوید و پودر دارچین بپاشید. دوباره یک لایه برنج و بعد شوید بریزید و دارچین و لایه ها را به همین صورت ادامه دهید تا مواد تمام شوند. در قابلمه را روی آن بگذارید. بعد از این که بخار، داخل قابلمه جمع شد، حرارت را پایین بیاورید و حدود ۴۵ دقیقه تا ۱ ساعت صبر کنید تا برنج کاملا دم بکشد. روی برنج را کره بگذارید و بعد از ذوب شدن، باقالی پلو را هم بزنید تا کاملا مخلوط شود. سپس آن را در ظرف مناسب همراه با گردن سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/10/baghali-polo-ba-gardan-3-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (26, 'باقالی پلو با گوشت چرخ کرده', 'در اولین مرحله از طرز تهیه باقالی پلو با شوید تازه باید شویدها را بشویید و روی پارچه پهن کنید تا خشک شوند. سپس شویدها را ساطوری و ریز خرد کنید (به جای این مقدار شوید تازه از ۱ لیوان فرانسوی دسته‌دار ش', 'در اولین مرحله از طرز تهیه باقالی پلو با شوید تازه باید شویدها را بشویید و روی پارچه پهن کنید تا خشک شوند. سپس شویدها را ساطوری و ریز خرد کنید (به جای این مقدار شوید تازه از ۱ لیوان فرانسوی دسته‌دار شوید خشک می‌توانید استفاده کنید). در این مرحله باید برنج را چند بار بشویید. سپس آن را در قابلمه آب بریزید و مقداری نمک به آن اضافه کنید. اجازه دهید برنج برای حدود ۲ ساعت در آب بماند. بعد از خیساندن برنج به سراغ باقالی‌ها بروید. برای شروع باید پوست باقالی‌ها را جدا کنید و در صورت تمایل آن‌ها را از وسط نصف کنید. این کار موجب می‌شود تا باقالی پلوی شما ظاهر زیباتری داشته باشد. سپس باقالی‌های تازه را در قابلمه آب بریزید و برای ۱۰ دقیقه بپزید. در صورتی که از باقالی منجمد استفاده می‌کنید، می‌توانید باقالی را همراه با برنج در آب جوش بپزید. در این مرحله باید مایه گوشت چرخ کرده را درست کنید. پیاز را بشویید و پوست آن را جدا کنید. سپس پیاز را به صورت نگینی خرد کنید. مقداری روغن در ماهیتابه بریزید و پیازها را سرخ کنید تا سبک و طلایی شوند. بعد از این که پیاز سرخ شد، مقداری زردچوبه به آن اضافه کنید. حالا نوبت اضافه کردن گوشت چرخ‌ کرده است. گوشت و پیاز را به خوبی با هم ترکیب کنید. کمی فلفل سیاه و نمک به گوشت بزنید و مایه گوشت چرخ کرده را کمی دیگر تفت دهید. بعد از اینکه گوشت سرخ شد، شعله گاز را خاموش کنید. برای درست کردن برنج باید یک قابلمه آب روی حرارت قرار دهید و صبر کنید تا آب شروع به جوشیدن کند. مقداری نمک به آب اضافه کنید و سپس برنج از قبل خیس شده را اضافه کنید. آب برنج را دور بریزید و برنج را به آب جوش اضافه کنید. چند دقیقه صبر کنید تا برنج نیم‌پز شود. برای اینکه مطمئن شوید، زمان آبکش کردن برنج شده یا نه؛ سطح روی برنج نرم و وسط دانه برنج سفت باشد. سپس مقداری آب سرد روی برنج بریزید تا دانه‌های برنج به هم نچسبند. برای پخت باقالی پلو باید مقداری روغن در قابلمه مورد نظر بریزید. می‌توانید برای ته‌ دیگ از سیب‌زمینی، نان یا ترکیب کردن مقداری برنج با زعفران استفاده کنید. سپس برنج، شوید و باقالی را به صورت لایه لایه در قابلمه بریزید. مقداری کره لای پلو قرار دهید. کمی زعفران آب کرده هم روی برنج بریزید. سپس درب قابلمه را ببندید تا باقالی پلو برای حدود ۴۰ دقیقه دم بکشد. بعد از این که برنج دم کشید، آن را داخل دیس مورد نظر بریزید. مقداری از برنج را با زعفران ترکیب کنید و روی برنج بریزید. حالا ترکیب گوشت چرخ‌ کرده را روی برنج پخش کنید و از طعم این غذا لذت ببرید. در اولین مرحله از طرز تهیه باقالی پلو با گوشت قلقلی باید پیاز را رنده کنید و آب آن را بگیرید. سپس گوشت چرخ‌ کرده را همراه با نمک، فلفل و زردچوبه به آن اضافه کنید. مواد را چند دقیقه ورز دهید و سپس به صورت قلقلی درست کنید. اندازه قلقلی‌ها باید کمی بزرگ‌تر از فندق باشد. بعد از این که همه قلقلی‌ها را آماده کردید، باید آن‌ها را در سینی بچینید و برای حدود ۱ ساعت در یخچال قرار دهید. در این مرحله باید یک قابلمه آب جوش روی شعله گاز قرار دهید. بعد از این که آب شروع به جوشیدن کرد، برنج و باقالی را به آن اضافه کنید. اجازه دهید برنج و باقالی برای حدود ۶ دقیقه بجوشند. کف روی مواد را بگیرید. صبر کنید باقالی کمی نرم شود و زمانی که سطح روی دانه برنج نرم و مغز آن هنوز سفت بود، برنج و باقالی را آبکش کنید. در این مرحله از طرز تهیه باقالی پلو با گوشت چرخ‌ کرده قلقلی مقداری روغن کف قابلمه بریزید. سیب‌ زمینی را به صورت لایه‌ای برش دهید و کف قابلمه بچینید. مقداری زعفران به آن اضافه کنید. سپس برنج و باقالی، شوید خشک و دارچین را به صورت لایه‌ای در قابلمه بریزید. قابلمه را با حرارت ملایم روی شعله گاز قرار دهید و صبر کنید تا برنج شما برای حدود ۴۰ دقیقه دم بکشد. در این مرحله گوشت قلقلی‌ها را سرخ کنید. یک عدد پیاز را ریز رنده کنید و در روغن تفت دهید. سپس گوشت قلقلی‌ها را به آن اضافه و سرخ کنید. بعد از این که همه قلقلی‌ها سرخ شدند، آن‌ها را در یک طرف جمع کنید. مقداری رب گوجه فرنگی در تابه بریزد و کمی نمک، فلفل و زردچوبه به آن اضافه کنید. بعد از این که رب سرخ شد، به اندازه یک استکان آب به آن اضافه کنید. درب قابلمه را روی آن قرار دهید و صبر کنید تا گوشت قلقلی‌ها در این سس بپزند. بعد از این که باقالی پلو آماده شد، آن را در ظرف مورد نظر بریزید. مقداری زعفران دم کرده روی برنج بریزید و آن را با گوشت قلقلی یا همان میت بال تزیین کنید. اگر از باقالی تازه در باقالی پلو استفاده می‌کنید، بهتر است آن را چند دقیقه به صورت جداگانه بپزید. برای تهیه باقالی پلو با باقالی فریزری می‌توانید باقالی را همراه با برنج بجوشانید و آبکش کنید. به منظور خوش‌طعم شدن باقالی پلو می‌توانید از دارچین، زعفران و مقداری پودر سیر یا یک سر سیر تازه استفاده کنید. سر سیر تازه را با چاقوی ببرید و آن را موقع دم کشیدن باقالی پلو با پوست در قابلمه بگذارید. در کنار باقالی پلو با گوشت می‌توانید از ماست، ترشی، سالاد شیرازی و سبزی خوردن استفاده کنید.', 26, 54, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/08/%D8%B53.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (27, 'باقالی پلو با ماهیچه و گوشت', 'برای پختن ماهیچه گوسفندی ابتدا پیازها را خلالی خرد کنید و نیمی از آن‌ها را در قابلمه بریزید و با مقداری روغن تفت دهید. وقتی پیازها سبک شدند ماهیچه‌ها را روی آن بچینید، حبه‌های سیر و بقیه پیازهای خلالی', 'برای پختن ماهیچه گوسفندی ابتدا پیازها را خلالی خرد کنید و نیمی از آن‌ها را در قابلمه بریزید و با مقداری روغن تفت دهید. وقتی پیازها سبک شدند ماهیچه‌ها را روی آن بچینید، حبه‌های سیر و بقیه پیازهای خلالی را هم روی ماهیچه‌ها بریزید. فلفل سیاه، زردچوبه و زعفران دم کرده غلیظ را اضافه کنید. برگ بوها و چوب دارچین را هم روی سایر مواد بگذارید و درب قابلمه را ببندید. ۳ ساعت روی شعله ملایم گاز ماهیچه را بپزید. در این مدت چند بار ماهیچه‌ها را زیر و رو کنید تا یکدست پخته شوند. بعد از ۳ ساعت نمک را اضافه کنید و ۱ الی یک و نیم ساعت دیگر برای پخت به ماهیچه زمان بدهید. برای درست کردن باقالی پلو برای سرو در کنار ماهیچه ابتدا برنج را بشورید و ۱ الی دو ساعت در آب و نمک خیس کنید. باقالی‌ها را هم بشورید و ۵ دقیقه در آب بپزید و کف روی باقالی را بریزید و آبکش کنید. در یک قابلمه آب را جوش بیاورید و برنج را در آن بپزید. باقالی‌های آبکش شده را هم به برنج در آب جوش اضافه کنید. وقتی روی دانه برنج دو ترک افتاد آن را آبکش کنید. حالا به برنج و باقالی آبکش شده، شوید تازه و ساطوری خرد شده را هم اضافه کنید. این ترکیب را به آرامی با کفگیر هم بزنید تا برنج شفته و شکسته نشود. (می‌توانید به جای شوید تازه از یک مشت بزرگ شوید خشک هم استفاده کنید.) کف یک قابلمه روغن بریزید و ته دیگی که دوست دارید را بگذارید. در قابلمه ترکیب باقالی و برنج و شویدی که مخلوط کرده بودید را بریزید و گنبدی شکل کنید و دم بگذارید. روی باقالی پلو در صورت دلخواه کمی دارچین بپاشید یا لا‌به‌لای آن چند حبه سیر بگذارید. بعد از دم کشیدن برنج روی آن کره آب شده محلی یا روغن حیوانی آب شده بریزید. بعد از دم کشیدن باقالی پلو، ماهیچه پخته شده را در کنار باقالی پلو سرو کنید. برای درست کردن باقالی پلو با گوشت تکه‌ای، ابتدا تکه‌های گوشت را در مقداری روغن سرخ کنید. سپس گوشت‌ها را خارج کنید و پیازهای خلالی شده، سیر، زردچوبه و رب گوجه‌فرنگی را در همان روغن تفت دهید. درنهایت گوشت را به پیاز و رب سرخ شده اضافه کنید، ۲ لیوان آب در قابلمه بریزید و اجازه دهید گوشت برای حدود ۳ ساعت بپزد. در نیم ساعت نهایی پخت گوشت مقداری دارچین، آب لیموترش، زعفران، نمک و فلفل سیاه به آن اضافه کنید. برای درست کردن ته چین باقالی پلو با ماهیچه، همه مواد را به روش قبل آماده کنید. سپس ۲ تا ۳ عدد تخم مرغ را بشکنید و در یک ظرف بریزید. مقداری ماست، روغن و زعفران به تخم مرغ‌ها اضافه کنید و مواد را خوب هم بزنید. مقداری برنج با این مواد ترکیب کنید و بقیه برنج را با باقالی و شوید مخلوط کنید. کف یک قابلمه را چرب کنید و بعد ترکیب برنج و ماست و تخم مرغ را کف قابلمه بریزید. با پشت قاشق برنج‌ها را فشار دهید و بعد مایه پلو باقالی و شوید را اضافه کنید. قابلمه را روی شعله ملایم گاز قرار دهید تا ته چین شما آماده شود. ماهیچه را هم به روشی که پیش‌تر در بالا به آن اشاره شد آماده کنید و در انتها کنار ته چین سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/08/baghali-polo-ba-ghost-1024x684.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (28, 'باقلا پلو با گوشت بوقلمون', 'برای تهیه باقالی پلو با ماهیچه بوقلمون، ابتدا پوست ماهیچه بوقلمون را جدا کنید. سپس، گوشت بوقلمون را بشویید و آن را به مدت ۲ ساعت در محلول نسبتا غلیظ آب و نمک بخیسانید.', 'برای تهیه باقالی پلو با ماهیچه بوقلمون، ابتدا پوست ماهیچه بوقلمون را جدا کنید. سپس، گوشت بوقلمون را بشویید و آن را به مدت ۲ ساعت در محلول نسبتا غلیظ آب و نمک بخیسانید. برنج را به خوبی بشویید و همراه با مقداری آب و نمک در قابلمه مناسبی بریزید تا برای ۲ ساعت خیس بخورد. پیازهای شسته شده و بدون پوست را به صورت خلالی ریز خرد کنید و کنار بگذارید. حبه‌های سیر را هم بشویید، پوست بگیرید و پس از له کردن کنار بگذارید. ساقه‌های ضخیم و قسمت‌های زرد یا چوبی شده شوید تازه را جدا کنید. سپس، برگ‌های تر و تازه شوید را به خوبی بشویید و در سبد بریزید تا آب اضافی آن خارج شود. در ادامه، شوید تازه را ساطوری کنید. در صورت تمایل، برای پخت باقالی پلو می‌توانید از شوید خشک یا منجمد شده استفاده کنید. اگر قرار است از شوید منجمد شده استفاده کنید، بسته شوید یخ‌زده را به مدت ۲ ساعت در محیط آشپزخانه قرار دهید تا یخ آن کاملا باز شود. ماهیچه بوقلمون را از محلول آب و نمک بیرون بیاورید و با دستمال، کاملا خشک کنید. سپس، تابه مناسبی را روی حرارت متوسط بگذارید و مقداری روغن در آن بریزید تا داغ شود. گوشت بوقلمون را همراه با مقداری زردچوبه، فلفل سیاه و زعفران در تابه بریزید و به اندازه‌ای تفت دهید که همه قسمت‌های آن طلایی شود. پس از سرخ شدن همه قسمت‌های گوشت بوقلمون، برگ بو، رزماری و چوب دارچین را نیز همراه با پیازهای نگینی و حبه‌های سیر له شده به تابه اضافه کنید. درب تابه را ببندید و حرارت را ملایم کنید تا بوقلمون آب بیندازد و حداقل به مدت ۳ ساعت با حرارت کم کاملا پخته شود. در طول این مدت، هر نیم ساعت یک‌بار بدون باز کردن درب تابه به گوشت بوقلمون سر بزنید و تابه را کمی تکان بدهید تا مواد داخل آن جابه‌جا شود. قابلمه مناسبی را تا نیمه پر از آب کرده و مقداری نمک و روغن مایع به آن اضافه کنید. قابلمه را روی حرارت زیاد بگذارید تا بجوشد. سپس، آب برنجی که قبلا خیس کردید را خالی کنید و برنج خیس خورده را در قابلمه حاوی آب در حال جوش بریزید. حدود ۷ دقیقه منتظر بمانید تا برنج قد بکشد. پس از گذشت این مدت، باقالی‌های سبز و تازه را به قابلمه حاوی برنج و آب در حال جوش اضافه کنید. بسته به میزان تردی و تازگی باقالی‌ها حدود ۲ تا ۴ دقیقه صبر کنید تا باقلا نیز نیم‌پز شود. حالا، باقالی پلوی نیم‌پز را در سبد بریزید. سبد حاوی باقالی پلو را برای یکی دو دقیقه زیر آب سرد بگیرید تا نشاسته برنج و باقلا شسته و آبکشی شود. سپس، سبد را کنار بگذارید تا آب اضافی آن خارج شود. قابلمه مناسبی را روی حرارت متوسط بگذارید و مقداری روغن در آن بریزید. کمی زعفران دم کرده و پودر دارچین نیز به قابلمه اضافه کنید و ته دیگ برنج دلخواه خود را در قابلمه بچینید. سپس، یک لایه باقالی پلو روی ته دیگ بریزید و مقداری شوید تازه یا خشک را به آن اضافه کنید. این کار را تا زمانی ادامه دهید که باقالی پلو و شوید شما تمام شود. در ادامه، مقداری روغن یا کره را با یک سوم لیوان آب جوش ترکیب کرده و به قابلمه حاوی شوید باقالی پلو اضافه کنید. درب قابلمه را با دم‌کنی بپوشانید و ببندید. چند دقیقه منتظر بمانید تا قابلمه بخار کند و کاملا داغ شود. سپس، حرارت را ملایم کنید و اجازه دهید باقالی پلو برای یک ساعت و نیم روی حرارت ملایم دم بکشد. پس از گذشت این مدت، شوید باقالی پلوی خوشمزه شما کاملا دم کشیده و آماده سرو است. گوشت بوقلمون هم پس از ۳ ساعت پخته و جا افتاده شده است. بنابراین، وقت آن است که حرارت را خاموش کنید. باقالی پلو را در ظرف سرو بریزید. به اندازه یکی دو کفگیر از باقالی پلو بردارید و با مقداری زعفران دم کشیده ترکیب کنید. سپس، پلوی زعفرانی را روی دیس بریزید. روی گوشت بوقلمون هم مقداری زعفران دم کرده و در صورت تمایل، کمی آبلیموی تازه بریزید. بسته به میل خود، می‌توانید ماهیچه بوقلمون را در ظرف جداگانه‌ای قرار دهید یا روی باقالی پلو بگذارید و سرو کنید. این غذا را می‌توانید با انواع ترشی، انواع سالاد و سبزی خوردن نوش جان کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/%D8%A8%D8%A7%D9%82%D9%84%D8%A7-%D9%BE%D9%84%D9%88-%D8%A8%D8%A7-%DA%AF%D9%88%D8%B4%D8%AA-%D8%A8%D9%88%D9%82%D9%84%D9%85%D9%88%D9%86-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (29, 'بریانی ماهی', 'در اولین مرحله، پیاز شسته شده و بدون پوست را به صورت خلالی نازک خرد کنید و کنار بگذارید.', 'در اولین مرحله، پیاز شسته شده و بدون پوست را به صورت خلالی نازک خرد کنید و کنار بگذارید. سیر شسته شده و بدون پوست را هم له یا رنده کنید و در ظرف مجزایی بریزید. زنجبیل تازه را هم بشویید و پوست بگیرید و به صورت ریز رنده کنید. لیموترش را نیز به خوبی بشویید و آب آن را بگیرید. آب لیموترش تازه را صاف کنید و کنار بگذارید تا بعدا به ماهی اضافه کنید. شوید، نعنا و گشنیز تازه را هم پاک کنید و به خوبی بشویید. سپس، این سبزیجات تازه را ساطوری کنید. ماهی را هم بشویید و در سبد بگذارید تا آب اضافی آن خارج شود. در ادامه، برنج هندی را نیز به خوبی بشویید و همراه با یک قاشق غذاخوری نمک به مدت ۱ الی ۲ ساعت بخیسانید تا در روند پخت حسابی قد بکشد. حالا، فیله‌های ماهی را در ظرف مناسبی بگذارید و آبلیمو را همراه با شنبلیله خشک، ادویه گرام ماسالا، فلفل قرمز و زردچوبه به ماهی اضافه کنید. در این مرحله، سیر له شده، زنجبیل رنده شده و ماست را نیز همراه با مقداری نمک، روغن و زعفران دم کرده روی فیله‌های ماهی بریزید. ماهی را با دست ماساژ دهید تا کاملا به این مواد آغشته شود. اگر به سیر یا زنجبیل تازه دسترسی ندارید می‌توانید از پودر سیر و پودر زنجبیل استفاده کنید. حالا درب ظرف حاوی ماهی را با سلفون کاملا بپوشانید و آن را به مدت ۲ ساعت در یخچال بگذارید تا فیله‌های ماهی کاملا استراحت کنند و طعم‌دار شوند. در فاصله استراحت ماهی و خیس خوردن برنج، وقت آن است که پیاز داغ را آماده کنید. برای این منظور، مقداری روغن را در تابه مناسب و نچسبی بریزید و روی حرارت متوسط بگذارید تا داغ شود. سپس، پیازهایی که قبلا خلالی کردید را در تابه بریزید. پیازها را هم بزنید تا همه قسمت‌های آن به طور همزمان و به یک اندازه طلایی شود. در ثانیه‌های آخر، مقداری زردچوبه را نیز به پیاز اضافه کنید و تفت دهید تا پیاز رنگ و لعاب بهتری داشته باشد و عطر زردچوبه نیز آزاد شود. پیاز داغ را از تابه خارج کرده و به ظرف دیگری منتقل کنید. حالا، مقداری روغن را در همان تابه‌ای بریزید که قبلا پیاز داغ درست کردید. تابه را روی حرارت متوسط بگذارید تا روغن داغ شود. سپس، فیله‌های ماهی طعم‌دار را از یخچال خارج کنید و در این تابه قرار دهید. هر طرف ماهی سالمون را به مدت ۳ دقیقه تفت دهید. پس از گذشت این مدت و سرخ شدن ماهی، نعنا، گشنیز و شوید ساطوری شده را هم به تابه اضافه کنید و همراه ماهی تفت دهید. نیازی نیست این سبزیجات را زیادی سرخ کنید. پس از تفت دادن سبزیجات، حرارت را خاموش کنید. ماهی را از تابه به ظرف دیگری منتقل کنید و باقی محتویات تابه را کنار بگذارید تا بعدا لابه‌لای پلو بریزید. در این مرحله، نوبت به آماده کردن برنج می‌رسد. بنابراین، قابلمه مناسبی را تا نیمه پر از آب کنید و روی حرارت زیاد بگذارید تا بجوشد. به اندازه یک قاشق غذاخوری نمک و مقدار کمی روغن نیز به قابلمه اضافه کنید. پس از جوشیدن آب درون قابلمه، آب برنج خیس خورده را خالی کنید و برنج را در قابلمه در حال جوش بریزید. پس از گذشت ۷ الی ۱۲ دقیقه (بسته به نوع برنج)، یک دانه برنج را از قابلمه در حال جوش بیرون بیاورید و بین انگشتان خود فشار دهید. در این مرحله، باید اطراف برنج نرم شده و مغز آن هنوز سفت و دندان‌گیر باشد؛ در این صورت، برنج شما آماده آبکشی است. پس، برنج را در آبکش بریزید و اجازه دهید آب اضافی آن خارج شود. حالا وقت آن است که مقداری روغن را در قابلمه مناسبی بریزید و روی حرارت متوسط بگذارید تا داغ شود. سپس، یک لایه برنج را کف قابلمه بریزید و روی آن را با مقداری مخلوط نعنا، شوید و گشنیز سرخ شده و باقی موادی که همراه با ماهی تفت دادید، بپوشانید. این کار را به صورت لایه لایه ادامه دهید تا برنج و مایه بریانی ماهی تمام شود. در نهایت، پیاز داغ را روی برنج بریزید و قطعات ماهی را هم روی آن بگذارید. مقداری روغن و چند قاشق زعفران دم کرده را نیز به قابلمه اضافه کنید. حالا، درب قابلمه را ببندید و پس از آن که قابلمه بخار کرد و داغ شد، حرارت را کم کنید. از این‌جا به بعد، حدود ۴۵ دقیقه منتظر بمانید تا برنج شما دم بکشد و بریانی ماهی آماده شود. پس از گذشت این مدت، حرارت را خاموش کنید و ماهی را با احتیاط از قابلمه خارج کنید تا وا نرود. سپس، پلو را در ظرف سرو بریزید و قطعات ماهی را نیز روی آن قرار دهید. برای تزیین بریانی ماهی پلو می‌توانید مقداری گشنیز تازه خرد شده را روی پلو بریزید و چند برش لیموترش یا نارنج را هم در اطراف ظرف قرار دهید. شما می­توانید این پلوی مخلوط خوشمزه را همراه با انواع سالاد و انواع ترشی نوش جان کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/12/%D8%A8%D8%B1%DB%8C%D8%A7%D9%86%DB%8C-%D9%85%D8%A7%D9%87%DB%8C-2-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (30, 'بریان اصفهان', 'ابتدا گوشت و دنبه را با ۸ لیوان آب، پیاز، ادویه‌جات شامل زردچوبه، فلفل سیاه و دارچین (به جز نمک) و ۲ قاشق غذاخوری روغن مایع در یک قابلمه مناسب بپزید.', 'ابتدا گوشت و دنبه را با ۸ لیوان آب، پیاز، ادویه‌جات شامل زردچوبه، فلفل سیاه و دارچین (به جز نمک) و ۲ قاشق غذاخوری روغن مایع در یک قابلمه مناسب بپزید. گوشت باید کاملا پخته شود اما آب گوشت نباید کاملا جذب شود. مقداری از آب گوشت را نگه دارید. بعد از پخته شدن گوشت و دنبه آن‌ها را از آب جدا کنید. صبر کنید گوشت و دنبه از داغی بیفتند و سپس در چرخ‌گوشت چرخ کنید. به آب گوشت ۱ قاشق چای‌خوری نمک اضافه کنید. در اواسط چرخ کردن گوشت دو قاشق آب گوشت به گوشت در حال چرخ شدن اضافه کنید. این کار گوشت شما را پوک‌تر و لذیذتر می‌کند. دنبه را حتما باید همراه با گوشت چرخ کنید تا گوشت لطیف شود و حالت پرزدار پیدا نکند. ۱ قاشق غذاخوری زعفران دم کرده، ۱ قاشق مرباخوری نمک، کمی فلفل سیاه و نوک قاشق چای‌خوری دارچین به گوشت اضافه کنید. در مرحله بعد زرشک و خلال بادام را با ۱ قاشق مرباخوری (یا بیشتر) شکر تفت دهید و سپس مقداری زعفران دم کرده به آن اضافه کنید. می‌توانید در صورت تمایل مقداری خلال پسته هم اضافه کنید که البته اختیاری است. در ادامه کار باقی مانده روغن مایع را با کمی زردچوبه به آب گوشت اضافه کنید و اجازه دهید آب گوشت داغ شود. یک کاسه از آب گوشت را بردارید و کنار بگذارید. در یک ماهیتابه پهن و ترجیحا مسطح (در قسمت وسط گودی نداشته باشد) ½ ملاقه آب گوشت بریزید و زمانی که به جوش آمد و روی آن حباب تشکیل شد یک عدد نان را داخل آب گوشت بچرخانید تا رنگ بگیرد. کف هر کدام از کفگیرها را کمی از آب گوشتی که کنار گذاشته بودید بریزید. کمتر از نوک قاشق دارچین روی آب گوشت بپاشید و به اندازه هر کفگیر از گوشت چرخ کرده بردارید و داخل کفگیر پهن کنید. ضخامت هر عدد بریون بهتر است یک سانتی‌متر باشد. هر کفگیر را روی حرارت خیلی ملایم گاز قرار دهید و پنج تا ده دقیقه زمان دهید. اگر کفگیر به اندازه کافی ندارید بعد از درست کردن هر عدد بریون کفگیر را سریع بشورید و خشک کنید و بریون بعدی را به همان ترتیب درست کنید. وقتی که کنارهای هر بریون در کفگیر سرخ شد آن را در نانی که از قبل چرب کرده بودید برگردانید. روی هر عدد بریون مقداری زرشک و خلال بادام بریزید. برای تزیین بریون اصفهان می‌توانید از مقداری زرشک، خلال بادام، گردو و نعنا خشک استفاده کنید.', 26, 54, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/10/%D8%B7%D8%B1%D8%B2-%D8%AA%D9%87%DB%8C%D9%87-%D8%A8%D8%B1%DB%8C%D9%88%D9%86-%D8%A7%D8%B5%D9%81%D9%87%D8%A7%D9%86-%DB%B2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (31, 'بز قورمه کرمانی', 'بزقرمه یا قرمه گوشت کرمانی از غذاهای سنتی استان کرمان و یک غذای خوشمزه با کشک است. بز قرمه نمونه یک غذای ایرانی نونی لذیذ برای ناهار یا افطاری نونی ماه رمضان است.', 'بزقرمه یا قرمه گوشت کرمانی از غذاهای سنتی استان کرمان و یک غذای خوشمزه با کشک است. بز قرمه نمونه یک غذای ایرانی نونی لذیذ برای ناهار یا افطاری نونی ماه رمضان است. بز قرمه در اصل نوعی آبگوشت بزباش کرمانی‌هاست. کرمانی‌ها به جای گوجه فرنگی یا رب گوجه، به بز قرمه کشک اضافه می‌کنند. بزقرمه بیشتر در شهرهای شمال استان کرمان یعنی سیرجان، رفسنجان، شهر بابک و کرمان طبخ می‌شود. این غذای کرمانی با کشک و گوشت را می‌توانید مرحله به مرحله به روش زیر درست کنید. در ابتدا نخودها را پاک کنید و با آب بشویید. سپس نخودها را در یک کاسه بریزید و به آنها مقداری آب اضافه کنید، در حدی که ۲ بند انگشت روی آنها را بپوشاند. حدود ۸ تا ۱۲ ساعت نخودها را بخیسانید تا نفخ آنها گرفته شود و نرم‌تر شوند. در این مدت، ۲ تا ۳ بار آب روی نخودها را با آب تازه عوض کنید. برای شروع کار، گوشت‌ گوسفندی یا بز را بشویید و به صورت درشت خرد کنید. یکی از پیازها را پوست بگیرید و به صورت نگینی خرد کنید. در یک قابلمه مقداری روغن بریزید و روی حرارت بگذارید تا داغ شود. سپس پیازهای خرد شده را در قابلمه بریزید و پیازها را هم بزنید تا تفت بخورند و سبک و شیشه‌ای شوند. بعد از آماده شدن پیازها، تکه‌های گوشت‌ را در قابلمه بریزید و تفت دهید تا رنگ آنها به سفیدی بزند. سپس زردچوبه، فلفل سیاه و ادویه‌های دلخواه خود را اضافه کنید و چند ثانیه تفت دهید تا ادویه‌ها از خامی بیفتند و به خورد گوشت‌ها بروند. نخودها را آبکش کنید. سپس در قابلمه بریزید و کمی با گوشت‌ها تفت دهید. در این مرحله، به اندازه ۴ تا ۵ لیوان فرانسوی دسته‌دار آب جوش به گوشت و نخود اضافه کنید و صبر کنید که آب شروع به جوشیدن کند. شعله را کم کنید و صبر کنید تا گوشت‌ها و نخودها به مدت ۲ تا ۲/۵ ساعت بپزند و نرم شوند. در زمان پخت مواد یا اواخر پخت گوشت و نخود، ۲ پیاز دیگر را پوست بگیرید و به صورت خلالی نازک خرد کنید. سیرها را هم پوست بگیرید و ریز خرد کنید. نعناع را هم بسایید و کنار بگذارید. در یک تابه مقداری روغن بریزید و روی حرارت بگذارید تا داغ شود. سپس پیازها را در روغن داغ سرخ کنید تا کاملا طلایی شود. بعد پیاز داغ را از تابه بیرون بیاورید و در یک بشقاب بریزید. سپس سیرها را در تابه بریزید و وقتی عطرشان بلند شد، سیر داغ را از تابه خارج کنید. در آخر، نعناع‌ها را در تابه بریزید و ۳۰ ثانیه تا ۱ دقیقه تفت دهید تا نعناع داغ حاضر شود. بعد از پختن نخود و گوشت، استخوان‌های گوشت را جدا کنید. می‌توانید گوشت‌ها را هم بیرون بیاورید و آنها را ریش ریش کنید. سپس گوشت‌ها را به قابلمه برگردانید. آب گوشت بزقرمه را جدا کنید و کنار بگذارید. با گوشتکوب دستی، نخود و گوشت را بکوبید تا کاملا یکدست شود. دوباره شعله گاز را روشن کنید و کمی نمک یا در صورت دلخواه، زعفران دم کرده را به قابلمه اضافه کنید (می‌توانید کشک را هم در همین مرحله اضافه کنید). حدود ۵ دقیقه دیگر مواد بزقرمه را بجوشانید تا ادویه و چاشنی‌ها به خورد غذا برود. بعد از اینکه بز قورمه طعم دلخواهتان را گرفت، آنها را در ظرف سرو بریزید و روی بز قورمه را با نعناع داغ، پیاز داغ، سیر داغ و مغز گردوی خرد شده، تزیین و سرو کنید. می‌توانید آب گوشت بز قورمه را ترید کنید. بز قورمه را مثل خورش با برنج کته یا چلو یا مثل آبگوشت با نان، سبزی خوردن و ترشی دلخواه، سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2019/08/1246037_732.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (32, 'بوقلمون سرخ شده با سس مخصوص', 'در طرز تهیه بوقلمون سرخ شده می‌توانید از ران یا سینه بوقلمون استفاده کنید. ابتدا گوشت بوقلمون را بشویید و در آبکش قرار دهید تا آب اضافی خود را از دست بدهد.', 'در طرز تهیه بوقلمون سرخ شده می‌توانید از ران یا سینه بوقلمون استفاده کنید. ابتدا گوشت بوقلمون را بشویید و در آبکش قرار دهید تا آب اضافی خود را از دست بدهد. برای طرز تهیه سینه بوقلمون، با یک چاقوی تیز پوست بوقلمون را جدا کنید و آن را به صورت تکه‌ای یا فیله خرد کنید. اگر هم از ران بوقلمون استفاده می‌کنید برای طرز پخت ران بوقلمون مجلسی سرخ شده، جدا کردن پوست بوقلمون کافی است و لازم نیست آن را خرد کنید. ابتدا در یک تابه روغن را بریزید و داغ کنید. سپس تکه‌های گوشت بوقلمون را در روغن تفت دهید تا دو طرف گوشت طلایی یا کمی قهوه‌ای شود. سپس بوقلمون را از تابه بیرون بیاورید. در یک قابلمه دیگر پیاز را درشت رنده کنید و تفت دهید. وقتی پیاز طلایی و سبک شد، ۱ حبه سیر له شده را هم اضافه کنید و تفت دهید. سپس تکه‌های گوشت بوقلمون را در قابلمه قرار دهید و ۱ لیوان فرانسوی دسته‌دار آب جوش و ۲ عدد برگ بو اضافه کنید. حدود ۳ و نیم الی ۴ ساعت صبر کنید تا بوقلمون روی شعله ملایم گاز حسابی مغز پخت شود. در اواخر پخت زعفران دم کرده غلیظ را اضافه کنید. وقتی بوقلمون مغزپخت و نرم شود و آبی که اضافه کردهâ€اید کشیده شد، کره را اضافه کنید و بوقلمون را کمی دیگر در همان قابلمه سرخ کنید. در این مرحله آب پرتقال و شکر را در یک شیرجوش بریزید و روی شعله متوسط گاز بگذارید تا به حالت جوش برسد و شکر در آب پرتقال حل شود. وقتی شکر در پرتقال حل شد آن را روی تکهâ€های بوقلمون بریزید. بعد از اضافه کردن آب پرتقال، شعله گاز را کم کنید تا آب پرتقال به خورد تکهâ€های گوشت بوقلمونâ€ برود تا تبدیل به سس پرتقال شود. در این مرحله نمک سس را اندازه کنید. بسته به ذائقهâ€تان مقداری چاشنی به بوقلمون سرخ شده پرتقالی اضافه کنید؛ اگر دوست دارید غذایâ€تان کمی ملس باشد مقداری شکر و کمی هم آبلیمو اضافه کنید در غیر این صورت اضافه کردن نمک کافی است. بوقلمون سرخ شده را میâ€توانید به جای مرغ همراه با زرشک پلو در کنار برنج چلو آبکش به صورت چلو گوشت بوقلمون درست مثل چلو گوشت رستورانی سرو کنید. اگر هم باقالی پلو دوست دارید بوقلمون سرخ شده را با باقالی پلو سرو کنید درست مثل روش سرو ماهیچه بوقلمون.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/08/Thanksgiving-Dinner-4.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (33, 'بوقلمون شکم پر مخصوص کریسمس', 'بوقلمون را بشویید و با دستمال خشک کنید. می‌توانید بوقلمون را به مدت ۳ ساعت در آب نمک بگذارید و در یخچال قرار دهید تا نمک به خورد گوشت برود.', 'بوقلمون را بشویید و با دستمال خشک کنید. می‌توانید بوقلمون را به مدت ۳ ساعت در آب نمک بگذارید و در یخچال قرار دهید تا نمک به خورد گوشت برود. به ازای ۴ لیتر آب، نصف لیوان دسته دار فرانسوی نمک اضافه و حل کنید و بوقلمون را با پوست در آب نمک بگذارید. سپس آن را بیرون بیاورید و نمک‌های اضافی را با دستمال پاک کنید. بوقلمون را ۲ ساعت قبل از پخت در دمای محیط بگذارید. آلوهای برغانی را هم در کمی آب ولرم خیس کنید. در یک تابه ۵۰ میلی‌لیتر روغن زیتون و ۵۰ گرم کره بریزید و روی حرارت قرار دهید. پیاز خرد شده و سیر خرد شده را به تابه اضافه کنید و تفت دهید تا پیاز سبک شده و عطر سیر خارج شود. سپس گوشت چرخ کرده را اضافه کنید و مرتب هم بزنید تا تغییر رنگ دهد. شعله گاز را کم کنید و جعفری خرد شده و مغز دانه کاج (اگر مغز دانه کاج ندارید از مغز گردو استفاده کنید) را به تابه اضافه کنید. در ادامه پودر دارچین را اضافه کنید. درب تابه را ببندید تا گوشت آب بیندازد و بپزد. حدود ۲۵ دقیقه صبر کنید تا گوشت چرخ کرده بپزد و آماده شود. در این مرحله، فر را روشن و روی دمای ۲۰۰ درجه سانتی‌گراد و فن ۱۸۰ درجه سانتی‌گراد گرم کنید. بعد از پخت مایه داخل بوقلمون، حرارت زیر تابه را قطع کنید و اجازه دهید کمی سرد شود. بوقلمون را با دستمال حوله‌ای خشک کنید و قسمت داخلی و خارج بوقلمون و زیر پوست آن را با نمک و فلفل سیاه مزه دار کنید. رو و زیر پوست آن را هم کره نرم شده بمالید تا همه سطح بوقلمون را بپوشاند. بعد از سرد شدن موادِ پر کردن شکم بوقلمون، فلفل قرمز را ریز خرد کنید. هسته آلوهایی را که در ابتدای کار خیس کرده بودید بگیرید و به تابه حاوی مایه گوشت چرخ کرده اضافه کنید. زیتون سبز بدون هسته و نصف شده، مغز بادام خرد شده و کشمش پلویی را هم به مایه گوشت چرخ‌کرده اضافه کنید. ۲ سیب‌ درختی خرد شده و زیتون‌ها را هم اضافه کنید. همه مواد را هم بزنید تا یکدست شوند. این مواد را به آرامی داخل شکم بوقلمون بریزید و آن را پر کنید. با سیخ چوبی، پاهای بوقلمون را محکم کنید. پاها را با نخ گره بزنید. بین پاهای بوقلمون را یک سیب درختی درسته قرار دهید. بال‌های بوقلمون را بالا بیاورید و آنها را تا کنید. بال‌ها را در قسمت زیرین بوقلمون فیکس کنید (با سیخ چوبی). بعد از پیچیدن و سفت کردن بوقلمون، دوباره روی آن مقداری کمی کره بمالید و بوقلمون را داخل یک قالب با عمق زیاد بگذارید. آب پرتقال را هم کف قالب بریزید. روی قالب را فویل آلومینیومی بکشید. قالب را در فر بگذارید و دما را به ۱۸۰ درجه سانتی‌گراد و فن را به ۱۶۰ درجه سانتی‌گراد کاهش دهید. به مدت ۲ ساعت صبر کنید. بعد از ۲ ساعت درب فر را باز کنید. فویل را کنار بزنید و آب جمع شده در کف قالب را روی بوقلمون بریزید و دوباره درب فر را ببندید. هر ۳۰ دقیقه یک بار این کار را انجام دهید. در ۳۰ دقیقه پایانی، فویل را بردارید تا روی بوقلمون برشته شود. بعد از ۳/۵ ساعت هر ۱۵ دقیقه یک بار یک سیخ داخل گوشت بوقلمون فرو کنید و اگر گوشت آن کاملا پخته بود و نرم شده بود، بوقلمون را از فر بیرون بیاورید. سسی که داخل قالب باقی مانده را در یک قابلمه بریزید و روی بوقلمون را به مدت ۳۰ دقیقه با فویل آلومینیومی بپوشانید تا گرمای آن حفظ شود. آرد را با سس باقی‌مانده در قالب و آب بوقلمون روی حرارت بگذارید تا بجوشد و کمی غلیظ شود. سپس فویل را بردارید و نخ‌ها و سیخ‌های بوقلمون را جدا کنید. سیب‌های درختی پخته‌شده را در دیس سرو بوقلمون بگذارید، بوقلمون را هم در دیس بگذارید و سسی که آماده کردید را در یک ظرف جداگانه کنار آن سرو کنید (درست کردن سس اختیاری است).', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/0_GettyImages-1043629872.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (34, 'بوقلمون شکم پر', 'در اولین مرحله فر را روی ۱۷۵ الی ۱۸۰ درجه سانتی‌گراد تنظیم کنید تا داغ شود.', 'در اولین مرحله فر را روی ۱۷۵ الی ۱۸۰ درجه سانتی‌گراد تنظیم کنید تا داغ شود. بوقلمون را بشویید و با دستمال خشک کنید. گردن بوقلمون را جدا کنید. سپس فلفل سیاه و نمک را در شکم بوقلمون بریزید و به خوبی با دست آن را پخش کنید. ۳ حبه سیر را له کنید. پیازها و لیمو را به ۴ تکه تقسیم کنید و به همراه نصف آویشن و رزماری خشک، داخل شکم بوقلمون بگذارید. نصف ریحان، جعفری و شوید ساطوری شده را زیر پوست و بقیه را داخل بوقلمون شکم قرار دهید. مقداری از کره را کمی حرارت دهید تا نرم شود. مغز گردوی خرد شده را در آن بریزید و تفت بدهید. سپس بقیه کره را به همراه زعفران دم کرده، با فرچه روی بوقلمون بمالید. بوقلمون را در فویل بپوشانید و داخل فر قرار دهید. برای اینکه گوشت بوقلمون خشک نشود هر یک ساعت یک بار، بوقلمون شکم پر را از فر بیرون بیاورید و آب بوقلمون را روی آن بریزید و دوباره در فر قرار بدهید تا سطح روی آن کاملا برشته و پخته شود. در یک ساعت پایانی پخت فویل روی بوقلمون را باز کنید تا گوشت بوقلمون برشته و طلایی شود. مدت زمان پخت بوقلمون را با کم کردن عدد «یک» از وزن بوقلمون به دست آورید (مثلا اگر بوقلمون ۶ کیلو است، مدت زمان مناسب برای پخت ۵ ساعت خواهد بود). در نهایت بوقلمون را از فر خارج کنید. صبر کنید بوقلمون ۲۰ الی ۳۰ دقیقه در دمای محیط بماند بعد با چاقو آن را برش بزنید و سرو کنید. برای تهیه بوقلمون شکم پر بدون فر و در قابلمه، کف قابلمه را با مقداری هویج، سیب زمینی و کرفس بپوشانید. بوقلمون شکم پر را که به روش قبل آماده کرده و داخل فویل گذاشته‌اید، روی سبزیجات داخل قابلمه بگذارید به طوری که بوقلمون با کف قابلمه در تماس نباشد. مقداری آب در قابلمه بریزید و درب آن را بگذارید. اجازه دهید بوقلمون به آرامی در قابلمه به مدت ۴ الی ۵ ساعت بپزد. برای مزه دار کردن بوقلمون، سطح بوقلمون را کاملاً با روغن چرب کنید و سپس ادویه‌هایی مانند پودر آویشن، پاپریکا، پودر لیمو عمانی، مرزه خشک، فلفل سیاه، پودر سیر، پودر پیاز، و نمک را روی تمام سطح بوقلمون شکم پر بریزید. سپس مقداری آب لیمو ترش تازه یا سرکه به بوقلمون بزنید و آن را با سلفون بپوشانید تا مزه‌دار شود. معمولاً بهتر است از نیم ساعت تا ۲۴ ساعت بوقلمون مرینیت شده با ادویه‌ها را داخل یخچال بگذارید تا کاملاً عطر ادویه‌ها به خورد گوشت بوقلمون برود و بوی زهم بوقلمون گرفته شود. یکی از روش‌های مرینیت یا مزه‌دار کردن بوقلمون شکم پر در فر برای گرفتن بوی زهم بوقلمون، نمک سود کردن آن است. در این روش، در یک قابلمه بزرگ آب و نمک را با هم ترکیب کنید. بوقلمون را در آب‌نمک قرار دهید تا تمام سطح گوشت در آن قرار بگیرد. سپس برای حدود ۲ ساعت بوقلمون را در یخچال بگذارید تا در آب نمک مزه‌دار شود.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/11/83031691-777d-43b8-810e-ee7ad386dcde-e1605992681360.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (35, 'پاکوره هندی', 'پاکوره، پکوره، پاکورا یا پکورا نام غذایی هندی و در اصل از منطقه گجرات هندوستان است که به صورت یک فینگرفود برشته شده خمیری به همراه مخلفاتی مثل سس تمر هندی یا سس فلفل تند سرو می‌شود.', 'پاکوره، پکوره، پاکورا یا پکورا نام غذایی هندی و در اصل از منطقه گجرات هندوستان است که به صورت یک فینگرفود برشته شده خمیری به همراه مخلفاتی مثل سس تمر هندی یا سس فلفل تند سرو می‌شود. برای طرز تهیه پاکوره سیب زمینی هندی ابتدا باید سیب زمینی و هویج را آبپز کنید. سپس با رنده ریز رنده کنید. کدو و پیاز را هم درشت رنده کنید و آب پیاز را خوب بگیرید. تخم مرغ را جداگانه هم بزنید و سپس تمام مواد و ادویه‌جات با هم مخلوط کنید تا یک خمیر به دست بیاورید. غلظت خمیر باید مثل ماست یونانی باشد، نه خیلی شل و نه خیلی سفت. اگر خمیر خیلی سفت بود ۲ یا سه قاشق غذاخوری آب به آن اضافه کنید. برای سرخ کردن پاکوره با اسکوپ فلافل یا قاشق از خمیر پاکوره بردارید و در روغن داغ سرخ کنید. برای درست کردن سس پاکوره ابتدا ½ بسته تمر هندی را در لیوان آب جوش بریزید و هسته تمر هندی را با دست جدا کنید. سپس تمر هندی را از صافی رد کنید. تمر هندی غلیظ را با سایر مواد در غذاساز یا میکسر بریزید و میکس کنید تا به صورت پوره شود. سس تمر هندی را در کنار پاکور سرو کنید. برای درست کردن پاکوره هندی بدون سیب زمینی و تخم مرغ ابتدا باید آرد نخود، آرد گندم و آرد سفید، بیکینگ پودر و ۱ قاشق چایخوری زردچوبه را با هم مخلوط کنید. کمی آب اضافه کنید تا آرد حالت خمیر پیدا کند. مواد را با قاشق خوب هم بزنید تا یکدست شود. سپس پیاز و فلفل سبز را خیلی ریز خرد کنید. سبزیجات ساطوری خرد شده را با پیاز و فلفل سبز ریز خرد شده را به خمیر اضافه کنید و خوب هم بزنید تا مایه پاکورا یکدست شود. مایه پاکوره را با اسکوپ فلافل یا یک قاشق گود یکی یکی در روغن داغ بریزید تا پاکورا سرخ و طلایی شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/09/1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (36, 'پلو بریانی هندی', 'برای درست کردن این بریانی لذیذ قبل از هر چیز، از ۳۰ دقیقه قبل برنج را در آب و نمک، بخیسانید تا موقع پخت قد بکشد.', 'برای درست کردن این بریانی لذیذ قبل از هر چیز، از ۳۰ دقیقه قبل برنج را در آب و نمک، بخیسانید تا موقع پخت قد بکشد. قارچ‌ها را خلالی خرد کنید. گوجه فرنگی را پوره کنید. پیاز را پوست بگیرید و بشویید. به صورت نگینی یا خلالی خرد کنید. یک تابه را روی حرارت قرار دهید و در آن روغن بریزید. بعد از داغ شدن روغن، زیره سبز، برگ بو، هل، دارچین، تخم رازیانه، میخک و جوز هندی را در آن بریزید و سرخ کنید تا عطر آن‌ها بلند شود. می‌توانید این ادویه‌ها را به صورت ساییده نشده هم استفاده کنید. پیاز نگینی شده را به مواد اضافه کنید و تفت دهید تا طلایی و نرم و سبک شود. سیر را له و اضافه کنید. نعنا و گشنیز را به همراه پوره گوجه یا گوجه خیلی ریز خرد شده، به مواد اضافه کنید و بگذارید تفت بخورند تا طعم خامی آنها گرفته شود. قارچ را اضافه کنید. زردچوبه، نمک و فلفل را به مواد اضافه کنید. در طرف را باز بگذارید و آنها را تفت دهید تا آب قارچ بخار شود. برنج را به مواد اضافه و کمی آب جوش بیفزایید و حرارت را بیشتر کنید تا آب، بخار و برنج نرم شود. وقتی برنج دندان‌گیر و آب برنج کشیده شد، دم کنی را روی قابلمه بگذارید. بعد از اینکه داخل قابلمه را بخار گرفت، حرارت را کم کنید و ۲۰ دقیقه اجازه دهید تا مواد با هم بپزند. می‌توانید بریانی هندی را در فر هم تهیه کنید. در این صورت، قابلمه را به مدت ۳۵ دقیقه در فر قرار دهید و در ظرف را باز بگذارید. بعد از اینکه بریانی شما آماده شد، آن را در یک ظرف مناسب با انواع ترشی یا ماست سرو کنید.', 28, 57, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/02/Mrs-Lily-Singhs-Low-Fat-Vegetable-Biryani-e1613586356218.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (37, 'چکدرمه پلو ترکمنی', 'در طرز تهیه چکدرمه ابتدا برنج را به مدت ۱ الی ۲ ساعت در آب و نمک خیس کنید. در این فاصله گوشت را به تکه‌های بزرگ خرد کنید. تکه‌های گوشت برای پلو محلی چکدرمه باید از تکه‌های گوشت خورشتی درشت‌تر باشند. گ', 'در طرز تهیه چکدرمه ابتدا برنج را به مدت ۱ الی ۲ ساعت در آب و نمک خیس کنید. در این فاصله گوشت را به تکه‌های بزرگ خرد کنید. تکه‌های گوشت برای پلو محلی چکدرمه باید از تکه‌های گوشت خورشتی درشت‌تر باشند. گوشت را بشورید و اجازه دهید خشک شود. در این مرحله پیاز را خرد کنید و با مقداری روغن تفت دهید. مقداری زردچوبه به پیازها اضافه کنید و تفت دهید. تکه‌های گوشت را به پیازهای در حال تفت اضافه کنید و اجازه دهید روی شعله ملایم گاز سرخ شوند. نمک، فلفل سیاه و ادویه پلویی را هم به گوشت اضافه کنید و خوب هم بزنید. حالا گوجه‌های رنده شده را به گوشت در حال سرخ شدن اضافه کنید. اجازه دهید روی شعله ملایم گاز آب گوجه‌ها کشیده شود و به خورد گوشت برود. در این مرحله ۳ قاشق غذاخوری رب گوجه‌ فرنگی را اضافه کنید و صبرکنید تا رب سرخ شود و مزه خامی آن گرفته شود. برخلاف استامبولی پلو که برنج را به گوشت سرخ شده اضافه می‌کنند و با هم دم می‌کنند برای درست کردن چکدرمه گوشت باید ابتدا جداگانه پخته شود. ۴ لیوان فرانسوی دسته‌دار آب به گوشت سرخ شده اضافه کنید و روی شعله ملایم گاز بگذارید تا به آرامی پخته شود. بعد از پخت گوشت (مدت زمان پختن گوشت بستگی به گوشت شما دارد، ممکن است ۲ تا سه ساعت طول بکشد) برنج‌های شسته شده را به گوشت اضافه کنید. آب برنج را اندازه کنید. مثل سایر پلوهایی که به صورت برنج کته درست می‌کنید باید دو بند انگشت آب روی برنج باشد. پلو چکدرمه باید روی شعله ملایم گاز دم بکشد. در ابتدا شعله گاز را بیشتر کنید تا برنج بخار کند. وقتی برنج بخار کرد شعله را ملایم کنید تا برنج خوب دم بکشد. بعد از دم کشیدن در صورت دلخواه روی برنج روغن حیوانی بریزید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/04/88888.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (38, 'پلو سبزیجات', 'برنج را بر حسب نیاز به مدت ۲ الی ۳ ساعت همراه با نمک در آب خیس کنید. میزان آب باید حداقل یک بند انگشت از روی سطح برنج فاصله داشته باشد.', 'برنج را بر حسب نیاز به مدت ۲ الی ۳ ساعت همراه با نمک در آب خیس کنید. میزان آب باید حداقل یک بند انگشت از روی سطح برنج فاصله داشته باشد. در قابلمه دیگری، آب را تا نصفه (بستگی به مقدار برنج اولیه دارد) ریخته و روی اجاق با حرارت زیاد قرار دهید تا به جوش بیاید. برنج خیس خورده را درون قابلمه در حال جوش بریزید. وقتی روی دانه برنج دو ترک خورد آماده آبکش کردن است. برنج را در آبکش بریزید و با آب ولرم بشویید تا نمک‌های اضافی آن گرفته شود. برای پلو سبزیجات با مرغ (روش اول): ذرت را بپزید و همراه با کشمش، نخود فرنگی و هویج پخته شده در یک ماهیتابه تفت دهید. در یک ظرف دیگر، فیله های مرغ را به همراه پیاز، نمک و فلفل تفت دهید. بعد از پخت فیله‌ها، آن ها را به سایر مواد اضافه کنید. عصاره مرغ را می‌توانید در مقداری آب جوش حل کرده و روی مایه پلو بریزید. این سبزیجات را به برنج آبکش شده اضافه کنید. روش اضافه کردن آن به صورت لایه لایه است؛ یعنی لایه اول برنج و بعد یک لایه از سبزیجات و بعد برنج و باز یک لایه سبزیجات. این مرحله را تا اتمام مواد اولیه ادامه دهید. دقت کنید که برنج نباید به دیواره قابلمه بچسبد، همچنین بهتر است که به منظور دم کشیدن سریع، چندین سوراخ برروی سطح تا انتها ایجاد کنید. در انتها دم کنی را روی درب قابلمه قرار دهید و اجازه بدهید که کاملا برنج دم بکشد. برای سبزی پلو با مرغ (روش دوم): برنج را مانند دستور قبل آبکشی کنید. سبزی را ابتدا خرد کرده و اجازه بدهید تا خشک شود. سپس با کمی روغن آن ها را تفت دهید و زردچوبه و بقیه ادویه جات را نیز به آن اضافه کنید. بعد از اینکه سبزی ها به طور کامل تفت داده شد، آن ها را همراه با برنج به صورت لایه به لایه مانند قبل اضافه کنید و سپس اجازه بدهید تا پلو سبزیجات دم بکشد. مرغ را همراه با یک لیوان آب، پیاز، سیر و دیگر مواد مانند نمک، زعفران و فلفل به مدت نیم تا یک ساعت با هم ترکیب کرده و اجازه دهید تا کامل پخته شود و سپس کمی تفت دهید. بعد از این غذای شما آماده سرو است. برای سبزی پلو با ماهی: برای تهیه سبزی پلو مانند شیوه قبل اقدام می کنیم و فقط باید طرز پخت یا سرخ کردن ماهی را بدانیم. ابتدا پوست روی ماهی را باید کامل بگیرید. ابتدا در ماهیتابه کمی روغن بریزید تا داغ شود، سپس ماهی را به آرامی در ماهیتابه قرار دهید. بعد از ده دقیقه، ماهی را برگردانید و طرف دیگر را سرخ کنید. ممکن است که در هنگام سرخ کردن ماهی، چند بار مجبور به برگرداندن ماهی شوید؛ ولی حداقل ۲۰ دقیقه زمان لازم است تا ماهی شما کامل سرخ شود. برای برنج سبزیجات هندی: ابتدا برنج را آبکش کنید. گل کلم را به مدت ۱۵ دقیقه در آب جوش بپزید. سپس دانه های هل، میخک، چوب دارچین با هم در روغن تفت دهید. پیازها را در ماهیتابه تفت دهید و سپس زنجبیل با سیر له شده را به آن اضافه کرده تا کامل تفت داده شوند. سپس گوجه فرنگی را تفت دهید و بعد از چند دقیقه سیب زمینی را به آن اضافه کنید. بعد هویج، لوبیا سبز و گل کلم را به ماهیتابه اضافه کنید تا تفت داده شوند. در آخر هم برگ نعنا، گشنیز و آبلیمو را برای تفت دادن اضافه کنید. بعد از اینکه ترکیب سبزیجات شما تمام شد، آن را به همراه به صورت لایه به لایه به برنج اضافه کنید تا پلو سبزیجات هندی دم کشیده و آماده سرو شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/08/qq.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (39, 'پلو سنگ سیر کردی', 'پلو سنگ سیر یکی از غذاهای سنتی و محلی ایرانی و در اصل متعلق به شهرهای غربی ایران است که در کردستان و مهاباد تهیه و طبخ می شود. این غذای ایرانی با انواع گوشت مخصوصا گوشت بوقلمون و مرغ و سیب زمینی درست', 'پلو سنگ سیر یکی از غذاهای سنتی و محلی ایرانی و در اصل متعلق به شهرهای غربی ایران است که در کردستان و مهاباد تهیه و طبخ می شود. این غذای ایرانی با انواع گوشت مخصوصا گوشت بوقلمون و مرغ و سیب زمینی درست می شود و یکی از اجزای اصلی آن هم سیر است. طرز تهیه پلو سنگ سیر کردی (روش اول): در اولین مرحله، لوبیا چیتی را از قبل خیس کنید و بعد با مقداری آب بپزید. می توانید از کنسرو آماده آن هم استفاده کنید. لوبیا سبز را بشویید و سر و ته آن را جدا کنید. لوبیا ها را در قطعات ۱ تا ۲ سانتی متری برش بزنید و خرد کنید. هویج را پوست بگیرید و به صورت نگینی و در ابعادی مساوی با لوبیا سبز خرد کنید تا در نهایت ظاهر غذا زیباتر شود. برنج را از ۱ ساعت قبل در مقداری آب و نمک خیس کنید. سینه مرغ را بشویید و استخوان های آن را جدا کنید. سینه مرغ را به قطعات کوچک تر خرد و آن ها را چرخ کنید. سپس پوست پیاز را بگیرید و آن را جداگانه رنده یا چرخ کنید. اگر می خواهید پیاز را رنده کنید، قسمت ریشه یا انتهای آن را جدا نکنید تا هنگام رنده کردن، لایه های پیاز از هم باز نشوند. با دست آب پیاز را بگیرید و تفاله آن را به گوشت چرخ کرده اضافه کنید. در یک کاسه مناسب، مرغ چرخ شده، پیاز آب گرفته شده و آرد سوخاری را با هم مخلوط کنید. سپس فلفل سیاه، پودر فلفل پاپریکا، زردچوبه و فلفل را به همراه پودر سیر با هم مخلوط کنید و مواد را ورز دهید تا کاملا یکدست شوند. روی ظرف را با سلفون بپوشانید یا در ظرف را بگذارید و به مدت ۳۰ دقیقه تا ۱ ساعت در یخچال قرار دهید. این کار باعث ایجاد انسجام مواد می شود. بعد از یک ساعت، ظرف را بیرون بیاورید. از مواد داخل ظرف به اندازه یک گردو بردارید و به شکل گلوله در آورید. همه مواد را به همین صورت قلقلی کنید. بهتر است دوباره قلقلی ها را به مدت ۱۵ دقیقه در یخچال قرار دهید تا موقع سرخ کردن از هم وا نرود. در یک تابه مقداری روغن بریزید و اجازه دهید به خوبی داغ شود. کوفته ها را در تابه سرخ کنید. اجازه دهید کوفته قلقلی ها کاملا سرخ شوند. آن ها را از تابه خارج کنید و روی یک دستمال حوله ای قرار دهید تا روغن اضافی شان گرفته شود. آن ها را کنار بگذارید. لوبیا سبز را که از قبل خرد کرده اید به همراه هویج نگینی شده و لوبیا چیتی پخته و آبکش شده در یک قابلمه مناسب بریزید و به اندازه ۴ لیوان دسته دار فرانسوی، روی آن ها آب بریزید. قابلمه را روی حرارت قرار دهید تا وقتی که آب جوش بیاید. ۳۰ دقیقه بعد کوفته قلقلی ها را به مواد داخل قابلمه اضافه کنید و درب ظرف را بگذارید. اجازه دهید کوفته ها هم به مدت ۲ دقیقه با بقیه مواد بپزند. در این فاصله سیب زمینی ها را پوست بگیرید و نگینی خرد کنید. بهتر است سیب زمینی ها را با کمی روغن تفت دهید. سپس آن ها را به قابلمه اضافه کنید. یعنی ۲۰ دقیقه بعد از اضافه کردن کوفته ها، سیب زمینی ها را هم به مواد داخل قابلمه بیفزایید. ۱۰ دقیقه بعد از اضافه کردن سیب زمینی ها، برنجی را که از قبل خیس کرده بودید، آبکشی کنید و به قابلمه اضافه کنید. به میزان سلیقه و طبع خودتان، نمک اضافه کنید. وقتی آب داخل قابلمه کم شد، برنج های اطراف را در وسط قابلمه به شکل تپه جمع کنید و با انتهای قاشق، چند سوراخ داخل آن ایجاد کنید. درب ظرف را به همراه دم کنی روی قابلمه بگذارید. بعد از ۲ تا ۳ دقیقه که داخل قابلمه بخار جمع شد، حرارت را روی حالت ملایم تنظیم کنید. بسته به میزان حرارت و جنس قابلمه، ۳۰ تا ۴۰ دقیقه بعد پلو سنگ سیر شما آماده است. غذا را در ظرف مناسب بریزید و با ترشی، ماست یا سالاد دلخواه سرو کنید. طرز تهیه پلو سنگ سیر کردی (روش دوم): در ابتدا سینه مرغ بدون استخوان را چرخ کنید. پیاز را هم می توانید همراه با مرغ چرخ کنید. ولی بهتر است مانند روش قبل آن را جداگانه چرخ یا رنده و آب آن را با دست بگیرید و به مرغ چرخ شده اضافه کنید. در یک کاسه، مرغ چرخ شده را با پیاز، زردچوبه، فلفل سیاه و نمک مخلوط کنید. روی ظرف را بپوشانید و ۳۰ دقیقه در یخچال قرار دهید. هویج خرد شده، لوبیا قرمز پخته و لوبیا سبز خرد شده را در قابلمه با مقداری آب روی حرارت بگذارید. به مواد ۱ قاشق سبزی محلی کاکوتی اضافه کنید. درب ظرف را روی آن بگذارید و سیب زمینی ها را نگینی خرد کنید. ۱۰ دقیقه بعد از جوش آمدن آب، سیب زمینی های نگینی خرده شده را به مواد اضافه کنید. ۱۰ دقیقه صبر کنید تا سیب زمینی ها هم نرم شوند. مواد کوفته قلقلی را که در یخچال قرار داده بودید، بیرون بیاورید. آن ها را به شکل کوفته هایی کوچک تر از گردو، به شکل گلوله ای در آورید. در یک تابه به میزان لازم روغن بریزید و کوفته قلقلی ها را در آن سرخ کنید تا همه طرف آن ها طلایی شود. برنج را به مواد داخل قابلمه اضافه کنید و اجازه دهید آب آن، بخار و برنج کمی نرم شود. در حدی که دندان گیر باشد. کوفته قلقلی های سرخ شده را به برنج اضافه کنید. روی برنج را کمی روغن بریزید و دم کنی را روی آن بگذارید. بعد از ۳۰ تا ۴۰ دقیقه که پلو سنگ سیر پخت و دم کشید، روی آن را پودر سیر بریزید و آن را با ماست سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/08/polo-sang-sir-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (40, 'پلو مراکشی', 'برای درست کردن پلو مراکشی رستورانی ابتدا لوبیا سبز را به‌صورت ریز خرد کنید. مقداری آب را روی گاز قرار دهید و در آن نمک بریزید و صبر کنید تا به جوش بیاید. بعد لوبیا سبزهای خردشده را به قابلمه اضافه کنی', 'برای درست کردن پلو مراکشی رستورانی ابتدا لوبیا سبز را به‌صورت ریز خرد کنید. مقداری آب را روی گاز قرار دهید و در آن نمک بریزید و صبر کنید تا به جوش بیاید. بعد لوبیا سبزهای خردشده را به قابلمه اضافه کنید. ۲ دقیقه آنها را جوشانده و سپس آنها را آبکش کنید و آب سرد روی آن بریزید. سینه مرغ را به‌صورت مکعبی و ریز، خرد کنید. در تابه‌ای نچسب، کره بریزید و پس از گرم شدن تابه، سینه مرغ را به آن اضافه کنید. پس از کمی تفت دادن، زعفران نیز در تابه بریزید تا طعم خوبی پیدا کند. فلفل دلمه‌ای را در ظرفی دیگر، به صورت مکعبی خرد کنید و به تابه مرغ اضافه کنید و همه مواد را باهم تفت دهید. قارچ را به صورت دلخواه خرد کنید و به تابه قبلی اضافه کنید. پس از کمی تفت خوردن، رب، نمک و فلفل را به تابه اضافه کنید و هم بزنید تا رب باز شود. در همین حین، در تابه‌ای جدا پیاز را سرخ کنید. پس از طلایی شدن پیاز، ذرت پخته شده و لوبیا سبز را تابه اضافه کنید و هم بزنید تا تفت بخورد. پس از گذشت ۵ دقیقه، زیر شعله را خاموش کنید و مواد این تابه را به مواد مرغ و قارچ قبلی، اضافه و مخلوط کنید. در مرحله آخر، باید برنج را بپزید. برنج را از چند ساعت قبل، بگذارید خیس بخورد. برای پخت برنج، در قابلمه‌ای نچسب روغن بریزید و سپس ته‌دیگ مورد نظر خود را در آن قرار دهید. می‌توانید برای این پلو خوشمزه، ۱ سیب‌زمینی رنده شده، ۱ قاشق ماست، ۱ زرده تخم‌مرغ و کمی زعفران را با هم مخلوط کنید و کف قابلمه بریزید. البته اگر این ته‌دیگ را دوست ندارید، می‌توانید از نان لواش یا سیب‌زمینی نیز استفاده کنید. پس از قرار دادن ته‌دیگ، یک لایه برنج بریزید و روی برنج، چند قاشق از مواد ترکیب شده قبلی را اضافه کنید. این روند را ادامه دهید تا یکی در میان، یک لایه برنج و یک لایه سبزیجات و مرغ داشته باشید. در آخر، روی پلو دارچین و کره بریزید و در آن را خوب ببندید تا دم بکشد و حاضر شود. برای دم کشیدن، باید شعله را کم کنید؛ اما برای پخت ته‌دیگ بهتر است چند دقیقه زیر قابلمه را زیاد کنید و سپس کم کنید تا دم بکشد. نکته جالب توجه این است که برای تهیه پلو مراکشی، می‌توانید به جای مرغ، از تکه‌های کوچک گوشت استفاده کنید. فقط حواستان باشد که برای خوشمزه شدن غذا، گوشت را باید با دقت طعم‌دار کنید. اگر گیاهخوار هستید یا در مصرف گوشت محدودیت دارید و می خواهید یک پلوی بدون گوشت میل کنید پلو مراکشی را بدون گوشت درست کنید. عطر و طعم سبزیجات و ادویه موجود در این پلو برای داشتن یک پلو خوشمزه کافی است.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/Brown-Rice-with-Nuts-and-Raisins_ExtraLarge1000_ID-1056025-e1607780975506.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (41, 'پلو مکزیکی', 'برای تهیه استاک سبزیجات، تمام سبزیجات یخچالی شسته شده (مانند ۳ عدد هویج، ۴ ساقه کرفس، ۸ شاخه جعفری، ۲ ساقه رازیانه و ۸ شاخه آویشن تازه به همراه یک عدد پیاز قرمز بزرگ و ۵ حبه سیر، دو تا سه تکه زنجبیل،', 'برای تهیه استاک سبزیجات، تمام سبزیجات یخچالی شسته شده (مانند ۳ عدد هویج، ۴ ساقه کرفس، ۸ شاخه جعفری، ۲ ساقه رازیانه و ۸ شاخه آویشن تازه به همراه یک عدد پیاز قرمز بزرگ و ۵ حبه سیر، دو تا سه تکه زنجبیل، ۱۰ عدد دانه فلفل سیاه و دانه گشنیز، ۲ عدد برگ بو) را خرد کرده و در یک قابلمه بزرگ بریزید. ۱۲ فنجان آب به آنها اضافه کنید و ۴۵ دقیقه زمان دهید تا بپزند. بعد از این زمان سبزیجات را از صافی رد کرده و عصاره آن را استفاده کنید. روغن را در یک ماهیتابه تفلون بزرگ گرم کنید. برنج را به روغن اضافه کنید و به آرامی به مدت ۱۰ دقیقه برنج را در روغن هم بزنید تا رنگ برنج‌ها تغییر کند و کم کم طلایی شوند. حالا رب گوجه‌فرنگی یا گوجه فرنگی پوره شده، استاک سبزیجات، سیر رنده شده، زیره سبز، نمک و فلفل را اضافه کنید و هم بزنید. به مدت ۱۵ تا ۲۵ دقیقه اجازه دهید پلو مکزیکی دم بکشد و آب آن کاملا کشیده شود. بعد از سرو پلو مکزیکی آن را با جعفری خرد شده، گوجه‌فرنگی و برش لیموترش تزیین کنید.', 26, 54, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2018/11/Authentic-Mexican-Rice-square.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (42, 'پوره بادمجان هندی', 'در اولین مرحله، بادمجان‌های شسته شده را چندین بار با چنگال سوراخ کنید. در ادامه، بادمجان‌ها را روی شعله گاز یا منقل بگذارید و چند دقیقه صبر کنید تا پوست آن‌ها بسوزد و بادمجان‌ها کاملا کبابی شوند.', 'در اولین مرحله، بادمجان‌های شسته شده را چندین بار با چنگال سوراخ کنید. در ادامه، بادمجان‌ها را روی شعله گاز یا منقل بگذارید و چند دقیقه صبر کنید تا پوست آن‌ها بسوزد و بادمجان‌ها کاملا کبابی شوند. پیاز را بشویید، پوست بگیرید و به صورت نگینی خرد کنید و کنار بگذارید. سپس، گوجه فرنگی‌های شسته شده را هم پوست بگیرید و کاملا ریز کنید. گشنیز تازه را پاک کنید و به خوبی بشویید. گشنیز شسته شده را در آبکش بریزید تا آب اضافی آن خارج شود. سپس، برگ‌های ترو تازه گشنیز را ساطوری کنید و کنار بگذارید. پیازچه‌ها را هم بشویید و پس از خشک شدن، ریز کنید تا بعدا برای تزیین روی غذا بریزید. حالا، بادمجان‌های کبابی را در یک کیسه پلاستیکی قرار دهید یا زیر آب سرد بگیرید تا خنک شوند و پوست‌شان ور بیاید. سپس، بادمجان‌های دودی و خوشمزه را پوست بگیرید و با کمک گوشتکوب برقی یا حتی با چنگال، آن‌ها را له و پوره کنید. این کار را تا زمانی ادامه دهید که پوره بادمجان کاملا یکدست شود. مقداری روغن را در تابه مناسبی بریزید و روی حرارت متوسط بگذارید تا داغ شود. سپس، قطعات پیاز را به روغن داغ اضافه کنید تا کاملا سبک و طلایی شوند. در ادامه، گوجه فرنگی بدون پوست و خرد شده را نیز در تابه بریزید و به مدت چند دقیقه تفت دهید تا کاملا نرم شود. در این مرحله، پودر گشنیز، فلفل سیاه، فلفل قرمز و مقداری نمک را به ترکیب پیاز و پوره گوجه فرنگی اضافه کنید. مواد را هم بزنید تا ادویه‌ها تفت ببینند و عطرشان را آزاد کنند. سپس، پوره بادمجان کبابی شده را نیز در تابه بریزید و کاملا با باقی مواد ترکیب کنید. حالا درب تابه را ببندید و حرارت را ملایم کنید. حدود ۱۰ الی ۱۵ دقیقه منتظر بمانید تا پوره بادمجان هندی بپزد و غلیظ شود. در طول این مدت، همه مواد کاملا طعم یکدیگر را می‌گیرند و جا افتاده می‌شوند. پس از گذشت این مدت، پودر خردل و نعنا خشک را هم در تابه بریزید و مواد را هم بزنید. این کار را برای یکی دو دقیقه ادامه دهید تا عطر خردل و نعنا هم به مشام برسد. در نهایت، حرارت را خاموش کنید و تابه را از روی اجاق بردارید. پوره بادمجان هندی را در ظرف سرو بریزید. در حالی که غذا هنوز داغ است، گشنیز ساطوری و پیازچه خرد شده را روی پوره بادمجان هندی اضافه کنید. این غذای خوشمزه و تند و تیز در اصل، یک غذای نونی است که با نان سرو می‌شود. اما در صورت تمایل، می‌توانید آن را با برنج کته نیز نوش جان کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/08/%D9%BE%D9%88%D8%B1%D9%87-%D8%A8%D8%A7%D8%AF%D9%85%D8%AC%D8%A7%D9%86-%D9%87%D9%86%D8%AF%DB%8C-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (43, 'تاس کباب', 'پیش از شروع مراحل تهیه تاس کباب، گوشت گوسفندی را بشویید و در ابعاد خورشتی با ضخامت کم (تقریبا به صورت ورقه‌ای) برش بزنید تا هم در غذا به چشم بیاید و هم زودتر پخته شود. یک حبه سیر را پوست بگیرید و به ص', 'پیش از شروع مراحل تهیه تاس کباب، گوشت گوسفندی را بشویید و در ابعاد خورشتی با ضخامت کم (تقریبا به صورت ورقه‌ای) برش بزنید تا هم در غذا به چشم بیاید و هم زودتر پخته شود. یک حبه سیر را پوست بگیرید و به صورت ریز رنده کرده یا با استفاده از سیر له‌کن، کاملا له کنید و کنار بگذارید. پیاز، سیب زمینی و هویج را هم بشویید، پوست بگیرید و به صورت حلقه حلقه برش بزنید و کنار بگذارید. به را نیز بشویید و پس از خارج کردن هسته‌ها به قطعات نسبتا کوچک تقسیم کنید. برای زیباتر شدن شکل ظاهری تاس کباب، بهتر است سعی کنید همه سبزیجات را یک شکل و در ابعاد مشابه برش بزنید. حالا، رب گوجه فرنگی را با نمک، فلفل سیاه، زردچوبه و پودر پاپریکا مخلوط کنید. سپس، سیر له شده یا رنده شده را نیز به این مواد اضافه کنید. در ادامه، به اندازه ۲ لیوان آب جوش را روی این ترکیب بریزید و به خوبی هم بزنید تا مایع یکدستی ایجاد شود. این سس را کنار بگذارید تا هر وقت نوبتش شد در قابلمه بریزید. در این مرحله از طرز تهیه تاس کباب با گوشت تکه ای، قابلمه مناسبی را انتخاب کنید. سپس، پیاز حلقه شده را کف قابلمه بچینید. قطعات گوشت گوسفندی را هم روی پیازها قرار دهید. در ادامه، هویج‌های حلقه‌ای، به خرد شده و برش‌های سیب زمینی را به نوبت و به صورت لایه لایه در قابلمه قرار دهید. حتما قطعات سیب زمینی را به عنوان آخرین لایه روی باقی سبزیجات بچینید تا در طول پخت له نشوند. حالا، سس تاس کباب که قبلا درست کردید را روی مواد داخل قابلمه بریزید. درب قابلمه را ببندید و قابلمه را برای چند دقیقه روی حرارت زیاد بگذارید تا کاملا بخار کند. سپس، حرارت را ملایم کنید و حدود ۲ ساعت منتظر بمانید تا گوشت گوسفندی کاملا بپزد و تاس کباب شما جا افتاده شود. پس از گذشت این مدت، می‌توانید تاس کباب جا افتاده و خوشمزه را سرو کنید. توجه داشته باشید، تاس کباب باید کم آب و غلیظ باشد. این غذای لذیذ را می‌توانید با نان، سبزی خوردن، انواع ترشی و سالاد شیرازی نوش جان کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/04/tas-kabab-ba-goosht-min.jpeg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (44, 'تاکوی بوقلمون مکزیکی', 'در اولین مرحله، سبزیجات شامل فلفل دلمه قرمز، کدو سبز، گوجه گیلاسی و برگ کاهو را بشویید و در آبکش قرار دهید تا آب اضافه از آنها خارج شود و کمی خشک شوند.', 'در اولین مرحله، سبزیجات شامل فلفل دلمه قرمز، کدو سبز، گوجه گیلاسی و برگ کاهو را بشویید و در آبکش قرار دهید تا آب اضافه از آنها خارج شود و کمی خشک شوند. سپس کاهو را خرد کنید. فلفل دلمه را به صورت نگینی ریز خرد کنید. کدو سبز را نیز به صورت حلقه‌ای یا نگینی خرد کنید. گوجه گیلاسی‌ها را نیز از وسط به دونیم تقسیم کنید و همه مواد را برای مراحل بعدی کنار بگذارید. بعد از آن به سراغ گوشت بوقلمون بروید. گوشت بوقلمون را نیز تمیز کنید و با آب بشویید و اجازه دهید در آبکش برای چند دقیقه قرار بگیرد تا آب اضافه از آن خارج شود. برای تهیه تاکوی بوقلمون به شیوه مکزیکی، باید گوشت بوقلمون را چرخ کنید. پس بهتر است از سینه بوقلمون استفاده کنید تا کار چرخ کردن آن برایتان راحت‌تر باشد و گوشت چرخ کرده یکدست‌تری داشته باشید. اگر بعد از یکبار چرخ کردن، گوشت چرخ کرده بوقلمون به اندازه کافی نرم نشده بود حتما یکبار دیگر آن را چرخ کنید. حالا باید پیاز داغ درست کنید. پیاز را پوست بگیرید و بشویید. ابتدا و انتهای پیاز را جدا کنید و سپس با یک چاقوی تیز پیاز را به صورت نگینی ریز خرد کنید. یک تابه و یا قابلمه را روی حرارت قرار دهید و مقدار کافی روغن در آن بریزید. بعد از آنکه روغن داغ شد، پیازهای نگینی را اضافه کنید. پیاز را روی حرارت متوسط و با روغن کافی تفت دهید تا طلایی و سبک شوند. بعد از آنکه پیازها به اندازه کافی سبک و طلایی شدند، گوشت بوقلمون چرخ کرده را اضافه کنید. بوقلمون چرخ کرده را با یک قاشق و یا کفگیر چوبی باز کنید تا هنگام سرخ شدن گلوله گلوله نشود. سپس گوشت چرخ کرده بوقلمون را با پیاز داغ تفت دهید. تفت دادن را تا جایی ادامه دهید رنگ گوشت چرخ کرده برگردد و سرخ شود. در این مرحله، سبزیجاتی که از قبل خرد کرده‌اید شامل فلفل دلمه قرمز، کدو سبز و نصف گوجه گیلاسی‌ها را به تابه حاوی گوشت بوقلمون و پیاز داغ اضافه کنید. نصف دیگر از گوجه گیلاسی‌ها را برای سرو نهایی تاکوی بوقلمون نگه دارید. یک دور همه مواد را با یکدیگر هم بزنید تا به طور یکدست با هم مخلوط شوند. سپس یک قاشق غذاخوری سس سالسا را نیز به ترکیب فوق اضافه کنید و سس را نیز با دیگر مواد هم بزنید تا سس به خورد مایه تاکو برود. مقدار لازم از نمک، فلفل سیاه و فلفل قرمز را نیز اضافه کنید و اجازه دهید ترکیب به دست آمده حدود بیست دقیقه روی حرارت ملایم باقی بماند تا عطر و طعم همه مواد با یکدیگر ترکیب شود. در این مرحله از آماده سازی تاکوی بوقلمون با سبزیجات، کاهویی که از قبل خرد کرده‌اید و به همراه نیم دیگری از گوجه گیلاسی‌ها را در یک ظرف مناسب بریزید. یک قاشق غذاخوری سس سالسا را به همراه یک قاشق غذاخوری روغن زیتون نیز به آنها اضافه کنید و به خوبی هم بزنید. تعداد لازم از نان ترتیلا را روی یک سطح مناسب قرار دهید و سپس مایه تاکوی بوقلمون آماده شده روی حرارت را در نان‌ها بریزید. مقداری از مخلوط کاهو و گوجه گیلاسی و سس سالسا را نیز رو مواد گوشتی تاکو بریزید تا آن را خوشمزه‌تر کند. تا زمانی که مایه تاکو داغ است، پنیر چدار رنده شده را به آن اضافه کنید تا با حرارت مواد، پنیر چدار آب شود. بعد از آن تاکوی بوقلمون شما آماده است. می‌توانید این وعده غذایی جذاب را با سس دلخواه سرو کنید و از طعم تندش لذت ببرید!', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/Takoi-boghlamon.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (45, 'تاوا کبابی تبریزی', 'در اولین مرحله، اگر از گوشت چرخ کرده یخ زده استفاده می‌کنید، آن را از فریزر بیرون بیاورید و در دمای محیط قرار دهید تا یخ آن باز شود. گوجه فرنگی و فلفل سبز تند را بشویید و در آبکش قرار دهید.', 'در اولین مرحله، اگر از گوشت چرخ کرده یخ زده استفاده می‌کنید، آن را از فریزر بیرون بیاورید و در دمای محیط قرار دهید تا یخ آن باز شود. گوجه فرنگی و فلفل سبز تند را بشویید و در آبکش قرار دهید. یک عدد پیاز متوسط را پوست بگیرید و بشویید. زعفران را بسابید و روی آن مقداری آب جوش یا چند قطعه یخ قرار دهید تا رنگ بگیرد. پیاز شسته شده را ریز رنده کنید. آب پیاز رنده شده را بگیرید و دور بریزید و تفاله پیاز را در یک ظرف مناسب بریزید. گوشت چرخ کرده را به تفاله پیاز اضافه کنید و با دست به خوبی ورز دهید تا یک مایه منسجم درست شود. نمک، فلفل سیاه و زردچوبه را به ترکیب گوشت چرخ کرده و پیاز اضافه کنید. یک قاشق غذاخوری زعفران غلیظ دم کرده را نیز به مایه کباب اضافه کنید و همه را با هم ورز دهید تا عطر و طعم ادویه و زعفران کاملا به خورد مایه کباب برود. بعد از آنکه همه مواد به خوبی مخلوط شدند، ده دقیقه دیگر ورز دادن مایه تاوا کبابی را ادامه دهید. ورز دادن کمک می‌کند تا مایه کباب منسجم شود و هنگام پخت ترک نخورد و از هم نپاشد. روی ظرف مایه کباب را با سلفون بپوشانید و به مدت نیم ساعت تا یک ساعت آن را در یخچال قرار دهید تا استراحت کند. بعد از گذشت مدت زمان لازم، مایه کباب را از یخچال بیرون بیاورید. یک تابه با ابعاد مناسب انتخاب کنید. کف آن روغن بریزید و مایه کباب را روی آن پخش کنید تا به طور یکدست سطح تابه را بپوشاند. تابه را روی حرارت قرار دهید و اجازه دهید یک سمت کباب در مدت زمان ۱۵ دقیقه روی حرارت ملایم بپزد. بعد از گذشت یک ربع کباب را برگردانید تا سمت دیگرش نیز سرخ شود. می‌توانید کباب تابه‌ای را برش بزنید تا برگرداندنش راحت‌تر باشد. در این مرحله بعد از برگرداندن تاوا کبابی، دو عدد گوجه را از وسط نصف کنید و روی کباب قرار دهید. دو عدد فلفل سبز تند را نیز در اطراف تابه بچینید. یک قاشق غذاخوری رب گوجه فرنگی را در نصف لیوان آب حل کنید. به ترکیب به دست آمده مقداری زعفران دم کرده اضافه کنید و هم بزنید تا به خوبی با هم مخلوط شوند. سس به دست آمده را روی تاوا کبابی بریزید و حدود ۱۰ دقیقه زمان بدهید تا کباب همراه با سس به خوبی بپزد. بعد از آنکه سس غلیظ و کباب کاملا پخته شد، می‌توانید آن را از روی حرارت بردارید. تاوا کبابی را در یک ظرف مناسب بریزید و به همراه برنج آبکش، برنج کته یا نان آن را سرو کنید. برای تهیه تاوا کبابی در فر، بعد از آماده کردن و استراحت دادن مایه کباب، فر را با دمای ۲۵۰ درجه سانتی‌گراد گرم کنید. سینی فر را چرب کنید و مایه کباب را روی آن پهن کنید. گوجه فرنگی و فلفل سبز را نیز روی کباب قرار دهید و سینی را داخل فر بگذارید. دما را روی ۱۸۰ درجه سانتی‌گراد تنظیم کنید و زمان بدهید تا برای سی تا چهل و پنج دقیقه تاوا کباب در فر بپزد. در ده دقیقه پایانی پخت درب فر را باز کنید و مقداری زعفران غلیظ دم کرده به کباب در حال پخت اضافه کنید و بعد از آماده شدن آن را سرو کنید. اگر تمایلی به استفاده از گوشت قرمز ندارید، می‌توانید این غذای لذیذ تبریزی را با مرغ تهیه کنید. کافی است ۶۰۰ گرم ترکیب ران سینه مرغ را کاملا چرخ کنید و سپس مطابق مراحل بالا مایه تاوا کباب را آماده و آن را بپزید. سینه مرغ چون گوشت لخت و صافی دارد برای چرخ کردن مناسب‌تر است و ران مرغ از خشک شدن مایه کباب جلوگیری می‌کند. تهیه تاوا کبابی مرغ به زمان کمتری برای پخت نیاز دارد. برای تهیه تاوا کبابی با سیب زمینی، کافی است سیب زمینی را به صورت حلقه‌های نازک برش بزنید و به همراه گوجه و فلفل سبز روی کباب قرار دهید. هنگام پخت درب تابه را حتما ببندید تا سیب زمینی‌ها بخارپز شوند. می‌توانید سیب زمینی را جداگانه سرخ کنید و در کنار تاوا کباب آن را سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (46, 'تپسی کباب ترکیه ای', 'برای تهیه تپسی کباب ترکیه‌ای، اول پیاز را رنده کنید و آبش را بگیرید، سیر را هم ریز رنده کنید. جعفری، فلفل دلمه قرمز و فلفل سبز را هم خرد کرده و با گوشت چرخ‌کرده مخلوط کنید؛ بعد ادویه‌ها را اضافه کنید', 'برای تهیه تپسی کباب ترکیه‌ای، اول پیاز را رنده کنید و آبش را بگیرید، سیر را هم ریز رنده کنید. جعفری، فلفل دلمه قرمز و فلفل سبز را هم خرد کرده و با گوشت چرخ‌کرده مخلوط کنید؛ بعد ادویه‌ها را اضافه کنید و مایه را چند دقیقه به خوبی ورز دهید و ۳۰ تا ۶۰ دقیقه در یخچال استراحت دهید. در این فاصله، بادمجان‌ها را حلقه‌ای برش بزنید، نمک بپاشید و در روغن سرخ کنید. حالا کف سینی فر یا ظرف پیرکس را کمی چرب کرده و مایه گوشت را به ضخامت حدود ۱ سانتی‌متر پهن کنید. گوجه‌ فرنگی، پیاز، فلفل سبز و بادمجان‌های سرخ‌شده را روی مایه کباب بچینید. در ادامه، با رب گوجه یا رب فلفل، آب، روغن و نمک یک سس نسبتا رقیق درست کنید و روی کباب بریزید. این کباب را در فر از قبل گرم‌ شده با دمای ۲۰۰ درجه سانتی‌گراد به مدت ۴۰ تا ۴۵ دقیقه بپزید و در صورت تمایل، چند دقیقه آخر گریل را روشن کنید. در نهایت، تپسی کباب را مثل پیتزا برش بزنید و همراه با نان یا برنج، کنار ماست یا سالاد چوپان سرو کنید.', 48, 97, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/09/%D8%AA%D9%BE%D8%B3%DB%8C-%DA%A9%D8%A8%D8%A7%D8%A8-5.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (47, 'تترازینی بوقلمون', 'ابتدا سینه بوقلمون را بشویید و به صورت مکعبی یا نواری (ترجیحا کمی درشت) خرد کنید. بوقلمون‌ها را در صورت دلخواه، با مقداری ادویه مثل فلفل سیاه، نمک، پودر زنجبیل و … و چاشنی‌هایی مثل آبلیمو مخلوط کنید و', 'ابتدا سینه بوقلمون را بشویید و به صورت مکعبی یا نواری (ترجیحا کمی درشت) خرد کنید. بوقلمون‌ها را در صورت دلخواه، با مقداری ادویه مثل فلفل سیاه، نمک، پودر زنجبیل و … و چاشنی‌هایی مثل آبلیمو مخلوط کنید و نیم ساعت در یخچال بگذارید تا طعم‌دار شوند و بوی زهم بوقلمون هم گرفته شود. قارچ‌ها را هم بشویید و به صورت ورقه‌ای نازک خرد کنید. در یک قابلمه تا نیمه آب بریزید و روی حرارت بگذارید تا شروع به جوشیدن کند. سپس پاستا‌ها را در قابلمه بریزید. می‌توانید پاستاها را از وسط نصف کنید. اجازه دهید پاستا‌ها بجوشند تا ال دانته یا دندان‌گیر شوند. (در حدی که کاملا نپزند ولی نرم شوند). برای این حالت حدود ۷ تا ۱۰ دقیقه باید پاستا را بجوشانید. سپس پاستاها را آبکش کنید. ابتدا فر را روشن و روی دمای ۱۸۰ درجه سانتی‌گراد تنظیم کنید تا گرم شود. در یک تابه، نیمی از کره را بریزید و روی حرارت بگذارید تا ذوب شود. سپس قارچ‌ها را در تابه بریزید و تفت دهید. قارچ‌ها را کنار بگذارید. در یک تابه دیگر، بقیه کره را بریزید. سپس آرد الک شده را در تابه بریزید و تفت دهید تا طعم خام آن گرفته شود. در ادامه، شیر را اضافه کنید و هم بزنید تا کاملا با آرد مخلوط شده و اگر گلوله‌ای ایجاد شد، آن را باز کنید. آب مرغ را اضافه کنید و هم بزنید تا مخلوطتان کاملا یکدست شود. حدود ۵ دقیقه مواد را تفت دهید. بعد قارچ تفت داده شده را اضافه کنید و هم بزنید. شعله را روی حالت ملایم تنظیم کنید. در این مرحله، بوقلمون‌های خرد شده، پاستای آبکش شده، پنیر فتای خرد شده، نصف پنیر پارمزان، نخود فرنگی، فلفل سیاه و نمک را در تابه بریزید و همه مواد را به آرامی هم بزنید تا به خوبی با هم مخلوط شوند. شعله را خاموش کنید. کف و دیواره‌های قالبی که می‌خواهید تترازینی را در آن بپزید، با کمی کره یا روغن، به کمک برس آشپزی، چرب کنید. مایه تترازینی را در ظرف بریزید و کف ظرف را چند بار به سطح بکوبید تا کاملا روی سطح آن صاف شود. سپس نصف باقی‌مانده پنیر پارمزان را روی مواد تترازینی بپاشید. خرده‌های نان یا پودر سوخاری را هم به عنوان آخرین لایه بریزید. روی ظرف را فویل آلومینیومی بکشید. ظرف را در طبقه وسط فر که گرم شده است بگذارید. ظرف را به مدت ۴۵ دقیقه در فر بگذارید. وقتی تترازینی پخت، فویل روی ظرف را بردارید. گریل را روشن کنید و ۲ تا ۳ دقیقه دیگر صبر کنید تا روی تترازینی بوقلمون کمی طلایی شود. سپس ظرف را از فر بیرون بیاورید. ۵ دقیقه بعد، وقتی تترازینی کمی از دما افتاد، می‌توانید این غذای خوشمزه را با چاقو به اندازه‌های دلخواه، برش بزنید و برش‌ها را در ظرف سرو بگذارید. روی تترازینی بوقلمون را با جعفری تازه خرد شده تزیین و آن را میل کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/CEykCtdlQbCrinONhPRB_creamy-turkey-tetrazzini-IMG_9849.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (48, 'ته چین آلبالو با مرغ و گوشت', 'ته چین آلبالو یک غذای ایرانی خوش‌رنگ و لعاب و جزو انواع آلبالو پلو مجلسی و بسیار پرطرفدار برای شام و ناهار است. برای طرز تهیه ته چین آلبالو مجلسی و خوشمزه هم می‌توانید از گوشت استفاده کنید و هم مرغ؛ ب', 'ته چین آلبالو یک غذای ایرانی خوش‌رنگ و لعاب و جزو انواع آلبالو پلو مجلسی و بسیار پرطرفدار برای شام و ناهار است. برای طرز تهیه ته چین آلبالو مجلسی و خوشمزه هم می‌توانید از گوشت استفاده کنید و هم مرغ؛ به هر حال طعم این غذا با مرغ و برنج یا حتی با گوشت و برنج بسیار لذیذ خواهد بود. یکی از رایج‌ترین روش‌های تهیه ته چین آلبالوی خانگی، استفاده از تکه‌های مرغ در این غذا است. این ته چین بسیار خوشمزه است و برای پذیرایی از مهمان‌ها و همچنین دورهمی‌های خانوادگی نیز بسیار مناسب است. در ادامه می‌توانید با مراحل طرز تهیه ته چین آلبالو پلو با مرغ آشنا شوید. در اولین مرحله از طرز تهیه ته چین آلبالو با مرغ باید مرغ‌ها را به تکه‌های کوچک برش دهید. سپس آن‌ها را در یک ظرف بریزید و مقداری آب لیموترش تازه، فلفل سیاه، نمک و پودر سیر به آن اضافه کنید. مرغ‌ها را در یخچال قرار دهید و اجازه دهید برای نیم ساعت مزه‌دار شوند. در این فرصت تا مرغ‌ها مزه‌دار شوند، باید آلبالوها را بشویید و هسته آن‌ها را با یکی از روش‌های هسته‌گیری آلبالو با سیخ چوبی یا نی شیشه‌ای خارج کنید. آلبالوها را در یک قابلمه بریزید و شکر را اضافه کنید. قابلمه را با حرارت ملایم روی گاز قرار دهید تا آلبالو آب بیندازد و بجوشد. سپس آلبالوها را آبکش کنید و آب آن را نیز جداگانه در ظرف بریزید تا بعداً برای تهیه شربت آلبالو و یا رنگ دادن به برنج آلبالو پلو استفاده کنید. بعد از این که مرغ‌ها مزه‌دار شدند، در این مرحله از طرز تهیه ته چین آلبالو باید در یک ماهیتابه مقداری روغن بریزید و مرغ‌ها را در روغن سرخ کنید تا رنگ آن‌ها طلایی شود. برای این که برنج شما خوب پخته شود و قد بکشد، باید آن را برای یک ساعت در آب و نمک بخیسانید. سپس یک قابلمه آب روی گاز قرار دهید تا به جوش بیاید. برنج را به آب جوش اضافه کنید و بعد از این که برنج نیم‌پز شد و قد کشید، آن را آبکش کنید. برای درست کردن مواد تهچین آلبالو باید زرده‌های تخم مرغ را در یک کاسه بریزید و همراه با روغن خوب بزنید. حالا ماست چکیده و زعفران دم کرده را اضافه کنید و مواد را با همزن دستی مخلوط کنید. برنج را کم کم به مواد ته چین آلبالو اضافه کنید. برنج باید به اندازه‌ای باشد که کاملاً در مواد ته چین آلبالو غوطه‌ور شود. در این مرحله از طرز تهیه ته چین آلبالو با مرغ باید یک قالب مناسب انتخاب کنید و کف آن کاغذ روغنی بگذارید. حالا مقداری از برنج ترکیب شده با تخم مرغ را کف قالب بریزید و با قاشق آن را فشرده کنید. سپس یک لایه مرغ و آلبالو بریزید و دوباره یک لایه برنج. این کار را آنقدر ادامه دهید که مواد ته چین تمام شود. به یاد داشته باشید که بعد از اضافه کردن هر لایه برنج باید آن را با پشت قاشق فشار دهید تا کاملاً فشرده شود. بعد از این که مواد ته چین را در قالب ریختید، باید یک فویل روی برنج بکشید و چند سوراخ روی آن ایجاد کنید. سپس آن را در فری که از قبل برای یک ربع با دمای ۱۸۰ درجه سانتی‌گراد گرم شده قرار دهید. یک ساعت زمان برای پخت ته چین کافی است. در چند دقیقه انتهای پخت فویل را بردارید تا روی ته چین نیز طلایی شود. خیلی افراد طعم گوشت را بیشتر از طعم مرغ دوست دارند و ترجیح می‌دهند برای تهیه ته چین آلبالو از گوشت چرخ کرده استفاده کنند. برای تهیه ته چین آلبالو با گوشت چرخ کرده می‌توانید هم گوشت را به صورت قلقلی آماده کنید و دور تا دور غذا قرار دهید و هم آن را به صورت لایه‌ای داخل برنج بریزید. در ادامه می‌توانید با مراحل درست کردن ته چین آلبالو با گوشت چرخ کرده آشنا شوید. در اولین مرحله از طرز تهیه ته چین آلبالو با گوشت چرخ کرده باید آلبالوها را بشویید و هسته آن‌ها را جدا کنید. سپس آلبالوها را همراه با شکر در یک قابلمه بریزید و برای چند ساعت در یخچال قرار دهید تا شکر در آلبالو حل شود. در نهایت آلبالوها را آبکش کنید تا آب اضافه آن گرفته شود. بعد از آبکش کردن آلبالوها، برنج را بشویید و آن را در یک ظرف آب بریزید و مقداری نمک اضافه کنید. اجازه دهید برنج برای چند ساعت در آب خیسانده شود. پیاز را به صورت نگینی خرد کنید و آن را در ماهیتابه سرخ کنید. بعد از این که پیازها کمی سرخ شدند، گوشت چرخ کرده را اضافه کنید. مقداری نمک و فلفل به گوشت بزنید. سپس یک لیوان آب اضافه کنید و صبر کنید تا گوشت‌ها کاملاً بپزند. برای آبکش کردن برنج باید یک قابلمه آب جوش روی گاز قرار دهید. بعد از اینکه آب به جوش آمد برنج را اضافه کنید. سپس صبر کنید برنج شما مغز پخت شود و کمی قد بکشد. در این مرحله می‌توانید برنج را آبکش کنید. برای تهیه مواد ته چین در این مرحله از طرز تهیه آلبالو پلو قالبی با گوشت باید زرده تخم مرغ را با ماست و زعفران ترکیب کنید و خوب هم بزنید. سپس برنج را اضافه کنید. برای درست کردن ته چین آلبالو باید ابتدا کف قابلمه روغن بریزید. سپس مقداری برنج زعفرانی اضافه کنید. گوشت چرخ کرده و آلبالوها را همراه با برنج به صورت لایه لایه داخل قالب یا قابلمه بریزید و بعد از هر مرحله مواد را با پشت قاشق فشار دهید. در انتها قابلمه برنج را با شعله پخش کن روی گاز قرار دهید. مقداری شربت آلبالو و کره را با هم ترکیب کنید و روی برنج بریزید. اجازه دهید ته چین آلبالو برای حدود ۲ ساعت به آرامی دم بکشد. برای درست کردن ته چین آلبالو بدون فر باید ابتدا مقداری روغن در قابلمه بریزید و برای ته دیگ از مقداری برنج زعفرانی استفاده کنید. سپس مواد ته چین را به صورت لایه لایه در قابلمه بریزید و خوب فشار دهید. قابلمه را برای مدت ۱ ساعت یا ۱ ساعت و نیم با حرارت ملایم روی گاز قرار دهید تا دم بکشد. بهتر است برای دم کشیدن بهتر برنج از شعله پخش کن و دم کنی استفاده کنید. در نهایت ته چین آلبالو را به آرامی داخل دیس برگردانید و آن را با مقداری آلبالو، زرشک و خلال پسته و بادام تزیین کنید. برای خوش عطر شدن ته چین آلبالو می‌توانید مقداری گلاب به ته چین اضافه کنید. اگر دوست دارید ته چین آلبالو شما خوشمزه‌تر شود، می‌توانید از مقداری کره برای روی ته چین استفاده کنید. برای مزه‌دار کردن مرغ‌ها می‌توانید مقداری سس مایونز به آن‌ها بزنید. اگر طعم آلبالوها خیلی ترش بود، می‌توانید از مقدار شکر بیشتری استفاده کنید. در طرز تهیه انواع ته چین باید از ماست چکیده و پرچرب استفاده کنید. چرب نبودن و چکیده نبودن ماست موجب می‌شود تا ته چین شما وا برود و حالت خمیری پیدا کند. توجه داشته باشید که ماستی که برای ته چین آلبالو استفاده می‌کنید، ترش باشد. در طرز تهیه ته چین آلبالوی خانگی باید از زعفران دم کرده غلیظ استفاده کنید تا غذای شما خوش عطر و خوش طعم شود. در