INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (79, 'خورش غوره مسما مرغ', 'برای تهیه خورش غوره مسما با مرغ، ابتدا بادمجان‌ها را با نمک استراحت دهید تا تلخی آنها گرفته شود. سپس آنها را بشویید، خشک کنید و سرخ کنید.', 'برای تهیه خورش غوره مسما با مرغ، ابتدا بادمجان‌ها را با نمک استراحت دهید تا تلخی آنها گرفته شود. سپس آنها را بشویید، خشک کنید و سرخ کنید. پیاز و سیر را در قابلمه تفت دهید. مرغ را اضافه کرده و همه طرف آن را سرخ کنید. رب گوجه‌فرنگی و گوجه‌های رنده‌شده را به مرغ و پیاز اضافه کنید و چند دقیقه با هم تفت دهید تا رنگ و طعم باز شود. سپس آب جوش بریزید و صبر کنید تا مرغ نیم‌پز و خورش قوام‌دار شود. در نهایت بادمجان‌های سرخ‌شده و غوره‌های پاک‌شده را توی خورش بریزید و بگذارید همه مواد با هم مزه‌دار و خورش لعاب‌دار شود. وقتی همه طعم‌ها خوب به خورد هم رفت و خورش غلیظ شد، می‌توانید کنارش برنج سرو کنید. برای گرفتن تلخی بادمجان، بعد از اینکه بادمجان‌ها را پوست گرفتید و برش زدید، باید هر دو طرف‌شان را به نمک آغشته کنید و روی یک سینی بچینید. بادمجان‌ها باید حدود نیم ساعت تا چهل دقیقه بمانند تا روی سطح‌شان قطره‌های قهوه‌ای آب جمع شود. بعد با دستمال تمیز این آب را پاک کنید، بادمجان‌ها را با آب سرد آبکشی کنید تا نمک اضافی شسته شود و در آخر با دستمال خشک کنید تا برای سرخ کردن آماده شوند. برای آماده کردن غوره، ابتدا دم و برگ و چوب‌های ریزش را جدا کنید، بعد غوره‌ها را چند دقیقه توی آب سرد بریزید و ملایم هم بزنید تا هر گرد و خاک یا ذره‌ای روی سطح غوره‌ها پاک شود. بعد غوره‌ها را توی آبکش بریزید و اگر لازم بود دوباره آب بکشید تا کاملاً تمیز و آماده شدنش مطمئن شوید. یک تابه را روی شعله متوسط بگذارید و روغن بریزید. وقتی روغن داغ شد، بادمجان‌ها را توی تابه بچینید. هر دو طرف را سرخ کنید تا طلایی شوند. بادمجان‌ها را روی دستمال آشپزخانه بگذارید تا روغن اضافی گرفته شود. توی یک قابلمه یا تابه مناسب کمی روغن بریزید. پیازها را روی حرارت ملایم سرخ کنید تا سبک و طلایی شوند. وقتی پیازها طلایی شدند، سیر رنده شده را هم اضافه کنید و حدود یک دقیقه دیگر با هم تفت دهید تا عطرش بلند شود. مراقب باشید سیر نسوزد، چون سریع تلخ می‌شود. تکه‌های مرغ را با کمی نمک و فلفل مزه‌دار کنید و بعد داخل همان تابه‌ای که پیاز و سیر را سرخ کردید بریزید. حرارت را کمی زیاد کنید و مرغ‌ها را هر طرف چند دقیقه سرخ کنید تا طلایی و خوشرنگ شوند. این کار باعث می‌شود بوی زهم مرغ گرفته شود و گوشتش توی خورش شکل بهتری داشته باشد. رب گوجه‌فرنگی را داخل قابلمه بریزید. یکی دو دقیقه رب را با مرغ و پیاز تفت دهید تا رنگ باز کند. گوجه فرنگی رنده شده را اضافه کنید. کمی هم بزنید تا مواد مخلوط شوند. اجازه دهید آب گوجه کمی کشیده شود. حدود دو لیوان آب جوش اضافه کنید. شعله را کم کنید و در قابلمه را بگذارید. حدود سی تا چهل دقیقه منتظر بمانید تا مرغ نیم‌پز شود و خورش قوام بگیرد. بادمجان‌های سرخ‌شده و غوره را به قابلمه اضافه کنید. در قابلمه را دوباره بگذارید. بیست تا سی دقیقه بگذارید تا همه مواد با هم مزه‌دار و خورش لعاب‌دار شود. یادتان باشد از این به بعد خورش را زیاد هم نزنید تا بادمجان‌ها له نشوند و ظاهر خورش قشنگ بماند. هر وقت حس کردید غوره‌ها ترک خوردند و خورش حسابی جا افتاده، وقت خاموش کردن شعله است. نمک و ترشی خورش را بچشید. اگر لازم بود تنظیم کنید. خورش باید لعاب‌دار و نسبتاً غلیظ باشد. وقتی حسابی جا افتاد، خورش غوره مسما آماده سرو است. برای سرو، معمولاً خورش را با چلو آبکش یا چلو کته، انواع سالاد یا ترشی دلخواه سرو کنید. کنارش زیتون یا سیرترشی هم خوب می‌چسبد.', 40, 80, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2019/06/20250804_101519_1658006781.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
