INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (122, 'شفته اراکی (کوفته اراکی)', 'در مرحله اول، یکی از پیازها را پوست بگیرید و با رنده ریز، رنده کنید. آب پیاز را بگیرید و نگه دارید. هویج را هم پوست بگیرید و با رنده ریز رنده کنید.', 'در مرحله اول، یکی از پیازها را پوست بگیرید و با رنده ریز، رنده کنید. آب پیاز را بگیرید و نگه دارید. هویج را هم پوست بگیرید و با رنده ریز رنده کنید. گوشت چرخ‌کرده را در یک کاسه بزرگ بریزید، سپس هویج و پیاز رنده شده را به آن اضافه کنید. سبزی‌های معطر خرد شده، آرد نخودچی، نمک و فلفل سیاه را نیز اضافه کنید. همه مواد را با هم مخلوط کنید و خوب ورز دهید تا یکدست و چسبنده شود. ورز دادن خوب باعث می‌شود کوفته‌ها موقع پخت وا نروند. مقداری از مایه را بردارید و با دست به شکل کوفته‌های متوسط گرد کنید. قبل از گرد کردن هر کوفته، دست‌تان را با آب پیاز که کنار گذاشته‌اید خیس کنید تا مواد به دست‌تان نچسبد. در یک ماهیتابه روغن بریزید و بگذارید حسابی داغ شود. سپس کوفته‌ها را در روغن از همه طرف سرخ کنید تا رنگشان خوشرنگ شود. برای تهیه سس، یک پیاز دیگر را نگینی خرد کنید. در ماهیتابه کمی روغن بریزید و پیاز را با کمی فلفل سیاه و زردچوبه تفت دهید تا نرم شود. رب گوجه را اضافه کنید و چند دقیقه تفت دهید تا خامی رب گرفته شود. آرد را اضافه کنید و کمی تفت دهید، سپس سبزی‌های معطر را اضافه کنید و دو لیوان آب جوش بریزید. بگذارید سس بجوشد. وقتی سس شروع به جوشیدن کرد، کوفته‌ها را آرام در آن بیندازید. کمی سرکه، شکر و آبلیمو اضافه کنید و اجازه دهید حدود نیم ساعت سس غلیظ شود و کوفته‌ها حسابی مزه‌دار شوند. وقتی سس غلیظ شد و کوفته‌ها خوب جا افتادند، شعله را خاموش کنید. شفته اراکی را داغ و خوشمزه با نان تازه یا همراه با چلو سرو کنید.', 30, 60, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/03/kofteh-morgh.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (123, 'آبگوشت شله یا شله بریان با زیره', 'برنج را از قبل بشویید و در آب ولرم و نمک خیس کنید.', 'برنج را از قبل بشویید و در آب ولرم و نمک خیس کنید. پیاز را نگینی یا خلالی خرد کنید. سیر را له یا خرد کنید. در یک قابلمه روغن بریزید و پیاز و سیر را سرخ کنید تا سبک و طلایی شوند. زردچوبه و فلفل را اضافه کنید. گوشت را اضافه کنید و با پیاز تفت دهید. بعد از اینکه کمی رنگ گوشت تغییر کرد، به آن آب جوش اضافه کنید. اجازه بدهید گوشت‌ها به مدت ۲ ساعت بپزند. بعد از پختن گوشت‌ها اگر گوشت‌تان استخوان دارد، آنها را کاملا جدا کنید. می‌توانید کمی از گوشت را کنار بگذارید و برای تزیین استفاده کنید. برنج را از آب بکشید و به گوشت اضافه کنید. زیره را اضافه کنید. اگر آب گوشت کم شده است، به آن آب اضافه کنید. (دقت کنید که حتما آب جوش باشد.) آب را تا حدی بریزید که برنج و گوشت کاملا با هم بپزند و نرم شوند و آب کاملا به خورد گوشت و برنج برود. به مواد داخل قابلمه، نمک اضافه کنید. مراقب باشید، شله بریان‌تان ته نگیرد. در مراحل پایانی مواد را هم بزنید. بعد از اینکه گوشت و برنج کاملا پختند، بچشید و نمک آن را امتحان کنید. در صورت لزوم نمک را اضافه کنید. در آخرین مرحله از طرز تهیه شله بریان، مواد را با گوشت‌کوب له کرده و در ظرف مناسب سرو کنید. غلظت گوشت و برنج باید چیزی شبیه حلیم یا آش شله مشهدی باشد.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/Rice-feature-e1608141540749.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (124, 'شیربرنج هندی', 'برنج را بهتر است از قبل خیس کنید. از برنج نیم دانه هم می توانید در طرز تهیه شیربرنج هندی استفاده کنید. یک تابه یا قابلمه را روی حرارت قرار دهید. کره را در آن بریزید و وقتی ذوب شد، بادام هندی های خرد ش', 'برنج را بهتر است از قبل خیس کنید. از برنج نیم دانه هم می توانید در طرز تهیه شیربرنج هندی استفاده کنید. یک تابه یا قابلمه را روی حرارت قرار دهید. کره را در آن بریزید و وقتی ذوب شد، بادام هندی های خرد شده را در آن تفت دهید تا رنگ کاراملی و طلایی به خودشان بگیرند. بعد از تغییر رنگ بادام ها، کشمش را اضافه کنید. کمی تفت دهید ولی مراقب باشید نسوزند. چون کشمش خیلی زود می سوزد و طعم تلخی به خود می گیرد. البته می توانید کشمش را از دستور پخت شیربرنج هندی حذف کنید. بعد از تفت دادن کشمش و بادام زمینی، آن ها را از ظرف خارج کنید و دوباره قابلمه را روی حرارت بگذارید. برنج را آبکشی کنید و به تابه اضافه کنید. شروع به تفت دادن برنج کنید. رنگ برنج باید طلایی شود. بعد از این که برنج روی حرارت سرخ شد، شیر را به آرامی به آن اضافه کنید. حرارت را کمی بیشتر کنید تا شیر جوش بیاید و مدام مواد داخل قابلمه را به هم بزنید. شیر باید به خورد برنج برود و آن را نرم کند تا حدی که مقدار آن نصف شود. در این حالت یک ترکیب غلیظ و به شکل پودینگ خواهید داشت. شکر و پودر هل و گلاب را در این مرحله از طرز تهیه شیربرنج هندی اضافه کنید. حرارت را پایین بیاورید و اجازه دهید مواد داخل قابلمه کمی غلیظ شوند. حدود ۵ دقیقه زمان کافی است. بادام هندی و کشمش را که از قبل سرخ کرده و تفت داده بودید، به آن اضافه کنید و هم بزنید تا مخلوط و یکدست شوند. وقتی شیربرنج به اندازه دلخواهتان غلیظ شد، شعله گاز را خاموش کنید. شیربرنج را در ظرف مناسب سرو کرده و روی آن را با پودر دارچین و پودر یا خلال پسته تزیین و میل کنید.', 15, 30, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2021/03/1_PIuYCpq_nz-s8FgVkdzGQ-min-e1617183864371.jpeg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (125, 'عدس پلو با سویا', 'برای تهیه عدس پلو با سویا، پیش از هر چیز، مواد را در ظروف جداگانه بخیسانید. برنج، عدس و سویا را جدا جدا، دو بار بشویید و در ظرف‌های متفاوت، روی آنها آب بریزید تا مواد را بپوشاند. بهتر است سویا و عدس ر', 'برای تهیه عدس پلو با سویا، پیش از هر چیز، مواد را در ظروف جداگانه بخیسانید. برنج، عدس و سویا را جدا جدا، دو بار بشویید و در ظرف‌های متفاوت، روی آنها آب بریزید تا مواد را بپوشاند. بهتر است سویا و عدس را حداقل ۳۰ دقیقه خیس کنید و برنج را هم همراه با ۱ قاشق چای‌خوری نمک از حداقل یک ساعت قبل بخیسانید. پوست پیاز‌ها را بگیرید و به صورت نگینی یا خلالی باریک خرد کنید. در یک تابه پیازها را با مقداری روغن سرخ کنید تا سبک و طلایی شود. در این فاصله سیب زمینی‌ها را بشویید و پوست آنها را بگیرید. سیب زمینی‌ها را هم به صورت نگینی خرد کنید. بعد از سرخ شدن پیازها، آنها را از تابه بیرون بیاورید و سیب زمینی‌ها را در تابه سرخ کنید. سیب زمینی و پیاز سرخ شده را کنار بگذارید. در یک کاسه، کشمش‌ها را با مقداری آب بخیسانید. ۱۵ دقیقه برای خیساندن کشمش‌ها کافی است. سپس آنها را آبکش کنید و اجازه دهید آب خود را از دست بدهند. عدس‌ها را آبکش کنید و در یک قابلمه کوچک با مقداری آب، روی حرارت قرار دهید تا نیم پز شوند. سپس آنها را آبکش کنید. سویا را آبکش کنید. سویاها را با دست فشار دهید که آب اضافی خود را از دست بدهند. پیاز داغی که آماده کرده بودید را به تابه برگردانید (مقداری از پیاز داغ را برای تزیین عدس پلو کنار بگذارید) و سویا را در آن بریزید و کمی تفت دهید. زردچوبه، نمک، گرد لیمو عمانی، پودر دارچین، پودر آویشن و فلفل سیاه را اضافه کنید. مواد را هم بزنید و ۲ تا ۳ دقیقه صبر کنید تا ادویه‌ها به خورد سویا برود. سویا که تفت خورد و طعم و بافت خامی خود را از دست داد، رب گوجه فرنگی را به تابه حاوی مخلوط سویا اضافه کنید و به هم بزنید تا رب گوجه هم طعم خامی خود را از دست بدهد و رنگ باز کند. سپس به اندازه نصف لیوان آب جوش، در تابه آب بریزید و درب تابه را بگذارید تا سویا کمی بپزد. وقتی آبِ مایه سویا کشیده شد، شعله گاز را خاموش کنید. در یک قابلمه متوسط آب بریزید. آب نمکی که برنج را در آن خیسانده‌اید، دور بریزید. آب داخل قابلمه که شروع به جوشیدن کرد، کمی روغن مایع به آن اضافه کنید و برنج را در قابلمه آب جوش بریزید. به مدت ۷ تا ۱۰ دقیقه برنج را بجوشانید تا سطح روی آن کمی نرم شود و مغز برنج سفت باشد. سپس حرارت را قطع کنید و برنج را آبکش کنید. کف قابلمه‌ای که می‌خواهید عدس پلو با سویا را در آن درست کنید، کمی روغن بریزید و ته دیگ دلخواهتان را قرار دهید. سپس یک لایه برنج، یک لایه عدس، یک لایه مایه سویا، یک لایه سیب زمینی سرخ شده و یک قاشق زعفران دم‌کرده بریزید. لایه‌ها را به همین صورت تکرار کنید تا مواد تمام شوند. لایه آخر باید برنج باشد. سپس درب قابلمه را ببندید و به مدت ۴۵ دقیقه روی حرارت ملایم بگذارید تا عدس پلو دم بکشد. در آخرین مرحله، در یک تابه جدا، کمی کره یا روغن بریزید و کشمش‌ها در آن به مدت چند ثانیه تفت دهید. وقتی عدس پلو با سویا دم کشید، مواد داخل قابلمه را با هم مخلوط کنید و عدس پلو را در ظرف سرو بریزید. روی عدس پلو را با کشمش و پیاز داغ تزیین و سرو کنید. برای درست کردن عدس پلو با سویا قلقلی، بعد از اینکه سویاها را خیس کردید، آنها را آبکش کنید و در غذاساز بریزید. سویاها که کاملا میکس شدند، با ادویه‌های معطر مثل زردچوبه، فلفل، پودر آویشن، نمک، کمی آرد و … مخلوط کنید و ورز دهید. به مدت ۳۰ دقیقه مایه سویا را استراحت دهید. سپس مایه سویا را به صورت قلقلی در بیاورید و در روغن سرخ کنید. عدس را جداگانه نیم پز کنید و برنج را هم در آب جوش بریزید. بعد از ۷ تا ۱۰ دقیقه برنج را آبکش کنید. عدس و برنج را به صورت لایه لایه در قابلمه بریزید و ۴۵ دقیقه صبر کنید تا دم بکشد. در آخر، عدس پلو را با هم مخلوط کنید و در ظرف سرو بریزید. قلقلی‌های سویا را روی عدس پلو بریزید و آن را سرو کنید. برای تزیین عدس پلو می‌توانید از زرشک، کشمش و پیاز داغ استفاده کنید. برای درست کردن عدس پلو بدون گوشت به صورت دمی (کته) با سویا، ابتدا عدس را شسته و با کمی آب می‌گذاریم تا نیم پز شود. برنج را با نمک و روغن بصورت کته آماده کرده و روی حرارت می‌گذاریم، قبل از اینکه کاملاً آب برنج گرفته شود عدس (عدس آب نداشته باشد) را به برنج اضافه می‌کنیم و هم می‌زنیم تا کاملاً با هم مخلوط شوند. روغن را به برنج اضافه می‌کنیم، کمی هم زعفران دم کرده روی برنج می‌ریزیم و حرارت را ملایم می‌کنیم تا عدس پلو دم بکشد. بعد از پخت عدس پلو دمی، مایه سویا به صورت قلقلی یا ساده را روی عدس پلو می‌ریزیم و عدس پلو را با پیاز داغ، گردو، کشمش سرخ کرده و در صورت دلخواه خرما تفت داده شده تزیین می‌کنیم.', 45, 90, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/08/nokat-adas-polo-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (126, 'عدس پلو با مرغ', 'از ۲ ساعت قبل، برنج را بشویید و در یک کاسه بریزید. روی آن را به میزانی که برنج‌ها را بپوشاند، آب بریزید و یک قاشق غذاخوری نمک اضافه کنید. عدس را هم بشویید و به مدت ۳۰ دقیقه بخیسانید. کشمش پلویی را هم', 'از ۲ ساعت قبل، برنج را بشویید و در یک کاسه بریزید. روی آن را به میزانی که برنج‌ها را بپوشاند، آب بریزید و یک قاشق غذاخوری نمک اضافه کنید. عدس را هم بشویید و به مدت ۳۰ دقیقه بخیسانید. کشمش پلویی را هم تمیز کنید و در آب بخیسانید. پیاز را پوست بگیرید و به صورت نگینی ریز، یا خلالی باریک خرد کنید. در یک قابلمه کمی روغن یا کره بریزید و روی حرارت قرار دهید تا پیازها سبک و طلایی شوند. سپس مرغ‌ها را به آن اضافه کنید. حرارت را کم کنید و اجازه دهید تغییر رنگ دهند. بعد از تغییر رنگ مرغ‌ها، به آن‌ها، زردچوبه، فلفل، نمک و چوب دارچین را اضافه کرده و ۲ تا ۳ دقیقه صبر کنید تا ادویه‌ها به خورد مرغ بروند. سپس به اندازه ۲ لیوان دسته‌دار فرانسوی، آب جوش یا ولرم به قابلمه اضافه کنید. اجازه دهید آب شروع به جوشیدن کند. بعد حرارت را کم کنید و درب قابلمه را روی آن بگذارید. به مدت ۴۰ دقیقه صبر کنید تا مرغ‌ها بپزند و آب مرغ، غلیظ شود. عدس را آبکش کنید و در یک قابلمه جداگانه بریزید. روی آن مقداری آب بریزید (آب روی عدس را بپوشاند) و اجازه دهید عدس‌ها، روی حرارت ملایم، نرم و نیم‌پز شوند. (معمولا ۲۵ تا ۳۵ دقیقه برای پخت عدس کافی است) یک قابلمه دیگر را تا نیمه یا دو برابر اندازه برنج‌ها، آب بریزید و روی حرارت بگذارید تا آب شروع به جوشیدن کند. آبی که برنج را در آن خیس کرده‌اید، دور بریزید و برنج را به قابلمه اضافه کنید. به مدت ۷ تا ۱۰ دقیقه (بسته به نوع برنج) برنج را در آب بجوشانید تا آماده آبکشی شود. یعنی لایه بیرونی برنج باید نرم و لایه داخلی برنج، سفت باشد. سپس برنج را آبکش کنید. در قابلمه‌ای با اندازه مناسب، به اندازه ۲ تا ۳ قاشق غذاخوری، روغن بریزید. کف قابلمه را ته دیگی که دوست دارید، مثل نان یا سیب زمینی بگذارید. سپس یک لایه برنج آبکش شده بریزید و یک لایه عدس نیم پز را اضافه کنید. دوباره برنج و عدس را در قابلمه بریزید و مواد را به همین شکل تکرار کنید تا عدس و برنج تمام شوند. درب قابلمه را روی آن بگذارید و به مدت ۴۵ دقیقه صبر کنید تا برنج بپزد (مراقب باشید عدس‌ها آب نداشته باشند چون برنج را شفته می‌کنند). در این فاصله که برنج در حال دم کشیدن است، مرغ‌های پخته شده را از قابلمه بیرون بیاورید و از آب آن جدا کنید. اگر می‌خواهید عدس پلو با مرغ ریش ریش داشته باشید، مرغ‌های پخته را ریش ریش کنید. کشمش را هم آبکش کنید. کشمش‌های آبکش شده را در یک تابه با مقداری روغن تفت دهید. مراقب باشید کشمش‌ها نسوزند. سپس آنها را از تابه بیرون آورید و کنار بگذارید. بعد از ریش ریش کردن مرغ‌ها، آن‌ها را در تابه بریزید و سرخ کنید. مراقب باشید زیادی تفت ندهید تا خشک نشوند. مقداری پودر دارچین و پودر زیره یا ادویه‌های دلخواه دیگر را به مرغ‌های داخل تابه اضافه کنید تا طعم بگیرند. اگر می‌خواهید مرغ و کشمش را با هم مخلوط کنید، کشمش‌های تفت خورده را هم اضافه کنید. زیر عدس پلو را خاموش کنید و کمی روغن جامد آب شده یا کره روی برنج بریزید و هم بزنید تا عدس‌ها در برنج پخش شوند. سپس عدس پلو را در دیس یا ظرف مخصوص سرو بریزید و با مرغ، کشمش و پیاز داغ مجلسی، تزیین و سرو کنید. برای تهیه عدس پلو با مرغ به روش دمی، باید مرغ را با پیاز و ادویه‌ها بپزید. سپس به مرغ‌های پخته شده در قابلمه، برنجی را که از قبل خیسانده و آبکش کرده‌اید، اضافه کنید. به اندازه‌ای روی برنج و مرغ‌ها آب بریزید که یک بند انگشت بالاتر از سطح مرغ‌ها و برنج باشد. اجازه دهید، روی حرارت، آب برنج و مرغ کشیده و برنج نرم شود. سپس عدس نیم‌پز شده را هم به برنج و مرغ اضافه کنید. درب قابلمه را با دم کنی روی آن بگذارید و ۴۰ تا ۴۵ دقیقه صبر کنید تا عدس پلو با مرغ دم بکشد و آماده سرو شود. برای تهیه عدس پلو با مرغ چرخ کرده، گوشت مرغ‌ چرخ کرده را با پیاز رنده شده و آب گرفته، نمک و فلفل سیاه به صورت کوفته قلقلی یا میت بال مرغ درآورید. میت بال مرغ را سرخ کرده و به عدس پلو دم کشیده اضافه کنید. یک روش هم این است که مرغ چرخ شده را با پیاز و ادویه‌ها تفت دهید و روی عدس پلو (با کشمش و پیاز داغ) بریزید. عدس پلو با مرغ را با زرشک، کشمش و گردوی تفت خورده می‌توانید تزیین کنید.', 28, 57, 4, 'hard', 'https://blog.okcs.