INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (128, 'عدس پلو مشهدی با رب', 'در قدم اول برنج و عدس را در آب بخیسانید. عدس را حدودا یک ساعت قبل از پخت در آب بخیسانید تا نفخ آن گرفته شود. برنج را هم یک ساعت قبل از پخت در آب و نمک قرار دهید. اضافه کردن نمک کمک می‌کند تا برنج در ه', 'در قدم اول برنج و عدس را در آب بخیسانید. عدس را حدودا یک ساعت قبل از پخت در آب بخیسانید تا نفخ آن گرفته شود. برنج را هم یک ساعت قبل از پخت در آب و نمک قرار دهید. اضافه کردن نمک کمک می‌کند تا برنج در هنگام پخت نرم نشود. کشمش پلویی را نیز در آب قرار دهید و چند بار آب آن را عوض کنید تا سنگریزه‌های لابلای آن کاملا تمیز شود. در فاصله‌ای که برنج و عدس باید خیس بخورند شما می‌توانید دیگر مواد عدس پلو ربی مشهدی را آماده کنید. تکه‌های مرغ را کاملا تمیز بشویید و در آبکش قرار دهید تا آب آن خارج و خشک شود. سپس مرغ را به تکه‌های کوچکتر تقسیم و با یک پیاز آن را چرخ یا پوره کنید. برای چرخ کردن مرغ و پیاز می‌توانید از چرخ گوشت و یا غذاساز استفاده کنید و مرغ را به شکل پوره در بیاورید. یک عدد پیاز متوسط را انتخاب کنید. پیاز را پوست بگیرید و بشویید. پیازها را با یک چاقوی تیز و تخته کار به صورت خلالی درشت خرد کنید. یک تابه را روی حرارت قرار دهید و مقداری روغن سرخ کردنی به آن اضافه کنید. بعد از آنکه روغن داغ شد پیازهایی را که به شکل خلالی خرد کرده‌اید به آن اضافه کنید. پیازها را تفت دهید تا جایی که کاملا طلایی شوند. بعد از آنکه عدس به اندازه کافی خیس خورد، آن را آبکش کنید و با یک قابلمه آب روی حرارت آن را بپزید. مراقب باشید عدس‌های خیلی له نشوند. زمانی که پیازها سبک و طلایی شدند نوبت اضافه کردن مرغ چرخ شده به آن است. در این مرحله باید مرغی را که چرخ کردید به پیاز داغ اضافه کنید و تفت دهید تا به خوبی سرخ شود و رنگ آن تغییر کند. در این مرحله ادویه‌ها شامل نمک، فلفل سیاه، زردچوبه و دارچین را نیز به ترکیب مرغ و پیاز اضافه کنید و آنها را تفت دهید تا عطر آنها بلند شود. یک قاشق رب گوجه فرنگی را به مایه گوشت عدس پلو مشهدی اضافه کنید و خوب تفت دهید تا رنگ آن باز شود. گوجه‌ها را بشویید و به وسیله غذاساز و یا رنده آنها را به شکل پوره درآورید. پوره گوجه فرنگی را به دیگر مواد روی حرارت اضافه کنید. مواد را خوب با هم مخلوط کنید تا عطر و طعم آنها به خورد هم برود. سپس حرارت را کم کنید، در تابه را بگذارید و اجازه دهید مواد پخته شوند و آب گوجه فرنگی کشیده شود و مایه گوشت عدس پلو با گوجه به روغن بیفتد. برنج را که از قبل خیسانده‌اید باید بپزید. برای این کار یک قابلمه که دو سوم آن را با آب پر کرده‌اید روی حرارت قرار دهید مقداری نمک به آن اضافه کنید و اجازه دهید آب به جوش بیاید. بعد از آن که آب جوش آمد، برنج را به آن اضافه کنید. وقتی سطح روی دانه برنج نرم و مغز آن هنوز سفت بود، آن را آبکش کنید. حالا تمام مواد عدس پلو مشهدی آماده است. کف یک قابلمه مناسب مقداری روغن بریزید و بنا بر سلیقه خود برای ته دیگ نان و یا سیب زمینی قرار دهید. به صورت لایه به لایه یک کفگیر برنج، کمی عدس و مایه گوشت عدس پلو را بریزید. این کار را تا تمام شدن تمام برنج، عدس و مواد عدس پلو ادامه دهید. لایه آخر باید برنج باشد. سپس درب قابلمه را ببندید و اجازه دهید عدس پلو با رب و مرغ با حرارت ملایم حدود ۴۰ دقیقه دم بکشد. در فاصله دم کشیدن عدس پلو ربی فرصت کافی برای آماده کردن کشمشش عدس پلو دارید. کشمش‌ها را که از قبل شسته و تمیز کرده‌اید را در یک تابه مناسب و روی حرارت کم با مقداری روغن یا کره و در صورت دلخواه کمی زردچوبه یا دارچین و مقداری گلاب تفت دهید تا نرم شود. کشمش زود می‌سوزد پس مراقبت باشید. بعد از دم کشیدن عدس پلو مشهدی با مرغ، آن را در یک دیس مناسب بریزید و با کشمشش پلویی تفت داده شده تزیین و با سالاد و ماست و سایر مخلفات دلخواه سرو کنید.', 13, 27, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/07/20240729_135926_181234028.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (129, 'عدسی لعابدار', 'برای تهیه عدسی خوشمزه و لعابدار، ابتدا عدس‌ها را خوب پاک کرده و حداقل ۵ ساعت خیس کنید تا هم نفخ آن گرفته شود و هم رنگش بعد از پخت تیره نشود. آب عدس را حداقل سه بار عوض کنید.', 'برای تهیه عدسی خوشمزه و لعابدار، ابتدا عدس‌ها را خوب پاک کرده و حداقل ۵ ساعت خیس کنید تا هم نفخ آن گرفته شود و هم رنگش بعد از پخت تیره نشود. آب عدس را حداقل سه بار عوض کنید. پیاز و سیب‌زمینی را نگینی خرد کنید. در یک قابلمه، کمی روغن یا کره بریزید و پیاز را تفت دهید تا سبک و شفاف شود. سپس رب گوجه فرنگی و ادویه‌ها شامل زردچوبه و فلفل را اضافه کرده و با پیاز تفت دهید تا عطر ادویه‌ها آزاد شود. سیب‌زمینی را نیز اضافه کرده و کمی سرخ کنید. این کار باعث می‌شود سیب‌زمینی ظاهر خود را حفظ کند و به لعاب‌دار شدن عدسی کمک کند. نمک را در این مرحله اضافه نکنید تا عدس دیرپز نشود. آب یا استاک گوشت یا مرغ را داخل قابلمه بریزید و خوب هم بزنید تا رب و ادویه‌ها حل شوند. سپس عدس را اضافه کنید و اجازه دهید خوراک عدسی شروع به جوشیدن کند. بعد از جوش آمدن، شعله را کم کنید تا به آرامی بپزد و جا بیفتد. هر چند دقیقه به آن سر بزنید تا مطمئن شوید نسوخته یا زیادی غلیظ نشده است. اگر آب آن کم شد، کمی آب جوش اضافه کنید. بعد از حدود یک ربع جوشیدن، نمک را اضافه کنید. کمی آرد سفید یا نشاسته ذرت را در آب سرد حل کنید تا گلوله نشود و کاملا یکدست شود. سپس این محلول را به عدسی اضافه کنید تا غلظت مناسب پیدا کند. نشاسته ذرت غلظت بیشتری می‌دهد. وقتی عدسی پخت و لعابدار شد، شعله زیر آن را خاموش کنید. عدسی را در ظرف سرو بریزید و برای خوشمزه‌تر شدن، می‌توانید روی آن کمی پیاز داغ و فلفل سیاه بریزید و با نان دلخواه میل کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2018/04/adasi-kermanshah2-848x477.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (130, 'خورش فسنجان با اردک', 'در اولین مرحله، برای گرفتن بوی بد اردک، آن را بشویید و به مدت ۱ ساعت در مقداری سرکه، ماست یا آبلیمو بخیسانید. بعد از یک ساعت اردک را آبکشی کنید و آن را در یک قابلمه بگذارید. روی اردک را در حدی که آب،', 'در اولین مرحله، برای گرفتن بوی بد اردک، آن را بشویید و به مدت ۱ ساعت در مقداری سرکه، ماست یا آبلیمو بخیسانید. بعد از یک ساعت اردک را آبکشی کنید و آن را در یک قابلمه بگذارید. روی اردک را در حدی که آب، آن را بپوشاند، آب بریزید. یکی از پیازها را ۴ قسمت کنید و همراه با برگ بو، داخل قابلمه بریزید. قابلمه را روی حرارت بگذارید و اجازه دهید آب بجوشد و روی آن کف‌های سفید تشکیل شود. قابلمه را بردارید و اردک را آبکش کنید. ۲ تا ۳ بار اردک را بجوشانید ولی دیگر لازم به اضافه کردن پیاز و برگ بو برای دفعه‌های بعد ندارید. سپس اردک را کنار بگذارید تا آب اضافی خود را از دست بدهد و خشک شود. گردوهای پوست گرفته شده را با مخلوط‌کن پودر یا با گوشت‌کوب له کنید تا نرم شوند. سپس یک پیاز باقی مانده را با قسمت درشت رنده، رنده کنید. آب پیاز را بگیرید. در یک قابلمه مقداری روغن یا کره بریزید و روی حرارت بگذارید تا داغ شود. سپس پیاز رنده شده را اضافه کنید و اجازه دهید آب پیاز گرفته شده و پیازها سبک شوند و تفت بخورند. در این مرحله، به پیازهای سرخ شده، مقداری زردچوبه اضافه کنید و تفت دهید تا رنگ باز کند و بوی خامی آن از بین برود. سپس مغز گردوی خرد شده را اضافه کنید. گردو را به مدت ۲ دقیقه تفت دهید (بیشتر تفت ندهید چون ممکن است گردوها بسوزند و طعم تلخ بگیرند). بوی خامی گردوها که گرفته شد، به اندازه ۵ لیوان دسته دار فرانسوی، آب سرد اضافه کنید. صبر کنید تا آب شروع به جوشیدن کند و حرارت را کم کنید. در یک کاسه، زعفران دم کرده، زردچوبه، نمک و فلفل سیاه را ترکیب کنید. با فرچه یا برس آشپزی، این مخلوط را روی اردک بمالید تا همه سطح آن را بپوشاند و بوی بد اردک به این ترتیب گرفته شود. می‌توانید داخل شکم اردک هم از این محلول بزنید. سپس یک تابه را روی حرارت بگذارید و داخل آن، روغن بریزید. همه قسمت‌های اردک مزه‌دار شده را در تابه سرخ کنید. بعد از یک ساعت از پختن گردو، اردک سرخ شده را به گردوی روغن انداخته اضافه کنید. به اندازه ۱ قاشق غذاخوری زعفران دم‌کرده غلیظ هم اضافه کرده و حرارت را کم کنید تا اردک به مدت ۴ ساعت بپزد و گوشت آن کاملا نرم شود. همچنین آب فسنجون کم شده، غلیظ شود و جا بیفتد. در این مرحله، رب انار و شکر را اضافه کنید. اگر می‌خواهید خورشتان طعم ترش بگیرد، مقداری آبلیمو اضافه کنید. چون اردک دیرتر می‌پزد، رب انار را نیم ساعت مانده به خاموش کردن شعله گاز به خورش فسنجان اضافه کنید. در آخر طعم خورشت را بچشید و اگر نیاز است به دلخواه، مقداری رب انار، نمک یا شکر به آن اضافه کنید. سپس شعله گاز را خاموش کرده و خورش فسنجان با اردک را با برنج کته یا چلو آبکش سرو کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/fesenjan.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (131, 'فسنجون با گوشت قلقلی', 'در اولین مرحله، گوشت چرخ‌کرده را با مقداری نمک، فلفل سیاه و زردچوبه مزه‌دار کنید. یک پیاز را پوست بگیرید و آن را رنده کنید. آب اضافی پیاز را بگیرید. سپس پیاز رنده شده را با گوشت ترکیب کنید.', 'در اولین مرحله، گوشت چرخ‌کرده را با مقداری نمک، فلفل سیاه و زردچوبه مزه‌دار کنید. یک پیاز را پوست بگیرید و آن را رنده کنید. آب اضافی پیاز را بگیرید. سپس پیاز رنده شده را با گوشت ترکیب کنید. بعد از این که همه مواد گوشت قلقلی را با هم ترکیب کردید، مایه گوشت را برای چند دقیقه ورز دهید. این کار از وا رفتن گوشت در خورشت جلوگیری می‌کند. گوشت را برای ۳۰ دقیقه در یخچال قرار دهید. رب گوجه‌فرنگی را در مقداری روغن سرخ کنید. سپس رب گوجه را همراه با رب انار ملس به قابلمه فسنجان اضافه کنید. در این فاصله تا آماده شدن خورشت، مواد گوشت قلقلی را از یخچال بیرون بیاورید. سپس گوشت‌ها را به صورت قلقلی‌های کوچک آماده کنید و در روغن سرخ کنید. بعد از این که همه قلقلی‌ها را در روغن سرخ کردید، باید آن‌ها را به آرامی به قابلمه خورش اضافه کنید. در این مرحله شعله گاز نباید بیش از حد زیاد باشد، زیرا این موضوع موجب وا رفتن گوشت‌ها در خورشت می‌شود. حدود ۳۰ دقیقه صبر کنید تا خورشت شما جا بیفتد. ۱۵ دقیقه بعد از اضافه کردن گوشت‌قلقلی، کمی از خورشت فسنجان بچشید. در صورت نیاز نمک و فلفل سیاه خورشت را اندازه کنید. اگر دوست دارید طعم خورش فسنجان شما کمی شیرین رو به ملس باشد، می‌توانید مقدار کمی شکر به آن اضافه کنید. صبر کنید تا آب خورش کم شود و حسابی جا بیفتد. سپس شعله گاز را خاموش کنید و این فسنجان خوشمزه را همراه با برنج زعفرانی یا برنج چلو آبکش سرو کنید.', 15, 30, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2023/07/wer.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (132, 'خورش فسنجان با مرغ', 'برای ۴ الی ۶ نفر، تکه‌های ران یا سینه مرغ را با یک عدد پیاز متوسط و مقداری آب، نمک، فلفل سیاه و زردچوبه بپزید. اگر می‌خواهید تکه‌های مرغ مکعبی باشند، از ابتدا خرد کنید. اگر می‌خواهید مرغ را ریش ریش کن', 'برای ۴ الی ۶ نفر، تکه‌های ران یا سینه مرغ را با یک عدد پیاز متوسط و مقداری آب، نمک، فلفل سیاه و زردچوبه بپزید. اگر می‌خواهید تکه‌های مرغ مکعبی باشند، از ابتدا خرد کنید. اگر می‌خواهید مرغ را ریش ریش کنید، ابتدا بپزید و سپس ریش ریش کنید. بعد از پخت، تکه‌های مرغ را کمی در روغن تفت دهید. در قابلمه‌ای که می‌خواهید خورش فسنجان را درست کنید، یک عدد پیاز متوسط را ریز خرد یا رنده کنید و با روغن، کمی نمک و زردچوبه تفت دهید تا طلایی شود. پیاز داغ آماده شده را کنار بگذارید. مغز گردو را خرد، بسابید یا آسیاب کنید تا روغن پس بدهد. گردوها را به پیاز داغ اضافه کنید و ۳ تا ۴ دقیقه روی حرارت ملایم تفت دهید. بعد از تفت گردوها، ۴ لیوان آب سرد و چند تکه یخ به آن اضافه کنید. به مدت یک ساعت صبر کنید تا گردو روی شعله ملایم گاز بپزد و به روغن بیفتد. رب انار را با کمی نمک (اگر رب انار نمک ندارد) و ۲/۳ لیوان آب سرد مخلوط کنید تا یک مایه روان به دست بیاید و سپس به ترکیب گردو و پیاز داغ اضافه کنید. مرغ‌های پخته و تفت داده شده را به سایر مواد فسنجون اضافه کنید. شعله گاز را ملایم کنید و صبر کنید خورش فسنجون روی شعله ملایم گاز بپزد، جا بیفتد و به روغن بیفتد. اگر در این مرحله آب فسنجان کم بود، یک لیوان آب یخ اضافه کنید (همیشه آب سرد و یخ اضافه کنید تا فسنجان به روغن بیفتد). برای تیره و خوشرنگ شدن فسنجان (اختیاری)، یک کفگیر آهنی را روی شعله اجاق گاز داغ کنید (حدود ۱۰ دقیقه). سپس با احتیاط کفگیر داغ را داخل دیگ فسنجان قرار دهید و سریع درب قابلمه را ببندید. خورش فسنجان با مرغ برای جا افتادن به حداقل ۴ ساعت زمان روی شعله ملایم گاز نیاز دارد. در ۳۰ دقیقه پایانی پخت، ۲ قاشق غذاخوری شکر یا ۱ قاشق غذاخوری شیره خرما به همراه ۱ قاشق غذاخوری زعفران دم کرده غلیظ به خورش اضافه کنید. بعد از حدود ۴ ساعت، خورش فسنجان با مرغ شما آماده است. بلافاصله بعد از پخت درب قابلمه را بردارید و حدود ۳۰ دقیقه صبر کنید تا خورش باز هم بیشتر به روغن بیفتد. در این مدت خورش را هم نزنید. سپس خورش فسنجون را با برنج چلو آبکش سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/07/fesenjan-recipe-6752768-hero-01-e0ae8d958e2a4b3bafead261d59e2aa7-1024x683.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (133, 'فلافل لبنانی', 'نخودها را از یک روز قبل بخیسانید. در طی این مدت چند بار آب نخودها را عوض و تازه کنید.', 'نخودها را از یک روز قبل بخیسانید. در طی این مدت چند بار آب نخودها را عوض و تازه کنید. نخودهای خیس خورده، پیاز، فلفل، گشنیز، جعفری، نمک، سیر و زیره را در میکسر بریزید و همه را با هم مخلوط کنید تا زمانی که تمام مواد خوب خرد شوند و حالت خمیری پیدا کنند. مایه به دست آمده نباید زیاد شل باشد. ذرات مایه باید کمی درشت‌تر از ذرات شکر باشند. در مرحله بعد آرد نخودچی و بیکینگ پودر را روی مواد مخلوط شده بپاشید و هم بزنید. باید میزان آرد در خمیر فلافل به قدری باشد که وقتی می‌خواهید توپک‌های فلافل را درست کنید به دست‌تان نچسبند. برای چند ساعت مخلوط را در یک کاسه بریزید و روی آن را بپوشانید و داخل یخچال قرار دهید تا مایه فلافل استراحت کند. در یک ماهیتابه یا ظرف گود و عمیق روغن بریزید و اجازه بدهید روغن داغ شود. از خمیر به اندازه یک گردو بردارید و با دست گرد کنید. می‌توانید از اسکوپ مخصوص فلافل هم برای این کار استفاده کنید. توپ‌ها را داخل روغن داغ بریزید و اجازه دهید سرخ شوند. فلافل‌ها باید در روغن غوطه‌ور باشند. بعد از سرخ شدن آن‌ها را روی دستمال بگذارید تا روغن اضافی‌شان گرفته شود.', 15, 30, 4, 'hard', 'https://blog.okcs.ir/wp-content/uploads/2018/06/Falafel-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (134, 'قارچ پلو', 'قارچ پلو با مرغ، یک پلوی قاطی ساده و خوشمزه است که می‌تواند برای مهمانی‌ها هم مناسب باشد. در ادامه مراحل طرز تهیه قارچ پلو با مرغ را به ترتیب دنبال کنید.', 'قارچ پلو با مرغ، یک پلوی قاطی ساده و خوشمزه است که می‌تواند برای مهمانی‌ها هم مناسب باشد. در ادامه مراحل طرز تهیه قارچ پلو با مرغ را به ترتیب دنبال کنید. در ابتدا برای تهیه این غذا با برنج و مرغ و قارچ، برنج را پاک کنید، بشویید و در یک کاسه بریزید. روی برنج مقداری آب ولرم و مقداری نمک بریزید و به مدت ۱ تا ۳ ساعت بسته به کهنه یا نو بودن برنج، آن را خیس کنید. مرغ‌ها را بشویید و اگر از فیله یا سینه، استفاده می‌کنید، آن را به قطعات دلخواه خرد کنید. برای شروع آشپزی، در یک تابه مقداری روغن بریزید و روی حرارت بگذارید تا داغ شود. سپس تکه‌های مرغ را در تابه قرار دهید و هر دو طرف آن را سرخ کنید. سپس مرغ سرخ شده را بیرون بیاورید و کنار بگذارید. یک پیاز را پوست بگیرید و به صورت خلالی یا نگینی ریز، خرد کنید. پیازهای خرد شده را در تابه بریزید و تفت دهید تا طلایی و سبک شوند. سپس مرغ‌های سرخ شده را در تابه بریزید و زردچوبه، فلفل سیاه و کمی نمک اضافه کنید. به اندازه، نصف لیوان آب جوش در تابه بریزید. درب تابه را ببندید و اجازه دهید، به مدت ۱۵ تا ۲۰ دقیقه مرغ‌ها بپزند. در فاصله‌ای که مرغ‌ها در حال پختن هستند، قارچ‌ها را بشویید و به صورت اسلایسی باریک خرد کنید. فلفل دلمه‌ای را هم بشویید و دانه‌های آن را خارج کنید. فلفل دلمه‌ای را به صورت نگینی ریز خرد کنید. در یک تابه مقداری روغن یا کره بریزید و قارچ‌ها را در تابه بریزید. آبلیمو را اضافه کنید و اجازه دهید روی حرارت بالا، قارچ‌ها بپزند و آب آنها خشک شود. بعد از سرخ شدن قارچ‌ها، فلفل دلمه‌ای‌ها را هم اضافه کنید و ۲ دقیقه تفت دهید تا نرم شوند. سپس مرغ‌های پخته شده را از قابلمه بیرون بیاورید و داخل تابه قارچ و فلفل دلمه‌ای بگذارید. زعفران را اضافه کنید و چند دقیقه صبر کنید تا طعم‌ها به خورد هم بروند. در این فاصله، برنج را آماده کنید. در یک قابلمه تا نیمه آب بریزید و روی حرارت بگذارید تا آب بجوشد. داخل آب، کمی روغن و نمک بریزید. برنج را از آبی که آن را در آن خیس کرده‌اید، جدا کنید. سپس برنج را در قابلمه بریزید و بین ۷ تا ۱۰ دقیقه صبر کنید تا سطح روی دانه برنج نرم و مغز آن هنوز سفت باشد، سپس آنها را آبکش کنید. در قابلمه، مقداری روغن بریزید و روی حرارت بگذارید تا داغ شود. سپس کف قابلمه را ته دیگ برنج دلخواهتان بگذارید (سیب زمینی یا نان لواش). یک سوم از برنج آبکش شده را روی ته‌دیگ بریزید. روی برنج را نصف مایه قارچ پلو با مرغ بریزید. دوباره یک لایه برنج و بقیه مرغ، قارچ و فلفل دلمه‌ای را بریزید. لایه آخر را برنج بریزید و درب قابلمه را ببندید. قارچ پلو را به مدت ۴۵ دقیقه روی حرارت بگذارید تا پلو دم بکشد. سپس روی برنج مقداری کمی روغن یا کره بریزید و قارچ پلو را به آرامی هم بزنید. قارچ پلو را با سالاد شیرازی سرو کنید. برای تهیه قارچ پلو با گوشت چرخ کرده، به اندازه ۲۵۰ تا ۳۰۰ گرم گوشت چرخ کرده را با ۲ عدد پیاز رنده و آبگیری شده، نمک، زردچوبه و فلفل سیاه ورز دهید و به صورت کوفته قلقلی درآورید. کوفته‌های قلقلی را در روغن سرخ کنید. سپس جداگانه قارچ، فلفل دلمه‌ای و ادویه‌ها را با هم تفت دهید و برنج را هم آبکش کنید. سپس مانند روش بالا، کوفته فلفلی‌ها و مایه قارچ و فلفل دلمه‌ای را لابه‌لای برنج بریزید و ۴۵ دقیقه قارچ پلو را روی حرارت ملایم بگذارید تا دم بکشد. می‌توانید کوفته قلقلی‌ها را بعد از پخت قارچ پلو ساده بدون گوشت و در زمان سرو، روی قارچ پلو بریزید. روش دیگر برای تهیه قارچ پلو با گوشت چرخ کرده این است که یک پیاز را در روغن تفت دهید. سپس ادویه‌های معطر را اضافه کنید و گوشت چرخ کرده را هم بیفزایید. گوشت را به مدت ۲ تا ۳ دقیقه تفت دهید. می‌توانید به گوشت چرخ کرده، مقداری رب گوجه هم اضافه کنید. سپس قارچ و فلفل دلمه‌ای را هم با مواد تفت دهید. اجازه دهید گوشت چرخ کرده و مواد میانی به مدت ۱۵ تا ۲۰ دقیقه بپزند. سپس آن را لابه‌لای برنج بریزید و قارچ پلو را دم کنید. برای تهیه قارچ پلو وگان و بدون گوشت و مرغ، کافی است قارچ‌ها را جداگانه با سبزی‌های دلخواه مثل نخود فرنگی، هویج، ذرت و … تفت دهید. سپس این مایه را لابه‌لای برنج ساده و آبکش شده بریزید و قارچ پلو را دم کنید. برای طرز تهیه قارچ پلو با شوید، فرقی نمی‌کند که قارچ پلو را با گوشت چرخ کرده درست می‌کنید، یا کوفته قلقلی، یا مرغ و یا حتی قارچ پلو بدون گوشت می‌پزید. کافی است در این روش، بعد از اضافه کردن مواد میانی، به اندازه ۱ و نیم قاشق غذاخوری (برای ۴ پیمانه برنج) شوید خشک هم لابه‌لای برنج بریزید. اگر می‌خواهید از شوید تازه در تهیه قارچ پلو با شوید استفاده می‌کنید، شوید‌ها را بعد از شستن و ساطوری خرد کردن، چند دقیقه با مایه قارچ پلو تفت دهید. سپس این مایه را لای برنج بریزید. بعد از پخت، مواد را هم بزنید تا شوید‌ها در کل برنج پخش شوند. برای جلوگیری از سیاه و تیره شدن قارچ‌ها در زمان سرخ کردن، شعله زیر آن را بالا ببرید تا آب نیندازند و همچنین مقداری آبلیمو روی قارچ‌ها بریزید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/gharch-polo.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (135, 'قنبر پلو شیرازی', 'برنج را پاک کنید، بشویید و به مدت ۳۰ دقیقه با مقداری آب نمک بخیسانید.', 'برنج را پاک کنید، بشویید و به مدت ۳۰ دقیقه با مقداری آب نمک بخیسانید. یک پیاز را رنده کنید و آب آن را با دست بگیرید. در یک کاسه، گوشت چرخ کرده، آرد نخودچی یا آرد گندم، نمک، فلفل سیاه و زردچوبه را با هم مخلوط کنید و کاملا ورز دهید تا چسبناک شود. سپس مایه گوشت را به اندازه فندق، به صورت کوفته قلقلی درآورید. همه کوفته قلقلی‌ها را آماده کنید و در روغن داغ سرخ کنید. سپس قلقلی‌ها را کنار بگذارید. پیاز را پوست بگیرید و به صورت نگینی خرد کرده و در یک تابه با مقداری روغن سرخ کنید. سپس مغز گردوی ریز خرد شده و کشمش پلویی را به پیاز داغ اضافه کرده و تفت دهید. وقتی مواد تفت خوردند، رب انار ملس را به همراه زعفران دم کرده اضافه کنید و کمی دیگر تفت دهید. کوفته قلقلی‌های سرخ شده را هم به این ترکیب اضافه کنید. قلقلی‌ها را زیاد هم نزنید و صبر کنید رب انار به خورد آن‌ها برود. در یک قابلمه تا نیمه، آب بریزید و روی حرارت بگذارید تا آب شروع به جوشیدن کند. سپس برنج را به آن اضافه کنید و حدود ۷ دقیقه صبر کنید تا برنج کمی نرم شود. بعد برنج را آبکش کنید. کف یک قابلمه مناسب کمی روغن بریزید و ته دیگ مورد علاقه بگذارید و یک لایه برنج بریزید. سپس مایه گردو و کشمش و قلقلی‌ها را روی برنج بریزید. دوباره یک لایه برنج و یک لایه مایه قنبر پلو بریزید. لایه‌ها را طوری بچینید که لایه آخر برنج باشد. در آخر روی برنج ۱ قاشق غذاخوری زعفران دم کرده غلیظ بریزید و قنبر پلو را دم کنید. حدود ۴۵ دقیقه تا ۱ ساعت اجازه دهید پلو شما دم بکشد. سپس قابلمه را از روی حرارت بردارید و کمی کره یا روغن حیوانی روی آن بریزید و قنبر پلو را با کفگیر هم بزنید تا همه مایه قنبر پلو با برنج مخلوط شود. این پلو مخلوط خوشمزه را در دیس یا سینی بریزید و همراه با سالاد شیرازی، سبزی خوردن و ترشی دلخواه سرو کنید. برای تهیه قنبر پلو با مرغ: مرغ را با پیاز، زردچوبه، فلفل سیاه و نمک، تفت دهید و سپس به اندازه ۱ لیوان آب اضافه کنید و اجازه دهید مرغ بپزد. مرغ را بعد از پخت، آبکش و ریش ریش کنید. (می‌توانید به صورت تکه‌ای هم به کار ببرید. در این صورت، جداگانه، مرغ را با روغن سرخ کنید.) مایه گردو و کشمش قنبر پلو را آماده کنید. مرغ ریش ریش شده یا تکه‌ای را به همراه گردو، کشمش، رب انار و زعفران دم کرده تفت دهید و وقتی آماده شد کنار بگذارید. سپس برنج را آبکش کنید و مانند روش قبل، به صورت لایه لایه، برنج و مایه قنبر پلو را در قابلمه بپزید. در آخر، روی برنج مقداری کره یا روغن بریزید و مواد را هم بزنید تا به صورت یکدست، مواد غذا با هم مخلوط شوند. سپس غم‌بر پلو با مرغ را در ظرف مناسب سرو بریزید و با انواع ترشی یا سالاد شیرازی سرو کنید. نکات برای جلوگیری از وا رفتن قلقلی‌ها: ۱- حتما آب پیاز را بگیرید و از ۱ قاشق غذاخوری آرد نخودچی یا آرد گندم برای انسجام بیشتر مایه گوشت قلقلی استفاده کنید. ۲- مواد قلقلی را زیاد ورز دهید تا کاملا یکدست و منسجم شوند. ۳- بعد از آماده کردن مایه گوشت چرخ کرده، به مدت ۳۰ دقیقه، در یخچال قرار دهید و بعد قلقلی‌ها را درست کنید. ۴- قلقلی‌ها را قبل از سرخ کردن، به مدت ۱۵ دقیقه در یخچال بگذارید. ۵- حتما قبل از سرخ کردن کوفته‌های قلقلی، از داغ بودن روغن مطمئن شوید تا کوفته‌ها به قالب نچسبند. ۶- در هنگام سرخ کردن قلقلی‌ها، آنها را زیاد هم نزنید و حرارت را کم کنید تا مغز کوفته‌ها هم بپزد.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/08/polo-esfandi.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (136, 'قورمه سبزی گیاهی با قارچ و لوبیا قرمز', 'لوبیا قرمزها را بشویید و حداقل برای ۱۲ تا ۲۴ ساعت در آب قرار دهید تا نفخ آن برطرف شود. در این مدت زمان سعی کنید آب لوبیاها را چند بار عوض کنید.', 'لوبیا قرمزها را بشویید و حداقل برای ۱۲ تا ۲۴ ساعت در آب قرار دهید تا نفخ آن برطرف شود. در این مدت زمان سعی کنید آب لوبیاها را چند بار عوض کنید. سبزی مخصوص قورمه سبزی را خوب بشویید و ریز و ساطوری خرد کنید. سپس مقداری روغن در ماهیتابه بریزید و سبزی را خوب سرخ کنید. پیاز را پوست بگیرید و به صورت ریز یا نگینی خرد کنید. سپس در یک ماهیتابه کمی روغن و زردچوبه بریزید و پیاز را خوب سرخ کنید. بعد از این که پیاز کمی سرخ شد، قارچ‌های خرد شده (اسلایسی یا درسته) را اضافه کنید. سپس کمی فلفل سیاه به قارچ‌ها اضافه کنید. در حین تفت دادن قارچ‌ها، لوبیا قرمز را در آب بریزید و اجازه دهید نیم پز شود. سپس لوبیای نیم پز شده را به ترکیب پیاز و قارچ سرخ شده اضافه کنید. سپس سبزی سرخ شده را نیز به خورش اضافه کنید و خوب هم بزنید. چند لیوان آب به خورش قرمه سبزی گیاهی خود اضافه کنید. سپس حرارت را زیاد کنید تا ابتدا آب به جوش بیاید. سپس می‌توانید حرارت را کم کنید و درب قابلمه را به صورت نیمه باز بگذارید تا خورشت به خوبی جا بیفتد. لیمو عمانی‌ها را سوراخ کنید و داخل خورش بیندازید. برای گرفتن تلخی لیمو عمانی بهتر است که آن را نصف کنید و برای نیم ساعت در آب ولرم قرار دهید. لیموها را در ۲۰ دقیقه آخر همراه با مقداری نمک به خورش اضافه کنید و نمک خورش را اندازه بگیرید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/02/eef-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (137, 'قورمه سبزی', 'لوبیا (قرمز، چیتی یا چشم بلبلی) را پاک کرده، بشویید و حداقل ۲ ساعت یا یک شب تا صبح در آب خیس کنید. در این مدت چند بار آب آن را عوض کنید تا نفخ آن گرفته شود. سپس لوبیاها را آبکش کرده و در قابلمه با آب', 'لوبیا (قرمز، چیتی یا چشم بلبلی) را پاک کرده، بشویید و حداقل ۲ ساعت یا یک شب تا صبح در آب خیس کنید. در این مدت چند بار آب آن را عوض کنید تا نفخ آن گرفته شود. سپس لوبیاها را آبکش کرده و در قابلمه با آب بپزید تا نیم‌پز شوند. (می‌توانید از کنسرو لوبیا چیتی استفاده کنید و سس آن را خالی کرده و در نیم ساعت پایانی پخت به خورش اضافه کنید). سبزی قورمه (تره، جعفری، گشنیز، شنبلیله، اسفناج) را پاک کرده، بشویید و پس از از دست دادن آب اضافی، به صورت ریز و ساطوری خرد کنید. در یک تابه مقداری روغن بریزید و سبزی‌ها را کاملا تفت دهید تا سرخ شوند و رنگشان تیره شود. (هر چه سبزی بیشتر سرخ شود، قورمه سبزی تیره‌تر و خوشمزه‌تر می‌شود. بهتر است با روغن جامد تفت دهید). پیاز را پوست بگیرید و به صورت نگینی ریز خرد کنید. در یک قابلمه مقداری روغن بریزید و روی حرارت بگذارید. پیازها را در روغن تفت دهید تا سبک و شیشه‌ای شوند. گوشت خورشتی (گوسفندی یا گوساله) را به صورت تکه‌های نسبتا درشت خرد کنید. گوشت را به پیاز اضافه کنید و تفت دهید تا رنگ آن تغییر کند. سپس مقداری زردچوبه و فلفل سیاه اضافه کنید و تفت دهید تا بوی خامی ادویه‌ها گرفته شود و گوشت طعم‌دار شود. لوبیاهای نیم‌پز شده را آبکش کرده و به گوشت و پیاز اضافه کنید و کمی تفت دهید. (این کار از چروک شدن پوست لوبیا جلوگیری می‌کند). سبزی سرخ شده را به مواد اضافه کنید و هم بزنید. آب جوش به خورش اضافه کنید. مقدار آب نباید خیلی زیاد یا خیلی کم باشد. اجازه دهید آب جوش بیاید و قل بخورد، سپس درب قابلمه را ببندید و حرارت را کم کنید تا خورش به آرامی جا بیفتد. لیمو عمانی‌ها را با چنگال سوراخ کنید و به مدت ۱ ساعت در آب گرم خیس کنید تا تلخی آنها گرفته شود. (اگر با طعم لیمو عمانی میانه خوبی ندارید، می‌توانید از آبلیموی تازه استفاده کنید و آن را در ۱۵ دقیقه پایانی پخت به همراه نمک به خورش اضافه کنید). ۱ قاشق غذاخوری شنبلیله خشک پودر شده را در روغن سرخ کنید و در ۳۰ ثانیه آخر پخت خورش به آن اضافه کنید تا عطر فوق‌العاده‌ای به قورمه سبزی بدهد. اجازه دهید قورمه سبزی برای مدت طولانی روی شعله ملایم گاز جا بیفتد و به روغن بیفتد. (یک قورمه سبزی جا افتاده نباید آب داشته باشد و همه مواد آن با هم ترکیب و یکدست باشند). قورمه سبزی را با برنج چلو یا کته و در کنار سالاد شیرازی یا ماست سرو کنید. (برای مجلسی‌تر شدن، مقداری از برنج را زعفرانی کنید و در ظروف چینی سرو کنید).', 15, 30, 4, 'hard', NULL, 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (138, 'قورمه مشهدی', 'گوشت گوسفندی و دنبه‌ها را بشویید و در آبکش بگذارید تا آب اضافی خود را از دست بدهند. گوشت‌ها را به اندازه کوچک (در حد و اندازه گردو) خرد کنید. دنبه‌ها را هم به همان اندازه خرد کنید.', 'گوشت گوسفندی و دنبه‌ها را بشویید و در آبکش بگذارید تا آب اضافی خود را از دست بدهند. گوشت‌ها را به اندازه کوچک (در حد و اندازه گردو) خرد کنید. دنبه‌ها را هم به همان اندازه خرد کنید. پیازها را پوست بگیرید و به صورت خلالی نازک خرد کنید. در یک قابلمه، کمی روغن بریزید و روی حرارت بگذارید تا داغ شود. سپس پیازها را در قابلمه بریزید و تفت دهید تا پیازها سبک و شیشه‌ای شوند. سپس زردچوبه و فلفل سیاه را اضافه کنید و کمی تفت دهید تا طعم خام ادویه‌ها گرفته شود. بعد از تفت خوردن پیاز و ادویه‌ها، گوشت و دنبه را در قابلمه بریزید و با پیازها تفت دهید. تفت دادن را تا وقتی ادامه دهید که رنگ گوشت تغییر کند و سفید شود. شعله را روی حالت متوسط قرار دهید که گوشت کمی سرخ شود و آب نیندازد. ۲ تا ۳ دقیقه گوشت را تفت دهید. بعد از تفت خوردن گوشت، به اندازه ۲ لیوان آب جوش به قابلمه اضافه کنید. چوب دارچین و برگ بو را اضافه کنید و اجازه دهید آب به قل قل کردن بیفتد. سپس حرارت را روی حالت ملایم تنظیم کرده و به مدت ۲ تا ۳ ساعت صبر کنید تا گوشت خوب بپزد و آب آن کشیده شود. در این مدت، هر نیم ساعت یک بار، به غذا سر بزنید و میزان آب آن را چک کنید. بعد از اینکه گوشت پخت، درب قابلمه را بردارید تا آب داخل قابلمه بخار شود و دنبه و گوشت‌ها بمانند. چوب دارچین و برگ بو را بیرون بیاورید و نمک یا چاشنی دلخواه (مثل زعفران دم کرده و آبلیمو) اضافه کنید. شعله گاز را روی حرارت متوسط تنظیم کنید و مواد را هم بزنید تا دنبه‌ها آب شوند و گوشت‌ها در روغن دنبه سرخ شوند. وقتی گوشت‌ها سرخ شدند، شعله را خاموش کنید و قورمه مشهدی را همراه با نان یا با برنج سرو کنید.', 10, 20, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2024/07/%D9%82%D9%88%D8%B1%D9%85%D9%87-%D9%85%D8%B4%D9%87%D8%AF%DB%8C.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (139, 'خورش قیمه با بوقلمون', 'در اولین مرحله، لپه‌ها را بشویید و حداقل دو ساعت قبل خیس کنید تا نرم‌تر شوند و بهتر بپزند. گوشت بوقلمون را به قطعات خورشی یک‌اندازه خرد کرده و بشویید. بوقلمون‌های خرد شده را در آبکش قرار دهید تا آب اض', 'در اولین مرحله، لپه‌ها را بشویید و حداقل دو ساعت قبل خیس کنید تا نرم‌تر شوند و بهتر بپزند. گوشت بوقلمون را به قطعات خورشی یک‌اندازه خرد کرده و بشویید. بوقلمون‌های خرد شده را در آبکش قرار دهید تا آب اضافه از آن خارج شود. لیمو عمانی‌ها را بشویید، دو سه سوراخ ریز با چنگال روی آن‌ها ایجاد کنید و برای گرفتن تلخی، حدود ۱۰ دقیقه در آب ولرم بخوابانید. سیب‌ زمینی‌ها را خلالی کرده و در آب سرد بگذارید تا نشاسته‌شان بیرون بیاید. پیازها را هم خلالی کنید و آماده کنار بگذارید. داخل یک قابلمه مناسب، کمی روغن بریزید و روی حرارت متوسط داغ کنید. پیازهای خلال‌شده را اضافه کنید و به آرامی هم بزنید تا نرم و شفاف شوند. زردچوبه را اضافه کرده و تفت دهید تا رنگ طلایی یکدست بگیرند. مراقب باشید پیاز نسوزد چون هم طعم و هم رنگ خورش را تحت‌تأثیر قرار می‌دهد. گوشت بوقلمون خرد‌شده را به پیاز داغ اضافه کنید و با حرارت متوسط رو به بالا هم بزنید تا سطح گوشت‌ها تغییر رنگ بدهد و رویه بگیرد. این کار باعث می‌شود گوشت در طول پخت، خوش‌طعم و آبدار بماند. در صورت تمایل، کمی فلفل سیاه یا پودر دارچین برای عطر بهتر اضافه کنید. بعد از اینکه گوشت کمی طلایی شد، لپه‌های خیس‌خورده را آبکش کنید تا آب اضافه‌شان وارد قابلمه نشود. اضافه کردن لپه در این مرحله باعث می‌شود با عطر و روغن گوشت و پیاز داغ مخلوط شود و در طول پخت، هم بافتش دونه‌دونه بماند هم طعم‌دارتر شود. لپه‌ها را حدود دو تا سه دقیقه با حرارت متوسط تفت دهید. نیازی به هم‌زدن مداوم نیست، فقط مطمئن شوید که همه دونه‌ها لایه‌ای از روغن و زردچوبه را گرفته‌اند. این کار باعث می‌شود خورش از نظر ظاهری شکیل باشد و لپه‌ها له نشوند. رب را به یک سمت قابلمه یا وسط مواد اضافه کنید و کمی روغن کنارش بریزید. رب باید با حرارت ملایم و زمان کافی تفت داده شود؛ اگر عجله کنید، طعم خام و ترشی تیزش در خورش باقی می‌ماند و اگر بیش‌ازحد حرارت دهید، تلخ می‌شود. با قاشق مرتب زیر و رو کنید تا رنگش یکی دو درجه تیره‌تر شود و بوی خامی برود. وقتی رب خوشرنگ شد، آن را با گوشت و لپه مخلوط کنید تا همه مواد رنگ یکدست و لعاب خوش‌رنگ بگیرند. در ادامه، لیمو عمانی‌های سوراخ ‌شده و خیسانده را به قابلمه اضافه کنید. برای بوی مجلسی، یک تکه چوب دارچین کوچک هم بگذارید، ولی قبل از سرو آن را خارج کنید تا طعم خورش زیادی شیرین نشود. نمک را هنوز اضافه نکنید چون باعث سفت شدن لپه می‌شود. ۴ تا ۵ لیوان دسته‌دار فرانسوی آب جوش اضافه کنید تا روی همه مواد را کاملا بپوشاند. حرارت را کم کنید و درب قابلمه را ابتدا ببندید و در دقیقه‌های آخر نیمه‌باز بگذارید. خورش باید حدود ۶۰ تا ۷۰ دقیقه آرام بجوشد تا گوشت و لپه کاملاً بپزند و خورش غلیظ شود. گاهی سطح خورش را چک کنید، اگر آبش کم شده بود، کمی آب جوش بریزید. وقتی به غلظت دلخواه رسید، نمک را اندازه کنید. سیب‌زمینی‌های خلالی را آبکش و خشک کنید. در روغن داغ با حرارت متوسط رو به بالا سرخ کنید تا طلایی و ترد شوند. روی دستمال جاذب روغن بگذارید تا روغن اضافی‌شان برود. چوب دارچین را از خورش خارج کنید. خورش را در ظرف سرو بریزید، روی آن را با سیب‌زمینی‌های سرخ‌شده تزیین کنید. این خورش مقوی و خوشمزه را با چلو آبکش یا چلو کته و سالاد شیرازی یا سالاد دلخواه خود میل کنید و نوش جان!', 36, 74, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/08/20250820_125109_130547301.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (140, 'قیمه با گوشت چرخ کرده', 'در اولین مرحله پیازها را بشویید، پوست یکی از پیازها را بگیرید و نگینی خرد کنید. لیمو عمانی‌ها را سوراخ کرده و داخل آب جوش بیندازید تا تلخی آنها گرفته شود.', 'در اولین مرحله پیازها را بشویید، پوست یکی از پیازها را بگیرید و نگینی خرد کنید. لیمو عمانی‌ها را سوراخ کرده و داخل آب جوش بیندازید تا تلخی آنها گرفته شود. یک قابلمه مناسب برای خورش را روی گاز قرار دهید و مقداری روغن در آن بریزید. اجازه دهید روغن کمی داغ شود. سپس پیازهای خرد شده را به آن اضافه کنید و تفت دهید تا سبک شوند. بعد از اینکه پیازها سبک شدند، گوشت چرخ کرده را به پیاز سرخ شده اضافه کنید و مواد را خوب هم بزنید. بعد از تغییر رنگ گوشت و تفت دادن آن، مقداری زردچوبه، نمک، فلفل سیاه و دارچین به گوشت چرخ کرده اضافه کنید. سپس حدود ۱۰ دقیقه مواد را به خوبی تفت دهید تا گوشت کاملاً سرخ شود. رب گوجه فرنگی را به سایر مواد اضافه کنید. برای این کار بهتر است ابتدا رب گوجه فرنگی را در روغن سرخ کنید. در یک ماهیتابه کوچک مقداری روغن بریزید تا داغ شود. سپس رب گوجه فرنگی را به روغن اضافه کنید و خوب سرخ کنید تا رنگ آن تغییر کند. رب سرخ شده را به ترکیب مایه گوشت چرخ کرده اضافه کنید و مواد را خوب هم بزنید. سپس پوره گوجه فرنگی را هم اضافه کنید و با سایر مواد تفت دهید تا آب گوجه گرفته شود و سرخ شود. بعد از اینکه رب گوجه فرنگی و پوره گوجه سرخ شدند و جا افتادند، ۲ لیوان آب جوش به مواد قیمه اضافه کنید و صبر کنید مواد قیمه به جوش بیاید. وقتی قیمه جوش خورد، شعله گاز را ملایم کنید. برای پختن لپه، آن را جداگانه بپزید. سپس لپه پخته را به خورش در حال پخت اضافه کنید تا همراه با سایر مواد خورشت روی شعله ملایم گاز جا بیفتد. در این فاصله که خورش قیمه چرخ کرده در حال پختن است، سیب زمینی‌های قیمه را سرخ کنید. سیب زمینی خورش قیمه چرخ کرده باید به صورت ریزتر و خلالی نازک خرد شود. برای این کار ابتدا سیب زمینی‌ها را به صورت ورقه‌های نازک برش دهید. بعد آنها را با یک چاقوی تیز خرد کنید. سپس مقداری روغن در ماهیتابه بریزید تا داغ شود. سیب زمینی‌ها را در چند نوبت سرخ کنید. لیمو عمانی را در ۳۰ دقیقه پایانی پخت اضافه نمایید تا طعم خورش تلخ نشود. اگر طعم لیمو عمانی را در خورش دوست ندارید، می‌توانید کمی پودر لیمو عمانی را جایگزین کنید. برای پختن خورشت قیمه نذری بهتر است شعله گاز را کم کنید و اجازه دهید به آرامی پخته شود و روغن بیندازد. می‌توانید از یک شعله پخش‌کن روی گاز استفاده کنید و خورشت قیمه چرخ کرده را برای حدود ۲ ساعت با شعله ملایم روی گاز قرار دهید تا کم آب شود. در این مدت مراقب باشید لپه‌ها له نشوند. در نهایت بعد از اینکه خورشت قیمه چرخ کرده شما آماده شد، آن را داخل ظرف مناسب بریزید. مقداری سیب زمینی سرخ شده روی خورشت قرار دهید و همراه با برنج آبکش یا برنج کته میل کنید. یکی دیگر از روش‌های تهیه خورش قیمه با گوشت چرخ کرده، استفاده از گوشت قلقلی در این غذا است. برای تهیه قیمه با گوشت چرخ کرده قلقلی باید گوشت را با پیاز رنده شده و آب گرفته، نمک و فلفل سیاه ترکیب کنید و خوب ورز دهید. سپس گوشت را به صورت قلقلی درست کنید و در روغن سرخ کنید. خورش را به روشی که در بالا گفته شد، آماده کنید و نیم ساعت بعد از اضافه کردن لپه، قلقلی‌ها را به خورش اضافه کنید. بعد از اضافه کردن گوشت قلقلی به هیچ‌عنوان خورشت را هم نزنید تا قلقلی ها از هم نپاشند. بهترین طعم‌دهنده‌ها برای تهیه قیمه شمالی با گوشت چرخ کرده، لیمو عمانی، دارچین و گلاب است. استفاده از پودر دارچین در تهیه قیمه چرخ کرده موجب تیره شدن رنگ آن می‌شود. اگر قیمه رنگ روشن دوست دارید برای رفع این مشکل می‌توانید از چوب دارچین به جای پودر دارچین استفاده کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/07/a940cf023821e8b7ddaa3580c0f720a0-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (141, 'کوفته ریزه یا قیمه ریزه', 'گوشت چرخکرده یا سینه مرغ چرخکرده، آرد نخودچی الک شده و نمک و فلفل و زردچوبه را به همراه کمی دارچین با هم مخلوط کنید و خوب ورز دهید و قلقلی کنید.', 'گوشت چرخکرده یا سینه مرغ چرخکرده، آرد نخودچی الک شده و نمک و فلفل و زردچوبه را به همراه کمی دارچین با هم مخلوط کنید و خوب ورز دهید و قلقلی کنید. پیاز را جداگانه رنده کنید و آب آن را بگیرید. پیاز رنده شده را با روغن تفت دهید و وقتی سبک شد گوجه‌های رنده شده را به آن اضافه کنید. وقتی گوجه تفت خورد و آب آن کشیده شد رب گوجه را هم اضافه کنید و تفت دهید. خامی رب که گرفته شد نعنا پودر شده را اضافه کنید و تفت دهید. ۳ لیوان آب جوش به این ترکیب اضافه کنید و اجازه دهید ۵ دقیقه قل بخورد. در این فاصله قیمه ریزه‌ها را در کمی روغن مقداری تفت دهید و سپس به آرامی به سس اضافه کنید. برای پخت قیمه ریزه روی شعله ملایم گاز به یک ساعت زمان نیاز دارید. در صورت تمایل سیب زمینی‌ها را هم مکعبی خرد کنید و به این ترکیب اضافه کنید تا همراه با سایر مواد خوب بپزند. اگر احساس کردید آب سس کم است مقداری دیگر به آن اضافه کنید اما در پایان باید یک سس خیلی غلیظ داشته باشید. یادتان باشد اضافه کردن سیب زمینی اختیاری است و ادویه اصفهانی‌ها برای ترشی بیشتر این غذا در صورت تمایل گرد لیمو است. می‌توانید سیب زمینی را به صورت خلالی و سرخ شده در کنار این غذا داشته باشید.', 15, 30, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2019/09/12428462_184-min-300x225.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (142, 'قیمه نثار قزوینی', 'برای درست کردن قیمه نثار قزوینی مجلسی ابتدا گوشت شسته شده و خشک شده را به صورت قیمه‌ای خرد کنید. بهتر است قطعات گوشت قیمه نثار زیاد درشت نباشند. برنج را هم به مدت ۱ ساعت در آب و نمک خیس کنید.', 'برای درست کردن قیمه نثار قزوینی مجلسی ابتدا گوشت شسته شده و خشک شده را به صورت قیمه‌ای خرد کنید. بهتر است قطعات گوشت قیمه نثار زیاد درشت نباشند. برنج را هم به مدت ۱ ساعت در آب و نمک خیس کنید. خلال پسته و بادام را در یک فنجان گلاب خیس کنید. پیاز را به صورت نازک خلال کنید و پیاز داغ فراوان درست کنید. بعد از این‌که پیازها سبک و کاراملی شدند، گوشت قیمه شده را اضافه کنید و خوب با پیاز تفت دهید. زردچوبه را هم اضافه کنید و با گوشت و پیاز تفت دهید. بعد از این‌که رنگ گوشت تغییر کرد و کمی سرخ شد رب گوجه‌ فرنگی را هم اضافه کنید و با گوشت تفت دهید تا طعم خامی رب گرفته شود. ادویه قیمه نثار را به گوشت اضافه کنید و کمی دیگر گوشت را تفت دهید. گوشت باید روی شعله بسیار ملایم گاز تفت داده شود تا ترد و آبدار باقی بماند. ۴ لیوان آب جوش را به ترکیب گوشت و رب اضافه کنید و اجازه دهید روی شعله ملایم گاز خوب جا بیفتد. خورشی که برای قیمه نثار درست می‌کنید باید حسابی غلیظ و جا افتاده باشد. در ۳۰ دقیقه پایانی پخت نمک قیمه نثار را انداز کنید. در این فاصله برنج را طبق دستور تمام پلوها در آب جوش بپزید و آبکش کنید. سپس برنج را دم کنید. بعد از دم کشیدن برنج می‌توانید مقداری از آن را با زعفران دم کشیده غلیظ مخلوط کنید. زمانی که برنج قیمه نثار در حال دم کشیدن است خلال پوست پرتقال را آماده کنید. برای آماده کردن خلال پوست پرتقال ابتدا پوست پرتقال تازه را شسته و نازک خلال کنید. برای گرفتن تلخی پوست پرتقال آن را مقداری در آب جوش قرار دهید تا چند جوش بزند و سپس آبکش کنید و با آب سرد بشویید. اگر خلال پوست پرتقال هنوز طعم تلخی داشت یک بار دیگر این کار را تکرار کنید. حالا نوبت تفت دادن زرشک است. در این مرحله زرشک‌های شسته شده را با ۵۰ گرم کره و یک قاشق غذاخوری شکر کمی تفت دهید سپس خلال پوست پرتقال، خلال پسته و خلال بادام را هم اضافه کنید و کمی تفت دهید. بعد از این‌که خلال‌ها به همراه زرشک کمی تفت خوردند به مقدار دلخواه زعفران آب کرده اضافه کنید و صبر کنید تا آب مواد کشیده شود سپس زیر شعله گاز را خاموش کنید. زمانی که برنج دم کشید مقداری از برنج را از روی قابلمه بردارید و در یک بشقاب بریزید. بقیه برنج را در ظرف مورد نظرتان سرو کنید، روی آن مقداری کره آب شده بریزید و مخلوط گوشت خورش قیمه نثار که حسابی غلیظ و جا افتاده شده را دقیقا وسط برنج بریزید. یک بشقاب برنج را که جدا کرده بودید با زعفران دم کرده، خلال‌ها و زرشک تفت داده شده مخلوط کنید و روی قیمه نثار قزوینی را با آن تزیین کنید.', 40, 80, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2019/01/ghime-nesar-t-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (143, 'کاری بامیه هندی', 'در اولین مرحله بامیه‌ها را بشویید و در یک آبکش بریزید تا آب اضافی بامیه خارج شود. سپس، با استفاده از یک چاقوی تیز، دُم بامیه‌ها را تا جایی کوتاه کنید که بافت بامیه آسیب نبیند. در غیر این صورت، در انته', 'در اولین مرحله بامیه‌ها را بشویید و در یک آبکش بریزید تا آب اضافی بامیه خارج شود. سپس، با استفاده از یک چاقوی تیز، دُم بامیه‌ها را تا جایی کوتاه کنید که بافت بامیه آسیب نبیند. در غیر این صورت، در انتهای کار کاری بامیه شما لزج می‌شود. مقداری روغن را در تابه مناسبی بریزید و روی حرارت قرار دهید. مدتی منتظر بمانید تا روغن کاملا داغ شود. سپس، بامیه‌ها را در تابه بریزید و و حدود ۳ تا ۴ دقیقه تفت دهید. با انجام این کار، رنگ بامیه‌ها تغییر کرده و طعم و عطرشان بهتر می‌شود. در ادامه، بامیه‌ها را از تابه بیرون بیاورید و کنار بگذارید. در فاصله تفت دیدن بامیه‌ها باید پیازها را بشویید و پوست بگیرید. سپس، پیازها را به‌ صورت نگینی خرد کنید. مقداری روغن سرخ کردنی را در قابلمه مناسبی بریزید و روی حرارت متوسط قرار دهید تا کاملا داغ شود. سپس، قطعات پیاز خرد شده را در قابلمه بریزید. پیاز را حدود یکی دو دقیقه تفت دهید تا کاملا طلایی و سبک شود. برای پوره کردن زنجبیل تازه، ابتدا باید با کمک یک چاقوی تیز قطعه‌ای از ریشه زنجبیل را جدا کنید. پوست زنجبیل بسیار لطیف و نازک است. بنابراین، برای گرفتن پوست زنجبیل به‌ جای چاقو و پوست‌کن بهتر است از یک قاشق یا یک قطعه سیم ظرفشویی تمیز یا حتی پد ظرفشویی کمک بگیرید. سپس، زنجبیل بدون پوست را به قطعات کوچک تقسیم کرده و با استفاده از گوشتکوب برقی یا دستگاه غذاساز کاملا پوره کنید. در ادامه، پوره زنجبیل را به قابلمه حاوی پیاز تفت داده شده اضافه کنید و تفت دهید. حالا، حبه‌های سیر را هم بشویید و پوست بگیرید. سپس، سیر را هم درست مانند زنجبیل با کمک رنده ریز یا گوشتکوب برقی کاملا پوره کنید. پوره سیر را هم به قابلمه حاوی پیاز نگینی و پوره زنجبیل اضافه کنید. این ترکیب را یکی دو دقیقه تفت دهید تا عطر و بوی بی‌نظیر سیر و زنجبیل آزاد شود. در این مرحله برگ بو، زردچوبه، پودر کاری، پودر هل، پودر گشنیز، پودر زیره، گراماسالا، جوز هندی، فلفل قرمز و فلفل سیاه را به قابلمه اضافه کنید. این ترکیب را یکی دو دقیقه تفت دهید تا عطر ادویه‌ها آزاد شده و با بوی خوش مواد دیگر مخلوط شود. در این قسمت کشک را در ظرف مناسبی بریزید و آب را به‌صورت تدریجی و کم‌کم به کشک اضافه کنید. به این ترتیب، می‌توانید غلظت کشک را با دقت کنترل کنید. سپس، مخلوط کشک و آب را به‌ خوبی هم بزنید تا کاملا با هم ترکیب شوند. حالا، کشک رقیق شده، هم خورده و کاملا یک‌دست را به قابلمه حاوی پیاز، سیر، زنجبیل و ادویه‌ها اضافه کنید. چند دقیقه منتظر بمانید تا مایع درون قابلمه بجوشد. سپس، درب قابلمه را ببندید و حرارت را ملایم کنید. در ادامه، حدود ۱۵ تا ۲۰ دقیقه صبر کنید تا مایه کاری بامیه به غلظت دلخواه برسد. حالا نوبت به اضافه کردن بامیه‌ها می‌رسد. بامیه‌های تفت داده شده را همراه با مقداری نمک در قابلمه بریزید. خورش کاری بامیه را هم بزنید و درب قابلمه را ببندید. حدود ۱۵ تا ۲۰ دقیقه دیگر هم صبر کنید تا کاری بامیه شما با حرارت ملایم بپزد و کاملا جا افتاده و لعاب‌دار شود چون این خورشت یک خورش کم آب و خیلی غلیظ است. گشنیز تازه را با دقت تمیز کرده و بشویید. آب اضافی گشنیز که گرفته شد آن را خرد کنید. در نهایت، نیمی از گشنیز خرد شده را به قابلمه اضافه کنید و هم بزنید. حرارت را خاموش کنید تا رنگ گشنیز تغییر نکند و فقط مقداری حرارت ببیند. این کاری بامیه تند و تیز و خوشمزه را با باقی‌مانده گشنیز ساطوری تزیین کرده و همراه با برنج یا نان نوش جان کنید.', 16, 34, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/07/%D8%A8%D8%A7%D9%85%DB%8C%D9%87-2-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (144, 'کاری لپه', 'لپه را بشویید و با مقداری آب به مدت یک ساعت بخیسانید. پیاز را نگینی خرد کنید. سیر و زنجبیل تازه را پوست بگیرید و رنده کنید.', 'لپه را بشویید و با مقداری آب به مدت یک ساعت بخیسانید. پیاز را نگینی خرد کنید. سیر و زنجبیل تازه را پوست بگیرید و رنده کنید. گشنیز تازه را پاک کرده، بشویید و ساطوری کنید. مغز بادام هندی را بکوبید تا کاملا خرد شود. آب لپه را خالی کنید. قابلمه دیگری را تا نیمه پر از آب تازه کنید و روی حرارت متوسط بگذارید تا بجوشد. لپه خیس خورده را به آب در حال جوش اضافه کنید و اجازه دهید به مدت ۲ ساعت خوب بپزد. مقداری روغن را در تابه مناسبی بریزید و روی حرارت بگذارید تا داغ شود. پیاز نگینی خرد شده را به روغن داغ اضافه کنید و تفت دهید تا کاملا سبک شود. سپس، پوره سیر و زنجبیل را نیز در تابه بریزید و به مدت یکی دو دقیقه با پیاز تفت دهید. پس از آن‌که عطر سیر و زنجبیل آزاد شد، مقداری نمک، ادویه کاری، پودر تخم گشنیز، برگ بو، فلفل سیاه و فلفل قرمز را به تابه اضافه کنید. این ترکیب را یک دقیقه هم بزنید و تفت دهید تا بوی ادویه‌ها به مشام برسد و طعم خامی آن‌ها برطرف شود. در ادامه، رب گوجه فرنگی را نیز به باقی مواد اضافه کنید و تفت دهید تا رنگ رب گوجه باز شود. در نهایت، گشنیز تازه و ساطوری شده را هم داخل تابه بریزید و هم بزنید. بخشی از گشنیز خرد شده را برای تزیین غذا کنار بگذارید. بعد از پخت کامل لپه، لپه پخته شده را در آبکش بریزید تا آب اضافی آن خارج شود. سپس، لپه نرم و بادام هندی خرد شده را به مایه کاری لپه اضافه کنید. حالا، به اندازه ۲ یا ۳ لیوان دسته‌دار فرانسوی آب جوش هم در تابه بریزید. درب تابه را ببندید و حرارت را ملایم کنید تا خورش کاری لپه به مدت ۱۰ تا ۱۵ دقیقه بجوشد و جا افتاده شود. پس از گذشت این مدت، شیر نارگیل را نیز در تابه بریزید. محتویات تابه را هم بزنید تا همه مواد به خوبی با یکدیگر ترکیب شوند. سپس، درب تابه را ببندید و حدود ۱۰ دقیقه دیگر منتظر بمانید تا خورش کاری لپه غلیظ و کم آب و آماده سرو شود. حالا، خورش کاری لپه را با گشنیز خرد شده و قطعات لیموترش تازه تزیین کرده و همراه با برنج کته یا برنج چلو سرو کنید. این غذا را می‌توانید با انواع سالاد، سبزیجات تازه یا حتی با ماست نوش جان کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/08/%DA%A9%D8%A7%D8%B1%DB%8C-%D9%84%D9%BE%D9%87-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (145, 'کباب بختیاری', 'فیله گوشت و مرغ را بشویید و بعد از خشک کردن، به اندازه مناسب برای کباب برش دهید. سپس با استفاده از بیفتک یا گوشت کوب به آرامی گوشت و مرغ را بکوبید تا نرم شوند. گوشت و مرغ را در ظرف جداگانه‌ای قرار دهی', 'فیله گوشت و مرغ را بشویید و بعد از خشک کردن، به اندازه مناسب برای کباب برش دهید. سپس با استفاده از بیفتک یا گوشت کوب به آرامی گوشت و مرغ را بکوبید تا نرم شوند. گوشت و مرغ را در ظرف جداگانه‌ای قرار دهید. پیاز را رنده ریز کنید و آب آن را جدا کنید (می‌توانید با دست فشار دهید). آب پیاز را به ظرف گوشت و مرغ‌ها اضافه کنید. برای طعم‌دار کردن گوشت، مقداری سس سویا، روغن زیتون و فلفل به آن اضافه کنید و اجازه دهید برای چند ساعت مزه‌دار شود. برای مزه‌دار کردن مرغ، مقداری روغن زیتون، نمک، فلفل و زعفران آب شده را به مرغ اضافه کنید و برای چند ساعت صبر کنید تا مواد به خورد مرغ برود. در انتها مقداری فلفل دلمه و پیاز را خلالی کنید و به مواد اضافه کنید. نیم ساعت قبل از پخت، مقداری آب لیمو ترش تازه به گوشت و مرغ‌ها اضافه کنید. در این مرحله می‌توانید کمی نمک به گوشت قرمز اضافه کنید. برای به سیخ کشیدن، از دو تکه گوشت و دو تکه مرغ در کنار هم استفاده کنید. مواد را به ترتیب به سیخ بکشید و بعد از آماده کردن همه سیخ‌ها، با استفاده از گوشت کوب یا بیفتک به آرامی روی گوشت و مرغ‌ها بکوبید تا مرز بین گوشت‌ها از بین برود و به هم نزدیک‌تر شوند. برای پخت کباب بختیاری به روش زغالی، آتش یا منقل را روشن کنید. یک ظرف از کره آب شده با کمی زعفران را در کنار خود داشته باشید. کباب‌ها را روی منقل قرار دهید. در زمان پخت برای جلوگیری از خشک شدن کباب، چند بار ترکیب زعفران و کره را با کمک فرچه روی کباب بزنید. برای خارج کردن کباب‌ها از سیخ از یک تکه نان کمک بگیرید. در نهایت کباب‌ها را به صورت یکپارچه از سیخ جدا کنید و با ریحان و گوجه فرنگی تزئین کنید. اگر کباب بختیاری را داخل ماهیتابه درست می‌کنید، از سیخ چوبی استفاده کنید. قطعات گوشت و مرغ را کمی کوچک‌تر برش بزنید. تمامی روال مزه‌دار کردن کباب را همانند مراحل قبل انجام دهید. سپس مقداری روغن داخل ماهیتابه بریزید و بعد از داغ شدن آن سیخ کباب‌ها را داخل ماهیتابه قرار دهید. از روغن زیاد استفاده نکنید و فقط کف ماهیتابه را کمی چرب کنید. بعد از اینکه یک سمت کباب‌ها پخته شد، آن را برگردانید. سپس این غذا را همراه با انواع سبزیجات بخارپز یا گریل شده سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/bakhtiari_kebab.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (146, 'کباب برگ', 'برای تهیه کباب برگ ترد و آبدار، گوشت راسته گوسفند یا گوساله را به ضخامت کمتر از یک سانتی‌متر برش بزنید و با بیفتک‌کوب یا سیخ فلزی یا پشت چاقو بکوبید تا بافت سفت آن، نرم شود ولی پاره نشود.', 'برای تهیه کباب برگ ترد و آبدار، گوشت راسته گوسفند یا گوساله را به ضخامت کمتر از یک سانتی‌متر برش بزنید و با بیفتک‌کوب یا سیخ فلزی یا پشت چاقو بکوبید تا بافت سفت آن، نرم شود ولی پاره نشود. گوشت کباب برگ را روی تخته گوشت بگذارید و با استیک‌کوب حسابی بکوبید تا پهن شود و قطر آن به حدود نیم سانتی‌متر برسد. اگر گوشتی که استفاده می‌کنید ضخامت زیادی دارد، ابتدا با یک چاقوی تیز و با دقت از پهنا به دو قسمت مساوی تقسیم کنید و سپس هر قسمت را با بیفتک‌کوب آنقدر بکوبید تا به قطر و ضخامت دلخواه برسید. برای مرینیت کردن گوشت، پیازها را پوست بگیرید و رنده کنید. سیرها را هم رنده کنید. آب پیازهای رنده شده را بگیرید. گوشت را در یک پلاستیک زیپ‌کیپ یا ظرف مناسب بگذارید. به گوشت کباب برگ، روغن زیتون، آب پیاز رنده شده، برگ رزماری، سرکه قرمز، برگ بو، میخک، جوز هندی، زعفران دم کرده و دو حبه سیر رنده شده اضافه کنید. درب پاکت را ببندید و گوشت را در یخچال بگذارید. حدود ۲ تا ۸ ساعت گوشت‌ها را با مواد استراحت دهید تا مزه‌دار و ترد شوند. برای به سیخ زدن کباب برگ، گوشت‌ را از یخچال بیرون بیاورید. گوشت را روی سطح کار قرار دهید. با دست روی گوشت را بگیرید تا در جای خود ثابت بماند. سپس سیخ را آرام آرام از زیر دست‌تان وارد گوشت کنید. تکه‌های گوشت باید پس از سیخ کشیدن به هم چسبیده باشند. زغال‌های گداخته را آماده کنید تا حرارت یکسانی داشته باشند. در صورت تمایل چند عدد گوجه را هم سیخ بگیرید و روی منقل بچینید. سیخ‌های کباب را روی حرارت قرار دهید و با دقت زیر و رو کنید تا گوشت کباب شود. هر سمت سیخ‌ها را باید حدود ۵ تا ۷ دقیقه روی حرارت بگیرید. اگر احساس کردید در زمان کباب کردن، گوشت خشک می‌شود، می‌توانید روی گوشت‌ها با فرچه مقداری از ترکیب کره و زعفران دم کرده بمالید. در اواسط پخت مقداری نمک روی گوشت در حال کباب بپاشید. کباب برگ را در کنار برنج و گوجه کبابی سرو کنید. برای کشیدن کباب از سیخ، کباب را مستقیم روی برنج چلو بگذارید و با دقت از سیخ جدا و سرو کنید. اگر می‌خواهید کباب برگ را در فر تهیه کنید، گوشت‌ها را مانند روش بالا، آماده و مزه‌دار کنید و در یخچال استراحت دهید. سپس گوشت‌ها را سر سیخ بزنید و سیخ‌ها را در یک سینی فر بچینید، طوری که روی هم قرار نگیرند. فر را روشن و روی دمای ۱۸۰ درجه سانتی‌گراد تنظیم کنید. وقتی فر داغ شد، سینی حاوی سیخ‌های کباب برگ را در فر بگذارید. روی گوشت را زعفران دم کرده و کره بمالید و به مدت ۷ دقیقه صبر کنید. اجازه دهید یک سمت کباب‌ها طلایی شود. سپس درب فر را باز کنید و سیخ‌ها را برگردانید تا سمت دیگر آنها هم طلایی شود. هر دو طرف کباب برگ که پخت، آن را از فر خارج کرده و با برنج سرو کنید. نکته: اگر می‌خواهید کباب برگ در فر، طعم و عطر زغالی بگیرد، یک یا دو تکه زغال را روی گاز، گداخته کنید و در یک کاسه بگذارید. زغال‌ها را در فر قرار دهید و روی آنها، دو قطره روغن بریزید. درب فر را ببندید و اجازه دهید، دود زغال فضای فر را پر کند و کباب برگ بوی زغالی بگیرد. برای درست کردن کباب برگ نرم و آبدار در تابه و روی گاز، گوشت کباب برگ را آماده و مزه‌دار کنید. بعد از استراحت دادن گوشت‌ها با مواد مرینیت، در یک تابه، کمی کره یا روغن بریزید و گوشت‌ها را در تابه بچینید. درب تابه را ببندید. تابه را روی حرارت ملایم بگذارید و ۱۵ دقیقه صبر کنید تا کباب مغزپخت شود. سپس درب تابه را باز کنید و کباب‌ها را زیر و رو کنید. حدود نیم ساعت، گوشت‌ها را بپزید. در این مدت، به گوشت‌ها سر بزنید و در صورت لزوم کره و زعفران دم‌کرده به آنها بزنید. وقتی کاملا گوشت‌ها پختند و نرم شدند، شعله را خاموش کرده و کباب برگ را سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (147, 'کباب بناب ساطوری آذربایجان', 'برای تهیه کباب بناب بهتر است از ترکیب چرخ کرده گوشت گوساله، گوشت گوسفندی (ترجیحا راسته یا قلوه‌گاه) و مقداری دنبه استفاده کنید. برای این منظور، ابتدا گوشت‌ها و دنبه را به صورت درشت فقط یک‌بار چرخ کنید', 'برای تهیه کباب بناب بهتر است از ترکیب چرخ کرده گوشت گوساله، گوشت گوسفندی (ترجیحا راسته یا قلوه‌گاه) و مقداری دنبه استفاده کنید. برای این منظور، ابتدا گوشت‌ها و دنبه را به صورت درشت فقط یک‌بار چرخ کنید. پیازها را به ریزترین شکل ممکن ساطوری کرده و پیاز ساطوری شده را با گوشت چرخ کرده مخلوط کنید. نمک و فلفل سیاه را هم به این ترکیب اضافه کرده و مخلوط گوشت چرخ کرده و پیاز را بارها از جهات مختلف ساطوری کنید. این کار، جایگزین ورز دادن گوشت است و به چسبندگی هر چه بیشتر مایه کباب بناب کمک می‌کند. به اندازه یک پرتقال درشت از این ترکیب بردارید و آن را روی سیخ استیل ضخیم، به صورت یکنواخت پهن کنید. کباب‌ها را روی زغال گداخته گریل کنید تا سطح آن برشته و مغزپخت شود. کباب بناب معمولا با نان سنگک یا با برنج کته به همراه ریحان، سماق و دوغ سرو می‌شود. در اولین مرحله از طرز تهیه کباب بناب آذربایجان، درست مانند کباب کوبیده باید ترکیب گوشت و دنبه را چرخ کنید. با این تفاوت که برای تهیه کباب بناب، لازم است گوشت را به صورت درشت و فقط یک‌بار چرخی کنید تا گوشت زیادی له نشود. در این مرحله از طرز تهیه کباب بناب اصل، وقت آن است که پیازهای شسته شده و بدون پوست را روی تخته آشپزی قرار دهید و با چاقو به ریزترین شکل ممکن، پیازها را ساطوری کنید. توجه داشته باشید، در هنگام درست کردن کباب بناب به هیچ عنوان نباید پیاز را رنده کنید. در غیر این صورت، پیاز زیادی ریز می‌شود و آب می‌اندازد. این امر موجب خراب شدن بافت کباب بناب خواهد شد. در ضمن، موقع میل کردن کباب بناب باید طعم گوشت و پیاز به یک اندازه زیر دندان بیاید. پس از ساطوری کردن پیاز و اطمینان از ریز بودن آن، پیاز ساطوری شده را همراه با گوشت چرخ کرده (درشت) در ظرف مناسبی بریزید و تا اندازه‌ای ورز دهید تا با یکدیگر ترکیب شوند. با توجه به بافت مایه کباب بناب، بهتر است به جای ورز دادن از روش مشت زدن برای ترکیب گوشت و پیاز استفاده کنید. در این مرحله از طرز تهیه کباب بناب رستورانی، وقت آن است که ادویه‌ها شامل مقداری نمک و فلفل سیاه را به مخلوط گوشت و پیاز اضافه کنید و ورز دهید تا کاملا با یکدیگر مخلوط شوند. توجه داشته باشید، برای درست کردن کباب بناب به ادویه‌های متنوعی نیاز ندارید. دلیلش هم این است که در هنگام میل کردن این غذا باید مزه اصلی گوشت و پیاز زیر دندانتان بیاید. فقط در صورت تمایل، می‌توانید مقداری فلفل قرمز را نیز به این مواد اضافه کنید. پس از ترکیب کردن همه مواد، وقت آن است که مایه کباب بناب را دوباره روی تخته آشپزی بگذارید و با کمک یک چاقو این مواد را از تمامی جهات حسابی ساطوری کنید. با انجام این کار، شما چسبندگی کامل مایه کباب بناب را تضمین می‌کنید. پس از انجام مراحل بالا، در این مرحله از طرز تهیه کباب بناب بازاری، مایه کباب بناب را در ظرف مناسبی بگذارید و روی این ظرف را با سلفون بپوشانید. سپس، ظرف حاوی مایه کباب بناب را برای ۱ ساعت در یخچال قرار دهید تا استراحت کند و حسابی خودش را بگیرد. پس از گذشت این مدت، مایه کباب بناب را از یخچال خارج کنید. سیخ‌های ضخیم استیل را هم کنار دستتان بگذارید. سپس، به اندازه یک پرتقال درشت (حدود ۲۰۰ الی ۲۵۰ گرم) از مایه کباب بناب بردارید و به صورت لوله‌ای در میان دستان خود فرم دهید. در ادامه، سیخ کباب را از وسط این مایه لوله‌ای شکل رد کنید. توجه داشته باشید، از الان به بعد باید به طور مداوم کف دستتان را حسابی مرطوب کنید تا مایه کباب به دست شما نچسبد و به خوبی روی سیخ پهن شود. مایه کباب را روی سیخ پهن کنید و با انگشتان خودتان به آن فرم دهید. پیشنهاد می‌کنیم، با فشار انگشتان در فواصل مساوی، بندهای یکسانی را روی گوشت ایجاد کنید تا ظاهر زیباتری داشته باشد. پس از به سیخ کشیدن مایه کباب بناب، باید آن‌ها را روی منقل قرار دهید تا پخته شوند. برای این منظور، ابتدا باید زغال را روشن کنید و اجازه بدهید به صورت یکدست گداخته شود. سپس، سیخ‌های کباب را روی منقل بگذارید و در ابتدا کمی باد بزنید تا کباب‌ها کمی سفت شوند. توجه داشته باشید، اگر حرارت یکدست نباشد یا شعله آن خیلی کم باشد، احتمال ریختن گوشت کبابی از سیخ وجود دارد. بنابراین، تا جای ممکن کباب بناب را باد نزنید. در فاصله کباب شدن گوشت روی منقل، مقداری کره را داخل ظرف مناسبی بریزید و برای چند ثانیه داخل مایکروویو قرار دهید یا کنار آتش منقل بگذارید تا کره ذوب شود. سپس، کمی زعفران آب شده را هم به کره ذوب شده اضافه کنید. این ترکیب را با کمک فرچه آشپزی در زمان پخت روی کباب‌ها بمالید تا کباب بناب شما خوشمزه‌تر، آبدارتر و جذاب‌تر شود. اگر دوست ندارید کباب شما عطر زعفران را به خودش بگیرد می‌توانید فقط از کره برای رومال کباب بناب استفاده کنید. پس از آماده شدن کباب بناب، چند تکه نان سنگک، لواش یا تافتون را روی سینی سرو بچینید و کباب آماده را روی نان بگذارید. در صورت تمایل، می‌توانید تعدادی گوجه فرنگی و فلفل سبز را نیز کباب کرده و با کباب بناب سرو کنید. کباب بناب را به دلخواه می‌توانید با نان، برنج زعفرانی یا برنج چلو ساده میل کنید. راستی، می‌توانید مقداری پودر سماق را نیز روی کباب بناب بپاشید و سپس، آن را میل کنید. پیاز کباب بناب را به صورت کاملا ریز ساطوری کنید تا هیچ آبی نداشته باشد. از سوی دیگر، ترکیب گوشت چرخ شده (درشت) و پیاز را بارها ساطوری کنید تا این مواد کاملا با یکدیگر ترکیب شوند از چسبندگی کامل برخوردار شوند. در ضمن، مایه کباب بناب را برای یک ساعت در یخچال بگذارید تا استراحت کند. سپس، کباب‌ها را روی شعله یکنواخت برشته کنید و به صورت مداوم بچرخانید تا نریزید و کاملا مغز‌پخت شود. استفاده از ترکیب گوشت گوسفندی و گوساله تازه با دنبه گوسفندی از بهترین روش‌هایی است که به آبدارتر شدن کباب بناب کمک می‌کند. زغال باید کاملا گداخته و یکنواخت باشد. سیخ‌ها را مرتب بچرخانید تا کباب یکدست پخته شود و نسوزد.', 33, 67, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (148, 'کباب تابه ای بوقلمون', 'در اولین مرحله، گوشت بوقلمون را شسته و در آبکش قرار دهید تا آب اضافه از آن خارج شود و کمی خشک شود. می‌توانید از سینه بوقلمون یا ران بوقلمون و یا ترکیبی از هر دو استفاده کنید.', 'در اولین مرحله، گوشت بوقلمون را شسته و در آبکش قرار دهید تا آب اضافه از آن خارج شود و کمی خشک شود. می‌توانید از سینه بوقلمون یا ران بوقلمون و یا ترکیبی از هر دو استفاده کنید. بعد از آنکه آب اضافه از تکه‌های بوقلمون خارج شد، باید آنها را چرخ کنید. اگر از ران بوقلمون استفاده می‌کنید پیش از چرخ کردن، استخوان‌ها را از گوشت جدا کنید. گوشت بوقلمون را در چرخ گوشت و یا میکسر بریزید و آن را دو تا سه بار چرخ کنید تا کاملا به صورت یکدست به شکل پوره دربیاید. اگر هم چرخ گوشت ندارید چرخ کرده بوقلمون را به صورت آماده از بیرون خریداری کنید. دو عدد پیاز را پوست بگیرید و بشویید. قسمت انتهایی پیاز را جدا نکنید تا وقت رنده کردن لایه‌های آن از هم جدا نشوند و پیاز وا نرود. پیاز را با رنده ریز، رنده کنید. با دست پیاز رنده شده را فشرده کنید تا آب پیاز کاملا گرفته شود، ما به تفاله پیاز نیاز داریم. بعد از آنکه از جدا شدن آب پیاز از پیازهای رنده شده مطمئن شدید، تفاله‌های پیاز را به گوشت چرخ کرده بوقلمون اضافه کنید و آنها را به خوبی با هم ترکیب کنید. در سومین مرحله، یک عدد فلفل دلمه‌ای را بشویید. فلفل دلمه‌ای را به صورت نگینی ریز خرد کنید یا با رنده ریز، رنده کنید به ترکیب پیاز و چرخ کرده بوقلمون اضافه کنید و با هم مخلوط کنید. برای تهیه کباب تابه ای زعفرانی با بوقلمون، زعفران را از قبل بسابید و با مقداری آبجوش و یا چند تکه یخ اجازه دهید تا دم بکشد و خوش‌رنگ شود. سپس دو قاشق غذاخوری زعفران غلیظ دم کرده به همراه دیگر ادویه‌ها شامل نمک، فلفل سیاه و زردچوبه را به ترکیب مواد چرخ شده اضافه کنید و مواد را حسابی با دست ورز دهید تا مزه چاشنی‌ها به خورد مایه کباب تابه ای برود. ورز دادن گوشت را تا چند دقیقه ادامه دهید تا حسابی یکدست و چسبناک شود. بعد از آنکه حسابی مایه کباب را ورز دادید، روی ظرف آن سلفون بکشید. مایه کباب تابه ای با بوقلمون را به مدت دو ساعت در یخچال قرار دهید تا استراحت کند و کاملا خودش را بگیرد و به انسجام مناسب برسد. بعد از گذشت دو ساعت مایه کباب تابه ای تهیه شده با بوقلمون چرخ شده را از یخچال بیرون بیاورید. یک تابه نچسب با سایز مناسب انتخاب کنید و روی حرارت قرار دهید. مقدار کافی از روغن را در تابه بریزید و اجازه دهید داغ شود. سپس به سراغ سرخ کردن کباب‌های تابه ای بوقلمون بروید. به دو شکل می‌توانید کباب تابه ای را شکل دهید. در روش اول کل مایه را کف ظرف پهن کنید و بعد از گذشت چند دقیقه وقتی خودش را گرفت به وسیله نوک کفگیر یا یک چاقوی تیز در آن شیار ایجاد کنید. زمانی که یک طرف کباب‌ها سرخ شد، آن را برگردانید تا طرف دیگرش نیز سرخ شود. مایه کباب بوقلمون تابه ای را می‌توانید مثل کتلت گوشت با دست نیز شکل دهید و دو طرف آن را در روغن داغ سرخ کنید. یک قالب کره را با چاقو به صورت لایه‌های نازک برش دهید و روی کباب‌ها بگذارید. درب تابه را ببندید و اجازه دهید کره با حرارت آب شود و به خورد گوشت بوقلمون چرخ شده برود. بعد آنکه هر دو سمت کباب تابه‌ای‌ها سرخ شدند، یعنی کباب‌ها از کناره‌های تابه جمع شدند و آب آن کشیده شد، آن را از تابه خارج کنید و در ظرفی مناسب قرار دهید. می‌توانید از مقداری گوجه ورق سرخ شده در کنار این کباب تابه ای استفاده کنید. کباب تابه ای بوقلمون با برنج کته یا چلو آبکش و یا با نان سرو کنید و از طعم لذیذ و بافت لطیفش لذت ببرید. کباب تابه‌ای بوقلمون با سس گوجه فرنگی یک غذای لذیذ مجلسی محسوب می‌شود. برای تهیه سس گوجه فرنگی دو قاشق غذاخوری رب گوجه را به همراه نمک و فلفل در روغن و در همان تابه‌ای که کباب تابه ای را سرخ کردید تفت دهید، نصف استکان آب اضافه کنید و آب وقتی به جوش آمد تکه‌های کباب تابه‌ای سرخ شده را به آن اضافه کنید. درب تابه را ببندید، حرارت را کم کنید و اجازه دهید روی حرارت ملایم با سس گوجه به خوبی بپزد.', 11, 24, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/Kilis-tava-tarifi-1024x576.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (149, 'کباب تابه ای ترش گیلانی', 'برای تهیه کباب تابه ای ترش، ابتدا یک عدد پیاز را رنده کنید و بعد از آنکه آب آن را گرفتید، گوشت چرخ کرده را اضافه کنید. ادویه‌های لازم شامل نمک، فلفل سیاه و زردچوبه را نیز اضافه کنید و مایه کباب را برا', 'برای تهیه کباب تابه ای ترش، ابتدا یک عدد پیاز را رنده کنید و بعد از آنکه آب آن را گرفتید، گوشت چرخ کرده را اضافه کنید. ادویه‌های لازم شامل نمک، فلفل سیاه و زردچوبه را نیز اضافه کنید و مایه کباب را برای ۵ تا ۱۰ دقیقه با دست ورز دهید. مایه کباب ترش تابه ای را به مدت یک ساعت در یخچال یا ده دقیقه در فریزر استراحت دهید. در این فاصله به سراغ درست کردن سس ترش کباب تابه ای بروید. پیاز داغ آماده کنید و گردوی خرد شده و رب انار را با آن تفت دهید. سپس نصف لیوان دسته دار فرانسوی آب به آن اضافه کنید و اجازه دهید چند دقیقه روی حرارت باقی بماند تا غلیظ شود. مایه کباب تابه ای را از یخچال بیرون بیاورید. کف یک تابه را کمی چرب کنید و مایه کباب را روی آن پهن کنید. اجازه دهید هر دو طرف کباب تابه ای سرخ شود، سپس سس انار را به آن اضافه کنید. درب تابه را ببندید و صبر کنید تا حدود یک ربع دیگر کباب تابه ای ترش با سس مخصوصش بپزد. در انتها این غذای خوشمزه را با برنج کته یا برنج آبکش سرو کنید.', 18, 37, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (150, 'کباب تابه ای دو رنگ', 'در اولین مرحله از طرز تهیه کباب تابه ای دو رنگ، ۲ پیاز و یک حبه سیر را پوست بگیرید و رنده کنید. با دست آب پیاز را کاملا بگیرید. سیر رنده شده و پیازهای آبگیری شده را در یک کاسه بریزید و به آن‌ها گوشت چ', 'در اولین مرحله از طرز تهیه کباب تابه ای دو رنگ، ۲ پیاز و یک حبه سیر را پوست بگیرید و رنده کنید. با دست آب پیاز را کاملا بگیرید. سیر رنده شده و پیازهای آبگیری شده را در یک کاسه بریزید و به آن‌ها گوشت چرخ کرده مخلوط گوساله و گوسفند (کم‌چربی) اضافه کنید. سپس مقداری نمک، فلفل سیاه، پودر پاپریکا و دارچین به مایه گوشت چرخ کرده اضافه کنید و مواد را با هم ورز بدهید تا یکدست شوند. روی کاسه را با سلفون بپوشانید و به مدت ۱ ساعت در یخچال قرار دهید تا مایه گوشت منسجم شود. ترکیب سینه و ران مرغ بدون استخوان را به خوبی چرخ کنید. دو پیاز و ۱ حبه سیر باقی‌مانده را هم رنده و آبگیری کنید. در یک کاسه، مرغ چرخ کرده، پیاز و سیر آب‌گیری شده، نمک، فلفل سیاه، پودر پاپریکا و دارچین را اضافه کنید و مواد را با هم ورز بدهید تا یکدست شود. روی این کاسه را هم با سلفون بپوشانید و به مدت ۱ ساعت در یخچال قرار دهید. در این فاصله، زعفران را دم کنید. کف یک تابه نچسب را با مقداری روغن یا کره چرب کنید. بهتر است از اسپری یا برس استفاده کنید تا همه جای تابه، چرب شود. به اندازه یک پرتقال متوسط از مایه گوشت چرخ کرده بردارید و به صورت طولی در تابه قرار دهید. به اندازه یک پرتقال متوسط هم مایه مرغ چرخ کرده بردارید و به صورت طولی، کنار گوشت چرخ‌کرده قرار دهید طوری که لایه‌ها به هم بچسبند. همه لایه‌ها را به همین شکل کنار هم بچینید. در این مرحله از طرز تهیه کباب تابه ای دو رنگ، تابه را روی حرارت قرار دهید. ابتدا حرارت را بالا ببرید و بعد آن را روی حالت ملایم قرار دهید. اجازه دهید یک طرف کباب‌ها سرخ شود. با برس آشپزی، زعفران دم‌کرده و مقداری کره را روی کباب‌ها بمالید. سپس با یک چاقو به صورت عرضی، کباب‌ها را برش بزنید. به طوری که برای هر یک عدد کباب تابه‌ای یک تکه گوشت چرخ کرده و یک تکه مرغ چرخ کرده، کنار هم قرار بگیرد. به آرامی، کباب‌ها را برگردانید تا طرف دیگر کباب‌ها هم سرخ شود. برای اینکه از مغزپخت شدن کباب‌ها خیالتان راحت شود، می‌توانید برای ۱۰ دقیقه درب تابه را بگذارید. دوباره از مخلوط کره و زعفران دم کرده، روی کباب‌ها رومال کنید. هر دو طرف کباب‌ها که سرخ شد، حرارت را قطع کرده و کباب‌های دو رنگ را با برنج چلو آبکش یا نان، سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/01/kabab-tabe-2-rang.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (151, 'کباب تابه ای', 'برای درست کردن کباب تابه‌ای با گوشت چرخ کرده، ابتدا پیاز را رنده کنید و آب آن را با دست بفشارید. سپس تفاله پیاز را به گوشت چرخ کرده اضافه کنید. ادویه‌ها شامل سماق، گلپر، فلفل سیاه و نمک را اضافه کنید', 'برای درست کردن کباب تابه‌ای با گوشت چرخ کرده، ابتدا پیاز را رنده کنید و آب آن را با دست بفشارید. سپس تفاله پیاز را به گوشت چرخ کرده اضافه کنید. ادویه‌ها شامل سماق، گلپر، فلفل سیاه و نمک را اضافه کنید و با هم ورز دهید. مایه کباب تابه ای را ورز دهید تا مخلوطی یکدست و منسجم داشته باشید. درب ظرف را با سلفون بپوشانید و به مدت ۴۵ دقیقه تا ۱ ساعت در یخچال استراحت دهید. اگر زمان کافی ندارید، حدود ۱۰ دقیقه مایه کباب تابه‌ای را در فریزر قرار دهید. بدون اینکه در تابه روغن بریزید، مایه کباب تابه‌ای را در تابه بگذارید و با انگشت‌هایتان، مواد را کف تابه، پخش کنید تا یک لایه کباب تابه‌ای داشته باشید و همه قسمت‌های آن، به یک اندازه ضخامت داشته باشد. تابه را روی حرارت ملایم قرار دهید. اجازه دهید گوشت روی حرارت کم، چربی خود را پس بدهد و کباب تابه‌ای با همان چربی سرخ شود. به اندازه نصف یا ۱/۳ لیوان، آب به تابه اضافه کنید. حرارت را بالا نبرید تا کباب‌های تابه ای فرصت پخت داشته باشند و یک سمت آنها طلایی شود. وقتی یک طرف کباب‌ها سرخ شد، آن‌ها را برگردانید تا طرف دیگر هم سرخ شود. بعد از این که کباب تابه‌ای‌ها سرخ شدند، گوجه‌ فرنگی‌ها را بشویید و به صورت حلقه‌ای برش بزنید و روی سطح کباب تابه‌ای بچینید تا آن را بپوشاند. درب تابه را روی آن بگذارید و صبر کنید تا گوجه فرنگی‌ها هم بپزند و نرم شوند. سپس کباب تابه ای را در ظرف سرو بگذارید و با سبزی خوردن، پیاز خام، نان دلخواه، برنج آبکش یا برنج کته سرو کنید. برای تهیه سس کباب تابه ای در یک تابه مقداری روغن بریزید و به اندازه ۱ قاشق غذاخوری رب گوجه فرنگی را اضافه کنید و تفت دهید. وقتی رب گوجه فرنگی، رنگ باز کرد و طعم خامی آن گرفته شد، پودر پاپریکا و گرد لیمو را اضافه کنید. حداقل ۱ دقیقه تفت دهید. به اندازه ۲/۳ لیوان دسته‌دار فرانسوی آب جوش به مواد اضافه کنید. می‌توانید به مقدار دلخواه زعفران دم کرده غلیظ را هم به مواد اضافه کنید. اجازه دهید سس کمی بجوشد و غلیظ شود. سپس حرارت را قطع کنید. برای تهیه کباب تابه ای سرخ شده، مایه کباب تابه ای را آماده کنید و بعد از این که آن را استراحت دادید، از یخچال بیرون بیاورید. کمی روغن یا کره را کف تابه بریزید و روی حرارت قرار دهید تا گرم شود. کل مایه کباب تابه ای را کف تابه بریزید و با پشت قاشق صاف کنید. حرارت را کم کنید. درب ظرف را بگذارید. وقتی کباب خودش را گرفت و یک طرف آن سرخ شد، شروع به برش زدن کنید. برش‌های کباب را برگردانید تا سمت دیگر آن هم سرخ شود. بعد از این که هر دو طرف کباب‌ها سرخ شد، سس گوجه فرنگی را مانند روش دوم درست کنید و به آن بیفزایید. درب ظرف را بگذارید تا به مدت ۳۰ دقیقه ترکیب کباب و سس با هم بپزد. در تمام مدت پخت، حرارت را بسیار ملایم تنظیم کنید. در این روش از طرز تهیه کباب تابه ای، از گوجه فرنگی استفاده نمی‌شود و کباب‌ها در سس گوجه پخته می‌شوند. در نهایت کباب تابه ای را به همراه سس آن با نان یا برنج و مخلفات دلخواه سرو کنید.', 11, 24, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (152, 'کباب تتالی اراکی', 'در اولین مرحله از دستور پخت تتالی اراکی باید یکی از پیازها را پوست بگیرید و سپس با رنده ریز رنده کنید. آب اضافی پیاز را بگیرید و دور بریزید. گوشت چرخ کرده را به پیاز رنده شده اضافه نمایید.', 'در اولین مرحله از دستور پخت تتالی اراکی باید یکی از پیازها را پوست بگیرید و سپس با رنده ریز رنده کنید. آب اضافی پیاز را بگیرید و دور بریزید. گوشت چرخ کرده را به پیاز رنده شده اضافه نمایید. در این مرحله از دستور پخت تتالی باید مقداری نمک، زردچوبه، پودر سیر، فلفل و نمک به مواد گوشتی اضافه کنید. سپس یک دستکش به دست کنید و شروع به ورز دادن مواد گوشتی کنید. بعد از این که مواد گوشتی را خوب ورز دادید، باید آن را داخل یک کاسه مناسب بریزید. سپس یک پلاستیک روی آن بکشید و ظرف را برای حدود ۳۰ دقیقه در یخچال قرار دهید تا به خوبی منسجم شوند. در این فرصت که مواد گوشتی را در یخچال گذاشته‌اید، می‌توانید به سراغ درست کردن سس ته تالی گوشت اراکی بروید. برای این کار مقداری روغن در ماهیتابه بریزید. سپس پیاز را به صورت نگینی خرد کنید و داخل روغن تفت دهید. مقداری زردچوبه و فلفل سیاه به آن بزنید. بعد از این که پیاز سبک شد، سیر رنده شده و رب گوجه فرنگی را به آن اضافه کنید. رب را در روغن تفت دهید تا رنگ آن تغییر کند. در این مرحله از طرز تهیه کباب تتالی باید مواد گوشتی را از یخچال خارج کنید. سپس می‌توانید به اندازه گردو از مواد گوشتی بردارید و آن را با دست گرد کنید و بعد پهن کنید. این کار را برای همه گوشت‌ها انجام دهید. سپس گوشت‌ها را به سس در حال جوش اضافه کنید. بعد از این که همه گوشت‌ها را داخل سس قرار دادید، در این مرحله از طرز تهیه کباب تتالی باید سیب زمینی‌ها را به صورت حلقه‌ای خرد کنید. بهتر است قطر سیب زمینی‌ها یکسان و حدود ۱ سانتی‌متر باشد. سیب زمینی‌ها را روی گوشت قرار دهید. سپس گوجه فرنگی‌ها را به صورت حلقه برش دهید و روی سیب زمینی‌ها بگذارید. در این مرحله از طرز تهیه تتالی گوشت اراکی باید کمی گرد غوره به مواد اضافه کنید. مقداری آب جوش روی غذا بریزید و صبر کنید تا آب داخل قابلمه شروع به جوشیدن کند. سپس شعله گاز را کم کنید و درب قابلمه را روی آن قرار دهید تا سس غذا غلیظ شود. در انتها می‌توانید این غذای خوشمزه را همراه با نان و سبزی خوردن میل کنید. اگر می‌خواهید کباب تتالی شما کالری کمتری داشته باشد آن را با گوشت مرغ یا بوقلمون تهیه کنید. برای درست کردن کباب ته تالی با مرغ یا بوقلمون باید سینه مرغ میکس شده را با پیاز رنده شده که آب آن گرفته شده ترکیب کنید. سپس ادویه‌ها را به آن اضافه کنید. مواد را خوب ورز دهید و به شکل توپی درست کنید. مقداری روغن زیتون در ماهیتابه بریزید. سپس پیاز حلقه شده و گوجه خرد شده را داخل ماهیتابه بچینید. کمی نمک و آویشن به پیاز و گوجه بزنید. کباب مرغ تتالی را به صورت گرد آماده کنید و روی گوجه‌ها بچینید. سپس نصف لیوان آب جوش به آن اضافه کنید و درب قابلمه را روی آن بگذارید تا غذا به آرامی پخته شود. در انتها رب گوجه فرنگی را همراه با زرشک اضافه نمایید و ۳۰ دقیقه صبر کنید تا غذای شما آماده شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/03/qq-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (153, 'کباب توری با طعم کوبیده', 'در اولین مرحله، ۳۲۰ گرم گوشت راسته گوساله را با ۴۸۰ گرم گوشت قلوه‌گاه گوسفندی بشویید و اجازه دهید آب خود را از دست بدهند. دنبه‌های تازه را هم بشویید تا کاملا پاک شوند. گوشت گوساله، گوسفند و دنبه را در', 'در اولین مرحله، ۳۲۰ گرم گوشت راسته گوساله را با ۴۸۰ گرم گوشت قلوه‌گاه گوسفندی بشویید و اجازه دهید آب خود را از دست بدهند. دنبه‌های تازه را هم بشویید تا کاملا پاک شوند. گوشت گوساله، گوسفند و دنبه را در چرخ گوشت ۱ یا ۲ بار چرخ کنید تا کاملا یکدست شود. گوشت‌ها را در یک کاسه کنار بگذارید. می‌توانید از گوشت چرخ کرده آماده مخصوص کوبیده با نسبت‌های اصولی هم استفاده کنید. به اندازه ۳ پیاز درشت یا ۴ پیاز متوسط را پوست بگیرید و بشویید. پیازها را با رنده متوسط، رنده کنید. سپس با دست پیازهای رنده شده را بفشارید و آب پیازها را در یک کاسه جدا بریزید و کنار بگذارید. پیازها را به مدت ۱۵ دقیقه در یخچال بگذارید. بعد از استراحت کردن پیازها در یخچال، آنها را به گوشت چرخ کرده اضافه کنید. نمک، فلفل سیاه و ۲ قاشق زعفران دم کرده غلیظ را هم به مایه گوشت بیفزایید. سپس شروع به ورز دادن مایه کباب توری کنید. به مدت ۱۵ دقیقه مایه کباب را ورز دهید تا کاملا چسبندگی پیدا کند. سپس روی مایه کباب را با سلفون بپوشانید و حداقل ۲ ساعت در یخچال قرار دهید تا طعم‌ها به خورد هم بروند. برای اینکه کباب به توری نچسبد، قبل از قرار دادن گوشت کوبیده روی توری و شروع کباب کردن، دو طرف توری را با کمی کره ذوب شده یا روغن، چرب کنید. توری را در یخچال بگذارید. یک پلاستیک فریزری را با چاقو یا قیچی پهن کنید و روی سطح صاف بگذارید. ظرف حاوی مایه کباب توری را از یخچال بیرون بیاورید و سلفون روی آن را بردارید. دست‌هایتان را با آب پیاز که کنار گذاشته بودید، خیس کنید. از مایه کباب کوبیده بردارید و روی پلاستیک فریزری بگذارید. شروع به پهن کردن مایه کباب روی پلاستیک کنید. باید مایه کباب را به اندازه ابعاد توری باز کنید. اگر موادتان برای روی یک توری زیاد است، از دو توری استفاده کنید یا ۲ بار این کار را انجام دهید. ضخامت مایه کباب باید به یک سانتی‌متر یا کمی کمتر برسد. بعد از باز کردن مایه کباب و هم سطح شدن همه قسمت‌های آن، مایه کباب را همراه با پلاستیک روی توری (داخل آن) که چرب کرده بودید، بگذارید. توری را ببندید و به مدت ۵ دقیقه در یخچال بگذارید تا خودش را بگیرد. سپس توری را برگردانید و آن را باز کنید. پلاستیک فریزری را از روی مایه کباب بردارید و دوباره توری را ببندید. در این فاصله، منقل و زغال را آماده کنید. حرارت منقل نباید تیز و شدید باشد. در یک کاسه، کره را ذوب کرده و ۲ قاشق از زعفران دم کرده غلیظ را به آن اضافه کنید و هم بزنید. این کاسه را کنار دستتان بگذارید. زغال که آماده شد، توری را روی منقل بگذارید. توری را هر چند ثانیه یک بار زیر و رو کنید و برگردانید تا همه قسمت‌های کباب توری، به صورت یکدست بپزد. در حین پخت کباب توری، از مایه رومال، روی کباب توری با برس آشپزی، بمالید. هر دو طرف کباب را رومال کنید تا نرم و آبدار شود. وقتی هر دو طرف کباب توری طلایی شد و مغز آن پخت، آن را از روی حرارت بردارید. درب توری را باز کنید و کباب توری را به آرامی با چاقو از توری جدا کنید. کباب توری را در ظرف سرو بگذارید و همراه با برنج چلو یا نان، سبزی، گوجه کبابی، سماق و سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/03/kabab-toori.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (154, 'کباب حسینی', 'کباب حسینی یک کباب سنتی ایرانی است و از زمان‌های بسیار قدیم طبخ می‌شده است. کباب حسینی به عنوان یکی از غذاهای ایرانی لذیذ با گوشت به روش‌ها و با گوشت‌های مختلفی پخته می‌شود؛ با گوشت گوسفندی، گوشت گوسا', 'کباب حسینی یک کباب سنتی ایرانی است و از زمان‌های بسیار قدیم طبخ می‌شده است. کباب حسینی به عنوان یکی از غذاهای ایرانی لذیذ با گوشت به روش‌ها و با گوشت‌های مختلفی پخته می‌شود؛ با گوشت گوسفندی، گوشت گوساله، گوشت چرخ کرده و گوشت بوقلمون یا مرغ. کباب حسینی مشهدی و هندی از انواع مشهور کباب حسینی محسوب می‌شوند و این کباب جزو محبوب‌ترین غذاهای مشهدی هم هست. در گذشته برای درست کردن این غذای ایرانی با گوشت چرخ کرده یا سایر انواع گوشت به جای سیخ چوبی، از چوب درخت انار استفاده می‌کردند. کباب حسینی از انواع کباب ایرانی است که می‌توانید به صورت تابه‌ای یا کباب شده روی منقل تهیه کنید. در ادامه مراحل طرز تهیه کباب حسینی را به ترتیب دنبال کنید. برای تهیه کباب حسینی، ابتدا مواد کباب را آماده کنید. پیازها را پوست بگیرید. یکی از آنها را به صورت نگینی ریز خرد کرده و یکی را رنده کنید. فلفل دلمه‌ای را بشویید و هسته‌های وسط آن را بیرون بیاورید. فلفل‌ دلمه را به قطعات درشت (مکعب‌های ۲ تا ۳ سانتی‌متری) خرد کنید. گوجه فرنگی را بشویید و ۴ قسمت کنید. دنبه گوسفندی را بشویید و مکعبی خرد کنید. گوشت راسته گوسفندی را هم به قطعات با اندازه مکعب‌های ۲ سانتی‌متری برش دهید. همه این مواد را کنار بگذارید. سیخ‌های چوبی را هم به مدت ۳۰ دقیقه در آب بخیسانید. سیرها را پوست بگیرید و رنده یا له کنید. رزماری‌ها را بشویید. زعفران را هم دم کنید. تکه‌های گوشت را در یک ظرف بریزید. سیرهای له شده، زعفران دم کرده، یک سیر رنده شده (با آب پیاز)، فلفل سیاه، نمک (به مقدار کم) و شاخه‌های رزماری را به گوشت‌ها اضافه کرده و مواد را با هم مخلوط کنید تا روی آنها را بپوشاند. روی ظرف را بپوشانید و به مدت حداقل ۱ ساعت، در یخچال استراحت دهید تا گوشت‌ها طعم‌دار شوند. بعد از استراحت کردن گوشت، ظرف را بیرون بیاورید. گوشت‌ها را از مواد مرینیت جدا کنید. به ترتیب یک تکه گوشت، یک تکه دنبه، یک فلفل دلمه‌ای و یک تکه گوجه فرنگی را به سیخ‌های کوچک بزنید تا یک سیخ کامل شود. (طول سیخ ها باید ۱۰ تا ۱۵ سانتی‌متر باشد). همه سیخ‌ها را به همین شکل آماده کنید و کنار بگذارید. سپس، در یک تابه، کره را بریزید و روی حرارت قرار دهید تا ذوب شود. بعد پیاز خرد شده را به آن اضافه کنید. بعد از سرخ شدن پیازها، زردچوبه را اضافه کنید و تفت دهید تا پیازها طلایی و خوشرنگ شوند. ۱ قاشق غذاخوری رب گوجه فرنگی را هم در تابه بریزید و تفت دهید تا رنگ باز کند و از خامی بیفتد. سپس گوجه‌ گیلاسی را هم به صورت سالم و درسته در تابه قرار دهید. در این مرحله، سیخ‌های کباب آماده شده را در تابه بچینید و روی آن را به اندازه ۲ لیوان آب جوش بیفزایید. درب تابه را بگذارید و اجازه دهید بجوشد. حرارت را روی حالت ملایم تنظیم کنید تا آب داخل تابه، کشیده شده و گوشت‌ها کاملا بپزند. اگر گوشت کاملا پخته نشده، کمی آب به ظرف اضافه کنید. در پایان پخت، کمی نمک به کباب اضافه کنید. کباب ‌حسینی با گوشت گوسفندی در تابه را با برنج زعفرانی یا نان و سبزی، سرو کنید. در این روش، به خاطر استفاده از گوشت گوساله، به زمان بیشتری برای پخت نیاز خواهید داشت اما روش مرینیت یا مزه‌دار کردن گوشت مانند روش قبل است. در ضمن بهتر است گوشت را بیشتر استراحت دهید تا نرم‌تر شود. روش تهیه کباب حسینی با گوشت گوساله مرحله به مرحله را دنبال کنید. ابتدا گوشت را به شکل طولی با ضخامت حدود ۱.۵ تا ۲ سانتی‌متر برش دهید و حداقل ۳ ساعت، با یک عدد پیاز پوست گرفته و خرد شده، زعفران دم کرده، آبلیمو، نمک و فلفل سیاه (یا دیگر چاشنی‌های دلخواه) استراحت دهید. یکی از پیازها را پوست بگیرید و به صورت حلقه‌ای خرد کنید. گوجه فرنگی‌ها را هم بشویید و حلقه‌ای خرد کنید. یک پیاز دیگر را هم پوست بگیرید و به صورت درشت خرد کنید. فلفل دلمه‌ای را هم شسته و درشت خرد کنید و کنار بگذارید. بعد از استراحت کردن گوشت با مواد مرینیت، گوشت‌ها را به سیخ بزنید و کنار بگذارید. به این ترتیب که یک تکه گوشت، یک تکه پیاز و یک تکه فلفل دلمه‌ای سر سیخ‌ها بزنید. در یک تابه، مقداری روغن بریزید. ابتدا یک لایه پیاز حلقه‌حلقه شده و روی آن را یک لایه گوجه فرنگی حلقه‌حلقه‌شده قرار دهید. تابه را روی حرارت بگذارید. کمی نمک و فلفل سیاه و زردچوبه به گوجه‌ها بزنید و سپس سیخ‌های کباب را روی گوجه‌ها بگذارید. در آخر، یک لیوان آب جوش را با ۲ قاشق غذاخوری رب گوجه فرنگی، مخلوط کنید و به تابه بیفزایید. به مدت ۱ ساعت تابه را روی حرارت ملایم باشد تا کباب بخارپز شود. در پایان پخت، زعفران دم کرده را روی گوشت بریزید و اجازه دهید بپزد. بعد از کشیده شدن آب و پختن مواد، آن را با برنج چلو، برنج زعفرانی یا نان سرو کنید. در این روش، به جای گوشت قرمز، باید گوشت سینه مرغ یا بوقلمون را مزه‌دار کنید و استراحت دهید. سپس آنها را بپزید. در ادامه مراحل تهیه کباب حسینی به این روش را دنبال کنید. ابتدا سینه مرغ یا بوقلمون را بشویید و در اندازه جوجه‌ کبابی، خرد کنید. سینه بوقلمون یا مرغ‌ خرد شده را با پیاز خرد شده، نمک، زعفران دم کرده، آبلیمو، فلفل سیاه، زردچوبه و ۱ عدد فلفل دلمه‌ای خرد شده به مدت ۳ الی ۴ ساعت مزه‌دار می‌کنیم و کنار بگذارید تا نرم شود. بعد از مزه‌دار شدن تکه‌های مرغ‌ یا بوقلمون، آنها را سر سیخ بزنید. یک تکه مرغ یا بوقلمون، یک تکه فلفل دلمه‌ای خرد شده را به سیخ بزنید. مواد را به همین ترتیب سر سیخ بزنید تا تمام شوند. سپس یک پیاز متوسط ​​را پوست بگیرید و رنده کنید. در یک تابه مقداری روغن بریزید و روی حرارت بگذارید کنید تا پیازها طلایی شوند. سپس ۱ قاشق رب گوجه فرنگی را به آن اضافه کنید (البته استفاده از رب گوجه فرنگی اختیاری است). هویج‌ها را هم حلقه‌ای خرد کنید و در تابه بریزید. سپس نمک و زردچوبه را به آن اضافه کنید. در این مرحله، سیخ‌ها را داخل تابه قرار دهید. سپس نصف لیوان زعفران دم کرده را اضافه کنید و تابه را روی حرارت ملایم قرار دهید تا خوب بپزد. پس از گذشت نیم ساعت، چند گوجه‌ گیلاسی را به سیخ‌ها اضافه کنید و اجازه دهید تا گوجه‌ها هم بپزند. در آخر، مقداری فلفل سیاه به دلخواه روی جوجه‌ها بریزید. کباب حسینی مرغ یا بوقلمون را مثل کوبیده مرغ یا جوجه کباب زعفرانی با برنج یا نان سرو کنید. در طرز تهیه کباب حسینی با گوشت شترمرغ، به جای گوشت گوسفندی، مرغ، بوقلمون و گوساله، از گوشت شترمرغ استفاده کنید و بقیه مراحل را هر کدام از روش‌های بالا دنبال کنید و کباب را بپزید. در طرز تهیه کباب حسینی با رب انار، به جای سس گوجه فرنگی، از رب انار یا سس انار استفاده کنید و بقیه مراحل را به هر کدام از روش‌های بالا دنبال کنید. در این روش، در کنار رب انار می‌توانید کمی رب گوجه فرنگی هم به سس کباب حسینی اضافه کنید. طرز تهیه کباب حسینی با گوشت چرخ‌کرده، کاملا مشابه طرز تهیه کباب تابه‌ای است. یعنی باید گوشت چرخ‌کرده را با پیاز رنده و آب گرفته شده، زردچوبه، نمک و فلفل سیاه مزه‌دار کنید. بعد از استراحت دادن مایه کباب حسینی، کباب‌ها را باید به سیخ چوبی بزنید. سپس سیخ‌های کباب‌ را در سس گوجه و پیاز، فلفل دلمه‌ای و ادویه‌های دلخواه، بپزید.', 13, 27, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/10/5f981f414a2ce-3.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (155, 'کباب ساتای مرغ', 'مرغ‌ها را به صورت نواری برش دهید و با بیفتک‌کوب کمی بکوبید. اگر لایه‌های آن را نازک برش بزنید، می‌توانید از کوبیدن آن صرف نظر کنید.', 'مرغ‌ها را به صورت نواری برش دهید و با بیفتک‌کوب کمی بکوبید. اگر لایه‌های آن را نازک برش بزنید، می‌توانید از کوبیدن آن صرف نظر کنید. سیر را پوست بگیرید و رنده یا له کنید. زنجبیل را هم پوست بگیرید و رنده کنید. می‌توانید به جای زنجبیل تازه و سیر تازه از پودر آنها استفاده کنید. سیخ‌های چوبی را در آب بخیسانید تا موقع کباب کردن نسوزند. آب نصف یک عدد لیموترش را بگیرید و پوست آن را رنده کنید. در یک تابه، زنجبیل، سیر، پوست لیموی رنده شده، آب لیمو، عسل، سس سویا، ادویه کاری، روغن زیتون و کره بادام زمینی را بریزید و هم بزنید تا کاملا با هم مخلوط شوند و حالت خمیری بگیرند. به اندازه ۱/۳ از مواد مرینیت را از تابه بردارید و در یک پاکت فریزری یا زیپ کیپ بریزید. مرغ‌ها را در کیسه بگذارید و مواد مرینیت و مرغ را با هم ماساژ دهید تا مایه مرینیت به همه جای مرغ برسد. مرغ‌ها را در یخچال قرار دهید و اجازه دهید به مدت ۳ ساعت استراحت کنند تا طعم بگیرند. برای تهیه سس ساتای مرغ، شیر نارگیل را به تابه‌ای که مایه مرینیت را در آن ریخته بودید، اضافه کنید. تابه را روی حرارت متوسط قرار دهید تا شیر شروع به جوشیدن کند و غلیظ شود. غلظت سس بادام زمینی ساتای باید به اندازه سس کچاپ شود. سپس شعله گاز را خاموش کنید و تابه را کنار بگذارید. بعد از اینکه مرغ‌ها طعم گرفتند، از یخچال بیرون بیاورید و آنها را به سیخ بزنید. برای کباب کردن روی زغال: سیخ‌های کباب را روی زغال گداخته بگذارید و هر دو طرف مرغ‌ها را کباب کنید. برای تهیه در تابه: در یک تابه کمی روغن بریزید و سیخ‌ها را در تابه قرار دهید. حرارت را روی حالت ملایم رو به متوسط تنظیم کنید. وقتی یک طرف مرغ‌ها سرخ شد، طرف دیگر آن را سرخ کنید. برای تهیه در فر: فر را از ۳۰ دقیقه قبل روشن و روی دمای ۱۶۰ درجه سانتی گراد تنظیم کنید. سینی فر را چرب کنید و کباب‌ها را در آن بچینید. به مدت حدود ۲۰ دقیقه صبر کنید تا روی کباب‌ها طلایی شود. وقتی کباب‌ها پختند و مغزشان نرم شد، آنها را از فر بیرون بیاورید. کباب ساتای مرغ را با سس مخصوص بادام زمینی سرو و نوش جان کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/08/23COOKING-CHICKEN-PEANUT-SAUCE2-articleLarge.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (156, 'کباب سرداشی همدان', 'در اولین مرحله از طرز تهیه کباب سرداشی همدان باید ۱ عدد سیب زمینی و پیاز را پوست بگیرید و خوب بشویید. سپس پیاز و سیب زمینی را رنده کنید. آب اضافی سیب زمینی و پیاز را که ته ظرف باقی مانده دور بریزید. ب', 'در اولین مرحله از طرز تهیه کباب سرداشی همدان باید ۱ عدد سیب زمینی و پیاز را پوست بگیرید و خوب بشویید. سپس پیاز و سیب زمینی را رنده کنید. آب اضافی سیب زمینی و پیاز را که ته ظرف باقی مانده دور بریزید. برای ریز کردن پیاز و سیب زمینی می‌توانید از مولینکس نیز استفاده کنید. در این مرحله از طرز تهیه کباب سرداشی خوشمزه به روش رستورانی باید گوشت چرخ کرده را با سیب زمینی و پیاز رنده شده ترکیب کنید. سپس مقداری نمک و فلفل سیاه و در صورت تمایل ادویه به گوشت اضافه کنید. حالا یک دستکش به دست کنید و آن قدر گوشت را ورز دهید تا حالت چسبندگی گوشت برطرف شود. بعد از آماده شدن مایه کباب سرداشی باید سینی فر یا کف قالب مورد نظرتان را با مقداری روغن چرب کنید. سپس مایه کباب را که آماده کرده‌اید روی قالب یا سینی فر پخش کنید. اگر چربی گوشت شما کم است، در این مرحله می‌توانید مقداری روغن به مواد گوشتی اضافه کنید. سعی کنید گوشت را در تمام ظرف با قطر یکسان پهن کنید تا به صورت یکنواخت پخته شود. بعد از ریختن مایه کباب در قالب، در این مرحله از طرز تهیه کباب سرداشی همدانی خوشمزه و مجلسی باید یک عدد سیب زمینی و یک گوجه فرنگی ورق شده را روی گوشت‌ها قرار دهید. در این مرحله می‌توانید کمی فلفل تند یا فلفل دلمه‌ای خرد شده را هم روی گوشت بریزید. روی ظرف کباب را با فویل بپوشانید و چند سوراخ در آن ایجاد کنید. سپس فر را با دمای ۱۴۰ درجه سانتی‌گراد روشن کنید و ظرف کباب را داخل فر قرار دهید. اجازه دهید کباب سرداشی شما برای ۱ تا ۲ ساعت داخل فر بماند و به آرامی بپزد. در نهایت می‌توانید کباب را از فر خارج کنید و با برنج یا نان و در کنار سبزی خوردن تازه و ماست سرو کنید. اگر دوست دارید کباب سرداشی را بدون فر و داخل ماهیتابه آماده کنید نیز مراحل پخت غذا بسیار راحت است. ابتدا باید مایه کباب سرداشی را به روشی که پیش‌تر ذکر شد، آماده کنید و خوب ورز دهید. سپس داخل ماهیتابه دو لایه ورق آلومینیوم قرار دهید. روی آلومینیوم را با مقداری روغن چرب کنید. سپس مواد کباب را داخل ماهیتابه بریزید و خوب در سراسر ظرف پهن کنید. قطر گوشت باید در تمام بخش‌های ماهیتابه شما یکسان باشد. سیب زمینی و گوجه ورقه شده را روی کباب قرار دهید. یک شعله پخش کن روی گاز قرار دهید و شعله گاز را کم کنید. سپس درب ماهیتابه را روی آن قرار دهید و آن را برای مدت ۱ ساعت روی گاز قرار دهید تا کباب به آرامی پخته شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/03/sardashi.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (157, 'کباب قفقازی', 'ابتدا سینه مرغ و گوشت گوساله را بشویید و اجازه دهید آب اضافی خود را از دست بدهند. روی تخته آشپزخانه، گوشت‌ها را به قطعات مستطیلی ۲ در ۴ سانتی‌متری خرد کنید. دقت کنید که اندازه تکه‌ها باید یکسان باشد و', 'ابتدا سینه مرغ و گوشت گوساله را بشویید و اجازه دهید آب اضافی خود را از دست بدهند. روی تخته آشپزخانه، گوشت‌ها را به قطعات مستطیلی ۲ در ۴ سانتی‌متری خرد کنید. دقت کنید که اندازه تکه‌ها باید یکسان باشد و جوجه‌ها کوچک‌تر یا نازک‌تر نباشند. گوشت‌ها را در یک ظرف مناسب بریزید. در ادامه، پوست پیازها را بگیرید و به صورت خلالی نازک خرد کنید. پیازهای خرد شده را به گوشت‌ گوساله و مرغ اضافه کنید. روغن زیتون (یا روغن مایع)، نمک، فلفل سیاه و ماست چکیده را اضافه کنید و همه مواد را با هم مخلوط کنید، طوری که مواد مرینیت کاملا روی گوشت‌ها را بپوشاند. بعد از مرینیت کردن گوشت‌ها، روی آنها را با سلفون بپوشانید و در یخچال بگذارید. گوشت‌ها باید به مدت حداقل ۴ ساعت تا ۲۴ ساعت در مواد بمانند تا نرم و طعم‌دار شوند. قبل از شروع به کباب کردن آنها، فلفل دلمه‌ای‌ها را بشویید و با سایز درشت، هم‌اندازه تکه‌های گوشت و مرغ، خرد کنید. سپس منقل و زغال را آماده کنید. تکه‌های مزه‌دار شده مرغ و گوشت را از مواد مرینیت بیرون بیاورید. پیازها را جدا کنید. یک تکه گوشت گوساله، یک تکه فلفل و یک تکه فیله مرغ را به سیخ بزنید. این ترتیب را تکرار کنید تا سیخ‌ها را کامل کنید. سپس سیخ‌ها را روی منقل بگذارید. یک طرف کباب قفقازی که پخت و برشته شد، آن را برگردانید و سمت دیگر را کباب کنید. در آخر، وقتی نزدیک به برداشتن کباب‌ها از روی حرارت زغال شد، روی تکه‌های گوشت و مرغ، با برس آشپزی، زعفران دم کرده و کره بمالید. دوباره کمی دیگر کباب‌ها را حرارت دهید. سپس کباب‌ها را از روی زغال بردارید و آنها را با برنج زعفرانی، برنج چلو یا نان و سبزی خوردن و گوجه کبابی، سرو کنید. برای تهیه کباب قفقازی رولی: در یک تابه مقداری کره بریزید و قارچ‌های خرد شده را به همراه آبلیمو در تابه بریزید و تفت دهید تا قارچ‌ها آبشان کشیده شود. گوشت چرخ کرده را هم در یک کاسه بریزید. یک پیاز را رنده و آبگیری کنید. تفاله پیاز را به همراه نمک و فلفل سیاه به گوشت چرخ کرده اضافه کنید و ورز دهید. سینه‌های مرغ را از پهنا با چاقوی تیز برش دهید. ضخامت سینه‌های مرغ باید نیم تا یک سانتی‌متر باشد. سپس با بیفتک‌کوب، آنها را بکوبید در حدی که پاره و له نشوند. بعد داخل آن‌ها را مقداری گوشت چرخ کرده مزه‌دار شده، کمی جعفری و قارچ تفت داده شده بریزید و از یک طرف آنها را بپیچید. همه سینه‌های مرغ را به همین شکل رول کنید. در آخر، کباب قفقازی‌ها را به سیخ بزنید و روی زغال منقل یا روی کباب‌پز که آماده کرده‌اید، بگذارید. روی کباب کمی زعفران دم‌کرده بمالید. یک طرف کباب‌ها که طلایی شد، آن را برگردانید تا سمت دیگر آن هم طلایی شود و بپزد. سپس کباب قفقازی را با هر مخلفاتی که دوست دارید سرو کنید. برای درست کردن کباب قفقازی با گوشت چرخ کرده رولی یا ساده در تابه، کافی است به هر یک از روش‌های بالا، کباب قفقازی را مزه‌دار و به سیخ بزنید. در یک تابه مقداری روغن زیتون یا روغن مایع بریزید. وقتی روغن داغ شد، سیخ‌های کباب را کف ماهیتابه بچینید. هر طرف کباب‌ها را به مدت ۱۵ دقیقه بپزید تا مغزپخت و طلایی شوند. سپس آنها را سرو کنید. برای پخت کباب قفقازی در فر، فر را از ۲۰ دقیقه قبل از پخت کباب‌ها، روی دمای ۱۸۰ درجه تنظیم کنید تا گرم شود. سیخ‌ها را به روش دلخواه، آماده کنید. ترجیحا از سیخ چوبی استفاده کنید. سیخ‌ها را در سینی فر چرب شده بچینید و در فر به مدت ۲۰ دقیقه قرار دهید. در ۵ دقیقه آخر، روی کباب‌ها کره و زعفران دم کرده بمالید. گریل را روشن کنید. وقتی روی کباب‌ها طلایی شدند، آنها را از فر بیرون بیاورید.', 11, 24, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/03/188115_867.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (158, 'کباب قلقلی', 'در ابتدا برای تهیه قلقلی کباب، پیازها را پوست بگیرید و قسمت انتهایی یعنی ریشه را جدا نکنید تا در زمان رنده کردن، لایه‌های پیاز از هم باز نشوند. با رنده متوسط، پیازها را رنده کنید و در یک صافی بریزید ت', 'در ابتدا برای تهیه قلقلی کباب، پیازها را پوست بگیرید و قسمت انتهایی یعنی ریشه را جدا نکنید تا در زمان رنده کردن، لایه‌های پیاز از هم باز نشوند. با رنده متوسط، پیازها را رنده کنید و در یک صافی بریزید تا آب اضافی آنها خارج شود یا با دست آنها را بفشارید و تفاله پیاز را در یک کاسه بریزید. گوشت چرخ کرده را به پیازها اضافه کنید. سپس زردچوبه، فلفل سیاه، پودر سیر، پودر پاپریکا و نمک را اضافه کنید. سبزیجات معطر خشک مثل ترخون، ریحان، نعناع و مرزه را اضافه کنید و با دست مواد را ورز دهید تا بافت منسجمی بگیرند. اگر موادتان شل بود، می‌توانید کمی آرد نخودچی اضافه کنید و دوباره ورز دهید تا به انسجام کافی برسد. بعد از آماده شدن مایه کباب قلقلی، روی ظرف را بپوشانید و ۱۵ تا ۲۰ دقیقه در یخچال بگذارید تا هم طعم‌ها به خورد هم بروند و هم منسجم‌تر شوند. سیخ‌های چوبی را هم در یک ظرف آب بگذارید تا کاملا خیس شوند و در زمان پختن کباب، نسوزند. سیخ‌ها هم باید ۲۰ تا ۳۰ دقیقه خیسانده شوند. سپس فلفل دلمه‌ای‌ها را بشویید و دانه‌های آنها را بیرون بیاورید. سیب زمینی‌ها را بشویید و پوست آنها را بگیرید. فلفل دلمه‌ای‌ها را به صورت مکعب‌ها ۳ سانتی‌متری یا خلالی درشت، خرد کنید. سیب زمینی‌ها را هم نگینی درشت (ضخیم نباشند) خرد کنید. گوجه فرنگی‌ها را هم بشویید و به صورت حلقه‌ای یا مکعبی خرد کنید. در ادامه، فر را روشن و روی دمای ۱۸۰ درجه سانتی‌گراد تنظیم کنید تا گرم شود. بعد از استراحت کردن مایه کباب قلقلی، آن را از یخچال بیرون بیاورید و به اندازه یک گردو از مواد بردارید. گوشت چرخ کرده را بین دست‌هایتان گلوله کنید و در یک سینی کنار بگذارید. همه مایه کباب قلقلی را به همین شکل آماده کنید. سیخ‌ها را از آب بیرون بیاورید و سر هر سیخ، یک گوشت قلقلی شده، یک فلفل دلمه، یک سیب زمینی و یک گوجه، دوباره یک گوشت قلقلی، یک سیب زمینی و گوجه بزنید. این ترتیب را ادامه دهید تا سیخ‌ها پر شوند. همه سیخ‌ها را با مواد پر کنید. در این مرحله از روش درست کردن قلقلی کباب، کف یک سینی را با کمی روغن چرب کنید. سیخ‌ها را کف سینی بچینید، طوری که روی هم قرار نگیرند. سپس در یک کاسه، آب جوش، ۲ قاشق روغن مایع، رب گوجه فرنگی و ادویه‌های دلخواه را با هم ترکیب کنید تا یک سس گوجه روان داشته باشید. این سس را کف سینی بریزید. در آخرین مرحله از طرز تهیه کباب قلقلی با سیخ چوبی، سینی حاوی سیخ‌های کباب را در فر بگذارید و درب فر را ببندید. حدود ۴۰ دقیقه صبر کنید تا کباب‌ها بپزند و مغزپخت شوند. سپس سینی کباب را بیرون بیاورید و کباب قلقلی را با برنج آبکش یا برنج کته یا نان، سبزی خوردن، ترشی و سالاد دلخواه میل کنید. اگر به فر، مایکروفر یا توستر دسترسی ندارید، می‌توانید کباب قلقلی را در تابه هم درست کنید. در این صورت، سیخ‌های کباب قلقلی را به روش بالا درست کنید و مواد را سر سیخ بزنید. سپس سیخ‌ها را در یک تابه بچینید و سس رب گوجه فرنگی را اضافه کنید. تابه را روی حرارت ملایم قرار دهید تا به مدت ۱ ساعت بپزند. در این مدت مراقب باشید که سس کباب قلقلی، خشک نشود. وقتی کباب‌ها پختند، آنها را با برنج یا نان سرو کنید. اگر می‌خواهید طعم کباب قلقلی زغالی را بچشید، کافی است بعد از اینکه گوشت قلقلی شده و سبزیجات را سر سیخ زدید، آنها را مثل درست کردن کباب کوبیده روی زغال گداخته و یکدست کباب کنید تا همه قسمت‌های آنها طلایی شود و مغز آنها بپزد. سپس کباب قلقلی را با برنج یا نان و سبزی خوردن و پیاز خام، سرو کنید.', 18, 37, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (159, 'کباب کاردی', 'برای درست کردن کباب کاردی، گوشت چرخ کرده، پیاز رنده و آب گرفته شده، سیر رنده شده و ادویه‌های معطر را با هم ورز دهید تا یکدست شود.', 'برای درست کردن کباب کاردی، گوشت چرخ کرده، پیاز رنده و آب گرفته شده، سیر رنده شده و ادویه‌های معطر را با هم ورز دهید تا یکدست شود. پیازها را پوست بگیرید و قسمت انتهای آنها را جدا نکنید. پیازها را با رنده متوسط، رنده کنید. پیازها را با دست بفشارید تا آب آنها گرفته شود. آب پیازها را دور نریزید و کنار دستتان بگذارید. سیرها را هم با سیر له کن یا رنده ریز، رنده کنید. به کاسه‌ای که گوشت چرخ کرده و ادویه‌ها را اضافه کرده‌اید، پیاز رنده و آب گرفته شده و سیر رنده شده را اضافه کنید. همه مواد را ورز دهید تا کاملا با هم یکدست شوند. مایه گوشت چرخ کرده را روی تخته آشپزی بگذارید و با چاقوی ساطوری، روی آنها ضربه بزنید تا منسجم‌تر شود. یک سیخ (ترجیحا آلومینیومی) را در یک دست بگیرید و به اندازه یک مشت از مایه کباب کاردی را از لبه سیخ، به سیخ بزنید. طوری که سیخ را در مایه کباب کاردی، فرو کنید. با دست مایه کباب کاردی را روی سیخ پهن کنید و لبه‌های سیخ را هم با مایه کباب، بپوشانید. باید روی حدود ۲/۳ سیخ، یک لایه گوشت چرخ کرده پوشانده شود. در زمان سیخ زدن کباب‌ها، اگر حس کردید، مایه کباب به دستتان می‌چسبد، با آب پیاز یا آب معمولی، دستتان را خیس کنید و دوباره سیخ بگیرید. همه مایه کباب را به همین صورت، به سیخ بزنید تا مایه کباب شما تمام شود و سیخ‌ها آماده شوند. با لبه یک سیخ دیگر یا لبه یک کارد، به صورت موربی، از بالا به پایین، روی مایه کباب، خط بیندازید. دوباره به صورت پایین به بالا، خط‌هایی را در خلاف جهت خط‌های قبل، روی کباب ایجاد کنید. روی کباب باید لوزی‌های کوچک تشکیل شود. زیر و روی همه کباب‌ها را به همین شکل خط بیندازید. قبل از آماده کردن مایه رومال، زغال‌ها را گداخته کنید و در منقل بگذارید. در یک کاسه، روغن حیوانی را ذوب کنید و بریزید. سپس به آن، پودر زعفران (یا کمی زعفران دم‌کرده)، پودر شوید، پودر فلفل سیاه و کمی نمک اضافه کنید. ظرف را تکان دهید یا با برس آشپزی، مواد را هم بزنید تا یکدست شوند. این مایه رومال را کنار بگذارید. وقتی زغال‌ها گداخته شدند، سیخ‌ها را به ترتیب روی منقل بچینید. بعد از یک دقیقه، سیخ‌ها را برگردانید. قسمت لبه‌های سیخ را هم روی حرارت بگیرید تا گوشت‌ها به سیخ بچسبند. یک طرف کباب‌ها که طلایی شد، سمت دیگر را هم کباب کنید. روی سیخ‌ها را از مایه رومال بمالید و برای چند ثانیه دیگر حرارت دهید. کباب‌ها که آماده شدند، آنها را از روی زغال بردارید. کباب کاردی را با نان یا برنج آبکش یا برنج کته، پیاز، گوجه کبابی و سبزی خوردن می‌توانید میل کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (160, 'کباب کاسه ای اردبیل', 'کباب کاسه ای که به نام کاسه کباب هم آن را می شناسند، ترکیبی از گوشت، جگر، کره، گوجه و کنجد است که با نان یا برنج سرو می شود. در این کباب، جوجه، جگر، چنجه و کوبیده با هم مخلوط می شوند. البته در برخی دس', 'کباب کاسه ای که به نام کاسه کباب هم آن را می شناسند، ترکیبی از گوشت، جگر، کره، گوجه و کنجد است که با نان یا برنج سرو می شود. در این کباب، جوجه، جگر، چنجه و کوبیده با هم مخلوط می شوند. البته در برخی دستورهای طرز تهیه کباب کاسه ای، کوبیده و دل و جگر را با کره و گوجه مخلوط می کنند و جوجه و چنجه از طرز تهیه کاسه کباب حذف می شود. در اولین مرحله از طرز تهیه کباب کاسه ای، چنجه ها را آماده کنید. برای زیبایی یا بهتر بگوییم، جلوه بهتر و بیشتر کباب چنجه، باید گوشت را به قطعات مساوی برش بزنید. برش های طولی و عرضی که به گوشت می دهید، باید به قدری باشد که نه زیادی بزرگ و نه زیادی کوچک باشد. تکه های چنجه را در یک ظرف یا یک پلاستیک زیپ دار بریزید. یک پیاز را خرد کنید. زعفران را دم کنید. می توانید زعفران را در یک لیوان بریزید و یک قالب کوچک یخ را روی آن بیندازید. اجازه دهید یخ ذوب شود و زعفران رنگ باز کند. این بهترین روش آماده سازی زعفران است. یا اینکه زعفران را در یک استکان یا لیوان بریزید و روی آن را آب جوشیده بریزید. دوباره لیوان را روی بخار آب قرار دهید، در حالی که روی استکان را پوشانده اید. با این کار فرصت دهید که زعفران دم بکشد. به چنچه، نمک، فلفل، آبلیمو، پیاز خرد شده و زعفران دم کشیده را اضافه کنید. در ظرف یا پلاستیک زیپ دار را ببندید و مواد را در یخچال قرار دهید. بهتر است مواد چنجه یک شب تا صبح در یخچال بمانند ولی اگر فرصت کافی نداشتید و از قبل یادتان رفت که آن ها را در یخچال بگذارید، ۲ تا ۳ ساعت زمان هم کافی است. ولی هر چه زمان بیشتری را به استراحت دادن گوشت و مواد اختصاص دهید، بهتر است. در این صورت هم مواد به خورد گوشت می رود و هم بافت گوشت نرم تر می شود. در این مرحله جوجه ها را برش بزنید. همان طور که گفتیم، بهتر است اندازه قطعات جوجه با قطعات گوشت مورد استفاده در چنجه، برابر باشد. همان موادی را که برای آماده کردن چنجه اضافه کرده بودید، به جوجه بیفزایید. پیاز، زعفران دم کرده و آبلیمو را به همراه قطعات جوجه در ظرف بریزید و به آن فلفل دلمه ای خرد شده و در صورت دلخواه یک قاشق غذا خوری مایونز یا ماست اضافه کنید. در ظرف را ببندید و مواد را حداقل به مدت ۳ ساعت در یخچال بگذارید. برای جوجه ها هم بهتر است مواد را از شب قبل آماده کنید. برای این مرحله باید گوشت کوبیده را به درستی انتخاب کنید. حتما می دانید که گوشت مناسب برای تهیه کباب کوبیده مخلوط گوشت گوساله و گوسفند است. بهترین جای گوشت برای کوبیده هم، گوشت قلوه گاه (بین دنده)، سردست یا گردن گوسفند به همراه راسته گوساله است. معمولا اگر میزان قلوه گاه در گوشت کوبیده، بیشتر باشد، بهتر است. از آن جایی که بافت جگر و قلوه نرم است، بهتر است در بریدن و اندازه زدن آن دقت کنید تا هنگام به سیخ زدن، پاره نشوند. اندازه ای که برای بریدن جگر، قلوه ها و دل انتخاب می کنید، باید به اندازه جوجه و چنجه ای باشد که از قبل آماده کرده اید. روی دل و جگر و قلوه را به اندازه کافی نمک و کمی فلفل بپاشید. در همین مرحله، گوجه ها را از وسط نصف کنید و به آن ها نمک بزنید. مواد جوجه و چنجه را از یخچال خارج کنید. آنها را به سیخ بزنید. سیخ هایی که برای این دو انتخاب می کنید، نباید خیلی نازک یا خیلی ضخیم باشند. منقل را آماده کنید. زغال های گداخته را به صورت یکنواخت کف منقل بریزید و باد بزنید تا حرارتشان یک دست شود. چنجه ها را اول روی منقل بگذارید. سپس نوبت به جوجه های به سیخ کشیده شده می رسد. بعد کوبیده را روی حرارت آتش قرار دهید. و بعد نوبت دل و جگر و قلوه است. گوجه فرنگی های نصف شده را به سیخ بزنید و روی آن ها را به اندازه کافی نمک بپاشید. سیخ های گوجه فرنگی را روی حرارت قرار دهید تا کباب شوند. فلفل های سبز را هم به سیخ بزنید و همزمان با گوجه فرنگی ها کباب کنید. همه مواد را که آماده کردید و روی منقل کباب شدند، در کاسه سرو بریزید. چنجه، جوجه، دل، جگر، قلوه و کوبیده را به همراه گوجه های کباب شده. بعد از ریختن همه مواد در کاسه، کره را به کاسه اضافه کنید. بگذارید کره با دمای بالای گوجه ذوب شود و یک سس خوشمزه به وجود آید. روی کباب ها را مقداری کنجد بپاشید و با ریحان تزیین کنید. کباب کاسه ای را با نان و سبزی خوردن، یا ماست سرو کنید. روش دوم: کباب کاسه ای در فر پیش از هر چیز، فر را روشن و روی دمای ۲۰۰ درجه سانتی گراد، تنظیم کنید تا گرم شود. سیب زمینی ها را در یک ظرف، آب پز کنید. در یک ظرف مناسب، پیاز را رنده کنید و آب آن را با دست بگیرید. پیاز را خوب فشار دهید و تفاله خشک را جدا کنید. گوشت چرخ کرده را در یک ظرف مناسب بریزید و به همراه آرد، نمک، فلفل و پیاز آب گرفته شده، مخلوط کنید و به خوبی ورز دهید. این مایه کباب کاسه ای را، به مدت ۳۰ دقیقه تا یک ساعت در یخچال استراحت دهید. در این مدت، در ظرف را بپوشانید. یک ظرف پیرکس انتخاب کنید و گوشت را کف آن پخش کنید. طوری که دیواره ها و کف، با گوشت چرخ کرده پوشیده شوند. روی گوشت را کره ذوب شده یا روغن بریزید. ظرف را به فر منتقل کنید. ۱۵ تا ۲۰ دقیقه زمان برای پخت گوشت کافی است. برنج را از قبل بشویید و خیس کنید. یک قابلمه را روی حرارت قرار دهید و برنج را بعد از شستن، به قابلمه اضافه کنید. روی برنج آب بریزید تا حدی که به اندازه یک بند انگشت روی آن، آب باشد. حرارت را روی متوسط قرار دهید. کمی کره و نمک به برنج اضافه کنید و اجازه دهید آب، بخار و برنج هم نرم‌ تر شد. بعد از این که آب داخل قابلمه بخار شد، برنج را در کف قابلمه جمع کنید و در ظرف را بگذارید. بعد از ۲ یا ۳ دقیقه، حرارت را کم کنید و اجازه دهید برنج بپزد. گوجه ها را در یک تابه با کمی روغن یا کره، سرخ و به آن کمی نمک و فلفل اضافه کنید. سیب زمینی آبپز شده را از آب خارج کنید و پوست آن ها را بگیرید. بعد از آماده شدن کباب، آن را از فر بیرون بیاورید. کباب را از ظرف خارج کنید و در ظرفی قرار هید که می خواهید آن را سرو کنید. برنج کته شده را با کمی کره آغشته کنید و آن را در میان گوشتی که از فر بیرون آورده اید، بریزید. سیب زمینی آب پز و گوجه فرنگی سرخ شده را کنار برنج بگذارید و با هم سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/03/525627299_zFagIt8wEpKuMAX_CQumSGOhSDYGEOMKG1nwXaDjepg-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (161, 'کنجه کباب لاری', 'در مرحله اول گوشت‌ها را بشورید و خشک کنید. سپس گوشت‌ها را در اندازه‌های مناسب برای کباب برش می‌دهیم. توجه داشته باشید که ضخامت هر تکه گوشت حدود ۲ تا ۳ سانتی‌متر باشد و گوشت‌ها هم اندازه برش داده شوند.', 'در مرحله اول گوشت‌ها را بشورید و خشک کنید. سپس گوشت‌ها را در اندازه‌های مناسب برای کباب برش می‌دهیم. توجه داشته باشید که ضخامت هر تکه گوشت حدود ۲ تا ۳ سانتی‌متر باشد و گوشت‌ها هم اندازه برش داده شوند. بعد از تکه کردن گوشت، به گوشت هوا بدهید. برای این کار می‌توانید گوشت را روی یک پارچه پهن کنید و یا در معرض باد پنکه قرار دهید. این کار موجب می‌شود گوشت از حالت شل بودن خارج شود. بعد از اینکه گوشت هوا خورد، تکه‌های گوشت را داخل ظرف مناسبی بریزید. در مرحله دوم پیاز را با رنده ریز رنده کنید. پیاز رنده شده، ماست چکیده، فلفل و آب لیمو ترش تازه را به گوشت‌ها اضافه کنید و مواد را خوب با هم ترکیب کنید. حال یک سلفون روی ظرف کباب‌ها بکشید و اجازه دهید مواد برای حدود ۲ ساعت در بیرون از یخچال بماند تا آب بیفتد. بعد از این مدت گوشت‌ها را برای ۲۴ ساعت در یخچال قرار دهید. این کار موجب می‌شود کباب کنجه شما ترد و آبدار شود. بعد از گذشت ۲۴ ساعت گوشت‌ها را از یخچال خارج کنید و شروع به سیخ کردن آن کنید. توجه داشته باشید که شما می‌توانید گوشت را برای مدت ۸ تا ۱۰ ساعت هم در یخچال قرار دهید ولی هر چقدر این مرحله طولانی‌تر شود و به ۲۴ ساعت نزدیک‌تر باشد، کباب شما خوش طعم‌تر خواهد شد. بعد از اینکه همه گوشت‌ها را به سیخ کشیدید، باید آن را روی منقل پخت کنید. برای این که کباب‌ها بهتر پخته شوند، بهتر است آن‌ها را با شعله ملایم کباب کنید و در حین پخت حتما سیخ‌ها را چندین بار زیر و رو کنید. در نهایت بعد از اینکه همه بخش‌های گوشت به خوبی پخته شد، کباب‌ها را از روی منقل بردارید و در ظرف مناسب سرو کنید. برای این که کنجه کباب شما خوشمزه‌تر و چرب‌تر شود می‌توانید از دنبه در لا به ‌لای تکه‌های کباب استفاده کنید. این کار موجب می‌شود کباب شما نرم‌تر شود و گوشت نیز در حین پخت نسوزد. در زمان سرو کردن کنجه کباب می‌توانید مقدار آب لیمو ترش تازه روی آن بریزید و روی کباب را با ریحان تازه تزئین کنید. بسیاری از محلی‌های لاری برای خوش طعم‌تر شدن کنجه کباب دو تا چهار روز گوشت را در مواد می‌خوابانند و معتقد هستند این کار موجب تردتر شدن کباب می‌شود. توجه داشته باشید که برای درست کردن کباب کنجه حتما از ماست چکیده استفاده کنید. برای نرم و لطیف شدن کنجه کباب می‌توانید مقداری کیوی را در مواد گوشت خرد کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/d2c6e7f7e63b2a034d3529a93b4857bb.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (162, 'کباب کوبیده با سویا', 'سویاها را در یک کاسه بریزید و مقداری آب به آن اضافه کنید. چند ساعت سویا را در آب خیس کنید. سپس سویا را با دست فشار دهید و به ظرف دیگری منتقل کنید. بعد از خیس خوردن سویا باید به وسیله چرخ گوشت دو بار س', 'سویاها را در یک کاسه بریزید و مقداری آب به آن اضافه کنید. چند ساعت سویا را در آب خیس کنید. سپس سویا را با دست فشار دهید و به ظرف دیگری منتقل کنید. بعد از خیس خوردن سویا باید به وسیله چرخ گوشت دو بار سویا را چرخ کنید تا بافت آن نرم شود. سویاهای چرخ شده را در یک ظرف مناسب بریزید. وزن سویای شما بعد از خیس خوردن و فشار دادن باید حدود ۵۰۰ گرم باشد. ۳ عدد پیاز را با رنده ریز، رنده کنید. پیاز را تا آماده شدن سایر مواد در یخچال قرار دهید. این کار موجب می‌شود تا حالت اسیدی و تند پیاز از بین برود و به اصطلاح شیرین شود. حالا گوشت چرخ کرده (در صورت استفاده) را یک بار با سویا چرخ کنید. مواد را داخل یک کاسه بریزید. مقداری نمک، فلفل قرمز و فلفل سیاه به مواد کباب کوبیده بزنید. یک دستکش به دست کنید و شروع به ورز دادن مایه کباب کنید. این کار را برای مدت ۱۰ دقیقه ادامه دهید تا مواد کاملاً منسجم شوند. سپس مواد کباب کوبیده را داخل یک ظرف درب‌دار بریزید و برای ۳۰ دقیقه در یخچال قرار دهید تا استراحت کند. بعد از ۳۰ دقیقه مایه کباب کوبیده را از یخچال خارج کنید و با پیاز رنده شده ترکیب کنید. بیکینگ پودر و نمک را به مواد کوبیده اضافه کنید. دوباره مواد کباب کوبیده سویا را برای چند دقیقه ورز دهید. یک ظرف آب ولرم کنار دست‌تان قرار دهید. مقداری دست‌تان را با آب ولرم مرطوب کنید. سپس به اندازه یک نارنگی از مواد کباب کوبیده را بردارید. سیخ را داخل مواد کوبیده کنید و به آرامی و با کمک انگشتان مواد کباب را روی پخش کنید. سپس همه مواد را طبق همین اصول به سیخ بکشید. برای درست کردن کباب کوبیده سویا بهتر است آن را روی منقل کباب کنید. برای این کار ذغال را آماده کنید و سیخ‌ها را روی منقل بچینید. بعد از اینکه یک طرف کباب‌ها کمی روی سیخ خودش را گرفت، آن را برگردانید تا طرف دیگر کباب کوبیده نیز به سیخ بچسبد. هر چند دقیقه یک بار سیخ‌ها را برگردانید تا تمام قسمت­های کباب به صورت یکسان مغزپخت شود. در نهایت می‌توانید گوجه فرنگی، پیاز و فلفل را نیز روی منقل کباب کنید و در کنار کباب کوبیده سویا سرو کنید. برای طرز تهیه کباب سویا بدون گوشت خوشمزه می‌توانید به جای پخت روی ذغال مواد کباب را داخل ماهیتابه سرخ کنید و یک کباب تابه ای با سویا خوشمزه درست کنید. می‌توانید این غذای خوشمزه را هم با نان و هم با برنج چلو مجلسی میل کنید. در صورت تمایل می‌توانید کباب کوبیده بدون گوشت را تنها با سویا و بدون استفاده از گوشت چرخ کرده درست کنید. برای این کار بهتر است مقداری گندم یا آرد جو پرک به مواد اضافه کنید. این کار موجب می‌شود تا مواد انسجام بیشتری به دست آورد و کباب از روی سیخ نریزید. برای تهیه کباب تابه ای با سویا و گوشت به مواد مورد استفاده در طرز درست کردن کباب کوبیده، مقداری گوشت هم اضافه کنید. این کار باعث انسجام بیشتر کباب خواهد شد. طرز تهیه کباب تابه ای با گوشت و سویا مشابه طرز تهیه کباب با سویا بدون گوشت است و همه مراحل مشابه هم باید طی شود. اضافه کردن مقداری سیر یا فلفل دلمه به کباب کوبیده با سویا موجب می‌شود تا عطر بهتری پیدا کند. برای درست کردن کباب کوبیده با سویا و بدون گوشت باید از ترکیب سویای ریز، سیب زمینی پخته شده، آرد سوخاری، پیاز رنده شده و ادویه‌های مختلف استفاده کنید. سیب زمینی و آرد موجب می‌شوند تا سویا منسجم‌تر شود و روی سیخ قرار بگیرد. برای خوش عطر و طعم شدن کباب کوبیده با سویا بهتر است از مقداری پودر جعفری خشک شده، پودر سیر و فلفل سبز چرخ شده نیز استفاده کنید. به منظور درست کردن کباب کوبیده با سویا می‌توانید از مقداری کمی آرد جو پرک یا گندم نیز استفاده کنید. طبیعی است که اگر در طرز تهیه کباب کوبیده با سویا از چربی استفاده نشود، بافت آن کمی سفت‌تر می‌شود. برای نرم‌تر شدن کباب سویا، می‌توانید مقداری دنبه یا گوشت چرخ کرده به مواد کباب سویا اضافه کنید. اگر می‌خواهید کباب کوبیده سویا از روی سیخ نریزد، حتما قبل از اضافه کردن پیاز، آب آن را به صورت کامل بگیرید و بعد تفاله پیاز را با مواد تفت دهید. همچنین در هنگام چرخاندن کباب روی آتش، این کار را در ابتدا با سرعت انجام دهید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/01/04-Koobideh-with-Bread-Pita.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (163, 'کباب کوبیده مرغ', 'پیاز را پوست بگیرید و ریز رنده کنید. آب اضافی پیاز را بگیرید. فلفل دلمه‌ای را رنده کرده و با آب پیاز ترکیب کنید.', 'پیاز را پوست بگیرید و ریز رنده کنید. آب اضافی پیاز را بگیرید. فلفل دلمه‌ای را رنده کرده و با آب پیاز ترکیب کنید. مرغ چرخ کرده (ترکیب ران و سینه) را به ترکیب پیاز و فلفل دلمه‌ای اضافه کنید. نمک، فلفل سیاه، سماق، پاپریکا، زردچوبه و پودر سیر را اضافه کنید. مواد را برای چند دقیقه با دست ورز دهید تا کاملا یکدست شود. روی ظرف را با کیسه پلاستیکی بپوشانید و برای ۱ ساعت در یخچال قرار دهید تا استراحت کند. برای تهیه کوبیده تابه‌ای: مقداری روغن در ماهیتابه بریزید. مایه کوبیده مرغ را در ماهیتابه پهن کنید و با کفگیر چند برش عمودی ایجاد کنید. با حرارت ملایم بپزید تا یک سمت آن بپزد، سپس برگردانید تا طرف دیگر نیز بپزد. برای تهیه کوبیده سیخی روی منقل: پوست و استخوان‌های ران و سینه مرغ را جدا کنید. مرغ‌ها را به همراه سیر (یا پودر سیر)، فلفل دلمه‌ای و جعفری خرد شده و ادویه‌ها در غذاساز میکس کنید تا یکدست شود. پیازها را رنده کنید و آب اضافی آن را بگیرید. پیاز رنده شده را به مرغ چرخ کرده اضافه کنید. زعفران، فلفل سیاه و قرمز، نمک و زردچوبه را اضافه کرده و گوشت را خوب ورز دهید. مایه کوبیده را در یک ظرف درب‌دار بریزید و به مدت ۱ ساعت در یخچال نگه دارید تا منسجم‌تر شود. بعد از یک ساعت، مایه کوبیده را از یخچال بیرون بیاورید و به سیخ فلزی بکشید. سپس کوبیده مرغ را روی زغال منقل کباب کنید. برای تهیه کوبیده در توستر: سینی توستر را چرب کنید. مایه کوبیده مرغ را با دست حالت دهید و در سینی بچینید. مدت زمان پخت بستگی به درجه توستر دارد. برای تهیه کوبیده در فر: مواد را آماده کنید. مایه کوبیده مرغ را در سینی مخصوص فر پهن کنید و با نوک چاقو قسمت کنید. روی سینی فویل بکشید و چند سوراخ ایجاد کنید. در دمای ۱۸۰ درجه سانتی‌گراد فر به مدت ۳۰ الی ۴۵ دقیقه قرار دهید. در ۵ دقیقه پایانی فویل را بردارید تا سطح روی کوبیده گریل شود. برای کباب کوبیده مرغ و بادمجان: بادمجان‌ها را پوست بگیرید و حلقه‌ای برش بزنید. گوشت مرغ چرخ کرده، پیاز، سیر، جعفری، نمک، فلفل و تخم مرغ را با هم ترکیب و ورز دهید. یک در میان گوشت قلقلی مرغ، گوجه فرنگی گیلاسی و بادمجان حلقه‌ای را در سیخ‌های چوبی فرو کنید. سیخ‌ها را در ماهیتابه با مقداری روغن سرخ کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/08/233321.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (164, 'کباب کوبیده بلدرچین', 'برای درست کردن کباب کوبیده بلدرچین در تابه، ابتدا بلدرچین‌های پاک شده را حدود ۷ تا ۸ ساعت در آب و نمک قرار دهید تا بوی زهم آن از بین برود و گوشتش نرم شود. سپس آنها را خشک کرده و گوشت بلدرچین و مرغ را', 'برای درست کردن کباب کوبیده بلدرچین در تابه، ابتدا بلدرچین‌های پاک شده را حدود ۷ تا ۸ ساعت در آب و نمک قرار دهید تا بوی زهم آن از بین برود و گوشتش نرم شود. سپس آنها را خشک کرده و گوشت بلدرچین و مرغ را از استخوان جدا کنید. پیاز، سیر، فلفل دلمه‌ای و جعفری را شسته و خرد کنید. سپس این مواد را با آبلیمو، روغن زیتون، سس سویا و زعفران دم کرده در غذاساز بریزید و میکس کنید تا یکدست شوند. ادویه‌ها شامل زیره، پاپریکا، فلفل قرمز، نمک و فلفل سیاه را اضافه کرده و دوباره میکس کنید تا ادویه‌ها با مواد مخلوط شوند. گوشت بلدرچین و مرغ را به مواد داخل غذاساز اضافه کنید و برای یک دقیقه میکس کنید تا گوشت کاملا خمیری و به ادویه‌ها آغشته شود. ترکیب گوشت بلدرچین با مرغ به انسجام بیشتر مایه کمک می‌کند. مایه کباب را در کاسه‌ای بریزید، روی آن را با سلفون بپوشانید و حداقل ۳۰ دقیقه (بهتر است ۱ تا ۲ ساعت) در یخچال قرار دهید تا استراحت کند و مزه‌ها به خورد گوشت بروند. برای پخت در تابه، مقدار کمی روغن زیتون در تابه بریزید و روی حرارت متوسط داغ کنید. مایه کباب را به شکل دلخواه فرم دهید و در تابه بچینید. هر طرف کباب را حدود ۴ تا ۵ دقیقه سرخ کنید تا طلایی شود. در طول پخت درب تابه را ببندید. در انتها کمی کره و زعفران دم کرده روی کباب‌ها بمالید. برای پخت در فر، فر را روی دمای ۲۲۰ درجه سانتی‌گراد گرم کنید. سینی فر را چرب کرده یا با کاغذ روغنی بپوشانید. مایه کباب را فرم داده و روی سینی بچینید. سینی را به مدت ۲۰ تا ۲۵ دقیقه در طبقه میانی فر قرار دهید. در نیمه پخت، کباب‌ها را برگردانید. در آخر، کمی کره و زعفران روی کباب‌ها بمالید و چند دقیقه زیر گریل قرار دهید تا طلایی شوند. برای پخت در توستر، توستر را روی دمای ۲۰۰ تا ۲۱۰ درجه سانتی‌گراد تنظیم کنید. کباب کوبیده فرم گرفته را در سینی توستر بچینید و حدود ۲۰ دقیقه بپزید. در میانه پخت، کباب‌ها را برگردانید. در آخر، کمی کره و زعفران روی کباب‌ها بمالید و برای ۲ تا ۳ دقیقه در حالت گریل توستر قرار دهید تا طلایی شوند. کباب کوبیده بلدرچین را می‌توانید با گوجه فرنگی کبابی، سبزی خوردن، برنج زعفرانی یا نان تازه سرو کنید.', 38, 77, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/09/%DA%A9%D8%A8%D8%A7%D8%A8-%DA%A9%D9%88%D8%A8%DB%8C%D8%AF%D9%87-%D8%A8%D9%84%D8%AF%D8%B1%DA%86%DB%8C%D9%86-3.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (165, 'کباب کوبیده بوقلمون', 'در اولین مرحله، سینه بوقلمون را تمیز کرده و بشویید. سینه بوقلمون شسته شده را در آبکش قرار دهید تا آب اضافه آن خارج و کمی خشک شود. دنبه گوسفندی را نیز تمیز کرده و بشویید.', 'در اولین مرحله، سینه بوقلمون را تمیز کرده و بشویید. سینه بوقلمون شسته شده را در آبکش قرار دهید تا آب اضافه آن خارج و کمی خشک شود. دنبه گوسفندی را نیز تمیز کرده و بشویید. زعفران را بسابید و با مقداری آب جوش یا چند قطعه یخ دم کنید تا دو قاشق غذاخوری زعفران غلیظ داشته باشید. سینه بوقلمون بدون پوست و استخوان و دنبه گوسفندی را دو مرتبه با چرخ‌گوشت چرخ کنید تا کاملا له شوند. سپس این ترکیب را در یک ظرف مناسب بریزید. دو عدد پیاز متوسط را پوست بگیرید و بشویید. پیاز را با رنده ریز، رنده کنید. با دست پیاز رنده شده را فشرده کنید تا آب پیاز کاملا گرفته شود، ما به تفاله پیاز نیاز داریم. دور ریختن آب پیاز مهم است چون از پاشیدن مایه گوشت کوبیده جلوگیری می‌کند. یک عدد سیر را نیز پوست بگیرید و بشویید. سیر را با رنده مخصوص یا به وسیله سیر له کن به شکل پوره در بیاورید. سیر و پیاز را به ظرف چرخ کرده بوقلمون و دنبه اضافه کنید. به ترکیب مواد چرخ کرده و سیر و پیاز رنده شده، نمک، فلفل سیاه، زردچوبه و سماق به همراه دو قاشق غذاخوری زعفران غلیظ دم کرده و آبلیمو را اضافه کنید و مواد را حسابی با دست ورز دهید تا مزه چاشنی‌ها به خورد مایه کباب بوقلمون برود. ورز دادن را باید تا ده دقیقه ادامه دهید. ورز دادن باعث می‌شود تا مواد منسجم شوند و کباب کوبیده روی آتش خودش را ول نکند و نریزد. بعد از آنکه حسابی مایه کباب را ورز دادید، روی ظرف آن سلفون بکشید. مایه کباب با بوقلمون را به مدت دو ساعت در یخچال قرار دهید تا استراحت کند و کاملا خودش را بگیرد و به انسجام مناسب برسد. بعد از گذشت دو ساعت، مایه کوبیده بوقلمون را از یخچال بیرون بیاورید. دستتان را با آب خیس کنید و به اندازه یک نارنگی از مایه کباب بردارید. مایه کباب را روی سیخ قرار دهید و با دست آن را پهن کنید. طریقه سیخ زدن کباب کوبیده به این صورت است که بعد از این‌که گوشت را روی سیخ پهن کردید با دو انگشت اشاره و شصت (انگشت شصت روی گوشت و انگشت اشاره زیر گوشت) به آرامی روی گوشت فشارهای کوچک بیاورید تا پستی و بلندی‌های گوشت صاف شود و جای انگشتان‌تان روی گوشت به صورت یکی در میان فرو رفته باقی بماند. سر و ته گوشت را روی سیخ کمی بیشتر فشار دهید، تا موقع کشیدن کباب از روی سیخ به راحتی از آن جدا شود. گوجه فرنگی‌ها را هم سیخ بگیرید. در آخرین مرحله از طرز تهیه کباب کوبیده بوقلمون زغالی، سیخ‌های کباب را روی منقل قرار دهید و مانند هر کباب دیگری اجازه دهید تا با حرارت ذغال کاملا مغز پخت شوند. هر چند لحظه یکبار سیخ‌ها را بچرخانید تا کباب به طور یکنواخت پخته شود و یک سمتش نسوزد و سمت دیگرش خام بماند. کباب کوبیده بوقلمون را می‌توانید با برنج کته یا چلو آبکش و همچنین با نان سرو کنید. در کنار کباب کوبیده با گوشت بوقلمون می‌توانید از گوجه کبابی و همچنین سبزی خوردن و پیاز استفاده کنید. اگر تهیه کباب بوقلمون روی منقل و زغال برایتان سخت است، می‌توانید به وسیله تابه و روی حرارت آن را تهیه کنید. کافی است مایه کوبیده با بوقلمون را آماده کنید و در یخچال استراحت دهید. سپس با استفاده از سیخ‌های کوچک چوبی آنها را به سیخ بکشید. یک تابه با اندازه مناسب انتخاب کنید، کف تابه را با کره چرب کنید و سیخ‌های کباب را روی آن بچینید. درب تابه را ببندید و اجازه دهید کوبیده‌های بوقلمون روی حرارت ملایم مغز پخت شوند. هرچند دقیقه یکبار در تابه را بردارید و سیخ‌ها را بچرخانید تا به طور یکدست پخته شوند. اگر هم سیخ چوبی ندارید مایه کوبیده را در تابه بریزید و خوب پهن و صاف کنید. وقتی کمی گوشت خودش را گرفت آن را به شکل کباب کوبیده از طول با یک چاقوی تیز برش بزنید. برای تهیه کباب کوبیده با گوشت بوقلمون در فر کافی است مطابق مراحل قبل مایه کباب کوبیده را آماده کنید و به سیخ چوبی بکشید. لازم است فر را از یک ربع قبل روشن کنید و اجازه دهید با دمای ۱۸۰ درجه سانتی‌گراد گرم شود. یک قالب کره را ذوب کنید و با فرچه روی کباب‌ها بمالید. کف سینی فر یک کاغذ روغنی قرار دهید و سیخ‌های کباب را روی آن قرار دهید. سینی فر به همراه سیخ‌های کباب را در فر قرار دهید و اجازه دهید به مدت بیست دقیقه پخته شوند. در زمان پخت چند بار سیخ‌های کباب کوبیده بوقلمون را بچرخانید تا کباب به صورت یکدست پخته و مغزپخت شود. نمک، فلفل سیاه و زردچوبه از ادویه‌های متداولی هستند که در تهیه کباب کوبیده گوشت بوقلمون استفاده می‌شود. سماق یکی دیگر از ادویه‌های مخصوص کباب کوبیده است. شما می‌توانید از مقداری پودر پاپریکا، زیره سبز و پودر سیر در تهیه مایه کباب کوبیده بوقلمون نیز استفاده کنید.', 13, 27, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/20241009_022346_122533868.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (166, 'کباب کوبیده ترش', 'پیازها را رنده کنید. نصف پیاز رنده شده را فشار دهید تا آب اضافی آن خارج شود. تفاله این مقدار پیاز را برای مایه کباب ترش کنار بگذارید. آب پیاز را در ظرف جداگانه‌ای بریزید تا بعدا به سس ترش کباب اضافه ک', 'پیازها را رنده کنید. نصف پیاز رنده شده را فشار دهید تا آب اضافی آن خارج شود. تفاله این مقدار پیاز را برای مایه کباب ترش کنار بگذارید. آب پیاز را در ظرف جداگانه‌ای بریزید تا بعدا به سس ترش کباب اضافه کنید. باقی پیاز رنده شده را هم به سس مخصوص ترش کباب اضافه کنید. حبه‌های سیر شسته شده و بدون پوست را ریز رنده کنید و برای تهیه مایه کباب کوبیده ترش و سس آن کنار بگذارید. مغز گردو را پودر کنید. سبزی خالواش یا چوچاق را پاک کرده، بشویید و ساطوری کنید. تفاله پیاز و نیمی از سیر رنده شده را همراه با گوشت در ظرف مناسبی بریزید. این ترکیب را به خوبی ورز دهید تا پیاز، سیر و گوشت چرخ کرده به صورت یکنواخت با یکدیگر مخلوط شوند. پس از ورز دادن ترکیب گوشت چرخ کرده و پیاز رنده شده، یک قاشق غذاخوری مغز گردوی آسیاب شده، مقداری نمک، زردچوبه و پودر فلفل سیاه را نیز به این ترکیب اضافه کنید. این مواد را دوباره برای ۳ دقیقه به خوبی ورز دهید تا ترکیب یکدست و منسجمی ایجاد شود. درب ظرف حاوی گوشت و مواد ورز داده شده را ببندید یا با سلفون بپوشانید. سپس، این ظرف را برای یک ساعت در یخچال بگذارید تا گوشت کباب کوبیده ترش استراحت کند و کاملا خودش را بگیرد. تابه مناسب و نچسبی را انتخاب کرده و مایه کباب ترش را کف تابه پهن کنید؛ به طوری که سطح تابه را به صورت یکنواخت با ضخامت یکسان بپوشاند. سپس، سطح مایه گوشتی را با کمک لیسک برش بزنید و به قطعات مساوی تقسیم کنید. کره را خرد کنید و قطعات کره را روی مایه کباب ترش بچینید. تابه حاوی گوشت و کره را روی حرارت متوسط بگذارید تا یک طرف کباب‌ها تقریبا برای ۵ دقیقه سرخ شود. در هنگام سرخ کردن گوشت کباب ترش نباید درب تابه را ببندید. مقداری زعفران دم کرده را روی کباب‌ها بریزید و همه کباب‌ها را برگردانید تا طرف دیگر آن‌ها هم تقریبا به مدت ۵ دقیقه سرخ شود. سپس، حرارت را خاموش کنید و تابه را کنار بگذارید. برای درست کردن سس، تابه دیگری را روی حرارت متوسط بگذارید و مقداری روغن در آن بریزید. پس از داغ شدن روغن، باقی پیاز رنده شده را که برای درست کردن سس کنار گذاشته بودید به تابه اضافه کنید تا سرخ شود. مقداری زردچوبه، نمک و فلفل سیاه را همراه با ۲ قاشق غذاخوری گردوی آسیاب شده در تابه حاوی پیاز داغ بریزید و برای یک دقیقه تفت دهید. باقی سیر رنده شده و رب انار را نیز در تابه حاوی پیاز داغ، گردو و ادویه‌ها بریزید. به اندازه یک و نیم لیوان آب جوش نیز به محتویات تابه اضافه کنید. آب پیاز که قبلا کنار گذاشتید را نیز همراه با سبزی خالواش ساطوری شده در این ترکیب بریزید. این مواد را به خوبی هم بزنید تا کاملا با یکدیگر مخلوط شوند. درب تابه را ببندید و حرارت را ملایم کنید تا سس شما روی حرارت کم روغن بیندازد و جا افتاده شود. پس از آن که سس مخصوص حسابی جا افتاده و غلیظ شد، قطعات کباب ترش سرخ شده را داخل سس بچینید. سپس، ۲۰ دقیقه منتظر بمانید تا کباب کوبیده ترش همراه با سس ملس و خوشمزه بپزد و کاملا طعم‌دار شود. پس از گذشت این مدت، کباب ترش تابه ای را در ظرف سرو بگذارید و روی آن را با سس مخصوص بپوشانید. می‌توانید این غذای شمالی خوشمزه را با دانه‌های انار، گوجه فرنگی، فلفل سبز یا دلمه‌ای و دیگر انواع سبزیجات تزیین کرده و همراه با نان یا برنج سرو کنید.', 21, 44, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/12/%DA%A9%D8%A8%D8%A7%D8%A8-%DA%A9%D9%88%D8%A8%DB%8C%D8%AF%D9%87-%D8%AA%D8%B1%D8%B4-2-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (167, 'کباب کوبیده دو رنگ', 'برای تهیه کباب کوبیده دو رنگ، نیم کیلوگرم گوشت گوسفندی را با ۲۵ گرم دنبه دو بار چرخ کنید. ۱ پیاز رنده و آبگیری کنید. تفاله پیاز و فلفل سیاه و نمک را اضافه کنید و همه مواد را با هم ۱۰ دقیقه ورز دهید.', 'برای تهیه کباب کوبیده دو رنگ، نیم کیلوگرم گوشت گوسفندی را با ۲۵ گرم دنبه دو بار چرخ کنید. ۱ پیاز رنده و آبگیری کنید. تفاله پیاز و فلفل سیاه و نمک را اضافه کنید و همه مواد را با هم ۱۰ دقیقه ورز دهید. سینه مرغ، ۲۵ گرم دنبه، یک پیاز رنده و آبگیری شده، فلفل سیاه و نمک را هم با هم ورز دهید. ظرف‌های کوبیده مرغ و کباب کوبیده را بپوشانید و یک ساعت در یخچال استراحت دهید. به سیخ‌ها مقداری از مایه کوبیده مرغ و مقداری از کباب کوبیده بزنید. همه سیخ‌ها را به شکل دو رنگ فرم دهید. سیخ‌ها دو رنگ را روی زغال گداخته کباب کنید. یک سمت کوبیده‌های دو رنگ که طلایی شد، سمت دیگر آن را هم کباب کنید. سپس کباب کوبیده دو رنگ را با نان یا برنج، سرو کنید. در ابتدا برای طرز تهیه کباب کوبیده دو رنگ خانگی، گوشت گوسفندی را بشویید و همراه با نصف مقدار دنبه گوسفندی، دو بار در چرخ گوشت، چرخ کنید. گوشت دو بار چرخ شده را در یک کاسه بریزید. کمی نمک و فلفل سیاه به آن اضافه کنید. در ادامه، سینه مرغ را بشویید و بعد از اینکه آب اضافی خود را از دست داد، به تکه‌های درشت، خرد کنید. سینه مرغ خرد شده و نصف باقی‌مانده دنبه گوسفندی را در چرخ گوشت بریزید و دو بار چرخ کنید. مرغ چرخ کرده و دنبه را در یک کاسه بریزید و مقداری نمک و فلفل سیاه به آن اضافه کنید. در ادامه پیازها را پوست بگیرید و با رنده متوسط، رنده کنید. آب پیاز را با دست بگیرید و تفاله آن را جدا بگذارید. نصف پیازهای رنده شده را به گوشت چرخ شده اضافه کنید. نصف دیگر پیازها را به مرغ چرخ کرده بیفزایید. در این مرحله از طرز تهیه کباب کوبیده دو رنگ، مایه گوشت چرخ کرده، دنبه، پیاز و ۱ قاشق غذاخوری زعفران دم کرده را با هم به مدت ۱۰ دقیقه ورز دهید تا یکدست شود. سپس روی ظرف را با سلفون بپوشانید و در یخچال به مدت ۱ ساعت استراحت دهید. سپس مرغ چرخ کرده، دنبه، پیاز رنده و آبگیری شده و ۱ قاشق زعفران دم کرده را با هم ورز دهید. حدود ۱۰ دقیقه به ورز دادن ادامه دهید تا طعم‌ها به خورد هم بروند. روی ظرف را با سلفون بپوشانید و مایه مرغ چرخ کرده را به مدت ۱ ساعت در یخچال استراحت دهید تا کاملا منسجم شود. در اواخر استراحت کردن مایه گوشت چرخ کرده و مرغ، منقل را آماده کنید. زغال‌ها را گداخته کرده و در منقل بریزید. زغال‌ها باید به یک اندازه سرخ شوند. در این فاصله، سیخ‌های پهن را به آب پیاز، آغشته کنید. مرغ و گوشت چرخ کرده را از یخچال بیرون بیاورید. در این مرحله از درست کردن کباب دو رنگ، دستتان را با آب پیاز مرطوب کنید و به اندازه یک پرتقال از مایه گوشت چرخ کرده بین دست‌هایتان گلوله کنید. سیخ کباب را در دست دیگر بگیرید و به داخل مایه کباب کوبیده، فرو کنید. با انگشت‌هایتان، مایه کباب کوبیده را روی سیخ پهن کنید، در حدی که نه خیلی نازک و نه خیلی ضخیم باشد. نصف سیخ را مایه گوشت چرخ کرده بزنید. همین کار را با مرغ چرخ کرده هم انجام دهید. از مایه مرغ چرخ کرده هم به اندازه یک نارنگی بردارید و قسمت بالا یا پایین سیخ کوبیده بزنید تا سیخ کباب دو رنگ شود. همه سیخ‌ها را به روش مرحله بالا، آماده کرده و مایه مرغ و گوشت را سر سیخ‌ها بزنید. می‌توانید چند گوجه فرنگی و فلفل دلمه‌ای را هم به سیخ بزنید. سپس سیخ‌ها را روی منقل بگذارید و بعد از ۳۰ ثانیه، سیخ‌ها را برگردانید. ۳۰ ثانیه دیگر، دوباره سیخ‌ها را برگردانید. بعد از اینکه کوبیده‌ها به سیخ چسبیدند و دیگر نریختند، اجازه دهید یک سمت سیخ‌ها طلایی شود. در این فاصله، کره را روی حرارت ذوب کنید و ۱ قاشق غذاخوری زعفران دم کرده را اضافه کرده و هم بزنید. با قلم‌موی آشپزی، از مایه رومال روی کوبیده دو رنگ بمالید تا آبدار و دارای طعم زعفرانی شود. یک سمت کوبیده‌های دو رنگ که طلایی شد، سمت دیگر آنها را هم کباب کنید تا طلایی شود. بعد از اینکه همه کباب کوبیده‌های دو رنگ آماده شدند، آنها را روی نان بگذارید. اگر گوجه فرنگی و فلفل دلمه‌ای هم کباب کرده‌اید، سیخ‌ها را بکشید تا کباب‌ها جدا شوند. کباب کوبیده دو رنگ را با نان و سبزی خوردن، برنج زعفرانی یا برنج چلو ساده سرو کنید. برای تهیه کباب کوبیده دو رنگ در فر، مایه گوشت چرخ کرده و مرغ چرخ کرده را مانند روش بالا آماده کنید، ورز دهید و استراحت دهید. تعدادی سیخ چوبی را در آب سرد به مدت ۳۰ دقیقه بخیسانید تا در زمان کباب کردن، کباب‌های دو رنگ، نسوزند. وقتی مایه مرغ و گوشت چرخ کرده، استراحت کردند، به اندازه یک نارنگی از مایه گوشت چرخ کرده را به سیخ بزنید و فرم دهید تا دو سوم سیخ‌ را بگیرد. برای یک سوم دیگر هم مایه مرغ چرخ کرده را به سیخ بزنید تا کوبیده‌ها دو رنگ شوند. همه سیخ‌ها را به همین شکل درست کنید و در سینی فر بچینید. فر را روشن و روی دمای ۱۸۰ درجه سانتی‌گراد تنظیم کنید تا گرم شود. سینی را در طبقه وسط فر بگذارید و ۱۵ دقیقه صبر کنید تا روی کباب‌ها طلایی و داخل آن، مغزپخت شود. هر ۵ دقیقه یک بار، کوبیده‌های دو رنگ را بچرخانید تا همه قسمت‌ها، طلایی شود. روی کباب‌ها را هم با کره و زعفران، رومال کنید. کباب‌ها که پختند و آماده شدند، سیخ‌ها را از فر بیرون بیاورید و با نان، برنج، پیاز و سبزی خوردن میل کنید. برای طرز تهیه کباب کوبیده دو رنگ خانگی در تابه، گوشت گوسفندی را بعد از شستن و درشت خرد کردن، همراه با نصف دنبه گوسفندی، دو بار در چرخ گوشت، چرخ کنید. گوشت دو بار چرخ شده را در یک کاسه بریزید. کمی نمک و فلفل سیاه و یک پیاز رنده و آب گرفته شده، به آن اضافه کنید. سینه مرغ را هم به همین روش آماده کنید. در دو ظرف جدا مایه کباب کوبیده مرغ و مایه کباب کوبیده گوشت را ورز دهید و آنها را در یخچال بگذارید تا استراحت کنند. سپس کف یک تابه را چرب کنید و یک لایه از مایه کباب کوبیده و یک لایه مایه مرغ کوبیده بگذارید و سطح آن را صاف کنید. همه مایه کوبیده گوشت و مرغ دو رنگ را به همین شکل کف تابه بگذارید و صاف کنید. سپس تابه را روی حرارت بگذارید تا یک سمت کباب کوبیده دو رنگ سرخ شود. سپس آن را برگردانید و سمت دیگر را هم سرخ کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/03/kebab-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (168, 'کباب کوبیده نگینی', 'در اولین مرحله از طرز تهیه کباب نگینی ماهیتابه ای، هر دو پیاز را پوست بگیرید و رنده کنید. آب پیاز را با دست بگیرید و فقط از تفاله آن استفاده کنید. در یک کاسه گوشت چرخ کرده گوسفندی و گوساله را با نصف پ', 'در اولین مرحله از طرز تهیه کباب نگینی ماهیتابه ای، هر دو پیاز را پوست بگیرید و رنده کنید. آب پیاز را با دست بگیرید و فقط از تفاله آن استفاده کنید. در یک کاسه گوشت چرخ کرده گوسفندی و گوساله را با نصف پیازهای رنده شده مخلوط کنید. نصف پودر سوخاری (۳ و ۱/۲ قاشق غذاخوری) را به گوشت چرخ کرده اضافه کنید. ۱ قاشق غذاخوری زعفران دم کرده را به همراه مقدار کافی نمک و فلفل با هم مخلوط کنید. مواد را به مدت ۵ تا ۱۰ دقیقه ورز دهید تا کاملا یکدست و منسجم شود. روی کاسه را با سلفون بپوشانید و ظرف را به مدت حداقل ۱ ساعت در یخچال قرار دهید تا استراحت کند. گوشت مرغ را با استفاده از یک چاقوی تیز، از استخوان جدا کنید. گوشت ها را در چرخ گوشت یا غذاساز کاملا چرخ کنید. در یک کاسه جداگانه، بقیه پیاز آبگیری شده را به همراه پودر سوخاری، نمک و فلفل و زعفران با هم مخلوط کنید و ورز دهید. روی این ظرف را هم بپوشانید و به مدت ۱ ساعت در یخچال قرار دهید. سیخ های چوبی را از قبل در آب خیس کنید. بعد از این که مایه مرغ و گوشت چرخ کرده به اندازه کافی استراحت کردند، آن ها را از یخچال خارج کنید. به اندازه یک گردو از مخلوط گوشت را بردارید و گلوله کنید. همه گوشت ها را به شکل کوفته قلقلی در آورید. همین کار را با مرغ های چرخ و آماده شده هم انجام دهید. در آخرین مرحله از طرز تهیه کباب نگینی یک گلوله از گوشت و یک گلوله از مرغ چرخ شده را به سیخ بزنید. به صورت یکی در میان گوشت و مرغ به سیخ بزنید تا سیخ ها کامل شوند. همه سیخ ها که آماده شدند، در یک تابه کمی روغن بریزید و اجازه دهید داغ شود. سپس سیخ ها را در تابه کنار هم قرار دهید. یک طرف سیخ ها که طلایی شد، سمت دیگر را هم سرخ کنید. درب ظرف را روی آن قرار دهید و حرارت را کم کنید تا مغز کباب نگینی هم بپزد. بعد از این که کباب نگینی شما آماده شد، آن را همراه با برنج یا نان و سبزی خوردن و سالاد یا ترشی سرو کنید. سینه های مرغ را بشویید و از پهنا برش دهید. فیله ها را به شکل مستطیل هایی در ابعاد ۲ در ۳ برش بزنید. پیازها را رنده کنید و آب آن ها را بگیرید. در یک ظرف فیله های مرغ برش خورده را به همراه آب پیاز بریزید. نمک و فلفل و ۱ قاشق زعفران را اضافه کنید. همه مواد را با هم مخلوط کنید تا مواد به خورد مرغ ها برود. روی ظرف را بپوشانید و ۱ساعت در یخچال قرار دهید. گوشت چرخ کرده را با پیاز آبگیری شده (تفاله پیاز) به همراه نمک، فلفل و ۱ قاشق غذاخوری زعفران مخلوط کنید و به خوبی ورز دهید. باید مواد به خوبی منسجم شوند و به دست نچسبند. روی ظرف را بپوشانید و به مدت ۱ ساعت در یخچال استراحت دهید. فر را روشن و روی دمای ۲۰۰ درجه سانتی گراد گرم کنید. سینی فر را با کمی روغن چرب کنید. گوشت چرخ کرده را کف سینی پهن کنید در حدی که ضخامت آن به ۲ سانتی متر برسد. با یک کاتر یا چاقو، گوشت پهن شده را به قسمت های مساوی تقسیم کنید. مرغ های مرینیت شده را از یخچال بیرون بیاورید و به فاصله های یکسان و به صورت مرتب روی قسمت های برش خورده گوشت چرخ کرده بچینید. وقتی تکه های مرغ تمام شدند، روی ظرف را فویل بپیچید و در فر قرار دهید. به مدت ۳۰ دقیقه سینی را در فر با دمای ۲۰۰ درجه قرار دهید. سپس ظرف را از فر بیرون بیاورید. عصاره ای را که از کباب ها خارج شده است، روی کباب ها بریزید و دوباره فویل را روی آن بکشید. دمای فر را روی ۱۸۰ درجه سانتی گراد تنظیم و ۳۰ دقیقه دیگر، سینی را در فر قرار دهید. بعد از این مدت، سینی را از فر خارج کنید. کباب نگینی را بیرون بیاورید و در ظرف مناسب سرو کنید. در طرز تهیه کباب نگینی رولی، گوشت چرخ کرده را به روش های بالا آماده کنید. سینه مرغ را به صورت مستطیل های بلند و باریک، برش بزنید و مرینیت کنید و استراحت دهید. گوشت چرخ کرده را بعد از استراحت در یخچال، به شکل کوفته قلقلی در آورید. هر برش مستطیلی فیله مرغ را دور قلقلی ها بپیچید. به طوری که قلقلی گوشت چرخ کرده وسط یک لایه از فیله مرغ باشد. همه مواد را به همین شکل در آورید و سر هر سیخ، ۴ تا ۵ کباب رولی بزنید. سپس آن ها را در تابه یا در فر بپزید. می توانید کباب نگینی یا رولی را روی منقل هم کباب کنید. در این روش بهتر است از سیخ های فلزی استفاده کنید و اجازه دهید زغال ها کاملا گداخته شوند. هر دو طرف کباب های نگینی را مثل جوجه کباب و کباب کوبیده سرخ و طلایی و سپس آن ها را از سیخ جدا کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/09/kabab-negini-5-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
