INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (192, 'کوفته برنجی خرمایی', 'قبل از شروع به آشپزی و از شب قبل باید برنج مورد نظر برای درست کردن کوفته برنجی خرمایی را حداقل به مدت ۱۲ ساعت در محلول آب و نمک بخیسانید. در زمان شروع مراحل درست کردن کوفته، قابلمه مناسبی را انتخاب کن', 'قبل از شروع به آشپزی و از شب قبل باید برنج مورد نظر برای درست کردن کوفته برنجی خرمایی را حداقل به مدت ۱۲ ساعت در محلول آب و نمک بخیسانید. در زمان شروع مراحل درست کردن کوفته، قابلمه مناسبی را انتخاب کنید و مقداری آب و نمک هم در آن بریزید. قابلمه را روی حرارت زیاد قرار دهید تا بجوشد. سپس، آب برنج خیس خورده را خالی کنید و برنج را آبکش کنید. بعد برنج آبکش شده را در آب جوش بریزید. مدتی صبر کنید تا برنج شما نیم‌پز شود (سطح روی برنج نرم و مغز آن سفت باشد). در ادامه، برنج را از آب جوش آبکش کنید و منتظر بمانید تا آب اضافی آن خالی شود. در نهایت، برنج نیم‌پز را روی دستمال پارچه‌ای تمیزی پهن کنید و اجازه دهید کاملا خشک شود. پس از اطمینان از خشک شدن برنج، آن را در دستگاه آسیاب خانگی بریزید و کاملا آسیاب کنید. در فاصله انتظار برای خشک شدن برنج، سس کوفته را آماده کنید. برای این منظور، ابتدا یکی از پیازها را ریز و نگینی خرد کنید. سپس، مقداری روغن سرخ کردنی را در قابلمه مناسبی بریزید و روی حرارت متوسط قرار دهید تا کاملا داغ شود. پیازهای خرد شده را داخل قابلمه بریزید و صبر کنید تا سبک و طلایی شوند. در ادامه، رب گوجه فرنگی را به محتویات قابلمه اضافه کنید و تفت دهید تا رنگ باز کند. حالا، مقداری نمک، فلفل سیاه، زردچوبه و زعفران دم کرده را هم به این ترکیب اضافه کنید و هم بزنید تا طعم خامی ادویه‌ها گرفته شود. در نهایت، به اندازه ۲ لیوان دسته‌دار فرانسوی آب جوش به قابلمه اضافه کنید. درب قابلمه را ببندید و حرارت را کم کنید تا سس کوفته برنجی خرمایی جا افتاده شود و غلیظ شود. حالا، یک عدد پیاز باقی‌مانده را به‌ صورت ریز رنده کنید. آب پیاز را بگیرید و تفاله پیاز را به قابلمه حاوی سس اضافه کنید. تفاله پیاز رنده شده را با گوشت چرخ کرده (دو یا سه بار چرخ شده)، سبزیجات معطر ساطوری شده مقدار مناسبی از هر یک از ادویه‌ها و همچنین، آرد نخودچی داخل یک کاسه بریزید و به‌خوبی ورز دهید. ورز دادن مواد را تا زمانی ادامه دهید که مایه گوشت چرخ کرده چسبندگی لازم را پیدا کرده و بافت منسجمی پیدا کنند. سپس، برنج آسیاب شده را همراه با تخم مرغ به ترکیب گوشت، پیاز، آرد نخودچی و باقی مواد اضافه کنید. این ترکیب را دوباره به‌خوبی ورز دهید و از انسجام کامل آن مطمئن شوید. اگر زمان دارید مایه گوشت چرخ کرده را یک ساعت در یخچال استراحت دهید. حالا، به اندازه یک نارنگی متوسط از مایه گوشت کوفته برنجی خرمایی را بردارید و با کمک هر دو دست، کاملا گرد کنید. یادتان باشد، در وسط هر یک از این کوفته‌ها باید ۲ عدد خرمای بدون هسته را جاگذاری کنید. اکنون باید کوفته‌های گرد و آماده شده را در قابلمه حاوی سس در حال جوش قرار دهید و حدود ۴۰ دقیقه منتظر بمانید تا کاملا مغز پخت شوند. توجه داشته باشید، لازم است درب قابلمه را باز بگذارید و حرارت را کاملا ملایم کنید تا کوفته برنجی‌های شما وا رفته و شل نشوند و ترک بر ندارند. قابلمه حاوی کوفته برنجی خرمایی را در حالی از روی حرارت بردارید که سس کوفته به اندازه نصف یک لیوان دسته‌دار فرانسوی آب داشته باشد. سپس، کوفته‌ها را با نان و چاشنی دلخواه میل کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/07/%DA%A9%D9%88%D9%81%D8%AA%D9%87-%D8%A8%D8%B1%D9%86%D8%AC%DB%8C-%D8%AE%D8%B1%D9%85%D8%A7%DB%8C%DB%8C-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (193, 'کوفته برنجی ژاپنی (اونیگیری)', 'در ابتدا برای درست کردن کوفته برنجی سفید ژاپنی، برنج را پاک کنید و در یک کاسه بریزید. برنج را با مقداری آب بشویید تا نشاسته اضافی آن گرفته شود. سپس برنج را در یک قابلمه بریزید و آب را به آن اضافه کنید', 'در ابتدا برای درست کردن کوفته برنجی سفید ژاپنی، برنج را پاک کنید و در یک کاسه بریزید. برنج را با مقداری آب بشویید تا نشاسته اضافی آن گرفته شود. سپس برنج را در یک قابلمه بریزید و آب را به آن اضافه کنید. قابلمه را روی حرارت بگذارید و اجازه دهید آب شروع به جوشیدن کند. بعد از جوشیدن آب، شعله گاز را کم کنید و درب قابلمه را ببندید تا برنج به مدت ۱۵ تا ۲۰ دقیقه بپزد و آب اضافی آن بخار شود. سپس شعله را خاموش کنید ولی درب قابلمه را باز نکنید. صبر کنید تا به مدت ۱۰ دقیقه، برنج در همین حالت دم بکشد. در این فاصله، کنسرو تن ماهی را در یک قابلمه آب جوش بگذارید و به مدت ۲۰ دقیقه بجوشانید تا باکتری‌های مضر آن از بین بروند. بعد از دم کشیدن برنج و جوشیدن تن ماهی، اجازه دهید هر دو کمی سرد شوند. تن ماهی را در یک ظرف بریزید و با چنگال آنها را بفشارید تا لایه‌های ماهی از هم باز شوند. جلبک نوری یا جلبک دریایی را با قیچی به شکل مستطیلی یا مربعی برش بزنید و کنار بگذارید. در یک ظرف هم سیاه‌دانه یا کنجد را بریزید. بعد از اینکه برنج‌ها کمی سرد شدند، حالت شفتگی آن‌ها بیشتر می‌شود. به اندازه ۱ قاشق غذاخوری پر، از برنج بردارید و کف دستتان بگذارید. اگر برنج به دستتان می‌چسبد، برای جلوگیری از چسبیدن برنج، یک کاسه آب کنار دستتان بگذارید. کف دستتان را با آب خیس کنید و نوک انگشتانتان را به نمک آغشته کنید و نمک را کف دست‌هایتان بمالید. برنج را بین دست‌هایتان گلوله کنید. بعد از گلوله کردن کوفته‌ها، وسط کوفته اونیگیری را با انگشتتان باز کنید و از تن ماهی، داخل سوراخ بریزید. دوباره کوفته را بین دست‌هایتان بغلتانید تا مواد کوفته داخل برنج فرو برود. بعد کوفته‌ها را به شکل مثلث در آورید. در قسمت پایین مثلث یا اطراف کوفته را جلبک دریایی بگذارید. دورتادور کوفته را در سیاه‌دانه یا کنجد بزنید. کوفته‌‌های برنجی ژاپنی را در یک ظرف جداگانه بگذارید. همه کوفته‌ها را به همین شکل آماده کنید. سپس آنها را سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/471.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (194, 'کوفته برنجی یا کوفته کشک اصفهانی', 'ابتدا به مقدار دلخواه پیاز داغ مجلسی درست کنید. سپس یک عدد از پیازها را با رنده ریز رنده کنید و آب پیاز را خوب بگیرید. پیاز رنده شده را با گوشت چرخکرده و گوشت مرغ چرخکرده مخلوط کنید و خوب ورز دهید.', 'ابتدا به مقدار دلخواه پیاز داغ مجلسی درست کنید. سپس یک عدد از پیازها را با رنده ریز رنده کنید و آب پیاز را خوب بگیرید. پیاز رنده شده را با گوشت چرخکرده و گوشت مرغ چرخکرده مخلوط کنید و خوب ورز دهید. سبزی‌های ساطوری خرد شده و تخم مرغ را هم اضافه کنید و با اضافه کردن نمک، فلفل و زردچوبه حسابی گوشت را ورز دهید. باید آنقدر گوشت را ورز دهید که وقتی با یک دست گوشت را از داخل ظرف برمی‌دارید از هم نباشد و به عبارت دیگر با یک حرکت دست جمع شود. در اواسط ورز دادن گوشت برنج را اضافه کنید که در اثر ورز دادن گوشت خیلی له نشود. از مایه کوفته برای هر عدد کوفته به اندازه یک نارنگی بردارید و کمی زرشک و گردو، یک عدد آلو بخارا و پیازداغ وسط آن بریزید و گرد کنید. کمی هم کوفته را فشار دهید تا حباب‌های هوا از آن خارج شوند. کوفته‌های آماده شده را قبل از پخت به مدت ۲ ساعت در یخچال بگذارید تا خوب خودشان را بگیرند. بقیه پیازها را هم رنده کنید و آب پیازها را بگیرید. پیاز را در مقداری روغن تفت دهید. در این فاصله سیب زمینی‌ها را رنده کنید، آب‌شان را بگیرید. وقتی پیاز طلایی شد نعنا خشک را اضافه کنید. وقتی نعنا تفت خورد (مراقب باشید نسوزد چون تلخ می‌شود) سیب‌زمینی‌ها را اضافه کنید و صبر کنید به دمای جوش برسد و قل فل کند. مایه که شروع به قل قل کرد کوفته‌ها را یکی یکی اضافه کنید. وقتی کوفته‌ها کاملا سرد و سس کاملا داغ باشد کوفته‌ها از هم نمی‌پاشند. به مدت ۳۰ دقیقه درب ظرف را روی حرارت ملایم باز بگذارید و به مدت ۲۰ دقیقه هم درب ظرف را ببندید. بعد از این مدت کشک را هم اضافه کنید. مقدار کشک به دلخواه شماست اما مراقب باشید که کشک بیشتر از ۱۵ دقیقه نجوشد. روی کوفته‌ قلقلی برنجی را با پیازداغ و گردو تزیین کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/06/%DA%A9%D9%88%D9%81%D8%AA%D9%87-%D8%B3%D8%A8%D8%B2%DB%8C.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (195, 'کوفته برنجی یزدی', 'کوفته برنجی یزدی یکی از غذاهای اصالتا یزدی است که شباهت زیادی به کوفته تبریزی دارد، فقط میزان و طعم برنج در آن غالب است. در این کوفته حتما از برنج ایرانی استفاده کنید تا کیفیت طعم آن به بالاترین حد بر', 'کوفته برنجی یزدی یکی از غذاهای اصالتا یزدی است که شباهت زیادی به کوفته تبریزی دارد، فقط میزان و طعم برنج در آن غالب است. در این کوفته حتما از برنج ایرانی استفاده کنید تا کیفیت طعم آن به بالاترین حد برسد. کوفته برنجی یزدی علاوه بر طعم خوشمزه، بسیار مقوی است. قبل از شروع به آشپزی از روز قبل لپه‌ها را پاک کنید و بشویید. روی لپه‌ها مقداری آب بریزید و به مدت ۸ تا ۱۲ ساعت خیس کنید تا هم نرم‌تر شده و هم نفخ آن‌ها گرفته شود. برنج را هم پاک کنید و ۱ ساعت قبل از شروع آشپزی، در مقداری آب نمک بخیسانید. سبزی‌های معطر شامل ترخون، مرزه، جعفری، نعناع، پیازچه و تره را هم پاک کرده و بشویید. سپس به صورت ریز و ساطوری خرد کنید. در شروع آشپزی، لپه‌ها را از آب بکشید و در یک قابلمه با مقداری آب بریزید و روی حرارت بگذارید. اجازه دهید یک بار آب بجوشد و ۲ یا ۳ دقیقه بعد که روی لپه‌ها، کف ایجاد شد، لپه‌ها را آبکش کنید. دوباره لپه‌ها را در قابلمه بریزید و آب تازه روی لپه‌ها بریزید. دوباره محتویات قابلمه را بجوشانید. درب قابلمه را روی آن بگذارید تا لپه‌ها کاملا بپزند. بعد از پختن لپه‌ها، آنها را آبکش کنید. برنج را هم در این فاصله بعد از ۱ تا ۲ ساعت خیس خوردن، آبکش کنید. برنج‌ها را در یک قابلمه بریزید و مقداری آب اضافه کنید طوری که روی برنج را بپوشاند. سپس قابلمه برنج را روی حرارت بگذارید تا برنج بپزد و حسابی نرم شود. بعد از اینکه برنج نرم و شکفته شد، آن را آبکش کنید. در یک قابلمه، گوشت چرخ کرده، سبزی کوفته تازه ساطوری شده، برنج و لپه پخته و آبکش شده، آرد نخودچی، زردچوبه، کمی نمک و فلفل سیاه را اضافه کنید. اگر سبزی معطر تازه ندارید، می‌توانید از سبزی معطر خشک استفاده کنید. با گوشتکوب دستی، مواد کوفته را بکوبید تا یکدست شود. می‌توانید از گوشت‌کوب برقی یا غذاساز هم برای میکس و یکدست کردن مواد استفاده کنید. دستکش یک‌بار مصرف بپوشید و شروع به ورز دادن مواد کوفته با هم بکنید. هر چه مواد را بیشتر ورز بدهید، انسجام مواد کوفته بیشتر شده و از پاشیدن آن در هنگام پخت جلوگیری می‌شود. اگر مواد کوفته به دستتان می‌چسبد، کمی دیگر آرد نخودچی اضافه کنید. مواد کوفته باید کاملا به هم پیوسته باشند ولی به دست نچسبند. حدود ۱۰ تا ۱۵ دقیقه مواد را ورز دهید یا ۲ تا ۳ بار، مواد را چرخ کنید. برای اینکه کوفته برنجی یزدی را فرم دهید، ابتدا یک کاسه آب ولرم کنار دستتان بگذارید و کف دستتان را با آب مرطوب کنید. به اندازه یک نارنگی از مواد کوفته بردارید و بین دست‌هایتان گلوله کنید. کمی داخل مایه کوفته را گود کنید و مقداری زرشک، یک آلو، گردو یا مقداری پیاز داغ را داخل حفره کوفته بریزید. سپس مایه کوفته را به هم بچسبانید تا مواد داخل کوفته مشخص نباشند. دوباره کوفته را بین دست‌هایتان گلوله کنید و در یک سینی بگذارید. همه کوفته‌ها را به همین شکل آماده کنید. کوفته‌ها را در یخچال بگذارید تا خودشان را بیشتر بگیرند. پیاز را پوست بگیرید و به صورت نگینی ریز یا خلالی باریک خرد کنید. حتی می‌توانید پیاز را رنده کنید. در یک تابه عمیق و با قطر زیاد، مقداری روغن بریزید و روی حرارت بگذارید تا روغن داغ شود. سپس پیازها را در تابه بریزید و تفت دهید تا سبک و طلایی شوند. بعد ۱ قاشق رب گوجه فرنگی را هم اضافه کنید و تفت دهید تا رب رنگ باز کند و از خامی بیفتد. قبل از اینکه رب گوجه فرنگی بسوزد، کمی زردچوبه و فلفل سیاه اضافه کنید و با رب تفت دهید. بعد چند لیوان، آب جوش به تابه بیفزایید و روی حرارت بجوشانید تا قل قل کند. شعله گاز را کم کنید و کوفته‌ها را در سس بچینید، طوری که روی هم نباشند و کاملا به هم نچسبند. مقدار آب باید در حدی باشد که روی کوفته‌ها را بپوشاند. دوباره شعله را زیاد کنید تا آب، جوش بیاید. بعد حرارت را کم کنید و درب قابلمه را روی آن بگذارید. حدود ۱ و نیم ساعت کوفته‌ها را روی حرارت ملایم بپزید تا کاملا بپزند و جا بیفتند. یک ربع قبل از خاموش کردن شعله گاز، سس کوفته را بچشید و به میزان دلخواهتان، نمک و فلفل سیاه اضافه کنید. زعفران دم کرده را هم در ۱۰ دقیقه پایانی پخت کوفته در سس بریزید تا عطر و طعم آن حفظ شود. کوفته‌های برنجی که پختند، آنها را به آرامی در ظرف سرو بگذارید. می‌توانید سس کوفته را داخل ظرف سرو یا در یک ظرف جدا در کنار کوفته‌های برنجی سرو کنید. کوفته برنجی یزدی را با نان سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/05/koofteh.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (196, 'کوفته بلغور', 'ابتدا بلغورها را پاک کنید و در یک کاسه بریزید. روی بلغورها آب جوش بریزید که روی آنها را کمی بپوشاند. سپس کمی نمک در آب جوش بریزید و با قاشق هم بزنید تا نمک و آب با بلغور ترکیب شود. روی کاسه را درپوش ب', 'ابتدا بلغورها را پاک کنید و در یک کاسه بریزید. روی بلغورها آب جوش بریزید که روی آنها را کمی بپوشاند. سپس کمی نمک در آب جوش بریزید و با قاشق هم بزنید تا نمک و آب با بلغور ترکیب شود. روی کاسه را درپوش بگذارید یا سلفون بکشید تا بلغورها آب را به خود بکشند و کاملا به شکل دم کشیده دربیایند و نرم شوند. بعد از نیم ساعت که بلغورها دم کشیدند، آنها را در آبکش بریزید. سپس بلغورها را در یک کاسه بریزید. اگر بلغورهایتان درشت هستند، آنها را در غذاساز بریزید و پوره کنید یا با گوشت‌کوب دستی یا برقی بکوبید. سپس بلغورها را در یک کاسه بریزید و گوشت چرخ کرده را به آن اضافه کنید. مخلوط را هم بزنید تا یکدست شوند. در ادامه، یک تخم مرغ را در یک کاسه جدا بشکنید و با چنگال هم بزنید تا زرده و سفیده با هم ترکیب شوند. تخم مرغ همزده، آرد گندم، ۱ قاشق سس فلفل یا رب گوجه فرنگی، ادویه‌ها شامل فلفل سیاه، پودر پاپریکا، پودر سیر، پودر پیاز، ادویه یا سبزی خشک دلخواه خود را به مایه کوفته اضافه کنید. با دستکش یکبار مصرف، همه مواد کوفته را با دست ورز دهید تا کاملا مخلوط و منسجم شوند. اگر مایه کوفته شل بود، کمی دیگر آرد اضافه کنید. یک کاسه آب دم دستتان بگذارید و به اندازه یک نارنگی از مایه کوفته بلغور بردارید و بین دست‌هایتان گلوله کنید. گلوله‌های کوفته را به همین شکل و در اندازه‌های یکدست درست کنید. گلوله‌های کوفته را در یک ظرف یا سینی بگذارید. در یک تابه عمیق یا یک قابلمه که کف پهنی دارد، کره را بریزید و روی حرارت بگذارید. کره که ذوب شد، ۲ قاشق غذاخوری رب گوجه فرنگی اضافه کنید و تفت دهید تا رب رنگ باز کند و طعم خام آن گرفته شود. سپس مقداری آب جوش به آن اضافه کنید. صبر کنید تا آب شروع به حباب زدن از اطراف کند. سپس به آرامی، کوفته‌ها را در سس در حال جوشیدن بگذارید؛ طوری که روی هم قرار نگیرند. درب قابلمه را باز بگذارید. شعله را روی حالت ملایم تنظیم کرده و حدود ۲۰ تا ۳۰ دقیقه صبر کنید تا کوفته‌ها بپزند. وقتی کوفته‌ها پختند، آنها را از قابلمه یا تابه بیرون بیاورید و در ظرف سرو بگذارید. سس کوفته را همراه با کوفته‌ها یا در کنار آن سرو کنید. روی کوفته‌ بلغور را می‌توانید با سبزی دلخواه تزیین و با نان سرو کنید. در دستور تهیه کوفته بلغور، استفاده از گوشت چرخ کرده اختیاری است، می‌توانید گوشت را از مواد کوفته بلغور حذف کنید. در این صورت مانند روش بالا، بلغور را با آب جوش و نمک دم کرده و نمک و ادویه‌ها را به آن اضافه کنید و ورز دهید تا یکدست شود. کوفته‌ها را به شکل گرد در بیاورید. وسط کوفته‌ها را می‌توانید از مواد دلخواه مثل آلو، کشمش، گردو، زرشک و … بگذارید و کوفته‌ها را ببندید و دوباره گرد کنید. همه کوفته‌های بلغور را که فرم دادید، آنها را به روش بالا بپزید. برای سوخاری کردن کوفته‌های بلغور با گوشت یا بدون گوشت، تخم مرغ را جداگانه در یک کاسه بشکنید و هم بزنید تا سفیده از لختگی خارج شود. کوفته‌های بلغور را به روش‌های بالا (با گوشت یا بدون گوشت) درست کنید. سپس کوفته‌ها را در مخلوط تخم مرغ بچرخانید. بعد کوفته را بیرون بیاورید و در پودر سوخاری بغلتانید تا پودر به همه جای کوفته بچسبد. همه کوفته‌ها را به همین شکل آماده کنید. بعد کوفته‌های بلغور گندم را در یک تابه با مقداری روغن یا کره سرخ کنید. در یک ظرف دیگر، سس کوفته را درست کنید و پس از سرخ شدن کوفته‌های بلغور، آنها را در سس قرار دهید تا به مدت ۲۰ تا ۳۰ دقیقه بپزند. یکی دیگر از موادی که برای انسجام بیشتر کوفته بلغور استفاده می‌شود، سیب زمینی است. در این روش، علاوه بر گوشت چرخ کرده، بلغور و ادویه و چاشنی‌ها، ۲ تا ۳ سیب زمینی آبپز و پوره شده هم به مایه کوفته بلغور اضافه می‌شود. در ضمن دیگر نیازی به استفاده از آرد نیست. کوفته بلغور با سیب زمینی را که فرم دادید، سس را جداگانه درست کنید و کوفته‌ها را در آن بپزید. برای نرم‌تر کردن مایه کوفته بلغور، یک تخم مرغ را جداگانه هم بزنید و به مایه کوفته بیفزایید و دوباره مواد را هم بزنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/07/jhgfdsa.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (197, 'کوفته بوقلمون و گیلاس', 'گوشت بوقلمون بدون استخوان یا فیله را چرخ کنید و در ظرف مناسبی بریزید. سیر و پوست لیمو را رنده کنید. پیاز را رنده کرده و آب آن را بگیرید، سپس پیاز رنده شده را به گوشت اضافه کنید.', 'گوشت بوقلمون بدون استخوان یا فیله را چرخ کنید و در ظرف مناسبی بریزید. سیر و پوست لیمو را رنده کنید. پیاز را رنده کرده و آب آن را بگیرید، سپس پیاز رنده شده را به گوشت اضافه کنید. تخم مرغ، پودر سوخاری، پودر فلفل سیاه، نمک، آویشن خشک، ریحان خشک و فلفل قرمز را به مایه کوفته اضافه کنید و مواد را کاملا ورز دهید تا یکدست شوند. هر چه بیشتر ورز دهید، احتمال وا رفتن کوفته کمتر خواهد بود. مایه کوفته را در وسط ظرف جمع کرده و روی آن را بپوشانید. ظرف را به مدت ۳۰ دقیقه تا ۱ ساعت در یخچال قرار دهید تا مواد منسجم شوند و طعم یکدیگر را بگیرند. قبل از درست کردن کوفته‌ها، فر را روشن کرده و روی دمای ۲۰۰ درجه سانتی‌گراد تنظیم کنید تا گرم شود. به اندازه یک گردو از مایه کوفته بردارید و به شکل کوفته قلقلی در آورید. وسط هر کوفته یک گیلاس خشک قرار دهید. همه کوفته‌ها را به همین شکل آماده کنید. در یک تابه مقداری روغن بریزید و روی حرارت قرار دهید تا داغ شود. کوفته‌ها را در روغن سرخ کنید تا طلایی شوند. کوفته‌های سرخ شده را در سینی فر بچینید و در صورت دلخواه مقداری پنیر چدار یا پنیر پیتزا روی آنها بپاشید. سینی را در طبقه وسط فر قرار دهید و به مدت ۱۰ تا ۱۵ دقیقه صبر کنید تا کوفته‌ها مغز پخت شوند. کوفته‌ها را از فر بیرون آورده و در ظرف بچینید. روی آنها چند برگ ریحان بگذارید و با سس ورشتایر، سس خردل یا سس دلخواه سرو کنید. برای پخت در قابلمه: کوفته‌ها را مانند روش قبل آماده کنید (ورز دادن، استراحت در یخچال، قرار دادن گیلاس خشک و سرخ کردن). در یک قابلمه، مقداری رب گوجه یا رب انار (یا مخلوطی از هر دو) را به اندازه دلخواه (مثلاً یک قاشق از هر کدام) در یک لیوان آب جوش حل کنید. سس را روی حرارت قرار دهید تا شروع به جوشیدن کند. کوفته‌های سرخ شده را به آرامی در سس قرار دهید و درب قابلمه را بگذارید. حرارت را روی متوسط تنظیم کنید و اجازه دهید کوفته‌ها به مدت حدود ۲۰ تا ۳۰ دقیقه در سس بپزند. پس از مغز پخت شدن کوفته‌ها و غلیظ شدن سس، آنها را در ظرف مناسب سرو کرده و با سبزی یا سس دلخواه تزیین کنید.', 25, 50, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/09/Rosemary-Turkey-Meatballs-Cranberry-Balsamic-Sauce-Pic-680x921-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (198, 'کوفته بوقلمون', 'برای تهیه کوفته بوقلمون رژیمی، ابتدا نخود و برنج را جداگانه بپزید و چند بار چرخ کنید تا حالت پوره و چسبنده پیدا کنند. حدود یک لیوان نخود خشک را از شب قبل بخیسانید (حداقل ۸ ساعت). سپس آبکش کرده و با آب', 'برای تهیه کوفته بوقلمون رژیمی، ابتدا نخود و برنج را جداگانه بپزید و چند بار چرخ کنید تا حالت پوره و چسبنده پیدا کنند. حدود یک لیوان نخود خشک را از شب قبل بخیسانید (حداقل ۸ ساعت). سپس آبکش کرده و با آب تازه بپزید تا کاملاً نرم شوند (۱ تا ۱.۵ ساعت). نخودهای پخته را چند بار چرخ کنید تا پوره و چسبنده شوند. حدود نصف لیوان برنج ایرانی را بشویید و با کمی نمک و آب نیم‌پز کنید. سپس آبکش کرده و اجازه دهید کمی خنک شود. برنج را چند بار چرخ کنید تا حالت خمیری پیدا کند. نخود و برنج چرخ‌شده را در یک کاسه بزرگ کنار بگذارید. ۳۰۰ گرم گوشت بوقلمون چرخ‌کرده را در یک کاسه بریزید. یک عدد پیاز متوسط را ریز رنده کرده و آب آن را کامل بگیرید. پیاز آب‌گرفته را به گوشت اضافه کنید. دو حبه سیر له شده را همراه با زردچوبه، نمک، فلفل سیاه و کمی ادویه دلخواه (مانند ادویه کوفته یا کاری ملایم) روی گوشت بریزید. همه مواد را با دست خوب ورز دهید تا کاملاً با هم ترکیب شوند و گوشت مزه‌دار شود. ظرف گوشت را بپوشانید و حدود نیم ساعت در یخچال استراحت دهید تا طعم‌ها به خورد گوشت بروند و بافت آن بهتر شود. بعد از استراحت گوشت، آن را از یخچال خارج کرده و به مخلوط نخود و برنج اضافه کنید. سبزی خشک معطر (مانند نعناع، مرزه یا ترخون) را نیز اضافه کنید. دوباره کمی نمک، فلفل و ادویه بزنید تا طعم نهایی تنظیم شود. حالا همه مواد را با دست حدود ۱۰ دقیقه ورز دهید تا یک مایه منسجم و چسبنده داشته باشید. اگر مایه کوفته به اندازه کافی نمی‌چسبد یا احتمال وا رفتن هست، یک عدد تخم‌مرغ و کمی آرد نخودچی اضافه کرده و دوباره ورز دهید تا منسجم شود. برای فرم دادن کوفته‌ها، به اندازه یک نارنگی کوچک از مواد بردارید و کف دستتان بچرخانید تا گرد شود. سپس یک حفره درست کنید و داخل آن یک تکه گردوی خرد شده، چند عدد آلوچه و کمی کشمش بگذارید. با دقت آن را گلوله کنید و مطمئن شوید ترک نداشته باشد. اگر ترک داشت، با دست کمی فشار دهید تا صاف شود. کوفته‌های آماده را در سینی بچینید و حدود نیم ساعت در یخچال بگذارید تا فرم بگیرند و آماده پخت شوند. برای درست کردن سس کوفته بوقلمون، یک عدد پیاز بزرگ را خرد کنید و در قابلمه با کمی روغن تفت دهید تا طلایی شود. سپس زردچوبه، ادویه خورشتی، پودر شنبلیله و رب گوجه‌فرنگی را اضافه کرده و خوب تفت دهید تا رنگ رب باز شود و مواد با هم ترکیب شوند. سپس چند لیوان آب جوش بریزید و بگذارید سس به جوش بیاید. کوفته‌ها را با احتیاط داخل قابلمه بگذارید، طوری که آب تا لبه کوفته‌ها برسد ولی روی آنها را نپوشاند. چند دقیقه با در باز بگذارید بجوشند تا خودشان را بگیرند. سپس در قابلمه را ببندید و حرارت را کم کنید. حدود ۱.۵ تا ۲ ساعت زمان دهید تا کوفته‌ها خوب بپزند و طعم سس را به خود بگیرند. در یک ساعت پایانی پخت، آلوچه‌های خورشتی را بشویید و اضافه کنید تا طعم ترش و خوشمزه‌ای به کوفته‌ها بدهند. چاشنی‌ها و نمک کوفته‌ها را اندازه کنید. وقتی سس کوفته قوام گرفت و جا افتاد، شعله را خاموش کنید و کوفته بوقلمون را با چلو آبکش یا چلو کته یا نان، سبزی خوردن، ترشی و سالاد دلخواه سرو کنید. برای تهیه کوفته بوقلمون در فر، ابتدا نخود و برنج پخته و چرخ‌شده را با گوشت بوقلمون، پیاز آب‌گرفته، سیر، ادویه، سبزی خشک، تخم‌مرغ و آرد نخودچی خوب ورز دهید تا مایه منسجم شود. کوفته‌ها را گرد کنید و داخلشان مواد دلخواه مثل آلو، گردو یا کشمش بگذارید. کوفته‌ها را در سینی فر بچینید، کمی روغن یا اسپری روغن بزنید و با دمای ۱۸۰ درجه سانتی‌گراد حدود ۳۰ تا ۴۵ دقیقه بپزید تا طلایی و پخته شوند. در آخر می‌توانید کوفته بوقلمون را در کنار سس گوجه یا ماست سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/08/o6bgfkl8d1dsli37h0tg6qj68fck9mh0E8Bjy5hROKWNODMx.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (199, 'کوفته پسته مجلسی', 'در اولین مرحله از طرز تهیه کوفته پسته، پیاز را پوست بگیرید و بشویید. سپس آن را به وسیله رنده به صورت کاملا ریز رنده کنید و با فشار دست آب اضافه پیاز رنده شده را بگیرید و در یک ظرف گود و نسبتا بزرگ بری', 'در اولین مرحله از طرز تهیه کوفته پسته، پیاز را پوست بگیرید و بشویید. سپس آن را به وسیله رنده به صورت کاملا ریز رنده کنید و با فشار دست آب اضافه پیاز رنده شده را بگیرید و در یک ظرف گود و نسبتا بزرگ بریزید. مغز گردو و مغز پسته را نیز به وسیله رنده ریز، آسیاب یا گوشتکوب آنقدر خرد کنید تا به شکل پوره و پودر پسته و پودر گردو در بیایند و سپس آنها را نیز به ظرف حاوی پیاز اضافه کنید. حتی می‌توانید پیاز را چهار قاچ کنید و به همراه مغز پسته و مغز گردو در غذاساز بریزید و همه را با هم آسیاب کنید تا یک مایه یکدست درست شود. گوشت چرخ کرده را به همراه ادویه‌ها که شامل نمک، فلفل سیاه و پودر زیره هستند و همچنین آبلیمو و آرد سوخاری و تخم مرغ به کاسه حاوی پودر پسته، پودر گردو و پیاز رنده شده اضافه کنید و همه مواد را خوب با هم مخلوط کنید تا طعم یکدیگر را بگیرند. تمام موادی را که در مرحله پیش با هم ترکیب کردید باید به خوبی ورز دهید. ورز دادن را باید تا جایی ادامه دهید که ترکیب مواد به بافتی یکدست و منسجم و چسبنده برسند. ورز دادن مواد مرحله بسیار مهمی در پخت کوفته پسته و گردو است چون اگر ورز دادن به خوبی انجام نشود کوفته هنگام پخت وا می‌رود. بعد از ورز دادن روی کاسه حاوی مواد کوفته پسته را سلفون بکشید و آن را برای نیم ساعت تا یک ساعت در یخچال قرار دهید تا استراحت کنند. بعد از آنکه مایه کوفته به خوبی استراحت کرد آن را از یخچال بیرون بیاورید. برای شکل دادن به کوفته پسته هر بار به اندازه یک نارنگی کوچک از مواد کوفته بردارید و با دست به صورت گرد دربیاورید. این کار را تا جایی ادامه دهید تا تمام مایه کوفته تمام شود. یک تابه با سایز مناسب انتخاب کنید و با مقدار کافی از روغن روی حرارت متوسط قرار دهید. بعد از آنکه روغن داغ شد، کوفته‌ها را به تابه اضافه کنید و اجازه دهید تا به خوبی از همه طرف سرخ شوند. بعد از آنکه تمام کوفته‌ها سرخ شدند آن را از تابه خارج کنید. یک عدد پیاز متوسط را بشویید و پوست بگیرید و به صورت نگینی ریز خرد کنید. پیازها را در همان تابه‌ای که کوفته‌ها را سرخ کردید تفت دهید تا سبک و طلایی شوند. در فاصله‌ای که پیازها سبک و طلایی می‌شوند گوجه‌ها را بشویید و پوست بگیرید و به صورت نگینی خرد کنید. گوجه‌ها را به پیاز سبک و طلایی شده اضافه کنید و آنقدر تفت دهید تا به روغن بیفتد. ادویه‌ها را به همراه نصف لیوان آب گرم به آن اضافه کنید و اجازه دهید به جوش بیاید. بعد از آنکه سس گوجه به جوش آمد، حرارت را کم کنید، کوفته‌ها را به آرامی در سس گوجه بچینید و درب تابه را ببندید. اجازه دهید کوفته‌های پسته با حرارت ملایم حدود ده دقیقه در سس گوجه فرنگی بپزند تا کاملا مغز پخت شوند. کوفته‌های مغزپخت شده را در یک ظرف مناسب بریزید و به همراه سس گوجه فرنگی سرو کنید. کوفته پسته لذیذ و خوشمزه را می‌توانید در کنار نان تازه و سبزی خوردن سرو کنید و از طعم بی‌نظیرش لذت ببرید. برای تهیه سس انار، یک عدد پیاز را پوست بگیرید و بشویید و به صورت نگینی خرد کنید. پیازها را در همان تابه‌ای که کوفته‌ها را سرخ کردید، تفت دهید تا سبک و طلایی شوند. رب انار و رب گوجه فرنگی را به پیازهای طلایی شده اضافه کنید و تفت دهید تا عطر آنها بلند شود. یک لیوان آب انار را به همراه نمک و فلفل به دیگر محتویات تابه اضافه کنید و اجازه دهید تا به جوش بیایند. بعد از به جوش آمدن محتویات درون تابه، حرارت را کم کنید و کوفته‌های پسته را در آن بچینید. درب تابه را ببندید و اجازه دهید کوفته‌ پسته حدود ده دقیقه با حرارت ملایم مغز پخت شود. بعد از حدود ده دقیقه کوفته پسته با سس انار آماده است، آن را در ظرف مناسب بریزید و سرو کنید. می‌توانید کوفته پسته با سس انار را با دانه‌های انار تزیین کنید. برای تهیه سس مخصوص کوفته پسته، در ابتدا ماست یونانی را در یک ظرف مناسب بریزید و با چنگال بهم بزنید تا بافت آن یکدست شود. آبلیمو، روغن زیتون و سماق را به ماست همزده اضافه کنید و همه مواد را به خوبی با هم مخلوط کنید تا مزه‌ها به خورد هم بروند. سس مخصوص با ماست یونانی را در یخچال قرار دهید و به سراغ تهیه کوفته پسته بروید. این سس بر خلاف سس‌های دیگر به صورت سرد در کنار کوفته پسته سرو می‌شود.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/08/koufte-peste.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (200, 'کوفته تبریزی با سبزی خشک', 'ابتدا برای مواد میانی کوفته با یکی از پیازها، پیاز داغ درست کنید. پیازها را خلالی نازک خرد کنید و در مقداری روغن سرخ کنید. زرشک‌ها را هم بشویید و آبکش کنید و در صورت تمایل کمی در کره تفت دهید.', 'ابتدا برای مواد میانی کوفته با یکی از پیازها، پیاز داغ درست کنید. پیازها را خلالی نازک خرد کنید و در مقداری روغن سرخ کنید. زرشک‌ها را هم بشویید و آبکش کنید و در صورت تمایل کمی در کره تفت دهید. برای درست کردن کوفته تبریزی در ابتدا باید دو بار گوشت را چرخ کنید. لپه‌های نیم‌پز شده را هم چرخ کنید و کنار بگذارید. برنج را با مقداری نمک و کمی زعفران دم کرده، در مقداری آب بجوشانید تا نیم‌پز شوند. نیازی به اضافه کردن روغن به برنج نیست. سپس آن را آبکش کنید. پیاز را با رنده درشت رنده کنید. تفاله پیاز را نگه دارید. در یک کاسه، گوشت، برنج و لپه نیم‌پز شده را بریزید. سبزی خشک و پیاز رنده شده را به گوشت اضافه کنید و خوب ورز بدهید. ادویه‌ کوفته تبریزی، آرد نخودچی، تخم مرغ و نمک را هم به گوشت و سبزی اضافه کنید و باز هم ورز دهید. هر چه بیشتر گوشت کوفته را ورز بدهید امکان وا رفتن کوفته تبریزی کمتر می‌شود. حداقل ۱۵ دقیقه برای ورز دادن گوشت کوفته وقت بگذارید. به اندازه‌ای که می‌خواهید کوفته‌ها را درست کنید، از مایه گوشت بردارید و آن را گرد کنید. به آرامی وسط گوشت را باز کنید و مواد میانی کوفته را به اندازه دلخواه در آن بگذارید. داخل کوفته‌ها، مقداری زرشک، یک عدد مغز گردو، یک عدد آلو و کمی از پیاز داغی که درست کردید بریزید. کوفته را ببندید و دوباره گرد کنید. تا تمام شدن مواد و درست شدن تمام کوفته‌ها، این کار را تکرار کنید. در یک قابلمه مناسب ۱ عدد پیاز را خلالی باریک خرد کنید و در مقداری روغن تفت دهید. پیازها که سبک شدند، رب گوجه را به همراه نمک، فلفل قرمز، فلفل سیاه، نمک، زعفران دم کرده و زردچوبه اضافه کنید و کمی تفت دهید تا بوی خام رب و ادویه‌ها گرفته شود. در صورت دلخواه کمی مرزه خشک و زرشک (و دیگر مواد میانی) را هم به این مواد اضافه کنید. به این مواد به اندازه‌ای که لازم است، آب اضافه کنید و اجازه دهید سس بجوشد. (با توجه به تعداد و سایز کوفته‌ها، آب اضافه کنید، میزان آب باید به قدری باشد که کوفته‌ها تا نیمه در آب باشند) سپس کوفته‌ها را در سس در حال جوشیدن بگذارید و بعد شعله را روی حالت ملایم گاز تنظیم کنید. دقت کنید که در هنگام اضافه کردن کوفته‌ها به سس، شعله گاز بسیار ملایم باشد و سس کوفته در حال قل خوردن نباشد، چون کوفته‌ها وا می‌روند. درب قابلمه را باز بگذارید تا کوفته‌ها خوب بپزد و جا بیفتد. یک ساعت زمان برای پختن کوفته‌ تبریزی کافی است. بهتر است بعد از نیم ساعت به آرامی کوفته‌ها را برگردانید تا هر دو طرف خوب به سس و چاشنی کوفته آغشته شده و بپزند. یا در زمان پخت، چند بار از مایه سس، روی کوفته‌ها بریزید. کوفته تبریزی که پخت آن را با ترشی لیته، سیر ترشی، سبزی خوردن و نان (ترجیحا نان سنگک) میل کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/04/Koofteh_tabrizi-1-min-1024x848.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (201, 'کوفته تبریزی گیاهی با سویا', 'آلوها را در آب ولرم خیس کنید. برنج و لپه را جداگانه نیم‌پز کنید. سویا را با آب و یک یا دو عدد سیر و کمی زردچوبه ۵ دقیقه بجوشانید، سپس آبکش کرده و آب اضافی آن را با دست خارج کنید.', 'آلوها را در آب ولرم خیس کنید. برنج و لپه را جداگانه نیم‌پز کنید. سویا را با آب و یک یا دو عدد سیر و کمی زردچوبه ۵ دقیقه بجوشانید، سپس آبکش کرده و آب اضافی آن را با دست خارج کنید. مواد کوفته شامل برنج و لپه نیم پز شده و سویای خیسانده شده را چرخ کنید تا کاملا به شکل پوره درآیند. یکی از پیازها را با رنده درشت رنده کنید. آب پیاز رنده شده را بگیرید و تفاله پیاز را به دیگر مواد چرخ شده اضافه کنید و همه مواد را با هم ترکیب کنید. آب پیاز را برای سس کوفته نگه دارید. به مواد چرخ شده کوفته تبریزی، یک عدد تخم مرغ، زرشک، آرد نخودچی، سبزیجات معطر خردشده، زردچوبه، نمک و فلفل سیاه اضافه کرده و مخلوط کنید. مایه کوفته تبریزی را با دست حسابی ورز بدهید تا تمام مواد خوب به هم بچسبند و یکدست شوند. مایه کوفته تبریزی را به مدت یک ساعت در ظرفی در پوشیده در یخچال قرار بدهید تا استراحت کند. برای درست کردن سس کوفته، پیاز باقی مانده را به صورت خلالی نازک خرد کنید و در تابه تفت دهید تا سبک شود. زردچوبه را هم به پیازداغ اضافه کنید و کمی تفت دهید. رب گوجه فرنگی را به این ترکیب اضافه کنید و کمی تفت بدهید تا رنگ باز کند. ۳ لیوان آب جوش، آب پیاز (در صورت دلخواه) و چند عدد از آلوها را روی این ترکیب بریزید و حرارت را زیاد کنید تا به جوش بیاید، سپس حرارت را کم کنید و درب قابلمه را ببندید تا سس آرام آرام بجوشد و جا بیفتد. مایه کوفته تبریزی را از یخچال بیرون بیاورید. دستتان را کمی خیس کنید. مقداری از مواد کوفته تبریزی به اندازه یک پرتقال متوسط بردارید، یک عدد آلو خورشتی و نصف یک مغز گردو میان آن گذاشته و با دست کوفته را گرد کنید. دستانتان را مدام خیس کنید تا مواد کوفته به دست نچسبد. کوفته‌ها را یکی یکی در سس کوفته بچینید. در این مرحله سس کوفته نباید قل قل کند و بجوشد اما باید حتما داغ باشد تا کوفته‌ها وا نروند. درب قابلمه را هم نگذارید که کوفته‌ها ترک نخورند. هر از گاهی کمی از سس کوفته را با ملاقه روی کوفته‌ها بریزید تا طعم سس به خورد آنها برود. پس از گذشت یک ساعت درب قابلمه را بگذارید تا کوفته‌ها کاملا بپزند. پس از حدود ۱۵ دقیقه بعد از بستن درب قابلمه، کوفته تبریزی شما آماده است. کوفته‌ها را یکی یکی و با ملایمت از قابلمه در آورید و در ظرف مناسبی بریزید. سس کوفته را هم با آنها سرو کنید. کوفته تبریزی گیاهی را با نان تازه و داغ و سبزی خوردن و سایر مخلفات دلخواه میل کنید.', 35, 70, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/01/Malai_Kofta.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (202, 'کوفته تبریزی', 'برای درست کردن ساده کوفته تبریزی، در ابتدا لپه را از شب قبل با مقداری آب بخیسانید و هر چند ساعت یک بار، آب روی آن را دور بریزید تا نفخ آن گرفته شود. برنج را هم از ۱ ساعت قبل بشویید و با مقداری آب بخیس', 'برای درست کردن ساده کوفته تبریزی، در ابتدا لپه را از شب قبل با مقداری آب بخیسانید و هر چند ساعت یک بار، آب روی آن را دور بریزید تا نفخ آن گرفته شود. برنج را هم از ۱ ساعت قبل بشویید و با مقداری آب بخیسانید. سبزی کوفته تبریزی شامل نعناع، تره، جعفری، ریحان، ترخون و پیازچه را با مقداری آب بشویید و آبکش کنید. وقتی سبزی‌ها آب خود را کاملا از دست دادند، آنها را ساطوری و ریز خرد کنید و کنار بگذارید. برای شروع آشپزی، برنج را با مقداری آب و بدون اضافه کردن روغن، نیم‌پز کنید. لپه را هم با مقداری آب روی حرارت بگذارید تا نیم‌پز شود. سپس آن را با آب سرد آبکش کنید. پیازها را پوست بگیرید. در ادامه دو بار گوشت را با مواد کوفته چرخ کنید؛ یک بار با پیاز خرد شده و بار دیگر با لپه و برنج نیم پخته و آبکش شده. بعد از چرخ کردن گوشت با مواد کوفته، آن را در یک کاسه بریزید و سبزی کوفته، نمک، زردچوبه و فلفل سیاه (و در صورت نیاز آرد نخودچی) را به گوشت اضافه کنید. حدود ۱۵ دقیقه آن را ورز دهید تا مایه کوفته تبریزی کاملا منسجم شود. (علت اصلی ترک خوردن کوفته تبریزی ورز ندادن گوشت به اندازه کافی است.) روی ظرف را بپوشانید و نیم ساعت در یخچال گذارید تا بیشتر خودش را بگیرد. در فاصله استراحت کردن مایه کوفته، اگر می‌خواهید داخل کوفته‌ها، تخم مرغ آبپز بگذارید، تخم مرغ‌ها را در مقداری آب روی حرارت آبپز کنید. ۲ تا ۳ پیاز را پوست بگیرید و به صورت خلالی باریک خرد کنید. در یک قابلمه مناسب یا در یک تابه با عمق زیاد، مقداری روغن بریزید و روی حرارت بگذارید. پیازها را در قابلمه بریزید و تفت دهید. بعد از کاراملی و چیپسی شدن پیازها، رب گوجه را اضافه کنید و کمی تفت دهید تا بوی خامی رب گرفته شود. در این مرحله از طرز تهیه کوفته تبریزی ساده و شکم پر، تره و مرزه خرد شده، فلفل قرمز، فلفل سیاه، زعفران دم کرده و کمی نمک به سس کوفته اضافه کنید. مواد را تفت دهید و سپس به اندازه ۳ تا ۴ لیوان دسته‌دار فرانسوی (بسته به اندازه ظرف و اندازه کوفته‌ها) آب جوش در قابلمه بریزید. اجازه دهید سس روی حرارت متوسط بماند و کمی غلیظ شود. به اندازه‌ای که می‌خواهید کوفته‌ها را درست کنید (برخی افراد کوفته‌ها را به اندازه یک پرتقال بزرگ یا متوسط و برخی هم کوچک‌تر در نظر می‌گیرند، اگر می‌خواهید داخل کوفته تبریزی یک تخم‌ مرغ بگذارید حتما اندازه کوفته را به اندازه یک پرتقال بزرگ در نظر بگیرید) گوشت بردارید و آن را گرد کنید. حالا به آرامی وسط گوشت را باز کنید و مواد میانی کوفته را به اندازه دلخواه در آن بگذارید. می‌توانید داخل هر کوفته یک عدد تخم مرغ آب‌پز بدون پوست که خوب پخته و سفت شده هم بگذارید. کوفته را ببندید و دوباره گرد کنید. تا تمام شدن مواد این کار را تکرار کنید. (در آموزش دستور کوفته تبریزی این نکته بسیار مهم است که حتما اندازه کوفته‌ها یکسان باشد تا کوفته‌ها با هم و در یک زمان بپزند.) بعد از آماده کردن کوفته‌های خام، شعله زیر تابه یا قابلمه را کم کنید تا سس در حال قل خوردن نباشد. کوفته‌ها را به آرامی در سس بگذارید. میزان سس باید به قدری باشد که کوفته‌ها تا نیمه یا حدود ۲/۳ آنها در سس باشد. کوفته تبریزی را روی شعله ملایم گاز بپزید تا خوب بپزد و جا بیفتد. برای وا نرفتن احتمالی کوفته درب قابلمه را باز بگذارید. بعد از نیم ساعت به آرامی کوفته‌ها را برگردانید تا هر دو طرف کوفته‌ها، کاملا به سس و چاشنی کوفته آغشته شده و بپزند. کوفته‌ها که آماده شدند، شعله را خاموش کنید. کوفته تبریزی را با ترشی لیته، سیر ترشی، سبزی خوردن و نان سنگک میل کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/04/Koofteh_tabrizi-1-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (203, 'کوفته تره فرنگی ترکیه ای با گوشت چرخ کرده', 'تره فرنگی‌ها را خوب بشویید و به اندازه دلخواه خرد کنید. یک تابه را روی حرارت قرار دهید و در آن مقداری روغن بریزید. تره‌ فرنگی‌ها را در آن بریزید و تفت دهید. یک لیوان آب به آن اضافه کنید و بعد از اینکه', 'تره فرنگی‌ها را خوب بشویید و به اندازه دلخواه خرد کنید. یک تابه را روی حرارت قرار دهید و در آن مقداری روغن بریزید. تره‌ فرنگی‌ها را در آن بریزید و تفت دهید. یک لیوان آب به آن اضافه کنید و بعد از اینکه جوش آمد، در ظرف را بگذارید و اجازه دهید تره فرنگی‌ها کاملا بپزند. حدود ۱۰ تا ۱۵ دقیقه برای پخت تره‌فرنگی‌ها کافی‌ست. تره‌فرنگی‌ها را آبکش کنید و آب آنها را کاملا با دست بگیرید. آب گوشت را بگیرید و با تره‌ فرنگی‌های آبپز شده، مخلوط کنید. نمک، فلفل، تخم‌ مرغ و سیب زمینی را اضافه کنید. همه مواد را با هم ورز دهید تا یک مخلوط یکدست و منسجم داشته باشید. مواد را برای حداقل ۳۰ دقیقه در یخچال استراحت دهید. بعد از خارج‌ کردن مواد از یخچال، در دست آنها را به شکل توپ شکل دهید. در یک کاسه زرده تخم‌ مرغ را به هم بزنید و کوفته‌ تره فرنگی ترکی را در آن و سپس در آرد سوخاری بغلتانید. در یک تابه روغن بریزید و وقتی به حد کافی گرم شد، کوفته‌ها را در آن سرخ کنید. بعد از سرخ شدن، کوفته‌ تره فرنگی ترکی را روی دستمال حوله‌ای قرار دهید تا روغن اضافی آن گرفته شود. کوفته‌ها را در ظرف مناسب به همراه ماست چکیده و سبزی خوردن با نان یا برنج سرو کنید.', 15, 30, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2021/02/image-2-e1612191994153.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (204, 'کوفته کباب ترکی استانبولی', 'گوشت بوقلمون، پیاز رنده شده، سیر له شده، گشنیز، تخم مرغ، فلفل سبز و همه ادویه‌ها را در یک کاسه بزرگ با هم ترکیب کنید تا به خوبی مخلوط شوند. ورز دادن کوفته تأثیر زیادی در یک دست شدن و انسجام آن دارد پس', 'گوشت بوقلمون، پیاز رنده شده، سیر له شده، گشنیز، تخم مرغ، فلفل سبز و همه ادویه‌ها را در یک کاسه بزرگ با هم ترکیب کنید تا به خوبی مخلوط شوند. ورز دادن کوفته تأثیر زیادی در یک دست شدن و انسجام آن دارد پس سعی کنید گوشت و مواد ترکیبی را برای چند دقیقه مداوم به خوبی با دست ورز دهید. بعد از اینکه مواد را به خوبی ورز دادید، آن را به ۱۲ قسمت مساوی تقسیم کنید و به شکل بیضی شکل دهید و برای حداقل نیم ساعت در یخچال قرار دهید. اگر از سیخ چوبی استفاده می‌کنید، حتماً ۳۰ دقیقه قبل از کباب کردن سیخ‌ها را در آب قرار دهید تا نسوزند. بعد از اینکه مواد به مدت ۳۰ دقیقه در یخچال ماند، آن‌ها را بردارید و سیخ را به آرامی درون هر کباب فرو کنید. این کباب‌ها باید به مدت ۱۵ دقیقه روی حرارت غیرمستقیم کباب شوند. بهترین روش برای تهیه این کوفته کباب ترکی خوشمزه گریل کردن یا استفاده از کباب پز است و بهتر است از چند دقیقه قبل از پخت حتماً گریل یا کباب پز را گرم کنید. در زمان پخت چند بار کباب‌ها را برگردانید تا همه بخش­های آن به صورت یکدست پخته شود. برای گریل کردن کباب داخل ماهیتابه مقدار کمی سطح آن را چرب کنید و سپس اجازه دهید ماهیتابه داغ شود و کباب­ها را روی آن قرار دهید. برای تهیه سس کوفته کباب ترکی خوشمزه می­توانید از سس ماست استفاده کنید. برای این کار مقداری ماست یونانی را با کمی لیمو ترش ترکیب کنید و یک حبه سیر بزرگ له شده و مقداری نعناع ساطوری به آن اضافه کنید. سپس کمی نمک و فلفل و ادویه به آن اضافه کنید. سس ماستی را که آماده کرده­اید در یخچال قرار دهید تا خنک شود سپس کوفته خوشمزه­تان را با این سس جذاب و سالم میل کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/11/%D8%AA%D8%B5%D9%88%DB%8C%D8%B1-%D8%A7%D8%B5%D9%84%DB%8C-7.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (205, 'کوفته دال عدس و بلغور (مرجمک کوفتسی)', 'در ابتدا ۲ فنجان دال عدس را به خوبی بشویید و در یک قابلمه کوچک بریزید. سپس ۴ تا ۵ لیوان فرانسوی دسته‌دار آب به آن اضافه کنید. آب باید حدود ۳ انگشت بالاتر از سطح دال عدس قرار بگیرد. اجازه دهید آب به جو', 'در ابتدا ۲ فنجان دال عدس را به خوبی بشویید و در یک قابلمه کوچک بریزید. سپس ۴ تا ۵ لیوان فرانسوی دسته‌دار آب به آن اضافه کنید. آب باید حدود ۳ انگشت بالاتر از سطح دال عدس قرار بگیرد. اجازه دهید آب به جوش بیاید و عدس‌ها پخته شوند. زمانی که آب عدس روی حرارت بخار شد و عدس‌ها کمی نرم شدند، آن را از روی حرارت بردارید. در این مرحله بلغورها را به عدس اضافه کنید. مواد را خوب با هم ترکیب کنید و سپس درب قابلمه را روی آن قرار دهید و اجازه دهید برای ۲۰ دقیقه بلغور با دمای قابلمه دم بکشد. در حالی که به ترکیب دال عدس و بلغور استراحت می‌دهید، در این زمان ۵ قاشق غذاخوری روغن زیتون داخل یک ماهیتابه بریزید و صبر کنید تا روغن داغ شود. سپس پیازهای رنده شده یا ریز خرد شده را به روغن اضافه کنید. خوب هم بزنید تا پیاز نرم شود. سپس ۱ قاشق غذاخوری پوره فلفل قرمز و رب گوجه فرنگی را به آن اضافه کنید. برای ۵ دقیقه به طور مرتب مواد را هم بزنید. سپس نمک، فلفل سیاه و قرمز، زیره، نعناع و ریحان خشک را به آن اضافه کنید و مواد را از روی حرارت بردارید و برای ۱۵ دقیقه اجازه دهید خوب خنک شوند. (می‌توانید به میزان ذائقه خودتان این کوفته را تند کنید). در این مرحله همه مواد شامل بلغور و عدس و پیاز و ادویه‌ها را در یک کاسه بزرگ با هم ترکیب کنید. مواد را با دست به خوبی ورز دهید و برای ۱۰ دقیقه به این کار ادامه دهید تا خمیری تقریباً منسجم ایجاد شود. در انتها پیازچه‌هایی که را ریز خرد کرده‌اید را به همراه جعفری تازه خرد شده به مواد کوفته اضافه کنید و ۵ دقیقه دیگر ورز دهید. مواد کوفته را به صورت کوفته‌هایی با سایز و اندازه دلخواه در آورید و سپس داخل ظرف مخصوص سرو غذا قرار دهید. این کوفته نیاز به پخت مجدد ندارد و بعد از پخته شدن مواد و ترکیب به شکل کوفته در آورده می‌شود و سرو می‌شود. برای تزئین این غذای گیاهی می‌توانید آن را همراه با کاهو، گوجه فرنگی، تربچه، پیازچه و نعناع تزئین کنید و با ریختن کمی آب لیمو ترش تازه روی این کوفته‌های خوشمزه طعم لذیذ آن را چند برابر کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/IMG_20130119_161521-57bbd1565f9b58cdfdb3c8bf-scaled.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (206, 'کوفته روسی (کوفته تفتلی)', 'برنج را به خوبی بشویید و در یک قابلمه بریزید. کمی آب به برنج اضافه کنید و قابلمه را روی حرارت بگذارید تا برنج نیم‌پز شود. سپس برنج را آبکش کنید.', 'برنج را به خوبی بشویید و در یک قابلمه بریزید. کمی آب به برنج اضافه کنید و قابلمه را روی حرارت بگذارید تا برنج نیم‌پز شود. سپس برنج را آبکش کنید. جعفری تازه را پاک کنید، بشویید و آبکش کنید. جعفری‌ها را به صورت ریز و ساطوری شده، خرد کنید. پیازها را پوست بگیرید و به صورت نگینی ریز یا خلالی باریک خرد کنید. در یک تابه مقداری روغن بریزید و روی حرارت بگذارید. روغن که داغ شد، پیازها را در تابه بریزید و تفت دهید تا کاملا سرخ شوند. سپس پیازها را کنار بگذارید. در یک کاسه، زردچوبه، فلفل سیاه، فلفل قرمز و نمک را بریزید و با هم مخلوط کنید. در یک کاسه مناسب، جعفری خرد شده را بریزید و تخم مرغ را در آن بشکنید. تخم مرغ را هم بزنید. سپس گوشت چرخ کرده، برنج نیم‌پز شده، نصف پیاز داغ و نصف ادویه‌ها را اضافه کنید. همه مواد را با دست ورز بدهید تا کاملا با هم مخلوط شوند و مایه یکدستی داشته باشید. مایه کوفته را در یخچال بگذارید تا کمی استراحت کند و منسجم‌تر شود. برای تهیه سس کوفته روسی، فلفل‌ دلمه‌ای‌ها را بشویید و به صورت حلقه‌ای خرد کنید. گوجه فرنگی‌ها را هم بشویید و آنها را هم حلقه حلقه برش بزنید. سیرها را پوست بگیرید و رنده کنید. در یک تابه، نصف پیاز داغ‌ها را بریزید و روی حرارت بگذارید. کمی روغن اضافه کنید و سیر رنده شده و رب گوجه فرنگی را به پیاز داغ‌ها بیفزایید. رب گوجه فرنگی را تفت دهید تا طعم خام آن گرفته شود و رنگ باز کند. سپس نصف مخلوط ادویه‌هایی را که کنار گذاشته بودید، شکر و آبلیمو را اضافه کنید. همه مواد را هم بزنید. فلفل دلمه‌ای‌ها و گوجه فرنگی‌های حلقه‌ای شده را کف تابه بچینید. در ادامه، به اندازه ۳ لیوان دسته‌دار فرانسوی آب، به تابه اضافه کنید. درب تابه را ببندید و اجازه دهید سس کوفته روسی غلیظ شود. بعد از اینکه مایه کوفته روسی، منسجم شد، آن را از یخچال بیرون بیاورید. به اندازه یک نارنگی کوچک یا یک تخم مرغ، از مایه کوفته بردارید و بین دست‌هایتان گلوله کنید. همه کوفته‌های روسی را به همین شکل و در همین اندازه، درست کنید. سپس در یک تابه جدا، مقداری روغن بریزید و وقتی داغ شد، کوفته‌ها را در آن سرخ کنید تا روی آنها طلایی شود. در آخر، وقتی سس کوفته روسی غلیظ شد، به اندازه ۱ ملاقه از سس را در یک کاسه کوچک بریزید و خامه صبحانه را به آن اضافه کنید. سس کوفته روسی و خامه را با چنگال هم بزنید. سپس سس و خامه را به تابه برگردانید و هم بزنید. در ادامه کوفته‌ها را در تابه بچینید، طوری که روی هم قرار نگیرند و سس کوفته تا نصف یا ۲/۳ کوفته‌ها را بپوشاند. مقداری از سس کوفته را روی کوفته‌ها بریزید تا همه کوفته‌ها آغشته به سس شوند. درب تابه را به مدت ۱۰ تا ۱۵ دقیقه، به صورت نیمه باز بگذارید تا کوفته‌ها با سس بپزند و طعم سس بگیرند. بعد می‌توانید شعله را خاموش کرده و کوفته تفتلی را با برنج یا نان سرو کنید.', 16, 34, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/1588788527370.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (207, 'کوفته ریحان', 'در اولین مرحله، برنج را با آب سرد چند بار بشوئید و آب آن را خالی کنید. سپس آن را در محلول آب ولرم و نمک بخیسانید.', 'در اولین مرحله، برنج را با آب سرد چند بار بشوئید و آب آن را خالی کنید. سپس آن را در محلول آب ولرم و نمک بخیسانید. یک دسته ۱۵۰ گرمی ریحان را تمیز کنید. برگ‌های زرد و پلاسیده و ساقه‌های ضخیم آن را جدا کنید. برگ‌های تازه ریحان را با آب به صورت کاملا تمیز بشوئید. سپس در آبکش قرار دهید و اجازه دهید تا آب اضافه از آن خارج شود. بعد از آنکه سبزی‌ها کاملا خشک شدند آن را با چاقو و به صورت ساطوری ریز خرد کنید. ریحان‌های ساطوری شده را برای مراحل بعد کنار بگذارید. برنج‌ها را بعد از خیس خوردن آبکش کنید و آب آن را دور بریزید. یک قابلمه با سایز مناسب انتخاب کنید و برنج را داخل آن بریزید. سپس به برنج یک لیوان دسته دار فرانسوی آب اضافه کنید و آن را روی حرارت بگذارید. حرارت را زیاد کنید تا محتویات داخل قابلمه به جوش بیاید. بعد از آن صبر کنید تا برنج کاملا بپزد و آب آن کشیده شود. در این مرحله، مایه کوفته را آماده کنید. برای این کار یک ظرف مناسب و گود انتخاب کنید. یک عدد پیاز را پوست بگیرید و آن را بشویید. قسمت انتهایی پیاز را جدا نکنید تا موقع رنده کردن وا نرود و لایه‌های آن از هم جدا نشود. پیاز را با رنده، به صورت ریز رنده کنید. سپس با دست پیازهای رنده شده را فشار دهید تا آب آن جدا شود. آب پیاز را دور بریزید و تفاله آن را به یک ظرف مناسب انتقال دهید. گوشت چرخ کرده را به پیاز اضافه کنید و گوشت و پیاز را به خوبی و برای چند دقیقه ورز دهید تا به طور یکدست ترکیب شوند. برنج پخته شده، سبزی ریحان ساطوری شده، آرد نخودچی و نمک و فلفل سیاه را به مخلوط گوشت چرخ کرده و پیاز اضافه کنید. یک عدد تخم مرغ را نیز در ظرفی جداگانه بشکنید و وقتی از سالم بودن آن اطمینان حاصل کردید، آن را هم بزنید تا از لختگی خارج شود. سپس به کاسه‌ای که مواد کوفته را در آن ریخته‌اید، اضافه کنید. همه مواد را با دست به خوبی ورز دهید تا به طور یکدست مخلوط شوند. ورز دادن را تا چند دقیقه ادامه دهید. به اندازه یک گردوی بزرگ از مایه کوفته بردارید و با دست آنها را شکل دهید. کوفته‌ها را به شکل گرد درست کنید. تا زمانی که تمام مایه کوفته تمام شود، این کار را ادامه دهید. کوفته‌ها را در یک ظرف مناسب بچینید و روی آن را سلفون بکشید. ظرف حاوی کوفته‌های ریحان را برای نیم ساعت در یخچال قرار دهید تا استراحت کند و منسجم‌تر شود. بعد از گذشت نیم ساعت به سراغ کوفته‌های درست شده با ریحان بروید و آنها را از یخچال بیرون بیاورید. یک تابه با سایز مناسب انتخاب کنید. مقدار لازم از روغن را داخل آن بریزید. تابه را روی حرارت قرار دهید و صبر کنید تا روغن داغ شود. سپس حرارت را روی حالت متوسط بگذارید و کوفته‌ها را در آن بچینید تا سرخ شوند. کوفته‌ها را بچرخانید تا از همه سمت آن سرخ شوند. سپس آنها را از روغن بیرون بیاورید. برای تهیه سس کوفته، ابتدا دو عدد پیاز را پوست بگیرید و بشوئید و سپس آنها را به صورت نگینی خرد کنید. دو حبه سیر را نیز بشوئید. سیرها را با رنده مخصوص، رنده کنید یا با سیر له کن پوره کنید. در همان تابه‌ای که کوفته‌ها را در آن سرخ کردید، اقدام به درست کردن پیاز داغ کنید. اگر فکر می کنید روغن آن کم است، مقداری روغن اضافه کنید. پیازها را روی حرارت تفت دهید تا کاملا سبک و طلایی شوند. بعد از آن به سراغ سیر بروید. سیر را نیز اضافه کنید و روی حرارت ملایم کمی تفت دهید، فقط تا جایی که عطر آن بلند شود. به سیر و پیاز در حال تفت خوردن، ۲ قاشق غذاخوری رب گوجه فرنگی اضافه کنید. رب گوجه فرنگی را در روغن تفت دهید تا رنگش باز شود و از خامی دربیاید. سپس ادویه‌ها شامل نمک، فلفل سیاه و زردچوبه را نیز اضافه کنید. ادویه‌ها را در گوشه‌ای از تابه و در روغن تفت دهید تا عطرشان بلند شود. سپس همه مواد را با هم مخلوط کنید. برای تهیه یک کوفته ریحان لذیذ و خوشمزه باید اجازه دهید تا کوفته‌ها در سس، به خوبی بپزند. ابتدا دو لیوان آب به تابه افزوده و صبر کنید به جوش بیاید. سپس کوفته‌های سرخ شده را نیز به آن اضافه کرده و حرارت را کم کنید تا کوفته‌ها روی حرارت ملایم به خوبی بپزند و سس نیز غلیظ شود. در این مرحله یا درب تابه را کامل بردارید یا آن را به صورت نیمه باز قرار دهید. کوفته‌ها را در حین پخت، چند باری بچرخانید یا با قاشق از سس روی آنها بریزید تا کاملا سس مخصوص کوفته به خوردشان برود. بعد از گذشت ۱۵ دقیقه، کوفته ریحان شما آماده است. آنها را در یک ظرف مناسب بکشید و به همراه سس مخصوص سرو کنید. این کوفته خوش عطر و بو را با نان و سبزی تازه میل کنید و از طعم متفاوتش لذت ببرید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/20250109_153212_2064150271.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (208, 'کوفته ریزه مرغ', 'در اولین مرحله از طرز تهیه کوفته قلقلی با مرغ باید سینه و ران مرغ بدون پوست و استخوان را میکس یا چرخ کنید. استفاده از سینه و ران مرغ در کنار هم از خشک شدن میت بال مرغ جلوگیری می‌کند و طعم بهتری به آن', 'در اولین مرحله از طرز تهیه کوفته قلقلی با مرغ باید سینه و ران مرغ بدون پوست و استخوان را میکس یا چرخ کنید. استفاده از سینه و ران مرغ در کنار هم از خشک شدن میت بال مرغ جلوگیری می‌کند و طعم بهتری به آن می‌بخشد. می‌توانید گوشت چرخ کرده مرغ را به صورت آماده هم تهیه کنید. بعد از میکس کردن مرغ‌ها، باید پیاز را رنده کنید و آب آن را بگیرید. پیاز رنده شده را به مرغ‌ها اضافه کنید. سپس فلفل دلمه‌ای رنده شده، سیر رنده شده، جعفری ساطوری خرد شده، نمک، فلفل سیاه، پاپریکا، روغن زیتون و آرد سوخاری را هم اضافه کنید. همه مواد را به خوبی مخلوط کنید و با دست ورز دهید تا مایه کوفته مرغ یکدست شود. در این مرحله از روش تهیه کوفته قلقلی با مرغ چرخ کرده باید مقداری از مایه گوشت را بردارید و آن را به صورت قلقلی درست کنید. این کار را برای همه مواد انجام دهید. هر قلقلی به اندازه یک گردو باشد. در چهارمین مرحله از طرز تهیه کوفته ریزه مرغ باید مقداری روغن در ماهیتابه بریزید. کمی صبر کنید تا روغن داغ شود. سپس قلقلی‌ها را در روغن داغ سرخ کنید. در ابتدا شعله گاز باید زیاد باشد تا کوفته قلقلی‌ها سرخ شوند و وا نروند. بعد از این که اطراف قلقلی‌ها تغییر رنگ داد، حرارت را کم کنید و اجازه دهید که کوفته‌ها مغزپخت شوند. سپس شعله گاز را خاموش کنید. بعد از این که کوفته‌ها را آماده کردید، باید به سراغ درست کردن سس غذا بروید. ابتدا زرشک را بشورید و در مقداری آب خیس کنید. برای تهیه سس ابتدا یک پیاز را رنده کنید و آب آن را بگیرید. سپس مقدار کمی روغن در ماهیتابه بریزید و زمانی که روغن داغ شد، پیاز رنده شده را اضافه کنید. بعد از چند دقیقه رب گوجه فرنگی را همراه با نمک و فلفل سیاه به ماهیتابه اضافه کنید. رب با پیاز را تفت دهید تا طعم خامی آن گرفته شود. در این مرحله از طرز تهیه کوفته قلقلی با مرغ باید مقداری آب جوش به سس اضافه کنید. کمی سس را هم بزنید و سپس کوفته قلقلی‌های سرخ شده را به سس اضافه کنید. شعله گاز را روی حرارت ملایم تنظیم کنید و اجازه دهید سس کمی غلیظ شود و جا بیفتد. بعد از اینکه غذا تقریبا آماده شد، زرشک آبکش شده را به سس اضافه کنید (می‌توانید زرشک را کمی هم در مقداری روغن تفت دهید و سپس به سس اضافه کنید). کوفته ریزه مرغ را همراه با نان یا برنج سرو کنید. اضافه کردن مغز گردوی خرد شده به کوفته ریزه مرغ طعم بی‌نظیری می‌بخشد. در ادامه می‌توانید با مراحل طرز تهیه کوفته ریزه مرغ با گردو آشنا شوید. در اولین مرحله از طرز تهیه کوفته ریزه مرغ با گردو باید مرغ چرخ کرده، پیاز رنده شده، گردوی خرد شده، آرد و برگ گشنیز خرد شده را با هم ترکیب کنید. سپس مقداری نمک، فلفل سیاه، پاپریکا و پودر زیره به مواد اضافه کنید. بعد از ترکیب کردن مواد کوفته، باید مواد را برای چند دقیقه ورز دهید. سپس آن‌ها را به صورت قلقلی درست کنید و در یک سینی بچینید. سینی را برای ۱ ساعت در یخچال قرار دهید. بعد از ۱ ساعت سینی کوفته‌ها را از یخچال خارج کنید. یک ماهیتابه روی گاز قرار دهید و مقداری روغن در آن بریزید. یک کاسه آرد سوخاری در کنارتان قرار دهید. کوفته‌ها را یکی یکی در ظرف آرد سوخاری بغلتانید و سپس در روغن داغ سرخ کنید. اجازه دهید کوفته‌ها با حرارت ملایم مغزپخت و طلایی شوند. در نهایت کوفته‌ها را در ظرف بچینید. می‌توانید از این کوفته قلقلی برای روی انواع پاستا، پلو یا اسپاگتی با میت بال مرغ استفاده کنید یا به صورت نونی میل کنید.', 25, 50, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/08/%D8%B623%D8%AB%D9%82%D8%B5.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (209, 'کوفته زرشکی', 'در ابتدا جعفری‌ها را پاک کنید، بشویید و اجازه دهید آب خود را از دست بدهد. زرشک‌ها را در یک آبکش بریزید و آبکش را در یک کاسه آب بگذارید تا کاملا در آب باشند. به مدت ۳۰ دقیقه زرشک‌ها را بخیسانید. ۱ پیاز', 'در ابتدا جعفری‌ها را پاک کنید، بشویید و اجازه دهید آب خود را از دست بدهد. زرشک‌ها را در یک آبکش بریزید و آبکش را در یک کاسه آب بگذارید تا کاملا در آب باشند. به مدت ۳۰ دقیقه زرشک‌ها را بخیسانید. ۱ پیاز و حبه‌های سیر را پوست بگیرید. گوشت چرخ کرده را در غذاساز بریزید و چند بار بزنید تا بیشتر منسجم شود. ۱ عدد پیاز را هم در غذاساز بریزید و حدود ۱ تا ۲ دقیقه بزنید تا کاملا پوره شود. سپس سیرها را اضافه کنید و ۱ دقیقه بزنید. در ادامه جعفری‌های شسته و خشک شده را در غذاساز بریزید و پوره کنید. مغز گردوهای خرد شده را هم در آخر بریزید و یکی، دو دقیقه بزنید تا کاملا میکس شوند. همه مواد که یکدست شدند، مایه کوفته را از غذاساز بیرون بیاورید و در یک کاسه بریزید. به این مایه، پودر تخم گشنیز، پودر آویشن، فلفل سیاه، نمک و آرد نخودچی اضافه کنید. مواد کوفته را برای چند دقیقه ورز دهید تا طعم‌ها به خورد هم بروند. سپس روی ظرف را بپوشانید و به مدت ۳۰ دقیقه در یخچال بگذارید تا استراحت کند و منسجم شود. در این فاصله مواد سس زرشک را آماده کنید. گوجه فرنگی‌ها را بشویید و به ۴ قسمت خرد کنید. آبکشی که زرشک‌ها را در آن ریخته‌اید، از آب بیرون بیاورید و زیر آب سرد بگیرید. زرشک و گوجه فرنگی‌ها را در غذاساز بریزید و آن را روشن کنید تا کاملا همه مواد پوره شوند. پوره را کنار بگذارید. یک پیاز را پوست بگیرید و رنده یا ریز خرد کنید. در یک تابه عمیق یا قابلمه، مقداری روغن بریزید و پیازها را در آن سرخ کنید تا کاملا سبک و شیشه‌ای شوند. اگر از رب گوجه ‌هم در تهیه سس می‌خواهید استفاده کنید، در این مرحله آن را اضافه کنید و کمی تفت دهید تا رنگ باز کند. سپس گوجه و زرشک پوره شده را به همراه شکر در تابه یا قابلمه بریزید. شعله را کم کنید و درب ظرف را بگذارید. بعد از استراحت کردن کوفته‌ها، آنها را از یخچال بیرون بیاورید و دوباره یکی، دو دقیقه مواد کوفته را ورز دهید. در یک تابه جدا مقداری روغن بریزید و روی حرارت بگذارید تا داغ شود. از مواد کوفته به اندازه یک نارنگی کوچک (کمی بزرگ‌تر از گردو) بردارید و بین دست‌هایتان گلوله کنید. روغن که داغ شد، کوفته را در روغن بگذارید. همه کوفته‌ها را به همین شکل آماده کرده و همه قسمت‌های آنها را در روغن سرخ کنید. وقتی کوفته‌ها سرخ شدند، آنها را به آرامی در سس زرشک بگذارید. کوفته‌ها را طوری در ظرف سس در حال جوشیدن بگذارید که روی هم قرار نگیرند و تقریبا ۲/۳ آنها در سس باشد. اجازه دهید کوفته‌ها به مدت ۱ ساعت روی حرارت ملایم در سس بمانند و بپزند. در این مدت، از مایه سس، روی کوفته‌ها بریزید. وقتی کوفته‌ها پختند، شعله را خاموش کنید. بعد از آماده شدن کوفته‌های زرشکی، کوفته‌ها را در ظرف سرو بچینید و سس را روی کوفته‌ها بریزید. کوفته‌ها را با انواع سبزیجات معطر تازه خرد شده و برش‌های لیمو تزیین کنید. کوفته زرشکی را در کنار برنج یا نان سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/11/%D8%B7%D8%B1%D8%B2-%D8%AA%D9%87%DB%8C%D9%87-%DA%A9%D9%88%D9%81%D8%AA%D9%87-%D8%B2%D8%B1%D8%B4%DA%A9.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (210, 'کوفته ساده با گوشت چرخ کرده', 'در اولین مرحله، گوشت را دو بار چرخ کنید تا کاملا نرم شود. در صورتی که از گوشت چرخ‌ کرده آماده استفاده می‌کنید، می‌توانید گوشت را در مولینکس دوباره میکس کنید. اگر هم چرخ گوشت ندارید گوشت چرخ کرده را مق', 'در اولین مرحله، گوشت را دو بار چرخ کنید تا کاملا نرم شود. در صورتی که از گوشت چرخ‌ کرده آماده استفاده می‌کنید، می‌توانید گوشت را در مولینکس دوباره میکس کنید. اگر هم چرخ گوشت ندارید گوشت چرخ کرده را مقداری با گوشتکوب بکوبید یا از قصاب بخواهید گوشت را دو یا سه بار برایتان چرخ کند. بعد از آماده کردن گوشت، یک عدد پیاز بزرگ را پوست بگیرید. سپس آن را رنده ریز کنید. بعد از رنده کردن پیاز بهتر است آب پیاز را بگیرید تا مایه گوشت شما حالت آبکی پیدا نکند. در این مرحله گوشت، پیاز رنده شده، نمک، فلفل سیاه و زردچوبه را ترکیب کنید و مواد را برای چند دقیقه ورز دهید تا گوشت حالت چسبنده پیدا کند. بعد از این که مواد کوفته آماده شد، به اندازه یک گردو از مواد بردارید و با دست گرد کنید. همه کوفته‌ها را به همین شکل آماده کنید و در آرد سوخاری بغلتانید و سپس در یک سینی بچینید. در یک ماهیتابه مقداری روغن بریزید. صبر کنید تا روغن داغ شود. سپس کوفته‌ها را داخل روغن داغ سرخ کنید. برای تهیه سس کوفته، یک پیاز را به صورت نگینی خرد کنید و در روغن سرخ کنید. سپس مقداری نمک، فلفل سیاه و زردچوبه به آن اضافه کنید. ۲ قاشق رب گوجه‌ فرنگی را همراه با پیاز سرخ کنید و درنهایت مقداری آب اضافه کنید. حدود ۱۰ تا ۲۰ دقیقه صبر کنید تا سس بجوشد. حالا کوفته‌ها را در سس بریزید و اجازه دهید کوفته قلقلی شما با شعله ملایم بجوشد و در سس مزه‌دار شود. بعد از ۲۰ دقیقه غذای شما آماده است.', 18, 37, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2023/07/qwedf-3.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (211, 'کوفته شفته سماق کاشان', 'در اولین مرحله، پیازها را پوست بگیرید، بشویید و به صورت ریز رنده کنید. آب اضافی پیاز رنده شده را خارج کنید.', 'در اولین مرحله، پیازها را پوست بگیرید، بشویید و به صورت ریز رنده کنید. آب اضافی پیاز رنده شده را خارج کنید. گوشت چرخ کرده را به پیازهای رنده شده اضافه کنید. سپس نمک، فلفل سیاه و ۲۵۰ گرم آرد نخودچی را نیز اضافه کنید. ترکیب را برای چند دقیقه به خوبی ورز دهید تا گوشت چسبناک و منسجم شود. بعد از اینکه گوشت به انسجام کافی رسید، مواد را به صورت کوفته آماده کنید. به اندازه یک گردو از مواد گوشتی بردارید و با دقت با دست گرد کنید. اگر گوشت حین گرد کردن ترک می‌خورد، به ورز دادن بیشتری نیاز دارد. همه کوفته‌ها را به همین روش آماده کنید. کوفته‌های آماده شده را کمی در روغن تفت دهید. برای این کار کمی کره یا روغن در ماهیتابه بریزید و اجازه دهید داغ شود. سپس کوفته‌ها را به آرامی به ماهیتابه اضافه کنید. در این مرحله تنها فرم گرفتن کوفته‌ها و تغییر رنگ گوشت کافی است تا کوفته‌ها بعد از ترکیب شدن با آب وا نروند و شکل خود را حفظ کنند. بعد از تفت دادن کوفته‌ها، آن‌ها را در یک سینی بگذارید و به سراغ سماق بروید. اگر از سماق نکوبیده استفاده می‌کنید، باید سماق‌ها را برای یک شبانه‌روز در آب بخیسانید. سپس آب سماق خیس خورده را داخل یک کاسه بریزید و سماق‌های خیس خورده را از الک یا صافی عبور دهید تا طعم و عصاره سماق خارج شود و تنها پوسته و هسته سماق در صافی باقی بماند. اگر از سماق کوبیده و پودر شده استفاده می‌کنید، نیم پیمانه سماق را در آب ولرم خیس دهید و سپس آن را از صافی عبور دهید تا تکه‌های درشت‌تر سماق جدا شود. ما تنها به آب و عصاره سماق نیاز داریم. پیازها را پوست بگیرید و بشویید. سپس با کمک یک چاقوی تیز، پیازها را به صورت نگینی و ریز خرد کنید. یک ماهیتابه مناسب روی شعله ملایم گاز قرار دهید و مقداری کره به آن اضافه کنید تا گرم شود. پیاز خرد شده را به ماهیتابه اضافه کنید و خوب تفت دهید. زمانی که پیاز سبک شد و رنگ آن تغییر کرد، می‌توانید آب سماق را به آن اضافه کنید. سپس صبر کنید تا مواد به جوش بیایند. بعد از اینکه آب سماق به جوش آمد، کوفته‌هایی که از قبل تفت داده‌اید را به آن اضافه کنید. سپس حرارت را کم کنید و اجازه دهید غذا به خوبی جا بیفتد. اگر آب غذا کم است می‌توانید مقدار کمی آب نیز به آن اضافه نمایید. اجازه دهید کوفته‌ها به خوبی در آب سماق پخته شوند و طعم بگیرند. شفته کوفته نخودچی یا شفته کوفته سماق کاشان معمولاً با نان و سبزی خوردن سرو می‌شود و در صورت تمایل می‌توانید این غذا را با برنج نیز میل کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/08/kotlet-goosht3-small.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (212, 'کوفته سوخاری', 'در مرحله اول از طرز تهیه کوفته سوخاری باید برنج را که از قبل با آب و نمک خیسانده‌اید بپزید. یک قابلمه با سایز مناسب روی حرارت قرار دهید و وقتی آب به جوش آمد برنج را داخل آن بریزید و اجازه دهید خوب بپز', 'در مرحله اول از طرز تهیه کوفته سوخاری باید برنج را که از قبل با آب و نمک خیسانده‌اید بپزید. یک قابلمه با سایز مناسب روی حرارت قرار دهید و وقتی آب به جوش آمد برنج را داخل آن بریزید و اجازه دهید خوب بپزد. برنج باید تا حدی بپزد که حالتی کاملا چسبنده پیدا کند و به اصطلاح شفته باشد. بعد از پختن برنج آن را کنار بگذارید تا کاملا خنک شود. در فاصله‌ای که برنج خنک شود، سبزی جعفری‌ را بشویید. بعد از شستن جعفری‌ها آن را به صورت ریز و ساطوری خرد کنید. سپس گوشت چرخ کرده، برنج پخته شده، نمک، فلفل سیاه و زردچوبه را به ترکیب گوشت و جعفری اضافه کنید. مواد را کاملا ورز دهید تا یک مایه یکدست با بافتی کاملا منسجم به دست آید. مایه کوفته را برای یک ساعت در یخچال استراحت دهید. بعد از گذشت حدود یک ساعت مواد کوفته را از یخچال بیرون آورید. برای هر کوفته، به اندازه یک گردو از مایه کوفته را بردارید و آن را گلوله کنید و کنار بگذارید. این کار را تا تمام شدن تمام مواد کوفته انجام دهید. در این مرحله از طرز تهیه کوفته برنجی سوخاری بعد از آنکه تمام مواد کوفته را به شکل گلوله در آورید، در یک ظرف نسبتا بزرگ آرد سوخاری را بریزید. آرد سوخاری را با مقداری نمک، پودر سیر و پودر پیاز ترکیب کنید. یک ظرف دیگر بردارید و تخم مرغ‌ها را در آن بشکنید. تخم مرغ‌ها را با چنگال هم بزنید تا یکدست شوند و از حالت لختگی دربیایند. کوفته‌های برنجی را که آماده کردید یک بار در تخم مرغ و یک بار در آرد سوخاری بغلتانید. برای آنکه مطمئن شوید تمام قسمت‌های کوفته آغشته به آرد سوخاری شده‌اند این عمل را برای هر کوفته دو مرتبه تکرار کنید. یک ماهیتابه با سایز مناسب را با مقدار کافی از روغن پر کنید و روی حرارت زیاد قرار دهید تا کاملا داغ شوند. توجه داشته باشید که سایز تابه و مقدار روغن باید به صورتی باشد که کوفته‌ها هنگام سرخ شدن در روغن غوطه‌ور بمانند. بعد از اطمینان از داغ شدن روغن، کوفته‌هایی را که به آرد سوخاری آغشته کردید در روغن سرخ کنید. کوفته‌های سوخاری را بعد از سرخ شدن روی یک دستمال قرار دهید تا روغن اضافه‌شان گرفته شود. کوفته سوخاری خوشمزه با گوشت چرخ کرده و برنج شما آماده است. می‌توانید آن را با جعفری خرد شده و سس کچاپ به عنوان غذا یا پیش غذا در سفره‌های نهار و شام خود سرو کنید. برای درست کردن کوفته سوخاری در فر، فر را از قبل با دمای ۲۰۰ درجه سانتی‌گراد گرم کنید. سپس کف سینی فر یک کاغذ روغنی بیندازید و کوفته‌های آغشته به آرد سوخاری را روی آن بچینید. با یک ظرف اسپری روی کوفته‌ها کمی روغن اسپری کنید. کوفته‌ها را در فر قرار دهید. بعد از حدود بیست دقیقه و زمانی که رنگ سطح بیرونی کوفته طلایی رنگ شد، کوفته‌های سوخاری آماده هستند.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/07/lkjhgfdfghj.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (213, 'کوفته شوربا سنندجی', 'لپه‌ها را پاک کنید، بشویید و با مقداری آب در یک کاسه خیس کنید. بهتر است لپه‌ها را از شب قبل بخیسانید.', 'لپه‌ها را پاک کنید، بشویید و با مقداری آب در یک کاسه خیس کنید. بهتر است لپه‌ها را از شب قبل بخیسانید. برنج را بشویید و در مقداری آب ولرم حدود ۲ ساعت بخیسانید. گوشت‌های با استخوان را بشویید و آبکش کنید. یکی از پیازها را پوست بگیرید و به صورت خلالی باریک یا نگینی ریز خرد کنید. سیرها را پوست بگیرید و رنده یا ریز خرد کنید. در یک قابلمه مقداری روغن بریزید و روی حرارت بگذارید تا داغ شود. سپس پیازها را در قابلمه بریزید و تفت دهید. پیازها که سرخ و سبک شدند، سیرها را اضافه کنید تا عطر آنها خارج شود. گوشت‌های با استخوان را در قابلمه بریزید و تفت دهید تا رنگ آنها تغییر کند. سپس ادویه‌ها شامل زردچوبه، فلفل سیاه و ادویه‌های معطر دلخواه خود را به پیاز و سیر اضافه کنید و تفت دهید. اگر از رب گوجه فرنگی استفاده می‌کنید، رب را با پیاز و سیر در روغن تفت دهید. بعد از اینکه همه مواد داخل قابلمه تفت خوردند، ۵ لیوان فرانسوی دسته‌دار آب (ترجیحا ولرم یا جوش) به قابلمه اضافه کنید. وقتی آب شروع به جوشیدن کرد، شعله گاز را کم کنید و اجازه دهید گوشت‌ها به مدت ۱ تا ۱/۵ ساعت بپزند. در حین پخت گوشت‌ها، سبزی‌های کوفته (شامل شوید، تره، جعفری، مرزه و شنبلیله) را پاک کنید، بشویید و آب اضافی آنها را بگیرید. سپس سبزی‌ها را ریز و ساطوری خرد کنید و در یک کاسه بزرگ بریزید. آب برنج و لپه خیس خورده را دور بریزید. در دو ظرف جدا، لپه و برنج را با مقداری آب بریزید و روی حرارت بگذارید تا نیم پز شوند. سپس آنها را آبکش کنید و زیر آب سرد بگیرید. برنج و لپه نیم‌پز را با گوشتکوب دستی کمی بکوبید یا از غذاساز استفاده کنید. در کاسه سبزی‌های خرد شده، گوشت چرخ کرده، برنج و لپه نیم پز و کوبیده شده، زردچوبه، نمک و فلفل سیاه بریزید. پیاز باقیمانده را پوست بگیرید و رنده کنید. آب پیاز را بگیرید و تفاله آن را در کاسه بریزید. مواد کوفته را با دست ورز دهید تا کاملا یکدست و چسبناک شوند. در یک کاسه، تخم مرغ‌ها را بشکنید و هم بزنید تا از لختگی خارج شوند. از مایه کوفته به اندازه یک نارنگی (حدود ۳ یا ۴ قاشق غذاخوری) بردارید، بین دست‌هایتان گلوله کنید و وسط آن را یک حفره ایجاد کنید. داخل حفره را از مایه تخم مرغ بریزید و دوباره مایه کوفته را ببندید تا تخم مرغ وسط کوفته قرار بگیرد. همه کوفته‌ها را به همین شکل آماده کنید و در یک سینی بگذارید. در یک تابه مقداری روغن بریزید و کوفته‌ها را در آن کمی سرخ کنید تا در زمان پخت از هم نپاشند. لازم نیست کاملا طلایی شوند. وقتی کوفته‌ها آماده شدند و گوشت‌های داخل قابلمه نیم‌پز شده بودند، کوفته‌ها را در قابلمه‌ای که گوشت‌ها در حال پخت هستند، بگذارید. کوفته‌ها را در قابلمه به همراه گوشت، به مدت ۲ ساعت بپزید. در این مدت، در قابلمه را نیمه‌باز بگذارید و حرارت زیر آن را کم کنید. هر نیم ساعت یک بار، به کوفته‌ها سر بزنید و از سس کوفته روی آنها بریزید. نیم ساعت بعد از گذاشتن کوفته در سس، کوفته‌ها را به آرامی زیر و رو کنید تا همه قسمت‌های کوفته بپزد. وقتی کوفته‌ها پختند و سس آن غلیظ شد، شعله را خاموش کنید و کوفته‌ها را در ظرف سرو بگذارید. کوفته شوربا معمولا با نان و سبزی خوردن سرو می‌شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/12/koofte-shorba-1-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (214, 'کوفته شیرین کرمانشاهی', 'در اولین مرحله، برنج ایرانی را بشویید و با مقدار مناسبی آب و کمی نمک حدود یک ساعت بخیسانید.', 'در اولین مرحله، برنج ایرانی را بشویید و با مقدار مناسبی آب و کمی نمک حدود یک ساعت بخیسانید. بعد از خیس خوردن برنج، آب آن را خالی کنید. برنج را با یک لیوان دسته‌دار فرانسوی آب روی حرارت قرار دهید و اجازه دهید کاملا نرم شود و آب آن کشیده شود. پیاز را پوست بگیرید و با رنده درشت رنده کنید. آب پیاز رنده شده را با فشار دست بگیرید و تفاله پیاز را نگه دارید. در یک کاسه، گوشت چرخ کرده و تفاله پیاز رنده شده را بریزید و حداقل ۱۵ دقیقه خوب ورز دهید تا مایه کوفته چسبناک شود. برنج پخته و له شده را به ترکیب پیاز و گوشت اضافه کنید. نمک، فلفل سیاه و زردچوبه را نیز اضافه کرده و مواد را خوب ورز دهید تا یکدست شوند. در این مرحله می‌توانید نعنا خشک را نیز اضافه کرده و مجددا ورز دهید. یک عدد تخم مرغ را در ظرفی جداگانه بشکنید و پس از اطمینان از سالم بودن، به مایه کوفته اضافه کنید و حسابی ورز دهید تا حالت چسبندگی پیدا کند. برای شکل دادن به کوفته، از مواد به اندازه یک پرتقال متوسط بردارید و با دو دست به شکل گرد درآورید. این کار را تا اتمام مایه کوفته ادامه دهید. کوفته‌ها را در یک قابلمه مناسب بچینید به طوری که روی هم قرار نگیرند. برای تهیه سس، در یک قابلمه کوچک ۲ قاشق غذاخوری روغن، ۱ لیوان دسته‌دار فرانسوی آب، مقدار لازم شکر و سرکه را بریزید و روی حرارت ملایم قرار دهید تا شکر حل شود و سس غلیظ شود. بعد از غلیظ شدن سس، آن را روی کوفته‌ها بریزید. قابلمه حاوی کوفته و سس را روی حرارت ملایم قرار دهید. درب قابلمه را باز یا نیمه باز بگذارید و حدود یک ساعت زمان دهید تا کوفته‌ها کاملا مغز پخت شوند. بعد از یک ساعت، کوفته شیرین کرمانشاهی آماده است. آن را در ظرف مناسب سرو کرده و با نان تازه و سبزی خوردن میل کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/12/How-to-prepare-Kermanshahi-meatballs-4.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (215, 'کوفته عدس', 'عدس‌ها را پاک کنید و در یک کاسه بریزید. عدس‌ها را بشویید و مقداری آب اضافه کنید تا روی آنها را بپوشاند. به مدت ۱ تا ۲ ساعت، عدس‌ها را بخیسانید. بلغورها را هم در یک کاسه جدا بریزید و خیس کنید. جعفری‌ها', 'عدس‌ها را پاک کنید و در یک کاسه بریزید. عدس‌ها را بشویید و مقداری آب اضافه کنید تا روی آنها را بپوشاند. به مدت ۱ تا ۲ ساعت، عدس‌ها را بخیسانید. بلغورها را هم در یک کاسه جدا بریزید و خیس کنید. جعفری‌های تازه را بشویید و به صورت ریز و ساطوری خرد کنید. بعد از اینکه عدس‌ها خیس خوردند، آنها را آبکش کنید. در یک قابلمه عدس را بریزید و به اندازه ۲ و نیم لیوان دسته‌دار فرانسوی آب معمولی اضافه کنید. قابلمه را روی حرارت بگذارید و صبر کنید تا آب بجوشد. سپس شعله را کم کنید و نیم ساعت صبر کنید تا عدس‌ها نیم‌پز شوند. سپس آبی که بلغور را در آن خیسانده‌اید، دور بریزید و بلغورها را در قابلمه عدس‌های نیم پز شده بریزید. حدود نیم ساعت دیگر صبر کنید تا بلغورها هم بپزند. در این مدت، مراقب باشید آب داخل قابلمه، کاملا بخار نشود و ته نگیرد. عدس و بلغور که کاملا پخت، در یک آبکش بریزید و اجازه دهید آب اضافی آنها خارج شود. سپس یک پیاز را پوست بگیرید و به صورت نگینی ریز خرد کرده یا با رنده درشت، رنده کنید. در یک تابه مقداری روغن بریزید و پیازها را در آن تفت دهید تا سبک و شیشه‌ای شود. بعد رب گوجه فرنگی را اضافه کنید و هم بزنید تا رنگ باز کند و طعم خام آن گرفته شود. شعله را خاموش و صبر کنید تا از دما بیفتد. عدس و بلغور که از دما افتادند، آنها را در یک کاسه مناسب بریزید. جعفری خرد شده، رب گوجه تفت داده شده، زردچوبه، فلفل سیاه و نمک را اضافه کنید و تخم مرغ را در آن بشکنید و با دست ورز دهید تا کاملا یکدست و منسجم شود. می‌توانید برای انسجام بیشتر، مایه کوفته عدس را به مدت نیم ساعت در یخچال بگذارید تا خودش را بگیرد. به اندازه یک نارنگی از مایه کوفته بردارید و بین دست‌هایتان فرم دهید. اگر می‌خواهید کوفته عدس ترکیه‌ای یا همان مرجمک کوفته داشته باشید، کوفته‌ها را به صورت دوکی یا بیضی درآورید و بین انگشت‌هایتان فشار دهید تا رد انگشت‌هایتان روی کوفته‌ها بماند. در غیر این صورت، کوفته‌های عدس را به صورت گرد و توپی فرم دهید. بعد از آماده کردن و فرم دادن کوفته‌های عدس، در یک تابه مقداری روغن بریزید و روی حرارت بگذارید تا داغ شود. سپس کوفته‌ها را در روغن سرخ کنید تا همه قسمت‌های آنها طلایی شود. کوفته‌ها را بعد از سرخ شدن می‌توانید در ظرف سرو بگذارید و با نان، انواع ترشی، انواع سالاد، انواع سس و سبزی خوردن سرو کنید. سرو کوفته عدس سبز خوشمزه به همراه انواع پاستا ایده واقعا وسوسه انگیزی است. هر چند برخی مواقع کوفته عدس بدون سس و همراه با نان و سبزیجات سرو می‌شود ولی می‌توان از سس مارینارا یا سس گوجه هم برای روی کوفته عدس استفاده کرد. همچنین می‌توان برای بافت نرم‌تر کوفته عدس، کوفته‌ها را در سس گوجه بگذارید و اجازه دهید به مدت ۱۰ دقیقه بپزند، سپس آنها را سرو کنید. برای تهیه سس گوجه برای کوفته عدس، ۲ یا ۳ عدد گوجه را بشویید و رنده کنید. ۲ حبه سیر را هم پوست بگیرید و با سیر له کن یا رنده ریز، رنده کنید. سپس در یک تابه مقداری روغن بریزید و روی حرارت بگذارید تا داغ شود. سیر را تفت دهید تا عطر آن خارج شود. سپس پوره گوجه فرنگی را اضافه کنید. پوره گوجه را تفت دهید تا طعم خام آن گرفته شود. مقداری فلفل سیاه، زردچوبه، نمک و سبزی‌های معطر تازه خرد شده یا خشک، اضافه کنید. به اندازه نصف لیوان فرانسوی دسته‌دار، آب داغ به تابه اضافه کنید و اجازه دهید سس شما غلیظ شود. کوفته‌های عدس را بعد از سرخ کردن می‌توانید در سس هم به مدت ۱۰ دقیقه بپزید یا این سس را روی کوفته‌های سرخ و آماده شده بریزید و آنها را سرو کنید. برای جلوگیری از وا رفتن و شل شدن کوفته عدس، موارد زیر را رعایت کنید: دلیل فرم نگرفتن کوفته عدس، خشک بودن بیش از حد مایه کوفته است. در این صورت، سس رب گوجه‌ای را که در نظر گرفته‌اید، بیشتر کنید.', 16, 34, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/01/44207_ThreeMeatItalianMeatballs_MFS_449-0c32601474cf4e379a3b5cfca58ba8ef.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (216, 'کوفته فلفلی', 'در اولین مرحله از طرز تهیه کوفته فلفلی، فلفل دلمه‌ای و فلفل سبز را با آب به صورت تمیز بشویید. سپس در آبکش قرار دهید تا آب اضافه از آنها خارج شود. ۱ عدد پیاز را پوست بگیرید و آن را نیز بشویید. فلفل سبز', 'در اولین مرحله از طرز تهیه کوفته فلفلی، فلفل دلمه‌ای و فلفل سبز را با آب به صورت تمیز بشویید. سپس در آبکش قرار دهید تا آب اضافه از آنها خارج شود. ۱ عدد پیاز را پوست بگیرید و آن را نیز بشویید. فلفل سبز را از وسط باز کنید و هسته‌های آن را دور بریزید. به صورت حلقه‌ای باریک آنها را خرد کنید. پیاز را با رنده ریز، رنده کنید و آب آن را به وسیله فشار دست گرفته و دور بریزید. برای تهیه کوفته فلفلی فقط به تفاله پیاز نیاز است. فلفل دلمه‌ای رنگی را نیز از وسط نصف کنید، تخم‌های آن را جدا کنید و فلفل دلمه‌ای را به صورت نگینی کوچک خرد کنید. یک کاسه با سایز مناسب انتخاب کنید و گوشت چرخ کرده را در آن بریزید. فلفل دلمه‌ای و فلفل سبز خرد شده را اضافه کنید. مطمئن شوید که آب پیاز را گرفته‌اید و سپس پیاز رنده شده را نیز به دیگر مواد اضافه کنید. گوشت چرخ کرده را با دست با بقیه مواد ورز دهید تا کاملا و به طور یکدست با هم مخلوط شوند. در این مرحله دو قاشق غذاخوری آرد سوخاری به مایه کوفته اضافه کنید. یک عدد تخم مرغ را در ظرف دیگر بشکنید و بعد از آنکه از سالم بودن آن مطمئن شدید به باقی مواد اضافه کنید. ادویه‌ها شامل نمک، فلفل سیاه، پودر سیر، پودر زیره، پودر پاپریکا را نیز اضافه کنید. با دست همه مواد را با یکدیگر ورز دهید. ورز دادن را حداقل تا پنج دقیقه ادامه دهید تا بافت مایه کوفته فلفلی کاملا منسجم و یکدست شود. روی ظرفی که مایه کوفته با فلفل را آماده کردید، سلفون بکشید یا آن را در ظرفی بریزید که درب داشته باشد و درب آن نیز کاملا کیپ شود. مایه کوفته را برای یک ساعت در یخچال قرار دهید تا کاملا خودش را بگیرد و منسجم شود. بعد از گذشت یک ساعت، مایه کوفته را از یخچال بیرون بیاورید. یک تابه کوچک با عمق زیاد انتخاب کنید و در آن مقدار لازم از روغن را بریزید. مقدار روغن باید به قدری باشد که کوفته‌ها در آن غوطه‌ور شوند. تابه را روی حرارت قرار دهید و اجازه دهید تا کاملا داغ شود. از مایه کوفته به اندازه یک گردوی بزرگ بردارید و آن را به صورت گرد شکل دهید. کوفته‌ها را در روغن داغ بگذارید و اجازه دهید تا سرخ شود. حرارت را روی حالت متوسط بگذارید تا کاملا مغز پخت شوند. کوفته‌ها را بچرخانید تا از همه طرف به طور یکسان سرخ شوند. بعد از آنکه کوفته‌ها سرخ و مغزپخت شدند آنها را از روی حرارت بردارید. کوفته فلفلی‌ها را روی یک دستمال حوله‌ای قرار دهید تا روغن اضافه آنها جذب شود. سپس کوفته‌ها را در یک دیس مناسب بکشید و با انواع سس دلخواه و نان آن را سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (217, 'کوفته کاری', 'پیش از شروع مراحل طرز تهیه کوفته کاری هندی، ابتدا پیازهای شسته شده و بدون پوست را رنده کنید و کنار بگذارید. سیرهای شسته شده و بدون پوست را نیز درست مانند پیازها به صورت ریز رنده کنید تا بعدا آن را در', 'پیش از شروع مراحل طرز تهیه کوفته کاری هندی، ابتدا پیازهای شسته شده و بدون پوست را رنده کنید و کنار بگذارید. سیرهای شسته شده و بدون پوست را نیز درست مانند پیازها به صورت ریز رنده کنید تا بعدا آن را در تهیه سس کوفته کاری مورد استفاده قرار دهید. یک قطعه زنجبیل تازه را هم پوست بگیرید و به صورت ریز رنده کنید. از سوی دیگر، گوجه فرنگی‌های شسته شده را هم بلانچ کنید و پوست بگیرید. سپس، گوجه‌های بدون پوست را با استفاده از رنده، دستگاه غذاساز یا گوشتکوب برقی کاملا پوره کنید. پس از آماده‌سازی مواد اولیه، نوبت به درست کردن مایه کوفته کاری می‌رسد. بنابراین، گوشت چرخ کرده را در کاسه مناسبی قرار دهید. نیمی از پیاز رنده شده را جدا کنید و با دست فشار دهید تا آب پیاز گرفته شود. برای درست کردن مایه کوفته کاری شما فقط به تفاله پیاز نیاز دارید. حالا، تفاله پیاز را به کاسه حاوی گوشت چرخ کرده اضافه کنید. این مواد را به خوبی ورز دهید تا پیاز و گوشت چرخ کرده به طور یکنواختی با یکدیگر ترکیب شوند. در این مرحله از طرز تهیه کوفته هندی، مقداری نمک، فلفل سیاه، ادویه کاری و کمی پودر زیره سیاه را همراه با اندکی آرد نخودچی به مایه کوفته اضافه کنید. این مواد را حسابی ورز دهید تا ادویه‌ها و آرد نخودچی نیز کاملا با مایه کوفته ترکیب شوند. حالا، به اندازه یک نارنگی کوچک از مایه کوفته را بردارید و با کمک دست به صورت گلوله‌ای فرم دهید. این کار را تا زمانی ادامه دهید که تمام مایه گوشتی به تعدادی کوفته‌های گرد و قلنبه تبدیل شود. حالا وقت آن است که مقداری روغن را در تابه مناسبی بریزید و روی حرارت متوسط بگذارید تا داغ شود. سپس، کوفته‌های فرم داده شده را به تابه اضافه کرده و هر دو طرف آن‌ها را تفت دهید. توجه داشته باشید، لازم نیست کوفته‌ها را کاملا سرخ کنید. پس از سرخ شدن کوفته‌ها، آن‌ها را از تابه خارج کنید و کنار بگذارید. سپس، باقی پیاز رنده شده را در همان تابه‌ای بریزید که کوفته‌ها را تفت دادید. در صورت لزوم، می‌توانید مقدار کمی روغن به تابه اضافه کنید. پیاز را هم بزنید و چند دقیقه منتظر بمانید تا کاملا سبک شود. در ادامه، سیر رنده شده را نیز در این تابه بریزید و همراه با پیاز برای یکی دو دقیقه تفت دهید. پس از آن که مخلوط پیاز و سیر رنده شده به اندازه کافی تفت داده شد، آرد سفید را نیز در تابه بریزید و همراه با باقی مواد تفت دهید تا طعم خامی آن برطرف شود. حالا، زنجبیل تازه را نیز در تابه بریزید و برای چند ثانیه تفت دهید. سپس، پوره گوجه فرنگی را هم که قبلا آماده کردید به محتویات تابه اضافه کنید. این مواد را به خوبی هم بزنید تا طعم سیر و زنجبیل در سطح تابه به صورت یکسان پراکنده شود. یکی دو دقیقه منتظر بمانید تا آب پوره گوجه فرنگی نسبتا کشیده شود. پس از طی کردن مراحل بالا، وقت آن است که گشنیز خشک، مقداری ادویه کاری، زیره سیاه، نمک و فلفل سیاه را نیز به تابه اضافه کنید. این مواد را به خوبی هم بزنید تا کاملا با یکدیگر ترکیب شده و عطر ادویه‌ها آزاد شود. حالا، عصاره گوشت را در یک لیوان آب جوش حل کرده و به مواد داخل تابه اضافه کنید. محتویات تابه را به خوبی هم بزنید تا کاملا با یکدیگر مخلوط شوند. این ترکیب را حدود ۲ دقیقه هم بزنید تا سس نسبتا غلیظی ایجاد شود. سپس، خامه را نیز در تابه بریزید و مواد را دوباره هم بزنید. در این مرحله از تهیه این غذای هندی خوشمزه، کوفته‌هایی که قبلا سرخ کردید را در تابه حاوی سس بچینید. درب قابلمه را باز بگذارید و اجازه بدهید محتویات تابه همراه با یکدیگر بجوشند و جا افتاده شوند. پس از ۵ دقیقه، کوفته‌ها را زیر و رو کنید تا سمت دیگر آن‌ها نیز طعم بگیرد و پخته شود. پس از پختن کوفته‌ها و غلیظ و جا افتاده شدن سس، وقت آن است که این غذای هندی خوشمزه را در ظرف سرو بریزید و همراه با نان، برنج چلو یا برنج کته نوش جان کنید. برای تزیین کوفته کاری هندی می‌توانید از چند برگ گشنیز تازه یا ریحان تازه خرد شده استفاده کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/12/%DA%A9%D9%88%D9%81%D8%AA%D9%87-%DA%A9%D8%A7%D8%B1%DB%8C-2-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (218, 'کوفته کباب یونانی با سس ماست', 'برای درست کردن کوفته کباب یونانی ابتدا حبه‌های سیر را خرد کنید، به آن‌ها نمک بپاچید و با سمت صاف یک چاقوی بزرگ، مخلوط را له کرده تا خمیر مانند شود.', 'برای درست کردن کوفته کباب یونانی ابتدا حبه‌های سیر را خرد کنید، به آن‌ها نمک بپاچید و با سمت صاف یک چاقوی بزرگ، مخلوط را له کرده تا خمیر مانند شود. این خمیر را با گوشت، پیاز، جعفری و ادویه‌جات (گشنیز، زیره، دارچین، فلفل دلمه‌ای، فلفل کاین، زنجبیل، فلفل سیاه) مخلوط کنید. (می‌توانید از گوشت چرخ کرده مرغ یا بوقلمون نیز استفاده کنید). مخلوط گوشت را به ۲۸ توپ گوشتی تقسیم کنید. هر قطعه را به سیخ بگیرید (اگر از سیخ چوبی استفاده می‌کنید، آن‌ها را قبل از استفاده به مدت ۱۵ دقیقه در آب خیس کنید)، یک کباب بیضی شکل ۵ سانتی‌متری درست کنید و سعی کنید این کار را طوری انجام دهید که فقط نوک سیخ را بپوشاند. سیخ‌ها را در یک ظرف قرار دهید، روی آن را بپوشانید و حداقل به مدت ۳۰ دقیقه و تا ۱۲ ساعت در یخچال قرار دهید تا کباب‌ها استراحت کنند. یک توری مخصوص کباب‌پزی را روی حرارت متوسط گرم کنید یا از یک کباب پز استفاده کنید. ظرف را با روغن زیتون کمی چرب کنید و سیخ‌های کباب را روی آن بگذارید. حدود ۶ دقیقه زمان بدهید تا کباب‌ها بپزند؛ گاه گاهی سیخ‌ها را بچرخانید تا رنگ کباب‌ها کاملاً قهوه‌ای شده و پخته شوند. بعد از پخت کباب‌ها را به ظرف سرو منتقل کنید. برای تهیه سس ماست، خیار را با رنده‌های سایز بزرگ در ظرفی رنده کنید. روی خیارهای رنده شده نمک بپاشید و نمک را با دست به خیار بمالید. به مدت ۲۰ دقیقه کنار بگذارید و سپس خیار را در دست یا با پشت قاشق به خوبی فشار دهید تا تا جای ممکن آب آن گرفته شود. سیر را خرد کنید، روی آن نمک بپاشید و با کمک چاقو، خمیری مشابه با آنچه که برای کباب درست کردید، تهیه کنید. حالا خیار، خمیر سیر، روغن زیتون، آب لیمو و نعناع را در ماست هم بزنید. سس ماست شما آماده است. قبل از سرو حداقل ۱ ساعت در یخچال قرار دهید. سس ماست را می‌توانید روی کوفته کباب یونانی بریزید یا در کنار آن سرو کنید. در کنار کوفته کباب یونانی با سس ماست، سالاد سبزیجات هم سرو می‌شود که سالاد مدیترانه‌ای بیشتر رایج و محبوب است.', 12, 24, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/11/kofta_2018_92ground-020_crop_web_2048x-rotated-e1605689819410.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (219, 'کوفته گردو', 'کوفته گردو یکی از انواع کوفته خوش طعم و محلی است که معمولاً هم با گوشت و رب انار و هم بدون گوشت با استفاده از لپه و سبزیجات معطر مختلف طبخ می‌شود. ترکیب طعم‌های مختلف در کوفته گردو آن را به یکی از خوش', 'کوفته گردو یکی از انواع کوفته خوش طعم و محلی است که معمولاً هم با گوشت و رب انار و هم بدون گوشت با استفاده از لپه و سبزیجات معطر مختلف طبخ می‌شود. ترکیب طعم‌های مختلف در کوفته گردو آن را به یکی از خوشمزه‌ترین و سالم‌ترین غذاهای زمستانی با طبع گرم تبدیل کرده است. در این مقاله با طرز تهیه کوفته گردویی ساده به دو روش مختلف آشنا شوید. کوفته گردو با لپه و بدون رب گوجه یکی از ساده‌ترین و خوشمزه‌ترین انواع کوفته خانگی است که بدون گوشت و با استفاده از گردوی خرد شده و لپه با سبزیجات مختلف درست می‌شود. اگر دوست دارید یک کوفته خوشمزه گیاهی درست کنید کوفته گردویی بهترین گزینه برای شما است. در ادامه مراحل درست کردن کوفته گردو با لپه را دنبال کنید. در اولین مرحله از طرز تهیه کوفته گردو باید پیاز را بشویید و آن را به صورت نگینی و ریز خرد کنید. سپس مقداری روغن در ماهیتابه بریزید و پیازها را سرخ کنید تا رنگ آن‌ها طلایی شود. در این مرحله باید لپه‌ها را به همراه مقداری آب در قابلمه بریزید. سپس آن را روی شعله گاز قرار دهید تا لپه‌های کاملاً نرم و پخته شوند. با استفاده از گوشتکوب دستی یا غذاساز لپه‌ها را کاملاً له کنید و برای تهیه کوفته گردو کنار بگذارید. در این مرحله از دستور پخت کوفته گردو باید یک کاسه نسبتاً بزرگ انتخاب کنید. تخم مرغ‌ها را در کاسه بشکنید و با پودر نشاسته، پودر مغز گردو، مرزه و ترخون خشک، تره ساطوری خرد شده و لپه پخته شده ترکیب کنید. مقداری زردچوبه، نمک و فلفل سیاه به مواد بزنید و با دست به خوبی مواد را ورز دهید. بعد از آماده کردن مواد کوفته باید پیاز داغ را که آماده کرده بودید، به همراه کمی زردچوبه و آب جوش در قابلمه بریزید اجازه دهید آب به جوش بیاید و بعد مواد کوفته را به اندازه گردو بردارید و گرد کنید و در آب در حال جوش بیندازید. اجازه دهید کوفته‌ها برای نیم ساعت در آب جوش بپزند. بعد از این مدت شعله گاز را خاموش کنید و کوفته‌های گردویی را در ظرف بچینید و با گردو و مقداری سبزی خرد شده تزیین کنید. کوفته گردویی را با نان یا برنج کته یا آبکش و مخلفات دلخواه سرو کنید. کوفته گردو آذری‌ها به خصوص شهر ارومیه برخلاف کوفته گردوهای دیگر با استفاده رب گوجه و گوجه فرنگی تازه درست می‌شود. این کوفته درست مثل کوفته گردویی که پیش‌تر با طرز تهیه آن آشنا شدید، بدون گوشت آماده می‌شود؛ اما طعم رب گوجه و گوجه فرنگی تازه در سس این کوفته مزه فوق‌العاده‌ای به آن می‌بخشد. در ادامه می‌توانید با مراحل درست کردن کوفته گردو به سبک ارومیه آشنا شوید. در اولین مرحله از طرز تهیه مایه کوفته گردویی بدون گوشت باید لپه‌های خیس خورده را بپزید. بعد از پخت کامل لپه، آن‌ها را با گوشتکوب له کنید و سپس لپه له شده را با گردوی آسیاب شده، آرد ذرت، تره ساطوری خرد شده، تخم مرغ، زردچوبه، نمک و فلفل سیاه ترکیب کنید. یک دستکش به دست کنید و با دست مایه کوفته را برای چند دقیقه ورز دهید تا حالت چسبنده پیدا کند. در این مرحله از دستور پخت کوفته گردویی باید پیاز را به صورت نگینی خرد کنید و در مقداری روغن سرخ کنید. زمانی که پیاز طلایی شد، می‌توانید نمک، فلفل سیاه و زردچوبه را اضافه کنید. رب گوجه را همراه با گوجه فرنگی‌های خرد شده به پیاز داغ اضافه کنید و سپس ۴ لیوان آب به مواد اضافه کنید. شعله گاز را زیاد کنید تا آب به جوش بیاید. در این مرحله باید دستانتان را با مقداری روغن مایع چرب کنید. سپس به اندازه یک گردو از کوفته بردارید و آن را کاملاً گرد کنید و در سس در حال جوش بیندازید. این کار را برای همه کوفته‌ها تکرار کنید. بعد از اضافه کردن کوفته‌ها شعله گاز را کم کنید و اجازه دهید کوفته‌ها برای ۱۰ دقیقه به آرامی در سس کوفته بپزند. مقدار سس کوفته باید به اندازه‌ای باشد که همه کوفته‌ها به راحتی درون سس قرار بگیرند. سپس شعله گاز را کم کنید تا هم کوفته‌ها بپزند و هم سس کوفته کمی غلیظ شود. در نهایت کوفته گردویی را با نان، سبزی خوردن و مخلفات دلخواه سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/08/RT-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (220, 'کوفته گندی', 'اگر آرد گوندی آماده در دسترس ندارید، می‌توانید به میزان گفته شده، ماش، عدس، نخود، گندم، سویا، سنگک (تخم سبزی گرگرو)، ذرت و باقلا را یک شب تا صبح بخیسانید و آبکش کنید. وقتی آب خود را از دست دادند، همه', 'اگر آرد گوندی آماده در دسترس ندارید، می‌توانید به میزان گفته شده، ماش، عدس، نخود، گندم، سویا، سنگک (تخم سبزی گرگرو)، ذرت و باقلا را یک شب تا صبح بخیسانید و آبکش کنید. وقتی آب خود را از دست دادند، همه مواد را در تابه تفت دهید، در حدی که طعم خام آنها گرفته شود ولی سرخ نشوند. وقتی مواد آرد از دما افتادند، آنها را در هاون بکوبید یا در آسیاب پودر کنید. سپس آرد را الک کنید تا کاملا یکدست شود. آرد را برای درست کردن کوفته، کنار بگذارید. در ابتدا برای درست کردن کوفته گوندی، ۲ پیاز را پوست بگیرید و رنده کنید. به خاطر استفاده از آرد در ترکیبات مواد کوفته، لازم نیست آب پیازها را بگیرید. گوشت چرخ کرده را در یک کاسه بریزید و پیازهای رنده شده را به آن اضافه کنید. گوشت چرخ کرده و جعفری و تره تازه را به همراه مقداری زردچوبه، فلفل سیاه و نمک اضافه کنید. آرد گوندی را هم به مواد بیفزایید. با دست شروع به ورز دادن مواد کوفته کنید. به مدت ۱۰ تا ۱۵ دقیقه مایه کوفته گوندی را ورز دهید. اگر مایه کوفته بعد از ورز دادن، هنوز هم سفت بود، می‌توانید مقداری آب جوشیده و از دما افتاده (به تدریج و کنترل شده) به مایه کوفته اضافه کنید. وقتی مایه کوفته در حدی نرم شد که به دست نمی‌چسبید یا ترک نمی‌خورد، روی ظرف را بپوشانید و به مدت ۱۰ دقیقه در یخچال استراحت دهید. در فاصله‌ای که مایه کوفته در حال استراحت است، یک پیاز باقی مانده را پوست بگیرید و به صورت نگینی ریز خرد کنید. در یک تابه، دنبه را بیندازید و روی حرارت بگذارید تا ذوب شود. سپس پیاز را در آن سرخ کنید تا سبک و شیشه‌ای شود. شعله را خاموش کنید و بلافاصله، نعناع خشک را در تابه بریزید و تفت دهید تا با داغی روغن، نعناع هم داغ شود. مایه کوفته گندی را که استراحت کرده است، از یخچال بیرون بیاورید. یک کاسه آب کنار دستتان بگذارید و به اندازه یک گردوی بزرگ از مایه کوفته بردارید و بین دست‌هایتان گلوله کنید. برای اینکه کوفته‌ها ترک نخورند، هر بار دستتان را ابتدا در کاسه آب بزنید و بعد کوفته را فرم دهید. همه کوفته‌ها را به همین شکل درست کنید و کنار بگذارید. در یک قابلمه، به اندازه ۲ تا ۳ لیوان دسته‌دار فرانسوی، آب بریزید و روی حرارت بگذارید تا بجوشد. بعد از اینکه آب جوشید، کوفته‌ها را در تابه‌ای که پیاز و نعناع را در آن سرخ کرده‌اید، بچینید. بهتر است تابه، عمیق باشد. سپس آب جوش را از کناره‌های تابه، در تابه بریزید، در حدی که ۳/۴ کوفته‌ها در سس باشد. درب تابه را یا کلا باز بگذارید یا تا نیمه باز بگذارید. شعله را روی حالت ملایم تنظیم کنید و اجازه دهید کوفته‌ها به آرامی بپزند. هر از گاهی به کوفته‌ها سر بزنید تا سس کوفته تمام نشود. از مایه سس روی کوفته‌ها بریزید. اگر سس کوفته‌ها خیلی کم شد، کمی آب جوش اضافه کنید. به مدت ۳۰ تا ۴۵ دقیقه کوفته‌ها را روی حرارت ملایم بگذارید تا بپزد. سپس شعله را خاموش کنید و کوفته‌ها را در ظرف سرو بچینید و آن را با نان سرو کنید.', 21, 44, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/11/9804f42d31209950aba6d94eecbb57a3IVMAfgYsBdUEmmCW0.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (221, 'کوفته گوجه فرنگی با سس مخصوص', 'در اولین مرحله از طرز تهیه کوفته گوجه فرنگی، یک عدد پیاز را پوست بگیرید و آن را بشویید. قسمت انتهایی پیاز را جدا نکنید تا موقع رنده کردن وا نرود و لایه‌های آن از هم جدا نشود. پیاز را با رنده، به صورت', 'در اولین مرحله از طرز تهیه کوفته گوجه فرنگی، یک عدد پیاز را پوست بگیرید و آن را بشویید. قسمت انتهایی پیاز را جدا نکنید تا موقع رنده کردن وا نرود و لایه‌های آن از هم جدا نشود. پیاز را با رنده، به صورت ریز رنده کنید. سپس با دست پیازهای رنده شده را فشرده کنید تا آب آن جدا شود. آب پیاز را دور بریزید و تفاله آن را به یک ظرف مناسب انتقال دهید. نیم کیلو گوشت چرخ کرده را به پیاز اضافه کنید و گوشت و پیاز را به خوبی و برای چند دقیقه ورز دهید تا به طور یکدست ترکیب شوند. بعد از آنکه گوشت و پیاز حسابی با هم مخلوط شوند وسط آن را خالی کنید و یک تخم مرغ درون آن بشکنید. سپس آرد سوخاری، ادویه شامل نمک، فلفل سیاه و زیره را نیز روی تخم مرغ بریزید. مجددا با دست شروع به هم زدن و ورز دادن مایه کوفته گوجه فرنگی بکنید تا همه مواد به طور یکدست مخلوط شوند. به اندازه یک گردوی بزرگ از مایه کوفته بردارید و با دست آنها را شکل دهید. کوفته‌ها را به شکل گرد درست کنید. اندازه آنها نه باید زیاد بزرگ باشد و نه کوچک. این کار را تا تمام شدن مایه کوفته ادامه دهید. کوفته‌ها را در یک ظرف مناسب بچینید و روی آن را سلفون بکشید. ظرف حاوی کوفته‌ها را برای یک ساعت در یخچال قرار دهید تا استراحت کند. در مدت زمانی که گوشت در یخچال استراحت می‌کند فرصت دارید پوره گوجه را برای سس کوفته گوجه فرنگی آماده کنید. مقدار لازم از گوجه فرنگی را ابتدا با آب به خوبی بشویید و در آبکش قرار دهید تا آب اضافه از آن خارج شود. سپس آنها را به وسیله غذاساز یا میکسر به صورت پوره در آورید. برای پوره کردن گوجه‌ها می‌توانید آنها را از وسط نصف و سپس رنده کنید. پوره گوجه فرنگی را برای مراحل بعدی کنار بگذارید. در این مرحله از طرز تهیه کوفته گوجه فرنگی، بعد از گذشت یک ساعت کوفته‌ها را از یخچال بیرون بیاورید. یک تابه روی حرارت قرار دهید و در آن مقدار کافی از روغن بریزید. صبر کنید تا روغن به خوبی داغ شود، سپس کوفته‌ها را به روغن اضافه کنید. حرارت را روی حالت متوسط قرار دهید تا کوفته‌ها به خوبی سرخ و مغزپخت شوند. کوفته‌ها را بچرخانید تا از همه طرف به طور یکسان سرخ شوند. بعد از آنکه همه کوفته‌ها سرخ شدند آنها را از تابه بیرون بیاورید و کنار بگذارید. برای تهیه کوفته گوجه فرنگی لذیذ به سس مخصوص آن نیاز دارید. در همان تابه‌ای که کوفته‌ها را سرخ کردید یک قاشق رب گوجه فرنگی اضافه کنید. رب گوجه را به خوبی در روغن تفت دهید تا رنگش باز شود و از خامی در بیاید. سپس پوره گوجه فرنگی را اضافه کنید و حرارت را زیاد کنید تا به جوش بیاید. بعد از آن حرارت را کم کنید و اجازه دهید پوره گوجه فرنگی روی حرارت ملایم به غلظت برسد. کوفته‌ها را در یک ظرف مناسب فر بریزید و روی آن سس گوجه غلیظ شده را بریزید. به دلخواه می‌توانید کمی پنیر چدار به این ترکیب اضافه کنید. ظرف کوفته با سس گوجه فرنگی را در فر قرار دهید. اجازه دهید کوفته با سس گوجه به مدت ۲۰ دقیقه و با دمای ۱۸۰ درجه سانتی‌گراد بپزد. بعد از گذشت بیست دقیقه کوفته گوجه فرنگی شما آماده است. ظرف کوفته را از فر بیرون بیاورید و در ظرف مناسب سرو کنید. به دلخواه می‌توانید از مقداری جعفری خرد شده برای تزئین این غذای نونی خوشمزه استفاده کنید. کوفته گوجه فرنگی را با نان و سبزی خوردن میل کنید.', 11, 24, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/koufteh-ghelgheli-with-cotyledons-recipe.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (222, 'کوفته گوشت فوری', 'در اولین مرحله از طرز تهیه این کوفته فوری با گوشت کم باید یکی از پیازها را به‌صورت ریز رنده کرده یا با کمک غذاساز پوره کنید. سپس، پیاز رنده شده را با دست فشار دهید تا آبش گرفته شود. مراقب باشید، آب پی', 'در اولین مرحله از طرز تهیه این کوفته فوری با گوشت کم باید یکی از پیازها را به‌صورت ریز رنده کرده یا با کمک غذاساز پوره کنید. سپس، پیاز رنده شده را با دست فشار دهید تا آبش گرفته شود. مراقب باشید، آب پیاز را دور نریزید؛ چون برای تهیه سس کوفته به آن نیاز دارید. حالا تفاله پیاز را در کاسه مناسبی بریزید و گوشت چرخ شده، تخم مرغ، شیر، آرد سوخاری و همچنین، پودر مرزه خشک، مقداری پودر دارچین، زردچوبه، نمک و فلفل سیاه را به آن اضافه کنید. این ترکیب را حداقل به مدت ۵ دقیقه ورز دهید تا کاملا یکدست و منسجم شود. حالا، درب کاسه را با سلفون بپوشانید و برای چند دقیقه کنار بگذارید تا مایه گوشت کوفته استراحت کند. برای درست کردن سس کوفته گوشت، باید یکی از پیازها را نگینی خرد کنید. سپس، مقداری روغن را در تابه یا قابلمه مناسبی بریزید و روی حرارت متوسط قرار دهید تا داغ شود. قطعات پیاز را داخل روغن بریزید و صبر کنید طلایی و سبک شوند. سپس، مقدار کمی زردچوبه، نمک، فلفل سیاه و پودر دارچین را به پیاز داغ اضافه کنید و تفت دهید تا عطر ادویه‌ها آزاد شود و طعم خامی ادویه گرفته شود. رب گوجه فرنگی را هم به این ترکیب اضافه کنید و تفت دهید تا رب رنگ باز کند. حالا، به اندازه یک لیوان دسته‌دار فرانسوی آب جوش در قابلمه بریزید و مواد سس را هم بزنید تا کاملا با هم ترکیب شوند. یادتان باشد، آب پیاز رنده شده را هم به این ترکیب اضافه کنید. درب قابلمه را ببندید، حرارت را ملایم کنید و اجازه بدهید سس کوفته جا افتاده شود و به غلظت دلخواه برسد. حالا، یک پیاله آب سرد در کنار دست‌تان بگذارید و کف دست‌های خود را با آن مرطوب کنید. سپس، به اندازه یک گردوی درشت از مایه گوشت کوفته را بردارید و با استفاده از هر دو دست کاملا گرد کنید. توجه داشته باشید، سطح کوفته‌ها باید کاملا یکدست و یک‌پارچه بوده و هیچ ترکی نداشته باشد. همه مایه کوفته را به همین ترتیب درست کنید. برای چند لحظه زیر قابلمه حاوی سس را خاموش کنید تا از قل قل بیفتد. سپس، کوفته‌ها را به آرامی و با دقت داخل قابلمه بچینید. این کار را در عین آرامش با سرعت انجام دهید؛ چون سس کوفته به‌هیچ عنوان نباید سرد شود چون کوفته‌ها وا می‌روند. حالا، شعله گاز را دوباره روشن کرده و روی حرارت ملایم تنظیم کنید. در ادامه، سس گوجه فرنگی، سس خردل و شکر قهوه‌ای را کاملا با هم ترکیب کنید و خوب هم بزنید. این ترکیب را روی کوفته‌ها بریزید. درب قابلمه را ببندید و حدود نیم ساعت تا ۴۰ دقیقه منتظر بمانید تا کوفته گوشت خوشمزه شما آماده شود. این مدل کوفته گوشت یک غذای فوری است که معمولا با نان سرو می‌شود. با این حال، در صورت تمایل می‌توانید آن را همراه با برنج چلو یا برنج کته هم نوش جان کنید. به دلخواه می‌توانید کوفته‌ها را با مغز گردو، سبزی خوردن یا گوجه گیلاسی تزیین کرده و همراه با ترشی، ماست، زیتون و انواع و اقسام چاشنی‌های دیگر میل کنید.', 13, 27, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/07/%DA%A9%D9%88%D9%81%D8%AA%D9%87-2-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (223, 'کوفته ماهی جنوبی', 'ابتدا لپه را به مدت ۸ ساعت و برنج ایرانی را به مدت ۱ ساعت، در دو ظرف جدا، خیس کنید. سپس لپه و برنج را با مقداری آب، روی حرارت بگذارید. صبر کنید تا لپه و برنج نیم‌پز شوند. سپس آنها را آبکش کنید.', 'ابتدا لپه را به مدت ۸ ساعت و برنج ایرانی را به مدت ۱ ساعت، در دو ظرف جدا، خیس کنید. سپس لپه و برنج را با مقداری آب، روی حرارت بگذارید. صبر کنید تا لپه و برنج نیم‌پز شوند. سپس آنها را آبکش کنید. ماهی‌ها را بشویید و سر و دم آنها را جدا کنید. می‌توانید از ماهی شیر، ساردین یا مومغ، ماهی حلوا و هر ماهی که استخوان و تیغ کمی دارد، استفاده کنید. ماهی‌ها را بعد از شستن در یک قابلمه بگذارید. مقداری آب (به اندازه ۱ لیوان فرانسوی دسته‌دار) و زردچوبه را روی ماهی‌ها بریزید و قابلمه را روی حرارت بگذارید. اجازه دهید ماهی‌ها به مدت ۲۰ دقیقه با آب و زردچوبه بپزند و نرم شوند. زردچوبه بوی زهم ماهی را تا حد زیادی می‌گیرد. در این فاصله، سبزی‌های معطر را بشویید، در آبکش بگذارید تا آب اضافی خود را از دست بدهند. سپس آنها را روی تخته آشپزخانه، به صورت ریز و ساطوری خرد کنید و کنار بگذارید. بعد از اینکه ماهی‌ها آبپز شدند، آنها را در آبکش بریزید. وقتی ماهی‌ها سرد شدند، تیغ‌ها و استخوان‌ها و پوست آنها را به راحتی جدا کنید. ماهی‌ها را در یک کاسه بریزید. پیاز را رنده کنید و آب آن را با دست بگیرید. تفاله پیاز را در کاسه‌ای که ماهی‌ها را ریخته‌اید، بریزید. لپه و برنج را هم اضافه کنید. سبزی‌های خرد شده را هم اضافه کنید. مواد کوفته ماهی را با هم مخلوط کنید و در غذاساز یا چرخ گوشت بریزید و دو بار چرخ کنید تا کاملا یکدست شوند. سپس به آنها، فلفل سیاه، نصف میزان نمکی که دارید (نصف قاشق غذاخوری)، آرد گندم، آرد نخودچی و ۱ تخم مرغ را اضافه کنید. سپس با دست مواد را کاملا ورز دهید تا یکدست و منسجم شوند. روی ظرف را بپوشانید و در یخچال بگذارید. در فاصله‌ای که مایه کوفته ماهی در یخچال است، یک پیاز باقی‌مانده را پوست بگیرید و نگینی ریز خرد کرده یا با رنده درشت رنده کنید. گوجه‌فرنگی‌ها را بشویید و رنده کنید و کنار بگذارید. در یک تابه مقداری روغن بریزید و روی حرارت بگذارید. روغن که داغ شد، پیازها را در تابه بریزید و تفت دهید تا سبک و طلایی شوند. سپس رب گوجه فرنگی را اضافه کنید و تفت دهید. بعد از اینکه رب گوجه رنگ پس داد و خامی آن گرفته شد، پوره گوجه فرنگی را اضافه کنید. درب تابه را بگذارید و صبر کنید تا گوجه بپزد. سپس به اندازه ۲ لیوان آب جوش، در تابه بریزید و اجازه دهید آب بجوشد. در صورت تمایل، شکر، نمک و آبلیمو را هم اضافه کنید. حرارت را روی حالت خیلی ملایم قرار دهید. در حین آماده شدن سس کوفته، مواد کوفته را از یخچال بیرون بیاورید و به اندازه ۲ قاشق غذاخوری از مایه را کف دستتان بگذارید و گلوله کنید. داخل کوفته را یک حفره ایجاد کنید. سپس آلو و گردو را در کوفته بگذارید. دوباره کوفته را گرد کنید تا گردو و آلو داخل کوفته پنهان شوند. همه کوفته‌ها را به همین شکل آماده کنید و در یک سینی بگذارید. برای جلوگیری از وا رفتن کوفته ماهی می‌توانید در یک تابه، کوفته‌ها را با کمی روغن، سرخ کنید. نیازی نیست کاملا طلایی شوند. سپس کوفته‌ها را در سس کوفته بگذارید. دقت کنید که کوفته‌ها روی هم قرار نگیرند و تا ۲/۳ آنها، سس باشد. شعله را کم کنید و درب تابه را نیمه‌باز بگذارید. حدود ۱ ساعت، کوفته‌ها را روی حرارت ملایم بپزید. در این مدت، به کوفته‌ها سر بزنید و روی کوفته‌ها سس بریزید. نیم ساعت بعد پخت هم آنها را به آرامی، زیر و رو کنید. وقتی کوفته‌ها پختند و سس غلیظ شد، شعله را خاموش کنید و کوفته‌ها را در ظرف سرو بگذارید. کوفته ماهی را با نان و سبزی خوردن، انواع سالاد یا انواع ترشی می‌توانید سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/12/wgeinoe6f4p1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (224, 'کوفته مدیترانه ای (کوفته قبرسی)', 'پیش از هر کاری، ابتدا پیاز شسته شده و بدون پوست را رنده کنید و با دست فشار دهید تا آب آن خارج شود. برای درست کردن کوفته مدیترانه ای، شما فقط به تفاله پیاز نیاز دارید.', 'پیش از هر کاری، ابتدا پیاز شسته شده و بدون پوست را رنده کنید و با دست فشار دهید تا آب آن خارج شود. برای درست کردن کوفته مدیترانه ای، شما فقط به تفاله پیاز نیاز دارید. سپس، سیب زمینی را بشویید و پس از پوست گرفتن، به صورت ریز رنده کنید. سیب زمینی خام رنده شده را نیز درست مانند پیاز فشار دهید تا آب آن گرفته شود. آب سیب زمینی را هم دور بریزید و تفاله آن را نگهدارید. در این مرحله از طرز تهیه کوفته قبرسی، ظرف مناسبی را انتخاب کنید و گوشت چرخ کرده، تفاله پیاز رنده شده و تفاله سیب زمینی خام رنده شده را در این ظرف بریزید. این مواد را به خوبی ورز دهید تا به صورت یکنواخت با یکدیگر ترکیب شوند. حالا، وقت آن است که تخم مرغ، نمک، نعنا خشک، پودر دارچین و فلفل سیاه را نیز به مایه کوفته مدیترانه ای اضافه کنید. پس از افزودن این مواد، مایه کوفته را دوباره ورز دهید تا همه مواد به خوبی با یکدیگر ترکیب شوند و مایه منسجمی ایجاد شود. در این مرحله از طرز تهیه کوفته گوشت مدیترانه ای، درب ظرف را ببندید یا با سلفون بپوشانید. سپس، ظرف حاوی مایه کوفته را برای ۱ ساعت در یخچال بگذارید تا حسابی خودش را بگیرد. پس از گذشت این مدت، مایه کوفته قبرسی را از یخچال بیرون بیاورید و پودر سوخاری را به آن اضافه کنید. مواد داخل ظرف را مجددا ورز دهید تا پودر سوخاری نیز به خوبی با مایه اولیه کوفته ترکیب شود. توجه داشته باشید، به جای پودر سوخاری می‌توانید از نان خشک آسیاب شده نیز استفاده کنید. پس از ترکیب کردن پودر سوخاری با باقی مواد، کف دست‌تان را مرطوب کنید و به اندازه یک گردوی متوسط از مایه کوفته را بردارید. سپس، مایه کوفته را با دست به شکل دوکی یا گلوله‌ای فرم دهید. بهتر است کاری کنید که همه کوفته‌ها از اندازه و فرم یکسانی برخوردار باشند. پس از فرم دادن کوفته‌ها، مقداری روغن را در تابه مناسبی بریزید و روی حرارت متوسط بگذارید تا داغ شود. سپس، کوفته‌های مدیترانه‌ای خوش فرم و خوشمزه را با فاصله در تابه قرار دهید و اطراف آن‌ها را به یک اندازه سرخ کنید. پس از پختن و طلایی شدن هر یک از کوفته‌ها، آن‌ها را از تابه بیرون بیاورید و در ظرف سرو قرار دهید. این کوفته‌های گوشتی و خوشمزه را می‌توانید با گوجه فرنگی، سیب زمینی سرخ شده، انواع سالاد، سبزی خوردن و نان نوش جان کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/%DA%A9%D9%88%D9%81%D8%AA%D9%87-%D9%85%D8%AF%DB%8C%D8%AA%D8%B1%D8%A7%D9%86%D9%87-%D8%A7%DB%8C-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (225, 'کوفته نخود فرنگی با سس کاری', 'برای تهیه کوفته نخود فرنگی با سس کاری، ابتدا سیب‌ زمینی‌ها را آبپز یا بخارپز کنید و کنار بگذارید تا خنک شوند.', 'برای تهیه کوفته نخود فرنگی با سس کاری، ابتدا سیب‌ زمینی‌ها را آبپز یا بخارپز کنید و کنار بگذارید تا خنک شوند. پیاز و سیر را ریز خرد یا رنده کنید. جعفری یا گشنیز تازه را نیز ریز ساطوری کنید. (در صورت عدم دسترسی به سبزی تازه، از خشک آن استفاده کنید). نخود فرنگی را حدود ۵ دقیقه در آب جوش بجوشانید، سپس آبکش کرده و اجازه دهید آب اضافی آن خارج شود. نصف نخود فرنگی‌ها را له کرده و نصف دیگر را سالم نگه دارید. در یک کاسه بزرگ، نخود فرنگی له شده و سالم، سیب‌زمینی رنده شده، پیاز، سیر، سبزی خرد شده و ادویه‌ها (نمک، فلفل سیاه، زردچوبه، پودر تخم گشنیز و فلفل قرمز) را بریزید و خوب ورز دهید. اگر مایه کوفته شل بود، به تدریج آرد نخودچی یا آرد سوخاری اضافه کنید تا مایه سفت و چسبناک شود. اگر سفت شد، چند قطره آبلیمو یا شیر اضافه کنید. از مایه به اندازه یک گردوی درشت بردارید و در کف دست گرد کنید. می‌توانید وسط هر کوفته پنیر موزارلا، گردو یا بادام قرار دهید. کوفته‌های آماده را در سینی بچینید و حدود نیم ساعت در یخچال قرار دهید تا خودشان را بگیرند و هنگام پخت وا نروند. برای تهیه سس کاری، در یک قابلمه یا تابه، ۲ تا ۳ قاشق روغن بریزید و روی حرارت متوسط قرار دهید. پیاز نگینی شده را تفت دهید تا سبک و طلایی شود. سیر له شده و زنجبیل رنده شده را اضافه کرده و یکی دو دقیقه دیگر تفت دهید تا عطرشان بلند شود. زردچوبه، فلفل سیاه، فلفل قرمز و گشنیز خشک را اضافه کرده و سریع هم بزنید تا نسوزد. گوجه‌ها را پوست بگیرید و رنده کنید. پوره گوجه‌فرنگی و رب گوجه را به محتویات تابه اضافه کنید و ۵ تا ۷ دقیقه بجوشانید تا آب آن کشیده و سس غلیظ شود. یک لیوان شیر نارگیل و کمی آب اضافه کنید، حرارت را کم کرده و اجازه دهید سس حدود ۱۰ دقیقه ریز‌جوش بخورد تا جا بیفتد. در آخر ادویه کاری یا گرام ماسالا و کمی نمک را اضافه کنید و هم بزنید. کوفته‌های نخود فرنگی را به آرامی داخل سس کاری بچینید. درب تابه را ببندید، حرارت را ملایم کنید و اجازه دهید کوفته‌ها حدود ۸ تا ۱۰ دقیقه با درب بسته بپزند تا طعم سس به خوردشان برود. پس از پخت، کوفته نخود فرنگی را همراه با سس در ظرف سرو کنید. این غذا را می‌توانید با انواع نان یا برنج سرو کنید.', 33, 67, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/09/%DA%A9%D9%88%D9%81%D8%AA%D9%87-%D9%86%D8%AE%D9%88%D8%AF-%D9%81%D8%B1%D9%86%DA%AF%DB%8C-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (226, 'کوفته نخود کرمانشاهی', 'برای تهیه کوفته نخود کرمانشاهی، ابتدا نخودها را از شب قبل خیس کنید و سپس خوب بپزید و له کنید تا بافتی لطیف داشته باشند. برنج نیم دانه را نیز بپزید.', 'برای تهیه کوفته نخود کرمانشاهی، ابتدا نخودها را از شب قبل خیس کنید و سپس خوب بپزید و له کنید تا بافتی لطیف داشته باشند. برنج نیم دانه را نیز بپزید. در یک کاسه بزرگ، نخود و برنج چرخ شده، گوشت چرخ کرده، پیاز رنده شده و آبگیری شده، سبزی خشک معطر (نعناع، تره، جعفری)، نمک، فلفل سیاه و زردچوبه را بریزید. اگر مایه شل بود یا موقع فرم دادن ترک می‌خورد، یک عدد تخم مرغ اضافه کنید تا انسجامش بیشتر شود. مواد کوفته را حدود ۱۰ تا ۱۵ دقیقه با دست ورز دهید تا کاملاً به هم بچسبند و حالت چسبندگی پیدا کنند. این مرحله برای انسجام کوفته‌ها بسیار مهم است. اگر مایه خیلی چسبنده بود، می‌توانید کمی آرد نخودچی یا آرد برنج پخته اضافه کنید. بعد از ورز دادن، مایه را حدود نیم ساعت در یخچال استراحت دهید. از مایه کوفته به اندازه یک نارنگی بردارید و کف دستتان باز کنید. وسط آن را گود کرده و مقداری گردو خردشده و کشمش یا قیسی بگذارید. سپس آرام با دست ببندید و گرد کنید تا کاملاً گرد و بدون ترک شود. همه کوفته‌ها را به همین شکل آماده کنید. در یک ماهیتابه بزرگ، روغن بریزید و بگذارید داغ شود. کوفته‌ها را آرام داخل روغن بگذارید و با حرارت متوسط سرخ کنید تا سطحشان طلایی و کمی سفت شود. این مرحله باعث می‌شود کوفته‌ها موقع پخت در سس وا نروند. بعد از سرخ شدن، کوفته‌ها را روی دستمال آشپزخانه بگذارید تا روغن اضافی‌شان گرفته شود. برای تهیه سس کوفته نخود کرمانشاهی، در یک قابلمه با کف پهن، پیاز نگینی خرد شده را با کمی روغن تفت دهید تا سبک و طلایی شود. سپس رب انار یا رب گوجه فرنگی را اضافه کرده و حدود ۲ تا ۳ دقیقه با پیاز تفت دهید تا خامی رب گرفته شود. بعد، آلو بخارا یا قیسی، شکر (اختیاری)، نمک، فلفل و حدود ۲ لیوان آب جوش اضافه کنید. اجازه دهید سس با حرارت ملایم بجوشد تا مواد خوب با هم ترکیب شوند. اگر دوست دارید، می‌توانید کمی نعناع خشک هم آخرش اضافه کنید. برای لعاب دادن سس، کمی آرد برنج را با آب مخلوط کرده و به سس اضافه کنید. کوفته‌های سرخ شده را آرام داخل قابلمه سس بچینید. حرارت را کم کنید. ۱۵ دقیقه اول درب قابلمه را نیمه باز بگذارید، سپس درب قابلمه را ببندید تا کوفته‌ها حدود نیم ساعت تا ۴۵ دقیقه با سس بپزند. هر چند دقیقه یک بار با قاشق، سس را روی کوفته‌ها بریزید. اگر سس خیلی غلیظ شد، کمی آب جوش اضافه کنید. در آخر پخت، کوفته‌ها باید نرم، خوش‌عطر و کاملاً جا افتاده باشند. وقتی کوفته‌های نخود کاملاً پختند و سس به خوردشان رفت، شعله زیر آن را خاموش کنید. می‌توانید کوفته نخود را با نان تازه، سبزی خوردن یا چلو سرو کنید. ماست یا ترشی کنار آن، طعم غذا را کامل می‌کند. برای سرو مجلسی‌تر، می‌توانید کوفته‌ها را در ظرف سفالی یا سنتی چیده و با کمی نعناع داغ تزئین و سرو کنید.', 28, 57, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/09/photo.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (227, 'کوفته هلو شیرازی', 'برای درست کردن کوفته هلو شیرازی، ابتدا باید مایه کوفته هلو را آماده و درست کنید و بعد از ورز دادن مواد، آن‌ها را در یخچال استراحت دهید. در این فاصله سس کوفته هلو را درست کنید. در نهایت کوفته‌ها را به', 'برای درست کردن کوفته هلو شیرازی، ابتدا باید مایه کوفته هلو را آماده و درست کنید و بعد از ورز دادن مواد، آن‌ها را در یخچال استراحت دهید. در این فاصله سس کوفته هلو را درست کنید. در نهایت کوفته‌ها را به شکل گرد، فرم دهید و مواد میانی را در کوفته‌ها بگذارید. کوفته‌های آماده شده را در نهایت در سس قرار دهید و اجازه دهید کوفته هلو شیرازی به آرامی بپزد. در اولین قدم از طرز تهیه کوفته هلو شیرازی، پیاز و هویج‌ها را رنده کنید و آب آن را بگیرید. گوشت چرخ کرده، تخم مرغ، زردچوبه، نمک و فلفل را به همراه آرد نخودچی به پیاز و هویج رنده شده اضافه و مواد را با هم ترکیب کنید و ورز دهید تا یکدست شود. حدود ۱۰ دقیقه مواد را ورز دهید تا کاملا منسجم شوند. روی ظرفی که مایه کوفته را در آن درست کرده‌ بودید، بپوشانید و به مدت ۳۰ دقیقه تا ۱ ساعت در یخچال قرار دهید تا استراحت کند. بعد از آماده شدن، مایه کوفته را از یخچال بیرون بیاورید و به اندازه یک هلو از آن را بردارید و داخل دستتان باز کنید. وسط کوفته را به میزان دلخواه، از مواد میانی کوفته هلو شامل کشمش، گردو، زرشک و آلو بخارای بدون هسته بگذارید. سپس آن را به شکل گلوله در آورید. همه کوفته‌ها را به همین شکل درست کنید. در این مرحله از طرز تهیه کوفته هلو شیرازی اصل، سس آن را درست کنید. برای این کار پیاز و هویج را ریز و نگینی خرد و در تابه با مقداری روغن و پودر دارچین سرخ کنید. رب گوجه فرنگی را اضافه کنید و تفت دهید تا هم طعم خام آن گرفته شود و هم رنگ باز کند. نعنا خشک را به مدت ۱ دقیقه تفت دهید. سپس به اندازه ۴ لیوان، آب جوش به مواد داخل تابه اضافه کنید. برنج را به مواد داخل قابلمه اضافه کنید. می‌توانید از برنج نیم‌دانه هم استفاده کنید. صبر کنید تا سس شروع به جوشیدن کند و کمی غلیظ شود. یک تابه را روی حرارت بگذارید و به اندازه ۳ تا ۴ قاشق روغن در آن بریزید. وقتی روغن داغ شد، کوفته‌ها را در تابه بچینید. کوفته‌ها را سرخ کنید تا همه لایه بیرونی آن طلایی شود. کوفته‌ها که سرخ شدند، آنها را در سس بچینید و در ظرف را بگذارید. بعد از حدود ۴۵ دقیقه آبلیمو را به همراه چاشنی‌های دلخواه به تابه اضافه کنید. وقتی سس به خورد کوفته‌های هلو رفت و طعم یکدیگر را گرفتند، می‌توانید آن‌ها را در ظرف مناسب همراه با سبزی، سالاد شیرازی و نان سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/03/%DA%A9%D9%88%D9%81%D8%AA%D9%87-%D9%87%D9%84%D9%88-%D8%B4%DB%8C%D8%B1%D8%A7%D8%B2%DB%8C.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (228, 'کوفته هویج', 'برای درست کردن کوفته هویج، ابتدا هویج و پیاز را پوست بگیرید و رنده کنید. آب اضافی‌شان را بفشارید و دور بریزید.', 'برای درست کردن کوفته هویج، ابتدا هویج و پیاز را پوست بگیرید و رنده کنید. آب اضافی‌شان را بفشارید و دور بریزید. در یک کاسه، گوشت چرخ‌کرده، هویج و پیاز رنده‌ شده، تخم‌مرغ، آرد نخودچی و ادویه‌ها (نمک، فلفل سیاه، زردچوبه و دارچین) را بریزید. مواد را حدود ۷ تا ۱۰ دقیقه خوب ورز دهید تا کاملاً یکدست و چسبنده شوند. اگر مواد شل بودند، کمی آرد نخودچی اضافه کنید و اگر سفت بودند، کمی آب یا پیاز بیشتر اضافه کنید. ظرف مواد کوفته را با سلفون بپوشانید و حدود نیم ساعت داخل یخچال قرار دهید تا استراحت کنند و بهتر فرم بگیرند و طعم ادویه‌ها به خورد مواد برود. بعد از استراحت، از مواد به اندازه دلخواه بردارید و با دست به شکل گرد یا بیضی فرم دهید. می‌توانید داخل هر کوفته گردو یا کشمش قرار دهید. در یک ماهیتابه مقداری روغن بریزید و بگذارید داغ شود. کوفته‌ها را یکی‌یکی داخل روغن با حرارت متوسط سرخ کنید تا طلایی و کمی برشته شوند. نیازی به پخت کامل نیست، فقط یک پوسته سبک بگیرند. بعد از سرخ شدن، روی دستمال آشپزخانه قرار دهید تا روغن اضافی گرفته شود. برای تهیه سس کوفته هویج، در یک قابلمه کوچک رب گوجه‌فرنگی را با پیاز داغ کمی تفت دهید تا رنگ باز کند و بوی خامی‌اش از بین برود. سپس آب جوش، آبلیمو، شکر، زعفران دم‌کرده، نمک و فلفل را اضافه کنید و اجازه دهید سس کمی بجوشد تا طعم‌ها ترکیب شوند. می‌توانید مقدار شکر یا آبلیمو را تنظیم کنید و در صورت تمایل یک قاشق رب انار اضافه کنید. وقتی سس آماده شد، کوفته‌های سرخ‌شده را به آرامی داخل قابلمه بچینید و سس را روی آنها بریزید. در قابلمه را بگذارید و با حرارت ملایم حدود نیم ساعت اجازه دهید کوفته‌ها در سس بپزند و جا بیفتند. اگر سس خیلی غلیظ شد، کمی آب جوش اضافه کنید. کوفته هویج را می‌توانید با نان تازه، سبزی خوردن و ماست یا کنار برنج ساده سرو کنید. برای تهیه کوفته هویج در فر: مواد کوفته را آماده کرده و بعد از استراحت در یخچال، کوفته‌ها را شکل دهید و روی سینی فر که با کاغذ روغنی پوشانده شده بچینید. کمی روغن روی سطحشان بمالید و در فر از قبل گرم شده با دمای ۱۸۰ درجه سانتی‌گراد حدود ۳۰ تا ۴۰ دقیقه بپزید تا طلایی شوند. سس را جداگانه آماده کرده و کوفته‌ها را داخل سس قرار دهید تا جا بیفتند یا سس را جداگانه سرو کنید. برای تهیه کوفته هویج گیاهی: حدود ۲ عدد هویج متوسط و ۱ عدد کدو سبز را رنده کنید و با ۳ تا ۴ قاشق غذاخوری آرد نخودچی یا آرد سوخاری، ۲ برش نان خشک‌شده و خردشده، نصف قاشق چای‌خوری دارچین، کمی آویشن، نمک، فلفل، زردچوبه و در صورت تمایل کمی سماق ترکیب کنید. همه مواد را داخل غذاساز بریزید یا با دست خوب ورز دهید تا خمیر منسجمی به دست آید. از مواد به اندازه دلخواه بردارید، گرد کنید و در روغن کم با حرارت متوسط سرخشان کنید تا سطحشان طلایی شود. بقیه مراحل را مانند روش بالا دنبال کنید و کوفته‌های هویج را در سس بپزید.', 30, 60, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/09/YWU1YzcU6b1U.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (229, 'کوفته لپه یزدی', 'برای تهیه این کوفته قلقلی ساده و خوشمزه ابتدا لپه‌ را به مدت ۸ ساعت (از شب قبل یا حداقل ۲ ساعت) در آب خیس و چند بار آب آن را عوض کنید تا نفخ آن گرفته شود. زرشک را در آب بخیسانید. سیب زمینی‌ها را بشویی', 'برای تهیه این کوفته قلقلی ساده و خوشمزه ابتدا لپه‌ را به مدت ۸ ساعت (از شب قبل یا حداقل ۲ ساعت) در آب خیس و چند بار آب آن را عوض کنید تا نفخ آن گرفته شود. زرشک را در آب بخیسانید. سیب زمینی‌ها را بشویید و آبپز کنید. لپه‌ها را بپزید و آبکش کنید. ۵ تخم مرغ را جدا و آبپز کنید. زرشک‌ها را آبکش و خشک کنید. پوست ۲ تا از پیازها را بگیرید و به صورت خلالی نازک خرد کنید. در یک تابه مقداری روغن بریزید و پیاز‌ها را در آن سرخ کنید تا طلایی شود. در لحظه آخر، زرشک‌ها را به پیازداغ اضافه کنید و تفت دهید تا طعم خامی آن گرفته شود ولی از تفت دادن زیاد خودداری کنید چون زرشک خیلی زود می‌سوزد، سریع حرارت را قطع کنید. پوست سیب زمینی‌های آبپز را بگیرید و در یک ظرف نسبتا بزرگ رنده کنید. لپه‌های پخته شده را اضافه کنید. سبزی معطر، گوشت چرخ‌کرده، نمک، پیازداغ، کشمش و زرشک را با بقیه تخم‌ مرغ‌ها (۴ عدد) مخلوط کنید و ورز دهید تا یک ترکیب منسجم و یکدست داشته باشید. می‌توانید همه مواد را در گوشت‌کوب برقی یا مخلو ط کن بریزید تا همه با هم به خوبی مخلوط و له شوند. تخم مرغ های آبپز را پوست بگیرید و دور آنها را از مایه کوفته یزدی قرار دهید و در دستانتان بچرخانید تا گرد و شبیه پرتقال شوند. کوفته ها را در ظرف قرار دهید و به مدت ۱۵ دقیقه روی آنها را بپوشانید و در یخچال بگذارید تا خودشان را بگیرند. در آخرین مرحله از دستور پخت کوفته لپه یزدی پیاز باقی مانده را خلالی یا نگینی خرد کنید. در یک قابلمه، مقداری روغن بریزید و پیاز را در آن سرخ کنید. وقتی طلایی شد، رب گوجه را به آن اضافه کنید تا کمی سرخ شود و رنگ باز کند و خامی آن گرفته شود. ۲ یا ۳ لیوان دسته دار فرانسوی آب جوش یا ولرم به مواد قابلمه اضافه کنید و وقتی جوش آمد، کوفته ها را تک تک در آن بگذارید. حرارت را کم کنید و در قابلمه را باز بگذارید تا از هم وا نروند. اجازه دهید کوفته ها بپزند. یک ساعت زمان برای پختن کوفته لپه یزدی کافی است.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/01/1382545263210-e1611184918574.jpeg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (230, 'کوکو سویا و سیب زمینی', 'برای درست کردن این کوکوی خوشمزه ابتدا باید سویا را خیس کنید. برای این‌که سویا طعم لذیذتری پیدا کنید می‌توانید آن‌ها را در شیر خیس کنید. ۳۰ دقیقه زمان برای خیس خوردن سویاها در شیر یا آب کافی است.', 'برای درست کردن این کوکوی خوشمزه ابتدا باید سویا را خیس کنید. برای این‌که سویا طعم لذیذتری پیدا کنید می‌توانید آن‌ها را در شیر خیس کنید. ۳۰ دقیقه زمان برای خیس خوردن سویاها در شیر یا آب کافی است. بعد از خیس خوردن سویاها حسابی آب یا شیر آن‌ها بگیرید. سویاها را مشت مشت بردارید و حسابی بچلانید. سیب زمینی خام و پیاز را رنده کنید و آب‌شان را بگیرید. سیرها را هم رنده کنید و به سیب زمینی و پیاز اضافه کنید. تمام مواد را با هم مخلوط کنید. برای این‌که مایه کوکو یکدست شود ادویه‌ها، جعفری، آرد سوخاری و تخم مرغ‌ها را به علاوه مخلوط سویا، سیب زمینی، سیر و پیاز در غذاساز یا چرخگوشت بریزید. مایه کوکو باید صاف و یکدست شود. در یک ماهیتابه نچسب روغن را داغ کنید و مایه کوکو را به هر شکلی که دوست دارید سرخ کنید.', 15, 30, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2019/04/3881400.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (231, 'گشنیز کباب آذری و ماکویی', 'در اولین مرحله لپه‌ها را بشویید و برای ۲ ساعت در آب بریزید تا خیس بخورد. سپس لپه‌ها را به همراه مقداری آب در یک قابلمه بریزید و برای حدود ۱ ساعت با شعله متوسط روی گاز قرار دهید تا خوب پخته شود. بعد از', 'در اولین مرحله لپه‌ها را بشویید و برای ۲ ساعت در آب بریزید تا خیس بخورد. سپس لپه‌ها را به همراه مقداری آب در یک قابلمه بریزید و برای حدود ۱ ساعت با شعله متوسط روی گاز قرار دهید تا خوب پخته شود. بعد از اینکه لپه‌ها پخته شدند، لپه‌ها را از آب جدا کنید و آب آن را نگه دارید. در دومین مرحله گشنیزها را تمیز کنید و خوب بشویید. سپس آن‌ها را داخل آبکش قرار دهید تا آب اضافه آن خارج شود. یک پیاز را از وسط نصف کنید و همراه با گوشت چرخ کرده در دستگاه خرد کن بریزید. گوشت و پیاز را خوب میکس کنید. مخلوط گوشت و پیاز را در یک کاسه بریزید و سپس لپه‌های پخت شده را به آن اضافه کنید. پیاز دیگر را نیز در دستگاه خرد کن کاملاً ریز کنید و به بقیه مواد اضافه کنید. در سومین مرحله یک دستکش به دست کنید و مواد را خوب با هم ترکیب کنید و ورز دهید. سپس مقداری نمک، فلفل سیاه، زردچوبه به آن اضافه کنید. سپس تخم مرغ‌ها را نیز به سایر مواد گشنیز کباب آذری خوشمزه اضافه کنید و سپس خوب هم بزنید. در این مرحله یک ماهیتابه را روی گاز قرار دهید و شعله گاز را با حرارت ملایم روشن کنید. مقداری روغن داخل تابه بریزید تا داغ شود. سپس به اندازه یک گردو از مواد گوشتی بردارید و آن را به صورت گرد در آورید و داخل روغن بریزید. می‌توانید برای اینکه کباب شما جلوه زیباتری بگیرد، آن را به جای حالت کوفته‌ای، در سیخ چوبی درست کنید. در آخرین مرحله برای درست کردن سس، گشنیزها را خرد کنید و در مقداری روغن سرخ کنید. سپس یک پیاز را به صورت نگینی خرد کنید و سرخ کنید و یک قاشق آرد به آن اضافه کنید. آب لپه‌ای که از قبل کنار گذاشته بودید را به پیاز اضافه کنید و در صورت نیاز کمی آب هم به مواد بیفزایید. حال لیمو عمانی‌ها را سوراخ کنید و بعد از به جوش آمدن آب داخل قابلمه بیندازید. بعد از چند جوش خوردن گشنیزها را اضافه کنید. ۱۵ دقیقه صبر کنید و سپس کباب‌های را داخل سس بیندازید و اجازه دهید مواد ۵ دقیقه جوش بخورد تا آماده شود. برای درست کردن گشنیز کباب نباید گشنیزها را خیلی خرد کنید. بهتر است آن‌ها را درشت‌تر خرد نمایید. سس این کباب باید غلیظ شود و بعد از اینکه به غلظت مناسبی رسید، کباب‌ها را به آن اضافه کنید. شما می‌توانید این غذا را هم با نان و هم برنج میل کنید. ورز دادن گوشت موجب می‌شود تا کباب شما منسجم‌تر شود و راحت‌تر بتوانید آن را سرخ کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/02/Best-Turkish-Adana-Kebab-Recipe-800x445-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (232, 'گوشت و لوبیا کاشانی و اصفهانی', 'برای تهیه گوشت و لوبیا کاشان، از شب قبل لوبیا سفید را خیس کنید و چند بار آب آن را عوض کنید تا نفخ آن گرفته شود و هضم آن راحت‌تر شود.', 'برای تهیه گوشت و لوبیا کاشان، از شب قبل لوبیا سفید را خیس کنید و چند بار آب آن را عوض کنید تا نفخ آن گرفته شود و هضم آن راحت‌تر شود. پیاز را به صورت درشت خرد کنید. مقداری روغن در ماهیتابه بریزید و روی حرارت متوسط قرار دهید. بعد از داغ شدن روغن، پیازهای خرد شده را به آن اضافه کنید و خوب تفت دهید. وقتی پیازها سبک شدند، گوشت گوسفند را اضافه کنید. سپس مقداری فلفل سیاه، زردچوبه و دارچین به گوشت اضافه کنید تا مزه‌دار شود. وقتی رنگ گوشت عوض شد و کمی سرخ شد، لوبیاها و آب جوش را اضافه کنید. سپس شعله گاز را روی حرارت ملایم تنظیم کنید و درب قابلمه را بگذارید تا آبگوشت به طور کامل بپزد و گوشت و لوبیا کاملاً نرم شوند. در اواخر پخت، نمک غذا را اندازه کنید. بعد از پخت گوشت و لوبیا می‌توانید آن را با باقالی پلو یا برنج چلو سرو کنید و یا گوشت و لوبیا را از آبگوشت جدا کرده و با گوشتکوب دستی بکوبید و با نان سرو کنید. برای تهیه گوشت و لوبیا اصفهانی با سبزی، گوشت و لوبیا را با مقداری آب در قابلمه بریزید. یک عدد پیاز ۴ قاچ شده و ادویه‌ها را به آن اضافه کنید و روی شعله ملایم گاز قرار دهید تا کاملاً پخته شوند. می‌توانید از زودپز نیز استفاده کنید. بعد از پختن لوبیا و گوشت، برنج خیسانده شده را همراه با سبزی‌های خرد شده (تره، جعفری و شنبلیله) در قابلمه بریزید. سپس درب قابلمه را روی آن قرار دهید تا غذا کاملاً بپزد. گوشت و لوبیا معمولاً به صورت کوبیده سرو می‌شود. بعد از این که غذای شما کاملاً پخت و آماده شد، آن را در ظرفی بریزید و با گوشتکوب بکوبید. از گوشتکوب برقی استفاده نکنید، زیرا سبزی‌ها در گوشتکوب گیر می‌کند. اگر مقدار سبزی ۵۰۰ گرم است، مقداری جعفری و تره باید به نسبت بیشتر و مقدار شنبلیله کمتر باشد.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/04/g009.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (233, 'لپه پلو کرمانی', 'لپه‌ها را به مدت ۸ ساعت یا حداقل ۳ ساعت در آب بخیسانید و چند بار آب آنها را عوض کنید. اگر عجله دارید، لپه‌ها را ۲ بار در آب بجوشانید و هر بار آب آن را دور بریزید. برنج‌ها را هم پاک کنید و در مقداری آب', 'لپه‌ها را به مدت ۸ ساعت یا حداقل ۳ ساعت در آب بخیسانید و چند بار آب آنها را عوض کنید. اگر عجله دارید، لپه‌ها را ۲ بار در آب بجوشانید و هر بار آب آن را دور بریزید. برنج‌ها را هم پاک کنید و در مقداری آب ولرم و نمک به مدت ۱ تا ۳ ساعت خیس کنید. زرشک را هم به مدت ۱۵ دقیقه خیس کرده و آبکشی کنید. خلال‌های بادام را هم در مقداری گلاب یا آب به مدت ۱ ساعت خیس کنید. لپه‌های خیسانده یا جوشانده را آبکش کنید و در قابلمه بریزید. به لپه‌ها مقداری آب اضافه کنید و روی حرارت بگذارید تا آب شروع به جوشیدن کند. درب قابلمه را بگذارید و شعله را کم کنید. اجازه دهید لپه‌ها به مدت ۴۵ دقیقه یا ۱ ساعت بپزید تا نرم شوند. سپس لپه‌ها را آبکش کنید و کنار بگذارید. پیازها را پوست بگیرید و به صورت نگینی ریز یا خلالی باریک خرد کنید. در یک تابه مقداری روغن یا کره بریزید و روی حرارت بگذارید تا داغ شود. سپس پیازها را در تابه بریزید و تفت دهید تا طلایی و سبک شوند. بعد از تفت خوردن پیاز، زردچوبه، فلفل سیاه، پودر زیره و چوب دارچین را اضافه کنید و چند ثانیه تفت دهید تا عطر آنها خارج شود. بعد گوشت چرخ کرده را در تابه بریزید و تفت دهید تا رنگ آنها تغییر کند. حرارت را روی متوسط قرار دهید تا آب گوشت کشیده شود. سپس رب گوجه را اضافه کنید و تفت دهید تا رنگ باز کند. اگر از سیب زمینی استفاده می‌کنید، آنها را نگینی خرد کنید و کمی در یک تابه جداگانه با روغن تفت دهید و سپس به گوشت چرخ کرده اضافه کنید. لپه‌های آبکش شده را به مایه گوشت چرخ کرده اضافه کنید و در آخر، به این مواد، نمک هم بزنید. همه مواد را هم بزنید. شعله را روی حالت بسیار ملایم تنظیم کنید و اجازه دهید مایه لپه پلو برای ۱۰ تا ۱۵ دقیقه روی حرارت بمانند و گوشت چرخ کرده بپزد و روغن بیندازد. در این فاصله، در یک قابلمه مناسب، تا نیمه آب بریزید و روی حرارت بگذارید تا شروع به جوشیدن کند. آبی که برنج را در آن خیسانده‌اید، دور بریزید و برنج را در آب در حال جوش بریزید. کمی روغن و نمک اضافه کنید. اجازه دهید برنج به مدت ۷ تا ۱۰ دقیقه بجوشد و دندان‌گیر شود. سپس آن را آبکش کنید. در این مرحله، یک قابلمه مناسب را روی حرارت بگذارید و مقداری روغن در آن بریزید. کف قابلمه را ته دیگ دلخواهتان بگذارید و یک لایه از برنج آبکش شده را در آن بریزید. سپس یک لایه از مایه لپه پلو بریزید و دوباره یک لایه برنج اضافه کنید. لایه‌ها را به همین ترتیب تکرار کنید تا موادتان تمام شود و لایه آخر برنج باشد. در آخر، زعفران دم کرده روی برنج بریزید و درب قابلمه را دم‌کنی بپیچید و آن را ببندید. بعد از ۱ یا ۲ دقیقه که در قابلمه بخار پیچید، شعله پخش‌کن زیر قابلمه بگذارید یا شعله گاز را روی ملایم‌ترین حالت تنظیم کنید. در این فاصله زرشک را در مقداری کره تفت دهید. خلال‌های بادام آبکشی شده را هم در کمی کره تفت دهید و کنار بگذارید. ۴۵ دقیقه صبر کنید تا لپه پلو بپزد. سپس روی لپه پلو کمی کره بریزید و برنج و مواد را با هم مخلوط کنید. لپه پلو را در ظرف سرو بریزید. اگر در لپه پلو سیب زمینی استفاده نکرده‌اید، در کنار لپه پلو می‌توانید سیب زمینی سرخ کرده خلالی سرو کنید و با زرشک یا خلال بادام تفت داده در کره، لپه پلو را تزیین کنید. لپه پلو را می‌توانید با انواع سالاد، انواع ترشی یا ماست غلیظ و خوشمزه، به همراه سبزیجات تازه میل کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/06/%D9%84%D9%BE%D9%87-%D9%BE%D9%84%D9%88-%D8%A8%D8%A7-%DA%AF%D9%88%D8%B4%D8%AA-%DA%86%D8%B1%D8%AE-%DA%A9%D8%B1%D8%AF%D9%87.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (234, 'لوبیا پلو بدون گوشت', 'پیش از شروع مراحل، برنج را به خوبی بشویید و برای ۲ الی ۳ ساعت در آب نمک بخیسانید. سپس، لوبیاها را به دقت بشویید و در سبد بگذارید تا آب اضافی آن خارج شود. سر و ته لوبیا سبز شسته شده را با چاقو جدا کنید', 'پیش از شروع مراحل، برنج را به خوبی بشویید و برای ۲ الی ۳ ساعت در آب نمک بخیسانید. سپس، لوبیاها را به دقت بشویید و در سبد بگذارید تا آب اضافی آن خارج شود. سر و ته لوبیا سبز شسته شده را با چاقو جدا کنید و لوبیاها را در ابعاد متوسط (به اندازه یک بند انگشت یا کوچک‌تر) برش بزنید. اگر به لوبیا سبز تازه دسترسی ندارید می‌توانید از لوبیا سبز منجمد شده استفاده کنید. در این صورت، باید بسته لوبیا سبز را در بشقاب مناسبی بگذارید و برای چند ساعت در طبقه پایین یخچال قرار دهید تا یخ آن باز شود. گوجه فرنگی‌ها را هم بشویید و پوست بگیرید. سپس، گوجه‌های بدون پوست را با کمک گوشت‌کوب برقی، رنده یا دستگاه غذاساز کاملا پوره کنید. سیر را هم پوست بگیرید و به صورت ریز رنده کنید. تابه مناسبی را روی حرارت متوسط بگذارید و مقداری روغن در تابه بریزید. چند لحظه صبر کنید تا روغن داغ شود. سپس، پیازهای نگینی را به تابه اضافه کنید و تفت دهید تا کاملا سبک و طلایی شوند. در ادامه سیر رنده شده را نیز به پیازهای طلایی اضافه کنید. این ترکیب را هم بزنید تا عطر سیر آزاد شود. در ادامه، مقداری نمک و زردچوبه را نیز به سیر و پیاز اضافه کنید و هم بزنید. قطعات لوبیا سبز را نیز به تابه حاوی پیاز و سیر اضافه کنید. این ترکیب را هم بزنید تا لوبیاها نیز کاملا تفت داده شوند و آب اضافی خود را از دست بدهند. سپس، مقداری پودر سیر، فلفل سیاه و پودر دارچین را نیز به این ترکیب اضافه کنید. افزودن پودر سیر و دارچین موجب برطرف شدن طعم خامی لوبیاها و خوشمزه‌تر شدن مایه لوبیا پلو می‌شود. ابتدا رب گوجه فرنگی را در تابه حاوی مایه لوبیا پلو بریزید و برای مدتی تفت دهید تا رنگ باز کند. سپس، پوره گوجه فرنگی را نیز به باقی مواد اضافه کنید. این ترکیب را به اندازه‌ای تفت دهید تا پوره گوجه فرنگی هم آب اضافی خودش را از دست بدهد و نسبتا غلیظ شود. حالا، به اندازه یک لیوان فرانسوی دسته‌دار آب جوش در تابه حاوی مایه لوبیا پلو بریزید. درب تابه را ببندید و حرارت را ملایم کنید تا مواد داخل تابه ظرف نیم ساعت به آرامی بپزند و مایه لوبیا پلو گیاهی کاملا جا افتاده شود. پس از گذشت ۲ الی ۳ ساعت (بسته به نوع برنج)، برنج شما کاملا خیس خورده و برای جوشاندن آماده شده است. بنابراین، قابلمه مناسبی را تا نیمه پر از آب کرده و مقداری نمک و روغن به آن اضافه کنید. به محض جوشیدن آب، برنج خیس خورده را در قابلمه بریزید و حدود ۷ الی ۱۰ دقیقه صبر کنید تا سطح دانه‌های برنج نرم شود. توجه داشته باشید که مغز برنج همچنان باید سفت و دندان‌گیر باقی بماند (سطح روی برنج نرم و مغز آن سفت باشد) تا لوبیا پلو شفته نشود. حالا، برنج را در سبد بریزید و زیر آب گرم بگیرید تا کاملا آبکشی شود. سپس، سبد را برای چند دقیقه به حال خود بگذارید تا آب اضافی برنج کاملا خارج شده و برای دم کردن آماده شود. پس از اطمینان از پخت و جا افتاده شدن مایه لوبیا پلو، قابلمه مناسبی را روی حرارت متوسط بگذارید و مقداری روغن در آن بریزید. کف قابلمه را با ته دیگ برنج دلخواه خود بپوشانید. سپس، یک لایه برنج را روی ته دیگ بریزید. مقدار کمی پودر دارچین را هم به برنج اضافه کنید. در ادامه، مقداری از مایه لوبیا پلو را روی برنج بریزید. این کار را تا زمانی ادامه دهید که برنج و مایه لوبیا پلو تمام شوند، لایه آخر باید برنج باشد. حالا، درب قابلمه را با دم کنی بپوشانید و روی قابلمه قرار دهید. پس از بخار کردن و داغ شدن قابلمه، حرارت را ملایم کرده و حدود ۴۵ الی ۶۰ دقیقه صبر کنید تا لوبیا پلوی گیاهی شما کاملا دم بکشد و ته دیگ آن هم آماده شود. پس از گذشت این مدت، لوبیا پلو بدون گوشت را در ظرف سرو بریزید و با مقداری زعفران دم کرده غلیظ تزیین کنید. شما می‌توانید این غذای گیاهی خوشمزه را با انواع شور، انواع ترشی، انواع سالاد یا ماست نوش جان کنید.', 26, 54, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/06/%D9%84%D9%88%D8%A8%DB%8C%D8%A7-%D9%BE%D9%84%D9%88-%D8%A8%D8%AF%D9%88%D9%86-%DA%AF%D9%88%D8%B4%D8%AA-3-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (235, 'لوبیا پلو مجلسی با گوشت تکه ای', 'برنج را دو ساعت قبل از پخت در آب و نمک بخیسانید.', 'برنج را دو ساعت قبل از پخت در آب و نمک بخیسانید. گوشت گوسفند را به قطعات ریز (تکه‌های دو سانتی با قطر یک سانتی‌متر) تقسیم کنید، بشورید و اجازه بدهید خشک شود. پیاز را ریز و ساطوری خرد کنید. گوشت‌های ریز و قیمه شده را در مقداری روغن به تدریج سرخ کنید (حدود ۳ دقیقه). پیازهای ساطوری خرد شده را تفت بدهید تا سبک و کاراملی و سرخ شوند. پیاز داغ آماده شده را به گوشت تفت داده شده اضافه کنید و خوب با هم مخلوط کنید. نمک، فلفل، زردچوبه، پودر زنجبیل و دارچین را به گوشت اضافه کنید. اگر از گوجه‌ فرنگی تازه استفاده می‌کنید، گوجه فرنگی‌های رنده شده را به مواد داخل ماهیتابه اضافه کنید و اجازه بدهید آب گوجه‌ فرنگی کشیده شود. در یک ظرف جداگانه رب‌ گوجه‌ فرنگی را در مقداری روغن تفت بدهید تا مزه خامی رب گرفته شود. رب تفت داده شده را به مواد گوشت اضافه کنید و حدود یک دقیقه با هم روی شعله ملایم گاز بگذارید. لوبیا سبز خرد شده را بلانچ کنید (در آب جوش بریزید، بعد از ۳ دقیقه با آب کاملا سرد بشویید و آبکش کنید). لوبیا سبزهای آبکش شده را کمی در کره تفت بدهید. لوبیا سبز تفت داده شده را به مایه گوشت اضافه کنید. یک ملاقه آب جوش به مایه گوشت و لوبیا اضافه کنید و اجازه دهید حدود ۱۵ دقیقه روی شعله ملایم گاز بپزد. در یک قابلمه دیگر آب را جوش بیاورید و برنج را در آن بپزید. وقتی که دانه‌های برنج از وسط کمی حالت ترک خورده پیدا کرد، برنج را آبکش کنید. برای دم کردن لوبیا پلو، مایه گوشت و لوبیا را لا به لای برنج بگذارید و آن را دم کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2018/08/%D8%B7%D8%B1%D8%B2-%D8%AA%D9%87%DB%8C%D9%87-%D9%84%D9%88%D8%A8%DB%8C%D8%A7-%D9%BE%D9%84%D9%88-%D8%A8%D8%A7-%DA%AF%D9%88%D8%B4%D8%AA-%DB%B2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (236, 'لول کباب', 'لول کباب که به آن دولی کباب، غازی کباب یا لوله کباب هم می‌گویند یکی از انواع کباب‌های سنتی ایرانی است که اصالت آن به شهر تهران برمی‌گردد. لول کباب یا دول کباب تهرانی معمولا در تابه سرخ شده و همراه با', 'لول کباب که به آن دولی کباب، غازی کباب یا لوله کباب هم می‌گویند یکی از انواع کباب‌های سنتی ایرانی است که اصالت آن به شهر تهران برمی‌گردد. لول کباب یا دول کباب تهرانی معمولا در تابه سرخ شده و همراه با نان یا برنج سرو می‌شود. در طرز تهیه لول کباب تهرانی مثل درست کردن کباب تابه ای بهتر است از گوشت چرخ کرده مخلوط گوسفندی با گوساله و پیاز قرمز استفاده کنید تا کباب شما تردتر و خوشمزه‌تر شود. اگر برای استفاده از گوشت قرمز محدودیتی دارید، می‌توانید لوله کباب را مثل کباب کوبیده مرغ با استفاده از گوشت چرخ کرده مرغ درست کنید. پیش از شروع مراحل طرز تهیه لول کباب تهرانی، پیازها را بشویید و پوست بگیرید. سپس، ۲ عدد از پیازها را رنده کنید و در صافی بریزید تا آب اضافی آن‌ها خارج شود و تفاله پیاز باقی بماند، ما به تفاله پیاز نیاز داریم. یک عدد از پیازها را نیز به صورت نگینی ریز رنده کنید و کنار بگذارید تا بعدا در تهیه سس دول کباب از آن استفاده کنید. ساقه‌های ضخیم و برگ‌های زرد جعفری و گشنیز را هم جدا کنید. سپس، جعفری و گشنیز پاک شده را به خوبی بشویید و در سبد بریزید تا آب اضافی‌شان خارج شود. در ادامه، برگ‌های تر و تازه جعفری و گشنیز شسته شده را به صورت ریز و ساطوری کنید. گوجه فرنگی‌ها را نیز به خوبی بشویید، بلانچ کنید و پوست گوجه‌ها را بگیرید. سپس، گوجه‌های بدون پوست را رنده کرده یا با استفاده از گوشتکوب برقی پوره کنید و کنار بگذارید. این پوره گوجه فرنگی را هم بعدا باید در سس لوله کباب تهرانی بریزید. در این مرحله از تهیه لول کباب، گوشت چرخ کرده و تفاله پیاز رنده شده را در کاسه بزرگی بریزید. سپس، گشنیز و جعفری ساطوری شده را نیز به محتویات کاسه اضافه کنید. این ترکیب را برای یکی دو دقیقه ورز دهید تا مواد اولیه لول کباب به طور یکنواخت با یکدیگر ترکیب و یکدست شوند. حالا، مقداری پودر سیر، سماق، زردچوبه، نمک و فلفل سیاه هم در کاسه بریزید. این مواد را با گوشت چرخ کرده، پیازهای رنده شده و جعفری و گشنیز ساطوری شده برای ۵ الی ۷ دقیقه به خوبی ورز دهید تا گوشت لول کباب تهران قدیم کاملا منسجم، چسبناک و یکپارچه شود؛ درست مثل طرز تهیه کباب کوبیده. پس از ورز دادن همه مواد، در این مرحله از طرز تهیه کباب دولی باید روی کاسه را با سلفون بپوشانید. سپس، ظرف حاوی مایه لول کباب تهرانی را حداقل به مدت ۳۰ دقیقه در یخچال بگذارید تا استراحت کند و خودش را بگیرد. در فاصله استراحت کردن گوشت و باقی ترکیبات، می‌توانید سس لول کباب تهرانی را آماده کنید. برای این منظور، کره را در تابه مناسبی بریزید و روی حرارت ملایم بگذارید تا ذوب شود. سپس، پیازی که قبلا به صورت نگینی ریز خرد کردید را در تابه بریزید و تفت دهید تا کاملا سبک شود. پس از سبک شدن پیاز، مقداری زردچوبه را نیز به تابه اضافه کنید و تفت دهید تا زردچوبه رنگ باز کند و عطر آن آزاد شود. با انجام این کار، طعم خامی زردچوبه برطرف می‌شود و رنگ و لعاب بهتری پیدا می‌کند. در این مرحله از طرز تهیه غازی کباب یا همان دول کباب، پوره گوجه فرنگی که قبلا آماده کردید را نیز به محتویات تابه اضافه کنید. سپس، مقداری نمک، فلفل سیاه، پودر سیر و پول بیبر را نیز در تابه بریزید و همراه با پوره گوجه فرنگی تفت دهید تا آب گوجه کشیده شود. پس از آن که سس لول کباب به روغن افتاد و کاملا جا افتاده شد، حرارت را خاموش کنید و تابه را کنار بگذارید. پس از گذشت نیم ساعت، کاسه حاوی مایه لوله کباب را از یخچال خارج کنید. حالا، مقداری روغن را در تابه نچسب و مناسبی بریزید و روی حرارت متوسط بگذارید تا داغ شود. سپس، مقداری از مایه لول کباب (به اندازه یک گردوی درشت) را جدا کرده و به صورت لوله‌ای فرم دهید. کباب لوله‌ای را در تابه بگذارید تا سرخ شود. این کار را تا زمانی ادامه دهید که کل مایه کباب تمام شود. در ادامه، کباب‌های سرخ شده را روی یک توری فلزی بگذارید تا روغن اضافی آن‌ها خارج شود. پس از سرخ شدن همه کباب‌ها، لول کباب را در ظرف سرو قرار دهید و سس مخصوص را روی آن بریزید. شما می‌توانید این کباب سنتی خوشمزه را با نان، برنج چلو، برنج کته، پیاز و سبزی خوردن نوش جان کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/11/%D9%84%D9%88%D9%84-%DA%A9%D8%A8%D8%A7%D8%A8-2-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (237, 'ماش پلو', 'در یک قابلمه ماش‌های پاک شده را با مقداری آب به مدت ۳۰ دقیقه بپزید. لازم نیست ماش را بیش از حد بپزید چون در پلو له می‌شود.', 'در یک قابلمه ماش‌های پاک شده را با مقداری آب به مدت ۳۰ دقیقه بپزید. لازم نیست ماش را بیش از حد بپزید چون در پلو له می‌شود. گوشت چرخکرده را با پیاز و ادویه و مقداری نمک مزه‌دار کنید و در روغن به صورت قلقلی تفت دهید تا خوب سرخ شود. در یک ماهیتابه پیاز را نگینی به صورت خلالی نازک خرد کنید و با مقداری زردچوبه و نمک تفت دهید. در صورت تمایل کشمش را هم اضافه کنید و تفت دهید و روی کشمش و سایر مواد دارچین بریزید. اگر طعم کشمش را در پلو نمی‌پسندید فقط پیاز را با ادویه‌جاتی که گفتیم و نیز دارچین تفت دهید و کنار بگذارید. در یک قابلمه آب را جوش بیاورید. برنج را مثل همیشه بپزید و آبکش کنید. ماش و شوید خشک را با برنج آبکش مخلوط کنید. کف قابلمه مقداری روغن بریزید و کف آن را با سیب زمینی یا چند تکه نان بپوشانید. مخلوط برنج، ماش و شوید را در قابلمه بریزید اجازه بدهید به مدت ۳۰ دقیقه پلو ماش مجلسی دم بکشد. در پایان مقداری از روی برنج بردارید و با زعفران دم کرده مخلوط کنید. پلو ماش مجلسی را در ظرف مورد نظر سرو کنید و با مخلوط پیاز داغ و کشمش، برنج زعفرانی و نیز گوشت قلقلی تزیین کنید. پلو ماش مجلسی را با مرغ هم می‌توانید درست کنید. تکه‌های مرغ را ابتدا با پیاز، مقداری آب و زردچوبه و فلفل و نمک بپزید و سپس در روغن یا کره مقداری تفت دهید و در کنار ماش پلو مجلسی به صورت تکه‌ای یا ریش ریش سرو کنید. اگر پلو ماش را بدون گوشت می‌پسندید باز هم یک غذای بسیار مقوی و خوشمزه را انتخاب کرده‌اید و برای پخت آن همین روال را طی کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2019/06/6129d4658eaa379927deee123504e229-e1623149082453.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (238, 'ماکارونی با گوشت چرخ کرده', 'پیاز را پوست بگیرید و به صورت نگینی خرد کنید. در یک تابه کمی روغن بریزید و پیازها را در آن سرخ کنید تا سبک شود.', 'پیاز را پوست بگیرید و به صورت نگینی خرد کنید. در یک تابه کمی روغن بریزید و پیازها را در آن سرخ کنید تا سبک شود. زردچوبه، فلفل، پودر آویشن، پودر سیر و نمک را به تابه اضافه کنید و تفت دهید تا طعم خامی آن‌ها گرفته شود. گوشت چرخ کرده را اضافه کنید. کمی که گوشت تفت خورد و رنگ آن تغییر کرد، فلفل دلمه‌ای خرد شده را هم به گوشت اضافه کنید. اجازه دهید روی حرارت ملایم، گوشت با ادویه‌ها مزه‌دار و سرخ شود. بعد از آماده شدن گوشت چرخ کرده، قارچ‌های شسته و خرد شده را اضافه کنید. حرارت را روی حالت متوسط بگذارید تا آب قارچ کشیده شود. در یک تابه جدا، روغن بریزید و رب گوجه فرنگی را اضافه کنید و تفت دهید تا رب کاملا رنگ باز کند و طعم خامی ندهد. رب گوجه فرنگی سرخ شده را به تابه حاوی گوشت اضافه کنید. حدود ۱۰ دقیقه صبر کنید تا مایه ماکارونی کاملا بپزد و جا بیفتد. ۵ قاشق غذاخوری از کنسرو سبزیجات (نخود فرنگی و ذرت) را به مایه ماکارونی اضافه کنید. در یک قابلمه به اندازه کافی آب بریزید و روی حرارت بالا قرار دهید تا آب بجوشد. مقداری نمک و کمی زردچوبه به آب اضافه کنید. ماکارونی‌های رشته‌ای را بدون خرد کردن در قابلمه آب جوش بریزید. حدود ۷ دقیقه (یا بر اساس دستورالعمل روی پاکت ماکارونی) ماکارونی‌ها را بجوشانید تا نرم شوند ولی شل نباشند. ماکارونی‌ها را آبکش کنید و اگر چسبیده بودند، زیر آب سرد، رشته‌ها را جدا کنید. کف قابلمه مقداری روغن مایع بریزید و ته دیگ دلخواهتان بگذارید. روی ته دیگ یک لایه ماکارونی بریزید. روی لایه ماکارونی یک لایه سس ماکارونی بریزید. لایه‌ها را به همین شکل تکرار کنید تا ماکارونی و سس تمام شود. لایه آخر باید ماکارونی باشد. درب قابلمه را ببندید (دور درب قابلمه دم‌کنی بپیچید). حرارت را کم کنید و ۳۵ الی ۴۰ دقیقه صبر کنید تا ماکارونی بپزد و دم بکشد. سپس می‌توانید آن را سرو کنید. برای پخت ماکارونی با گوشت چرخ کرده و پنیر پیتزا، یک ظرف یا قالب فر را با کمی روغن مایع یا روغن زیتون چرب کنید و یک لایه ماکارونی آبکش شده، یک لایه گوشت چرخ کرده و یک لایه پنیر پیتزا بریزید. همه لایه‌ها را به همین صورت تکرار کنید تا موادتان تمام شود. لایه آخر باید پنیر پیتزا باشد. روی ظرف ماکارونی یک فویل نازک آلومینیومی بکشید و آن را به مدت ۲۰ دقیقه در فر با دمای ۱۷۰ درجه سانتی گراد قرار دهید. بعد از آماده شدن، ظرف را از فر خارج کرده و ماکارونی را سرو کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/08/%D9%85%D8%A7%DA%A9%D8%A7%D8%B1%D9%88%D9%86%DB%8C-%D8%A8%D8%A7-%D8%B3%D9%88%DB%8C%D8%A7-1024x683-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (239, 'مانی پلو دامغانی', 'برای درست کردن مانی پلو دامغانی ابتدا باید گوشت را مزه‌دار کنید. برای این کار ماست چکیده را با زیره سبز (حدود ۱ قاشق چای‌خوری)، زعفران دم کرده غلیظ، آبلیمو و نیمی از کره به صورت آب شده مخلوط کنید تا ی', 'برای درست کردن مانی پلو دامغانی ابتدا باید گوشت را مزه‌دار کنید. برای این کار ماست چکیده را با زیره سبز (حدود ۱ قاشق چای‌خوری)، زعفران دم کرده غلیظ، آبلیمو و نیمی از کره به صورت آب شده مخلوط کنید تا یکدست شود. سپس گوشت مغز ران گوسفند یا گوساله را که به اندازه دلخواه خرد کرده‌اید به آن اضافه کنید و ۱ ساعت زمان دهید تا گوشت مزه‌دار شود. به این ترتیب گوشت پلو حسابی نرم و ترد می‌شود. برنج را هم در آب و نمک خیس کنید. لپه را هم جداگانه بپزید اما مراقبت باشید نیم پز شود که حین پخت پلو له نشود. می‌توانید لپه نیم‌پز شده را کمی در روغن تفت دهید تا در پلو له نشود. خلال پسته و بادام را هم در آب یا چند قاشق غذاخوری گلاب بخیسانید. بعد از یک ساعت گوشت را از مایه ماست خارج کنید و مایه را هم نگه دارید چون به آن نیاز داریم. پیاز را نگینی یا خلالی و نازک خرد کنید و در مقداری روغن تفت دهید تا سرخ و طلایی شود. گوشت را به پیاز داغ اضافه کنید و تفت دهید تا رنگ گوشت عوض شود. فلفل سیاه و زردچوبه را هم اضافه کنید و با گوشت تفت دهید. حالا باید گوشت را بپزید. پس مقداری آب جوش اضافه کنید. مقداری آب به حدی باید باشد که صرفا روی گوشت را بپوشاند و در نهایت بعد از پخت گوشت یک سوپ غلیظ از آن را داشته باشیم. در اواخر پخت گوشت نمک آن را اندازه کنید. در فاصله پخت گوشت خلال پسته و خلال بادام را هم در مقداری کره کمی تفت دهید و کنار بگذارید. در اواخر پخت گوشت، برنج مانی پلو را هم آماده کنید. برنج خیس شده در آب و نمک را در آب جوش بپزید. در چند دقیقه آخر پخت رشته پلویی و لپه پخته و تفت داده شده را به برنج اضافه کنید و چند جوش که خورد همه را با هم آبکش کنید. برنج را دان آبکش کنید که موقع پخت پلو له نشود. سس باقی مانده از مزه‌دار کردن گوشت را با یک عدد تخم مرغ و ۳ قاشق غذاخوری گلاب مخلوط کنید. در یک قابلمه مناسب برای پخت پلو مقداری روغن بریزید و وقتی داغ شد سس آماده شده را کف قابلمه بریزید. گوشت پخته شده و مخلوط برنج، رشته پلویی و لپه را لایه لایه در قابلمه بریزید و در هر لایه مقداری خلال بادام و خلال پسته تفت داده شده بریزید. لایه آخر باید برنج باشد. مانی پلو را دم بگذارید و ۳۰ الی ۴۰ دقیقه صبر کنید تا روی شعله ملایم گاز پلو دم بکشید و حسابی ته دیگ ببندد. تا زمانی که مانی پلو دم بکشد مقداری کشمش پلویی و زرشک تفت دهید. وقتی ته چین مانی پلو دم کشید مقداری از برنج سفید روی پلو را جدا کنید و با زعفران دم کرده غلیظ مخلوط کنید و به همراه کشمش پلویی و زرشک موقع سرو با مانی پلو مخلوط کنید. آب گوشت را هم می‌توانید در کنار پلو سرو کنید. طرز تهیه مانی پلو با گوشت قلقلی هم به همین ترتیب است. برای درست کردن مانی پلو با گوشت قلقلی دیگر قرار نیست ته چین داشته باشیم. پلو را به همین ترتیب تهیه کنید و گوشت قلقلی‌ها را به سبک خودتان جداگانه درست کنید و موقع سرو مانی پلو روی آن بگذارید. به این ترتیب ترکیبی شبیه رشته پلو مجلسی اما با خلال پسته و بادام، کشمش پلویی و زرشک خواهید داشت. طرز تهیه مانی پلو با گوشت مرغ هم به همین ترتیب مثل گوشت گوسفند یا گوساله است. ته دیگ پلو را هر چه که دوست دارید بگذارید. مرغ را هم به سبکی هم می‌پسندید، به صورت سرخ شده یا هر سبک دیگری که دوست دارید تهیه کنید و سپس با پلو سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/07/Mani-Polo-A-visual-delight-e1623318526628.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (240, 'ماهیچه بوقلمون', 'پیازهای شسته شده و بدون پوست را خلالی کنید. قابلمه مناسبی را روی حرارت متوسط بگذارید. مقداری روغن را در قابلمه بریزید تا داغ شود. سپس، پیازهای خلالی را به قابلمه اضافه کنید و چند دقیقه منتظر بمانید تا', 'پیازهای شسته شده و بدون پوست را خلالی کنید. قابلمه مناسبی را روی حرارت متوسط بگذارید. مقداری روغن را در قابلمه بریزید تا داغ شود. سپس، پیازهای خلالی را به قابلمه اضافه کنید و چند دقیقه منتظر بمانید تا پیازها سبک شوند. در ادامه، مقداری زردچوبه را نیز به قطعات پیاز اضافه کنید تا زردچوبه نیز تفت ببیند و عطرش آزاد شود. ماهیچه بوقلمون یا همان ران بوقلمون را هم در قابلمه روی قطعات پیاز بگذارید و تفت دهید. مقداری از پیازهای سرخ شده را روی ماهیچه بوقلمون بریزید تا همه قسمت‌های بوقلمون با پیاز پوشانده شده و بخش قابل توجهی از بوی زهم بوقلمون برطرف شود. سیر را رنده کنید و همراه با برگ بو و پودر دارچین در قابلمه بریزید. درب قابلمه را ببندید و حرارت را کاملا ملایم کنید تا ماهیچه بوقلمون آب بیندازد و در طول یک ساعت و نیم با آب پیاز سرخ شود. در این فاصله، هر چند دقیقه یک‌بار، قابلمه را تکان دهید تا بدون باز کردن درب، مواد داخل آن جابه‌جا شود و تفت ببیند. پس از گذشت این مدت، درب قابلمه را باز کنید و زعفران دم کرده و فلفل سیاه را همراه با مقداری آب در حال جوش در قابلمه بریزید. مقدار آب باید به اندازه‌ای باشد که فقط سطح بوقلمون را بپوشاند. حرارت همچنان باید خیلی ملایم باشد. درب قابلمه در حال جوش را ببندید و حدود ۳ ساعت دیگر منتظر بمانید تا ماهیچه بوقلمون کاملا بپزد و با ادویه‌ها طعم‌دار شود. در طول این مدت هم اصلا نباید درب قابلمه را باز کنید. فقط هر نیم ساعت یک‌بار، قابلمه را کمی تکان بدهید تا ماهیچه‌های بوقلمون کمی جابه‌جا شوند. در ۱۵ دقیقه پایانی پخت، درب قابلمه را باز کرده و مقداری نمک را به بوقلمون اضافه کنید. دوباره درب قابلمه را ببندید و اجازه بدهید بوقلمون برای یک ربع دیگر بپزد و طعم نمک را به خودش بگیرد. حرارت را خاموش کنید و مدتی منتظر بمانید تا مواد داخل قابلمه کمی از حرارت بیفتند. سپس، درب قابلمه را باز کرده و ماهیچه بوقلمون را همراه با باقالی پلو، شوید پلو یا چلوی ساده سرو کنید. در صورت تمایل، می‌توانید ماهیچه بوقلمون خوشمزه را به تنهایی در ظرف سرو بریزید. سپس، آن را با دانه‌های انار، برش‌های نارنج و لیموترش و دیگر سبزیجات دلخواه تزیین کرده و به صورت خوراک همراه با نان، پوره سیب زمینی یا سیب زمینی سرخ کرده سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/%D9%85%D8%A7%D9%87%DB%8C%DA%86%D9%87-%D8%A8%D9%88%D9%82%D9%84%D9%85%D9%88%D9%86-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (241, 'مرصع پلو', 'در اولین مرحله از طرز تهیه مرصع پلو با مرغ، برنج را بشویید و در آب و نمک خیس کنید. قسمت نارنجی پوست پرتقال را بگیرید و در یک لیوان آب بریزید. به مدت حداقل ۱ ساعت صبر کنید یا از شب قبل، استراحت دهید. س', 'در اولین مرحله از طرز تهیه مرصع پلو با مرغ، برنج را بشویید و در آب و نمک خیس کنید. قسمت نارنجی پوست پرتقال را بگیرید و در یک لیوان آب بریزید. به مدت حداقل ۱ ساعت صبر کنید یا از شب قبل، استراحت دهید. سپس این پوست را خلالی خرد کرده و در شیرجوش بریزید و روی حرارت بگذارید. آب که جوش آمد، ۲ تا ۳ دقیقه بعد، آب آن را خالی کرده و با آب جدید عوض کنید. دوباره روی حرارت بگذارید. این کار را ۴ بار انجام دهید تا طعم تلخ خلال‌های نارنجی پوست پرتقال گرفته شود. بعد از شیرین شدن پوست‌های پرتقال یا گرفته شدن تلخی آنها، در یک تابه مقداری کره یا روغن بریزید. تابه را روی حرارت بگذارید و بعد از داغ شدن، پوست‌های پرتقال را تفت دهید و ۱ قاشق غذاخوری شکر به آن اضافه کنید. شکر که ذوب شد و خلال‌ها تفت خوردند، شعله گاز را خاموش کنید. در این مرحله، گوشت فیله مرغ را به صورت مکعبی و مدل جوجه‌ای خرد کنید. یک پیاز را خرد یا رنده کنید. سپس تکه‌های مرغ را با آبلیمو، نمک، زردچوبه و فلفل سیاه مزه‌دار کنید. مرغ را به مدت ۱ ساعت در یخچال بگذارید تا مرینیت شود. یک پیاز را خرد کرده و در تابه با روغن سرخ کنید. تکه‌های مرغ‌ را در تابه با روغن مایع یا جامد سرخ کنید. حرارت را کم کنید تا مرغ‌ها با آب پیاز بپزند و نرم شوند. بعد از ۲۰ دقیقه که از پختن مرغ‌ها گذشت، ۱ قاشق غذاخوری آبلیمو، پودر دارچین و زعفران دم‌کرده را اضافه کنید و ۵ دقیقه بعد حرارت را قطع کنید و مرغ‌های پخته را کنار بگذارید. در این مرحله خلال پسته و بادام را در یک کاسه بریزید و گلاب را اضافه کنید. زرشک‌ها و کشمش‌ها را هم در آب به صورت جداگانه خیس کنید. در این مرحله آب نمکی که برنج‌ها را در آن خیسانده‌اید، دور بریزید. در یک قابلمه مقداری آب بریزید و روی حرارت بگذارید تا آب شروع به جوشیدن کند. برنج را به قابلمه بیفزایید. با اولین ترک روی دانه‌های برنج آن را از روی شعله گاز بردارید و آبکش کنید. (حدود ۷ دقیقه کافی است). در یک قابلمه مناسب مقداری روغن بریزید و صبر کنید تا داغ شود. اگر ته دیگ نان یا هر ته دیگ دیگری دوست دارید کف قابلمه بچینید و سپس برنج را اضافه کنید. برنج را به سبک همیشگی خودتان روی شعله ملایم گاز مثل چلو رستورانی دم بگذارید. حدود ۴۰ دقیقه برای دم دادن برنج وقت بگذارید. در حد فاصل ۱۰ دقیقه پایانی پخت برنج، زرشک، کشمش پلویی و خلال پسته و بادام را آبکش کنید. در یک تابه مقداری کره یا روغن بریزید و روی حرارت بگذارید تا داغ شود. سپس این مواد آبکش شده را به تابه اضافه کنید. یک گوشه تابه را خلال بادام و یک گوشه را خلال پسته بریزید و ۲ تا ۳ دقیقه تفت دهید. یک گوشه زرشک را بریزید و ۱ قاشق غذاخوری شکر اضافه کنید و ۳۰ ثانیه تفت دهید. در یک گوشه هم کشمش‌ها را بریزید. سپس ۳۰ ثانیه تفت دهید و تابه را از روی حرارت بردارید. بعد از پخت برنج، کمی روی برنج روغن یا کره بریزید. سپس با کفگیر به آرامی برنج را هم بزنید و در ظرف مورد نظر سرو کنید. مقداری از برنج را با زعفران مخلوط کنید و روی برنج سفید بریزید. ظرف را با خلال پسته و بادام، زرشک، کشمش و خلال پوست پرتقال به روش دلخواه تزیین کنید. مرغ‌های پخته شده را هم روی برنج‌ها یا کنار ظرف سرو بچینید. نوش جان! برای تهیه مرصع پلو با گوشت، کافی است تکه‌های گوشت یا ماهیچه را با پیاز و ادویه‌ها بپزید. سپس برنج را دم کنید. خلال‌ها، کشمش و زرشک را هم مانند روش بالا آماده کنید. بعد از پخت گوشت‌ها، کمی آن‌ها را با کره سرخ کنید و روی مرصع پلو بگذارید و سرو کنید. در صورت دلخواه سس گوشت یا ماهیچه را جداگانه، کنار مرصع پلو سرو کنید. استفاده از گوشت چرخ کرده به صورت قلقلی هم برای درست کردن مرصع پلو مرسوم است. در این صورت ۱ عدد پیاز را رنده و آبگیری کنید. گوشت چرخ کرده را با پیاز و مقداری ادویه، شامل فلفل سیاه، فلفل قرمز، زردچوبه، نمک و ادویه‌های دلخواه دیگر ترکیب کنید و مواد را با هم به مدت ۵ دقیقه ورز دهید. با مخلوط به دست آمده، کوفته‌های قلقلی درست کنید و آنها را سرخ کنید. بعد از پخت و دم دادن برنج، روی مرصع پلو را با گوشت‌های قلقلی و خلال‌ها، تزیین و سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/02/%D8%AA%D9%87%DB%8C%D9%87-%D9%85%D8%B1%D8%B5%D8%B9-%D9%BE%D9%84%D9%88.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (242, 'مرغ تند با ماست به روش هندی', 'فیله‌ها یا سینه مرغ را بشویید و اجازه دهید آب خود را از دست بدهد. سپس فیله‌ها را به صورت مکعب‌های کوچک خرد کنید. پیاز را به صورت نگینی ریز خرد کنید و سیر را له کنید.', 'فیله‌ها یا سینه مرغ را بشویید و اجازه دهید آب خود را از دست بدهد. سپس فیله‌ها را به صورت مکعب‌های کوچک خرد کنید. پیاز را به صورت نگینی ریز خرد کنید و سیر را له کنید. در یک تابه مقداری روغن بریزید و روی حرارت بگذارید. پیاز را اضافه کنید و تفت دهید تا سبک و شیشه‌ای شود. سپس سیر پوره شده را اضافه کنید و تفت دهید تا عطر آن خارج شود. بعد پودر نارگیل را اضافه کنید و چند ثانیه تفت دهید. سپس تکه‌های مرغ را اضافه کنید و تفت دهید تا رنگ آن‌ها سفید شود. در این مرحله، ادویه‌ها شامل پودر دارچین، پودر زنجبیل، پودر فلفل قرمز تند، پودر هل، پودر زردچوبه و میخک را اضافه کنید و چند ثانیه تفت دهید تا بوی خام آنها گرفته شود. سپس ماست و رب گوجه فرنگی را اضافه کنید و همه مواد را هم بزنید تا کاملا یکدست شوند. کمی آب به مرغ اضافه کنید و اجازه دهید مواد داخل قابلمه یا تابه، از دیواره‌ها حباب بزنند. حرارت را کم کنید و درب تابه را تا نیمه روی آن بگذارید. فیله‌ها به مدت ۴۰ دقیقه باید بپزند تا نرم شوند و سس آن غلیظ شود. سپس مرغ که مغز‌پخت شد، نمک آن را اضافه و اندازه کنید. غذا که جا افتاد، شعله گاز را خاموش کنید و مرغ تند ماستی را در ظرف سرو بریزید. این خوراک را می‌توانید با برنج چلو یا زعفرانی سرو کنید. برای تزیین، روی خورش را کمی جعفری یا سبزی معطر خرد شده بریزید.', 16, 34, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/06/rice-diet-recipes-01.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (243, 'مرغ تندوری', 'مرغ را بشویید و روی آن را با چاقو شیارهایی ایجاد کنید تا ادویه‌ها به خورد آن برود.', 'مرغ را بشویید و روی آن را با چاقو شیارهایی ایجاد کنید تا ادویه‌ها به خورد آن برود. برای تهیه سس تندوری، در یک کاسه بزرگ آبلیمو، سیر و زنجبیل خرد و له شده، گرام ماسالا، پودر زردچوبه، پودر زیره، فلفل، پودر چیلی کشمیری یا پاپریکا، پودر گشنیز، روغن و نمک را مخلوط کنید. ماست را به آن اضافه و به خوبی همه مواد را با هم مخلوط کنید. مرغ را اضافه و در سس مرغ تندوری مرینیت یا مزه‌دار کنید تا مواد کل سطح مرغ را بپوشاند. روی مرغ را به مدت ۱۲ تا ۲۴ ساعت با سلفون بپوشانید و در یخچال نگه دارید. فر را با دمای ۱۸۰ درجه سانتیگراد گرم کنید. دور یک سینی را فویل بپیچید. مرغ را از موادی که در آن مرینیت شده است، خارج کنید (مواد را دور نریزید) و در سینی بگذارید. اجازه دهید به مدت ۲۰ دقیقه بپزد. بعد از ۲۰ دقیقه مرغ را از فر بیرون بیاورید و با برس از موادی که مرغ را با آن مرینیت کرده بودید، روی مرغ بمالید. دوباره مرغ را به مدت ۱۰ دقیقه در فر قرار دهید. مرغ را بعد از ۱۰ دقیقه از فر خارج کنید و روی آن را با روغن اسپری کنید یا کره بمالید. گرما را به ۲۲۰ درجه سانتیگراد افزایش دهید. ۱۰ دقیقه دیگر مرغ را در فر نگه دارید تا جایی که لکه‌های سرخ‌شدگی نمایان شود. مرغ تندوری آماده است. آن را می‌توانید با ماست و برنج زعفرانی یا برنج باسماتی ساده سرو کنید. برای تهیه مرغ تندوری در تابه یا قابلمه، از بال، سینه و ران استفاده و آنها را جدا کنید. مرغ را به روش قبل با ادویه مرغ تندوری در سس مرغ تندوری مرینیت کنید و در یخچال بگذارید. بعد از یک روز استراحت (حداقل ۸ ساعت) آن را از مواد خارج کنید. در یک تابه یا قابلمه مقداری روغن بریزید و مرغ‌ها را در آن قرار دهید. حرارت را کم کنید تا کمی بپزند. بعد از اینکه آب خود مرغ بخار شد، دوباره روغن اضافه و سرخ کنید. می‌توانید به اندازه ۱ یا ۲ لیوان دسته دار فرانسوی به آن آب جوش اضافه کنید و به مدت ۳۰ دقیقه اجازه دهید روی حرارت ملایم بپزد. در این صورت خورش مرغ تندوری خواهید داشت. برای تهیه مرغ تندوری کبابی، مرغ را مانند روش اول مرینیت کنید. می‌توانید از تکه‌های ران یا بال استفاده کنید و آنها را روی زغال کباب کنید. ران‌ها را می‌توانید روی توری قرار دهید و تکه‌های گوشت مرغ را سر سیخ بزنید. مرغ تندوری کبابی را با سبزیجات و ماست سرو کنید. برای تهیه ساندویچ مرغ تندوری، سینه مرغ را به روش اول مزه‌دار کنید و استراحت دهید. یک تابه را روی حرارت قرار دهید و در آن مقداری روغن بریزید. سینه را در آن سرخ کنید. حرارت را کم کنید و درِ ظرف را بگذارید تا رنگ مرغ تغییر کند. ۲ عدد قارچ را به چهار قسمت تقسیم کنید و بعد از ۱۰ دقیقه روی مرغ بریزید. ۲ تا ۳ دقیقه صبر کنید تا قارچ‌ها نرم شوند. ۲ قاشق غذا خوری سس سویا اضافه کنید و اجازه دهید غلیظ شود. ۲ ورق پنیر چندار را به مرغ اضافه کنید و ۵ دقیقه در ظرف را بگذارید تا کاملا ذوب شود. ۱ عدد گوجه فرنگی را به صورت حلقه‌ای برش بزنید. لای نان ساندویچی را باز کنید و مواد مرغ را در آن قرار دهید. روی مواد را گوجه‌های حلقه شده قرار دهید. می‌توانید از کاهو، خیارشور و پیازچه هم در ساندویچ استفاده کنید.', 13, 27, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/01/3-DSC_0139-e1610896418227.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (244, 'مرغ شاندیزی', 'تکه‌های مرغ را بشویید و در آبکش بگذارید تا خونابه آنها خارج شود. پیازها را پوست بگیرید و به صورت خلالی باریک خرد کنید. فلفل دلمه‌ای و هویج‌ها را هم بشویید و کنار بگذارید.', 'تکه‌های مرغ را بشویید و در آبکش بگذارید تا خونابه آنها خارج شود. پیازها را پوست بگیرید و به صورت خلالی باریک خرد کنید. فلفل دلمه‌ای و هویج‌ها را هم بشویید و کنار بگذارید. در یک قابلمه مقداری روغن بریزید و روی حرارت بگذارید تا روغن داغ شود. سپس پیازهای خلالی خرد شده را در روغن بریزید و تفت دهید تا پیازها کاملا نرم، سبک و شیشه‌ای شوند. بعد تکه‌های مرغ را روی پیازها بگذارید و نصف پیازهای سرخ شده را روی مرغ‌ها بریزید. شعله را کم کنید تا مرغ‌ها با پیاز آب بیندازند و عطر و طعم پیازها به خورد مرغ‌ها برود. بعد از گذشت ۱۵ دقیقه ادویه‌های غذا شامل فلفل سیاه، زردچوبه و زیره را اضافه کنید. پوست هویج را بگیرید و هویج را به صورت حلقه‌ای خرد کنید. حلقه‌های هویج را روی مرغ بریزید. درب قابلمه را ببندید تا مرغ‌ها طعم ‌بگیرند. سپس فلفل دلمه‌ای را از وسط نصف کنید و تخم‌های آن را بیرون بیاورید. فلفل دلمه‌ای را به صورت خلالی خرد کنید و در قابلمه بریزید. بعد از اینکه سبزیجات را اضافه کردید، سیر را پوست بگیرید و درشت خرد کنید یا به صورت درسته داخل قابلمه بیندازید. حدود ۵ دقیقه صبر کنید تا طعم سبزیجات و سیر با مرغ ترکیب شود. بعد به اندازه ۲ لیوان آب جوش در قابلمه بریزید. مقداری هم روغن مایع اضافه کنید. چوب دارچین را هم در قابلمه بیندازید و اجازه دهید مرغ‌ها روی حرارت ملایم، به مدت ۲ ساعت به خوبی بپزد و لعاب بیندازد. بعد از ۱ و نیم ساعت، گوشت‌ها کاملا طعم‌دار و نرم می‌شوند. زعفران را با کمی قند بسایید و یک تکه یخ روی آن بیندازید. صبر کنید تا یخ‌ها ذوب شوند و زعفران‌ها رنگ و طعم‌شان آزاد شود. لیمو‌ها را هم بشویید و نصف کنید. آب لیموها را بگیرید و هسته‌ آنها را جدا کنید. اگر لیمو ندارید از آبلیمو استفاده کنید. سپس نمک، زعفران و آبلیمو را به قابلمه مرغ شاندیزی اضافه کنید. دوباره اجازه دهید به مدت نیم ساعت مرغ‌ها بپزند و چاشنی‌‌ها به خورد این مرغ مشهدی لذیذ بروند. وقتی سس غذا غلیظ شد و مرغ‌ها جا افتادند، می‌توانید شعله را خاموش کنید. مرغ‌ شاندیزی را از قابلمه بیرون بیاورید و در ظرف سرو بگذارید. سپس سس داخل قابلمه را صاف کنید و در یک کاسه جدا بریزید. مرغ شاندیزی را همراه با برنج چلو ساده یا زعفرانی سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/08/khoresh-morgh-fer.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (245, 'مرغ کراهی', 'در ابتدا ماهیتابه را روی گاز قرار دهید و مقداری روغن در آن بریزید تا گرم شود. سپس پیاز خرد شده، زنجبیل و سیر رنده شده را به آن اضافه کنید و برای ۲ تا ۳ دقیقه خوب هم بزنید تا مواد کمی نرم شوند. برای چن', 'در ابتدا ماهیتابه را روی گاز قرار دهید و مقداری روغن در آن بریزید تا گرم شود. سپس پیاز خرد شده، زنجبیل و سیر رنده شده را به آن اضافه کنید و برای ۲ تا ۳ دقیقه خوب هم بزنید تا مواد کمی نرم شوند. برای چند دقیقه درب ماهیتابه را روی آن قرار دهید تا خوب پخته شوند. توجه داشته باشید که پیاز نباید طلایی شود و تغییر رنگ بدهد تنها لازم است که نرم شوند. به محض اینکه مشاهده کردید پیاز در حال تغییر رنگ است، چند قاشق آب به ماهیتابه اضافه کنید. در این مرحله بعد از اینکه پیاز، سیر و زنجبیل نرم شدند، مرغ‌ها را به مربع‌های نسبتاً بزرگ برش دهید و به همراه گوجه فرنگی خرد شده، زیره آسیاب شده، گشنیز خرد شده و همچنین گرام ماسالا، پودر چیلی، زردچوبه و نمک و فلفل به داخل ماهیتابه اضافه کنید. فلفل دلمه سبز را نیز به صورت مربعی خرد کنید و به مواد اضافه کنید. اضافه کردن فلفل دلمه به این غذا اختیاری است اما موجب عطر و طعم بهتر آن می‌شود. بعد از اضافه کردن مواد، همه را خوب هم بزنید تا ترکیب شوند. در این مرحله درب ماهیتابه را روی آن قرار دهید و اجازه دهید مواد برای ۵ دقیقه خوب پخته شوند. بعد از اینکه مرغ به خوبی پخته شد، غذا را داخل یک ظرف مناسب سرو کنید و روی آن را با گشنیز تازه تزئین کنید و همراه نان یا برنج نوش جان کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/chicken-karahi.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (246, 'مشته بادمجان شیرازی', 'در ابتدا، بادمجان‌ها را پوست بگیرید و کلاهک آن‌ها را جدا کنید. بادمجان‌ها را به صورت ورقه‌ای یا حلقه‌ای خرد کنید و روی آن‌ها مقداری نمک بپاشید. سپس بادمجان‌ها را به مدت ۳۰ دقیقه تا ۱ ساعت استراحت دهید', 'در ابتدا، بادمجان‌ها را پوست بگیرید و کلاهک آن‌ها را جدا کنید. بادمجان‌ها را به صورت ورقه‌ای یا حلقه‌ای خرد کنید و روی آن‌ها مقداری نمک بپاشید. سپس بادمجان‌ها را به مدت ۳۰ دقیقه تا ۱ ساعت استراحت دهید تا آب قهوه‌ای‌شان خارج شود. بعد بادمجان‌ها را بشویید و در آبکش بگذارید تا آب اضافی خود را از دست بدهند. پوست یکی از پیازها را بگیرید و آن را رنده کنید. با دست پیازهای رنده شده را بفشارید تا آب آن‌ها خارج شود. سپس پیاز رنده شده را در یک کاسه بریزید و گوشت چرخ کرده را به آن اضافه کنید. بعد نوبت به اضافه کردن آرد نخودچی یا آرد سوخاری، فلفل سیاه، پودر فلفل پاپریکا، نصف قاشق چای‌خوری زردچوبه و نمک می‌رسد. همه مواد کوفته را با دست ورز دهید تا کاملا با هم منسجم شوند. سپس روی ظرف را بپوشانید و به مدت حداقل ۳۰ دقیقه در یخچال بگذارید تا ادویه‌ها به خورد کوفته بروند و مایه کوفته قلقلی، سفت و یکدست شود. بعد از آبکش کردن بادمجان‌ها و گرفته شدن آب اضافی آن‌ها، بهتر است با دستمال حوله‌ای، دو طرف آن‌ها را خشک کنید تا روغن کمتری مصرف شود. در یک تابه مقداری روغن بریزید و روی حرارت بگذارید تا کاملا داغ شود. سپس بادمجان‌ها را در تابه بچینید و یک طرفشان که سرخ شد، طرف دیگر را سرخ کنید. همه بادمجان را به همین شکل سرخ کنید و کنار بگذارید. بعد از اینکه مایه گوشت چرخ کرده استراحت کرد و خوب مزه‌دار شد، آن را از یخچال بیرون بیاورید و به اندازه یک کشک (کوچک‌تر از یک گردو) از مایه جدا کنید و بین دست‌هایتان بچرخانید تا گلوله شود. بهتر است کف دست‌هایتان را خیس کنید تا مایه کوفته، به دستتان نچسبد. همه مایه گوشت چرخ‌کرده را به همین شکل، گلوله کنید و در یک سینی کنار بگذارید. بعد از آماده شدن مشته‌ها، در یک تابه مقداری روغن بریزید و بعد از داغ شدن، مشته‌ها را به آرامی در تابه بریزید و اجازه دهید همه قسمت‌های کوفته‌ها، سرخ شود. کف یک بشقاب را دستمال حوله‌ای بگذارید. سپس کوفته‌ها را از تابه بیرون بیاورید و روی دستمال بچینید تا روغن اضافی آن‌ها خارج شود. در این مرحله، گوجه‌فرنگی‌ها را بشویید و رنده کنید. رب گوجه فرنگی را با کمی آب رقیق کنید. این مواد را کنار بگذارید. یک پیاز باقی‌مانده را به صورت خلالی، نگینی یا حلقه‌ای خرد کنید. یک تابه را روی حرارت بگذارید و کمی روغن اضافه کنید. پیازها را کف تابه بریزید یا بچینید تا کمی سرخ شوند. سپس یک طرف تابه را بادمجان‌های سرخ شده و یک طرف را کوفته‌های سرخ شده بچینید. در آخر، گوجه فرنگی‌های رنده شده و رب گوجه فرنگی رقیق شده را به عنوان سس، روی کوفته‌ها و بادمجان‌های سرخ شده بریزید. کمی نمک، فلفل سیاه و آبلیمو به تابه اضافه کنید. درب تابه را ببندید و بعد از اینکه سس، شروع به قل زدن کرد، حرارت را کم کنید. اجازه دهید بادمجان‌ها به خوبی نرم شوند و سس کاملا به خورد غذا برود و غلیظ شود. بعد از اینکه مشته بادمجان پخت و به طعم و غلظت دلخواهتان رسید، آن را در ظرف سرو بریزید و همراه با نان، برنج کته یا برنج چلو، سالاد شیرازی یا ترشی دلخواه و سبزی خوردن، میل کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/01/images-58.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (247, 'میرزا قاسمی', 'بادمجان‌ها را به خوبی بشویید. سپس آن‌ها را به نوبت روی آتش یا شعله گاز بگیرید تا کبابی شوند. بادمجان‌ها را آن قدر کبابی کنید که پوست بادمجان کاملاً بسوزد و ترد شود و داخل بادمجان هم مغز پخت شود. برای', 'بادمجان‌ها را به خوبی بشویید. سپس آن‌ها را به نوبت روی آتش یا شعله گاز بگیرید تا کبابی شوند. بادمجان‌ها را آن قدر کبابی کنید که پوست بادمجان کاملاً بسوزد و ترد شود و داخل بادمجان هم مغز پخت شود. برای طعم بهتر، در صورت امکان آن‌ها را روی زغال کبابی کنید تا عطر دودی بیشتری بگیرد. بعد از اینکه همه بادمجان‌ها را کبابی کردید، اجازه دهید کمی سرد شوند. سپس پوست بادمجان‌ها را جدا کنید. برای راحت‌تر جدا شدن پوست، بعد از پخت آن را زیر شیر آب سرد بگیرید. بادمجان‌ها را کمی زیر آب بگیرید تا ذرات پوست سوخته به طور کامل تمیز شوند. سپس بادمجان‌ها را روی تخته آشپزی بگذارید و کاملاً ساطوری کنید. می‌توانید بادمجان را با استفاده از گوشت‌کوب دستی یا برقی له کنید. پوست گوجه‌ها را جدا کنید و گوشت گوجه‌ها را خرد کنید. در صورت تمایل می‌توانید گوجه‌ها را رنده یا پوره کنید. برای پوست گرفتن گوجه‌ها، ابتدا آن‌ها را بشویید، سپس در یک ظرف قرار دهید و مقداری آب جوش روی گوجه‌ها بریزید. حالا می‌توانید پوست گوجه‌ها را به راحتی جدا کنید. سپس گوجه فرنگی‌ها را به قطعات ریز خرد کنید. سیر داغ را آماده کنید. سیرها را با آب فراوان بشویید، پوست آن‌ها را جدا کنید و با استفاده از رنده ریز یا گوشتکوب تمام سیرها را له و ریز کنید. یک ماهیتابه با مقداری روغن را روی گاز قرار دهید. اجازه دهید روغن کمی داغ شود. سپس سیرهای خرد شده را به آن اضافه کنید و اجازه دهید کمی سرخ شوند. سپس کمی زردچوبه به سیر اضافه کنید تا همراه با روغن و سیر خرد شده سرخ شود. بعد از اینکه سیر سرخ شد، بادمجان‌های کبابی خرد شده را همراه با گوجه فرنگی خرد شده به ماهیتابه اضافه کنید. همه مواد را با هم ترکیب کنید تا خوب سرخ شوند و آب آن‌ها کشیده شود. می‌توانید ابتدا گوجه فرنگی را اضافه کنید و بعد از اینکه آب آن کمی بخار شد، بادمجان‌ها را به آن اضافه کنید. مقداری نمک و فلفل سیاه به مواد بزنید و خوب ترکیب کنید. سپس مواد میرزا قاسمی که حالا خوب سرخ شده و به روغن افتاده‌اند را در یک سمت ماهیتابه قرار دهید. در طرف دیگر ماهیتابه تخم مرغ‌ها را بریزید. با یک قاشق تخم مرغ‌ها را خوب هم بزنید تا کاملاً یکدست شوند. در حین سرخ شدن تخم مرغ‌ها آن‌ها را هم بزنید تا به صورت هم‌زده سرخ شوند. می‌توانید این کار را در یک ماهیتابه جداگانه نیز انجام دهید. بعد از اینکه تخم مرغ به طور کامل سرخ شد، آن را با سایر مواد ترکیب کنید. بعد از اینکه غذا به طور کامل پخته شد، آن را در یک ظرف مناسب سرو کنید. می‌توانید برای تزیین میرزا قاسمی به جای تخم مرغ هم زده یک عدد نیمروی کامل درست کنید و روی آن قرار دهید. ریختن مقداری پیازچه یا جعفری خرد شده روی میرزا قاسمی نیز روش مناسبی برای تزیین آن است.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/04/mirzaghasemi.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (248, 'نخود آب', 'نخودآب یک غذای ایرانی بسیار مقوی و خوشمزه است که به دو روش با گوشت و بدون گوشت تهیه می‌شود.', 'نخودآب یک غذای ایرانی بسیار مقوی و خوشمزه است که به دو روش با گوشت و بدون گوشت تهیه می‌شود. طرز تهیه نخودآب بدون گوشت: در مرحله اول، نخودها را در آب زیاد بپزید. بهتر است قبل از پختن نخودها، آن‌ها را یک روز قبل در آب خیس کنید تا نفخ‌شان گرفته شود. در مرحله دوم، یک عدد پیاز کوچک را خرد کرده و با یک قاشق غذاخوری روغن بادام در ماهیتابه تفت دهید. نمک، فلفل و زیره سیاه را به پیازها اضافه کنید. پس از آن، مواد داخل ماهیتابه را به نخودها که در حال پختن هستند اضافه کنید. اجازه دهید مواد آنقدر در آب بجوشند تا نخودها کاملاً پخته شده و شکلی حلیم مانند به خود بگیرند. نخودآب شما آماده سرو است. طرز تهیه نخودآب با گوشت: در مرحله اول، ابتدا نخودها به همراه گوشت گوسفند را با مقدار زیادی آب داخل قابلمه ریخته و اجازه دهید تا کاملا بپزند. در مرحله دوم، زمانی که گوشت پخته شد، آن را از نخودها جدا کنید. گوشت را در ظرفی جداگانه ریخته و اجازه دهید تا نخودها آن‌قدر بپزند که به شکل عصاره غلیظی درآیند. در این مرحله، گوشت را دوباره در داخل عصاره غلیظ شده نخود بریزید و برنج و زعفران و دارچین و نمک را هم اضافه کنید. کمی فرصت بدهید تا مخلوط گوشت و عصاره غلیظ نخود و برنج کاملاً در قابلمه پخته شوند. پس از آن نخودآب آماده سرو است.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/%D8%B7%D8%B1%D8%B2-%D8%AA%D9%87%DB%8C%D9%87-%D9%86%D8%AE%D9%88%D8%AF%D8%A2%D8%A8-%DB%B1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (249, 'نخود پلو با مرغ ترکیه ای', 'پیش از شروع مراحل طرز تهیه نخود پلو با مرغ، نخود آبگوشتی را به خوبی بشویید و از شب قبل همراه با مقداری آب در ظرف مناسبی بریزید تا به مدت ۱۲ الی ۲۴ ساعت حسابی خیس بخورد. هر چقدر مدت زمان خیس خوردن نخود', 'پیش از شروع مراحل طرز تهیه نخود پلو با مرغ، نخود آبگوشتی را به خوبی بشویید و از شب قبل همراه با مقداری آب در ظرف مناسبی بریزید تا به مدت ۱۲ الی ۲۴ ساعت حسابی خیس بخورد. هر چقدر مدت زمان خیس خوردن نخود بیشتر باشد، پخت نخود آسان‌تر خواهد بود. در این فاصله، چند بار آب نخود را عوض کنید تا نفخ آن تا حد ممکن گرفته شود. مقدار آب باید به اندازه‌ای باشد که حدود دو بند انگشت بالاتر از سطح نخود قرار گیرد. اگر نخود آبگوشتی ندارید یا زمان کافی برای خیساندن و پختن نخود ندارید از کنسرو نخود آبگوشتی استفاده کنید. در این صورت کنسرو را به مدت ۲۰ دقیقه بجوشانید و بعد استفاده کنید. برنج را نیز به خوبی بشویید و همراه با مقداری آب و یک قاشق غذاخوری نمک در ظرف مناسبی بریزید تا برای ۱ الی ۲ ساعت (بسته به نوع برنج) خیس بخورد. برای خیساندن برنج، مقدار آب باید به اندازه‌ای باشد که دو بند انگشت بالاتر از سطح برنج قرار گیرد. پیازهای شسته شده و بدون پوست را به صورت نگینی ریز یا خلالی نازک برش بزنید. کشمش را نیز به خوبی بشویید و همراه با مقداری آب در ظرف مناسبی بریزید تا خیس بخورد. گوشت سینه مرغ را هم بشویید و در سبد بگذارید تا آب اضافی آن خارج شود. سپس، گوشت سینه مرغ را به صورت نگینی برش بزنید و کنار بگذارید. نخود خیس خورده را در سبد بریزید و زیر شیر آب کاملا آبکشی کنید. سپس، نخود خیس خورده را همراه با مقداری آب در قابلمه مناسبی بریزید و روی حرارت متوسط بگذارید تا بجوشد. پس از جوشیدن آب، درب قابلمه را ببندید و حرارت را ملایم کنید تا نخود به مدت ۲ ساعت روی شعله کم بپزد و کاملا نرم شود. مقدار آب این قابلمه باید به اندازه‌ای باشد که ۳ بند انگشت بالاتر از سطح نخود قرار گیرد. پس از گذشت این مدت و اطمینان از پخته و نرم شدن نخود، نخود آبگوشتی پخته شده را در سبد بریزید تا آب اضافی آن خارج شود. حالا، قابلمه‌ای که می‌خواهید نخود پلو با مرغ را در آن درست کنید روی حرارت متوسط بگذارید و مقداری روغن به این قابلمه اضافه کنید. پس از داغ شدن روغن، پیازهایی که قبلا خرد کردید را در این قابلمه بریزید و تفت دهید تا کاملا سبک و طلایی شوند. پس از سبک شدن پیاز، مقداری زردچوبه نیز به قابلمه اضافه کنید و همراه با پیاز تفت دهید تا عطر و رنگ زردچوبه آزاد شود. گوشت سینه مرغ را که قبلا به صورت نگینی برش زدید در قابلمه حاوی پیاز داغ بریزید. کمی نمک هم روی مرغ بپاشید. این ترکیب را به خوبی هم بزنید تا مواد کاملا با یکدیگر ترکیب شوند و قطعات سینه مرغ حسابی تفت ببینند و طعم پیاز و زردچوبه را بگیرند. در صورت تمایل، می‌توانید دوباره مقداری زردچوبه به این قابلمه اضافه کنید. پس از تفت دیدن گوشت سینه مرغ، نوبت به اضافه کردن کشمش پلویی می‌رسد. برای این منظور، کشمش خیس خورده را در سبد بریزید تا آب اضافی آن کاملا خارج شود. در ادامه، کشمش پلویی خیس خورده را نیز به ترکیب پیاز و قطعات سینه مرغ اضافه کنید. مواد داخل قابلمه را هم بزنید تا کشمش برای یک دقیقه همراه باقی مواد تفت داده شود. در این مرحله، باید فلفل سیاه را نیز به محتویات این قابلمه اضافه کنید و با کشمش تفت دهید. حالا، نخود پخته شده و نرم را نیز به محتویات این قابلمه اضافه کنید. مواد داخل قابلمه را هم بزنید و اجازه دهید نخود هم برای چند لحظه همراه با باقی مواد تفت داده شود. پس از دنبال کردن همه مراحل بالا، وقت آن است که آب برنج خیس خورده را خالی کنید و خود برنج را در قابلمه حاوی مواد بریزید. سپس، به ازای هر لیوان برنج، یک لیوان آب به این قابلمه اضافه کنید. محتویات قابلمه را به خوبی هم بزنید. پس از جوشیدن آب، درب قابلمه را ببندید و حرارت را ملایم کنید تا نخود پلو با مرغ برای ۴۵ دقیقه الی یک ساعت روی شعله کم باقی بماند و کاملا دم بکشد. پس از گذشت این مدت، نخود پلو با مرغ را در ظرف سرو بریزید و در صورت تمایل، با کمی کشمش تفت داده شده و زعفران دم کرده تزیین کنید. شما می‌توانید نخود پلو با مرغ را با انواع ترشی، سبزی خوردن و سالاد شیرازی سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (250, 'برگر بوقلمون', 'همبرگر با گوشت بوقلمون یکی از مقوی‌ترین انواع برگر است. برای آموزش طرز تهیه همبرگر با گوشت بوقلمون، مراحل زیر را به ترتیب دنبال کنید.', 'همبرگر با گوشت بوقلمون یکی از مقوی‌ترین انواع برگر است. برای آموزش طرز تهیه همبرگر با گوشت بوقلمون، مراحل زیر را به ترتیب دنبال کنید. اگر می‌خواهید ساندویچ برگر بوقلمون را بعد از تهیه، سرو کنید، بهتر است سس آن را ابتدا تهیه کنید. برای تهیه سس همبرگر خانگی بوقلمون، ابتدا در یک کاسه کوچک ماست یا سس مایونز، روغن زیتون، آب لیموترش، شوید تازه خرد و ساطوری شده، نمک و فلفل سیاه را با هم مخلوط کنید تا کاملا یکدست شود. این سس را در یخچال بگذارید تا طعم‌ها به خورد هم بروند. برای شروع کار تهیه برگر بوقلمون، قارچ‌ها را بشویید و اجازه دهید آب خود را از دست بدهد. سپس قارچ‌ها را به صورت ساطوری و ریز خرد کنید. گشنیزهای تازه را هم بشویید و وقتی آب خود را از دست دادند، آنها را ریز و ساطوری خرد کنید. در یک تابه، روغن زیتون یا کره را بریزید و روی حرارت بگذارید تا داغ شود. سپس قارچ‌ها را در تابه بریزید و تفت دهید تا آب آن بخار شود. سپس گشنیزها را هم کمی تفت دهید تا آب آن هم بخار شود. این مخلوط را کنار بگذارید. گوشت بوقلمون (سینه یا ترکیب سینه و ران) را بشویید. اجازه دهید گوشت آب خود را کاملا از دست بدهد. سپس بوقلمون را در چرخ گوشت بریزید و ۲ بار چرخ کنید تا کاملا نرم شود. پیاز و سیر را پوست بگیرید. پیاز و سیر را با رنده ریز، رنده کنید. آب پیاز را بگیرید. در یک کاسه، گوشت بوقلمون، تفاله پیاز، سیر، قارچ و گشنیز خرد و تفت داده شده، پودر زنجبیل، پودر فلفل سیاه، نمک، پودر فلفل پاپریکا و فلفل سبز ریز خرد شده را اضافه کنید. بعد از اینکه همه مواد برگر بوقلمون را آماده کردید و در کاسه ریختید، مواد را با هم ورز دهید تا کاملا یکدست شوند و انسجام بگیرند. اگر مایه برگر، خیلی شل بود، می‌توانید مقداری پودر سوخاری به آن اضافه کنید. اگر خیلی سفت بود، کمی روغن زیتون و آب به آن اضافه کنید. سپس روی کاسه را با سلفون بپوشانید و به مدت ۱ ساعت در یخچال بگذارید تا طعم‌ها به خورد هم بروند. بعد از آماده شدن همبرگر بوقلمون، از مایه برگر به اندازه یک پرتقال بردارید و کف دست‌هایتان گرد کنید. سپس همبرگر را بین دست‌هایتان بفشارید و صاف کنید. همه مایه برگر را به همین شکل آماده کنید. در یک ماهیتابه گریل مقداری روغن زیتون اسپری کنید و اجازه دهید روغن گرم شود. سپس هر دو طرف همبرگر را با دقت در ماهیتابه گریل کنید. برای گریل شدن هر عدد همبرگر، حدود ۷ تا ۱۰ دقیقه زمان کافی است. برای آماده کردن ساندویچ برگر بوقلمون، روی هر نان همبرگر ۲ قاشق غذاخوری از سسی را که آماده کرده‌اید، بمالید و روی سس، همبرگر را بگذارید. روی همبرگر خانگی بوقلمون را با یک برگ کاهو و برش‌های خیارشور تزیین و سرو کنید. ادویه برگر بوقلمون را می‌توانید بنا به سلیقه خود انتخاب کنید، ولی اگر در ترکیب ادویه برگر با گوشت بوقلمون از فلفل سیاه، پودر فلفل پاپریکا، پودر سیر (یا سیر تازه)، پودر پیاز (یا پیاز رنده و آبگیری شده)، پودر فلفل چیلی (یا پودر فلفل قرمز)، پودر خردل و پودر فلفل سفید بریزید، خوشمزه‌تر خواهد شد.', 11, 24, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2018/09/a9cad302-1003-4032-ab35-d0c434716fbe.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (251, 'همبرگر سویا (سویا برگر)', 'برای درست کردن همبرگر سویا، ابتدا سویا را بخیسانید، آبکش و در نهایت دو مرتبه چرخ کنید. سپس همه مواد را با هم ترکیب کنید و در مخلوط‌کن بریزید و با هم ترکیب کنید تا شکل خمیری بگیرند.', 'برای درست کردن همبرگر سویا، ابتدا سویا را بخیسانید، آبکش و در نهایت دو مرتبه چرخ کنید. سپس همه مواد را با هم ترکیب کنید و در مخلوط‌کن بریزید و با هم ترکیب کنید تا شکل خمیری بگیرند. مایه همبرگر سویا را برای چند دقیقه ورز دهید تا منسجم شوند. همبرگرها را فرم دهید و در یخچال بگذارید تا خودشان را بگیرند. تمام مدت زمانی که برای آماده سازی و تهیه برگر با سویا نیاز دارید، حدود ۶۰ دقیقه است. در اولین مرحله از طرز تهیه همبرگر با سویا و سبزیجات به صورت ساده، سویا را در یک کاسه بریزید و روی آن را با آب گرم یا ولرم بپوشانید. بهتر است سویا یک شب تا صبح در آب خیس بخورد تا نفخ آن کاملا گرفته شود. کمی نمک و آبلیمو هم به آبی که سویا را در آن خیس کرده‌اید، اضافه کنید. اما اگر عجله دارید، سویا را ۳۰ دقیقه تا ۲ ساعت در آب بخیسانید. در این مرحله، پیاز را پوست بگیرید (می‌توانید از یک یا دو حبه سیر هم به دلخواه استفاده کنید) و آن را رنده کنید. وقتی پیاز را رنده کردید، آب آن را کاملا با دست بگیرید تا فقط تفاله‌ها باقی بمانند. یک روش دیگر هم این است که به جای رنده کردن پیاز، آن‌ها را نگینی‌ ریز خرد و کاملا طلایی و سرخ کنید. سپس پیازها را به مواد اضافه کنید. سویا را آبکش کنید و با دست آن‌ها را کاملا فشار دهید تا آب باقی مانده در سویاها، کاملا خارج شود. سپس سویاها را در چرخ گوشت بریزید و ۲ بار چرخ کنید. می‌توانید از مخلوط‌کن، میکسر یا غذاساز هم استفاده کنید. این کار انسجام برگر سویا را بیشتر خواهد کرد. در این مرحله از طرز تهیه همبرگر با سویا بدون گوشت، سویاهای چرخ شده را به غذاساز برگردانید. سپس به آن‌ها آرد نخودچی، آرد گندم، سس خردل و سبزی‌های معطر را اضافه کنید. زیره، پودر سیر، نمک، فلفل سیاه و پیاز را هم به مواد اضافه کنید. همه مواد را با هم به مایه برگر سویا بیفزایید تا کل مواد تمام شود. همه مواد را با هم میکس و مخلوط کنید تا نرم و یکدست شوند. سپس تخم مرغ را اضافه کرده و شروع به ورز دادن کنید. حدود ۵ دقیقه مواد را با دست ورز دهید تا کاملا حالت چسبندگی پیدا کنند و خودشان را بگیرند. در این مرحله از طرز تهیه همبرگر با سویا، باید یک مایه خمیری داشته باشید. مواد را به اندازه یک نارنگی بردارید و کف دستتان بچرخانید تا به شکل گرد دربیاید. سپس آن را کمی بین دو دست پهن کنید. دو کیسه فریزر بردارید و یکی را روی سطح قرار دهید. گلوله‌ را روی کیسه فریزر قرار دهید. یک پلاستیک فریزر دیگر روی آن قرار دهید. وقتی مواد همبرگر بین دو پلاستیک فریزری قرار دادید، یک بشقاب را روی مواد فشار دهید تا پهن شود و ضخامت آن کاهش پیدا کند. قطر همبرگر باید به حدود نیم سانتی‌متر برسد. همه مواد را به همین شکل فرم دهید و سپس در یخچال بگذارید تا خودشان را کاملا بگیرند. حدود ۳۰ دقیقه زمان کافی است. می‌توانید آنها را به مدت ۱۵ دقیقه در فریزر قرار دهید. بعد از این که همه همبرگرها خودشان را گرفتند، آن‌ها را از یخچال خارج کنید. یک تابه روی حرارت قرار دهید و به آن روغن اضافه کنید و اجازه دهید تا داغ شود. همبرگر سویا را داخل روغن داغ بیندازید. حرارت را کم کنید صبر کنید تا برگر کاملا مغزپخت شود. همبرگرها را برگردانید تا سمت دیگرش نیز سرخ شود. برای آماده کردن برگرها می‌توانید آنها را روی گریل کباب کنید. همبرگرهای گیاهی و بدون گوشت را از روی روغن بیرون بیاورید و آنها را روی دستمال آشپزخانه قرار دهید تا روغن اضافه‌شان گرفته شود. همبرگر سویا را با نان مخصوص همبرگر، کاهو، گوجه فرنگی و خیارشور سرو کنید. اگر وگان نیستید و می‌توانید از مقداری گوشت چرخ کرده در طرز تهیه همبرگر با سویا هم استفاده کنید و ارزش غذایی آن را بالاتر ببرید. لازم نیست مقدار گوشت زیاد باشد، همین که طعم همبرگر و انسجام آن را بیشتر کند، کافی است. در این صورت حتما گوشت را همراه با سویایی که خیسانده و آبکش کرده‌اید و آبش را خوب گرفته‌اید، دو مرتبه چرخ کنید. به همراه گوشت هم از کمی دنبه استفاده کنید تا همبرگرتان کمی چرب باشد و موقع سرخ یا گریل کردن، روغن پس بدهد. بقیه مراحل را می‌توانید مانند مراحل طرز تهیه همبرگر با سویا انجام دهید و همبرگرتان را آماده کنید. این همبرگر را به شکل گیاهی و در ترکیب با سیب زمینی و سویا نیز می‌توانید تهیه کنید. در اولین مرحله از طرز تهیه همبرگر با سویا و سیب زمینی، سویا را با آب گرم و کمی آبلیمو یا شیر برای ۲۰ دقیقه خیس کنید تا بوی خامی آن گرفته شود. سپس سویاها را آبکش کرده و با دست خوب فشار دهید تا آب اضافی‌ سویا گرفته شود. در ادامه، یک عدد سیب زمینی متوسط را بشویید. یک قابلمه را از آب پر کنید و روی حرارت قرار دهید. سیب زمینی را به آن اضافه کنید و صبر کنید تا سیب‌زمینی‌ بپزد. سپس آن را از قابلمه بیرون بیاورید و بعد از سرد شدن پوست بگیرید و رنده کنید. یک عدد پیاز را پوست گرفته و رنده کنید و آب آن را به وسیله فشار دست جدا کنید. یک حبه سیر را نیز پوست بگیرید و بشوئید. سپس به وسیله رنده مخصوص یا سیر له کن آن را به شکل پوره در آورید. سویا را با غذاساز نرم کنید. سپس سویا را به همراه سیب‌زمینی، پیاز رنده‌شده و آب‌گرفته، سیر، ادویه‌ها و پودر سوخاری مخلوط کنید و خوب ورز دهید تا ترکیب منسجم و چسبنده‌ای به‌دست آید. از مواد گلوله‌هایی به اندازه یک نارنگی بردارید، آن‌ها را به شکل همبرگر فرم دهید و در ظرفی در یخچال بگذارید تا حداقل ۳۰ دقیقه استراحت کنند. بهتر است همبرگرها را بین دو نایلون پلاستیکی قرار دهید. تابه را با مقدار کمی روغن گرم کنید. حرارت را کم کنید. همبرگرها را به آن اضافه کنید و از دو طرف سرخ کنید تا طلایی و برشته شوند. شما می‌توانید همبرگر سویا و سیب زمینی را گریل و کباب نیز کنید. در انتها همبرگر گیاهی آماده شده را به شکل ساندویچی و با نان، گوجه فرنگی، خیارشور و کاهو با سس همبرگر در صورت دلخواه سرو کنید. همبرگر با سویا و مرغ یکی از گزینه‌های سالم، خوش‌طعم و مقرون‌به‌صرفه برای وعده‌های غذایی است که می‌تواند جایگزین خوبی برای همبرگرهای آماده باشد. ترکیب مرغ و سویا در این دستور، بافتی لطیف و طعمی دلچسب ایجاد می‌کند که برای بزرگسالان و کودکان مناسب است. در اولین مرحله از طرز تهیه همبرگر با سویا و مرغ، سویا را در یک کاسه بریزید. آب جوش روی آن بریزید تا کاملاً پوشیده شود. کمی آبلیمو یا شیر به آن اضافه کنید تا بوی خامی آن گرفته شود. حدود ۲۰ دقیقه اجازه دهید بماند. سپس با آب سرد آبکش کرده و با دست کاملاً فشار دهید تا آب اضافی‌اش گرفته شود. پوست پیاز را بگیرید، آن را بشویید و با رنده ریز رنده کنید. سپس با دست، آب اضافی پیاز را بگیرید. سیر را هم پوست بگیرید و با رنده ریز، رنده کنید یا کاملاً بکوبید. مقدار لازم از سینه مرغ را بشویید تا تمیز شود. آن را در آبکش قرار دهید تا آب اضافی آن خارج شود. سینه مرغ را با چاقو به تکه‌های کوچک برش بزنید و سپس در چرخ گوشت یا غذاساز بریزید و آن را کاملاً چرخ یا پوره کنید. در یک کاسه بزرگ، مرغ چرخ‌شده، سویای آب‌گرفته، پیاز و سیر رنده‌شده، تخم‌مرغ، آرد سوخاری و ادویه‌ها را اضافه کنید. مخلوط را با دست خوب ورز دهید تا حالت خمیری و منسجم پیدا کند. در صورت شل بودن مواد، کمی دیگر پودر سوخاری اضافه کنید. از مایه آماده‌شده، گلوله‌هایی به اندازه دلخواه بردارید. آن‌ها را به شکل گرد و ضخامت مناسب فرم دهید. سپس روی یک سینی بچینید، روی آن را بپوشانید و حداقل ۳۰ دقیقه در یخچال قرار دهید تا فرم خود را حفظ کنند. در یک تابه، کمی روغن بریزید و روی حرارت ملایم قرار دهید. همبرگرها را در تابه بگذارید و هر طرف را حدود ۴ تا ۵ دقیقه سرخ کنید تا کاملاً طلایی و پخته شوند. همبرگر را بعد از آنکه از تابه بیرون آورید روی یک دستمال قرار دهید تا روغن اضافی آن جذب شود. سپس آن را با نان همبرگری و گوجه، کاهو و خیارشور سرو کنید. اگر در طرز تهیه همبرگر با سویا از گوشت استفاده نکنید، این غذا مناسب افراد گیاهخوار خواهد بود. همبرگر با سویا یکی از خوراکی‌های محبوب گیاه‌خواران و کسانی است که به هر دلیلی از خوردن گوشت خودداری می‌کنند. برای انسجام پیدا کردن مواد برگر گیاهی سویا، حتما آب پیاز را با دست بگیرید تا فقط تفاله آن باقی بماند. در صورتی که پیاز آب داشته باشد، مواد از هم می‌پاشد. علاوه بر این، بعد از تهیه مواد، برگر را حتما در یخچال قرار دهید تا خودش را بگیرد. اگر مایه همبرگر بیش از حد شل بود، به‌تدریج آرد سوخاری یا پودر نان خشک اضافه کنید تا سفت شود. برای اینکه همبرگر هنگام پخت ترک نخورد، اجازه دهید پیش از سرخ کردن حداقل ۳۰ دقیقه در یخچال استراحت کند.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/01/Best-Vegan-Burgers-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (252, 'هویج پلو با مرغ', 'برنج را با آب چند مرتبه بشویید و آب آن را خالی کنید. برنج را با مقداری آب و نمک به مدت یک تا دو ساعت بخیسانید.', 'برنج را با آب چند مرتبه بشویید و آب آن را خالی کنید. برنج را با مقداری آب و نمک به مدت یک تا دو ساعت بخیسانید. مقداری زعفران را بسابید و روی آن آبجوش یا چند قطعه یخ قرار دهید تا دم بکشد و رنگ بگیرد. خلال نارنج را ابتدا برای بیست دقیقه بخیسانید و سپس آن را چند بار بجوشانید و آب آن را خالی کنید تا تلخی آن کاملا گرفته شود. خلال بادام و خلال پسته را نیز در مقداری گلاب بخیسانید تا نرم شوند. کشمش پلویی و زرشک را تمیز کنید و برای چند دقیقه در مقداری آب بخیسانید. سینه مرغ را بشویید و در آبکش قرار دهید. سپس مرغ را به شکل مکعبی خرد کنید یا به صورت درسته بپزید و ریش ریش کنید. برای گرفتن بوی زهم مرغ، سینه مرغ را در قابلمه‌ای پر از آب قرار دهید. یک عدد پیاز چهارقاچ شده، چند عدد برگ بو و ادویه‌های لازم شامل نمک، فلفل سیاه، زردچوبه و دارچین را به قابلمه اضافه کنید. قابلمه را روی حرارت قرار دهید تا به جوش بیاید، سپس حرارت را کم کنید و اجازه دهید سینه مرغ کاملا بپزد. در اواخر پخت مرغ یک قاشق غذاخوری زعفران اضافه کنید. هویج‌ها را بشویید، پوست بگیرید و با رنده درشت رنده کنید یا خلال کنید. در یک ماهیتابه مناسب مقداری کره را ذوب کنید و خلال‌های هویج را در کره تفت دهید. بعد از این‌که هویج‌ها کمی سبک شدند، ۵ قاشق غذاخوری شکر اضافه کنید و هم بزنید. حرارت را کم کنید و صبر کنید هویج‌ها سبک شوند. یک عدد پیاز را پوست بگیرید و نگینی خرد کنید. در یک تابه با مقدار کافی روغن، پیازهای خرد شده را تفت دهید تا سبک و طلایی شوند. پیاز داغ را به هویج خلالی روی حرارت اضافه کنید و به خوبی با هم ترکیب کنید. سینه مرغ پخته شده را ریش ریش کنید. مرغ ریش ریش شده را به ترکیب هویج و پیاز داغ روی حرارت اضافه کنید و به خوبی با یکدیگر ترکیب کنید. در یک تابه با مقدار لازم روغن، خلال‌های نارنج را با شکر تفت دهید تا عطر آن بلند شود. سپس از روی حرارت بردارید. زرشک را به روغن اضافه کنید و روی حرارت ملایم تفت دهید. مقداری شکر نیز به آن اضافه کنید. زرشک را از روی حرارت بردارید. کشمش را نیز درون همان تابه تفت دهید تا پف کند سپس آن را از روی حرارت بردارید. مقداری از زرشک، کشمش، خلال نارنج، خلال بادام و خلال پسته را برای تزیین پلو نگه دارید و بقیه آنها را به ترکیب مرغ و هویج و پیاز داغ روی حرارت اضافه کنید. مایه هویج پلو را به خوبی مخلوط کنید. حداقل ۲ قاشق غذاخوری زعفران غلیظ دم کرده، نمک، فلفل سیاه و دارچین را به مایه هویج پلو اضافه کنید. ادویه‌ها را در گوشه‌ای از تابه با روغن تفت دهید تا عطر آنها بلند شود، سپس با مایه هویج پلو مخلوط کنید. یک قابلمه را با مقدار آب کافی روی حرارت قرار دهید تا به جوش بیاید. آب برنج خیسانده شده را دور بریزید. بعد از به جوش آمدن آب، برنج را به همراه نمک اضافه کنید و منتظر بمانید تا برنج کمی نرم شود. برنج را کمی زنده‌تر آبکش کنید. برای ته دیگ، کف قابلمه مقداری روغن بریزید و با نان یا سیب زمینی حلقه حلقه شده بپوشانید. سپس یک کفگیر برنج آبکش شده را روی آن بریزید و یک کفگیر از مواد هویج پلو. این لایه‌ها را همین‌طور تا انتها و تا زمانی که برنج و مواد میانی آن تمام شوند، تکرار کنید. لایه آخر باید برنج باشد. روی لایه آخر پلو مقداری کره و مقداری زعفران دم کرده بریزید و اجازه دهید هویج پلو به مدت ۴۵ دقیقه روی شعله ملایم گاز دم بکشد. بعد از ۴۵ دقیقه هویج پلو آماده است. آن را در ظرف مورد نظر خود سرو کنید و روی آن را با مخلوط زرشک، کشمش و خلال پسته و بادام تزیین کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2018/11/%D9%87%D9%88%DB%8C%D8%AC-%D9%BE%D9%84%D9%88-%D8%B4%DB%8C%D8%B1%D8%A7%D8%B2%DB%8C-%D9%85%D8%AC%D9%84%D8%B3%DB%8C-%D8%B3%D8%A7%D8%AF%D9%87-%DB%B2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (253, 'وادا پاو', 'یک ظرف بزرگ را انتخاب کنید. حالا مقداری روغن را در آن بریزید و اجازه دهید تا روغن کمی داغ شود. خردل و آنغوزه و برگ کاری را به روغن اضافه کنید. پیازهایی که نگینی کردید را هم در این روغن تفت دهید تا طلا', 'یک ظرف بزرگ را انتخاب کنید. حالا مقداری روغن را در آن بریزید و اجازه دهید تا روغن کمی داغ شود. خردل و آنغوزه و برگ کاری را به روغن اضافه کنید. پیازهایی که نگینی کردید را هم در این روغن تفت دهید تا طلایی شوند. زنجبیل، ۲ حبه سیر و گشنیز خرد شده را هم به این ترکیب اضافه کرده و خوب تفت دهید. زردچوبه را به موادی که حالا خوب با هم تفت داده شده‌اند، اضافه کنید و دوباره به مدت ۳۰ ثانیه مواد را در روغن تفت دهید. سیب زمینی‌هایی که آب پز و له کرده بودید را به همراه کمی نمک به ترکیب اضافه کنید. وقتی که مطمئن شدید این ترکیب به خوبی با هم مخلوط شده، می‌توانید ظرف را از روی حرارت بردارید و در آخرین مرحله کمی هم آبلیو تازه را به آن اضافه کنید. آرد و آرد برنج، کمی زردچوبه، پودر چیلی، نمک و کمی هم جوش شیرین (اگر دوست داشتید) را با هم ترکیب کنید. شما باید خمیری کاملاً صاف و بدون گلوله‌های آرد داشته باشد، پس در صورت لزوم می‌توانید از نصف پیمانه آب گرم هم استفاده کنید. مخلوط سیب زمینی که در مرحله قبل آماده کردید بودید را به هر شکلی که دوست دارید، گلوله‌ای و یا به شکل بیضی در آورده و دور آن را با دست صاف کنید. سیب زمینی‌ها در خمیری که آماده کردید خوب بغلتانید. ظرف مناسبی را انتخاب کنید، داخل آن روغن بریزید و حرارت را زیاد کنید. سیب زمینی‌هایی که قالب گیری کرده بودید را در روغن داغ بریزید. فراموش نکنید که باید مدام سیب زمینی‌ها را هم بزنید تا ترد و کاملاً طلایی شوند. وقتی که بعد از طلایی شدن آن را از روغن خارج کردید، برای سالم‌تر شدن برگر گیاهی می‌توانید سیب زمینی‌ها را روی پارچه یا کاغذهای مناسب خشک کنید تا روغن اضافی‌اش از بین برود. فلفل چیلی قرمز را در ظرف دیگری در روغن سرخ کنید. اما خیلی مراقبت باشید! بعد از اینکه مطمئن شدید فلفل‌ها خوب سرخ شده‌اند، آن را مثل ترکیب سیب زمینی روی کاغذ قرار دهید تا روغن اضافی‌اش گرفته شود. مواد برگر گیاهی آماده است. اما اگر دوست دارید طعم متفاوتی را تجربه کنید، فلفل چیلی سبز، تمر هندی و پودر سیر را روی نان برگر پخش کنید. وادا پاو روی نان بگذارید و فلفل‌های چیلی که سرخ کرده بودید را هم در کنار وادا پاو بگذارید. تا زمانی که وادا پاو گرم و داغ است، آن را سرو کنید. پشیمان نمی‌شوید!', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/%D8%B7%D8%B1%D8%B2-%D8%AA%D9%87%DB%8C%D9%87-%D9%88%D8%A7%D8%AF%D8%A7-%D9%BE%D8%A7%D9%88%D8%9B-%D9%87%D9%85%D8%A8%D8%B1%DA%AF%D8%B1-%DA%AF%DB%8C%D8%A7%D9%87%DB%8C-%D9%87%D9%86%D8%AF%DB%8C-%E2%80%8C%D9%87%D8%A7.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (254, 'واویشکا بوقلمون', 'سینه بوقلمون را تمیز بشویید و در آبکش قرار دهید تا آب اضافه آن خارج شود. سپس سینه‌های بوقلمون را به صورت مکعبی خرد کنید. اندازه تکه‌ها نه باید زیاد درشت باشد و نه ریز.', 'سینه بوقلمون را تمیز بشویید و در آبکش قرار دهید تا آب اضافه آن خارج شود. سپس سینه‌های بوقلمون را به صورت مکعبی خرد کنید. اندازه تکه‌ها نه باید زیاد درشت باشد و نه ریز. تکه‌های سینه بوقلمون را با یک عدد پیاز چهار قاچ شده و به همراه مقداری آب روی حرارت قرار دهید و اجازه دهید به جوش بیاید. زمانی که روی قابلمه کف جمع شد، کف را با یک قاشق بگیرید و بلافاصله بوقلمون‌ها را آبکش کنید. این کار فقط برای رفع بوی زهم بوقلمون انجام می‌شود. چهار عدد پیاز را تمیز بشویید و پوست بگیرید. یک قابلمه با سایز مناسب انتخاب کنید و مقدار کافی از روغن در آن بریزید و اجازه دهید تا روی حرارت داغ شود. پیازها را به صورت نگینی خرد کنید و روی حرارت ملایم تفت دهید تا پیازها طلایی و سبک شوند. سیرها را پوست بگیرید و با رنده ریز و یا سیر له کن به صورت پوره درآورید. سیرها را به پیاز اضافه کنید و روی حرارت ملایم تفت دهید تا عطر آن بلند شود. سیر را بعد از طلایی شدن پیازها به تابه اضافه کنید تا نسوزد. بعد از آماده شدن سیر داغ و پیاز داغ، سینه بوقلمون را که از قبل شسته و خرد کرده‌اید اضافه کنید. تکه‌های بوقلمون را در روغن به خوبی تفت دهید تا رنگ آن تغییر کند. سپس ادویه‌ها شامل نمک، فلفل سیاه، دارچین، پاپریکا، فلفل قرمز را در گوشه‌ای از تابه با روغن تفت دهید تا عطر آنها بلند شود و از حالت خامی دربیایند. ادویه‌ها را با تکه‌های بوقلمون و سیر داغ و پیاز داغ به خوبی مخلوط کنید تا عطر و طعم مواد به خورد هم برود. ۶ عدد گوجه را بشویید، پوست بگیرید و با غذاساز، میکسر و یا رنده به شکل پوره دربیاورید. پوره گوجه فرنگی را به محتویات تابه روی حرارت اضافه کنید و با دیگر مواد مخلوط کنید. اجازه دهید پوره گوجه فرنگی روی حرارت متوسط به خوبی تفت بخورد تا آب آن کاملا کشیده شود، به غلظت برسد و روغن بیندازد. یک چهارم لیوان دسته‌دار فرانسوی غوره بدون هسته را به ترکیب واویشکا روی حرارت اضافه کنید و با دیگر مواد هم بزنید. یک قالب پنجاه گرمی کره نیز به اضافه کنید و اجازه دهید با حرارت ذوب شود. سپس درب قابلمه را ببندید و اجازه دهید واویشکای بوقلمون روی حرارت ملایم به روغن بیفتد و جا افتاده شود. بعد از حدود چهل و پنج دقیقه واویشکای شمالی با گوشت بوقلمون شما آماده است. آن را در یک ظرف مناسب بریزید و به همراه نان، برنج کته یا چلو آبکش، انواع ترشی خانگی سرو کنید. در کنار این غذای لذیذ و مقوی می‌توانید از سبزی خوردن و ماست نیز استفاده کنید. همچنین می‌توانید مقداری سیب زمینی سرخ کرده هم کنار واویشکا بوقلمون سرو کنید.', 21, 44, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/10/18e02acb8e233359441e57dfbf79b095__charsoogh-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (255, 'یتیمچه', 'ابتدا بادمجان‌ها را پوست بگیرید و مکعبی خرد کنید. گوجه فرنگی‌ها را هم بشورید و پوست بگیرید یا رنده کنید. بادمجان‌های خرد شده را در مقداری آب و نمک بگذارید تا تلخی بادمجان گرفته شود و البته روغن کمتری', 'ابتدا بادمجان‌ها را پوست بگیرید و مکعبی خرد کنید. گوجه فرنگی‌ها را هم بشورید و پوست بگیرید یا رنده کنید. بادمجان‌های خرد شده را در مقداری آب و نمک بگذارید تا تلخی بادمجان گرفته شود و البته روغن کمتری هم به خودش بگیرید. پیازها را نگینی خرد کنید و در مقدار کمی روغن تفت دهید. سیر رنده شده را هم اضافه کنید. همین که بوی سیر بلند شد حالا بادمجان‌هایی که آبکش کرده و شسته‌ایم و آبش خشک شده به همراه فلفل دلمه‌ای نگینی خرد شده را به ترکیب پیاز و سیر تفت داده شده اضافه کنید. نمک، فلفل و زردچوبه را اضافه کنید و تفت دهید. وقتی بادمجان و فلفل دلمه‌ای طلایی و نرم شدند ۱ قاشق غذاخوری رب گوجه را هم اضافه کنید و تفت دهید تا طعم خامی رب گرفته شود. گوجه فرنگی‌های پوست گرفته و ریز خرد شده یا رنده شده را اضافه کنید و درب ماهیتابه یا قابلمه را بگذارید تا آب گوجه فرنگی به خورد تمام مواد برود و گوجه‌ها هم سرخ شوند. وقتی گوجه فرنگی آبش کشیده شد، سیب زمینی را هم نگینی خرد و اضافه کنید. ۲ لیوان آبجوش روی مواد بریزید و اجازه دهید یتیمچه روی شعله ملایم گاز حسابی بپزد و جا بیفتد و آبش به مقدار دلخواه شما کشیده شود.', 15, 30, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2020/02/fefb94d75db8fd3868d87ba3a064d344.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (256, 'پلو میگو مجلسی بوشهری', 'پیاز را نگینی و ریز خرد کنید.', 'پیاز را نگینی و ریز خرد کنید. وقتی پیازها سبک و طلایی شدند سیرهای رنده شده را هم اضافه کنید و تفت دهید تا بوی سیر بلند شود. سبزی پلویی را هم به این ترکیب اضافه کنید و کمی تفت دهید. حالا میگوهای تمیز پاک شده و شسته شده را اضافه کنید و همراه بقیه مواد تفت دهید. ادویه پلویی را به این ترکیب اضافه کنید و تفت دهید تا بوی خامی ادویه گرفته شود. وقتی مواد مقداری تفت خوردند تمر هندی را هم اضافه کنید و تمام مواد را خوب با هم مخلوط کنید. برنج را جداگانه بپزید و آبکش کنید. بهتر است برنج را کمی زنده (یک ترک روی دانه برنج بخورد کافی است) آبکش کنید تا پلو میگو شفته نشود. حالا کف قابلمه روغن بریزید و ته دیگ مورد نظرتان را بچینید. سپس لایه لایه برنج آبکش شده و مایه میگو را بریزید و پلو را دم بگذارید. بعد از پخت پلو میگو آن را با انواع ترشی لیته، ترشی بندری یا ترشی سالاد گوجه سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/09/meigo-polo-bushehri.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (257, 'شیرین پلو با مرغ مجلسی', 'برنج را به خوبی بشویید تا آب آن زلال شود. سپس، برنج را با مقداری آب و یک قاشق غذاخوری نمک در قابلمه مناسبی بریزید و بسته به نوع برنج برای ۱ الی ۲ ساعت بخیسانید. مقدار آب باید به اندازه‌ای باشد که دو ب', 'برنج را به خوبی بشویید تا آب آن زلال شود. سپس، برنج را با مقداری آب و یک قاشق غذاخوری نمک در قابلمه مناسبی بریزید و بسته به نوع برنج برای ۱ الی ۲ ساعت بخیسانید. مقدار آب باید به اندازه‌ای باشد که دو بند انگشت بالاتر از سطح برنج قرار گیرد. خلال پسته و خلال بادام را هم در ظروف مجزایی بریزید و در مقدار کمی گلاب بخیسانید. سپس، زرشک را به خوبی پاک کرده، بشویید و آبکشی کنید. زرشک شسته شده را نیز در ظرف مناسبی بریزید و مقداری آب به آن اضافه کنید تا خیس بخورد. هویج‌های شسته شده و بدون پوست را نیز به صورت درشت رنده کرده یا به صورت خلالی ریز برش بزنید. پیاز شسته شده و بدون پوست را هم به چهار قسمت برش بزنید و کنار بگذارید. سینه مرغ را به خوبی بشویید و در سبد بگذارید تا آب اضافی آن خارج شود. سپس، گوشت سینه مرغ را همراه با پیازی که قبلا برش زدید در قابلمه مناسبی بریزید. یک لیوان آب و همچنین، مقداری نمک، فلفل سیاه و زردچوبه نیز به آب مرغ اضافه کنید. این قابلمه را روی حرارت متوسط بگذارید تا بجوشد. سپس، درب قابلمه را ببندید و حرارت را ملایم کنید تا مرغ به مدت ۳۰ دقیقه روی حرارت ملایم بپزد و آب آن کشیده شود. در فاصله پختن مرغ و خیس خوردن برنج، پوست پرتقال خلالی را در قابلمه مناسبی بریزید و مقداری آب به آن اضافه کنید. این قابلمه را روی حرارت متوسط بگذارید تا بجوشد. سپس، پوست پرتقال خلالی را از صافی رد کنید. دوباره پوست پرتقال جوشیده شده را در همان قابلمه بریزید و مقداری آب اضافه کنید تا برای بار دوم بجوشد و تلخی آن کاملا گرفته شود. پس از آن، محتویات قابلمه را بار دیگر از صافی رد کنید و پوست پرتقال‌ها را زیر آب سرد بگیرید و حسابی آبکشی کنید. خلال پوست پرتقال آبکشی شده را همراه با ۳ قاشق غذاخوری شکر و یک لیوان آب در قابلمه مناسبی بریزید و برای ۲۰ دقیقه بجوشانید تا شیرین شود. اگر سینه مرغ کاملا پخته و نرم شده، باید گوشت مرغ را از قابلمه بیرون بیاورید و در ظرف مناسبی بگذارید. سپس، با کمک چنگال، گوشت سینه مرغ پخته شده را ریش ریش کنید. قابلمه مناسبی را تا نیمه پر از آب کنید و روی حرارت متوسط بگذارید تا بجوشد. سپس، آب برنج خیس خورده را خالی کرده و خود برنج را به قابلمه در حال جوش اضافه کنید. بسته به نوع برنج، باید حدود ۷ الی ۱۰ دقیقه منتظر بمانید تا دانه‌های برنج دندان‌گیر شده و برای دم کردن آماده شوند. بهتر است برای پلوهای مخلوط برنج را کمی زنده آبکش کنید. یک دانه برنج را از قابلمه در حال جوش بیرون بیاورید و امتحان کنید. الان باید لایه خارجی برنج کاملا نرم و هسته مرکزی آن سفت و دندان‌گیر باشد. سپس، برنج را در سبد بریزید تا آب اضافی آن خارج شود. پیشنهاد می‌کنیم مقداری آب ولرم روی برنج بریزید تا نشاسته اضافی آن هم شسته شود. قابلمه مناسبی را روی حرارت متوسط بگذارید و مقداری روغن به آن اضافه کنید. سپس، ته دیگ دلخواه خود را کف قابلمه بچینید و برنج آبکشی شده را روی ته دیگ بریزید. در ادامه، به اندازه یک چهارم لیوان آب و مقداری روغن مایع روی برنج بریزید. درب قابلمه را با دم‌کنی بپوشانید و روی قابلمه قرار دهید تا قابلمه بخار کند. سپس، حرارت را ملایم کنید و اجازه دهید برنج شما برای ۴۵ دقیقه الی یک ساعت روی حرارت ملایم کاملا دم بکشد. در فاصله دم کشیدن برنج، مقداری کره را در تابه مناسبی بریزید و روی حرارت بگذارید تا گرم شود. سپس، هویج‌هایی که قبلا به دلخواه رنده یا خلالی کردید را به این تابه اضافه کنید. هویج‌ها را چند دقیقه تفت دهید تا نرم شوند. ۳ قاشق غذاخوری شکر را همراه با یک لیوان آب به تابه حاوی هویج اضافه کنید. درب تابه را ببندید و مدتی منتظر بمانید تا آب هویج کاملا کشیده شود. سپس، درب تابه را بردارید و یک قاشق غذاخوری زعفران دم کرده به این ترکیب اضافه کنید. محتویات تابه را هم بزنید تا همه هویج‌های شیرین شده به زعفران آغشته شوند. حالا، محتویات تابه را در ظرف مناسبی بریزید و برای تزیین شیرین پلو کنار بگذارید. مقداری کره را در تابه بریزید و روی حرارت قرار دهید. سپس، گوشت سینه مرغ را که قبلا ریش ریش کردید به این تابه اضافه کنید. گوشت مرغ ریش ریش شده را همراه با کره کاملا تفت دهید و یک قاشق غذاخوری زعفران هم به آن اضافه کنید تا کاملا خوشمزه و خوش آب و رنگ شود. سپس، گوشت مرغ تفت داده شده را نیز در ظرف مناسبی بریزید و کنار بگذارید. زرشک خیس خورده را از صافی رد کنید تا آب اضافی آن خارج شود. سپس، زرشک را زیر آب سرد بگیرید و حسابی آبکشی کنید. تابه مناسبی را روی حرارت بگذارید و مقداری کره به آن اضافه کنید تا گرم شود. زرشک خیس خورده و آبکشی شده را نیز همراه با چند قاشق غذاخوری شکر در این تابه بریزید و تفت دهید. یک قاشق زعفران دم کرده را هم به این ترکیب اضافه کنید و پس از پفکی شدن زرشک، تابه را از روی حرارت بردارید. زرشک شیرین و پفکی را هم به ظرف مناسبی منتقل کنید و برای تزیین شیرین پلو کنار بگذارید. مقداری کره را در تابه بریزید و روی حرارت بگذارید تا گرم شود. سپس، خلال پوست پرتقال را که قبلا شیرین و مربایی کردید به تابه حاوی کره اضافه کنید. خلال پوست پرتقال را تفت دهید تا کاملا طعم کره را به خودش بگیرد. در آخر، مقداری زعفران دم کرده را نیز روی پوست پرتقال تفت داده شده بریزید. این ترکیب را به خوبی هم بزنید تا همه خلال‌ها به زعفران آغشته شوند. پس از آن، خلال پوست پرتقال شیرین و تفت داده شده را نیز به ظرف مناسبی منتقل کنید. خلال بادام خیس خورده در گلاب را از صافی رد کنید. سپس، دوباره مقداری کره را در تابه بریزید و روی حرارت قرار دهید. خلال بادام را هم به این تابه اضافه کنید و تفت دهید تا طعم خامی بادام گرفته شود. خلال پسته را نیز درست مانند خلال بادام همراه با مقداری کره تفت دهید تا طعم خامی آن برطرف شود. سپس، خلال پسته تفت داده شده را از تابه خارج کنید و در ظرف مناسبی بریزید. برنج را در ظرف سرو بریزید و روی آن را با مواد شیرین پلو شامل مرغ ریش ریش، خلال پرتقال، خلال هویج، خلال پسته و بادام و زرشک به صورت لایه لایه و به زیباترین شکل ممکن تزیین کنید. شما می‌توانید این غذای لذیذ را همراه با سبزی خوردن، ترشی و سالاد دلخواه میل کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
