INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (276, 'شکر پلو شیرازی', 'برنج را از قبل حداقل به مدت ۳۰ دقیقه تا حداکثر ۳ ساعت در مقداری آب ولرم و نمک خیس کنید. این کار باعث می‌شود برنج بعد از پخت له نشود و قد بکشد.', 'برنج را از قبل حداقل به مدت ۳۰ دقیقه تا حداکثر ۳ ساعت در مقداری آب ولرم و نمک خیس کنید. این کار باعث می‌شود برنج بعد از پخت له نشود و قد بکشد. خلال بادام و پسته را در یک کاسه بریزید و ۲ تا ۳ قاشق گلاب و کمی آب اضافه کنید. حداقل ۳۰ دقیقه و حداکثر ۳ ساعت صبر کنید تا خلال‌های بادام و پسته، هم نرم شوند و هم عطر گلاب را به خودشان بگیرند. برای درست کردن شهد، شکر را با ۱ لیوان آب در یک قابلمه بریزید و روی حرارت متوسط بگذارید. حدود ۵ دقیقه این مواد را روی حرارت ملایم قرار دهید تا بجوشند. دانه‌های شکر باید کاملا حل شوند و شربت شکر پلو غلیظ شود. ۴ قاشق غذاخوری زعفران دم کرده غلیظ را هم به شربت بیفزایید. وقتی محلول یکدست شد، شعله را خاموش کنید تا شربت دمای خود را از دست بدهد. در یک قابلمه مقداری آب بریزید و صبر کنید تا روی حرارت بالا، جوش بیاید. حرارت را روی حالت متوسط تنظیم کنید. آب برنج خیسانده را دور بریزید و برنج را به آب در حال جوش اضافه کنید. حدود ۷ تا ۸ دقیقه، بسته به نوع برنجی که استفاده می‌کنید، صبر کنید تا برنج کمی نرم شود. باید مغز برنج سفت بماند و سطح روی برنج نرم شود. در این مرحله برنج را آبکش کنید. کف یک قابلمه کمی روغن یا کره بریزید. اجازه دهید گرم و داغ شود. ته دیگ برنج مورد نظرتان را کف قابلمه بگذارید. از نان لواش یا سیب زمینی ورقه‌ای می‌توانید استفاده کنید. برنج را روی ته‌دیگ بریزید. شربت غلیظ شکر پلو را روی آن بریزید. شربت را به آرامی با شکر پلو مخلوط کنید. دم کنی را روی برنج بگذارید و اجازه دهید شکر پلو دم بکشد. اگر خلال پوست پرتقال شیرین شده ندارید، آن را به روشی که توضیح می‌دهیم، شیرین کنید. برای شیرین کردن پوست پرتقال، نارنگی یا نارنج را بشویید و به صورت خلالی خرد کنید. لایه سفید داخلی پوست‌ها را جدا کنید و دور بریزید. در یک قابلمه ۱ لیوان دسته‌دار فرانسوی آب بریزید و روی حرارت بگذارید. اجازه دهید آب به جوش بیاید. خلال پوست پرتقال یا نارنج را در قابلمه بریزید و ۲ تا ۳ دقیقه صبر کنید. خلال‌ها را آبکش کنید. این کار را ۳ مرتبه انجام دهید و هر بار آب را دور بریزید تا وقتی که تلخی پوست نارنج یا پرتقال از بین برود. دوباره در یک قابلمه به اندازه نصف لیوان آب و ۱ قاشق غذاخوری شکر بریزید. خلال‌هایی را که تلخی آن‌ها گرفته شده است، در ترکیب آب و شکر بریزید و بجوشانید تا شیرین شوند. ۱ قاشق زعفران هم به خلال‌ها اضافه کنید و هم بزنید. خلال‌ها که سرخ شدند، آنها را از تابه بیرون بیاورید و کنار بگذارید. خلال‌های بادام و پسته را که در گلاب و آب خیسانده‌اید، آبکش کنید و اجازه دهید آب خود را از دست بدهند. خلال بادام را در تابه بریزید و با روغن، کمی تفت دهید. خلال بادام را کنار بگذارید و بعد خلال پسته را در تابه بریزید و با کمی روغن تفت دهید. خلال پسته و بادام را کنار بگذارید. شکر پلو آماده شده را در ظرف مناسب بریزید. روی آن را با خلال‌هایی که از قبل سرخ کرده‌اید، خلال بادام و پسته و خلال پوست پرتقال و …، تزیین کرده و با قیمه بوشهری، مرغ سرخ کرده یا خورش قیمه نذری همراه با سالاد شیرازی سرو کنید.', 16, 34, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/07/shekar-polo-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (277, 'ته چین گرمساری', 'برای تهیه ته چین گوشت گرمساری، ابتدا گوشت را به صورت ورقه‌های نازک برش‌ بزنید و در ادویه پلویی مخصوص ته‌چین گوشت گرمساری (شامل پودر گوجه خشک، زردچوبه، فلفل، دارچین، گرده غوره، گرده لیمو و پودر زیره) ب', 'برای تهیه ته چین گوشت گرمساری، ابتدا گوشت را به صورت ورقه‌های نازک برش‌ بزنید و در ادویه پلویی مخصوص ته‌چین گوشت گرمساری (شامل پودر گوجه خشک، زردچوبه، فلفل، دارچین، گرده غوره، گرده لیمو و پودر زیره) به علاوه مقداری فلفل سیاه و قرمز، پودر پیاز، پودر پاپریکا و دو عدد پیاز نگینی خرد شده بزرگ بخوابانید. گوشت را با دست در این مواد بچرخانید تا تمام قسمت‌ها آغشته شوند. مدت زمان لازم برای مرینیت گوشت بهتر است از یک شب تا صبح باشد. در یک قابلمه مناسب آب را جوش بیاورید و برنج را در آن بپزید. برنج را کمی دان بردارید (همین که دانه برنج دو ترک برداشت کافی است). برنج را آبکش کنید. کف قابلمه مقداری روغن بریزید. وقتی روغن کمی داغ شد کف قابلمه مقداری نان لواش بچینید و ½ برنج آبکش شده را روی نان‌ها بریزید. حالا گوشت مزه‌دار شده را به صورت خام و به همراه سایر موادی که گوشت در آن‌ها مزه‌دار شده یعنی پیاز خرد شده و ادویه‌جات را روی برنج بریزید. روی گوشت، زعفران دم کرده غلیظ یا زعفران سابیده شده و ادویه پلویی (اختیاری است) بریزید و به اندازه دلخواه نمک بپاشید. سپس روی مواد را با یک لایه دیگر برنج پر کنید. ته‌ چین گوشت گرمساری را ابتدا روی شعله متوسط گاز قرار دهید تا حسابی بخار کند و سپس شعله گاز را ملایم کنید. ته‌ چین گوشت را روی شعله ملایم گاز دم کنید تا حسابی بخار کند و گوشت مغز پخت و نرم شود. برای این‌که ته‌ چین گوشت گرمساری بهتر بخار کند و دم بکشد ۱/۲ فنجان آب جوش روی برنج بریزید. ۳ الی ۴ ساعت زمان برای دم کشیدن ته‌ چین گرمساری لازم است. در این فاصله کشمش‌ها را چند ساعت در آب بخیسانید و بعد آبکش کنید. سپس با کمی روغن محلی یا کره تفت دهید و به آن‌ها ½ قاشق چای‌خوری زردچوبه و ۱ قاشق چای‌خوری دارچین اضافه کنید. در یک ساعت پایانی پخت ته‌ چین گوشت گرمساری، کشمش‌های سرخ شده را روی برنج بریزید. (اختیاری: می‌توانید یک عدد پیاز متوسط را هم ابتدا سرخ و کاراملی کنید و سپس کشمش‌ها را به آن اضافه و سرخ کنید و در یک ساعت پایانی پخت روی برنج بریزید). برای تهیه ته چین گرمساری با مرغ، از ران مرغ استفاده کنید. برنج را آبکش کنید. مرغ را با پیاز و ادویه مزه‌دار کنید و چند ساعت در یخچال قرار دهید. سپس مرغ‌ها را با مقداری آب بپزید و بعد همراه با سیب زمینی ورقه شده کف قابلمه قرار دهید. در انتها برنج آبکش را روی مرغ‌ها بریزید و اجازه دهید برنج دم بکشد. بعد از پخت این غذا را با زرشک و خلال پسته و بادام تزیین کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2019/08/%D8%AA%D9%87-%DA%86%DB%8C%D9%86-%DA%AF%D9%88%D8%B4%D8%AA-%DA%AF%D8%B1%D9%85%D8%B3%D8%A7%D8%B1%DB%8C-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (278, 'زیره پلو کرمانی', '**طرز تهیه زیره پلو ساده:**', '**طرز تهیه زیره پلو ساده:** 1. زیره‌ها را تمیز کرده، داخل ظرفی ریخته و به مدت ۳۰ دقیقه بخیسانید. سپس آبکشی کرده و روی دستمال بریزید تا خشک شوند. 2. برنج را به خوبی بشویید و در مقداری آب و نمک بخیسانید. 3. قابلمه‌ای حاوی آب و نمک را روی حرارت قرار دهید تا به جوش آید. برنج را داخل آب جوش بریزید. زمانی که برنج کمی نرم شد و قد کشید، آن را آبکش کنید. 4. مقداری روغن یا کره در قابلمه بریزید. برای ته دیگ می‌توانید از نان لواش، سیب زمینی حلقه شده یا برنج و کمی زعفران استفاده کنید. 5. برنج آبکش شده را با زیره ترکیب کنید. برای خوش رنگ شدن می‌توانید مقداری زعفران دم کرده نیز اضافه کنید. 6. درب قابلمه را با دم‌کنی روی آن قرار دهید تا برنج دم بکشد. زمانی که قابلمه داغ شد، مقداری آب را با روغن ترکیب کرده و روی برنج بریزید و درب قابلمه را بگذارید تا برنج بخار کند و خوب دم بکشد. 7. در انتها می‌توانید مقداری کره آب شده روی برنج بریزید و آن را با گوشت، مرغ یا هر غذای دلخواه سرو کنید. **طرز تهیه زیره پلو با گوشت قلقلی:** 1. گوشت چرخ کرده را با پیاز، نمک، فلفل و زردچوبه ترکیب کنید. 2. گوشت را به صورت قلقلی درست کرده و در روغن سرخ کنید. 3. گوشت قلقلی را در کنار زیره پلوی ساده سرو کنید. **طرز تهیه زیره پلو با مرغ:** 1. مرغ‌ها را داخل یک ظرف بریزید و مقداری نمک و فلفل به آن‌ها بزنید تا مزه‌دار شوند و بوی زهم مرغ گرفته شود. 2. پیاز را به صورت حلقه‌ای برش دهید و کف قابلمه بچینید. تکه‌های مرغ مزه‌دار شده را روی پیاز قرار دهید و مقداری برگ بو یا چوب دارچین روی آن بگذارید. درب قابلمه را ببندید و شعله گاز را کم کنید تا مرغ با حرارت ملایم بپزد. 3. بعد از چند دقیقه، مرغ‌ها آب می‌اندازند. کمی زعفران به مرغ‌ها اضافه کنید. درب قابلمه را دوباره بگذارید تا آب مرغ کم شود و با روغن خودش سرخ شود. 4. برنج را آبکش کنید (مانند روش ساده). 5. زیره‌ها را با برنج آبکش شده ترکیب کرده و خوب هم بزنید. مقدار زیره بستگی به ذائقه شما دارد. 6. مقداری روغن در قابلمه بریزید، سپس کمی از ترکیب زیره و پلو را داخل قابلمه بریزید. مرغ‌ها را داخل قابلمه قرار دهید و دوباره کمی برنج بریزید. ابتدا شعله گاز را زیاد کنید تا برنج بخار کند، سپس حرارت را کم کنید. کمی آب و روغن به برنج اضافه کنید و یک دم کنی روی درب قابلمه بگذارید تا برنج به خوبی دم بکشد. **طرز تهیه زیره پلو با گوشت:** 1. گوشت (راسته گوسفند یا گوساله) را همراه با یک عدد پیاز خرد شده، برگ بو، زردچوبه، فلفل سیاه و آب روی حرارت قرار دهید تا بپزد (حدود ۲ تا ۳ ساعت). بعد از پخت، گوشت را از قابلمه خارج کرده و آب گوشت را از صافی رد کنید. 2. پیازها را به صورت خلالی خرد کرده و در روغن سرخ نمایید. سپس مقداری زردچوبه و رب گوجه فرنگی به پیازهای سرخ شده اضافه کنید. 3. گوشت پخته شده را خرد کنید و با مقداری پودر دارچین داخل ماهیتابه بریزید. سپس مقداری زعفران دم کرده و نمک به گوشت اضافه کنید و آب گوشت صاف شده را به ترکیب گوشت و پیاز اضافه کنید و اجازه دهید مواد برای ۱۵ تا ۲۰ دقیقه پخته شوند. 4. زیره پلو ساده را به روشی که پیش‌تر ذکر شد آماده کنید و همراه با گوشت سرو کنید. **طرز تهیه زیره پلو با مرغ ریش ریش:** 1. بعد از پختن مرغ، آن را ریش ریش کنید. 2. مرغ‌های ریش ریش شده را با برنج آبکش شده و زیره (زیره پلو ساده) مخلوط کنید و داخل قابلمه بریزید تا دم بکشد. برای عطر و طعم بهتر، زعفران بیشتری اضافه کنید.', 16, 34, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/04/186724.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (279, 'والک پلو (سیرک پلو) تهرانی', 'برای تهیه والک پلو شمیرانی، ابتدا باید برنج را به‌ دقت بشویید و با نمک بخیسانید. والک‌های تازه را هم پاک کنید، بشویید و در ابعاد نسبتا درشت خرد کنید. ساقه‌های سیر را نیز پس از شست‌وشو به صورت حلقه‌ای', 'برای تهیه والک پلو شمیرانی، ابتدا باید برنج را به‌ دقت بشویید و با نمک بخیسانید. والک‌های تازه را هم پاک کنید، بشویید و در ابعاد نسبتا درشت خرد کنید. ساقه‌های سیر را نیز پس از شست‌وشو به صورت حلقه‌ای نازک خرد کنید. والک و سیر را با اندکی روغن و ادویه مختصر، روی حرارت ملایم برای چند دقیقه کوتاه تفت دهید. در ادامه، برنج خیس‌ خورده را در قابلمه در حال جوش بریزید و بجوشانید؛ پیش از آن‌که برنج کاملا نرم شود، آن را آبکش کنید. سپس، در قابلمه مناسبی، ته‌دیگ دلخواه خود را قرار دهید و روی ته‌دیگ را لایه‌لایه برنج و مخلوط والک و سیر بچینید؛ میان لایه‌ها هم به دلخواه می‌توانید اندکی ادویه پلویی بپاشید. برنج را به شکل گنبدی جمع کنید و کره‌ آب‌ شده یا روغن را روی آن بریزید. درب این قابلمه را با دم‌کنی بپوشانید و روی حرارت بگذارید؛ ابتدا شعله را زیاد و پس از بخار کردن قابلمه، حرارت را ملایم کنید و اجازه دهید والک پلو در حدود یک ساعت دم بکشد. سپس، والک پلوی معطر و خوش‌رنگ را با زعفران دم‌ کرده و ته‌دیگی طلایی تزیین کرده و همراه کوفته قلقلی، ماهی سرخ کرده یا مرغ سرخ کرده زعفرانی یا اصلا فقط با کوکوی والک سرو کنید. طرز تهیه والک پلو مجلسی یا سیرک پلو به این صورت است که ابتدا باید برنج را به خوبی بشویید تا آب برنج زلال شود. سپس، برنج شسته شده را با مقداری آب و ۱ قاشق غذاخوری نمک در ظرف مناسبی بریزید و برای ۱ الی ۲ ساعت بخیسانید. در این مرحله از طرز تهیه والک پلو تهرانی، والک‌ها را به خوبی پاک کنید و برگ‌های زرد و ریشه‌های اضافی را دور بریزید. سپس، برگ‌های تر و تازه والک را با دقت بشویید و در سبد بریزید تا آب اضافی آن‌ها خارج شود. در ادامه، والک‌های شسته شده را به صورت نسبتا درشت برش بزنید و کنار بگذارید. حالا، ساقه‌های سیر تازه را نیز به خوبی بشویید و پوست نازک آن‌ها را جدا کنید. سپس، ساقه‌های سیر شسته شده را هم در سبد بریزید و پس از خارج شدن آب اضافی، آن‌ها را به صورت مورب یا حلقه‌ای نازک خرد کنید. حالا مقداری روغن را در تابه مناسبی بریزید و با شعله ملایم روی گاز قرار دهید. سپس، والک خرد شده را داخل ماهیتابه بریزید و برای یک دقیقه تفت دهید. ساقه‌های برش خورده سیر تازه را نیز به والک‌ها اضافه کنید و برای یک دقیقه دیگر تفت دهید. در ادامه، مقداری زردچوبه و فلفل سیاه را هم به این ترکیب اضافه کنید. مواد داخل تابه را برای چند ثانیه هم بزنید تا سبزی‌ها و ادویه‌ها به خوبی با یکدیگر ترکیب شوند و زردچوبه برای مدت کوتاهی تفت ببیند و عطر زردچوبه آزاد شود. مراقب باشید مواد بیش از حد سرخ نشوند و سبزی‌ها رنگ سبز خود را حفظ کنند. سپس، حرارت را خاموش کنید و تابه را کنار بگذارید. اکنون، قابلمه مناسبی را تا نیمه پر از آب کنید و روی حرارت متوسط بگذارید تا بجوشد. به اندازه یک قاشق غذاخوری نمک و کمی روغن نیز در این قابلمه بریزید. سپس، آب برنج را خالی کرده و برنج‌های خیس خورده را به قابلمه در حال جوش اضافه کنید. اجازه دهید برنج به مدت ۷ الی ۱۰ دقیقه (بسته به نوع برنج) بجوشد و دندان‌گیر شود. زمانی که برنج اندکی نرم شد ولی مغز آن هنوز سفت بود، آن را آبکش کنید. در این مرحله از درست کردن والک پلو شمیران، بعد از آبکش کردن برنج کمی صبر کنید تا آب آن کاملا خارج شود. سپس، قابلمه مناسبی را انتخاب کرده و مقداری روغن به این قابلمه اضافه کنید. کف این قابلمه را با ته‌دیگ دلخواه بپوشانید. روی ته‌دیگ، لایه‌ای از برنج بریزید. پس از آن، مقداری از مخلوط والک و ساقه‌ سیر را اضافه کنید. این کار را لایه‌لایه تکرار کنید تا مواد تمام شوند. در صورت تمایل، روی هر لایه برنج مقدار کمی ادویه پلویی بپاشید. در آخر، برنج را به صورت گنبدی جمع کرده و چند حفره در آن ایجاد کنید. حالا، مقداری کره‌ آب‌ شده یا روغن روی برنج بریزید. درب قابلمه را با دم‌کنی بپوشانید و ببندید. پس از آن، قابلمه حاوی والک پلو را ابتدا روی حرارت زیاد بگذارید تا بخار کند؛ سپس، شعله را ملایم کرده و به مدت ۴۵ الی ۶۰ دقیقه صبر کنید تا والک پلو دم بکشد. پس از گذشت این مدت، والک پلوی خوشمزه و دم کشیده را در ظرف سرو بریزید. روی این والک پلو مجلسی را با زعفران دم کرده غلیظ و تکه‌های ته‌دیگ خوش آب و رنگ تزیین کنید. شما به دلخواه می‌توانید والک پلو تهرانی را به همراه گوشت چرخ کرده (کوفته قلقلی)، ماهی سرخ کرده به سبک سبزی پلو با ماهی یا مرغ سرخ کرده زعفرانی در کنار انواع ترشی، ماست یا سالاد سرو کنید. طرز تهیه والک پلو کته در مراحل ابتدایی عینا مانند همان چیزی است که در دستور بالا برایتان گفتیم. یعنی، پس از شستن و خرد کردن والک و ساقه‌های سیر باید آن‌ها را برای مدت کوتاهی روی حرارت ملایم تفت دهید و کنار بگذارید. سپس، برنج شسته شده را با مقداری آب (یک و نیم لیوان آب به ازای هر لیوان برنج) و کمی نمک در قابلمه مناسبی بریزید و روی حرارت قرار دهید تا بجوشد. زمانی که آب برنج در حال تبخیر شدن بود و برنج هنوز کاملا نپخته بود، مخلوط والک و ساقه‌ سیر تفت‌ داده‌ شده را به برنج اضافه کنید. محتویات این قابلمه را به آرامی و بدون له کردن، با کفگیر مخلوط کنید تا سبزی در برنج پخش شود. پس از مخلوط کردن، حرارت را کاملا ملایم کنید. مقداری روغن یا کره بر روی برنج بریزید؛ درب قابلمه را با دم‌کنی بپوشانید و اجازه دهید برنج به مدت ۳۰ الی ۴۵ دقیقه روی شعله کم دم بکشد. پس از دم کشیدن، در صورت تمایل، مقداری زعفران دم‌کرده روی برنج بریزید و والک‌ پلو را با ته‌دیگ طلایی در ظرف سرو قرار دهید. شما می‌توانید این غذای خوشمزه را همراه با ماست چکیده یا انواع ترشی میل کنید. روش پخت والک پلو کرمانشاهی نیز تا حدودی مشابه والک پلو شمیران است؛ با این تفاوت که باید ۱ عدد پیاز متوسط و ۲ حبه سیر (به جای ساقه سیر تازه) را نیز به صورت نگینی ریز خرد کرده و با روغن حیوانی کرمانشاهی یا کره سرخ کنید. سپس، قطعات والک خرد شده را به تابه حاوی پیاز داغ و سیر داغ اضافه کنید و برای مدت کوتاهی تفت دهید. باقی مراحل را مانند دستور بالا دنبال کرده و در صورت تمایل، موقع دم گذاشتن والک پلو دوباره روی آن مقداری روغن حیوانی بریزید.', 25, 50, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/05/%D9%88%D8%A7%D9%84%DA%A9-%D9%BE%D9%84%D9%88.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (280, 'سالاد شیرازی', 'خیار و گوجه فرنگی ها را کاملا بشویید. سر و ته خیارها را جدا کنید. بدون این که پوست آن ها را جدا کنید، شروع به ایجاد برش های مربعی کوچک روی خیار کنید.', 'خیار و گوجه فرنگی ها را کاملا بشویید. سر و ته خیارها را جدا کنید. بدون این که پوست آن ها را جدا کنید، شروع به ایجاد برش های مربعی کوچک روی خیار کنید. خیارها را در یک کاسه، به صورت کاملا ریز خرد کنید. همین کار را با پیاز شسته و پوست کنده و گوجه فرنگی ها هم انجام دهید. وقتی همه مواد را کاملا خرد و با هم مخلوط کردید، نمک و فلفل سیاه را اضافه کنید. (معمولا سالاد شیرازی را کمی تندتر درست می کنند و به خاطر آبغوره نمک بیشتری اضافه می کنند. چون ترکیب نمک و آبغوره بسیار لذیذ خواهد شد. ولی در نهایت سلیقه و طبع خودتان را در طرز تهیه سالاد شیرازی مد نظر قرار دهید.) آبغوره را به ترکیب سالاد شیرازی اضافه کنید و روی سالاد را نعنا خشک بپاشید. حواستان باشد که استفاده از نعنا خشک زیاد، باعث تلخ شدن سالاد می شود و اگر هم از نعنای کمی استفاده کنید، عطر مناسب را نخواهد داشت. بعد از اینکه همه مواد را کاملا با هم مخلوط کردید، چند دقیقه سالاد را استراحت دهید تا طعم یکدستی بگیرد. سپس سالاد شیرازی را در ظرف مناسب سرو کنید.', 10, 20, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2021/05/salad-shirzi-asil-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (281, 'آش انار شیرازی', 'برای طرز تهیه آش انار به روش شیرازی، باید ابتدا لپه را به مدت ۱ الی ۲ ساعت در آب خیس کنید.', 'برای طرز تهیه آش انار به روش شیرازی، باید ابتدا لپه را به مدت ۱ الی ۲ ساعت در آب خیس کنید. پیازها را به صورت خلالی و یک‌دست خرد کنید و در روغن سرخ کنید تا پیاز داغ فراوان درست شود. در قابلمه‌ای که می‌خواهید آش را در آن بار بگذارید، مقداری روغن بریزید و اجازه دهید داغ شود. سپس گوشت چرخ کرده را در روغن تفت دهید. کمی فلفل سیاه، زردچوبه و نمک به گوشت اضافه کنید. می‌توانید گوشت را به صورت قلقلی نیز آماده کنید. بعد از عوض شدن رنگ گوشت، لپه‌های خیس خورده را اضافه کنید و به خوبی با گوشت تفت بدهید. (لپه را از قبل نپزید). بعد از تفت دادن گوشت و لپه، سبزی آش انار که ساطوری خرد شده را به همراه برنج و مقداری پیاز داغ اضافه کنید و با سایر مواد هم بزنید. چند قاشق پیاز داغ برای تزیین کنار بگذارید. آب انار و آب جوش را اضافه کنید. شعله گاز را روی حرارت ملایم تنظیم کنید و اجازه دهید آش به آرامی جا بیفتد. در اواسط پخت، رب انار را به آش اضافه کنید. در ۱۰ دقیقه پایانی پخت، ۱ قاشق غذاخوری زعفران دم کرده را به آش اضافه کنید. بعد از پخت آش، آن را با پیاز داغ و در صورت تمایل نعناع داغ و دانه‌های قرمز میوه انار تزیین کنید. برای تهیه آش انار شیرازی بدون گوشت، تنها باید گوشت را از مواد اولیه حذف کنید و آش را به صورت ساده و بدون گوشت آماده کنید. در طرز پخت آش انار به روش شیرازی باید از سبزی‌هایی مثل تره، گشنیز، جعفری و اسفناج خرد شده استفاده کنید. اضافه کردن برنج نیم دانه به آش انار شیرازی موجب می‌شود تا آش شما سریع‌تر پخته شود و برنج زودتر در آش له شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/11/iranwatching-2100-1617483939-ash-anar-min.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (282, 'آش کارده شیرازی', 'اگر سبزی کارده را به صورت خشک استفاده می‌کنید، سبزی کارده خشک را در یک ظرف بریزید و روی آن را آب بریزید تا آن را بپوشاند. ۲ تا ۳ ساعت سبزی را بخیسانید. سپس سبزی‌ را آبکش کرده و در یک ظرف دیگر بریزید.', 'اگر سبزی کارده را به صورت خشک استفاده می‌کنید، سبزی کارده خشک را در یک ظرف بریزید و روی آن را آب بریزید تا آن را بپوشاند. ۲ تا ۳ ساعت سبزی را بخیسانید. سپس سبزی‌ را آبکش کرده و در یک ظرف دیگر بریزید. آبی که سبزی‌ها را در آن خیس کرده‌اید، کنار بگذارید. آرد را به سبزی کارده اضافه کنید. با گوشتکوب، سبزی و آرد را بکوبید تا کاملا له شود و آرد به خورد سبزی‌ها برود. سپس آبی را که از خیساندن سبزی‌ها به دست آمده‌ است، به مخلوط سبزی و آرد اضافه کنید و هم بزنید. این مخلوط را در یک قابلمه مناسب بریزید و روی حرارت بگذارید. قابلمه را باید تا ۳/۴ حجم آن آب کرده و بعد از جوش آمدن، شعله گاز را کم کنید. حدود ۱۵ دقیقه بعد، ماست یا دوغ ترش بدون گاز را به آش اضافه کنید و مدام هم بزنید تا ماست نبرد و خراب نشود. سپس درب قابلمه را بگذارید و ۳ ساعت صبر کنید تا سبزی کارده بپزد و نرم شود. جو پرک را بشویید و به قابلمه آش اضافه کنید. مواد را هم بزنید و در این فاصله بقیه مواد را آماده کنید. پیاز را پوست بگیرید و خیلی ریز خرد کنید. می‌توانید پیاز را رنده هم بکنید. سپس در یک تابه مقداری روغن بریزید و روی حرارت بگذارید تا داغ شود. پیاز را اضافه کنید و هم بزنید تا سبک و طلایی شود. سپس شنبلیله خشک، نمک، زردچوبه و فلفل سیاه را اضافه کنید تا همه مواد با هم تفت بخورند. مخلوط داخل تابه را به قابلمه اضافه کنید و هم بزنید. اگر غلظت آش کارده، کم است، می‌توانید کمی آرد با آب ولرم مخلوط کرده و به قابلمه اضافه کنید. همه مواد را هم بزنید و ۳۰ دقیقه دیگر صبر کنید تا آش کاملا جا بیفتد و بپزد. در این مدت، آش کارده را هر چند دقیقه یک بار هم بزنید. در صورت دلخواه، مقداری نمک اضافه کنید. وقتی آش جا افتاد، شعله را خاموش کرده و آن را سرو کنید. برای تهیه آش کارده با آبغوره و سبزی تازه: سبزی‌های کارده را بشویید و وقتی آب خود را از دست دادند، آنها را به قطعات کوچک‌تر خرد کنید. آرد را به سبزی‌ها اضافه کنید و سبزی و آرد را با هم بکوبید. کوبیدن را تا حدی ادامه دهید که سبزی‌ها له شوند. سپس آبغوره را اضافه کنید. به مدت ۲۴ ساعت، روی ظرف را بپوشانید و سبزی‌های آغشته شده به آبغوره را در دمای محیط بگذارید. سبزی کارده را بعد از استراحت کردن، در یک قابلمه بریزید و روی حرارت بگذارید. روی آش مقداری آب بریزید و به مدت ۲ ساعت روی حرارت ملایم بپزید. سپس برنج نیم‌دانه را بشویید و به قابلمه اضافه کنید. حدود ۱ ساعت صبر کنید تا برنج‌ها هم کاملا نرم شوند. پیاز را پوست بگیرید و خرد کنید. پیاز را در تابه با مقداری روغن و زردچوبه سرخ کنید. سپس شنبلیله خشک را اضافه کنید و تفت دهید. حرارت را قطع کنید و این مواد داخل تابه را به قابلمه حاوی آش کارده اضافه کنید. ۱۵ دقیقه دیگر آش را روی حرارت هم بزنید تا کاملا جا بیفتد. به اندازه دلخواه، نمک و فلفل سیاه اضافه کنید. وقتی آش کارده به قوام و طعم دلخواهتان رسید، شعله را خاموش کرده و آش را سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/11/ash-karde.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (283, 'حشو ماهی شکم پر جنوبی', 'برای درست کردن حشو ماهی شکم پر جنوبی در فر ابتدا ماهی را تمیز کنید و استخوان ماهی را جدا کنید. برای جدا کردن استخوان ماهی آن را به پشت برگردانید و کمی روی پشت ماهی فشار دهید تا استخوان‌ها به آرامی از', 'برای درست کردن حشو ماهی شکم پر جنوبی در فر ابتدا ماهی را تمیز کنید و استخوان ماهی را جدا کنید. برای جدا کردن استخوان ماهی آن را به پشت برگردانید و کمی روی پشت ماهی فشار دهید تا استخوان‌ها به آرامی از ماهی جدا شوند. ماهی را برگردانید و از قسمت آبشش به آرامی استخوانی که ورآمده را از گوشت جدا کنید. برای این‌که استخوان و تیغ‌های ماهی به راحتی از گوشت جدا شوند انگشت اشاره خود را زیر استخوان اصلی قرار دهید و آن را به سمت بالا بیاورید. این کار را به آرامی تا قسمت دم ماهی انجام دهید تا استخوان کاملا جدا شود. وقتی به قسمت انتها و دم ماهی رسیدید با یک چاقوی تیز استخوان را از قسمت آبشش و نیز انتهای ماهی ببرید. با این روش استخوان کاملا و به راحتی از گوشت ماهی جدا می‌شود. ماهی را خوب بشورید و در آبکش قرار دهید تا خشک شود. در این فاصله تمر هندی را خیس کنید. نیم ساعت زمان برای خیس خوردن تمر هندی کافی است. سپس تمر هندی را در صافی بریزید و با فشار دست هسته و پوست تمرهندی را جدا کنید. عصاره به دست آمده از تمر هندی را با یک قاشق غذاخوری آرد مخلوط کنید. در یک ماهیتابه پیازهای نگینی خرد شده را در روغن مایع تفت دهید تا سبک و طلایی شوند. سپس سیر له شده را اضافه کرده و کمی تفت دهید. زردچوبه و فلفل قرمز را هم اضافه دهید و تفت دهید تا طعم خامی زردچوبه گرفته شود. در یک ماهیتابه جداگانه سبزی ماهی شکم پر شامل گشنیز و شنبلیله را تفت دهید. وقتی آب سبزی‌ها گرفته شد مقداری روغن اضافه کنید و سبزی را سرخ کنید. مراقب باشید زیاد سبزی را تفت ندهید چون طعم شنبلیله تلخ می‌شود. بهتر است به صورت جداگانه سبزی‌ها را تفت دهید و میزان تفت دادن شنبلیله کمتر باشد تا طعم تلخی به خودش نگیرد. برای درست کردن سس حشو باید تمر هندی را به سبزی‌های سرخ شده اضافه کنید و هم بزنید. مخلوط سبزی و تمر هندی را به سیر و پیاز داغ اضافه کنید. اگر مایه حشو شما کمی آبدار است اجازه دهید روی حرارت ملایم گاز چند جوش بزند تا غلیظ شود. کمی از مایه حشو بچشید اگر به نمک نیاز داشت، به مقدار لازم نمک اضافه کنید. مایه حشو برای ماهی شکم‌پر شما آماده است. فر را روشن کنید و روی درجه ۱۸۰ درجه سانتی‌گراد تنظیم کنید. ابتدا سینی فر را کمی چرب کنید. ماهی را به صورتی که شکم ماهی باز و به سمت بالا باشد در سینی فر بگذارید. حدود ۲۰ دقیقه ماهی را در طبقه وسط فر قرار دهید تا ماهی کمی سرخ شود. سپس سینی فر را بیرون بیاورید و قسمت روی ماهی را با مایه حشو پر کنید. به مدت ۱۵ دقیقه دیگر در طبقه وسط فر، ماهی را گریل کنید. وقتی که رنگ ماهی طلایی شد حشو ماهی شکم‌پر جنوبی شما برشته و سرخ شده و آماده است. برای درست کردن حشو ماهی شکم‌پر جنوبی روی منقل باید از توری مخصوص کباب استفاده کنید. به همان ترتیبی که گفته شد مایه حشو را داخل شکم ماهی بریزید و از قسمت پشت داخل توری بگذارید. قسمتی شکم ماهی که پر از سبزی شده باید رو به بالا باشد تا مایه حشو در آتش نریزید. به مدت ۱۵ تا ۲۰ دقیقه ماهی را روی منقل کباب کنید. وقتی که ماهی و حشو خوب خودشان را گرفتند به مدت ۵ دقیقه توری را برگردانید تا قسمت شکم ماهی که آغشته به حشو است هم کمی برشته شود. در طرز تهیه ماهی شکم پر روی منقل حتما برگرداندن توری را با دقت انجام دهید تا حشو از ماهی جدا نشود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/05/e.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (284, 'قلیه میگو جنوبی', 'اگر از میگوهای منجمد استفاده می‌کنید، از قبل میگوها را از فریزر بیرون بیاورید تا یخ آن ذوب شود. اگر از میگوی تازه استفاده می‌کنید، پوست و رگ پشت میگوها را جدا کنید. اگر هم از میگوی خشک استفاده می‌کنید', 'اگر از میگوهای منجمد استفاده می‌کنید، از قبل میگوها را از فریزر بیرون بیاورید تا یخ آن ذوب شود. اگر از میگوی تازه استفاده می‌کنید، پوست و رگ پشت میگوها را جدا کنید. اگر هم از میگوی خشک استفاده می‌کنید، میگوها را بشویید و در آبکش بریزید. شنبلیله و گشنیز را پاک کنید و بشویید. بعد از اینکه سبزی‌ها آب خود را از دست دادند، آنها را سرخ کنید. (اگر از سبزی خشک استفاده می‌کنید، میزان سبزی‌ها را نصف مقداری که در دستور تهیه ذکر شده است، استفاده کنید. می‌توانید سبزی‌های خشک را هم بخیسانید و آبکش کنید.) پیاز را پوست بگیرید و به صورت نگینی خرد کنید. سیرها را هم پوست بگیرید و پوره یا ریز خرد کنید. در یک تابه، مقداری روغن بریزید و پیازها را در آن سرخ کنید تا طلایی شوند. سپس شنبلیله و گشنیز را اضافه کنید. چند دقیقه سبزیجات را با پیاز تفت دهید تا رنگ سبزی‌ها عوض شود. سیر خرد شده، زردچوبه، نمک و فلفل سیاه و قرمز را اضافه کنید و هم بزنید تا عطر سیر خارج شود. ۱ قاشق چای‌خوری آرد را به همراه سبزیجات اضافه کنید و تفت دهید تا طعم خام آن گرفته شود. به اندازه ۲/۳ از محتویات داخل تابه (سبزیجات) را جدا کنید و کنار بگذارید. میگوها را به تابه اضافه کنید و کمی تفت دهید تا بوی زهم آنها گرفته شود. درب تابه را بگذارید و حرارت را کم کنید. تمر هندی را با مقداری آب ولرم مخلوط کنید و با دست هم بزنید تا عصاره آن خارج شود. سپس عصاره جدا شده از هسته و پوسته تمر را در یک قابلمه بریزید و روی حرارت بگذارید. میگوها را از تابه بیرون بیاورید و کنار بگذارید. سبزی‌های داخل تابه و ۲/۳ سبزی را که جدا کرده بودید، در قابلمه بریزید. ۲ و نیم لیوان آب جوش اضافه کنید و وقتی مواد جوش آمدند، درب قابلمه را نیمه‌باز بگذارید. به مدت ۱ ساعت صبر کنید تا سبزی‌ها بپزند و طعم بگیرند. بعد از گذشت ۱ ساعت، میگوهای سرخ شده را که کنار گذاشته بودید به قابلمه اضافه کنید. حدود ۳۰ تا ۴۰ دقیقه دیگر صبر کنید تا میگوها کاملا بپزند و طعم سبزیجات و تمبر هندی را بگیرند. وقتی قلیه میگو کاملا لعاب انداخت، در صورت دلخواه، یک قاشق رب گوجه فرنگی را جداگانه با روغن سرخ کرده و به قابلمه اضافه کنید. قلیه میگو را بچشید و در صورت دلخواه، ادویه و فلفل و نمک آن را اندازه کرده و چاشنی‌هایی مثل آبغوره، آب نارنج و آبلیمو اضافه کنید. قلیه میگو را با برنج زعفرانی یا چلو سرو کنید. نوش جان!', 33, 67, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/11/%D9%82%D9%84%DB%8C%D9%87-%D9%85%DB%8C%DA%AF%D9%88%D8%8C-%D8%BA%D8%B0%D8%A7%DB%8C-%D8%AC%D9%86%D9%88%D8%A8%DB%8C-%D8%A8%D8%AD%D8%B1%D8%A7%D9%84%D9%81%D9%86%D9%88%D9%86.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (285, 'آداب و رسوم شب یلدا در مازندران', 'در مازندران، تدارکات شب یلدا از چند روز قبل آغاز می‌شود و در آخرین روز پاییز، کارهایی مانند خانه‌تکانی، دانه کردن انار و پاک کردن برنج برای شام شب یلدا انجام می‌گیرد.', 'در مازندران، تدارکات شب یلدا از چند روز قبل آغاز می‌شود و در آخرین روز پاییز، کارهایی مانند خانه‌تکانی، دانه کردن انار و پاک کردن برنج برای شام شب یلدا انجام می‌گیرد. در شب یلدا، دوستان و فامیل در خانه یکی از اهالی روستا جمع می‌شوند. خانم‌های فامیل و همسایه‌ها از صبح روز ۳۰ آذر دور هم جمع می‌شوند و با کمک هم شیرینی‌ها و غذاها را آماده کرده و تزیینات سفره شب یلدا را انجام می‌دهند. برای تهیه شیرینی پشت زیک، شکر را حرارت می‌دهند تا به حالت عسلی درآید. سپس کنجد یا گردوی خرد شده را به آن اضافه می‌کنند و با وردنه پهن می‌کنند و وقتی سرد شد، شیرینی را به صورت لوزی برش می‌دهند. در مناطق کلاردشت، رسم بر این است که برای شام شب یلدا غذای سنتی سبزی پلو با ماهی، اردک یا مرغ بخورند. فسنجان با مرغ یا اردک و کوکو سبزی نیز از غذاهای مخصوص مهمانی شب یلدا در اغلب شهرها و روستاهای مازندران است. برخی از خانواده‌های قدیم مازنی برای شام شب چله «برنج با گوشت خروس» آماده می‌کردند، اما در سال‌های اخیر اردک، غاز، بوقلمون، ماهی (به ویژه ماهی سفید شکم پر) نیز به سفره شام شب چله اضافه شده است. کلاردشتی‌ها برای شام شب یلدا حتما برنج می‌پزند. سفره شام معمولا شامل بورانی اسفناج، کوکو سبزی، سبزی پلو با مرغ، بوقلمون یا ماهی گرده‌بیج (ماهی سفید شکم پر) است. سبزی پلو را با سبزی فراوان و روغن گوسفندی طبخ می‌کنند. در مازندران، رسم بر این است که اگر افراد فامیل برای شام دور هم جمع نشوند، حتما برای شب‌نشینی بعد از شام با هم قرار می‌گذارند و معمولا همه افراد به خانه بزرگ‌تر فامیل می‌روند و تا پاسی از شب با خوردن خوراکی‌های مخصوص شب یلدا بیدار می‌مانند. برای سرگرمی، گاهی با هم کشتی می‌گیرند یا مچ می‌اندازند، شعرها و آوازهای قدیمی به زبان مازندرانی و منظومه‌های امیری و طبری می‌خوانند. در زبان مازندرانی به میز یا سفره‌ای که خوراکی‌های نمادین شب یلدا را روی آن می‌چینند «میزد» می‌گویند. این «میزد» که معمولا روی یک کرسی چیده می‌شود، پر است از انواع میوه‌های مختلف، مغزها و آجیل و انواع دانه‌های بو داده. مازندرانی‌ها اعتقاد دارند در این شب بهتر است میوه‌هایی که دانه بیشتری دارند (مثل انار و هندوانه) را میل کنند که نمادی از برکت و حاصلخیزی و باروری هستند. فلسفه خوردن انار و هندوانه را هم رنگ سرخ‌شان می‌دانند که سرخی خورشید را در تاریکی طولانی‌ترین شب سال تداعی می‌کند. گذاشتن گل‌های طبیعی و سبزه روی میز شب یلدا یا سفره چله شو یک رسم دیگر در میان آداب و رسوم شب یلدا در مازندران است. آملی‌ها معتقدند اگر در شب یلدا هندوانه بخورند در طول سال دچار سکته قلبی و خفقان نمی‌شوند. اهالی شهسوار معتقدند خوردنی‌های شب چله بهتر است چهل عدد باشد و مهم‌ترین خوراکی از این چهل عدد هم «هندوانه» است. ساروی‌ها می‌گویند اگر کسی در شب یلدا یک قاچ هندوانه بخورد در طول سال به دندان درد مبتلا نمی‌شود و کسی هم که حاجتی دارد در این شب با خوردن هندوانه حاجت روا می‌شود. در برخی از روستاهای مازندران برای خیر و برکت یک بره سفید کوچک می‌خرند و به خانه می‌آورند. در سال‌های دور اهالی روستاهای دورافتاده که به بستنی دسترسی نداشتند با برف و شکر یک ترکیب سرد و خوشمزه درست می‌کردند تا به عنوان دسر شب یلدا فالوده هم میل کرده باشند. برای گرفتن فال حافظ در شب یلدا، بزرگ فامیل به نیت تک تک جوانان جمع فال می‌گیرد و با صدایی بلند می‌خواند. برای فال گردو، یک سینی بزرگ گردو را روی سفره یلدا می‌گذارند. در انتهای شب هر فرد چشم‌هایش را می‌بندد و مقداری گردو از سینی برمی‌دارد (تقریباً ۵ تا ۶ گردو). شب موقع خواب گردوها را زیر تشک خود می‌گذارد. صبح که بیدار شدند همه گردوها را می‌شکند. اگر گردوها سفید و خوب بودند، یعنی فال خوب بوده و نیت آن فرد برآورده خواهد شد. در «فالگوش ایستادن»، دو زن یا دختر یک کلید و آینه در دست می‌گیرند و سر راهی می‌ایستند. سپس نیت می‌کنند و با گوش دادن به صحبت عابرانی که رد می‌شوند، فال خود را تعبیر می‌کنند. یکی دیگر از آداب و رسوم شب یلدا در مازندران عیدی دادن به کوچک‌ترهاست. یک رسم دیگر این است که دختران جوان صورت خود را می‌پوشانند و به میان جمع می‌روند. اگر دیگران بتوانند فرد را تشخیص بدهند نشانه خوبی است و به قول معروف احتمالا بخت آن دختر باز می‌شود. اگر خانواده‌ای در آن سال برای پسرشان نامزد گرفته باشند، برای او خرجی می‌فرستند. این خرجی شامل موادی مثل مرغ، ماهی، اردک یا بوقلمون در کنار تمام میوه‌ها، آجیل تزیین شده شب یلدا و شب‌چره‌های شب یلدا است. در شهرستان کلاردشت مازندران برای دخترانی که عقد کرده‌اند و هنوز در خانه پدر هستند، از طرف خانواده داماد شام می‌فرستند. تمام مایحتاج شب یلدا به علاوه بوقلمون، مرغ یا ماهی و یک بشقاب کوکو سبزی را در یک مجمع می‌چینند و به همراه یک هدیه که اصطلاحا به آن «تیکه‌ای» می‌گویند بعد از غروب آفتاب به در خانه عروس می‌فرستند. دلجویی از خانواده‌های عزادار که پیش از شب یلدا عزیزی را از دست داده‌اند، یکی دیگر از آداب و رسوم شب یلدا در مازندران است. در چنین مواردی بزرگان و بستگان درجه یک، شب چله را در نزد خانواده عزادار می‌گذرانند تا از آن‌ها دلجویی کنند. اگر دو خانواده قصد داشته باشند با هم وصلت کرده و دختر و پسرشان را عروس کنند، یکی از خوش‌یمن‌ترین شب‌ها را برای گذاشتن قول و قرار عقد و عروسی چله شو می‌دانند. در رامسر قدیم و برخی شهرها و روستاهای مازندران مراسم «قاشق زنی» برگزار می‌شود. در این مراسم زنان و دختران روی صورت خود چادر می‌اندازند و یک قاشق چوبی بزرگ یا ظرف در دست می‌گیرند و به درب خانه همسایه‌ها می‌روند. سپس قاشق خود را به سمت همسایه‌ها می‌گیرند و بدون این که حرف بزنند از او درخواست می‌کنند تا ظرف را با انواع تنقلات یلدایی پر کند. بعد از جمع شدن انواع تنقلات شب یلدا، این خوراکی‌ها را به فردی که لال است یا لکنت زبان دارد، می‌دهند تا درمان شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/12/sd-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (286, 'خورش مرغ ناردون مازندرانی', 'برای درست‌کردن خورش مرغ ناردونی، اول مرغ‌ها رو با نمک، فلفل سیاه، زردچوبه و زعفرون مزه‌دار کنین و بذارین بمونه تا طعم‌دار بشه. بعد توی قابلمه با روغن یا کره سرخ کنین تا طلایی بشه و کنار بذارین.', 'برای درست‌کردن خورش مرغ ناردونی، اول مرغ‌ها رو با نمک، فلفل سیاه، زردچوبه و زعفرون مزه‌دار کنین و بذارین بمونه تا طعم‌دار بشه. بعد توی قابلمه با روغن یا کره سرخ کنین تا طلایی بشه و کنار بذارین. پیاز نگینی رو با روغن تفت بدین، بعد سیر، ادویه‌ها و رب گوجه‌ فرنگی رو اضافه کنین و خوب تفت بدین تا رنگ بگیره. مرغ‌های سرخ‌ شده رو اضافه کنین و یه لیوان آب جوش بریزین. حرارت رو کم کنین تا بپزه. بعد از ۱۵ دقیقه، آب انار و رب انار رو اضافه کنین و بذارین خورش یه ساعت دیگه جا بیفته. در آخر، دونه‌های انار و زعفرون دم‌کرده رو بریزین و بذارین طعم‌ها خوب ترکیب بشن. این خورش خوش‌ عطر رو با برنج سفید یا زعفرونی سرو کنین و نوش جان کنین.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/11/khoresh-nardoon4-e1763542805636.webp', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (287, 'بورانی اسفناج', 'ابتدا اسفناج‌ها را بشویید و وقتی کاملا آب آن‌ها خشک شد درشت خرد کنید.', 'ابتدا اسفناج‌ها را بشویید و وقتی کاملا آب آن‌ها خشک شد درشت خرد کنید. برای پختن اسفناج‌ها نهایتا ½ فنجان آب نیاز دارید تا بخارپز شوند (اگر هم آب اضافه نکنید اتفاقی نمی‌افتد). کافی است که اسفناج‌ها نرم شوند. حالا سیرها را رنده کنید و به اسفناج در حال پخت اضافه کنید. وقتی آب اسفناج کاملا کشیده شد نمک و فلفل آن را اندازه کنید و از روی شعله گاز بردارید. اجازه دهید اسفناج‌ها خنک شوند و سپس با ماست مخلوط کرده و سرو کنید می‌توانید مقداری گردو خرد شده و در صورت تمایل کشمش هم به بورانی اسفناج اضافه کنید که در این صورت بورانی اسفناج تبریزی خواهید داشت.', 20, 40, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2021/05/16-1-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (288, 'کوکو سبزی مجلسی', 'برای درست کردن کوکو سبزی مجلسی خوشمزه با بافت پف‌دار تخم مرغ‌ها را در یک ظرف بشکنید و با زعفران دم کرده، سیر رنده شده، پیاز رنده شده و آب گرفته شده، پیاز داغ خشک، نمک و فلفل سیاه خوب هم بزنید. زرشک را', 'برای درست کردن کوکو سبزی مجلسی خوشمزه با بافت پف‌دار تخم مرغ‌ها را در یک ظرف بشکنید و با زعفران دم کرده، سیر رنده شده، پیاز رنده شده و آب گرفته شده، پیاز داغ خشک، نمک و فلفل سیاه خوب هم بزنید. زرشک را هم ۳۰ دقیقه در آب خیس کنید. بعد از این که تخم‌مرغ را با سایر مواد را کاملا هم زدید، سبزی کوکو را هم به مایه تخم مرغ اضافه کنید و باز هم بزنید تا سبزی و مایه تخم مرغ خوب با هم ترکیب شوند. سبزی‌ها باید به تخم مرغ آغشته باشند و مقدار تخم مرغ کم نباشد. حالا مغز گردو خرد شده و زرشک آبکش را اضافه کنید. مقداری روغن سرخ کردنی در ماهیتابه بریزید و صبر کنید داغ شود. روغن که داغ شد مایه کوکو را به آرامی در مرکز ماهیتابه بریزید یا مایه کوکو سبزی را با قاشق در ماهیتابه بریزید و کوکو سبزی را دانه دانه سرخ کنید تا هر دو طرف کوکو طلایی شود. اگر مایه کوکو سبزی را به صورت کامل در ماهیتابه ریختید، درب ماهیتابه را به همراه دم کنی به مدت ۱۵ دقیقه بگذارید و شعله گاز را ملایم کنید. بعد از ۱۵ دقیقه دم‌کنی را بردارید اگر دور کوکو طلایی شده بود می‌توانید کوکو را برش بزنید. بین برش‌ها را کمی باز کنید و به مقدار لازم روغن سرخ کردنی بریزید. ۱۵ دقیقه دیگر درب ماهیتابه را با دم‌کنی بگذارید. بعد از این زمان دوباره کوکو را چک کنید و اگر حسابی خودش را گرفته بود کوکو را برگردانید و اجازه دهید سمت دیگر کوکو به همین ترتیب سرخ شود. کوکو سبزی را با نان یا در کنار برنج سرو کنید. برای درست کردن کوکو سبزی در فر، فر را روشن و روی دمای ۲۰۰ درجه سانتی‌گراد گرم کنید. برای درست کردن کوکو سبزی در فر برای آماده کردن مایه کوکو سبزی دقیقا به روش بالا عمل کنید. سپس برای پختن و سرخ کردن کوکو سبزی کف قالب فر را چرب کنید و مایه کوکو را در قالب فر بریزید و با ته قاشق پهن کنید. مایه کوکو سبزی را ابتدا به مدت ۵ دقیقه در دمای ۲۰۰ درجه فر بپزید و سپس دمای فر را به ۱۷۰ درجه سانتی‌گراد برسانید تا کوکو حسابی در دمای کمتر مغز پخت شود. به محض این‌که بوی کوکو سبزی را حس کردید قالب را از فر بیرون بیاورید، روی کوکو سبزی ۲ قاشق غذاخوری روغن مایع بریزید و سپس گریل فر را روشن کنید. قالب را به فر برگردانید تا کوکو سبزی به مدت ۵ دقیقه دیگر گریل شوند. بعد از ۵ دقیقه یا کمتر (اگر احساس کردید روی کوکو پخته شده) قالب را از فر بیرون بیاورید و بلافاصله کوکو را در ظرف مورد نظر سرو کنید. برای تهیه کوکو سبزی با خمیر، از خمیر بربری استفاده می‌شود. خمیر را می‌توانید از نانوایی‌ها تهیه کنید. در این روش، مایه کوکو سبزی را مانند روش‌ بالا درست کنید. سپس روی یک تخته مقداری آرد بپاشید و خمیر را روی آن قرار دهید. نیمی از خمیر را با وردنه باز کنید تا نازک شود. سپس مایه کوکو سبزی را روی آن بریزید. نصف دیگر خمیر را هم جداگانه با وردنه باز کنید و روی مایه کوکو بیندازید. با چاقوی تیز، به اندازه دلخواه مایه کوکو با خمیر را برش دهید. برش‌های کوکو با خمیر را در روغن داغ یا سرخ کنید یا در قالب فر چرب شده بچینید و در فر سرخ کنید. بعد از پخت، کوکو سبزی با خمیر را سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/03/kuku1.webp', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (289, 'ماهی سفید شکم پر', 'در اولین مرحله ماهی را خوب بشویید و تمیز کنید. سپس مقداری نمک، فلفل سیاه، آب لیمو ترش تازه به ماهی بزنید تا مزه‌دار شود. برای نیم ساعت ماهی را به حال خود رها کنید.', 'در اولین مرحله ماهی را خوب بشویید و تمیز کنید. سپس مقداری نمک، فلفل سیاه، آب لیمو ترش تازه به ماهی بزنید تا مزه‌دار شود. برای نیم ساعت ماهی را به حال خود رها کنید. پیاز را رنده کنید و مقداری روغن در ماهیتابه بریزید و بعد از داغ شدن روغن، پیاز را به آن اضافه کنید. برای چند دقیقه پیاز را تفت دهید تا طلایی شود. سپس سبزیجات را به آن اضافه کنید و مواد را خوب هم بزنید تا کمی سرخ شوند. سیرها را با رنده ریز کنید و به سبزی و پیاز اضافه کنید. کمی نمک، فلفل و زردچوبه به مواد اضافه کنید و بعد از اینکه مواد را کمی تفت دادید، شعله گاز را خاموش کنید. گردوها را داخل غذاساز یا میکسر بریزید و کاملاً خرد کنید. سپس گردوی خرد شده را به مخلوط سبزی و پیاز اضافه کنید. نصف لیوان رب انار ملس را به این ترکیب اضافه کنید و مواد را خوب هم بزنید. مقدار کمی گلپر هم به مواد اضافه کنید و باز ترکیب را هم بزنید. طعم مایه آماده شده برای داخل شکم ماهی بچشید. اگر دوست دارید مواد داخل شکم ماهی شما ترش‌تر باشد، می‌توانید مقدار رب انار را دو برابر کنید. موادی را که برای داخل شکم ماهی تهیه کرده‌اید را داخل شکم ماهی جای دهید. برای بسته شدن شکم ماهی می‌توانید به کمک نخ و سوزن تمیز و استریل شده شکم ماهی را بدوزید و یا از چند خلال دندان استفاده کنید. برای سرخ کردن ماهی شکم پر می‌توانید در یک ماهیتابه بزرگ و مناسب به اندازه یک لیوان فرانسوی دسته‌دار روغن بریزید و اجازه دهید روغن کاملاً داغ شود. سپس ماهی را به آرامی داخل روغن قرار دهید. شعله را روی حرارت متوسط رو به زیاد تنظیم کنید. زمانی که یک سمت ماهی کاملاً طلایی شد، آن را با کمک دو کفگیر به آرامی برگردانید تا طرف دیگر ماهی هم سرخ شود. اگر دوست دارید ماهی شکم پر شما با روغن کمتر و در فر آماده شود، ابتدا باید فر را روشن کنید تا برای ۱۵ دقیقه خوب گرم شود. سپس یک ظرف مناسب فر انتخاب کنید و سطح آن را به خوبی با چند قاشق روغن چرب کنید. ماهی را داخل ظرف قرار دهید. دمای فر را روی ۱۸۰ درجه سانتی‌گراد تنظیم کنید. برای حدود ۴۵ دقیقه ماهی را در فر قرار دهید تا کاملاً مغز پخت و طلایی شود. در نهایت ماهی سفید شکم پر را با برش‌های لیمو و سبزی تازه تزئین کنید و همراه با برنج شمالی و زیتون پرورده نوش جان کنید و لذت ببرید.', 30, 60, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/80dd5872-ef81-408a-8d2c-5cb912a3166f.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (290, 'کدو پلو مازندرانی', 'برای تهیه کدو پلو مازندرانی، ابتدا برنج ایرانی را بشویید و با آب ولرم و نمک خیس کنید. در این فاصله، کدو حلوایی را پوست بگیرید و به قطعات مکعبی ۲ تا ۴ سانتی‌متری خرد کنید. پیاز را نگینی خرد کنید و سیر', 'برای تهیه کدو پلو مازندرانی، ابتدا برنج ایرانی را بشویید و با آب ولرم و نمک خیس کنید. در این فاصله، کدو حلوایی را پوست بگیرید و به قطعات مکعبی ۲ تا ۴ سانتی‌متری خرد کنید. پیاز را نگینی خرد کنید و سیر را له کنید. در یک قابلمه یا تابه، کمی روغن یا کره حیوانی بریزید و پیاز را تفت دهید تا طلایی شود. سپس سیر را اضافه کرده و یکی دو دقیقه تفت دهید تا عطر آن بلند شود. کدوهای خردشده را به پیاز و سیر اضافه کنید. کمی زردچوبه، نمک و فلفل سیاه بزنید و حدود ۱۰ تا ۱۵ دقیقه با حرارت ملایم تفت دهید تا کدوها نیم‌پز شوند. در آخر، زعفران دم‌کرده را اضافه کنید و اجازه دهید چند دقیقه دیگر کدوها روی حرارت ملایم بمانند. برنج خیس‌خورده را در آب جوش بریزید و کمی روغن اضافه کنید. وقتی دانه‌های برنج نیم­پز شدند (لایه رویی نرم و مغز آن کمی سفت باشد)، آبکش کنید. کف قابلمه کمی روغن یا کره حیوانی بریزید و ته­دیگ دلخواه (نان یا سیب­زمینی حلقه­ای) بچینید. یک لایه برنج روی ته­دیگ بریزید و روی آن مایه کدو را اضافه کنید. این کار را لایه به لایه تکرار کنید. لایه آخر را برنج بریزید. می­توانید لابه­لای هر لایه، کمی زعفران دم­کرده، کشمش یا خلال بادام هم اضافه کنید. درب قابلمه را با دم­کنی بگذارید و ابتدا با حرارت بالا اجازه دهید داخل قابلمه بخار بیفتد. بعد از اینکه بخار بلند شد، شعله را کم کنید و حدود ۴۵ دقیقه تا یک ساعت اجازه دهید پلو کدو به خوبی دم بکشد. هنگام سرو، می­توانید روی کدو پلو روغن یا کره بریزید و با کشمش یا خلال بادام تزئین و سرو کنید. در صورت تمایل می­توانید کنار کدو پلو بدون گوشت، چند تخم مرغ نیمرو کرده و روی پلو بگذارید. برای تهیه کدو پلو با مرغ: فیله­های مرغ را به قطعات نواری یا مکعبی خرد کنید. در تابه­ای جداگانه، مرغ­ها را با کمی نمک، فلفل سیاه، زردچوبه و زعفران دم­کرده تفت دهید تا طلایی و مزه­دار شوند. سپس مرغ­های پخته شده را لابه­لای برنج و مایه کدو در قابلمه بریزید. برای تهیه کدو پلو با گوشت چرخ­کرده: ۵۰۰ گرم گوشت چرخ­کرده را با نمک، فلفل، زردچوبه و کمی فلفل پاپریکا مزه­دار کنید. می­توانید گوشت را به صورت کوفته قلقلی درآورده و سرخ کنید، یا آن را با پیاز نگینی و سیر له­شده تفت دهید. سپس گوشت را لابه­لای برنج و کدو در قابلمه بریزید. برای تهیه کدو پلو به صورت کته­ای: برنج خیس­خورده را با آب تازه (حدود یک بند انگشت بالاتر از سطح برنج) و مخلوط کدو در قابلمه بریزید. اجازه دهید آب بخار شود و به خورد برنج برود. سپس درب قابلمه را با دم­کنی بگذارید و ابتدا با حرارت متوسط و سپس با حرارت کم، حدود ۳۰ تا ۴۵ دقیقه دم کنید.', 30, 60, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/11/kadupolo-1-2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (291, 'خورش کدو حلوایی با مرغ', 'کدو حلوایی را شسته، پوست و هسته آن را جدا کرده و به شکل دلخواه (نگینی، هلالی یا خلالی) خرد کنید. کدوها را با مقداری روغن سرخ کنید تا موقع پخت، شکل خود را حفظ کنند.', 'کدو حلوایی را شسته، پوست و هسته آن را جدا کرده و به شکل دلخواه (نگینی، هلالی یا خلالی) خرد کنید. کدوها را با مقداری روغن سرخ کنید تا موقع پخت، شکل خود را حفظ کنند. پیاز را به صورت نگینی یا خلالی خرد کنید و در روغن تفت بدهید. فیله مرغ خرد شده را با پیاز حدود ۲ دقیقه تفت بدهید. رب گوجه فرنگی را به این ترکیب اضافه کنید و تفت دهید تا طعم خامی آن گرفته شود. نمک، فلفل سیاه، شکر، زردچوبه و آلو بخارا را هم اضافه کنید و یک دقیقه تفت بدهید. به مواد داخل قابلمه، ۲ لیوان آب جوش اضافه کنید و صبر کنید گوشت مرغ و سایر مواد خورشت کدو حلوایی روی شعله ملایم گاز بپزند. بعد از ۳۰ دقیقه، کدوهای سرخ شده را به سایر مواد خورش اضافه کنید. ۱۰ دقیقه قبل از خاموش کردن اجاق گاز، آبلیمو و زعفران دم‌کرده را هم به خورش کدو حلوایی اضافه کنید. خورش کدو حلوایی یک خورشت کم آب و غلیظ است. این خورش خوشمزه را با برنج چلو میل کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/11/Tasty-chicken-and-pumpkin-stew-2-1-1024x652.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (292, 'خورش مرغ ترش مازندرانی', 'مرغ را با مقداری نمک و زردچوبه مزه‌دار کنید. مقداری روغن در ماهیتابه بریزید و صبر کنید تا روغن داغ شود. مرغ مزه‌دار شده را در روغن سرخ کنید و سپس مرغ‌ها را برگردانید تا طرف دیگر آن‌ها نیز به طور کامل', 'مرغ را با مقداری نمک و زردچوبه مزه‌دار کنید. مقداری روغن در ماهیتابه بریزید و صبر کنید تا روغن داغ شود. مرغ مزه‌دار شده را در روغن سرخ کنید و سپس مرغ‌ها را برگردانید تا طرف دیگر آن‌ها نیز به طور کامل سرخ شود. در این مدت که مرغ‌ها در حال سرخ شدن هستند، سبزی‌های مخصوص خورشت مرغ ترش را به صورت ریز خرد کنید. سپس در یک ماهیتابه دیگر مقداری روغن بریزید و سبزی‌ها را خوب سرخ کنید. برای سرخ کردن سبزی‌ها حتما از شعله ملایم استفاده کنید و مراقب باشید که سبزی‌ها نسوزند و سیاه نشوند. توجه داشته باشید که سبزی خورشت مرغ ترش نیاز به سرخ کردن طولانی ندارد و باید رنگ سبز خود را حفظ کند. مرغ‌های کاملا سرخ شده را از روغن خارج کنید. سپس پیاز را رنده کنید و در همان روغن بریزید و تفت دهید تا طلایی شود. بعد از این که پیاز در روغن سرخ شد، رب انار را اضافه کنید و چند دقیقه تفت دهید. در ادامه مغز گردوی آسیاب شده را نیز اضافه کنید و همه مواد را با هم مخلوط کنید و خوب در روغن تفت دهید. سبزی سرخ شده را به ترکیب پیاز و رب انار اضافه کنید و مواد را خوب هم بزنید. طعم این ترکیب را بچشید و در صورت نیاز کمی دیگر رب انار به مواد خورش اضافه کنید. مقدار رب اناری که به این غذا استفاده می‌کنید، کاملا سلیقه‌ای است. حالا باید ۱ و نیم لیوان آب جوش به قابلمه اضافه کنید و کمی نمک و فلفل سیاه نیز به خورشت بزنید. مرغ‌های سرخ شده را در قابلمه بیندازید و آن را با شعله ملایم روی گاز قرار دهید تا خورشت شما برای حدود ۱ ساعت بپزد. زمانی که مرغ به طور کامل پخت و آب خورشت کم شد، خورشت مرغ ترش مازندرانی شما آماده است. می‌توانید این خورشت خوشمزه مازندرانی را با برنج کته زعفرانی یا چلو آبکش میل کنید و از طعم آن لذت ببرید.', 10, 20, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2023/12/355.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (293, 'حلوای آغوز یا حلوا گردو شمالی (حلوا سیاه)', 'برای طرز تهیه حلوای اغوز مازندرانی، در مرحله اول برنج را پاک کنید و بشویید. چون برنج باید خرد شود، بهتر است از برنج نیم‌دانه استفاده کنید. برنج را ۲ تا ۳ بار بشویید و در یک ظرف با مقداری آب، به مدت حد', 'برای طرز تهیه حلوای اغوز مازندرانی، در مرحله اول برنج را پاک کنید و بشویید. چون برنج باید خرد شود، بهتر است از برنج نیم‌دانه استفاده کنید. برنج را ۲ تا ۳ بار بشویید و در یک ظرف با مقداری آب، به مدت حداقل ۲ ساعت بخیسانید. سپس آب برنج را خالی کنید و برنج‌های خیسانده شده را روی یک پارچه نخی تمیز بریزید و پهن کنید. برنج‌ها را در مجاورت نور خورشید یا هوای آزاد بگذارید تا خشک شوند و آب خود را از دست بدهند. حدود ۲۴ تا ۴۸ ساعت برنج‌ها را در هوای آزاد بگذارید تا کاملا حالت شکننده بگیرند. سپس آنها را در آسیاب کن برقی یا دستی بریزید و کمی خرد کنید. لازم نیست برنج‌ها پودر شوند. به روش سنتی، برنج‌ها در هاون کوبیده می‌شدند و دانه‌ها در حد خرد شدن، شکسته می‌شدند. در همین حد کافی است. برنج‌های آماده شده را کنار بگذارید. در این مرحله، مغزهای گردو را خرد کنید. لازم نیست گردوها پودر شوند، ولی باید ریز شوند. بهتر است ۲ لیوان را ریزتر و ۲ لیوان از گردو را کمی درشت‌تر خرد کنید. گردوها را هم کنار بگذارید. در ادامه، در یک ظرف، آب را بریزید و مقداری شکر به آن اضافه کنید. چون شیره خرمالو هم شیرین است، بهتر است شکر را زیاد در نظر نگیرید. شکر و آب را هم بزنید تا دانه‌های شکر در آب حل شوند. محلول آب و شکر را کنار بگذارید. در این مرحله از طرز تهیه حلوای آغوز شمالی، یک قابلمه را روی حرارت بگذارید. سپس گردوها را به قابلمه اضافه کنید. گردوها را بدون این که روغن به آن اضافه کنید، در قابلمه تفت دهید؛ در حدی که کمی روغن پس بدهند ولی نسوزند. مدام گردوهای خرد شده را هم بزنید تا رنگشان کمی تیره شود. بعد نوبت به اضافه کردن برنج خرد شده است. چند دقیقه دیگر هم برنج را با گردوها، تفت دهید. شیره خرمالو را هم اضافه کنید و بعد محلول آب و شکر را بیفزایید. همه مواد را هم بزنید تا کاملا یکدست شود. در تمام مدتی که مایه حلوا آغوزه روی حرارت است، آن را مدام هم بزنید تا ته نگیرد. در این مدت حرارت را روی حالت خیلی ملایم تنظیم کنید. ممکن است بیش از ۱ ساعت طول بکشد تا گردوها روغن بیندازند و حلوا جمع شود. حلوای آغوز که به اندازه کافی غلیظ شد، حرارت را قطع کنید. در آخرین مرحله از طرز تهیه حلوای آغوز مازندرانی، حلوا را در ظرف سرو بریزید و با قاشق آن را صاف کنید. می‌توانید حلوا را قالب هم بزنید. یا حلوا را در قالب دلخواه بریزید و اجازه دهید سرد شود، بعد حلوا را از قالب خارج کنید. یا حلوا را در یک سینی بریزید و سطح آن را با پشت قاشق صاف کنید. روی حلوا را با چاقو برش‌هایی ایجاد کنید. بعد حلوا را در یخچال بگذارید تا خودش را بگیرد. روی حلوا را با مواد دلخواه مثل انواع مغزهای خرد شده یا پودر نارگیل، تزیین و آن را سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/%D8%AD%D9%84%D9%88%D8%A7-%DA%AF%D8%B1%D8%AF%D9%88%DB%8C%DB%8C-%D8%B1%DA%98%DB%8C%D9%85%DB%8C.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (294, 'آش کلم برگ تبریزی', 'برای درست کردن آش کلم برگ تبریزی ابتدا حبوبات و بلغور جو یا گندم را از شب قبل خیس کنید و چند بار آب آن‌ها را عوض کنید. یک ساعت قبل از پخت آش برنج نیم دانه را هم خیس کنید.', 'برای درست کردن آش کلم برگ تبریزی ابتدا حبوبات و بلغور جو یا گندم را از شب قبل خیس کنید و چند بار آب آن‌ها را عوض کنید. یک ساعت قبل از پخت آش برنج نیم دانه را هم خیس کنید. برای پخت آش کلم برگ تبریزی ابتدا نخودها را بپزید. اواسط پخت نخود وقتی که نخود نیم‌پز شد، لوبیا را هم اضافه کنید. لوبیا هم که نیم‌پز شد بلغور جو یا گندم را اضافه کنید. در این مرحله درب قابلمه را نیمه باز بگذارید چون بلغور سر می‌رود. وقتی بلغور پخت حالا برنج نیم دانه را هم اضافه کنید تا بپزد و به اصطلاح شکفته شود. در این مرحله کلم برگ را بشورید و خرد کنید و به آش که تمام بار و بنشن آن پخته شده اضافه کنید. کلم زود می‌پزد و له می‌شود. وقتی که کلم پخت، نمک، فلفل سیاه، زردچوبه و سبزی آش را هم اضافه کنید و صبر کنید تا آش خوب بپزد و جا بیفتد. در این مرحله پیازها را خرد کنید و پیاز داغ درست کنید و کشک را آماده کنید. نیمی از پیازداغ را به آش در حال پخت اضافه کنید و نیمی دیگر را برای تزیین آش کلم نگه دارید. حین پخت آش مقداری کشک هم می‌توانید به آن اضافه کنید. آش کلم برگ جا افتاده را در ظرف مورد نظر سرو کنید و با کشک، پیازداغ و نعنا داغ تزیین کنید. برای درست کردن آش کلم به روش تبریزی باید از ترکیب تره، جعفری، گشنیز و اسفناج و در صورت تمایل کمی ترخون استفاده کنید. برای مزه‌دار کردن آش کلم برگ تبریز می‌توانید از مقداری فلفل سیاه، پودر سیر، کشک، نعنا داغ، زردچوبه و کمی آب قلم استفاده کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/10/2344.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (295, 'آش خشیل آذری', 'در اولین مرحله از طرز تهیه خشیل ترکی، بلغور گندم را در یک ظرف آب بریزید و برای چند ساعت بخیسانید. سپس آن را بپزید تا آب بلغور کشیده شود و خوب نرم شود. معمولا پختن بلغورها حدود ۱ ساعت طول می‌کشد.', 'در اولین مرحله از طرز تهیه خشیل ترکی، بلغور گندم را در یک ظرف آب بریزید و برای چند ساعت بخیسانید. سپس آن را بپزید تا آب بلغور کشیده شود و خوب نرم شود. معمولا پختن بلغورها حدود ۱ ساعت طول می‌کشد. در این مرحله در یک ظرف ۴ قاشق آرد را با ۴ لیوان آب سرد ترکیب کنید. سپس ترکیب آب و آرد را همراه با بلغور گندم پخته شده در قابلمه بریزید و روی حرارت ملایم قرار دهید. اجازه دهید ترکیب آرد، آب و بلغور با شعله ملایم بجوشد و غلیظ شود. در این مدت باید مدام مواد را هم بزنید تا ته نگیرند و بوی خامی آرد به طور کامل از بین برود. در صورت نیاز می‌توانید از ۱ تا ۲ قاشق غذاخوری دیگر آرد برای افزایش غلظت آش استفاده کنید. غلظت آش خشیل باید از حلیم غلیظ‌تر شود. بعد از این که آش خشیل ترکی به غلظت مناسب رسید، شعله گاز را خاموش کنید و خشیل را در ظرف مورد نظرتان بریزید. وسط آش را گود کنید و مقداری شیر و عسل روی آن بریزید. سپس مقداری کره آب شده به آش اضافه کنید. مقدار کره‌ای به این آش اضافه می‌شود، کاملا به سلیقه شما بستگی دارد. برای تهیه خشیل ساده بدون بلغور، بلغور گندم را حذف کنید و این آش را تنها با استفاده از آرد گندم تهیه کنید. در اولین مرحله از طرز تهیه آش خشیل بدون بلغور، آرد گندم را در ماهیتابه تفت دهید تا بوی خامی آن از بین برود. سپس مقداری شیر و آب به آرد اضافه کنید و آن را خوب هم بزنید. اجازه دهید ترکیب آرد، شیر و آب برای چند دقیقه بجوشد و در این مدت مدام آش را هم بزنید تا ته نگیرد و غلیظ شود. در انتها زمانی که خشیل به غلظت مناسب رسید، شعله گاز را خاموش کنید و خشیل آذری را در ظرف مورد نظرتان بریزید. در ادامه وسط خشیل را کمی گود کنید و کمی شیر، عسل یا شکر روی آن بریزید. مقداری کره روی آش قرار دهید و بعد آن را میل کنید. برای این که آش شما مقوی‌تر و خوشمزه‌تر شود، می‌توانید از مقداری مغز گردوی خرد شده نیز استفاده کنید. برای تهیه آش خشیل با زردچوبه، نصف قاشق مرباخوری زردچوبه به ترکیب آرد، شیر و آب اضافه کنید. رنگ این آش برخلاف آش خشیل معمولی، زرد است و مزه آن هیچ تفاوتی با آش خشیل بدون زردچوبه ندارد. برای مزه‌دار کردن آش خشیل آذری می‌توانید در زمان پخت یا بعد از آماده شدن، مقداری دارچین به آش اضافه کنید. در طرز تهیه خشیل اردبیل می‌توانید از هر نوع کره‌ای استفاده کنید، اما اضافه کردن کره محلی به این آش طعم بسیار لذیذتری می‌بخشد. بعد از اینکه آش خشیل آماده شد می‌توانید مقداری شیره خرما یا شیره انگور روی آن بریزید و کمی دارچین روی آش بپاشید. در ادامه می‌توانید مغز گردو را هم خرد کنید روی آش بریزید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/11/233-3.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (296, 'آش انار تبریزی', 'در اولین مرحله، لپه‌ها را پاک کنید و در یک ظرف بریزید. روی لپه‌ها را با مقداری آب بپوشانید و از شب قبل یا حداقل ۲ ساعت پیش از شروع آشپزی، آن‌ها را بخیسانید. در این مدت چند بار آب لپه‌ها را عوض کنید. ب', 'در اولین مرحله، لپه‌ها را پاک کنید و در یک ظرف بریزید. روی لپه‌ها را با مقداری آب بپوشانید و از شب قبل یا حداقل ۲ ساعت پیش از شروع آشپزی، آن‌ها را بخیسانید. در این مدت چند بار آب لپه‌ها را عوض کنید. برنج را هم از نیم ساعت قبل با مقداری آب بخیسانید. سبزی آش را هم تمیز کرده، بشویید و آبکش کنید. سپس آنها را به صورت ساطوری ریز خرد کنید و کنار بگذارید. در این مرحله، بعد از اینکه لپه‌ها خیسانده شدند، آنها را آبکش کنید و در یک قابلمه بریزید. روی لپه‌ها مقداری آب بریزید و روی حرارت قرار دهید تا زمانی که لپه‌ها کاملا بپزند. سپس آنها را کنار بگذارید. یک پیاز را پوست بگیرید و به صورت نگینی خرد کنید. در یک تابه مقداری روغن بریزید و اجازه دهید پیازها، سبک و طلایی شوند. سپس گوشت چرخ کرده را اضافه کنید و تفت دهید تا رنگ آن تغییر کند. بعد از اینکه گوشت برای چند دقیقه تفت خورد، زردچوبه، نمک و فلفل سیاه را اضافه کنید تا گوشت طعم بگیرد. در این مدت، حرارت را کم کنید و برای ۱۰ دقیقه اجازه دهید گوشت با ادویه‌ها بپزد و بوی زهم آن گرفته شود. سپس سبزی آش را اضافه کنید. کمی سبزی آش را با گوشت چرخ کرده، تفت دهید. در این مرحله، لپه پخته شده را به آش اضافه کنید. سپس برنج خیسانده شده را آبکش کرده و به مواد آش بیفزایید. به اندازه نصف لیوان آب به آش اضافه کرده و صبر کنید به مدت ۱۵ دقیقه مواد آش کمی نرم‌تر شوند. بعد از نرم شدن سبزیجات و برنج، آب انار را به همراه ۱ قاشق رب انار به آش اضافه کنید. حرارت را بالا ببرید تا آب انار شروع به جوشیدن کند. سپس حرارت را کم کنید و درب قابلمه را نیمه باز بگذارید. به مدت ۲ ساعت صبر کنید تا آش جا بیفتد و قوام پیدا کند. پیازهای باقی‌مانده را پوست بگیرید و خلالی باریک خرد کنید. در یک تابه مقداری روغن بریزید و پیازها را روی حرارت متوسط مدام تفت دهید تا سرخ شوند. در آخر می‌توانید کمی زردچوبه هم اضافه کنید تا طلایی‌تر شوند. سپس پیازها را بیرون بیاورید و نعناع خشک را اضافه کنید و با روغن چند ثانیه تفت دهید. نعناع داغ و پیاز داغ را برای تزیین کنار بگذارید. در آخرین مرحله، بعد از اینکه آش به قوام کافی رسید، طعم آن را بچشید و مزه‌ها را اندازه کنید. در صورت دلخواه از چاشنی‌های مختلف می‌توانید استفاده کنید. بعد از آماده شدن آش، آن را در ظرف مناسب سرو بریزید و روی آن را با پیاز داغ، نعناع داغ و دانه‌های انار، تزیین و سرو کنید. نوش جان!', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/287.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (297, 'دسر پالدا تبریزی', 'در اولین مرحله، نشاسته ذرت را در یک لیوان آب سرد بریزید و هم بزنید. سپس، نشاسته را چند دقیقه کنار بگذارید تا ناخالصی‌های آن جدا شده و روی آب جمع شود.', 'در اولین مرحله، نشاسته ذرت را در یک لیوان آب سرد بریزید و هم بزنید. سپس، نشاسته را چند دقیقه کنار بگذارید تا ناخالصی‌های آن جدا شده و روی آب جمع شود. آب کدر روی نشاسته را خالی کنید. این کار را ۲ بار دیگر هم تکرار کنید تا همه آلودگی‌ها و ناخالصی‌های نشاسته برطرف شود. با انجام این کار، دسر شفاف‌تر شده و بوی ناخوشایندی نخواهد داشت. پس از شستن نشاسته و اطمینان از خلوص آن، قابلمه مناسبی را روی حرارت ملایم بگذارید. حالا، یک لیوان شیر و یک و نیم لیوان آب را در قابلمه بریزید. نشاسته شسته شده را نیز به ترکیب آب و شیر اضافه کنید. این ترکیب را هم بزنید و ۱۰ الی ۱۵ دقیقه منتظر بمانید تا مایع داخل قابلمه کم کم بجوشد. توجه داشته باشید، از وقتی نشاسته را اضافه می‌کنید باید محتویات داخل قابلمه را مرتبا هم بزنید تا مایه دسر به طور یکنواخت و تدریجا غلیظ شود و ته نگیرد. پس از گذشت ۵ دقیقه از جوشیدن مایع درون قابلمه، شکر را اضافه کنید. محتویات داخل قابلمه را به طور مرتب برای ۱۰ دقیقه دیگر هم بزنید تا نشاسته کاملا بپزد و در عین حال، ته نگیرد. به این ترتیب، مایه دسر به طور کامل غلیظ شده و طعم خامی نشاسته برطرف می‌شود. پس از آن‌که غلظت دسر شبیه فرنی شد، گلاب را نیز در قابلمه بریزید و مایه دسر را برای یک دقیقه دیگر هم بزنید. سپس، حرارت را خاموش کنید و قابلمه را از روی حرارت بردارید. در این مرحله، باید سطح داخلی قالب مورد نظرتان را با مقدار کمی کره چرب کنید. سپس، مایه دسر را در حالی که هنوز گرم است در قالب بریزید. قالب را حداقل برای یک ساعت در دمای محیط بگذارید تا خنک شود. پس از خنک شدن دسر، آن را برای ۲ الی ۳ ساعت در یخچال قرار دهید تا کاملا خودش را بگیرد. در صورت تمایل، می­توانید این دسر را به صورت قالبی درست نکرده و در عوض، داخل پیاله­های تک نفره کوچک یا جام سرو کنید. پس از گذشت این مدت، دسر پالدا را از یخچال بیرون بیاورید و از قالب خارج کرده یا در همان ظروف تک نفره سرو کنید. برای تزیین پالدا می­توانید از خامه فرم گرفته قنادی، مغز گردوی آسیاب شده، پودر پسته یا گل محمدی استفاده کنید.', 13, 27, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/11/%D9%BE%D8%A7%D9%84%D8%AF%D8%A7-2-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (298, 'زیر برنجی (چلو گوشت لری)', 'گوشت را بشویید و به قطعات درشت خرد کنید. پیاز را پوست بگیرید و به ۸ قسمت خرد کنید. قابلمه را روی حرارت بگذارید و گوشت و پیاز را به قابلمه اضافه کنید. حرارت را کم کنید و درب قابلمه را بگذارید تا گوشت آ', 'گوشت را بشویید و به قطعات درشت خرد کنید. پیاز را پوست بگیرید و به ۸ قسمت خرد کنید. قابلمه را روی حرارت بگذارید و گوشت و پیاز را به قابلمه اضافه کنید. حرارت را کم کنید و درب قابلمه را بگذارید تا گوشت آب بیندازد. بعد از اینکه گوشت تغییر رنگ داد، زردچوبه، چوب دارچین و فلفل سیاه را اضافه کنید. ۲ تا ۳ دقیقه صبر کنید تا ادویه‌ها به خورد گوشت بروند. سپس ۳ تا ۴ لیوان آب جوش به قابلمه اضافه کنید. درب قابلمه را ببندید و اجازه دهید گوشت به مدت ۳ ساعت روی شعله ملایم بپزد و نرم شود. برنج را بشویید و به مدت ۳۰ دقیقه در مقداری آب نمک بخیسانید. بعد از اینکه گوشت نرم شد و پخت، در ۱۵ دقیقه پایانی پخت مقداری نمک به همراه آبلیمو و زعفران دم کرده اضافه کنید و دوباره درب قابلمه را ببندید تا چاشنی‌ها به خورد گوشت بروند. در یک قابلمه تا نیمه آب بریزید و روی حرارت بگذارید تا شروع به جوشیدن کند. برنج را از آب نمک آبکش کنید و در قابلمه بریزید تا شروع به جوشیدن کند. حدود ۷ تا ۱۰ دقیقه (بسته به نوع برنج) آن را بجوشانید تا لایه روی برنج نرم و لایه داخلی آن سفت باشد. سپس برنج را آبکش کنید. کف قابلمه کمی روغن و نمک بریزید. می‌توانید نصف قاشق زعفران دم کرده یا کمی زردچوبه هم اضافه کنید تا رنگ ته‌دیگتان خوشرنگ شود. سپس ته دیگ دلخواهتان را کف قابلمه بگذارید (از ورقه‌های نازک سیب زمینی یا نان لواش می‌توانید استفاده کنید). سپس برنج آبکش شده را در قابلمه بریزید و درب قابلمه را بگذارید. وقتی بخار داخل قابلمه پیچید، حرارت را کم کنید. حدود ۴۵ دقیقه صبر کنید تا برنج دم بکشد و آماده شود. زرشک را بشویید و آبکش کنید. در یک تابه کمی روغن یا کره بریزید و نصف قاشق چای‌خوری شکر اضافه کنید. بعد از داغ شدن روغن یا کره، زرشک‌ها را به مدت چند ثانیه در روغن تفت دهید و شعله را خاموش کنید. وقتی برنج دم کشید و گوشت‌ها پختند، حرارت را خاموش کنید. روی برنج کمی کره یا روغن حیوانی بریزید و به آرامی هم بزنید. گوشت‌ها را از قابلمه بیرون بیاورید و در سینی سرو بگذارید. آب یا عصاره گوشت را از یک صافی رد کنید تا پیازها و چوب دارچین جدا شود. عصاره باقیمانده را در یک کاسه بریزید. روی گوشت‌های داخل سینی را با برنج و زرشک بپوشانید. می‌توانید مقداری از برنج را به زعفران آغشته کرده و روی برنج سفید بریزید. چلوگوشت لری را به همراه سالاد دلخواه یا انواع ترشی سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/19a8b8cd-c4a0-42b1-baaa-2f9f62dfbc85.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (299, 'خاگینه خرما', 'در اولین مرحله از طرز تهیه خاگینه زنجانی باید هسته خرماها را جدا کنید و به وسیله چاقو هر خرما را به ۴ تکه مساوی تقسیم کنید.', 'در اولین مرحله از طرز تهیه خاگینه زنجانی باید هسته خرماها را جدا کنید و به وسیله چاقو هر خرما را به ۴ تکه مساوی تقسیم کنید. مقداری کره داخل ماهیتابه بریزید و اجازه دهید با حرارت ملایم گرم شود. سپس خرما را به آن اضافه کنید و بگذارید برای چند دقیقه نرم شود. دارچین و زنجبیل و گردوی خرد شده را به آن اضافه کنید و مواد را ۲ دقیقه تفت دهید. تخم مرغ‌ها را داخل یک کاسه بشکنید و با هم زن دستی یا چنگال به خوبی بزنید و مقداری نمک به آن اضافه کنید. سپس خرماها را در کف ماهیتابه پخش کنید و سپس تخم مرغ‌ها را به آن اضافه کنید. مطمئن شوید که خرما به طور یکنواخت در همه بخش‌های ماهیتابه پخش شده است. سپس درب ماهیتابه را بگذارید و اجازه دهید تخم مرغ‌ها برای چند دقیقه خوب پخته شوند. بعد از پخته شدن تخم مرغ‌ها، خاگینه خرمای مقوی و خوشمزه را در ظرف مناسب سرو کنید و چند قطره عسل و مقداری پودر دارچین روی آن بریزید و میل کنید. در اولین مرحله از طرز تهیه خاگینه خرما باید خرماها را از وسط نصف و به دو تکه مساوی تقسیم کنید و یا اگر دوست داشته باشید می‌توانید از خرمای خرد شده استفاده کنید. سپس خرما، دارچین، زنجبیل و ۲ قاشق آب را داخل ماهیتابه بریزید و هم خوب هم بزنید و برای ۵ دقیقه درب ماهیتابه را ببندید تا خرما کمی نرم شود. سپس بعد از بخار شدن آب، کمی کره به آن اضافه کنید و اجازه دهید مواد ۲ دقیقه با کره تفت بخورند. برای طرز تهیه خاگینه خرما مجلسی پیازها را به صورت خلالی خرد کنید و در یک ماهیتابه جدا با مقداری روغن سرخ کنید. مقداری نمک، فلفل سیاه و زردچوبه به آن اضافه کنید و اجازه دهید پیازها طلایی شوند. بعد پیازها را در ظرف جداگانه کنار بگذارید. کشمش پلویی، خلال پسته و بادام را کمی با کره تفت دهید. تخم مرغ‌ها را در یک کاسه کوچک با نمک و شیر ترکیب کنید و با چنگال خوب هم بزنید. سپس خرماها را داخل ماهیتابه پخش کنید، کشمش و خلال پسته و بادام را هم داخل ماهیتابه بریزید و ترکیب تخم مرغ‌های هم‌زده را به آن اضافه کنید. در آخرین مرحله از طرز تهیه خاگینه خرما باید درب ماهیتابه را بگذارید تا تخم مرغ به خوبی پخته شود و مقداری زعفران روی تخم مرغ‌ها بریزید تا رنگ بگیرند. سپس خاگینه خرمای مقوی و خوشمزه را از وسط تا کنید و با دارچین تزئین کنید و نوش جان کنید. می‌توانید خاگینه خرما مجلسی را به صورت خاگینه خرما رولتی هم آماده کنید. برای طرز تهیه خاگینه خرما رولتی مجلسی ابتدا تخم مرغ زده شده را با کمی آرد و شیر ترکیب کنید و داخل ماهیتابه بریزید و وقتی که کمی خودش را گرفت خرما و گردوی خرد شده را که با ادویه‌ها ترکیب شده است روی آن پخش کنید. سپس تخم مرغ را به آرامی رول کنید و اجازه دهید به خوبی پخته شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/11/1-10.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (300, 'آش لبو', 'برای طرز تهیه این آش لبو یا آش چغندر قرمز در ابتدای کار نخود، لوبیا قرمز و عدس را هر کدام به صورت جدا از شب قبل خیس کنید و چندین بار آب آنها را عوض کنید تا نفخ‌شان گرفته شود. سپس هر کدام را به صورت جد', 'برای طرز تهیه این آش لبو یا آش چغندر قرمز در ابتدای کار نخود، لوبیا قرمز و عدس را هر کدام به صورت جدا از شب قبل خیس کنید و چندین بار آب آنها را عوض کنید تا نفخ‌شان گرفته شود. سپس هر کدام را به صورت جدا با کمی آب بپزید و در ظرف جدایی قرار دهید. در ظرفی دیگر، لبوها را بشویید و با کمی آب بپزید. بعد از اینکه لبوها کاملا پخته و خنک شدند، آن‌ها را پوست بگیرید و به صورت نگینی خرد یا رنده کنید. در یک قابلمه متوسط حبوبات و لبوی پخته را همراه با آبِ لبو بریزید. بگذارید مواد آش به همراه آبِ لبو چند قل بخورد تا خوب جا بیفتد. جعفری و گشنیز خرد شده را اضافه کنید. در مرحله آخر هم رشته آش و آرد را به مواد اضافه کنید و اجازه دهید همه مواد با هم بپزند و آش لبو جا بیفتد و لعاب بیندازد. برای تزیین روی آش لبو یا آش چغندر قرمز از نعناع داغ استفاده کنید. در صورت تمایل روی آش را با پیاز داغ هم تزیین کنید و مقداری کشک یا قره قروت به آش اضافه کنید.', 65, 130, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/10/russian-beet-and-bean-soup-1456777585.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (301, 'پلو ماش و کلم قمری', 'برای تهیه پلو ماش و کلم قمری اصفهانی، ابتدا برنج شسته شده را با آب و نمک برای ۱ الی ۲ ساعت بخیسانید.', 'برای تهیه پلو ماش و کلم قمری اصفهانی، ابتدا برنج شسته شده را با آب و نمک برای ۱ الی ۲ ساعت بخیسانید. پیازهای شسته شده و بدون پوست را رنده کنید و با دست فشار دهید تا آب اضافی پیاز گرفته شود و تفاله پیاز رنده شده باقی بماند. سبزی پلویی تازه شامل تره، جعفری، شوید، گشنیز و تره سیر را پاک کرده، بشویید و نسبتا درشت ساطوری کنید. در صورت نبود سبزی تازه، از سبزی منجمد یا خشک (پس از آماده‌سازی) استفاده کنید. اگر از سبزی خشک استفاده می‌کنید، آن را به مدت ۱ الی ۲ ساعت در آب بخیسانید تا نرم شود و سپس آب اضافی آن را بگیرید. گوشت چرخ کرده را همراه با تفاله پیاز، نمک، فلفل سیاه و زردچوبه در ظرف مناسبی بریزید. مواد را به خوبی مخلوط کنید و ورز دهید تا مایه منسجم و یکنواختی ایجاد شود. با استفاده از این مایه گوشتی، گلوله‌هایی به اندازه گردو تهیه کنید. مقداری روغن را در تابه مناسبی بریزید و روی حرارت متوسط بگذارید تا داغ شود. کوفته قلقلی‌ها را در روغن داغ سرخ کنید تا طلایی شوند. پس از سرخ کردن کوفته قلقلی‌ها، کلم قمری شسته شده را پوست بگیرید و به صورت نگینی یا خلالی برش بزنید. کلم قمری خرد شده را همراه با مقدار کمی نمک، فلفل و زردچوبه تفت دهید تا کلم‌ها مزه‌دار و نرم شوند. تکه‌های کلم قمری تفت خورده را از تابه خارج کنید و کنار بگذارید. ماش پاک شده را به خوبی بشویید و با ۴ لیوان آب در قابلمه مناسبی بریزید. این قابلمه را روی حرارت بگذارید تا ماش برای ۲۰ دقیقه بجوشد و نیم‌پز شود. در صورت تمایل، در دقایق پایانی می‌توانید مقدار کمی نمک هم به این قابلمه اضافه کنید تا ماش طعم بگیرد. سپس، ماش نیم‌پز را در سبد بریزید تا آب اضافی خودش را از دست بدهد. سبزی پلویی ساطوری شده را در همان تابه‌ای بریزید که کلم‌ها را سرخ کردید. در صورت لزوم، مقداری روغن هم به این تابه اضافه کنید و سبزی پلویی را به مدت ۵ دقیقه تفت دهید تا عطر آن خارج شود و رنگ سبزی‌ها کمی تغییر کند. سپس، حرارت را خاموش کنید و سبزی پلویی تفت داده شده را نیز کنار بگذارید. قابلمه مناسبی را تا نیمه پر از آب کنید و روی حرارت متوسط بگذارید تا بجوشد. به اندازه یک قاشق مرباخوری نمک و کمی روغن هم به این قابلمه اضافه کنید. آب برنج خیس خورده را خالی کنید و خود برنج را در قابلمه در حال جوش بریزید تا برنج حدود ۷ الی ۱۰ دقیقه بجوشد و دندان‌گیر و قد کشیده شود. پس از آن، برنج نیم‌پز را در سبد بریزید و آبکشی کنید. مقداری روغن را در قابلمه مناسبی بریزید و روی حرارت بگذارید تا داغ شود. کف این قابلمه را با ته‌دیگ دلخواه بپوشانید. سپس، روی ته‌دیگ را با لایه‌هایی از برنج نیم‌پز، ماش پخته شده، کلم تفت داده شده، سبزی تفت خورده و گوشت‌های قلقلی سرخ شده پوشش دهید. در صورت تمایل، مقداری پودر دارچین و زعفران دم کرده را نیز بر روی لایه‌ها بریزید. درب قابلمه را با دم‌کنی بپوشانید و پلو را حدود ۴۵ الی ۶۰ دقیقه روی حرارت ملایم دم کنید. برای بهتر شدن نتیجه کار، پیشنهاد می‌کنیم از شعله پخش‌کن هم استفاده کنید. پس از گذشت این مدت، پلو ماش و قمری را در ظرف سرو بکشید و همراه با ماست، انواع سالاد، انواع ترشی و سبزی خوردن سرو کنید. شما می‌توانید این قاطی پلوی خوشمزه را با انواع مغزیجات مانند گردو و خلال بادام و خلال پسته تفت داده شده در روغن یا کره، با چند عدد کوفته قلقلی، پیاز داغ و کشمش پلویی تفت داده شده تزیین کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/04/%D9%BE%D9%84%D9%88-%D9%85%D8%A7%D8%B4-%D9%88-%DA%A9%D9%84%D9%85-%D9%82%D9%85%D8%B1%DB%8C-2-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (302, 'آش کدو حلوایی اصفهان', 'در اولین مرحله، حبوبات مختلف شامل نخود، لوبیا چیتی، ماش، لوبیا سفید و بلغور گندم را پاک کرده و سپس در آب حداقل به مدت ۲ ساعت بخیسانید تا نفخ آنها از بین برود و راحت‌تر پخته شوند.', 'در اولین مرحله، حبوبات مختلف شامل نخود، لوبیا چیتی، ماش، لوبیا سفید و بلغور گندم را پاک کرده و سپس در آب حداقل به مدت ۲ ساعت بخیسانید تا نفخ آنها از بین برود و راحت‌تر پخته شوند. یک عدد کدو حلوایی متوسط را بشویید، پوست و تخم‌های آن را جدا کنید. سپس کدو حلوایی را به قطعات مکعبی کوچک خرد کنید. سبزی آش (تره، جعفری، گشنیز و اسفناج) را پاک کرده و در آب و نمک چند دقیقه بخیسانید تا ضدعفونی شوند. سپس آبکشی کرده و ریز خرد کنید. در یک قابلمه کوچکتر، بلغور گندم را با حدود چهار لیوان آب روی حرارت متوسط قرار دهید تا به جوش بیاید. پس از جوش آمدن، حرارت را کم کرده و اجازه دهید تا بلغور گندم کمی نرم و نیم‌پز شود. در این فاصله چند بار هم بزنید تا ته نگیرد. بعد از نیم‌پز شدن بلغور گندم، آن را آبکش کنید. حبوبات خیسانده شده را نیز آبکش کرده و همه مواد را به آب در حال جوش در یک قابلمه بزرگتر اضافه کنید. حرارت را کم کرده، درب قابلمه را ببندید و اجازه دهید حبوبات کاملا بپزند و نرم شوند. سه عدد پیاز متوسط را پوست بگیرید، بشویید و به صورت خلالی درشت خرد کنید. در یک تابه با مقدار کافی روغن داغ، پیازها را تفت دهید تا کاملا طلایی شوند. حدود یک سوم پیاز داغ را برای تزئین آش کنار بگذارید. تکه‌های کدو حلوایی خرد شده را به پیاز داغ باقی‌مانده در تابه اضافه کنید و تفت دهید تا آب میان بافتی کدو حلوایی کشیده شود و کمی نرم شده و از حالت خامی در بیاید. زمانی که حبوبات پختند و کاملا نرم شدند، مخلوط کدو حلوایی و پیاز داغ را به همراه سبزی آش به قابلمه اضافه کنید. سپس نمک، فلفل سیاه، زردچوبه و دارچین را نیز اضافه کرده و یک بار همه مواد را با هم بزنید. درب قابلمه را بگذارید و به مدت نیم ساعت دیگر روی حرارت ملایم قرار دهید تا آش کدو حلوایی کاملا جا بیفتد. برای تهیه نعنا داغ، یک تابه کوچک را روی حرارت قرار دهید و در آن روغن و نعنا خشک بریزید. نعنا خشک را تفت دهید تا عطر آن بلند شود. مراقب باشید روی حرارت ملایم این کار را انجام دهید و زود از روی حرارت بردارید، چون نعنا خشک خیلی زود می‌سوزد. بعد از آنکه آش کدو حلوایی کاملا پخت و جا افتاد، آن را از روی حرارت بردارید و در یک کاسه گود بریزید. آش کدو حلوایی اصفهانی را با پیاز داغ و نعنا داغ تزیین کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/11/200320_594.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (303, 'خورش سیر قلیه گیلانی با مرغ', 'لپه‌ها را از شب قبل بخیسانید و آب آن‌ها را چند بار عوض کنید. سپس لپه‌ها را به صورت جداگانه بپزید اما مراقب باشید له نشوند.', 'لپه‌ها را از شب قبل بخیسانید و آب آن‌ها را چند بار عوض کنید. سپس لپه‌ها را به صورت جداگانه بپزید اما مراقب باشید له نشوند. برگ‌های سیر را شسته، آب آن‌ها را بگیرید و ریز خرد کنید (اندازه سبزی خورشتی). پیاز را خلالی خرد کرده و کمی تفت دهید تا سبک شود. برگ سیرهای خرد شده را به پیاز اضافه کرده و هر دو را با هم سرخ کنید تا سبک و طلایی شوند. مراقب باشید برگ‌های سیر نسوزد. بعد از سرخ شدن سیر و پیاز، تخم‌مرغ‌ها را اضافه کنید و اجازه دهید کمی خودشان را بگیرند، سپس هم بزنید و همراه با سیر و پیاز سرخ کنید. لپه‌های پخته شده را به ترکیب سیر، پیاز و تخم‌مرغ اضافه کنید. مرغ را به صورت جداگانه همراه با کمی زردچوبه، نمک، فلفل و یک عدد پیاز بپزید. اگر از مرغ کوچک یا اردک استفاده می‌کنید، می‌توانید بعد از شستن و تمیز کردن به صورت درسته آن را بپزید. ابتدا مرغ را با آب خالی بپزید، وقتی آب به جوش آمد کف روی آب مرغ را بگیرید و بگذارید دوباره مرغ همراه با پیاز، فلفل، زردچوبه و نمک بپزد. وقتی مرغ‌ها کاملا پخت، آن‌ها را در یک ظرف جداگانه سرخ کنید. مرغ‌های سرخ شده را به مواد سیر، تخم‌مرغ و پیاز اضافه کنید. برای آب خورش از آب مرغ استفاده کنید. آب مرغ را به مواد فوق اضافه کنید و اجازه دهید خورش سیر قلیه گیلکی با مرغ خوب جا بیفتد و به روغن بیفتد. برای چاشنی این خورش می‌توانید از دو قاشق غذاخوری آبغوره استفاده کنید. خورش سیر قلیه گیلکی با مرغ یک خورش کم آب است، پس هم در ریختن آب مرغ دقت کنید و هم اجازه دهید آب آن کم شود. خورش سیر قلیه گیلکی با مرغ را با برنج و ترب سفید و انواع ترشی و سالاد و زیتون سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2018/12/tordh-tareh-gilan1-848x477.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (304, 'خورش انار مسما گیلانی', 'در اولین مرحله از طرز تهیه انار مسما باید تکه‌های مرغ را با مقداری نمک، فلفل، زردچوبه و نصف مقدار زعفران دم‌کرده مزه‌دار کنید. سپس کمی روغن در ماهیتابه بریزید و مرغ‌ها را در روغن سرخ کنید تا طلایی شون', 'در اولین مرحله از طرز تهیه انار مسما باید تکه‌های مرغ را با مقداری نمک، فلفل، زردچوبه و نصف مقدار زعفران دم‌کرده مزه‌دار کنید. سپس کمی روغن در ماهیتابه بریزید و مرغ‌ها را در روغن سرخ کنید تا طلایی شوند. بعد از این که مرغ‌ها طلایی شدند آن‌ها را از روغن خارج کنید و کنار بگذارید. در این مرحله باید پیاز را به صورت نگینی و ریز سرخ کنید. سپس در روغن تفت دهید. سپس سیر رنده شده را به آن اضافه کنید و بعد از این که پیازها سبک شدند، نمک، فلفل و زردچوبه را اضافه کنید. در سومین مرحله رب گوجه را به ترکیب پیازها اضافه کنید و خوب در روغن تفت دهید تا بوی خامی رب گرفته شود. در این مرحله مرغ‌های سرخ شده را همراه با ۱ لیوان آب جوش به سایر مواد اضافه کنید. سپس درب قابلمه را روی آن قرار دهید و صبر کنید تا مرغ به آرامی بپزد. چند دقیقه بعد از پخت مرغ‌ها، باید رب انار و آب انار را به خورش اضافه کنید. سپس دوباره درب قابلمه را روی آن قرار دهید تا خورش بپزد و جا بیفتد. در اواخر پخت باقی مانده زعفران دم‌کرده را به خورش اضافه کنید. سپس خورش انار مسما را در ظرف مورد نظر بریزید و همراه با برنج کته یا برنج چلو رستورانی میل کنید. برای تزیین این خورش می‌توانید از دانه انار استفاده کنید. در اولین مرحله از طرز تهیه انار مسما به روش گیلانی باید پیاز را به صورت نگینی خرد کنید و مرغ را در آن سرخ کنید. برای خوش‌طعم شدن و گرفتن بوی زهم مرغ، بعد از اینکه مرغ به رنگ سفید یا کمی طلایی درآمد، زردچوبه، نمک و فلفل سیاه را به آن اضافه کنید. در این مرحله رب گوجه، جعفری و نعنا را به همراه دانه‌های انار به مرغ اضافه کنید و کمی تفت بدهید. سپس درب ظرف را بگذارید. بعد از ۵ دقیقه آب انار را اضافه کنید. بعد از اینکه آب انار جوش آمد، درب قابلمه را بگذارید و حرارت را کم کنید تا مرغ در خورش بپزد. اگر آب انار کم بود، کمی آب جوش اضافه کنید ولی باید حواستان باشد که در پایان خورش را وقتی از شعله بردارید که آب آن تقریبا کشیده شده و خورش انار مسما کم آب و غلیظ باشد. در ۱۵ دقیقه پایانی از پخت انار مسما گیلانی باید زعفران دم کشیده را به مواد داخل قابلمه اضافه کنید. همچنین اگر می‌خواهید خورش انار مسما، کمی ترش‌تر شود، در همین مرحله به آن کمی آبغوره اضافه کنید. اگر از انار ترش استفاده می‌کنید در صورت تمایل می‌توانید یک قاشق چای‌خوری شکر به خورش انار اضافه کنید تا طعم ملسی پیدا کند. به دلیل وجود انار در این دستور غذایی، ممکن است خورش مسما انار ترش شود، پس مراقب باشید. خورش انار مسما باید کاملا غلیظ و کم آب تهیه شود، بنابراین اجازه دهید خورش با حرارت ملایم و برای مدت طولانی بپزد تا جا بیفتد. در دستور پخت انار دانه مسما گیلانی می‌توانید بنا به سلیقه خودتان از رب انار ترش، ملس یا شیرین استفاده کنید. به طور کلی بهتر است طعم خورش شما ملس و کمی ترش و شیرین باشد.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/11/23%D9%82%DB%8C.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (305, 'سیرابیج (سیرواویج) گیلانی با برگ سیر و تخم مرغ', 'در اولین مرحله از دستور تهیه سیرابیج باید برگ سیرها را به خوبی بشویید و اجازه دهید کمی خشک شوند. سپس با استفاده از یک چاقوی تیز برگ سیرها را به صورت یکنواخت و ریز خرد کنید. برخی افراد برگ سیر را بعد ا', 'در اولین مرحله از دستور تهیه سیرابیج باید برگ سیرها را به خوبی بشویید و اجازه دهید کمی خشک شوند. سپس با استفاده از یک چاقوی تیز برگ سیرها را به صورت یکنواخت و ریز خرد کنید. برخی افراد برگ سیر را بعد از خرد کردن می‌شویند. شما نیز می‌توانید ابتدا برگ سیرها را خرد کنید و سپس داخل یک آبکش بریزید و با فشار آب زیاد بشویید. سپس یک ماهیتابه را روی شعله ملایم گاز قرار دهید و مقداری روغن سرخ کردنی در آن بریزید تا گرم شود. بعد از داغ شدن روغن، برگ سیرها را به ماهیتابه اضافه کنید و کمی تفت دهید. توجه داشته باشید که برگ سیرها نباید سرخ شوند، تنها لازم است با روغن تفت بخورند. سپس مقداری زردچوبه، نمک و فلفل سیاه به برگ سیرها اضافه کنید و مواد را هم بزنید تا رنگ برگ سیرها با کمک زردچوبه کمی تغییر کند. به یاد داشته باشید که حجم برگ سیر بعد از اینکه تفت بخورد کم می‌شود، پس در ابتدا نگران زیاد بودن مقدار سیرها نباشید. در این مرحله تخم مرغ‌ها را یکی یکی به برگ سیرهای تفت خورده اضافه کنید و سریع هم بزنید. در این مرحله باید تخم مرغ‌ها را خوب هم بزنید تا خودشان را نگیرند و سفت نشوند. تخم مرغ باید به خورد سیر برود و کاملاً با برگ سیر ترکیب شود. بعد از اینکه تخم مرغ به طور کامل پخته شد غذا را داخل ظرف مناسب بریزید و در کنار برنج کته یا همراه با نان تازه میل کنید.', 13, 27, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2020/12/img_1353-e1510659064971-1024x768-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (306, 'آوکونوس (آب کونوس) یا آب ازگیل گیلانی', 'در اولین مرحله از طرز تهیه آوکونوس باید ازگیل‌ها را بشویید و خوب تمیز کنید. سپس آن‌ها را در آبکش بریزید تا آب اضافه آن‌ها خارج شود. در ادامه گوشه ازگیل‌ها را تمیز کنید و اجازه دهید کاملا خشک شوند.', 'در اولین مرحله از طرز تهیه آوکونوس باید ازگیل‌ها را بشویید و خوب تمیز کنید. سپس آن‌ها را در آبکش بریزید تا آب اضافه آن‌ها خارج شود. در ادامه گوشه ازگیل‌ها را تمیز کنید و اجازه دهید کاملا خشک شوند. در این مرحله از طرز تهیه آب ازگیل گیلانی باید ازگیل‌ها را در یک شیشه تمیز و خشک بریزید. سپس ظرف را با آب پر کنید و مقداری نمک به آن اضافه کنید. سپس شیشه را چند بار تکان دهید تا نمک در آب حل شود. بعد از این که آوکونوس را آماده کردید، درب ظرف را ببندید و آن را در دمای آشپزخانه قرار دهید تا ترش و نرم شود. حدود دو هفته تا ۱ ماه طول می‌کشد تا آوکونوس آماده شود. در انتها برای سرو کردن این خوراکی خوشمزه باید آن را در یک ظرف بریزید و با کمی گلپر میل کنید.', 10, 20, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2023/11/1-19.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (307, 'مرغ ترش گیلانی', 'سبزی‌ها را بشویید و ساطوری و ریز خرد کنید. سپس سبزی را بپزید و جداگانه سرخ کنید (درست مثل نحوه سرخ کردن سبزی قورمه).', 'سبزی‌ها را بشویید و ساطوری و ریز خرد کنید. سپس سبزی را بپزید و جداگانه سرخ کنید (درست مثل نحوه سرخ کردن سبزی قورمه). مرغ را بشورید و به قطعات دلخواه تقسیم کنید. لپه‌ها را جداگانه بپزید و سپس آب لپه را دور بریزید. در یک قابلمه پیازها را نگینی خرد کنید و در مقداری روغن تفت دهید. کمی زردچوبه به پیازها اضافه کنید و همراه پیاز سرخ کنید تا طعم خامی زردچوبه گرفته شود. تکه‌های مرغ را اضافه کنید و با پیاز سرخ شده تفت دهید. لپه‌ها را هم اضافه کنید و به آرامی کمی تفت دهید که له نشوند. سیرها را جداگانه رنده و سرخ کنید. سیر تفت داده شده را به مواد داخل قابلمه اضافه کنید. وقتی مرغ‌ها کمی سرخ شدند و رنگ مرغ عوض شد سبزی مرغ ترش را هم که تفت داده بودید اضافه کنید. مقداری آب جوش، نمک، فلفل سیاه و فلفل قرمز به مواد اضافه کنید (در حدی که آب فقط یک بند انگشت روی مواد را بگیرد). کمی شعله گاز را زیاد کنید و وقتی خورش به قل قل خوردن افتاد، شعله گاز را کم کنید تا مرغ ترش رشتی روی شعله ملایم گاز خوب بپزد و جا بیفتد و به روغن بیفتد. یک ساعت مانده به پایان پخت مرغ ترش گیلانی، لیمو عمانی را اضافه کنید. به جای لیمو عمانی از دو قاشق غذاخوری رب انار هم می‌توانید استفاده کنید. خورشت مرغ ترش را با برنج چلو، کته ساده یا برنج زعفرانی سرو کنید.', 30, 60, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2019/09/%D8%AE%D9%88%D8%B1%D8%B4-%D9%85%D8%B1%D8%BA-%D8%AA%D8%B1%D8%B4-%DB%B2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (308, 'آش انار شمالی', 'در اولین مرحله از طرز پخت آش انار شمالی مخصوص استان گیلان باید لوبیا چیتی و باقلا را در ۲ کاسه جدا بریزید. سپس ظرف آن‌ها را پر از آب کنید و اجازه دهید حداقل برای ۶ ساعت خیس بخورند. در این مدت حتما ۲ ت', 'در اولین مرحله از طرز پخت آش انار شمالی مخصوص استان گیلان باید لوبیا چیتی و باقلا را در ۲ کاسه جدا بریزید. سپس ظرف آن‌ها را پر از آب کنید و اجازه دهید حداقل برای ۶ ساعت خیس بخورند. در این مدت حتما ۲ تا ۳ بار آب آن‌ها را عوض کنید. این کار موجب می‌شود تا نفخ حبوبات از بین برود. در مرحله دوم از طرز تهیه آش انار شمالی باید سبزی‌ها را پاک کنید و با آب فراوان بشویید. سپس سبزی‌ها را آبکش کرده و کاملا ریز خرد کنید. بعد از آماده شدن سبزی آش باید لبوها را پوست بگیرید و آن‌ها را نیز به صورت ریز خرد کنید. بعد از این که همه مواد آش انار گیلان را خرد و آماده کردید، یک قابلمه نسبتا بزرگ برای آش آماده کنید. مقداری آب داخل قابلمه بریزید. سپس لوبیا چیتی و باقالی خیسانده شده را به همراه لبوی خرد شده به آن اضافه کنید. شعله گاز را با حرارت ملایم روشن کنید و اجازه دهید حبوبات و لبو به خوبی بپزند. بعد از این که حبوبات و لبو پختند، در این مرحله از طرز تهیه آش انار شمالی باید برنج شسته شده را به آش اضافه کنید. سپس مقداری نمک به آش بزنید و ۱۵ دقیقه بعد از اضافه کردن برنج، سبزی خرد شده آش را به آن اضافه کنید. درب قابلمه را به صورت نیمه روی آن قرار دهید و کمی صبر کنید تا آش انار به خوبی جا بیفتد. در این مرحله پیاز را خلالی نازک خرد کنید و پیاز داغ درست کنید. سیرها را هم پوست بگیرید، خرد کنید و بعد از سرخ شدن پیازها اضافه کنید و کمی تفت دهید. به این ترکیب فلفل سیاه و زردچوبه را اضافه کنید و مقداری تفت دهید. در نهایت مقداری از سیر داغ و پیاز داغ را برای تزیین روی آش کنار بگذارید. در آخرین مرحله از طرز تهیه آش انار گیلان، هر زمان که احساس کردید آش شما به خوبی پخته شده، ۱۵ دقیقه قبل از خاموش کردن شعله زیر آش، مقداری شکر و رب انار به آش اضافه کنید. نمک آش را هم اندازه کنید. ۱۵ دقیقه دیگر برای جا افتادن آش انار صبر کنید. می‌توانید این آش خوشمزه را همراه با سیر داغ، پیاز داغ، سبزی خشک نعناع و دانه‌های انار تزیین کنید و از طعم لذیذ آن لذت ببرید. در اولین مرحله از طرز تهیه آش انار شمالی با آب انار، حبوبات شامل عدس و لوبیا چیتی، برنج نیم دانه و بلغور گندم یا جو را جداگانه به مدت حداقل ۲ ساعت بخیسانید و آبکشی کنید. لوبیا و عدس را در دو ظرف جدا با مقداری آب روی حرارت قرار دهید تا بپزند و نرم شوند. بعد از پختن لوبیا و عدس، در یک قابلمه بزرگ، عدس و لوبیا را ریخته و روی آن‌ها آب قلم را بریزید. برنج نیم دانه و بلغور را آبکشی کرده و به قابلمه اضافه کنید. قابلمه را روی حرارت بگذارید و اجازه دهید مواد بپزند. در این فاصله گوشت‌های قلقلی را آماده کنید. ۲ عدد از پیازها را رنده کنید و آب آن‌ها را بگیرید. در یک کاسه، تفاله پیاز را با گوشت چرخ کرده، نمک، فلفل سیاه و زردچوبه بریزید و ورز دهید. بعد از اینکه گوشت چرخ کرده کاملا با ادویه‌ها یکدست و منسجم شد، به اندازه یک گردوی کوچک، از مایه گوشت بردارید و بین دست‌هایتان بچرخانید تا گرد شود. همه مایه گوشت را قلقلی کنید. برای انسجام بیشتر به مدت ۱۵ تا ۳۰ دقیقه، قلقلی‌ها را در یخچال بگذارید. سپس در یک تابه مقداری روغن بریزید و روی حرارت بگذارید. روغن که داغ شد، گوشت‌های قلقلی را سرخ کنید تا طلایی شوند. قلقلی‌ها را کنار بگذارید. در این مرحله، سیرها را پوست بگیرید و ریز خرد کنید. بقیه پیازها (۳ پیاز دیگر) را هم پوست بگیرید و خلالی یا نگینی خرد کنید. در تابه‌ دوباره مقداری روغن بریزید و پیازها را در آن سرخ کنید تا طلایی شوند. سیرها را هم اضافه کنید و تفت دهید. همین که عطر آنها خارج شد، حرارت را قطع کنید و کنار بگذارید. سراغ قابلمه آش بروید و سبزی آش را به قابلمه اضافه کنید و هم بزنید. آب انار، نصف پیاز داغ، رب انار و مقداری نمک و فلفل سیاه و زردچوبه را به آش اضافه کنید. همه مواد را هم بزنید و ۳۰ دقیقه صبر کنید تا سبزی آش و ادویه‌ها به خورد آش بروند. در این مدت، درب قابلمه را نیمه باز بگذارید. در آخرین مرحله از طرز تهیه آش انار شمالی، وقتی آش به غلظت کافی رسید، گوشت‌های قلقلی را به قابلمه اضافه کنید. به آرامی مواد را هم بزنید. سپس ادویه‌ها و چاشنی آش را اندازه کنید. وقتی به غلظت و طعم دلخواهتان رسید، حرارت را قطع کنید. آش انار را در کاسه‌های سرو بریزید و روی آنها را با پیاز داغ و دانه‌های انار، تزیین و سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/ash-anar-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (309, 'خورش فسنجان رشتی', 'در اولین مرحله از طرز تهیه خورش فسنجون رشتی باید مقداری روغن در قابلمه بریزید. صبر کنید روغن داغ شود، سپس تکه‌های مرغ را به آن اضافه کنید و مرغ‌ها را کمی در روغن تفت دهید. مرغ باید آنقدر تفت بخورد که', 'در اولین مرحله از طرز تهیه خورش فسنجون رشتی باید مقداری روغن در قابلمه بریزید. صبر کنید روغن داغ شود، سپس تکه‌های مرغ را به آن اضافه کنید و مرغ‌ها را کمی در روغن تفت دهید. مرغ باید آنقدر تفت بخورد که رنگ سطح روی مرغ طلایی شود. پیاز را در قابلمه‌ای که می‌خواهید فسنجان را در آن درست کنید نگینی خرد کنید و با کمی روغن تفت دهید. پیازها که سبک شدند، گردوی چرخ کرده یا سابیده شده را به آن اضافه کنید و ۵ الی شش دقیقه با هم تفت دهید. بعد از تفت دادن گردوها، در این مرحله از تهیه فسنجون شمالی باید فلفل سیاه، نمک، زردچوبه، مغز گردو تفت داده شده و رب گوجه را به مرغ‌های تفت داده شده اضافه کنید و کمی با هم تفت دهید. مقداری آب به قابلمه اضافه کنید. ابتدا شعله گاز را زیاد کنید تا آب به جوش بیاید. سپس شعله گاز را کم کنید، درب قابلمه را روی آن بگذارید و اجازه دهید خورش به آرامی بپزد و فسنجان به روغن بیفتد. خورش فسنجان رشتی باید برای چند ساعت به آرامی بپزد تا کاملاً جا بیفتد. در این مدت هر زمان آب خورش کم شد، می‌توانید کمی آب سرد یا چند تکه یخ به آن اضافه کنید. این کار موجب روغن انداختن خورش فسنجون می‌شود. وقتی مرغ کاملا پخت رب انار را اضافه کنید. صبر کنید تا خورش کاملا جا بیفتد و رنگ آن سیاه شود. در نهایت مقدار ترشی و نمک غذا را بچشید و اگر لازم بود کمی رب انار یا نمک به آن اضافه کنید. بعد از آماده شدن خورش فسنجان رشتی آن را با برنج چلو آبکش یا برنج کته به همراه ماهی شور یا ماهی دودی مثل رشتی‌ها، میل کنید. برای پخت خورش فسنجان رشتی می‌توانید ابتدا مواد خورش را در زودپز بریزید و برای ۴۵ دقیقه خورش را در زودپز بپزید. سپس خورش فسنجان را برای چند ساعت نیز روی گاز قرار دهید تا خورش کاملاً جا بیفتد و آماده شود.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/07/r22-min-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (310, 'نان کشتا گیلانی', 'در اولین مرحله، یک عدد کدو حلوایی رسیده را با آب کاملا تمیز بشویید. سپس پوست آن را جدا کرده و کدو حلوایی را به شکل تکه‌های مکعبی درشت برش بزنید. یک قابلمه با سایز مناسب انتخاب کنید و یک سوم آن را از آ', 'در اولین مرحله، یک عدد کدو حلوایی رسیده را با آب کاملا تمیز بشویید. سپس پوست آن را جدا کرده و کدو حلوایی را به شکل تکه‌های مکعبی درشت برش بزنید. یک قابلمه با سایز مناسب انتخاب کنید و یک سوم آن را از آب پرکرده و روی حرارت قرار دهید تا به جوش بیاید. بعد از آنکه آب به جوش آمد کدو حلوایی را به آن اضافه کنید، حرارت را کم کنید و اجازه دهید کدو حلوایی روی حرارت ملایم بپزد و نرم شود. بعد از آنکه تکه‌های کدو حلوایی کاملا پختند و نرم شدند آنها را آبکش کنید و اجازه دهید تا آب اضافه از آن خارج شود. کدو حلوایی پخته شده را به همراه مقدار لازم از شکر در میکسر یا غذاساز بریزید و له کنید تا به شکل پوره دربیاید. شما به یک لیوان دسته‌دار فرانسوی پوره کدو حلوایی نیاز دارید. پوره کدو حلوایی را در دمای محیط قرار دهید تا خنک شود. آرد گندم و آرد برنج را هر کدام جداگانه سه بار الک کنید. سپس یک ظرف گود مناسب انتخاب کنید و آردهای الک شده را درون آن بریزید و با یکدیگر هم بزنید تا یکدست شوند. نمک و تخم شنبلیله را اضافه کنید و مواد پودری را با یکدیگر هم بزنید تا یکدست شود. سپس وسط کاسه را خالی کنید و پوره کدو حلوایی را آنجا بریزید. پوره کدو حلوایی را به تدریج با دیگر مواد ورز دهید. حدود ۱۵ دقیقه خمیر را ورز دهید تا کاملا یکدست شود. سپس مقداری آب را به تدریج به خمیر اضافه کنید تا از سفتی دربیاید و حالت کشسانی پیدا کند. بعد از اضافه کردن آب مجددا شروع به ورز دادن خمیر کنید تا جایی که به دست نچسبد و بافت یکسانی پیدا کند. بعد از آنکه خمیر را حسابی ورز دادید تا جایی که بافت یکدستی پیدا کرد و دیگر به دست نچسبید، روی ظرف حاوی خمیر را با سلفون کاملا بپوشانید تا هوا به درون آن نفوذ نکند. سپس آن را در دمای محیط به مدت دو ساعت استراحت دهید تا حجم خمیر دو برابر شود. بعد از دو ساعت به سراغ خمیر بروید و سلفون روی آن را بردارید. روی یک سطح صاف کمی آرد بپاشید تا خمیر روی آن نچسبد. سپس از خمیر آماده شده به اندازه مشت دست یک چانه بردارید و کاملا گرد کنید. چانه‌های خمیر را روی سطح آردپاشی شده قرار دهید و به وسیله وردنه آنها را باز کنید. ضخامت خمیر باز شده باید نازک و در حد چند میلی‌متر باشد. این کار را تا زمان تمام شدن کل خمیر ادامه دهید. به یک تابه نچسب نیاز دارید. کف تابه را کمی با روغن چرب کنید و روی حرارت ملایم قرار دهید. بعد از آنکه تابه داغ شد، خمیر نان را در آن قرار دهید. درب تابه را ببندید. بعد از آنکه روی خمیر حباب‌هایی به وجود آمد و رنگ آن طلایی شد درب تابه را بردارید و خمیر را برگردانید تا سمت دیگر آن نیز بپزد. این مرحله را تا پختن تمام خمیر نان ادامه دهید. بعد از آنکه کل خمیر را در تابه و روی حرارت پختید نان گیلانی آماده است. از آنجایی که این نان طعم شیرینی دارد می‌توانید آن را به تنهایی سرو کنید و یا در کنار هر غذا و خوراکی که تمایل دارید از آن در شب یلدا یا سایر مناسبت‌ها استفاده کنید. روش دوم: به خمیر نان مقدار ۲ قاشق غذاخوری ماست ترش و نصف قاشق چای‌خوری بیکینگ پودر اضافه کنید. این کار کمک می‌کند تا خمیر زودتر ور بیاید و لطیف‌تر شود. این خمیر را به مدت یک ساعت استراحت دهید و سپس مانند مراحلی که در بالا ذکر شد نان را در تابه بپزید و سرو کنید. زیاد بودن مقدار آرد، به ویژه آرد برنج در تهیه نان، باعث خشک شدن آن می‌شود. نسبت معینی از آرد را در تهیه این نان رعایت کنید تا نان کشتا به نرمی پخته شود. این نان را به مدت یک هفته می‌توانید در یخچال نگهداری و از آن استفاده کنید. اگر مقدار زیادی از این نان را تهیه کرده‌اید آن را به مدت طولانی در فریزر می‌توانید نگهداری کنید. قبل از استفاده، نان را از فریزر بیرون آورده و زمان بدهید تا یخ آن در دمای محیط باز شود.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2024/11/%D9%86%D8%A7%D9%86-%D8%AE%D8%B1%D9%81%D9%87-%D8%AE%D9%84%D9%81%D9%87-%DB%8C%D8%A7-%DA%A9%D8%B4%D8%AA%D8%A7-%D8%AF%D8%B3%D8%AA%D9%88%D8%B1-%D8%A7%D8%B5%D9%84%DB%8C-%D8%B9%DA%A9%D8%B3.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (311, 'آش شله قلمکار', 'در مرحله اول از طرز تهیه آش شله قلمکار ابتدا حبوبات را خیس کنید. حبوبات را از شب قبل بخیسانید و چند بار آب آن‌ها را عوض کنید. برنج ایرانی را هم به مدت ۴ ساعت در آب و نمک خیس کنید.', 'در مرحله اول از طرز تهیه آش شله قلمکار ابتدا حبوبات را خیس کنید. حبوبات را از شب قبل بخیسانید و چند بار آب آن‌ها را عوض کنید. برنج ایرانی را هم به مدت ۴ ساعت در آب و نمک خیس کنید. در یک قابلمه بزرگ، یک عدد پیاز را به صورت نگینی خرد کنید و با مقدار روغن تفت دهید تا سبک و طلایی شود. گوشت گوسفند را که تکه تکه کرده‌اید به پیاز اضافه کنید، مقداری زردچوبه، فلفل سیاه و فلفل قرمز به دلخواه اضافه کنید و کمی گوشت را تفت دهید. حالا آب قلم و نخود گرد را اضافه کنید. آب قلم، نخود و گوشت باید روی شعله ملایم گاز به مدت ۴ ساعت بپزند. بعد از این‌که یک ساعت از پخت نخود و گوشت گذشت، لوبیا قرمز و لوبیا چیتی را هم اضافه کنید. در یک ساعت آخر پخت، عدس و برنج را هم اضافه کنید. در این فاصله تخم گشنیز و کمی زردچوبه را در مقداری روغن مایع تفت دهید. ۳۰ دقیقه بعد از اضافه کردن عدس و برنج و در حالی که نیم ساعت به پایان پخت آش باقی مانده تخم گشنیز و زردچوبه تفت داده شده را به همراه سبزی آش به مواد در حال پخت اضافه کنید. نمک و فلفل آش شله قلمکار را هم اندازه کنید. بعد از پخت کامل نخود و گوشت و سایر حبوبات آش، باید آش شله قلمکار را با دقت آبکش کنید. آب آش را کنار بگذارید. گوشت را ریش ریش کنید و اگر استخوان دارد آن را از گوشت با دقت جدا کنید. اگر گوشت‌کوب برقی دارید تمام مواد از گوشت ریش شده تا تمام حبوبات و مخلفات آش را با این وسیله خوب بکوبید. اگر هم گوشت‌کوب برقی ندارید برای له کردن تمام مواد از هاون و گوشت‌کوب دستی استفاده کنید. تمام مواد را خوب بکوبید و به قابلمه برگردانید. آبی را که از آش آبکش کرده بودید به مواد کوبیده شده آش اضافه کنید و به مدت ۳۰ الی ۴۵ دقیقه دیگر اجازه دهید تا آش خوب بپزید و جا بیفتد. در این فاصله با یک عدد پیاز باقی مانده پیاز داغ مجلسی را آماده کنید. برای این‌که آش شله قلمکار مجلسی حسابی غلیظ و کشدار شود باید حین پخت و در تمام ۳۰ الی ۴۵ دقیقه پایانی پخت مرتب آش کوبیده و له شده را هم بزنید. در ۱۵ دقیقه پایانی پخت ماست چکیده و نیمی از پیاز داغ را به آش اضافه کنید و باز هم آش را هم بزنید. بعد از این‌که آش کاملا پخت و جا افتاد آن را با نعنا داغ و باقی مانده پیاز داغ مجلسی تزیین کنید. آش شله قلمکار را با نان بربری یا نان سنگک سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2021/07/%D8%B7%D8%B1%D8%B2-%D8%AA%D9%87%DB%8C%D9%87-%D8%B4%D9%84%D9%87-%D9%82%D9%84%D9%85%DA%A9%D8%A7%D8%B1-%DB%B2.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (312, 'دمپختک باقالی', 'برنج را با آب فراوان بشویید. سپس آن را برای ۱ تا ۲ ساعت در آب و مقداری نمک خیس دهید.', 'برنج را با آب فراوان بشویید. سپس آن را برای ۱ تا ۲ ساعت در آب و مقداری نمک خیس دهید. باقالی‌ زرد را همراه با آب در قابلمه بریزید و مقداری نمک به آن اضافه کنید. سپس درب قابلمه را روی آن بگذارید تا باقالی‌های زرد بپزند. پیازها را به صورت نگینی خرد کنید. مقداری روغن در ماهیتابه بریزید و آن را روی شعله گاز قرار دهید. در ادامه پیازها را در روغن سرخ کنید. بعد از این که پیازها کمی سبک شدند، مقداری زردچوبه و فلفل سیاه به پیازها اضافه کنید. باقالی پخته شده را آبکش کنید و به ترکیب پیاز و ادویه‌ها اضافه کنید. کمی باقالی‌ها را تفت دهید و سپس برنج خیس خورده را اضافه کنید. در نهایت یک لیوان آب و کمی نمک در قابلمه بریزید. ابتدا شعله گاز را زیاد کنید و بعد از این که آب برنج کم شد، درب قابلمه را با دم‌کنی روی آن قرار دهید تا دمپختک باقالی با شعله ملایم دم بکشد. می‌توانید مقداری کره نیز روی برنج قرار دهید تا دمپختک شما خوش طعم‌تر شود. مقداری روغن در ماهیتابه بریزید و بعد از این که روغن داغ شد، تخم مرغ را در روغن بریزید تا سرخ شود. در ادامه مقداری نمک و فلفل سیاه به تخم مرغ بزنید و صبر کنید تا نیمرو شود. بعد از این که دمپختک باقالی آماده شد، آن را در دیس بریزید و سپس تخم مرغ نیمرو را روی دمپختک قرار دهید و از طعم این غذای خوشمزه در کنار انواع ترشی، سالاد شیرازی و ماست لذت ببرید.', 15, 30, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2021/04/dampokhtak-ba-nimroo.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (313, 'آش انار تهرانی', 'در کاسه‌های جداگانه، عدس، لوبیا، نخود و برنج نیم دانه را خیس کنید. نخود و لوبیا قرمز را از شب قبل خیس کنید و برنج و عدس را یک ساعت قبل از شروع به پخت، بخیسانید. آب حبوبات را چند بار عوض کنید تا نفخ آن', 'در کاسه‌های جداگانه، عدس، لوبیا، نخود و برنج نیم دانه را خیس کنید. نخود و لوبیا قرمز را از شب قبل خیس کنید و برنج و عدس را یک ساعت قبل از شروع به پخت، بخیسانید. آب حبوبات را چند بار عوض کنید تا نفخ آنها گرفته شود. نخود و لپه را از آب بکشید و در دو قابلمه جدا، بپزید تا نرم شوند. سپس عدس را جداگانه بپزید. همه مواد را بعد از پخت، آبکش کنید و کنار بگذارید. سبزی آش تازه را بشویید، آبکش و به صورت ساطوری خرد کنید. در یک قابلمه با اندازه مناسب، تا نیمه آب بریزید و روی حرارت بگذارید. اجازه دهید آب شروع به جوشیدن کند. بعد از جوش آمدن آب، برنج نیم‌ دانه را به قابلمه اضافه کنید. اجازه دهید برنج کمی نرم شود، سپس سبزی آش را اضافه کنید. عدس، نخود و لوبیا قرمز پخته شده را به قابلمه اضافه کنید. درب قابلمه را نیمه باز بگذارید و اجازه دهید به مدت ۱۵ دقیقه مواد به آرامی بپزند. سپس کال گندم را با مقداری از آب انار مخلوط کرده و به قابلمه اضافه کنید. مواد را هم بزنید و نمک، گلپر، فلفل سیاه و زردچوبه را اضافه کنید. بعد از نرم شدن مواد، رب انار و آب انار را با هم مخلوط کنید تا رب انار رقیق شود. سپس مایه به دست آمده را به قابلمه آش اضافه کنید و هم بزنید. اجازه دهید آش انار به غلظت دلخواهتان برسد. در آخر، دانه‌های انار را در آش بریزید و هم بزنید و چند دقیقه بعد، شعله گاز را خاموش کنید. برای تهیه پیاز داغ، پیازها را پوست بگیرید و به صورت خلالی نازک خرد کنید. در یک تابه پیازها را با روغن سرخ کنید تا کاملا طلایی و سبک شوند. پیازها را از تابه بیرون بیاورید و کنار بگذارید. سپس نعناع خشک را به مدت چند ثانیه تفت دهید و نعناع داغ درست کنید. آش انار تهرانی را در ظرف سرو بریزید و روی آن‌ها را با دانه‌های انار، پیاز داغ و نعناع داغ تزیین و سرو کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/11/6ddefee8-9196-4de6-8e43-888df0e310d3.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (314, 'رشته پلو تهرانی', 'در اولین مرحله از طرز تهیه رشته پلو تهرانی باید برنج را به خوبی بشویید و آن را در یک ظرف آب بریزید. کمی نمک به آن اضافه کنید و اجازه دهید برنج برای ۲ ساعت در آب بماند. سپس کشمش را بشویید و آن را در ظر', 'در اولین مرحله از طرز تهیه رشته پلو تهرانی باید برنج را به خوبی بشویید و آن را در یک ظرف آب بریزید. کمی نمک به آن اضافه کنید و اجازه دهید برنج برای ۲ ساعت در آب بماند. سپس کشمش را بشویید و آن را در ظرف آب جداگانه‌ای بریزید. هسته خرماها را هم جدا کنید و کنار بگذارید. در این مرحله باید یک قابلمه آب روی شعله گاز قرار دهید. مقداری نمک به آن اضافه کنید و صبر کنید تا آب شروع به جوشیدن کند. در ادامه آب اولیه برنج را خالی کنید و آن را در قابلمه آب جوش بریزید. اجازه دهید برنج برای چند دقیقه بجوشد و نیم پز شود. در این مرحله باید رشته‌های پلویی را به اندازه دلخواه خرد کنید و در چند دقیقه پایانی به برنج و آب جوش اضافه کنید. برنج و رشته را به آرامی با قاشق هم بزنید تا با هم ترکیب شوند. برنج و رشته پلویی را آبکش کنید. در این مرحله بهتر است برنج را بچشید و اگر طعم آن شور بود، حتما چند دور برنج را با آب سرد بشویید تا شوری آن از بین برود. در این مرحله از طرز تهیه رشته پلو تهرانی باید مقداری روغن در قابلمه بریزید. برای ته دیگ دلخواه خود از نان لواش یا سیب زمینی خرد شده استفاده کنید. سپس ترکیب برنج و رشته را در قابلمه بریزید. یک دم کنی روی درب قابلمه قرار دهید و سپس حدود ۴۵ دقیقه صبر کنید تا رشته پلوی شما دم بکشد. در ۱۵ دقیقه نهایی پخت رشته پلو باید مقداری زعفران دم کرده و روغن به غذا اضافه کنید و به آرامی آن را با پشت قاشق هم بزنید. سپس صبر کنید تا رشته پلو کامل دم بکشد. در این مرحله باید پیاز را به صورت خلالی نازک خرد کنید. مقداری روغن در ماهیتابه بریزید و پیازها را در روغن سرخ کنید تا طلایی شوند و یک پیاز داغ مجلسی درست کنید. سپس زردچوبه و دارچین را اضافه کنید و کمی تفت دهید تا طعم خامی ادویه‌ها گرفته شود. در این مرحله از طرز تهیه رشته پلو حرفه‌ای به روش رستورانی باید کشمش‌ها را به پیاز داغ اضافه کنید. کشمش را برای چند دقیقه تفت دهید. سپس خرماهایی که هسته آن‌ها را جدا کرده‌اید را هم به ترکیب پیاز و کشمش اضافه کنید و برای چند دقیقه در روغن سرخ کنید. بعد از این که رشته پلو دم کشید، آن را در دیس مورد نظرتان بریزید. سپس روی برنج را با ترکیب پیاز داغ، کشمش و خرما تزیین کنید و از طعم این غذای ساده و خوشمزه لذت ببرید.', 25, 50, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/577.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (315, 'چنگال (چنگالی)', 'چنگالی یک غذای ساده و خوشمزه است که برای تهیه آن از ترکیب نان، شکر، کنجد و روغن حیوانی استفاده می‌شود. در ادامه این بخش می‌توانید با مراحل طرز تهیه چنگالی بلوچی آشنا شوید.', 'چنگالی یک غذای ساده و خوشمزه است که برای تهیه آن از ترکیب نان، شکر، کنجد و روغن حیوانی استفاده می‌شود. در ادامه این بخش می‌توانید با مراحل طرز تهیه چنگالی بلوچی آشنا شوید. در اولین مرحله از طرز تهیه چنگال بلوچی باید یک ماهیتابه کوچک را با شعله ملایم روی گاز قرار دهید. سپس مقداری روغن حیوانی در آن بریزید و صبر کنید تا روغن گرم شود. بعد از این که روغن داغ شد، شعله گاز را خاموش کنید. نان تافتون را با دست یا به کمک قیچی خرد کنید و در روغن داغ بریزید. میزان روغنی که برای تهیه این غذا استفاده می‌شود، کاملا سلیقه‌ای است. اگر دوست دارید غذای شما کم چرب‌تر باشد، می‌توانید از مقدار روغن کمتری استفاده کنید. در این مرحله از طرز تهیه چنگال بلوچی باید شکر را به ترکیب نان و روغن اضافه کنید. سپس با دست مواد را چنگ بزنید و خوب ورز دهید تا شکر کاملا در روغن حل شود و به خورد نان برود. در آخرین مرحله از دستور تهیه چنگالی باید کنجد را به سایر مواد اضافه کنید و همه مواد را به خوبی با یکدیگر هم بزنید. از آنجایی که کنجد روی حرارت بالا سریع می‌سوزد، حواستان باشد هنگام اضافه کردن کنجد حرارت پایین باشد و هم اینکه حتما در آخرین مرحله اضافه شود. بعد از آنکه تمام مواد روی حرارت به خوبی با هم مخلوط شدند، چنگالی بلوچی را از روی حرارت برداشته و در یک ظرف مناسب سرو کنید. یک قاشق غذاخوری شیره انگور و یا شیره خرما جهت تزیین روی آن بریزید. چنگالی مقوی و پرخاصیت شما آماده است، نوش جان! یکی دیگر از روش‌های تهیه چنگال جایگزین کردن خرما با شکر است. برای تهیه این چنگالی می‌توانید از نان سریک محلی استفاده کنید. در ادامه می‌توانید با مراحل طرز تهیه چنگالی بلوچی با خرما آشنا شوید. در اولین مرحله از طرز تهیه چنگال با خرما باید یک ماهیتابه را روی شعله ملایم گاز قرار دهید و بعد کره محلی را اضافه کنید. صبر کنید تا کره کاملا ذوب شود. سپس شعله گاز را خاموش کنید. در این مرحله از روش تهیه چنگمالی باید هسته‌های خرما را جدا کنید و خرماها را به دو قسمت تقسیم کنید. بعد از این که کره ذوب شد و خرماها را آماده کردید، باید نان را به صورت ریز خرد کنید. برای خرد کردن نان‌ها می‌توانید از چاقو یا قیچی کمک بگیرید. در آخرین مرحله از طرز تهیه چنگالی بلوچی با خرما باید در یک ظرف نان و خرما را با هم مخلوط کنید و سپس کره را روی آن‌ها بریزید. برای این که کره به خورد نان و خرما برود، می‌توانید مواد را برای چند دقیقه با دست ورز دهید. بعد از آنکه کره به خوبی به خورد نان و خرما رفت نوبت به سرو کردن این غذای نونی ساده می‌رسد. چنگالی را در یک ظرف مناسب بریزید و آن را در وعده‌های اصلی غذا و یا پیش غذا میل کنید. یکی دیگر از روش‌های درست کردن چنگال بلوچی جایگزین کردن آرد با نان است. در این روش باید به جای نان خرد شده از مقداری آرد و آب استفاده کنید. در ادامه می‌توانید با مراحل طرز تهیه چنگال بلوچی با آرد آشنا شوید. در اولین مرحله از طرز تهیه چانگالی باید آرد را سه بار الک کنید. الک کردن آرد باعث می‌شود تا ذرات آن از هم جدا شود و هنگام آماده کردن خمیر گوله گوله نشود. مقداری آب به آرد اضافه کنید تا خمیر شلی ایجاد شود. خمیر این غذای بلوچی نباید خیلی سفت یا خیلی شل باشد. در این مرحله باید یک تابه صاف را روی حرارت قرار دهید و کمی روغن در آن بریزید. به اندازه یک ملاقه از خمیر را بردارید و در تابه بریزید و خوب پخش کنید و نان را بپزید. زمانی که نان نیم‌پز شد، آن را برگردانید تا طرف دیگر آن هم بپزد. همه خمیر را به همین روش در ماهیتابه بپزید و نان‌ها را آماده کنید. در این مرحله باید هسته‌های خرما را جدا کنید و سپس خرماها را با نان‌های پخته شده مخلوط کنید و با دست خوب ترکیب کنید تا نان و خرما کاملا با هم مخلوط شوند و یک ترکیب با شیرینی مورد نظرمان ایجاد شود. بعد از این که نان و خرما به خوبی با هم ترکیب شدند، روغن حیوانی را گرم کنید و به سایر مواد اضافه کنید و دوباره مواد را ورز دهید. می‌توانید این چنگالی را در قالب بریزید و بعد به آرامی برگردانید تا ظاهر جذاب‌تری پیدا کند. بعد از آنکه تمام مواد به خوبی ورز داده شدند و به خورد هم رفتند غذای چنگالی با آرد شما آماده است و می‌توانید آن را سرو کنید. این مدل از چنگال را برای آنکه ظاهر جذاب‌تری داشته باشد می‌توانید قالب بزنید. چنگالی‌های قالب زده را در ظرف مناسبی بریزید و آن را سرو کنید. در سیستان و بلوچستان برای مزه‌دار کردن چنگال از ادویه‌ای مخصوص به نام «سبز دارو» استفاده می‌شود. اگر به این ادویه دسترسی ندارید، می‌توانید از ترکیب دارچین، هل و گلاب استفاده کنید. برای تهیه چنگال باید از روغن حیوانی یا کره محلی استفاده کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/11/1-18.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (316, 'قاووت کرمانی', 'در اولین مرحله از طرز تهیه قاووت قهوه باید پوست هل سبز، هل سیاه و تخمه آفتابگردان را جدا کنید. سپس همه مواد را در ماهیتابه بریزید و با شعله ملایم تفت دهید تا بوی خامی آن‌ها گرفته شود.', 'در اولین مرحله از طرز تهیه قاووت قهوه باید پوست هل سبز، هل سیاه و تخمه آفتابگردان را جدا کنید. سپس همه مواد را در ماهیتابه بریزید و با شعله ملایم تفت دهید تا بوی خامی آن‌ها گرفته شود. بعد از این که همه مغزها و دانه‌ها را تفت دادید، صبر کنید تا خنک شوند و سپس آن‌ها را آسیاب کنید. بعد از آماده شدن قاووت قهوه می‌توانید آن را به صورت پودری میل کنید و یا یک قاشق از آن را در آب یا شیر بریزید و بنوشید. در این روش برای تهیه قاووت قهوه کرمانی ابتدا باید همه مواد را پاک کنید و در صورت نیاز بشویید. پوست تخمه‌ها را جدا کنید و سپس آن‌ها را برای چند دقیقه با حرارت ملایم تفت دهید تا برشته شوند. مقداری شکر پودر شده به قاووت اضافه کنید. سپس همه مواد را آسیاب و با هم ترکیب کنید. برای این که طعم قاووت قهوه شما شیرین شود، می‌توانید از شکر سفید، شکر قهوه‌ای و یا مقداری نبات پودر شده استفاده کنید. می‌توانید قاووت قهوه را به صورت پودری بخورید و یا آن را با آب، شیر، چای یا حتی ماست ترکیب کنید و میل کنید.', 20, 40, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/11/34-3.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (317, 'شیرینی کلمپه کرمانی', 'فر را روشن کرده و روی دمای ۱۷۰ درجه سانتیگراد تنظیم کنید تا گرم شود.', 'فر را روشن کرده و روی دمای ۱۷۰ درجه سانتیگراد تنظیم کنید تا گرم شود. آرد را الک کرده و در یک ظرف مناسب بریزید. روغن یا کره را به آن اضافه و با دست مخلوط کنید تا خمیر یکدستی درست شود. در ظرفی دیگر، زرده تخم مرغ، زعفران، هل، شکر، ماست یا شیر، وانیل و بیکینگ پودر را با هم مخلوط کنید. مواد دو ظرف را با هم مخلوط کرده و یک خمیر یکدست درست کنید. خمیر را زیاد ورز ندهید تا به روغن نیفتد. روی یک سطح صاف، آرد بپاشید و خمیر را زیر و رو کنید تا به دست نچسبد. خمیر به دست آمده را در نایلون بگذارید و به مدت ۲ ساعت در یخچال استراحت دهید تا چسبندگی آن از بین برود. خرما را برای چند دقیقه در آب داغ بگذارید تا پوست آن به راحتی جدا شود. هسته خرماها را خارج کنید. خرماها را با پشت قاشق نرم کنید یا در مخلوط‌کن بریزید و همراه با گردو، میخک، زنجبیل و دارچین مخلوط کنید. خمیر را از یخچال بیرون بیاورید و روی سطح آردپاشی‌شده بگذارید و با وردنه باز کنید. ضخامت خمیر را به حدود نیم سانتیمتر برسانید و قالب بزنید. روش اول: خمیر را با وردنه روی سطحی صاف پهن کنید و با ته فنجان یا لیوان به شکل دایره‌های یک اندازه در آورید. مواد خرمایی را به شکل گلوله‌های کوچک در آورید و وسط خمیر بگذارید. حالا یک دایره خمیری را روی آن قرار دهید و دور آن را ببندید. روش دوم: خمیرها را با دست گلوله‌ای شکل کنید. خمیر را در کف دست پهن کنید و مواد خرما را وسط آن قرار دهید و اطراف آن را ببندید. خمیر را بین دو تا دستانتان فشار دهید و صاف کنید. خیلی محکم فشار ندهید که مواد داخل آن خارج شود. در طرز تهیه شیرینی کلمپه، مهرها یا قالب‌های خاصی استفاده می‌شود و روی شیرینی طرح دلخواهی زده می‌شود. مهر را خیلی محکم فشار ندهید چون ممکن است مواد داخل کلمپه بیرون بریزد. روی کلمپه‌ها را با کنجد یا پسته تزیین کنید. سینی فر را آرد پاشی کنید. کلمپه‌ها را در سینی بگذارید و در طبقه وسط فر به مدت ۱۰ تا ۱۵ دقیقه قرار دهید. برای طرز تهیه کلمپه کرمانی بدون فر، یک شعله پخش کن را روی شعله ملایم اجاق گاز قرار دهید و سپس یک ماهیتابه نچسب به سایز مناسب را چرب کنید و روی شعله پخش کن قرار دهید و سپس روی درب تابه را دم‌کنی بگذارید و اجازه دهید تا به مدت ۱۵ دقیقه بپزد.', 26, 54, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/12/%D8%B4%DB%8C%D8%B1%DB%8C%D9%86%DB%8C-%DA%A9%D9%84%D9%85%D9%BE%D9%87.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (318, 'شله مشهدی', 'برای تهیه شله مشهدی، از شب قبل حبوبات را بخیسانید. از ۲ ساعت قبل برنج و بلغور را هم خیس کنید.', 'برای تهیه شله مشهدی، از شب قبل حبوبات را بخیسانید. از ۲ ساعت قبل برنج و بلغور را هم خیس کنید. در یک قابلمه پیاز و گوشت را روی حرارت بگذارید. مقداری آب اضافه کنید و اجازه دهید گوشت کاملا بپزد و نرم شود. سپس آن را ریش ریش کنید. حبوبات را هم جداگانه با آب بپزید. در یک قابلمه دیگر، بلغور را بپزید و بعد از لعاب انداختن، برنج را هم اضافه کنید. بعد از اینکه همه مواد پختند، حبوبات، بلغور و برنج را با گوشت‌کوب بکوبید. این ترکیب را به قابلمه گوشت ریش ریش شده اضافه کنید. ادویه شله مشهدی (زردچوبه، چوب دارچین، فلفل قرمز و کمی جوز هندی رنده شده) را اضافه کنید. همه مواد را هم بزنید تا یکدست شود. دوباره به مدت ۳ ساعت، ترکیبات را روی حرارت ملایم بپزید تا جا بیفتد و غلیظ شود. بعد از پختن کامل مواد، نمک شله را هم اضافه و اندازه کنید. بعد از پخت شله، روغن مایع یا جامد را به اندازه دلخواه اضافه کنید و اجازه دهید شله مشهدی به مدت یک ساعت دیگر روی شعله بسیار ملایم گاز با دم‌کنی مثل برنج دم بکشد. شله مشهدی را در ظرف سرو بریزید و روی آن را با خورش قیمه، تزیین کنید.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2019/08/%D8%B4%D9%84%D9%87-%D9%85%D8%B4%D9%87%D8%AF%DB%8C-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (319, 'دیگچه مشهدی ساده و دو رنگ', 'برنج را با آب بشویید تا زمانی که آب برنج شفاف شود. سپس برنج را به مدت دو تا سه ساعت بخیسانید. این کار کمک می‌کند تا برنج برای پختن کاملا آماده شود. بهتر است از برنج نیم‌دانه استفاده کنید تا هنگام پخت', 'برنج را با آب بشویید تا زمانی که آب برنج شفاف شود. سپس برنج را به مدت دو تا سه ساعت بخیسانید. این کار کمک می‌کند تا برنج برای پختن کاملا آماده شود. بهتر است از برنج نیم‌دانه استفاده کنید تا هنگام پخت کاملا له شود و در غذا دیده نشود. زعفران را بسابید و روی آن چند قطعه یخ بگذارید یا مقداری آبجوش بریزید و صبر کنید تا دم بکشد و کاملا رنگ بگیرد. شیر و برنج را در یک قابلمه مناسب بریزید و آن را روی حرارت قرار دهید. شیر و برنج را روی حرارت با یکدیگر هم بزنید. اجازه دهید شیر کاملاً به خورد برنج برود. برنج باید کاملاً شفته شود. درب قابلمه را ببندید و صبر کنید شیر کاملاً به خورد برنج برود و برنج با شیر خوب بپزد. هر چه مقدار شیر بیشتر باشد بهتر است چون در پایان بافت دیگچه ژله‌ای و لرزان می‌شود. در این مرحله شکر را اضافه کنید و حسابی برنج را هم بزنید تا روی شعله ملایم گاز شکر با برنج مخلوط شود. سپس زعفران، هل و گلاب را هم اضافه کنید و دوباره هم بزنید. زعفران باید غلیظ باشد. اگر عطر گلاب را هم نمی‌پسندید می‌توانید آن را حذف کنید. در مدت زمانی که دیگچه روی حرارت است مدام آن را هم بزنید که ته نگیرد. مقدار سفتی یا شلی دیگچه سلیقه‌ای است. ولی به طور استاندارد وقتی دیگچه به غلظتی شبیه شیر برنج رسید، درب قابلمه را با دم کنی ببندید و اجازه دهید دیگچه روی شعله بسیار ملایم گاز به مدت ۳۰ دقیقه دم بکشد. بعد از گذشت مدت زمان لازم و زمانی که بافت دیگچه کاملا منسجم و ژله‌ای شد آن را از روی حرارت بردارید. اجازه دهید قابلمه کمی از حرارت بیفتد سپس آن را در یک دیس گرد برگردانید و به عنوان یک دسر خوشمزه دیگچه را سرو کنید. برای تهیه دیگچه مشهدی دو رنگ باید مایه دیگچه را قبل از اضافه کردن زعفران، در دو قابلمه جداگانه نصف کنید، روی نیمی زعفران بریزید و نیمی دیگر را سفید نگه دارید. بعد از آماده شدن دیگچه به صورت دلخواه، دو رنگ را در قالب یا پیاله بریزید. یک روش دیگر هم این است که هر دو رنگ را در یک قابلمه دم کنید. مقداری از مواد دیگچه را جدا کنید و با زعفران مخلوط کنید و دوباره روی باقی‌مانده دیگچه در قابلمه بریزید و آن را دم کنید. این طرز تهیه را می‌توانید برای طرز تهیه دیگچه نذری استفاده کنید. بعد از پخت دیگچه مشهدی آن را در یک سینی متوسط یا یک ظرف مناسب با عمق مناسب بریزید و به مدت ۱۲ ساعت (شب تا صبح) در دمای اتاق نگهدارید تا دیگچه خنک شود و خودش را بگیرد. بعد از خنک شدن دیگچه آن را با یک چاقوی تیز برش بزنید. در صورت تمایل روی دیگچه را با دارچین، پودر نارگیل، مغز پسته، گل محمدی، مغز بادام و … تزیین و سرو کنید. یک روش دیگر هم این است که دیگچه را بعد از دم کشیدن در قالب‌ یا کاسه‌های کوچک بریزید و اجازه دهید در همان قالب یا پیاله سرد شود. قبل از ریختن دیگچه کف قالب را چرب کنید. در نهایت نیز می‌توانید به آرامی قالب را برگردانید. سپس روی هر پیاله دیگچه را با انواع مغزها، دارچین، گل محمدی و پودر نارگیل تزیین کنید. روش دیگری که می‌توانید دیگچه مشهدی را سرو کنید، این است که دیگچه را برای مدت زمان بیشتری روی شعله گاز قرار دهید. برای این کار باید ۳۰ تا ۴۰ دقیقه بیشتر آن را روی شعله ملایم بگذارید تا ته دیگ دیگچه طلایی رنگ شود. برای درست کردن دیگچه مشهدی ته دیگی باید درب قابلمه را همراه با دم کنی روی آن قرار دهید و شعله گاز را کم کنید تا دیگچه به آرامی دم بکشد. سپس باید شعله گاز را خاموش کنید و کمی صبر کنید تا دیگچه خنک شود. در نهایت می‌توانید دیگچه را به آرامی و به صورت قالبی برگردانید. سپس روی آن را با انواع مغزها و پودر نارگیل یا تکه‌های شکلات تزیین کنید. برای درست کردن دیگچه مشهدی ته دیگی در فر می‌توانید بعد از آماده شدن دیگچه یک قالب فر را چرب کنید. سپس مواد دیگچه را داخل قالب چرب شده بریزید. روی قالب را با فویل بپوشانید و برای اینکه به خوبی دم بکشد با کمک یک چاقوی تیز چند سوراخ کوچک روی آن ایجاد کنید. در نهایت قالب دیگچه را برای حدود ۴۵ دقیقه در فر قرار دهید. توجه داشته باشید که حتماً در ۱۵ دقیقه آخر فویل را بردارید تا سطح روی دیگچه نیز خشک شود. برای اینکه دیگچه راحت‌تر از قالب خارج شود، می‌توانید آن را برای چند ثانیه در آب سرد قرار دهید و سپس به راحتی روی دیس یا ظرف مناسب برگردانید. در زمان پخت دیگچه مشهدی هر کجا احساس کردید، دسر در حال ته گرفتن است، می‌توانید یک لیوان شیر به آن اضافه کنید. اما توجه داشته باشید که کمی ته گرفتن در طرز تهیه دیگچه مشهدی کاملاً طبیعی است. تنها سعی کنید مواد را خوب هم بزنید و در صورتی که ته گرفت به هیچ عنوان ته دیگ را از کف قابلمه جدا نکنید تا تکه‌های ته گرفته داخل دیگچه پخش نشود.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2025/01/17350519504934.png', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (320, 'کوکوی شیرین قزوین', 'سیب زمینی‌ها را بشویید و در یک قابلمه با مقداری آب، آبپز کنید یا بخارپز کنید. بعد از نرم شدن سیب‌زمینی‌ها حرارت را خاموش کنید و اجازه دهید سرد شوند. سیب‌زمینی‌های پخته شده را رنده یا پوره کنید (با رند', 'سیب زمینی‌ها را بشویید و در یک قابلمه با مقداری آب، آبپز کنید یا بخارپز کنید. بعد از نرم شدن سیب‌زمینی‌ها حرارت را خاموش کنید و اجازه دهید سرد شوند. سیب‌زمینی‌های پخته شده را رنده یا پوره کنید (با رنده متوسط). تخم‌مرغ‌ها را در یک ظرف جدا بشکنید و با چنگال یا همزن بزنید تا لخته‌ها باز شوند و زرده و سفیده کاملا با هم ترکیب شود. سپس آن‌ها را به سیب زمینی پوره شده اضافه کنید. ماست، زعفران دم کرده و پودر هل را به مواد اضافه کنید و مواد کوکو را با هم ورز بدهید تا یکدست شوند. (اگر مایه کوکو خیلی رقیق و شل بود، می‌توانید از سیب زمینی بیشتر یا کمی آرد نخودچی یا آرد سفید استفاده کنید.) روش پخت در فر: فر را روشن و روی دمای ۱۷۵ درجه سانتی‌گراد تنظیم کنید تا گرم شود. کف و دیواره‌های یک قالب را با مقداری روغن یا روغن زیتون چرب کنید. مایه کوکو شیرین را در قالب چرب و آماده شده بریزید و کف قالب را چند بار روی سطح بکوبید تا هوای اضافی از مواد کیک خارج شود. روی مواد را با پشت قاشق صاف کنید. سپس قالب را در فر گرم شده قرار دهید. بسته به سایز و ارتفاع قالب، مدت زمان پختن کوکو متفاوت است ولی به صورت میانگین، حدود ۱ ساعت زمان برای تهیه کوکو شیرین نیاز است. روش پخت در تابه: مواد کوکو شیرین را به روش قبل درست کنید. سپس در یک تابه مقداری روغن بریزید و روی حرارت قرار دهید. سپس مواد کوکوی شیرین قزوین را به تابه اضافه کنید. بهتر است از تابه‌های دو طرفه در این روش استفاده کنید. تابه را روی حرارت ملایم قرار دهید و صبر کنید که یک طرف کوکو سرخ شود و بپزد. کوکو را برگردانید تا طرف دیگر آن هم سرخ شود. شعله گاز را خاموش کنید. برای تهیه شربت کوکو شیرین، در یک ظرف مناسب، آب و شکر را با هم ترکیب کنید و روی حرارت قرار دهید. صبر کنید تا مواد جوش بیایند. بعد از حدود ۵ دقیقه که شربت شروع به جوشیدن کرد، زعفران دم کرده، گلاب و پودر هل را به مواد اضافه کنید و بعد از این‌که یک یا دو جوش خوردند، شعله گاز را خاموش کنید. (نیازی به غلیظ و کشدار شدن شربت نیست.) بعد از این که کوکو شیرین آماده شد و پخت، قالب را از فر بیرون بیاورید یا از تابه خارج کنید و با چاقو به شکل مستطیل یا لوزی (به اندازه دلخواه) کوکو را برش بزنید. شربت کوکو را روی سطح کوکو و داخل شیارهای بین کوکوها بریزید تا وقتی که شربت تمام شود. اجازه دهید کوکوها خنک شوند. در ۱۰ دقیقه پایانی پخت کوکو، یک زرده تخم مرغ را هم بزنید و با کمی کره ذوب شده و زعفران غلیظ روی کوکو رومال کنید. کوکو را دوباره در فر یا تابه به مدت ۱۰ دقیقه قرار دهید تا روی آن طلایی و ترد شود. بعد از سرد شدن کوکوها، آن‌ها را از قالب بیرون بیاورید و در ظرف مناسب قرار دهید. در نهایت کوکو شیرین را با مواد دلخواه مثل پودر یا خلال پسته و بادام یا برگ گل محمدی تزیین کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/03/kooloo-ghazvin-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (321, 'اشکنه قروت', 'برای تهیه اشکنه قروت، به دو روش می‌توانید تخمه خربزه را آماده کنید. استفاده از تخمه خربزه کاملا اختیاری است و می‌توانید آن را از دستور تهیه، حذف کنید. روش اول این است که تخمه‌ها را در تابه تفت دهید و', 'برای تهیه اشکنه قروت، به دو روش می‌توانید تخمه خربزه را آماده کنید. استفاده از تخمه خربزه کاملا اختیاری است و می‌توانید آن را از دستور تهیه، حذف کنید. روش اول این است که تخمه‌ها را در تابه تفت دهید و سپس آن‌ها را در آسیاب‌کن پوره کنید. تخمه خربزه پوره شده را مستقیما در اشکنه به کار ببرید. روش دوم این است که تخمه خربزه آسیاب شده را با نصف لیوان آب سرد مخلوط کرده و هم بزنید تا رنگ آب، کرمی شود. سپس مایه به دست آمده را از صافی رد کنید و محلول شیری رنگ را به اشکنه اضافه کنید. پیاز و سیر را پوست بگیرید و به صورت نگینی ریز خرد کنید. در یک قابلمه، کره یا روغن محلی بریزید و روی حرارت قرار دهید. بعد از داغ شدن روغن، پیازها را در آن سرخ کنید تا کاملا سبک و طلایی شود. سپس سیر را اضافه کنید و هم بزنید، در حدی که عطر آن خارج شود. نعناع خشک را به قابلمه اضافه کنید و تفت دهید تا از خامی بیفتد و کمی سرخ شود ولی مراقب باشید نسوزد. مغز گردوی خرد شده را هم اضافه کنید و برای ۲ تا ۳ ثانیه تفت دهید. اگر تخمه خربزه را به صورت آسیاب شده استفاده می‌کنید، تخمه را هم اضافه کنید و کمی تفت دهید. در غیر این صورت، شیره تخمه خربزه را اضافه کنید. در آخرین مرحله، کشک ساییده را اضافه کنید. اگر از کشک‌های کارخانه‌ای استفاده می‌کنید، کشک را با مقداری آب رقیق کرده و به قابلمه بیفزایید. اجازه دهید مواد داخل قابلمه کمی قُل بخورند. فلفل سیاه را هم اضافه کنید و اگر کشک‌تان به اندازه کافی شور نیست، کمی نمک اضافه کنید. اشکنه قروت که به غلظت و طعم دلخواهتان رسید، حرارت را قطع کرده و با نان خشک و سبزی خوردن، آن را سرو کنید.', 10, 20, 4, 'medium', 'https://blog.okcs.com/wp-content/uploads/2023/11/%D8%B7%D8%B1%D8%B2-%D8%AA%D9%87%DB%8C%D9%87-%D9%82%D8%B1%D9%88%D8%AA-%D8%A8%DB%8C%D8%B1%D8%AC%D9%86%D8%AF.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (322, 'رشته پلو با مرغ', 'برنج را در مقداری آب و نمک خیس کنید.', 'برنج را در مقداری آب و نمک خیس کنید. برای درست کردن پیاز داغ، ۳ عدد پیاز را خلالی و نازک خرد کنید و با کمی روغن و کره تفت دهید. مقداری از پیازهای سرخ شده را برای تزیین کنار بگذارید. سینه مرغ را با بقیه پیاز داغ، یک قاشق غذاخوری روغن مایع، کمی زردچوبه، فلفل سیاه و نمک تفت دهید. وقتی رنگ مرغ تغییر کرد، یک لیوان آب اضافه کنید و اجازه دهید بپزد. وقتی مرغ پخته شد و آب آن کشیده شد، آن را از قابلمه بیرون بیاورید و اجازه دهید کمی خنک شود. سپس سینه مرغ را ریش ریش یا نگینی خرد کنید. مرغ‌های نگینی یا ریش شده را با نصف دیگر کره و زعفران دم کرده در همان قابلمه‌ای که مرغ را پخته بودید بریزید. شعله گاز را روشن کنید و صبر کنید آب به خورد مرغ برود. سپس ½ قاشق چایخوری یا کمتر دارچین اضافه کنید. اجازه دهید مرغ تمام آب موجود در قابلمه را جذب کند و بعد کمی تفت دهید. شعله گاز را خاموش کنید. مرغ نباید خشک شود و بهتر است که مقداری آب‌دار باشد. برنج و رشته‌ها را به صورت جداگانه در آب بپزید و آبکش کنید. رشته پلویی را مثل ماکارونی در آب جوش بپزید و آبکش کنید. در یک قابلمه آب را جوش بیاورید و سپس مقداری نمک و روغن اضافه کنید. رشته‌های پلویی را اضافه کنید و بعد از این‌که نرم شد آبکش کنید. برنج را که از قبل خیس کرده بودید بپزید و آبکش کنید. قبل از آبکش کردن برنج، رشته‌های آبکش شده را هم در قابلمه‌ای که برنج در حال جوشیدن است بریزید و با هم مخلوط کنید و بلافاصله آبکش کنید. مقداری روغن مایع کف قابلمه بریزید و وقتی که داغ شد، چند ورق سیب زمینی یا چند تکه نان برای درست کردن ته دیگ کف قابلمه بگذارید و سپس مخلوط برنج و رشته پلویی آبکش شده را به آن اضافه کنید. کمی کره روی برنج بگذارید و رشته پلو را دم کنید. برای این‌که رشته‌ها خشک نشوند علاوه بر کره، ½ فنجان هم آب روی رشته پلو بریزید و سپس آن را دم کنید. ۴۵ دقیقه برای دم کشیدن رشته پلو روی شعله ملایم گاز کافی است. بهتر است ابتدا کمی شعله گاز را زیاد کنید تا برنج بخار کند و سپس بعد از ۵ دقیقه شعله را کم کنید تا برنج دم بکشد. در فاصله دم کشیدن رشته پلو، کشمش‌ها را آماده کنید. در یک ماهیتابه مناسب کمی کره بریزید و کشمش‌ها را در کره یا روغنی که داغ نیست کمی تفت دهید. به مقدار دلخواه پودر دارچین روی کشمش بپاشید و تفت دهید. اگر طعم کشمش را در رشته پلو دوست ندارید از زرشک استفاده کنید. زرشک‌ها را آماده کنید. حدود ۱۵ دقیقه زرشک‌ها را در مقداری آب بخیسانید. در یک ماهیتابه مناسب کمی کره بریزید و بعد از آب شدن کره، زرشک‌ها را اضافه کنید. زرشک‌ها را روی شعله ملایم گاز تفت دهید که نسوزند. اگر طعم ترش زرشک را زیاد دوست ندارید ½ قاشق چای‌خوری شکر به زرشک‌ها اضافه کنید و با هم مخلوط کنید. بعد از ۴۵ دقیقه رشته پلو مجلسی شما حاضر است. مقداری کره داغ کنید و روی رشته پلو بریزید. حتی می‌توانید به کره مقداری زردچوبه اضافه کنید. سپس رشته پلو را در دیس بکشید و روی آن را با مرغ زعفرانی نگینی یا ریش شده و تفت داده شده و کشمش یا زرشک تزیین کنید، نوش جان!', 40, 80, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/%D8%B1%D8%B4%D8%AA%D9%87-%D9%BE%D9%84%D9%88.jpg-1.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (323, 'لبو', 'لبوها را با دقت بشویید. برای شستن لبو بهتر است از یک اسکاچ سیمی استفاده کنید تا تمام آلودگی‌ها از پوست لبو پاک شود. پوست لبوها و سر و ته لبو را قبل از پختن نگیرید تا رنگ آن حفظ شود.', 'لبوها را با دقت بشویید. برای شستن لبو بهتر است از یک اسکاچ سیمی استفاده کنید تا تمام آلودگی‌ها از پوست لبو پاک شود. پوست لبوها و سر و ته لبو را قبل از پختن نگیرید تا رنگ آن حفظ شود. لبوهای شسته شده را همراه با مقداری آب در قابلمه بریزید. به اندازه‌ای آب به لبوها اضافه کنید که مقدار آن ۲ بند انگشت بالاتر از سطح لبوها باشد. لبوها را باید روی شعله ملایم گاز به مدت ۳ ساعت بپزید. در این مدت درب قابلمه را روی آن قرار دهید و به هیچ عنوان شعله گاز را زیاد نکنید. بعد از پخت لبوها را از آب بیرون بیاورید و پوست لبوها را بگیرید. آب داخل قابلمه را دور نریزید و کنار بگذارید. بهتر است قسمت سر و ته لبو را نیز قبل از پخت جدا نکنید. در این مرحله می‌توانید سر و ته لبو را نیز جدا کنید و آن را به هر شکلی که دوست دارید برش دهید. می‌توانید برای خرد کردن لبوها از قالب های فلزی طرح‌دار مثل قلب و ستاره و … نیز استفاده کنید. لبوهای خرد شده را دوباره در قابلمه بریزید. ۳ قاشق غذاخوری شکر (برای سه لبوی متوسط) اضافه کنید و اجازه بدهید لبوها با شکر بپزند و شهد داخل قابلمه غلیظ شود. اگر شکر دوست ندارید یا برای‌تان ضرر دارد لبو را با شیره انگور یا شیره افرا بپزید. یادتان باشد شیره انگور یا افرا برخلاف شکر رنگ لبو را تیره و کدر می‌کنند. بعد از این‌که لبوها کاملا پخت و آب لبو غلیظ و کشدار شد لبو آماده سرو است. نوش جان! برای پخت لبو در زودپز: لبوها را بشویید و به صورت درسته در زودپز بریزید. مقداری آب به آن اضافه کنید و اجازه دهید لبو برای نیم ساعت بپزد. بعد از این که لبو پخت، می‌توانید پوست آن را جدا کنید و لبوی پخته شده را خرد کنید. برای این که آب لبو غلیظ شود، بهتر است آن را همراه با آب آن، به مدت نیم ساعت روی شعله گاز قرار دهید. در این مدت می‌توانید مقداری شکر یا نبات به لبو اضافه کنید و بعد از غلیظ شدن آب لبو، آن را سرو کنید. برای تهیه لبو تنوری در فر: فر را با دمای ۲۰۰ درجه سانتی‌گراد روشن کنید. سپس سینی فر را با کمی روغن چرب کنید و لبوهای شسته را با پوست روی سینی بچینید. اجازه دهید لبوها برای ۴۵ دقیقه در فر بپزند. سپس آن‌ها را از فر خارج کنید و از طعم لذیذ این لبو لذت ببرید. برای مزه‌دار کردن لبو تنوری می‌توانید از کمی نمک و آویشن تازه استفاده کنید. برای پخت لبو در مایکروفر: لبوها را بشویید و پوست بگیرید و به صورت حلقه‌ای یا هر شکل دیگری که دوست دارید برش بزنید. سپس به لبوها مقدار کمی سرکه بمالید. مایکروفر را بر روی درجه بالا تنظیم کنید. چغندرها را در یک ظرف مناسب مایکروفر بگذارید سپس مقداری آب و شکر هم به لبوها اضافه کنید. برای پخت ۲۵۰ گرم لبو به حدود ۱۰ دقیقه و برای ۵۰۰ گرم به حدود ۱۵ دقیقه زمان نیاز دارید. می‌توانید لبوها را یک مرتبه موقع پخت زیر و رو نمایید تا به طور کامل مغزپخت شوند.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/10/1406090265513-min.jpeg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (324, 'چیپس لبو', 'برای درست کردن چیپس لبو در فر، لبوها را به صورت ورقه‌های باریک خرد کرده و لایه‌های چیپس را مزه‌دار کنید. سپس آن‌ها را‌ از صافی رد کرده و در سینی فر بچینید.', 'برای درست کردن چیپس لبو در فر، لبوها را به صورت ورقه‌های باریک خرد کرده و لایه‌های چیپس را مزه‌دار کنید. سپس آن‌ها را‌ از صافی رد کرده و در سینی فر بچینید. در اولین مرحله، لبوها را با برس بشویید تا کاملا گل و لای از روی پوست آن پاک شود. پوست لبو گرفته نمی‌شود، پس باید تا جای ممکن لبوها را تمیز بشویید. سپس سر و ته لبوها را با چاقو جدا کنید. فر را روشن کرده و روی دمای ۱۹۰ درجه سانتی‌گراد تنظیم کنید تا گرم شود. در این مرحله، لبوها را با چاقو، به صورت باریک و حلقه حلقه، برش بزنید. هر چه لایه‌ها باریک‌تر باشند، چیپس‌تان سریع‌تر درست می‌شود. برای اینکه ضخامت همه لایه‌ها یا ورقه‌های لبو یکسان باشد و چیپس لبو علاوه بر ظاهر زیبا، یکدست شود، بهتر است از رنده مخصوص برای چیپسی کردن برش‌ها استفاده کنید. برای طعم دار کردن ورقه‌های لبو، برش‌های چغندر قرمز را در یک کاسه بریزید و روی آنها را نمک، فلفل سیاه، روغن زیتون و رزماری بریزید. مواد را با هم مخلوط کنید تا کاملا طعم بگیرند. به مدت ۱۵ تا ۲۰ دقیقه صبر کنید تا آب قرمز لبوها، کمی خارج شود و نمک و فلفل به خورد ورقه‌های لبو برود. در این مرحله، لبوهای طعم گرفته را در یک صافی بریزید و اجازه دهید عصاره اضافی خود را از دست بدهد. برای شکل بهتر می‌توانید ورقه‌ها را در کمی آرد بغلتانید. سپس آنها را به ترتیب و بدون اینکه روی هم قرار بگیرند، داخل سینی فر بچینید. درب فر را نیمه باز بگذارید. چیپس‌ها را به مدت ۳۰ دقیقه در فر بگذارید و بعد آنها را بیرون بیاورید و بررسی کنید که کاملا ترد شده و آب خود را از دست داده باشد. می‌توانید با احتیاط آنها را زیر و رو کنید تا یک طرف آن قهوه‌ای نشود. وقتی چیپس لبویتان آماده شد، آنها را از فر خارج کرده و صبر کنید تا از دما بیفتند. سپس ورقه‌های لبو را از سینی جدا کرده و در یک ظرف دربسته، در مکانی خشک و خنک و دور از آفتاب نگهداری کنید. برای درست کردن چیپس لبو سرخ شده، لبوها را بعد از برش زدن، عسل‌مال کنید و هر دو طرف آن را آردپاشی کنید. سپس آنها را در روغن داغ سرخ کنید. در اولین مرحله از طرز تهیه چیپس لبو بدون فر، لبوها را کاملا تمیز بشویید و به صورت ورقه‌های نازک با چاقو برش بزنید یا از رنده مخصوص چیپس استفاده کنید. روی چیپس‌ها را با قلم موی آشپزی، به عسل آغشته کنید. آرد را در الک ریز بریزید و روی ورقه‌های لبو بپاشید. یک طرف لبوها که به آرد آغشته شد، آنها را بتکانید و طرف دیگر را آرد بپاشید. هر دو طرف که آردی شد و از چسبندگی افتاد، مقداری روغن را در یک تابه بریزید و صبر کنید تا کاملا داغ شود. در آخرین مرحله از طرز تهیه چیپس لبو سرخ شده، ورقه‌های لبو را در تابه بیندازید. بهتر است میزان روغن به اندازه‌ای باشد که لبوها کاملا در آن غوطه‌ور شده و هر دو سمت آنها سرخ شود. وقتی چیپس لبو آماده شد، آنها را روی دستمال کاغذی یا دستمال حوله‌ای قرار دهید تا روغن اضافی خود را از دست بدهند. سپس آنها را سرو کنید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2022/10/25.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (325, 'لواشک زردآلو', 'ابتدا زردآلوهای نرم و رسیده را به‌خوبی بشویید و در قابلمه‌ای بریزید. می‌توانید هسته‌هایشان را جدا کنید. نیازی به افزودن آب نیست، چون خود زردآلوها هنگام پخت، آب پس می‌دهند.', 'ابتدا زردآلوهای نرم و رسیده را به‌خوبی بشویید و در قابلمه‌ای بریزید. می‌توانید هسته‌هایشان را جدا کنید. نیازی به افزودن آب نیست، چون خود زردآلوها هنگام پخت، آب پس می‌دهند. قابلمه را روی حرارت بگذارید و مدام مواد را هم بزنید تا ته نگیرند. وقتی بافت میوه کاملا نرم شد و با فشار قاشق له شد، شعله را خاموش کنید و اجازه دهید مخلوط سرد شود. سپس با کمک صافی آن را صاف کنید تا پوست و هسته جدا شده و فقط عصاره باقی بماند. این عصاره را دوباره بجوشانید تا غلیظ شود و در اواخر پخت کمی آبلیمو و نمک اضافه کنید. در آخر مایه را در سینی روی سلفون یا نایلون پهن کرده و در آفتاب یا هوای آزاد به‌مدت چند روز قرار دهید تا خشک شود. برای خشک کردن مایه لواشک زردآلو خانگی، چند راه دارید: یا کف سینی مورد نظرتان را با روغن مایع کمی چرب کنید و یا اگر سلفون مخصوص لواشک دارید، کف سینی پهن کنید. این سلفون یا نایلون باید کمی ضخیم باشد. مایه لواشک زردآلو را در سینی و روی نایلون یا سلفون بریزید. اگر مایه لواشک خیلی غلیظ است، با لیسک یا پشت قاشق، آن را صاف کنید تا ضخامت مایه لواشک کمتر شود. سینی را به سطح کار بکوبید تا مایه کاملا در سینی یکدست پخش شود. این سینی را در مجاورت نور خورشید بگذارید یا اگر آفتاب ندارید در هوای آزاد و جایی که هوا جریان داشته باشد قرار دهید. ۳ روز تا ۱ هفته، بسته به ضخامت لواشک و دمای هوا، زمان لازم است که لواشک در آفتاب خشک شود. برای اینکه لواشک زردآلو گرد و خاک نگیرد، یک توری نازک و لطیف روی آن پهن کنید. روش دیگر برای آبپز کردن زردآلوها این است که زردآلوها را بشویید. هسته‌های آنها را بگیرید. کف یک سینی فر یک ورقه پخت یا کاغذ روغنی ببندازید و قسمت گوشت زردآلو را سمت بالا بگذارید. همه زردآلوها را به همین ترتیب در سینی بچینید. فر را روشن و روی دمای ۲۰۰ درجه سانتی‌گراد تنظیم کنید. سینی را در فر بگذارید و به مدت ۱۵ تا ۲۰ دقیقه صبر کنید تا زردآلوها نرم شوند. بعد آنها را پوره کرده و در سینی چرب شده یا پوشیده شده با سلفون‌ بریزید. سپس آنها را خشک کنید. برای تهیه لواشک زردآلو در فر، زردآلوهای پخته را پوره کنید. کف یک سینی فر، کاغذ روغنی بیندازید، به طوری که دیواره‌های سینی را هم کمی بپوشاند. سپس از مایه یا عصاره زردآلو در سینی فر بریزید و آن را صاف کنید. فر را روشن و روی دمای ۲۰۰ درجه سانتی‌گراد تنظیم کنید. سینی را در فر بگذارید. لای درب فر را کمی باز بگذارید تا هوای خشک به داخل فر راه پیدا کند. به مدت ۲ تا ۳ ساعت صبر کنید. بسته به ضخامت مایه زردآلو، زمان خشک شدن زردآلو در فر فرق می‌کند. بعد از اینکه آب اضافی مایه لواشک بخار شد، سینی را به مدت ۱ شبانه روز در دمای محیط بگذارید. سپس لواشک زردآلو را به اندازه دلخواه برش بزنید و سرو کنید. اگر می‌خواهید زردآلو طعم ملس بگیرد، می‌توانید رب انار هم به لواشک زردآلو اضافه کنید. در این روش، رنگ لواشک طبیعتا تیره‌تر می‌شود. برای درست کردن لواشک زردآلو با رب انار، زردآلو را بپزید یا آبپز کنید و عصاره آن را بگیرید. سپس عصاره را با میزان دلخواه رب انار مخلوط کرده و روی حرارت بگذارید تا طعم رب انار و زردآلو با هم ترکیب شود. وقتی عصاره لواشک به غلظت و طعم دلخواهتان رسید، آن را در آفتاب یا فر خشک کنید. روش دیگر این است که زردآلوها را بشویید و هسته آنها را بگیرید. زردآلوهای هسته گرفته شده را در غذاساز بریزید و پوره کنید یا با گوشتکوب برقی آنها را له کنید. باید مایه به دست آمده، کاملا نرم و یکدست شود. این پوره را روی حرارت بجوشانید تا کمی غلیظ شود. اگر لواشک زردآلو ترش می‌خواهید، می‌توانید مقداری آبلیمو و نمک یا حتی عصاره زرشک را به آن اضافه کنید. سپس لواشک را در فر یا آفتاب خشک کنید. از خشک‌شدن کامل لواشک مطمئن شوید. لواشک را در ظروف دربسته و محیط خشک و خنک نگهداری کنید. همچنین افزودن کمی نمک یا آب‌لیمو از کپک‌زدگی جلوگیری می‌کند.', 10, 20, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2020/06/1596418069-parsnaz-ir22.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (326, 'آش انار یزدی', 'در اولین مرحله از طرز تهیه آش انار یزدی باید عدس و نخود را حداقل به مدت ۲ ساعت در آب بخیسانید. سپس آن‌ها را همراه با کمی آب در قابلمه بریزید و صبر کنید تا کمی نرم شوند. اگر عجله دارید از کنسرو نخود و', 'در اولین مرحله از طرز تهیه آش انار یزدی باید عدس و نخود را حداقل به مدت ۲ ساعت در آب بخیسانید. سپس آن‌ها را همراه با کمی آب در قابلمه بریزید و صبر کنید تا کمی نرم شوند. اگر عجله دارید از کنسرو نخود و کنسرو عدس استفاده کنید. زمانی که حبوبات نیم‌پز شدند، می‌توانید گوشت دنده گوسفند، پیاز خرد شده، برنج، فلفل سیاه، زردچوبه و لبو خرد شده یا رنده شده را به قابلمه اضافه کنید. اجازه دهید مواد آش انار یزدی برای چند ساعت روی شعله متوسط گاز بجوشند و گوشت و لبو کاملا بپزند. بعد از این که گوشت دنده کاملا پخت، آن را از قابلمه خارج کنید. صبر کنید تا کمی خنک شود و سپس گوشت را از استخوان‌ها جدا کنید و سپس دوباره در قابلمه آش بریزید. می‌توانید برای تهیه این آش از گوشت ریش‌ریش شده یا تکه‌ای استفاده کنید. در این مرحله از طرز تهیه آش انار یزدی با لبو باید آب انار ترش تازه و نمک را به آش اضافه کنید و سپس صبر کنید تا آش با شعله ملایم بجوشد و جا بیفتد. بعد از این که آش انار کاملا جا افتاد، مقداری پودر نعنا خشک به آن اضافه کنید. سپس شعله گاز را خاموش کنید و بعد آش را در ظرف مورد نظر بریزید و در صورت تمایل با نعنا داغ و دانه‌های انار تزیین کنید. برای تهیه آش انار یزدی با گوشت چرخ کرده: در اولین مرحله لپه را برای چند ساعت در آب بخیسانید و سپس آن را همراه با مقداری پیاز سرخ شده، نمک، فلفل سیاه و ۵ لیوان آب در قابلمه بریزید تا بپزد. بعد از این که لپه‌ها به طور کامل پختند، باید برنج نیم دانه را با آب فراوان بشویید و به قابلمه اضافه کنید. سپس صبر کنید تا برنج به خوبی بپزد و نرم شود. در این مرحله ۲ عدد پیاز را رنده کنید و آب اضافه آن‌ها را بگیرید. سپس در یک کاسه پیاز رنده شده، نمک، فلفل سیاه و گوشت چرخ کرده را ترکیب کنید. مواد را برای چند دقیقه با دست ورز دهید تا کاملا منسجم شوند. بعد از آماده کردن مواد گوشتی باید به اندازه یک گردو از مواد را بردارید و به صورت کوفته قلقلی درست کنید. در ادامه مقداری روغن در ماهیتابه بریزید و بعد از این که روغن داغ شد، کوفته قلقلی‌ها را در روغن سرخ کنید و بعد از این که لپه و برنج به خوبی پختند، کوفته‌ها را به آش اضافه کنید. در این مرحله سبزی آش را بشویید و به صورت ریز خرد کنید. سپس سبزی‌ها را به آش اضافه کنید. درب قابلمه را روی آن قرار دهید و صبر کنید تا آش کمی جا بیفتد. بعد از این که آش انار شما جا افتاد، در مرحله آخر می‌توانید دانه‌های انار را همراه با کمی گلپر به آش اضافه کنید. چند دقیقه صبر کنید و بعد آش را در ظرف مورد نظرتان بریزید. آش را با نعنا داغ و پیاز داغ تزیین کنید و از طعم آن لذت ببرید. برای تهیه آش انار یزدی بدون گوشت می‌توانید گوشت را حذف کنید و همین مراحل را بدون اضافه کردن گوشت انجام دهید.', 15, 30, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/456.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (327, 'آش شولی یزدی', 'در اولین مرحله چغندرها را پوست بگیرید و به صورت خلالی برش بزنید. سپس چغندرهای برش زده را به همراه مقداری آب در قابلمه بریزید و روی شعله گاز قرار دهید تا نیم‌پز شوند.', 'در اولین مرحله چغندرها را پوست بگیرید و به صورت خلالی برش بزنید. سپس چغندرهای برش زده را به همراه مقداری آب در قابلمه بریزید و روی شعله گاز قرار دهید تا نیم‌پز شوند. بعد از نیم‌پز شدن چغندر، عدس و سبزی آش خرد شده را هم اضافه کنید تا همه مواد خوب با هم بپزند. در این فاصله پیاز داغ و نعناع داغ را آماده کنید. برای این کار پیازها را به صورت خلالی خرد کنید و در روغن سرخ کنید تا طلایی رنگ شوند. سپس نعنا خشک را در روغن بریزید و آن را نیز برای چند ثانیه در روغن سرخ کنید و سپس شعله گاز را خاموش کنید. بعد از این که پیاز داغ و نعنا داغ را آماده کردید، به یک لیوان فرانسوی دسته‌دار آرد، ۳ یا چهار لیوان آب اضافه کنید و با دست خوب با آب مخلوط کنید تا گوله نشود. مخلوط آرد و آب آماده شده را کنار بگذارید. وقتی سبزی و عدس و چغندر کاملا پخت، نیمی از پیاز داغ و نعنا داغ را به آن‌ها اضافه کنید و در آش بریزید. زردچوبه و فلفل سیاه را هم اضافه کنید. وقتی آش شولی چند جوش خورد مخلوط آرد و آب را هم اضافه کنید. همزمان آش را هم بزنید تا آرد گوله نشود. اگر احساس کردید آش شولی خیلی غلیظ است کمی دیگر آب اضافه کنید و اجازه دهید آش خوب جا بیفتد. غلظت آش شولی اسفناج یزدی باید شبیه یک سوپ غلیظ باشد. در ۱۰ دقیقه پایانی پخت باقی مانده پیاز داغ و نعنا داغ و سرکه یا رب انار را هم اضافه کنید (مقداری از پیاز داغ و نعنا داغ را برای تزیین آش شولی نگه دارید). اجازه دهید آش شولی یزدی چند جوش بزند تا بوی تند سرکه گرفته شود. در نهایت آش شولی را با پیاز داغ و نعنا داغ و در صورت تمایل مقداری کشک مایع تزیین کنید.', 33, 67, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2019/11/weq.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
INSERT INTO recipes (id, title, description, instructions, prep_time, cook_time, servings, difficulty, image, author_id, category_id, views_count, average_rating, ratings_count, is_public, created_at, updated_at) VALUES (328, 'فسنجون یزدی (متنجینه)', 'مرغ را بشویید و استخوان‌های آن را جدا کنید. سپس گوشت مرغ را به صورت مکعبی خرد کنید.', 'مرغ را بشویید و استخوان‌های آن را جدا کنید. سپس گوشت مرغ را به صورت مکعبی خرد کنید. آلوها را بشویید و سپس در یک ظرف آب بریزید و اجازه دهید برای چند ساعت در آب بمانند. پیاز را بشویید و پوست آن را جدا کنید. سپس آن را به صورت نگینی خرد کنید. مقداری روغن در ماهیتابه بریزید و پیازهای خرد شده را در روغن سرخ کنید تا طلایی شوند. مغز بادام و گردو را با کمک یک چاقوی تیز به صورت نگینی خرد کنید و یا در آسیاب برقی پودر کنید. سپس پودر مغزیجات را به پیاز سرخ شده اضافه کنید و برای چند دقیقه در روغن تفت دهید تا بوی خامی آن‌ها گرفته شود. تکه‌های خرد شده مرغ را به ترکیب پیاز داغ و مغزیجات اضافه کنید. چند دقیقه مرغ‌ها را تفت دهید و زمانی که رنگ مرغ‌ها تغییر کرد، نمک، فلفل سیاه و پودر سیر را اضافه کنید. در ادامه ۴ لیوان آب سرد به قابلمه اضافه کنید و درب قابلمه را روی آن بگذارید و صبر کنید تا مرغ‌ها برای نیم ساعت بپزند. به و چغندر را بشویید و پوست آن‌ها را جدا کنید. سپس آن‌ها را همراه با کمی آب در قابلمه مناسبی بریزید و صبر کنید تا بپزند. معمولا پخت چغندر و به حدود ۲۰ تا ۳۰ دقیقه طول می‌کشد. آلوهای خیسانده شده، به و چغندر پخته شده را به سایر مواد قابلمه اضافه کنید. مواد را هم بزنید و سپس مقداری رب انار و نمک به آن اضافه کنید. درب قابلمه را ببندید و حدود ۱۵ دقیقه صبر کنید تا خورشت شما آماده شود. بعد از این که خورشت به غلظت مناسب رسید، می‌توانید آن را در ظرف مورد نظر بریزید و همراه برنج کته یا برنج چلو سرو کنید.', 35, 70, 4, 'hard', 'https://blog.okcs.com/wp-content/uploads/2023/12/233.jpg', 1, NULL, 0, 0.00, 0, TRUE, NOW(), NOW());
